INSERT INTO `messages` (`id`, `fio`, `phone`, `email`, `message`, `status`, `address`, `job`, `dateline`, `created_at`, `updated_at`, `sendbyemail`, `publish`) VALUES
(1, 'Ситникова Марина Александровна', '89633149721', 'marinka2308@mail.ru', 'Обращение в Интернет-приемную\r\nФИО: Ситникова Марина Александровна\r\nПочтовый адрес: Ленинградская обл, Всеволожский рай-он\r\nМесто работы/учебы: МОУ Всеволожский р-он, учитель\r\nЭлектронная почта: marinka2308@mail.ru\r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nЗдравствуйте. У меня Временное Убежище, дочь едет в лагерь в Крым, нужно ли мне уведомить УФМС о ее выезде? Можно ли сделать это в электронной форме? Если нет, то по какому адресу ехать? На Римского Корсакова 39,\r\nИ еще вопрос? У мамы ВУ, хочет съездить в Донецк на 10 дней, куда ей ехать, чтобы забрать свой паспорт? На Римского Корсакова 39, как раньше?\r\nИ последний вопрос?! В декабре была на консультации на Римского Корсакова 39, на 29 июня назначили подачу документов на Программу Переселения, никакого талона не дали, сказали прийти ко времени- вызовут. Теперь, в связи с реорганизацией УФМС, куда мне ехать подавать на Программу-? На Римского Корсакова 39? И вызовут ли меня в назначенное время? Или снова выстаивать живую очередь? Может можно подать документы в электронном виде? \r\nCпасибо.\r\n', 0, NULL, NULL, 1466171023, NULL, NULL, 0, 0),
(2, 'Мухмудов Азамат Шамильевич', '', 'muhmut1978@mail.ru', 'Обращение в Интернет-приемную\r\nФИО: Мухмудов Азамат Шамильевич\r\nПочтовый адрес: Санкт-Петербург\r\nМесто работы/учебы: \r\nЭлектронная почта: muhmut1978@mail.ru\r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nдравствуйте скажите божалйста когда я магу дат на получения гражданства в РФ,\r\nя 2месяс назад получил виднажителство, и имею офисалное брак с кражданкой РФ палтары года, и я магу получат гражданство РФ в упрашоном порятке, и умею свайо жилйо? ', 1, NULL, NULL, 1468573629, NULL, NULL, 0, 0),
(3, 'Панчеенко Алена Витальевна', '89500034590', '', 'Обращение в Интернет-приемную\r\nФИО: Панчеенко Алена Витальевна\r\nПочтовый адрес: СПб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nЯ приехала в СПбс Украины больше 2х месяцев назад.Все время искала работу безрезультатно, но недавно нашла.Мне сказали оформлять патент. Что делать, подскажите?! В миграционной карте подчеркнута частная цель въезда.', 0, NULL, NULL, 1468581010, NULL, NULL, 0, 0),
(4, 'Никоноров Илья Михайлович', '89219975170', '', 'Обращение в Интернет-приемную\r\nФИО: Никоноров Илья Михайлович\r\nПочтовый адрес: Спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nПоследняя дата оплаты патента у меня стоит 6ое число. Я 5ого числа попросил жену оплатить, но так как в квитанции была сумма в 4000р она столько и заплатила(я забыл ей сказать что сумма другая). Остальные 200р я внес уже сам 6ого числа. Является ли это просрочкой оплаты и что можно в этом случае предпринять? ', 0, NULL, NULL, 1468593413, NULL, NULL, 0, 0),
(5, 'Митканчук Илья Петрович', '89046003012', '', 'Обращение в Интернет-приемную\r\nФИО: Митканчук Илья Петрович\r\nПочтовый адрес: Спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nЗдравствуйте. Хочу воспользоваться программой «носитель русского языка», у меня мама родилась в Приморском крае в 1941 г, до этого бабушка жила в Омске 39-41 гг. Для участия в программе необходимо справка или выписка подтверждающая факт проживания… Архив и ФМС Омска говорят (по тел.) что у них данные от 1974 г. Подскажите где можно получить такую информацию (справку/выписку)?', 0, NULL, NULL, 1468740106, NULL, NULL, 0, 0),
(6, 'Кучеренко Вадим Андреевич', '89674321055', '', 'Обращение в Интернет-приемную\r\nФИО: Кучеренко Вадим Андреевич\r\nПочтовый адрес: ЛО, г. Всеволожск\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nЗдравствуйте! Можете помочь мне с регистрацией во Всеволожском районе Ленинградской области для участия в программе переселения соотечественников для получения РВП? ', 0, NULL, NULL, 1468759916, NULL, NULL, 0, 0),
(7, 'Назаров Болбурждан Н', '89616121804', '', 'Обращение в Интернет-приемную\r\nФИО: Назаров Болбурждан Н\r\nПочтовый адрес: спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nРаботал в ООО СПБ риновация.не дали зарплату за 2 месяца. можити помоч?', 0, NULL, NULL, 1468836871, NULL, NULL, 0, 0),
(8, 'Сафаров Насим Насимович', '89526661831', '', 'Обращение в Интернет-приемную\r\nФИО: Сафаров Насим Насимович\r\nПочтовый адрес: спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nздарствуйте я граданин узбикестана прожыл на теретории россии 8 лет. у меня есть жена и маленький ребенок. как мне можно получить гражданство и втечении какого времени??????', 0, NULL, NULL, 1468854313, NULL, NULL, 0, 0),
(9, 'Мансарова Елена Николаевна', '89219480811', '', 'Обращение в Интернет-приемную\r\nФИО: Мансарова Елена Николаевна\r\nПочтовый адрес: Ленинградская область. Тосно\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nЯ гражданка Украины. Как я могу участвовать в программе переселения соотечественников? Живу и работаю в области. есть регистрация. Можете помочь?', 0, NULL, NULL, 1469027388, NULL, NULL, 0, 0),
(10, 'Кирильченко Виталий Прокофьевич', '89990386933', '', 'Обращение в Интернет-приемную\r\nФИО: Кирильченко Виталий Прокофьевич\r\nПочтовый адрес: Петербург\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nКак я могу подтвердить свое образование врача в России? Работал на скорой, реаниматолог. Сейчас вынужден работать на стройке.', 0, NULL, NULL, 1469084223, NULL, NULL, 0, 0),
(11, 'Фофанов Алексей Иванович', '89218600706', '', 'Обращение в Интернет-приемную\r\nФИО: Фофанов Алексей Иванович\r\nПочтовый адрес: спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nмне 22 года. как я могу поступить в вуз в Спб? гражданин Украины. атестат утерян.', 0, NULL, NULL, 1469184264, NULL, NULL, 0, 0),
(12, 'маюнов шахтамбек мед', '89602313045', '', 'Обращение в Интернет-приемную\r\nФИО: маюнов шахтамбек мед\r\nПочтовый адрес: питер\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nне дают патент. есть неаплаченые штрафы. боюсь депорт. что мне делат?', 0, NULL, NULL, 1469194552, NULL, NULL, 0, 0),
(13, 'Рогачева Екатерина Павловна', '89819806011', '', 'Обращение в Интернет-приемную\r\nФИО: Рогачева Екатерина Павловна\r\nПочтовый адрес: Спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nХочу поступить в вуз Спб. Харьков не выдает аттестат о среднем образовании. Могу я поступить со справкой гимназии о том, что мне выдан аттестат?', 0, NULL, NULL, 1469525631, NULL, NULL, 0, 0),
(14, 'Кононенко Петр Дмитриевич', '89110966721', '', 'Обращение в Интернет-приемную\r\nФИО: Кононенко Петр Дмитриевич\r\nПочтовый адрес: спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nмогу ли я переоформить патент без трудового договора. уведомление о работе не посылал. мои действия? будет ли штраф?', 0, NULL, NULL, 1469538648, NULL, NULL, 0, 0),
(15, 'Мининчук Антон Богданович', '89312483462', '', 'Обращение в Интернет-приемную\r\nФИО: Мининчук Антон Богданович\r\nПочтовый адрес: Украина\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nкак я могу купить авто в Спб как гражданин Украины? какие нужны документы? ', 0, NULL, NULL, 1469542986, NULL, NULL, 0, 0),
(16, 'Кыштынбаев Нарзан М', '89110967754', '', 'Обращение в Интернет-приемную\r\nФИО: Кыштынбаев Нарзан М\r\nПочтовый адрес: сп\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nя патерял паспарт узбекистана. как новый зделать?', 0, NULL, NULL, 1469602188, NULL, NULL, 0, 0),
(17, 'Кадырова Надия н', '89052582033', '', 'Обращение в Интернет-приемную\r\nФИО: Кадырова Надия н\r\nПочтовый адрес: гатчина\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nкак я могу сделать гражданству росси моего отцу пенсионеру который сичас жывет в узбекистани? хочу его перевести к себе в гатчину.', 0, NULL, NULL, 1469800829, NULL, NULL, 0, 0),
(18, 'Бусько Виктор Ярославович', '89312507988', '', 'Обращение в Интернет-приемную\r\nФИО: Бусько Виктор Ярославович\r\nПочтовый адрес: Украина\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Нет\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nМогу я участвовать в программе переселения если утеряна трудовая книжка в связи с военными действиями на Украине?', 0, NULL, NULL, 1470030909, NULL, NULL, 0, 0),
(19, 'Газанов Ибрагим Гамзв оглы', '89110035470', '', 'Обращение в Интернет-приемную\r\nФИО: Газанов Ибрагим Гамзв оглы\r\nПочтовый адрес: спб\r\nМесто работы/учебы: спб\r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nкак получить вмж? имеем статус бежнцы. семя 2 человека.', 0, NULL, NULL, 1470042220, NULL, NULL, 0, 0),
(20, 'АБАШИДЗЕ Нино Вахтанговна', '89819808711', '', 'Обращение в Интернет-приемную\r\nФИО: АБАШИДЗЕ Нино Вахтанговна\r\nПочтовый адрес: спб\r\nМесто работы/учебы: полушка\r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nу моих детей грузинское гражданство, на данный момент визы в паспортах просрочены, могу ли я подать их документы на российское гражданство в упрощённом порядке, я мать, гражданка россии. что мне нужно для этого сделать?', 0, NULL, NULL, 1470059555, NULL, NULL, 0, 0),
(21, 'Ахмедов Алим С', '89046502030', '', 'Обращение в Интернет-приемную\r\nФИО: Ахмедов Алим С\r\nПочтовый адрес: спб\r\nМесто работы/учебы: \r\nЭлектронная почта: \r\n\r\nПолучить ответ по email: Да\r\nОпубликовать вопрос: Да\r\n\r\nТекст вопроса:\r\nКо мне в Спб с Киргизии планируют приехать родственники на срок на 25 дней. Какие документы им необходимо оформить, для нахождения в Питере и беспрепятственного передвижения по территории РФ, если мы выедем за пределы региона?', 0, NULL, NULL, 1470124352, NULL, NULL, 0, 0),
