INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(1, 0, 1471364213, 'Как мигранту помог грант Президента РФ', '', '<p>В Санкт-Петербурге с 1 апреля 2016 года действует приемная по правовой поддержке иностранных граждан. Создание приемной осуществлено в рамках реализации социально значимого проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 01.04.2015 № 79-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (http://civildignity.ru). Данный грант получен Фондом поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, который является организатором открытия Приемной. Приемная находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.</p>\n<p>В своей практике, юристы, оказывающие квалифицированную помощь, часто сталкиваются с обращениями иностранных граждан, связанными с фактами нарушений их прав со стороны должностных лиц государственных органов, а именно &ndash; незаконное изъятие документов, необоснованные аресты и задержания, отказ в приеме документов, наложение штрафов. Защититься от произвола представителей власти и обезопасить себя от необоснованных претензий в дальнейшем можно, обжаловав их действия в административном и судебном порядке.</p>\n<p>Случай из практики. Гражданин Таджикистана Н. обратился за помощью в приёмную по юридической защите с целью урегулирования спора по неправомерным действиям сотрудников миграционной службы по Санкт-Петербургу и Ленинградской области. Суть обращения такова.</p>\n<p>Гражданин Таджикистана Н. легально работает в России на протяжении 5 лет водителем маршрутного автобуса. В мае 2016 года он вновь подал документы на получение патента на работу.</p>\n<p>10 июня 2016 года состоялась очередная выездная проверка документов водителей транспортных средств города Санкт-Петербурга сотрудниками миграционной службы и полиции. Гражданин Таджикистана Н. в это время находился в рабочем автомобиле в качестве пассажира и обучался новому маршруту следования у своего коллеги. В ходе проверки было установлено, что у гражданина Таджикистана Н. нет в наличии патента на работу, поэтому согласно составленному протоколу ему предъявили обвинение в незаконной трудовой деятельности с наложением штрафа и выдворением из России. И дело было передано в суд Невского района Санкт-Петербурга. На все объяснения гражданина Таджикистана Н. о том, что патент он получит 10 июня в 16.00 ч, сотрудники миграционной службы и полиции не реагировали и считали пустыми. У иностранного гражданина Н. были изъяты все документы до решения суда.</p>\n<p>В оперативном порядке юристами приёмной правовой защиты выдана консультация о незамедлительных действиях в данной ситуации. А именно при получении патента его следовало предъявить судье, рассматривающему дело, и в кротчайшие сроки.</p>\n<p>16 июня 2016 года гражданин Таджикистана Н., получив патент, предъявил его судье, который вернул материалы судебного производства миграционной службе, т.к. не было обнаружено состава нарушения.</p>\n<p>В результате выполнения рекомендованных юристами правовой приемной действий и полного контроля над возникшей проблемой ситуация была полностью урегулирована и не допущены судебные тяжбы. Мигрант не был выдворен, а ему грозили именно выдворением.</p>', NULL, '', '', NULL, NULL, 1),
(2, 0, 1471364268, 'Фонд \"ОРИЕНТИР\" и Сабантуй-2016', '', '<p>В текущем году жители Санкт-Петербурга и Ленинградской области в очередной раз отметили национальный праздник башкирского и татарского народов Сабантуй, в котором принимают участие все народности. В 2016 года Сабантуй прошел 9 июля в деревне Энколово, Всеволожский район Ленинградской области.</p>\n<p>Сабантуй в Энколово - это масштабное событие, на котором гости праздника смогли поучаствовать в конкурсах по борьбе и бегу в мешках, принять участие в перетягивании каната, посмотреть концертную программу с выступлениями различных коллективов и состязания в национальной борьбе куреш, посетить выставку национальных ремесел, попробовать национальную кухню - чак-чак, плов, бишбармак и др.</p>\n<p>На празднике Сабантуй 2016 в Санкт-Петербурге были замечены почетные гости - председатель Государственной Думы РФ Сергей Нарышкин, губернатор Ленинградской области Александр Дрозденко, вице-губернатор Санкт-Петербурга Александр Говорунов, председатель Государственного Совета Республики Татарстан Фарид Мухаметшин и полномочный представитель Республики Башкортостан при президенте России Артур Шайнуров.</p>\n<p>Спикер Госдумы Сергей Нарышкин в своей речи отметил ценность и важность праздника Сабантуй с таким ярким национальным колоритом и всеми почитаемого в России.</p>\n<p>К участию в мероприятии были приглашены все руководители национально-культурных объединений, а также президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М., который поздравил глав татарской и башкирской диаспор с национальным праздником с пожеланием продуктивного сотрудничества на благо всех представителей этих народностей, населяющих Санкт-Петербург и Ленинградскую область.</p>', NULL, '', '', NULL, NULL, 1),
(3, 0, 1471364325, 'Фонд \"ОРИЕНТИР\" - участник межнационального праздника \"Энколово собирает друзей\"', '', '<p>31 июля во Всеволожском районе состоялся межнациональный праздник \"Энколово собирает друзей\".</p>\n<p>Он объединил сразу несколько праздников народов, населяющих Ленинградскую область, которые традиционно отмечаются в летнее время: марийский &laquo;Пеледыш пайрем&raquo; (&laquo;Праздник цветов&raquo;), армянский &laquo;Вардавар&raquo;, чувашский &laquo;Акатуй&raquo;, финнов-ингерманландцев &laquo;Юханнус&raquo;, татаро-башкирский &laquo;Сабантуй&raquo;, славянский Ивана Купала, якутский &laquo;Ысыах&raquo;, эвенкийский &laquo;Нургэнэк&raquo;, литовский &laquo;Йонинес&raquo;, вепсский &laquo;Древо жизни&raquo;.</p>\n<p>Организаторы праздника &laquo;Энколово собирает друзей&raquo;: &laquo;Дом дружбы Ленинградской области&raquo; и &laquo;Дом дружбы Всеволожского района&raquo; при поддержке комитета по местному самоуправлению, межнациональным и межконфессиональным отношениям Ленинградской области.</p>\n<p>В программу празднования вошли концерт творческих коллективов и исполнителей, молодецкие игры и забавы; мастер-классы художников по живописи и национальных ремесел гончарного и декоративно-прикладного искусства, выставка национальных костюмов, дегустация национальных блюд. Также организована ярмарка традиционных изделий и промыслов.</p>\n<p>Президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М. поздравил всех руководителей приглашенных национально-культурных объединений с летним праздником с пожеланием продуктивного сотрудничества &nbsp;и вручил им официальные письма-приглашения на &laquo;Дни диаспор&raquo;, организуемые Фондом &laquo;ОРИЕНТИР&raquo; в рамках реализации социально значимого проекта &laquo;&laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;.</p>', NULL, '', '', NULL, NULL, 1),
(4, 0, 1487145451, 'Открытие приемной бесплатной юридической помощи мигрантам', '<p>1 октября 2016 года состоялось официальное открытие Приемной правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области.</p>', '<p>1 октября 2016 года состоялось официальное открытие Приемной правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области. Мигранты получают юридическую помощь исключительно на безвозмездной основе. Консультации предоставляются в устной и письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы Приемной &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области.</p>\n<p>Проект является продолжением проекта \"Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области\", выигравшего грантовый конкурс Движения \"Гражданское достоинство\" 2015 года.</p>\n<p>В рамках реализации проекта будет продолжена работа по внедрению комплекса социальных мероприятий, направленных на адресное информирование, оказание консультационной и оперативной профессиональной юридической помощи гражданам, мигрантам, лицам без гражданства, вынужденным переселенцам, иностранным гражданам в критических ситуациях, а именно: прибытие в PФ, потеря документов, незаконное увольнение с работы, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, принуждение работать сверхурочно, незаконное обвинение в нанесении материального ущерба, незаконное привлечение к дисциплинарной и административной ответственности, принуждение к увольнению, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p>С первых дней работы Приемной идут обращения мигрантов-соотечественников на телефон горячей линии правовой поддержки. На базе сайта Фонда &laquo;ОРИЕНТИР&raquo; организована интернет-приемная для дистанционной работы с обращениями иностранных граждан. Основные вопросы, с которыми обращаются граждане, - это миграционное законодательство и трудовые споры.</p>\n<p>Прием мигрантов ведут квалифицированные юристы, которые в полном объеме дают разъяснение по назначению государственной социальной помощи по всем интересующим вопросам миграции. Все случаи обращений будут рассматриваться в пределах компетенции правовой Приемной, а также в тесном сотрудничестве со всеми партнерами. При необходимости к рассмотрению обращений будут привлекаться непосредственно сотрудники Управления по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области, а также национальные диаспоры, посольства и представительства миграционных служб других государств.</p>\n<p>Создание Приемной осуществлено в рамках реализации социально значимого проекта &laquo;Оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (http://civildignity.ru).</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для переселенцев-соотечественников на территориях вселения. Фонд &laquo;ОРИЕНТИР&raquo; имеет филиалы не только в России, но и представительства в Европе.</p>\n<p>Расположение Приемной правовой поддержки выбрано не случайно. Приемная находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.</p>', NULL, '', '', NULL, NULL, 1),
(5, 0, 1487145586, 'Акция для трудовых мигрантов', '<p>С ноября 2016 года Фонд &laquo;ОРИЕНТИР&raquo; организовал массовую акцию по распространению информационных листовок о приемной бесплатного правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области.</p>', '<p>С ноября 2016 года Фонд &laquo;ОРИЕНТИР&raquo; организовал массовую акцию по распространению информационных листовок о приемной бесплатного правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области. Акция проходит в самых массовых местах скопления трудовых мигрантов &mdash; на улице Красного Текстильщика, Сенной рынок, рынок &laquo;Народный&raquo;.</p>\n<p>Раздачи осуществляются в этих местах почти непрерывно с 8 до 13 часов. Благодаря довольно большому потоку людей, удалось распространить огромное количество материалов.</p>\n<p>Трудовые мигранты, лишь увидев заголовки, живо интересуются листовками, вступают в беседы с промоутерами, узнают более подробную информацию. Количество поступающих обращений по телефону &laquo;горячей линии&raquo; бесплатной приемной правового консультирования заметно увеличилось. Как и показывает рабочая статистика, основные вопросы, которые задают иностранные горожане, - это миграционные трудности и споры с работодателями.</p>\n<p>Данная акция проводится в рамках реализации проекта по внедрению комплекса социальных мероприятий, направленных на адресное информирование, оказание консультационной и оперативной профессиональной юридической помощи гражданам, мигрантам, лицам без гражданства, вынужденным переселенцам, иностранным гражданам в критических ситуациях.</p>\n<p>Приемная бесплатной юридической помощи организована с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (<a href=\"http://civildignity.ru/\">http://civildignity.ru</a>).</p>\n<p>Офис приемной находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.&nbsp;</p>', NULL, '', '', NULL, NULL, 1),
(6, 0, 1487145710, 'Социально-значимые проекты снижают преступность.', '<p>В Санкт-Петербурге при реализации государственных программ решаются вопросы трудоустройства иностранных граждан, оказывается значимая поддержка в социальной, языковой и культурной адаптации, а также ведется эффективная работа по снижению количества преступлений и их предупреждению.</p>', '<p>В Санкт-Петербурге при реализации государственных программ решаются вопросы трудоустройства иностранных граждан, оказывается значимая поддержка в социальной, языковой и культурной адаптации, а также ведется эффективная работа по снижению количества преступлений и их предупреждению. Результативность проводимой работы определяется совместными усилиями и координированными действиями исполнительной власти, правоохранительных органов, а также общественными организациями, такими как Фонд &laquo;ОРИЕНТИР&raquo;, реализующими социально значимые проекты по адаптации мигрантов-соотечественников в Санкт-Петербурге и Ленинградской области.</p>\n<p>К примеру, губернатор Георгий Полтавченко в ноябре 2016 года провел в Смольном очередное заседание Межведомственной комиссии по профилактике правонарушений, где использовались материалы Фонда &laquo;ОРИЕНТИР&raquo;, наработанные за время открытой правовой приемной для трудовых мигрантов в рамках реализации социально значимых проектов по оказанию юридической помощи мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области в период с 1 марта 2016 по настоящее время.</p>\n<p>Безвозмездная юридическая помощь мигрантам стала возможной благодаря использованию средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (<a href=\"http://civildignity.ru/\">http://civildignity.ru</a>).</p>\n<p>Данные мероприятия направлены на бытовую, языковую и социокультурную адаптацию мигрантов, профилактику экстремизма, укрепление межнационального согласия и гражданского единства, реализуемые в соответствии с Указом Президента Российской Федерации от 7 мая 2012 года № 602&nbsp; \"Об обеспечении межнационального согласия\", Постановлением Правительства&nbsp; САНКТ-ПЕТЕРБУРГА от 4 июня 2014 г. N 452 &laquo;О государственной программе Санкт-Петербурга &laquo;Создание условий для обеспечения общественного согласия в Санкт-Петербурге&raquo; на 2015-2020 годы&raquo;.</p>\n<p>Губернатор в своем докладе отметил, что наблюдается позитивная тенденция снижения количества преступлений, которые совершают иностранные граждане. По сравнению с аналогичным периодом прошлого года за прошедшие 11 месяцев количество преступлений снизилось на 9 %.</p>\n<p>&nbsp;&laquo;Количество иностранцев, прибывающих в наш город на заработки, остается по-прежнему значительным. Часть из них, сталкиваясь с затруднениями в быту, в работе встает на путь правонарушений&raquo;, &ndash; сказал Георгий Полтавченко. Поэтому, по словам губернатора, необходимо обеспечить неукоснительное применение мер ответственности к нарушителям законов, в то же время обеспечить порядок пребывания иностранных граждан в городе более прозрачным.</p>', NULL, '', '', NULL, NULL, 1),
(7, 0, 1490601180, 'Фонд \"ОРИЕНТИР\" окажет бесплатную юридическую помощь мигрантам в г. Сланцы.', '<p>17 января 2017 года с 11.00 часов до 17.00 часов в здании Сланцевской районной администрации по адресу: г. Сланцы, пер. Почтовый, д. 3 состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Сланцевскомй районе Ленинградской области.&nbsp;</p>', '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, '', '', NULL, NULL, 1),
(8, 0, 1490602790, 'Фонд \"ОРИЕНТИР\" окажет бесплатную юридическую помощь мигрантам в г. Луга.', '<p>24 января 2017 года с 11.00 часов до 17.00 часов в здании Лужской районной администрации по адресу: г. Луга, пр. Кирова, д. 73 состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Лужском районе Ленинградской области.&nbsp;</p>', '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, '', '', NULL, NULL, 1),
(9, 0, 1490603082, 'Мигранты получают бесплатную юридическую помощь в Ленинградской области.', '<p>Согласно календарному плану проекта, предусмотрена выездная работа общественной приемной бесплатной юридической помощи Фонда &laquo;ОРИЕНТИР&raquo; в районных центрах Ленинградской области для консультирования и оказания социальной поддержки мигрантам-соотечественникам.</p>', '<p>Фонд &laquo;ОРИЕНТИР&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта, в соответствии с распоряжением Президента Российской Федерации&nbsp; от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo;, с 01.10.2016 г. по настоящее время безвозмездно оказывает юридическую помощь иностранным гражданам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области, в сфере миграционного и трудового законодательства Российской Федерации.</p>\n<p>В рамках указанного проекта открыта общественная приемная бесплатной юридической помощи, действующая с 1 октября 2016 года по президентскому гранту, где оказывается помощь в решении миграционных вопросов и трудовых споров.</p>\n<p>Согласно календарному плану проекта, предусмотрена выездная работа общественной приемной бесплатной юридической помощи Фонда &laquo;ОРИЕНТИР&raquo; в районных центрах Ленинградской области для консультирования и оказания социальной поддержки мигрантам-соотечественникам.</p>\n<p>Мигранты получают юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по миграционному законодательству, а также гражданскому и трудовому праву. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>\n<p>Оперативную юридическую помощь смогут получить трудовые мигранты, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в PФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для соотечественников на территориях вселения.&nbsp;</p>', NULL, '', '', NULL, NULL, 1),
(10, 0, 1490691634, 'Фонд \"ОРИЕНТИР\" окажет бесплатную юридическую помощь мигрантам в г. Сясьстрой.', '<p>13 февраля 2017 года с 11.00 часов до 16.00 часов на базе предприятия ООО &laquo;Северный Тракт&raquo; по адресу: г. Сясьстрой, ул. Петрозаводская, д. 14а состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Волховском районе Ленинградской области.</p>', '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, '', '', NULL, NULL, 1),
(11, 0, 1490692527, 'Фонд \"ОРИЕНТИР\" окажет бесплатную юридическую помощь в г. Гатчина.', '<p>16 февраля 2017 года с 10.00 часов до 16.00 часов в здании Гатчинского центра занятости населения по адресу: г. Гатчина, ул. Карла Маркса, д. &nbsp;66 А (актовый зал) состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Гатчинском районе Ленинградской области.</p>', '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, '', '', NULL, NULL, 1),
(12, 0, 1490694569, 'Статистика: уехавшие из-за кризиса мигранты начали возвращаться в Петербург.', '<p class=\"entry-subtitle\">В 2016 году в город приехали 18 756 тыс. мигрантов.</p>', '<p>Мигранты из стран СНГ, ранее покинувшие Петербург из-за экономического кризиса и девальвации рубля, начали снова приезжать в город. Об этом говорится в материалах, опубликованных в среду Петростатом.</p>\n<p>В прошлом году из стран ближнего зарубежья в Петербург приехали 18 756 человек, тогда как уехали 17 041. Таким образом, число мигрантов выросло на 1751 человека (при том, что в статистике учитываются только те, кто зарегистрировал&nbsp;свое пребывание в&nbsp;ФМС).</p>\n<p>Петростат зафиксировал увеличение числа мигрантов из&nbsp;Украины, Молдовы, Казахстана, Киргизии, Азербайджана и Армении. При этом число приезжих из Белоруссии, Таджикистана, Туркменистан и Узбекистана сокращается.</p>\n<p>Добавим, в 2015 году из Петербурга уехали&nbsp;51 505 граждан стран СНГ. Приехало вдвое меньше, из-за чего&nbsp;число мигрантов уменьшилось&nbsp;на 25 177 человек.</p>\n<p>Источник:&nbsp;http://abnews.ru</p>', NULL, '', '', NULL, NULL, 1),
(13, 0, 1490695154, 'Заполняемость доходных домов для трудовых мигрантов в Петербурге достигла 80%.', '<p class=\"entry-subtitle\">Несмотря на запрет заселяться в помещения вместе с семьей.</p>', '<p>Заполняемость заселяемых помещений доходных домов для трудовых мигрантов в Петербурге достигла 80%. Об этом во вторник журналистам сообщила начальник сектора доходных домов жилищного комитета Анжела Медведева, передает корреспондент АБН.</p>\n<p>По ее словам, дома пользуются у иностранных работников популярностью, хотя мигрантам запрещают заселяться вместе с семьей. Стоимость койко-места в таких помещениях начинается от 120 рублей, комнаты &ndash; от 490 рублей, а квартиры &ndash; от 730 рублей в сутки. В доходных домах предусмотрена круглосуточная охрана, а также уборка номеров и мест общего пользования. Гражданам России предоставляют возможность регистрации по месту временного пребывания, сообщила Медведева.</p>\n<p>Отметим, по данным государственного автономного учреждения &laquo;Центр трудовых ресурсов&raquo;, в Петербурге снизилась потребность работодателей в привлечении иностранных работников. Отечественные компании предпочитают российских работников из-за введения ряда ограничений и запретов в отношении иностранных граждан. Работодателей не устраивает временный характер разрешительных документов трудовых мигрантов.</p>\n<p>Источник:&nbsp;http://abnews.ru.</p>', NULL, '', '', NULL, NULL, 1),
(14, 0, 1490704541, 'В России появятся новые формы уведомлений об осуществлении трудовой деятельности в РФ .', '<p>Министерство внутренних дел Российской Федерации опубликовало <span style=\"text-decoration: underline;\">проект Приказа</span>&nbsp;об утверждении новых форм уведомлений об осуществлении&nbsp;иностранными гражданами трудовой деятельности на территории Российской Федерации, в том числе высококвалифицированными специалистами.</p>', '<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.4\">Приказом утверждаются новые документы:</p>\n<ol class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5\">\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.0\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.0.0\">Форма&nbsp;ходатайства иностранного гражданина о привлечении его в качестве высококвалифицированного специалиста.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.1\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.1.0\">Порядок заполнения ходатайства иностранного гражданина о привлечении его в качестве высококвалифицированного специалиста.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.2\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.2.0\">Форма&nbsp;уведомления о трудоустройстве иностранного гражданина организацией, оказывающей услуги по трудоустройству иностранных граждан на территории Российской Федерации.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.3\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.3.0\">Порядок представления организацией, оказывающей услуги по трудоустройству иностранных граждан на территории Российской Федерации, уведомления о трудоустройстве иностранного гражданина.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.4\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.4.0\">Форма&nbsp;уведомления об исполнении работодателями и заказчиками работ (услуг) обязательств по выплате заработной платы (вознаграждения) иностранному гражданину &ndash; высококвалифицированному специалисту.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.5\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.5.0\">Порядок представления работодателями или заказчиками работ&nbsp; гражданско-правовых договоров на выполнение работ (оказание услуг) с иностранными гражданами (лицами без гражданства), обучающимися в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию, а также о предоставлении им отпусков без сохранения заработной платы продолжительностью более одного календарного месяца в течение года.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.6\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.6.0\">Форма&nbsp;уведомления о заключении трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) с иностранным гражданином (лицом без гражданства), обучающимся в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.7\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.7.0\">Форма&nbsp;уведомления о расторжении трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) с иностранным гражданином (лицом без гражданства), обучающимся в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.8\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.8.0\">Форма&nbsp;уведомления о предоставлении отпуска без сохранения заработной платы продолжительностью более одного календарного месяца в течение года иностранному гражданину (лицу без гражданства), обучающемуся в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.9\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.9.0\">Порядок представления уведомления профессиональной образовательной организацией или образовательной организацией высшего образования о завершении или прекращении обучения иностранного гражданина (лица без гражданства), осуществляющего трудовую деятельность на территории Российской Федерации и обучавшегося (обучающегося) в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию, или о предоставлении данному иностранному гражданину (лицу без гражданства) академического отпуска.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.a\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.a.0\">Форма&nbsp;уведомления о завершении или прекращении обучения иностранного гражданина (лица без гражданства), осуществляющего трудовую деятельность на территории Российской Федерации и обучавшегося (обучающегося) в Российской Федерации по очной форме в профессиональной образовательной организации или образовательной организации высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию, или о предоставлении данному иностранному гражданину (лицу без гражданства) академического отпуска.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.b\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.b.0\">Форму уведомления о заключении трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) с иностранным гражданином или лицом без гражданства.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.c\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.c.0\">Форму уведомления о прекращении (расторжении) трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) с иностранным гражданином или лицом без гражданства.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.d\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.d.0\">Порядок представления работодателями и заказчиками работ (услуг) уведомлений о заключении и прекращении (расторжении) трудовых договоров или гражданско-правовых договоров на выполнение работ (оказание услуг) с иностранными гражданами или лицами без гражданства.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.e\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5.e.0\">Порядок представления работодателями и заказчиками работ (услуг) уведомлений об исполнении обязательств по выплате заработной платы (вознаграждения) высококвалифицированным специалистам.</p>\n</li>\n</ol>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.6\">Также проект Приказа отменяет действие таких Приказов ФМС России, как:&nbsp;</p>\n<ol class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7\">\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.0\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.0.0\">Приказ ФМС России от 28 июня 2010 г. №&nbsp;147 &laquo;О формах и порядке уведомления Федеральной миграционной службы об осуществлении иностранными гражданами трудовой деятельности на территории Российской Федерации&raquo;&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.1\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.1.0\">Приказ ФМС России от 5 сентября 2013 г. №&nbsp;372 &laquo;О внесении изменений в приказ ФМС России от 28 июня 2010 г. № 147&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.2\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.2.0\">Приказ ФМС России от 18 декабря 2013 г. №&nbsp;688 &laquo;О внесении изменений в приказ ФМС России от 28 июня 2010 г. № 147&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.3\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.3.0\">Приказ ФМС России от 12 марта 2014 г. №&nbsp;182 &laquo;О внесении изменения в приложение № 12 к приказу ФМС России от 28 июня 2010 г. №&nbsp;147 &laquo;О формах и порядке уведомления Федеральной миграционной службы об осуществлении иностранными гражданами трудовой деятельности на территории Российской Федерации&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.4\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.4.0\">Приказ ФМС России от 18 августа 2014 г. №&nbsp;489 &laquo;О внесении изменений в приложения № 7, № 8 и № 11 к приказу ФМС России от 28 июня 2010 г. № 147 &laquo;О формах и порядке уведомления Федеральной миграционной службы об осуществлении иностранными гражданами трудовой деятельности на территории Российской Федерации&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.5\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.5.0\">Приказ ФМС России от 8 декабря 2014 г. №&nbsp;640 &laquo;О внесении изменений в приказ ФМС России от 28 июня 2010 г. № 147 &laquo;О формах и порядке уведомления Федеральной миграционной службы об осуществлении иностранными гражданами трудовой деятельности на территории Российской Федерации&raquo;.</p>\n</li>\n<li data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.6\">\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7.6.0\">Приказ ФМС России от 13 марта 2015 г. №&nbsp;149 &laquo;О внесении изменений в приказ ФМС России от 28 июня 2010 г. № 147 &laquo;О формах и порядке уведомления Федеральной миграционной службы об осуществлении иностранными гражданами трудовой деятельности на территории Российской Федерации&raquo;.</p>\n</li>\n</ol>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.8\">&nbsp;</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.9\">Источник:&nbsp;правовая система \"Кодекс\".</p>', NULL, '', '', NULL, NULL, 1),
(15, 0, 1490707711, 'Для граждан Украины произошли изменения в миграционном законодательстве РФ.', '<p>Теперь распространяется норма 90/180.&nbsp;</p>', '<div><span style=\"color: #333333; font-family: Arial, Helvetica, Garuda, sans-serif;\">Для граждан Украины теперь распространяется норма 90/180. Таким образом, срок пребывания&nbsp;</span>на территории государства не должен превышать 90 дней без получения специального разрешения. При этом схема, когда человек выезжает и сразу же возвращается в Россию не работает.</div>\n<p><br />Это значит, что если украинец не оформил на официальном уровне разрешительные документы, то пребывать и работать в России после 90 дней запрещено. В таком случае&nbsp;обязательным является выезд за территорию государства. Повторное посещение возможно через 3 месяца. Оформив патент на работу, прошение о пребывании, статус беженца или политическое убежище, вы сможете оставаться в РФ больше указанного срока. Миграционное законодательство требует обязательного выполнения всех правил. В противном случае будет применена депортация и запрет на въезд на территории Российской Федерации. Исключение составляют жители Донецкой и Луганской областей, для которых действует льготный миграционный режим. Это необходимая мера, которая была принята из-за огромного количества эмигрантов из&nbsp;Украины. В 2017 году в России находится около 500-600 тыс. переселенцев из восточных областей Украины, обладающих статусом беженца. Однако в этом году планируются изменения и постепенная ликвидация предоставления статуса беженцев всем жителям Донецкой и Луганской области. Более того, будут применяться определенные лимиты на проживание в России для этой категории граждан.<br /><br />Источник: http://migrantinform.ru</p>', NULL, '', '', NULL, NULL, 1),
(16, 0, 1490707929, 'Фонд \"ОРИЕНТИР\" окажет бесплатную юридическую помощь в г. Тихвин.', '<p>21 марта 2017 года с 11.00 часов до 17.00 часов в районном Доме культуры по адресу: г. Тихвин, пл. Свободы, д. 1 состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Тихвинском районе Ленинградской области.</p>', '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(17, 0, 1490709387, 'В Санкт-Петербурге откроют государственный Многофункциональный миграционный центр.', '<p>В Петербургском Доме национальностей на заседании Консультационного совета по вопросам реализации миграционной политики Петербурга обсудили возможность создания в городе Государственного многофункционального миграционного центра.</p>', '<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.4\">Решение о его создании уже&nbsp;принято&nbsp;и требует лишь оформления и одобрения на заседании правительства в Смольном, но в Доме национальностей надо было получить формальное одобрение от представителей диаспор, что и было сделано.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5\">&nbsp;Председательствовал вице-губернатор Александр Говорунов, &nbsp;который сказал, что трудовые ресурсы мигрантов нужны городу для развития его экономики, а мигрантам нужна работа, чтобы кормить свои семьи.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7\">&nbsp;Говорунов напомнил о годовщине терактов &nbsp;в Брюсселе и об &laquo;огромных проблемах современной Европы, с которыми она столкнулась из-за неконтролируемой миграции&raquo;. И о том, что &laquo;наша власть в погонах и без погон&raquo;, а также другие институции, включая уполномоченных по правам человека и ребенка, должны быть едины в решении вопросов миграции: &laquo;За спокойствие надо побороться, а как &ndash; подумаем вместе&raquo;.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.9\">&nbsp;Чиновник говорил о том, что из-за отсутствия единого государственного МФЦ сейчас цена &laquo;входного билета&raquo; на петербургский рынок труда для мигранта в полтора-три &nbsp;раза дороже,&nbsp; чем в Москве, а&nbsp; нелегальная миграция &ndash; потенциальное зло. И невыплата зарплаты мигрантам &ndash; зло, потому что не получил мигрант зарплату &ndash; пошел и в парке ограбил бабушку. За своевременную выплату зарплаты головой отвечают работодатели. &laquo;Рабов из людей делать нельзя&raquo;, - возвысил голос вице-губернатор.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.b\">&nbsp;Потом выступал московский гость. Директор Государственного&nbsp; многофункционального миграционного центра Николай Федосеев. Он поделился опытом работы, отметив, что учредителем центра&nbsp; стал Департамент экономической политики и развития Москвы &ndash; это подчеркивает экономические задачи привлечения мигрантов, приезжающих, прежде всего, работать.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.d\">&nbsp;Деятельность &nbsp;самого центра не направлена на получение прибыли, она должна быть безубыточной, в центре сосредоточены все государственные структуры, необходимые для оформления легальной работы мигранта в Москве. Обязательным условием выбора места для строительства центра была его удаленность от жилья, чтобы жители близлежащих домов не жаловались &ndash; в итоге он появился в Новой Москве в 50-ти километрах от центра районов столицы.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.f\">&nbsp;Мигранты приезжают туда с паспортами и миграционными картами, в которых указана цель въезда &ndash; работа, а не туризм. Центр стал единственным местом в столице, где мигрант может законно оформить документы. Занимает весь процесс около двух часов. Медицинское освидетельствование &ndash; полчаса, анализы мигранту забирать не надо &ndash; в течение суток они будут сделаны и доставлены в отдел, где оформляются документы, тест на знание русского языка, истории и законодательства России &ndash; это еще 40 минут.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.h\">&nbsp;Что касается денег, то стоимость патента для мигранта в Москве &nbsp;теперь составляет &nbsp;примерно 17 тыс. рублей, сюда входит 3700 рублей &ndash; собственно услуги центра, 700 рублей &ndash; сдача тестов по языку, истории и законодательству, оформление полиса добровольного медстрахования (ДМС) на 12 месяцев&nbsp; -&ndash; &nbsp;от 3900 до 4100 рублей, 2300 рублей &ndash; оформление медицинских справок и анализов, нотариус &ndash; 400 рублей, 100 рублей &ndash; банковская транзакция, 4200 рублей - НДФЛ на 1 месяц. Центр работает без выходных и праздников (кроме 1 января) &nbsp;по 12 часов в день, без очередей, так как рассчитан с избыточной мощностью на 7 тысяч человек за смену, а сейчас за 12 часов работы через него проходит не больше 1500 человек.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.j\">Московский центр построили в 2015 году, потратив 8 млрд рублей, &nbsp;2 млрд &ndash; это средства на информационную систему центра, ее успели за год вернуть именно за счет НДФЛ.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.l\">Федосеев сообщил, что в бюджет Москвы поступило в минувшем году 11 млрд рублей за счет продажи 450 тысяч патентов мигрантам и 14 млрд &ndash; за счет НДФЛ, что превышает налоговые поступления от нефтяных компаний, зарегистрированных в Москве.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.m\">После сообщения Федосеева за создание такого государственного центра высказались &nbsp;два представителя узбекских общественных организаций. Хотя были и грузины, украинцы и другие, но, видимо, двух мнений от выходцев из Узбекистана было достаточно. Говорунов же потом прокомментировал журналистам: &laquo;Нужно политические решение, вынесем на правительство &ndash; чего тянуть&raquo;. И назвал срок &ndash; примерно через две недели.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.o\">&nbsp;Вице-губернатор не назвал ни возможный участок под строительство такого государственного центра, ни сроки возведения, ни возможную сумму, но подчеркнул &ndash; строить будем вдалеке от жилья. Как вариант, это может быть земля в районе&nbsp;&nbsp;аэропорта Пулково.</p>\n<p class=\"font_9\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.q\">&nbsp;Источник:&nbsp;MR7.RU</p>', NULL, '', '', NULL, NULL, 1),
(18, 0, 1511870706, 'Вводится виза на въезд в Россию для получения разрешения на временное проживание', '', '<p style=\"text-align: justify;\"><img src=\"http://fondorientir.ru/filemanager/i/33349951/n_1.jpg\" alt=\"\" width=\"325\" height=\"244\" />&nbsp;</p>\n<p style=\"text-align: justify;\">&nbsp; Постановлением Правительства от 20 ноября 2017 года №1400 вводится новая разновидность обыкновенной визы &ndash; виза на въезд в Россию в целях&nbsp;получения разрешения на временное проживание в России. Эта виза будет&nbsp;выдаваться иностранному гражданину на срок до четырёх месяцев дипломатическим представительством или консульским учреждением Российской Федерации при наличии решения территориального органа МВД России о выдаче этому иностранному гражданину разрешения на временное проживание в России.</p>\n<p style=\"text-align: justify;\">Документ внесен МИД России, сообщается на сайте Кабмина.<br />&nbsp;даваться иностранному гражданину на срок дФедеральным законом от 7 июня 2017 года №111-ФЗ внесены изменения в Федеральный закон &laquo;О порядке выезда из Российской Федерации и въезда в Российскую Федерацию&raquo;, которыми установлено, что иностранному гражданину или лицу без гражданства (далее &ndash; иностранный гражданин) при наличии решения территориального органа МВД России о выдаче разрешения на временное проживание в России выдаётся обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России. Виза временно проживающего лица выдаётся иностранному гражданину территориальным органом МВД России при выдаче ему разрешения на временное проживание в России на срок действия такого разрешения.</p>\n<p style=\"text-align: justify;\">&nbsp; В&nbsp;связи с этим подписанным постановлением внесены изменения в Положение об установлении формы визы, порядка и условий её оформления и выдачи, продления срока её действия, восстановления её в случае утраты, а также порядка аннулирования визы (утверждено постановлением Правительства от 9 июня 2003 года №335). Категория &laquo;обыкновенная виза&raquo; дополнена новой разновидностью &ndash; обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России.</p>\n<p style=\"text-align: justify;\">&nbsp; Обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России будет выо четырёх месяцев дипломатическим представительством или консульским учреждением Российской Федерации при наличии решения территориального органа МВД России о выдаче этому иностранному гражданину разрешения на временное проживание в России. Такая виза на въезд может быть только однократной.</p>\n<p style=\"text-align: justify;\">&nbsp;Кроме того, соответствующие изменения внесены в положение, касающееся визы временно проживающего лица. Установлено, что такая виза может быть только многократной.</p>\n<p style=\"text-align: justify;\">Источник:&nbsp;<a href=\"http://www.allmedia.ru\">ИА \"Альянс Медиа\"</a></p>', NULL, '', '', NULL, NULL, 1),
(19, 0, 1516701590, 'Квоты закончились. В Петербурге распределили РВП', '', '<p style=\"text-align: justify;\"><img title=\"уфмс300x225.jpg\" src=\"http://fondorientir.ru/filemanager/i/0c725eee/уфмс300x225.jpg\" alt=\"уфмс300x225.jpg\" width=\"300px\" height=\"225px\" /></p>\n<p style=\"text-align: justify;\">В Санкт-Петербурге распределили квоты на выдачу разрешений на временное проживание. Иностранным гражданам, которые собирались оформлять РВП, но по каким-то причинам не успели попасть на прием к указанному сроку, придется ждать следующего года. О расписании распределения квот было объявлено всего лишь за несколько дней до начала приема документов.</p>\n<p style=\"text-align: justify;\">Напомним, что в ноябре 2017-го года стали известны обновленные квоты на РВП. Цифры не сулили ничего хорошего &ndash; количество разрешений на временное проживание на территории России уменьшили примерно на 20 тысяч. Если ранее квоты составляли без малого 111 тысяч, то в нынешнем году было выделено чуть более 90,3 тысячи РВП. Отметим, что ситуация по Санкт-Петербургу и Ленинградской области в сравнении с 2017-м годом не изменилась &ndash; 1500 и 400 квот соответственно &ndash; тогда как в той же Московской области мигранты не досчитаются сразу 3 тысяч РВП &ndash; всего лишь 5 тысяч квот против 8 тысяч в прошлом году.</p>\n<p style=\"text-align: justify;\">Информация о выдаче квот на РВП по Петербургу и области была опубликована в ночь со второго на третье января, а уже пятого января началось непосредственно распределение. Длилось оно три дня подряд, за исключением воскресенья. Восьмого января распределение разрешений на временное проживание завершилось.</p>\n<p style=\"text-align: justify;\">В похожих условиях распределяли квоты и в других регионах. К примеру, в Омске мигранты провели в очередях по несколько дней, но многим так и не представилась возможность подать документы на оформление РВП.</p>\n<p style=\"text-align: justify;\">На фоне ажиотажа иностранных граждан активизировались и мошенники. Так, в Южно-Сахалинске зафиксированы случаи попыток продажи электронных талонов на место в очереди.</p>\n<p style=\"text-align: right;\">Источник: <a href=\"http://ufms.spb.ru/\">ufms.spb.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(20, 0, 1516701928, 'ДМС для мигрантов: правила приобретения обязательной страховки во время пребывания в России', '<h3><span id=\"i-2\">Страховка при въезде в Россию</span></h3>', '<p><img title=\"мед-страховка-для-мигрантов-300x200.jpg\" src=\"http://fondorientir.ru/filemanager/i/62164c35/мед-страховка-для-мигрантов-300x200.jpg\" alt=\"мед-страховка-для-мигрантов-300x200.jpg\" width=\"300px\" height=\"200px\" /></p>\n<p style=\"text-align: justify;\">Отсутствие у мигранта <a href=\"http://ufms.spb.ru/sovety-migracionnogo-advokata/prava-i-obyazannosti-inostrannyx-grazhdan/okazanie-inostrannym-grazhdanam-medicinskoj-pomoshhi/\">медицинской страховки</a> может стать основанием в отказу во въезде в РФ. Об этом гласит ст.26 Федерального закона &laquo;О порядке въезда в РФ и выезда из РФ&raquo;. На практике наличие страхового полиса требуется не только при въезде, но и в течение всего периода пребывания в РФ. За отсутствие страхового полиса добровольного медицинского страхования (ДМС) мигрант может не только схлопотать штраф, но и быть выдворен из РФ.</p>\n<p style=\"text-align: justify;\">Обязанность иметь <strong>действующей страховой полис ДМС</strong> лежит на каждом иностранном гражданине. Исключение составляют лишь те мигранты, которые получили на руки РВП или вид на жительство. Данные категории граждан вправе получать медицинскую помощь бесплатно по системе ОМС.</p>\n<p style=\"text-align: justify;\">Требование иметь страховой полис на время пребывания в РФ обозначено в Правилах оформления виз иностранным гражданам. Одним из обязательных документов, необходимых для оформления визы значится &ndash; полис медицинского страхования. Формально данное требование не касается мигрантов, въезжающих из стран СНГ в безвизовом порядке. Оказание медицинской и амбулаторной помощи безвизовым мигрантам производиться на основании международных соглашений, отсюда следует, что и страховой медицинский полис им не требуется. Однако на практике грамотно обосновать причину отсутствия полиса, сославшись нормы международного права, иностранному гражданину весьма затруднительно. Проверка наличия у мигранта полиса входит в обязанность рядовых сотрудников полиции, которые не настроены вести долгие дискуссии, и при любой возможности спешат наложить на мигранта штраф.</p>\n<p style=\"text-align: justify;\"><strong>Чтобы обезопасить себя от любых претензий со стороны правоохранителей мигрантам следует приобрести самый простой вариант полиса медицинского страхования.</strong></p>\n<p style=\"text-align: justify;\">Стоимость самой дешевой трехмесячной страховки в СПб равняется 600-800 рублей. Ее можно приобрести официально в любой страховой компании: Росгосстрах, ВТБ-страхование, Альфа-страхование и прочих СТРАХОВЫХ организациях. Приобретать полиса у посредников может быть рискованно, поскольку встречались случаи продажи мигрантам поддельных страховок. Использование поддельного документа может обернуться чредой неприятностей, вплоть до уголовного преследования. <strong>Если Вы приобрели страховку у посредника, не поленитесь связаться с представителями страховой компании (позвонить в контакт-центр по телефонам, указанным в полисе) и уточнить действительность Вашей страховки.</strong></p>\n<h3><span id=\"i-3\">Добровольное медицинское страхование трудовых мигрантов</span></h3>\n<p style=\"text-align: justify;\">К страховке для оформления патента на работу закон предъявляет дополнительные требований в части суммы страховой выплаты и объема медицинской помощи, которую трудящийся мигрант может при необходимости получить. Именно поэтому трудовые страховки стоят в разы дороже обычных. <strong>Примерная стоимость страхового медицинского полиса, который подойдет для оформления патента, составляет от 4000 рублей за год.</strong> К приобретению подобных страховок также следует отнестись максимально внимательно, тем более, что данный страховой полис Вы предоставляете в Управление по вопросам миграции при оформлении разрешения на работу. Подробные требования к стразовому полису ДМС для трудящихся мигрантов указаны в ст.13.3 ФЗ &laquo;О правовом положении иностранных граждан&raquo;: <strong>полис добровольного медицинского страхования должен обеспечивать мигранту первичной медико-санитарной помощи и специализированной медицинской помощи в неотложной форме.</strong></p>\n<h3><span id=\"i-4\">Обязательное медицинское страхование (ОМС)</span></h3>\n<p style=\"text-align: justify;\">Полис обязательного медицинского страхования (ОМС) выдается БЕСПЛАТНО мигрантам, получившим в РФ право на проживание. Счастливыми обладателями полиса ОМС станут иностранцы, которым было выдано разрешение на временное проживание или вид на жительство. Имея полис ОМС, мигрант вправе закрепиться за любой районной поликлиникой и получать медпомощь наравне с гражданами России, не платя за медицинские услуги. Неотложная медицинская помощь и лечение в стационаре при наличии полиса ОМС тоже оплачиваются государством. Для получения полиса ОМС необходимо обратиться в страховую компанию, которая работает в системе обязательного медицинского страхования.</p>\n<p style=\"text-align: right;\">Источник: <a href=\"http://ufms.spb.ru/\" target=\"blank_\">ufms.spb.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(21, 0, 1516715424, 'Эксперт: На пороге новой миграционной реальности', '<p><span style=\"font-family: times new roman, times; font-size: medium;\"><em><strong>Рахмон Ульмасов</strong></em></span></p>\n<p><span style=\"font-family: times new roman, times; font-size: medium;\"><em>Профессор Российско-Таджикского (славянского) университета</em></span></p>', '<p><img title=\"9-1-300x286.jpg\" src=\"http://fondorientir.ru/filemanager/i/d4ba6b66/9-1-300x286.jpg\" alt=\"9-1-300x286.jpg\" width=\"300px\" height=\"286px\" /></p>\n<p>&nbsp;</p>\n<p>На протяжении четверть века Таджикистан видел все виды миграций, с разными особенностями и последствиями. Мы живем в непростое, но интересное и ответственное время больших перемен, избежать которых вряд ли кому удастся. Ускоряющиеся процессы глобального потепления, разрастающиеся социальные и политические конфликты, экономические кризисы, миграционный коллапс &mdash; всё это вместе взятое свидетельствует об очень важном: в жизненных процессах, в миграционном поле, в частности, мы находимся на пороге Новой реальности.</p>\n<p>Однако, что мы наблюдаем сегодня? Мы приходим к выводу, что гораздо опаснее взрыв ксенофобии и бытовой ненависти:</p>\n<p>1. Между мигрантами и коренным населением.</p>\n<p>2. Между работающими и живущими на пособие.</p>\n<p>3. Между приверженцами разных религий и культур.</p>\n<p>4. Между людьми, резко отличающимися друг от друга внешне и внутренне.</p>\n<p>Раз за разом мы получаем доказательства неэффективности искусственных мер по регулированию миграционных процессов, поднятию нашего мигранта с колен, но с упорством, достойным лучшего применения, продолжаем биться головой о стену. Может, пора уже осознать, что мы делаем что-то не так? Хотя уже прошло 25 лет:</p>\n<p>&mdash; 25 лет тому назад у Европы не было единой валюты &ndash; евро;</p>\n<p>&mdash; 25 лет назад еще не было крупных революций новой экономики, хотя уже видны были первые сигналы;</p>\n<p>&mdash; 25 лет назад мир был совсем другим;</p>\n<p>&mdash; 25 лет назад не было мобильных телефонов;</p>\n<p>&mdash; 25 лет назад никто не думал, что Великобритания выйдет из ЕС;</p>\n<p>&mdash; за 25 лет Таджикистан стал мощным игроком на рынке труда России;</p>\n<p>&mdash; еще 25 лет назад эти &laquo;гастарбайтеры&raquo; не были &laquo;гастарбайтерами&raquo; или мигрантами &ndash; они были жителями единой страны. Это бывшие соотечественники, поэтому и адаптируются они легче, чем китайцы или вьетнамцы.</p>\n<p>Нам думается, что мы сможем наладить миграционную ситуацию при взаимодействии с передовыми странами. Это взаимодействие должно выстраиваться прагматично, без элементов &laquo;торговли суверенитетом&raquo;. После миграционного коллапса в Европе в сознании европейцев, как и россиян, начинает преобладать здравый смысл.</p>\n<p>Сейчас есть возможность продемонстрировать шаги вперед, которые мы сделали, а также представить, какими могут стать следующие 25 лет.</p>\n<p>В современном мире остается всё меньше тайных механизмов и скрытых пружин. Наш мир подобен компасу, установленному в определенной точке на определенном историческом отрезке. И нам нужно думать также и о том, как мы можем улучшить качество жизни, и мигранта в частности, решить, каким будет мигрант ближайшего будущего. Где и как мы будем жить? Это будет общество Интернета, общество гигабайтов, с ультраширокополосным доступом в сеть? Или что-то ещё?</p>\n<p><strong>О том, как нужно решать миграционные проблемы</strong></p>\n<p>Существуют серьезные проблемы трудовых мигрантов и очевидно, что каждый из нас по-своему видит их причины. Но мы считаем фундаментально важным, чтобы на том этапе, на котором мы сейчас находимся, в связи со сложностью вызовов, которые стоят перед нами, в связи со значением проблем, которые существуют, крайне необходимо, чтобы мы предприняли вместе, имеется в виду вместе с Россией, все усилия и попытались решить эту проблему, глядя в будущее и обязательно помня то, что нас объединяет. Все, что можно сделать вместе, мы с удовольствием должны и обязаны сделать вместе!</p>\n<p>В 21 веке все страны ставят в центр своего внимания человеческий капитал, человеческие ценности, технологические инновации &mdash; иначе не будет ни миграции, ни развития во всем мире.</p>\n<p>&laquo;Мы 45 лет жили при мировом порядке, созданном в Ялте, затем мы 25 лет жили в мире, сформированном на Мальтийском саммите. Болезненный урок украинского кризиса заключается в том, что фундаментальные проблемы европейской безопасности по-прежнему не решены. Запад живет в мире, где время застыло. Миф о его победе в холодной войне можно считать основой существующего мирового порядка. Если мы хотим встать на путь стабильности в Европе, мы должны разрушить старые мифы. Принципы европейской и глобальной безопасности должны быть пересмотрены, ставки сейчас высоки как никогда&raquo;, &mdash;&nbsp;<a href=\"https://ria.ru/world/20150420/1059666430.html\">подытоживает</a>&nbsp;политолог Ричард Саква.</p>\n<p>Можем ли мы вообще повлиять на миграционную ситуацию? Времени для сомнений у нас уже не осталось. В последнее время интерес людей к происходящему в миграционном поле и переменам проявляется всё шире. Всё чаще эти темы звучат в СМИ и на просторах интернета. К сожалению, сведения часто поступают из некомпетентных источников, поэтому противоречивы и не вызывают доверия.</p>\n<p>Когда речь идет о мигрантах, мы всегда должны говорит откровенно, доверительно как о наших достижениях, так и о новых возможностях и, конечно, о проблемах, трудностях, с которыми мы сталкиваемся, о задачах, которые нам пока не удалось решить. И это не лозунг &mdash; это содержание нашей миграционной политики, той работы, которую мы сегодня ведём по созданию условий для мигранта, по поиску новых партнёров и открытию новых рынков труда.</p>\n<p>Популисты, как в Европе, так и в России, после британского референдума получат ветер вдохновения в свои паруса, причем очень вероятно, что они активизируются во всех регионах.</p>\n<p>За эти годы наши мигранты уверенно проходили через полосу трудностей. Если мы оперативно будем принимать меры, то механизмы защиты трудовых мигрантов, безусловно, сработают.</p>\n<p>Надо сосредоточиться на решении системных задач, на повестке долгосрочного развития.</p>\n<p><strong>Первое.&nbsp;</strong>Необходимо сформировать целый класс государственных менеджеров в области миграции, которые смогут работать гибко, по-современному, будут понимать запросы мигранта, его боль и страдания. Одним из важнейших шагов должен стать запуск механизма постоянного совершенствования управленческих кадров, начиная от джамоатов до республиканского уровня. Миграция &ndash; это не только поле деятельности Минтруда, это дело всех министерств и ведомств, парламентов всех уровней. В этой связи на базе НИИ труда необходимо создать центр подготовки руководящих кадров, который сможет стать хорошей площадкой не только для повышения квалификации специалистов, но и для обмена опытом, для выработки новых идей, для налаживания горизонтальных связей между представителями диаспор, регионами России.</p>\n<p><strong>Второе.</strong>&nbsp;Считаю целесообразным создать в каждом районе, городе, области специальные штабы &ndash; проектные офисы, которые станут своего рода местом для обращения мигрантов, обеспечат всем необходимым информационно-пропагандистскими материалами, консультациями; организовать курсы русского языка, внедрить самые эффективные механизмы адаптации мигрантов на новом месте. Мигрант должен получать здесь необходимую финансовую, юридическую, методическую поддержку, в том числе помощь в получении микрокредитов.</p>\n<p><strong>Третье.</strong>&nbsp;Нужно провести инвентаризацию уже существующих законов, программ, концепций, и проанализировать, как они работают. Проблема миграции в Таджикистане кроется в нас самих, поскольку мы сейчас пытаемся найти равновесное положение в миграционном пространстве. Поворот отношений к мигранту означает разворот к самому себе, решать проблемы миграции нужно прежде всего с Россией. Сегодня самая главная наша задача &mdash; занять свою нишу в сложном, быстро меняющемся мире.</p>\n<p>Миграция, несмотря на отдельные ее негативные особенности, внесла большой вклад в российскую и таджикскую экономики. Сейчас Россия вполне самодостаточна в своих нынешних границах, но она нуждается в трудовых ресурсах.</p>\n<p>25 лет миграции показали, что таджикский мигрант способен к длительному монотонному труду, но ему очень не хватает креативности. Если сравнить наших мигрантов с другими мигрантами, например, армянами, азербайджанцами, то отличительные черты наших мигрантов являются продуктом длительного исторического развития, формировались постепенно и стали результатом общественного компромисса, выстраданного в ходе многовековой ожесточенной борьбы. К этим незыблемым теперь ценностям таджикские мигранты пришли не сразу, но именно эти ценности сформировали менталитет наших мигрантов, и именно эти ценности стали составной частью преобладающей универсальной русской культуры.</p>\n<p>Очевидно, что любая неприятная история с мигрантами оказывает воздействие на таджикский имидж. Вследствие этого постоянно необходимо углубленно все анализировать, оценивать. Нельзя валить все проблемы мигрантов только на глобальные просчеты, за которые обычно никого не наказывают и после которых выводов не делают.</p>\n<p>Вина наших мигрантов тоже очевидна, поскольку для таджикского мигранта цель (деньги) важнее процесса (постоянного обучения, прогресса). Это говорит об отсутствии стратегического мышления. Проблема гораздо серьезнее, чем, кажется на первый взгляд, но пусть этой проблемой занимаются профессионалы. Надеемся, что высокий уровень профессионализма позволит им спокойно и объективно во всем разобраться.</p>\n<p>Проблема миграции в Таджикистане, как и на просторах многих других стран, &ndash; удивительный и по-прежнему слабо изученный феномен вроде черной дыры или Бермудского треугольника. В настоящее время по итогам переписи населения России 2010 года из 194 наций и народностей, проживающих в РФ, таджики занимают 29 место, и рост составил 67,73%. Сейчас таджиков в России больше, чем калмыков, евреев, черкесов, корейцев и др. Думается, что после последней переписи (между 2010 и 2016 гг.) количество таджиков увеличилось в разы.</p>\n<p>В Республике Таджикистан, как и в других странах СНГ, в настоящее время наиболее актуальной является проблема трудовой миграции и занятости населения. Таджикистан традиционно считается трудоизбыточной страной. Это обусловлено высоким уровнем рождаемости. С 2000 по 2013 гг. население Республики Таджикистан увеличилось на 2034,1 тыс. человек, т.е. с 6127,0 до 8161,1 тыс. человек.</p>\n<p>В РФ демографическое положение очень неблагополучное. Около 80% естественной убыли населения России компенсирует приток мигрантов. По&nbsp;<a href=\"http://www.kroupnov.ru/5/232_1.shtml\">словам</a>&nbsp;одного из ведущих демографов России Л.Л. Рыбаковского, &laquo;сложившийся режим воспроизводства населения&raquo; сочетает в себе &laquo;европейскую рождаемость и африканскую смертность&raquo;.</p>\n<p>Мигрант &ndash; не просто профессия, а образ жизни, который не все люди понимают и принимают. Сегодня в этот процесс вовлечены миллионы людей во всем мире. В соответствии с прогнозом, сделанным Международным валютным фондом, в период с 2015 года по 2017 год в Европу прибудет порядка четырех миллионов граждан из-за пределов ЕС, то есть в год ожидается прибытие порядка 1,33 миллиона человек. Как отмечал в своем послании по случаю Дня мигранта экс-генсек ООН Пан Ги Мун, 2015 год войдет в историю как год человеческих страданий и трагедий мигрантов. &laquo;В течение последних 12 месяцев более 5000 женщин, мужчин и детей погибли, пытаясь найти защиту и лучшие условия для жизни. Еще десятки тысяч стали жертвами эксплуатации и жестокого обращения со стороны тех, кто занимается торговлей людьми. А миллионы людей превратились в &laquo;козлов отпущения&raquo;, они стали объектом политики ксенофобии и алармистской риторики&raquo;, &mdash;&nbsp;<a href=\"http://www.fergananews.com/news.php?id=24274\">подчеркивается</a>&nbsp;в послании.</p>\n<p>В России 6,5 миллионов смешанных браков, в том числе с мигрантами, ежегодно 12 тыс. таджиков создают новую семью в России, множество примеров счастливых смешанных семей, но нам их не показывают, а показывают только угрозы, якобы исходящие от приезжих. Нельзя не согласиться с мнением Президента Фонда &laquo;Миграция XXI век&raquo; Вячеслава Поставнина. &laquo;Мы самого главного в миграции и не видим &ndash; это два простейших принципа, на которых должна строиться любая миграционная политика: экономические интересы &ndash; миграцию нужно повернуть так, чтобы она служила развитию общества, и гуманизм (мигранты &ndash; люди, и это нужно понимать). А у нас мигранты практически находятся на положении&nbsp;<a href=\"http://migrant.ru/my-i-oni-o-migrantax-yazyke-vrazhdy-i-agressivnoj-mentalnosti/\">полурабов</a>&raquo;.</p>\n<h5><strong>Чем отличается современная таджикская миграция от миграции в начале 2000 года?</strong></h5>\n<p><strong>Во-первых</strong>, миграция из Таджикистана стала сельской. И многие проблемы, которые мы наблюдаем во взаимоотношениях коренных россиян с мигрантами, порождены проблемами между городским и сельским населением.</p>\n<p><strong>Во-вторых</strong>, это менее образованные мигранты, чем прежде. Более образованные уже давно выехали в Россию и другие страны.</p>\n<p><strong>В-третьих</strong>, это люди, которые хуже знают русский язык. Надо признать, что даже при СССР в сельских районах Таджикистана русским владели хуже, чем в городах. А сейчас тем более. В результате &ndash; причины многих трений, возникающих между россиянами и приезжими, основаны именно на этих факторах.</p>\n<h5><strong>Как быть в такой ситуации?</strong></h5>\n<p><strong>Первое.</strong>&nbsp;Для Таджикистана и для других республик региона как воздух нужны сильные и авторитетные эксперты-ньюсмейкеры. Необходимо провести масштабные социологические исследования, привлечь авторитетных ученых, которые способны формировать благоприятное общественное мнение как в Таджикистане, так и за его пределами. Ничто в жизни так не раздражает, как бытовые мелочи и потуги непрофессионалов.</p>\n<p><strong>Второе.</strong>&nbsp;Государство должно инициировать образование журналистов, занимающихся проблемой миграции. Большой проблемой является отсутствие компетентных журналистов-экспертов, которые могут освещать проблемы миграции. Журналистика, освещающая вопросы миграции, связана с политикой и идеологией, так как роль СМИ не только в информировании, но и в формировании общественного мнения.</p>\n<p><strong>Третье.</strong>&nbsp;Более 300 тыс. представителей русскоязычного населения выехали из Таджикистана в РФ. Время идет, и уходит из жизни поколение, помнящее положительный опыт межэтнического взаимодействия в Таджикистане в то время, как молодёжь такого опыта не имеет. Пока живо и здорово старшее поколение, надо использовать его опыт, в противном случае, завтра может сложиться конфликтная ситуация.</p>\n<p><strong>Четвертое.&nbsp;</strong>Большинство конфликтов с коренным населением возникает именно в местах замкнутого проживания мигрантов в России. Применяя опыт Германии, мы можем разработать общероссийскую программу адаптации мигрантов.</p>\n<p>Проблема миграции выходит на первый план среди приоритетных вопросов мировой политики вместе с геополитикой и геоэкономикой, т.е.&nbsp;<strong>три &laquo;Г&raquo;: Геополитика, Геоэкономика и Геомиграция.</strong></p>\n<h5><strong>Предложения на пороге новой миграционной реальности</strong></h5>\n<p><strong>Первое.</strong>&nbsp;Необходимо предложить российской стороне провести амнистию по административным правонарушениям мигрантов.</p>\n<p>Амнистия &ndash; это всегда хорошо. Сегодня очень много выписано штрафов, не всегда разумных из-за несоответствия уровня ущерба, уровню наказания, который предполагается. Поэтому имеет смысл поговорить об &laquo;административной&raquo; реформе для трудовых мигрантов. Административная амнистия &laquo;больше, чем бизнес&raquo;, она призвана стать мерой, которая, предположительно, улучшит &laquo;в целом миграционную среду&raquo;.</p>\n<p><strong>Второе.&nbsp;</strong>Для выхода из миграционного кризиса надо стремиться не к тому, как было раньше, в &laquo;не санкционную&raquo; эпоху, а к новым ориентирам и новому месту на рынке труда. Внедрять лучший мировой опыт, сотрудничать и вместе идти вперёд.</p>\n<p><strong>Третье.</strong>&nbsp;Таджикистану и другим республикам ЦА нужно сосредотачиваться не на том, как было, а думать о том, как будет, как должно быть. Россияне (и это почти общественный консенсус) вообще очень хотят, чтобы был порядок в миграционном поле.</p>\n<p><strong>Четвертое.</strong>&nbsp;Необходимо в корне менять кадровую политику в миграционной сфере. На смену &laquo;своим&raquo; должны прийти люди честные, грамотные и профессионально подготовленные, готовые к бескорыстному служению государству, имеющие соответствующий опыт работы.</p>\n<p>В мире не может быть ничего вечного и неизменного. Особенно сейчас, когда мир явно, но пока непонятно, в какую сторону, снова стремительно меняется. Впрочем, перед Таджикистаном стоит не просто та же задача, она троекратно сложнее.</p>\n<p style=\"text-align: right;\">Источник: <a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></p>', NULL, '', '', NULL, NULL, 1),
(22, 0, 1516715565, ' Судебная практика: Что будет если не уведомить МВД за 3 рабочих дня?', '', '<p><span style=\"font-family: Arial;\"><img title=\"12-1.jpg\" src=\"http://fondorientir.ru/filemanager/i/a64525b0/12-1.jpg\" alt=\"12-1.jpg\" width=\"371\" height=\"237\" /></span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы всегда призываем своих партнеров легально трудоустраивать иностранцев в штат компании и следить за их миграционными документами, ведь незаконное привлечение иностранцев к трудовой деятельности является административным правонарушением, за которое работодатель отвечает в суде.</span></p>\n<p><span style=\"font-family: Arial;\">Одним из таких судебных дел является постановление суда г. Рязани&nbsp;<strong>№ 5-518/2017</strong>&nbsp;от 28 июля 2017 г. 29 июня 2017 года была проведена проверка ГБУ РО &laquo;N&raquo;, в ходе которой было выявлено, что работодатель нанял на работу гражданку Украины в качестве медицинской сестры и&nbsp;<strong>не уведомил&nbsp;</strong>об этом МВД в течение 3 дней.</span></p>\n<p><span style=\"font-family: Arial;\">Трудовой договор ГБУ РО &laquo;N&raquo; с гражданкой Украины был заключен 01.08.2016 года. Однако в положенный срок ответчик не предоставил уведомление в МВД.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ответчик признал свою вину в суде, но добавил, что он уведомил МВД о трудоустройстве иностранного гражданина позже, а именно 25.04.2017 года.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Принимая во внимание все смягчающие обстоятельства, судья выдвинул приговор на основании статьи&nbsp;<strong>2.9 КоАП РФ</strong>, которая дает возможность освободить ответчика от ответственности при малозначительности правонарушения.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Так как ответчик подал уведомление и все-таки выполнил свою обязанность до проведения проверки госорганами, суд решил, что ответчика можно&nbsp;<strong>освободить</strong>&nbsp;от ответственности, предусмотренной&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/docs/koap18-15/\"><span style=\"color: #007236;\"><strong>ст. 18.15 КоАП</strong></span></a><span style=\"font-family: Arial;\">, а именно от штрафа в размере&nbsp;<strong>от 400 до 800 тыс. рублей</strong>&nbsp;или приостановления деятельности его организации на срок&nbsp;<strong>от 14 до 90 суток</strong>, ограничившись устным предупреждением.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Тем не менее, данной статьей предусмотрено наказание не только за отсутствие&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/notification/\"><span style=\"color: #007236;\"><strong>уведомления в МВД</strong></span></a><span style=\"font-family: Arial;\">, но и за нарушение порядка (неподача в течение 3 рабочих дней) и формы (старая форма, ошибки в заполнении) уведомления. И такой положительный исход дела в сторону ответчика бывает крайне редко, особенно в Москве и Санкт-Петербурге!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому не забывайте&nbsp;<strong>в течение 3 рабочих дней</strong><span style=\"color: #007236;\"><strong>&nbsp;</strong></span></span><a href=\"http://all-migration.ru/services/addservices/notification/\"><span style=\"color: #007236;\"><strong>уведомлять МВД</strong></span></a><span style=\"font-family: Arial;\">&nbsp;о трудоустройстве или увольнении иностранных сотрудников. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Источник: </span><a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></p>', NULL, '', '', NULL, NULL, 1),
(23, 0, 1516716170, 'Опоздал с оформлением патента? Выход есть!', '', '<p><img title=\"20-1-300x224.jpg\" src=\"http://fondorientir.ru/filemanager/i/78f74f98/20-1-300x224.jpg\" alt=\"20-1-300x224.jpg\" width=\"266\" height=\"199\" /></p>\n<p><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #00a650; font-family: Arial;\"><strong>Трудовой патент</strong></span></a><span style=\"font-family: Arial;\">&nbsp;&ndash; документ, разрешающий иностранному гражданину легально работать на территории РФ. С момента въезда у иностранного гражданина есть 30 дней на его оформление, а это важный процесс для дальнейшего пребывания и проживания мигранта в России.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">За это время иностранный гражданин может столкнуться с рядом трудностей в оформлении патента (проблемы с подготовкой документов, прохождением медкомиссии, сдачей тестирования), из-за которых он может не успеть подать документы на патент в течение 30 дней.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что делать в таком случае?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Есть несколько вариантов решения проблемы:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Первый &ndash; выехать на любую границу</strong>&nbsp;(ближайшие &ndash; Финляндия, Украина, Казахстан), а затем снова вернуться на территорию России. Это позволит оформить новую миграционную карту, благодаря чему у мигранта снова появятся 30 дней на оформление трудового патента. Нужно помнить, что если за 90 отведенных миграционным учетом суток гражданин не успеет оформить патент, то по закону &laquo;90 из 180&raquo;, он должен будет покинуть территорию РФ на 3 месяца и только потом сможем вернуться.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Второй вариант предполагает оплату штрафа в размере от 10 000 до 15 000 рублей.</strong>&nbsp;Но такой вариант подразумевает под собой административное правонарушение. А получение нескольких административных правонарушений может привести к серьезным последствиям, таким, как запрет на въезд на территорию РФ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому мы рекомендуем&nbsp;<strong>пользоваться первым вариантом</strong>, т.к. он несет за собой меньшие затраты. К тому же он позволяет избежать административного правонарушения.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Источник: </span><a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></p>', NULL, '', '', NULL, NULL, 1),
(24, 0, 1516716594, 'АНОНС. Петербург: Для работы с мигрантами создадут центр компетенций', '', '<p><img title=\"21-1.jpg\" src=\"http://fondorientir.ru/filemanager/i/e0285b28/21-1.jpg\" alt=\"21-1.jpg\" width=\"249\" height=\"156\" /></p>\n<p>&nbsp;</p>\n<p style=\"text-align: justify;\">Число мигрантов в России, в том числе в Петербурге, ежегодно растет. Как адаптировать их к жизни в нашей стране? Группа экспертов СПбГУ и Высшей школы социальной работы Женевы начала работу над проектом, который поможет ответить на этот и другие вопросы.</p>\n<p style=\"text-align: justify;\">На сегодня в России число мигрантов достигло 12 млн, и эта цифра со временем не уменьшается. Ученые напоминают, что миграция &ndash; неизбежный процесс.</p>\n<p style=\"text-align: justify;\">Специалисты СПбГУ совместно с ведущими исследователями Высшей школы социальной работы Женевы запустили уникальный трехлетний проект, направленный на разработку механизмов социальной работы с мигрантами.</p>\n<p style=\"text-align: justify;\">В рабочую группу вошли 33 эксперта СПбГУ из различных областей: социология, социальная работа, международные отношения, право, математика, журналистика, межкультурная коммуникация. Главная цель проекта &ndash; определить основные принципы социальной работы с мигрантами, учитывая традиции, сложившиеся по отношению к ним в нашей стране.</p>\n<p style=\"text-align: justify;\">Специалисты также будут использовать опыт других стран в этой области, предполагается изучение механизмов, регулирующих въездную миграцию в страны Европы, Азии и Африки. Результаты исследования должны способствовать успешной адаптации мигрантов, а также их принятию обществом.</p>\n<p style=\"text-align: justify;\">По словам участников проекта, перед ними стоит непростая задача &ndash; понять, как правильно организовать работу с мигрантами, как прививать им культуру и традиции страны, обучать языку. Такая адаптация позволит создать комфортную обстановку как для местных жителей, так и для приезжих.</p>\n<p style=\"text-align: justify;\">&ldquo;Ситуация в области миграции является вызовом для всего человечества. Взвешенный, честный и разумный международный ответ на него позволит укрепить мир, добиться благополучия, преодолеть нищету и кризисы&rdquo;, &ndash; отметил руководитель проекта доцент СПбГУ Александр Кутейников.</p>\n<p style=\"text-align: justify;\">Одним из самых крупных результатов проекта должен стать многофункциональный центр компетенций, который объединит в себе ресурсы в сфере работы с мигрантами. Он будет располагать самыми успешными мировыми практиками и различными методиками социальной работы с мигрантами, информацией о том, как контролировать миграционные потоки.</p>\n<p style=\"text-align: justify;\">Результаты работы планируется распространять среди государственных и местных органов власти, учреждений и организаций, связанных с социальной работой с мигрантами.</p>\n<p style=\"text-align: justify;\">В качестве эксперта в проекте принимает участие председатель Комитета по труду и занятости населения СПб Дмитрий Чернейко. &ldquo;В рамках проекта хотелось бы увидеть методологически значимые исследования, которые помогут оценить реальные масштабы и необходимость трудовой миграции, понять, кто такой сегодняшний трудовой мигрант, выстроить формы работы, которые максимально учитывают интересы всех сторон процесса&rdquo;, &ndash; отметил он.</p>\n<p style=\"text-align: justify;\">Проект поддерживает Министерство образования и науки РФ. </p>\n<p style=\"text-align: right;\">Источник: <a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></p>', NULL, '', '', NULL, NULL, 1),
(25, 0, 1517838180, 'Мигрантов приглашают на вебинар «Работа в России на основании патента»', '<p><span style=\"font-size: small;\"><strong>Иностранные граждане, работающие в России, могут принять участие в вебинаре на тему &laquo;Работа в России на основании патента&raquo;, организованном порталом Migranto.ru, который состоится 10 февраля 11:00 &mdash; 12:00 по московскому времени. Участие бесплатное</strong></span></p>', '<p><span style=\"font-size: small;\">На сайте организаторов отмечается, что на вебинаре трудовые мигранты узнают об общем порядке получения трудового патента в России. Какие документы для этого необходимы и какие нюансы нужно учесть, если вы решили работать в Российской Федерации. ВОПРОСЫ ДЛЯ РАССМОТРЕНИЯ: &mdash; Порядок оформления патента на работу в Российской Федерации; &mdash; Специальность в патенте. На что она влияет? &mdash; Можно ли иметь регистрацию в одном городе или субъекте, а работать в другом? &mdash; Что такое &laquo;закрытые территории&raquo;? Какое значение они имеют для иностранных граждан? &mdash; Как переоформить патент еще на год? &mdash; Что делать, если патент и другие документы были утеряны или украдены? &mdash; Нужно ли вносить изменения в патент, если иностранный гражданин поменял паспорт или фамилию? &mdash; Что делать, если иностранный гражданин решил переехать жить и работать из одного субъекта РФ в другой? Зарегистрироваться на вебинар можно через портал migranto.ru по этой ссылке <a href=\"https://www.migranto.ru/rabota-v-rf-na-osnovanii-patenta\"> https://www.migranto.ru/rabota-v-rf-na-osnovanii-patenta</a></span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: small;\">Источник: </span><span style=\"font-size: small;\"><a href=\"http://https://migrant.news.tj/post/2332\" target=\"blank_\">Migrant.news.tj</a></span></p>\n<p><span style=\"font-size: small;\">&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(26, 0, 1519045682, 'Третейский суд вместо арбитражного', '', '<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: small;\"><img title=\"Treteyskiy_sud.jpg\" src=\"http://fondorientir.ru/filemanager/i/5be4f034/Treteyskiy_sud.jpg\" alt=\"Treteyskiy_sud.jpg\" width=\"512px\" height=\"462px\" /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: small;\">&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">За последнее время в приемную Фонда &laquo;ОРИЕНТИР&raquo; трудовые мигранты очень часто обращаются по вопросам невыплаты заработной платы, сообщил юрист Фонда &laquo;ОРИЕНТИР&raquo;. В силу низкой юридической грамотности у большинства мигрантов возникают проблемы с работодателем. Как сообщают сами трудовые мигранты, они договариваются с заказчиком в устном виде, что в последствии трудовым мигрантам становится практически невозможным доказать факт выполнения работ на объекте заказчика.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">У юристов Фонда &laquo;ОРИЕНТИР&raquo; уже был опыт по возврату денежных средств трудовым мигрантам. Так например, в 2016 году юристы фонда добились выплат по заработной плате на сумму свыше 5 млн. рублей. Однако этот процесс очень долгий и трудоемкий. Судебный процесс может длиться годами, проводится несколько судебных заседаний, очень много бюрократических моментов. В связи с этим, право на решение вопросов такого характера можно передать третейскому суду, чтобы сэкономить время как заявителя, так и ответчика, а также самого арбитражного судьи.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">На данный момент Фонд поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; реализует проект &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; при поддержке &laquo;Фонда-оператора президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(27, 0, 1522746320, 'Жалобы трудовых мигрантов на Миграционный Центр', '', '<p>В рамках реализации проекта &laquo;&raquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo;, поддерживаемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo;, в приемную Фонда &laquo;ОРИЕНТИР&raquo; ежедневно обращаются трудовые мигранты за разъяснениями и консультациями по миграционному законодательству.</p>\n<p>При этом юристы Фонда отмечают, что со стороны мигрантов не редко звучат жалобы на сотрудников миграционного центра. Жалобы поступают по ряду вопросов: некомпетентность сотрудников, грубое отношение сотрудников службы охраны, несоблюдение правил, несвоевременная выдача документов, выдача документов с ошибками, из-за чего мигрантам приходится заново оформлять документы, что требует дополнительных затрат по времени и денежных средств. В то время как срок постановки на миграционный учет трудовых мигрантов ограничен. Также одной из существенных проблем является отсутствие контроля за движением очереди, из-за чего мигрантам порой приходится ночевать прямо на улице, чтобы на следующий день успеть получить документы.</p>\n<p>Для решения данных проблем необходимо провести опрос среди мигрантов и выявить основные проблемы, с которыми они сталкиваются при оформлении документов в Миграционном Центре, и направить усилия для их решения, чтобы мигранты, намеревающиеся вести трудовую деятельность на территории Российской Федерации, могли в срок и без проблем оформить необходимые документы и не чувствовали себя чужими, подытожил юрист Фонда &laquo;ОРИЕНТИР&raquo;.</p>', NULL, '', '', NULL, NULL, 1),
(28, 0, 1524484240, 'Туристы вместо мигрантов', '<p>Полицейские пытаются отсудить у Смольного здание на наб. реки Фонтанки, 78, переданное чиновниками структурам совладельца сети &laquo;585&raquo; для реконструкции под отель</p>', '<p>Арбитражный суд Петербурга и Ленобласти отказал ГУ МВД по городу и области в иске к комитету имущественных отношений (КИО). Полицейские просили обязать чиновников передать в безвозмездное пользование ГУ МВД часть здания на наб. реки Фонтанки. 78, площадью 2 тыс. м<sup>2</sup> (общая площадь здания -7 тыс. м<sup>2</sup>). Силовое ведомство хочет разместить там свое Управление по вопросам миграции (УВМ) - подразделение, созданное на базе упраздненной Федеральной миграционной службы (ФМС) в 2017 году. Требование основано на том, что эти помещения уже были в пользовании УВМ в 2005-2007 годах. Но суд в иске отказал, указав, что в отношении здания имеется действующий инвестдоговор между КИО и ООО &laquo;СВ-Консалт&raquo; на реконструкцию. Полиция подала апелляционную жалобу. В ГУ МВД пока не ответили на запрос &laquo;ДП&raquo;.</p>\n<p><strong>&nbsp;Золотой инвестор</strong></p>\n<p><strong>&nbsp;</strong>Как выяснил суд, Смольный передал в пользование УВМ спорные помещения в 2005 году. Но в 2008-м власти решили передать все здание на реконструкцию под отель. Инвестором стало ООО &laquo;СВ-консалт&raquo; Оно обещало вложить в проект 500 млн рублей и к 2011 году устроить там пятизвездочный отель на 80 номеров. Главным условием для начала работ было освобождение здания от занимавших его организаций: помимо УФМС там располагалось издательство &laquo;Детгиз&raquo;, а также фонд &laquo;Возрождение ислама и другие. На тот момент компания имела связь с совладельцем сети .585. Антоном Петровым, но впоследствии он заверил &laquo;ДП&raquo;, что вышел из этого проекта. С новыми владельцами компании (по данным СПАРК, это Олег Орлов и другие) связаться пока не удалось.</p>\n<p>&nbsp;<strong>Не виноватый он</strong></p>\n<p><strong>&nbsp;</strong>Ни в 2011 году, ни в 2016-м &laquo;СВ-Консалт&raquo; не только не завершил реконструкцию, но даже не приступил к ней: занимавшие здание госорганы не спешили его покидать. Лишь к 2016 году удалось переселить УФМС в здание на ул. Смолячкова, 15.</p>\n<p>В 2016 году истек срок инвестдоговора, и Смольный прислал инвестору извещение о его расторжении. &laquo;СВ-Консалт&raquo; обжаловал действия КИО в суде и выиграл: в декабре 2017 года кассационный суд признал незаконность расторжения в связи с тем, что чиновники сами не исполнили своих обязательств, не выселив всех пользователей здания, и инвестор в срыве сроков не виноват. А на основе этого вывода (что договор действует) был отклонен и иск ГУ МВД. &laquo;Договор с компанией действующий. Со стороны инвестора нарушений нет. Оснований для расторжений инвестдоговора тоже нет. Передача объекта или его части третьему лицу невозможна. Для передачи его в федеральную собственность оснований нет, а у ГУ МВД нет прав и полномочий выступать с такими требованиями&raquo; - пояснили в пресс-службе КИО.</p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\">ИСТОЧНИК: Деловой Петербург</p>', NULL, '', '', NULL, NULL, 1),
(29, 0, 1535615401, 'Последствия нелегальной миграции: запрет на въезд или колония?', '', '<p><span style=\"font-family: Arial;\">Сколько не предупреждай иностранцев, насколько важно уже с момента въезда в РФ оформлять свои разрешительные документы легально, в любом случае, найдутся те, кто решит обойти закон. Многие иностранцы наивно полагают, что их соотечественники лучше разбираются в законах РФ и смогут помочь им в решении миграционных проблем.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Так, двум выходцам из Узбекистана и Казахстана суд постановил два и три года тюремного заключения. Они нелегально провозили своих клиентов в Челябинск через границу с Казахстаном, и давали свои указания на счет того, как не попасть под прицел миграционной службы, сообщает пресс-служба Генпрокуратуры России.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Благодаря такой схеме, злоумышленникам удалось<strong>&nbsp;нелегально провезти на территорию РФ девять иностранцев</strong>&nbsp;и заработать порядка 300 тысяч рублей.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Самое интересное, что у каждого из девяти человек был&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Advokat/\"><span style=\"color: #005824; font-family: Arial;\"><strong>запрет на въезд</strong></span></a><span style=\"font-family: Arial;\">! Скорее всего иностранцы, пересекающие границу, не знали как&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Advokat/\"><span style=\"color: #005824; font-family: Arial;\"><strong>снять запрет на въезд</strong></span></a><span style=\"font-family: Arial;\">&nbsp;законно, поэтому обратились за помощью к злоумышленникам. Тем самым, только усугубили ситуацию, ведь им был увеличен срок запрета до максимального (10 лет)!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">На данный момент возможно&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Advokat/\"><span style=\"color: #005824; font-family: Arial;\"><strong>снять запрет на въезд</strong></span></a><span style=\"font-family: Arial;\">&nbsp;абсолютно легально, тем более, что существует ряд смягчающих обстоятельств! При том, что сделать это можно как через МВД, так и через суд.&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(30, 0, 1535708117, 'Два гражданства и двойное гражданство - в чем разница?', '', '<p><span style=\"font-family: Arial;\"><strong>Два гражданства</strong>&nbsp;- согласно ст.6 № 65-ФЗ означает наличие у россиянина&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><span style=\"color: #005824; font-family: Arial;\"><strong>гражданства</strong></span></a><span style=\"font-family: Arial;\">&nbsp;другого государства. Это не противоречит закону, но в пределах России документы, подтверждающие факт гражданства другой страны, не признаются. Это значит, что для нашей страны Вы являетесь, в любом случае, гражданином РФ. В других же странах Вы сможете пользоваться любым из двух паспортов на Ваше усмотрение. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">То есть если Вы, являясь россиянином, оформляете гражданство иностранного государства и при этом Вам не нужно отказываться от российского &ndash; то вы автоматически приобретаете второе гражданство.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Что же касается понятия&nbsp;<strong>&laquo;двойное гражданство&raquo;</strong>&nbsp;- это значит, что иностранец является гражданином двух стран, но в этом случае все немного сложнее. Еще недавно было две страны, с которыми у России был урегулирован вопрос о двойном гражданстве - это Таджикистан и Туркмения. С каждой из сторон был подписан договор, предоставляющий право жителям данных государств вступить в гражданство другой страны, не утрачивая нынешнего гражданства. Но с 2015 года соглашение с Туркменией утратило свою силу. Следовательно, лицами с двойным гражданством в РФ официально могут считаться только приезжие из Таджикистана.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35070/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(31, 0, 1535708233, 'Оформить патент без ИНН - возможно ли это?', '', '<p><span style=\"font-family: Arial;\">В последнее время в связи с Чемпионатом Мира по футболу 2018 мы очень часто напоминали владельцам&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><strong><span style=\"color: #005824; font-family: Arial;\">трудовых патентов</span></strong></a><span style=\"font-family: Arial;\">&nbsp;о важности миграционного учета. Но есть еще один документ, без которого пребывать на основании патента в РФ невозможно. Речь пойдет об&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/inn/\"><span style=\"color: #005824; font-family: Arial;\"><strong>идентификационном номере налогоплательщика</strong></span></a><span style=\"font-family: Arial;\">, или ИНН.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">ИНН - это номер, по которому компания-работодатель или сам иностранец оплачивает за себя налог на доходы в России. Сейчас этот номер присваивается один раз и на всю жизнь. Но следует помнить, что номер привязан к паспорту иностранного гражданина. И если он был оформлен по внутреннему паспорту, то при въезде в Россию по заграничному паспорту, нужно оформлять себе новый номер или вносить изменения в этот.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для иностранцев, работающих по патенту, очень важно, знать свой номер ИНН, ведь именно по нему должны будут вноситься все авансовые платежи по патенту на работу.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Проверить наличие ИНН можно по адресу&nbsp;<a href=\"https://service.nalog.ru/inn.do\"><strong><span style=\"color: #005824;\">https://service.nalog.ru/inn.do</span></strong></a>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ранее ИНН надо было обязательно оформить еще до получения патента, т.к. первый авансовый платеж должен был быть уже с указанным номером.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но сейчас все немного проще. Если Вы оформляете трудовой патент через нашу компанию, то заниматься получением ИНН Вы можете одновременно с оформлением патента или уже после его выдачи, так как мы за Вас вносим первый платеж. Но помните, что ИНН обязательно нужно получить до оплаты второго авансового платежа за патент! Иначе Ваш патент может быть аннулирован. Подробнее об аннулировании патента из-за неправильно оплаченных чеков, читайте&nbsp;<a href=\"http://all-migration.ru/news/34873/\"><span style=\"color: #005824;\"><strong>здесь</strong></span></a>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Напоминаем, что оформить патент иностранцу после въезда в РФ&nbsp;<strong>необходимо за 30 календарных дней</strong>. ИНН же оформляется&nbsp;<strong>около 10 рабочих дней</strong>. Поэтому, чтобы не пропустить допустимые для оформления патента сроки, советуем сначала заняться его получением. А пока он будет изготавливаться, спокойно оформить себе ИНН.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35097/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(32, 0, 1535708324, 'Миграционные новости: требования к постановке на миграционный учет изменились!', '', '<p><span style=\"font-family: Arial;\">Последнее время миграционное законодательство меняется очень стремительно.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Совсем недавно мы информировали вас о новых сроках постановки на миграционный учет в связи с проведением ЧМ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Почитать об этом вы можете&nbsp;</span><a href=\"http://all-migration.ru/news/34847/\" target=\"_blank\"><span style=\"color: #007236; font-family: Arial;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В конце июня этого года был принят&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/info/zakon/fz163/\"><span style=\"color: #007236; font-family: Arial;\"><strong>Федеральный Закон № 163&laquo;О внесении изменений в ФЗ &laquo; О миграционном учете иностранцев&raquo;.</strong></span></a></p>\n<p><span style=\"font-family: Arial;\">Изменения, как вы поняли, вновь касаются миграционного учета и затронут мигрантов и работодателей, которые оформляют сотрудникам миграционный учет по месту работы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Напомним, что ранее иностранец мог оформить себе&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236; font-family: Arial;\"><strong>регистрацию</strong></span></a><span style=\"font-family: Arial;\">&nbsp;либо по адресу фактического проживания, либо по месту работы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Согласно этому закону,&nbsp;<strong>с 8 июля</strong>&nbsp;регистрации иностранных граждан должны быть оформлены по адресу их фактического проживания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы хотим подробнее разъяснить данный закон, поэтому ответим на вопросы, которые на данный момент волнуют работодателей иностранных граждан.&nbsp;</span></p>\n<p><strong><span style=\"font-family: Arial;\">1 вопрос. Можно ли регистрировать иностранцев на юридический адрес?&nbsp;</span></strong></p>\n<p><span style=\"font-family: Arial;\">В ФЗ-163 сказано, что с 08.07.2018 регистрировать иностранных граждан можно только по тому адресу, который используется иностранцами для сна и отдыха. Это значит, что вы можете зарегистрировать иностранного сотрудника на адрес своей компании, если он там не только работает, но и фактически проживает.&nbsp;</span></p>\n<p class=\"order_service\"><strong><span style=\"font-family: Arial;\">2 вопрос. Будет ли действовать миграционный учет, который был оформлен по месту работы до 8 июля?&nbsp;</span></strong></p>\n<p><span style=\"font-family: Arial;\">Да! Это следует из ст. 54 Конституции РФ, в которой сказано, никто не будет нести ответственность за деяние, которое не являлось правонарушением в момент его совершения.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Это значит, что если вы оформили (продлили) миграционный учет иностранному гражданину по месту работы до 8.07.2018, то данная регистрация будет легальна до окончания срока продления. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong>&nbsp;после этого срока, ваш сотрудник должен зарегистрироваться по новым требованиям, т. е. по адресу проживания.&nbsp;</span></p>\n<p><strong><span style=\"font-family: Arial;\">3 вопрос. Что делать работодателям в данной ситуации?&nbsp;</span></strong></p>\n<p><span style=\"font-family: Arial;\">Мы можем порекомендовать вам следующее:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1.</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/#prodlenie\"><span style=\"color: #007236; font-family: Arial;\"><strong>Продлить временную регистрацию</strong></span></a>&nbsp;<span style=\"font-family: Arial;\">иностранцу по месту работы на максимально возможный срок (необходимо успеть до 8 июля)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2.Заняться поиском нового места регистрации для сотрудника (снять хостел, гостиницу или квартиру, при условии, что собственник квартиры будет готов зарегистрировать его в этой квартире.)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. Либо предоставить сотруднику место для сна и отдыха на территории вашей компании (тогда новая принимающая сторона ему не понадобится)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>4 вопрос. Что будет, если пренебречь данным законом?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Таким образом, после 08. 07. 2018 вы не сможете поставить на миграционный учет иностранца на адрес вашей компании (если сотрудник действительно не живет по данному адресу).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В противном случае, штраф работодателю за предоставление ложных сведений об иностранце при постановке на миграционный учет будет следующим - от 350 тыс. руб. до 800 тыс. руб. (<a href=\"http://all-migration.ru/services/addservices/info/zakon/koap19-27/\" target=\"_blank\"><span style=\"color: #007236;\"><strong>ч.2 ст.19.27 КоАП</strong></span></a>).&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(33, 0, 1535709691, 'Иностранец получил травму во время работы. Что делать?', '', '<p><span style=\"font-family: Arial;\">Бухгалтеры организаций, в штате которых есть иностранные граждане, часто задаются вопросом о том, на какие социальные пособия могут рассчитывать данные сотрудники.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В частности их интересует, положены ли данным сотрудникам выплаты в случае получения травмы на производстве? Разберем этот вопрос подробнее.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Обратимся за информацией к&nbsp;<strong>ФЗ-125 &laquo;Об обязательном социальном страховании от несчастных случаев на производстве&raquo;.</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Согласно данному закону, иностранцы, легально оформленные в компании (по трудовому договору), подлежат обязательному страхованию.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Таким образом, если ваш иностранный сотрудник получил производственную травму, вы должны оплатить ему больничный лист.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Каким образом оплачивается больничный лист сотруднику?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">В уже рассматриваемом сегодня федеральном законе сказано, что независимо от стажа иностранного работника, вы выплачиваете ему пособие за весь период больничного в размере 100% среднего заработка сотрудника.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">После чего данная сумма идет в счет уплаты будущих взносов в ФСС.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35277/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(34, 0, 1535710046, 'Возмещение НДФЛ. С чего начать?', '', '<p><span style=\"font-family: Arial;\">Данная статья адресована тем работодателям, в штате которых работают иностранные граждане с&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #197b30; font-family: Arial;\"><strong>патентами на работу</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мигранты, работающие в РФ на основании патента, обязаны ежемесячно выплачивать авансовый платеж (это необходимо для того, чтобы патент не прекращал свое действие в течение года).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В свою очередь Вы, как работодатель данного сотрудника, каждый месяц удерживаете 13 % с дохода иностранца. И в результате получается, что иностранный гражданин дважды оплачивает НДФЛ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если Вы думаете, что этим вопросом должен заниматься иностранец, то это не так!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В данной ситуации получается, что Вы ведете двойное налогообложение, что запрещено законом РФ. Поэтому именно Вы должны сообщить сотруднику о том, что он имеет возможность вернуть часть денежных средств за счет возмещения НДФЛ и подать заявку в ФНС на основании его заявления.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Как подать заявку в ФНС?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">После того, как Вы получили заявление от иностранного сотрудника о его желании получать возмещение денежных средств с оплаты подоходного налога, Вам следует:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Собрать копии всех чеков за оплату патента;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Получить справку 3-НДФЛ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">С этими документами Вы можете подать заявку на возмещение НДФЛ. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если в течение 10 дней Вы получите в ФНС согласие на возврат денежных средств, то не забудьте получить от них соответствующее уведомление.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что дальше?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Далее Вы ежемесячно прекращаете удерживать часть или весь подоходный налог с иностранного сотрудника. Сумма, которая будет возмещаться иностранцу, не может превышать сумму налога за патент, который ежемесячно оплачивает Ваш сотрудник.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Например, если 13% от зарплаты сотрудника равны или больше налога за патент, то при возмещении он получит сумму, равную данному налогу (т.е. 3500 руб. в СПб и ЛО), если же 13% от дохода иностранца, меньше суммы налога за патент, то возместить ему необходимо именно эти 13%.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35325/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(35, 0, 1535710840, 'Профессия в патенте: можно ли изменить или нужен новый патент?', '', '<p><span style=\"font-family: Arial;\">Как Вы помните, чтобы безвизовый иностранец мог официально трудоустроиться, ему обязательно нужно&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>получить трудовой патент</strong></span></a><span style=\"font-family: Arial;\">. Работать на его основании можно будет один год, затем его возможно один раз&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #005824; font-family: Arial;\"><strong>продлить без выезда на границу</strong></span></a><span style=\"font-family: Arial;\">. Трудиться по патенту можно только в определенном регионе и исключительно по профессии, указанной в нем.&nbsp;<strong>Но, что делать иностранцу если он не нашел работу по профессии, которую выбрал изначально, либо проработав некоторое время по ней, решил найти работу по другой? &nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Есть два варианта решения этой проблемы и в обоих случаях мы сможем Вам помочь!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>1) Внесение изменений в действующий патент.</strong></span></p>\n<p><span style=\"font-family: Arial;\">Вы можете изменить профессию в трудовом патенте. Это значит, что, когда Вы получили патент с измененной профессией, все данные Вашего патента будут сохранены, в том числе и дата выдачи. То есть Вы получили не новый патент, а тот же самый, но с новой специальностью!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Конечно, данная процедура потребует денежных трат, но в разы меньше, чем при оформлении нового патента на работу. Ведь вам не нужно будет заново проходить тестирование, медицинскую комиссию и т.д.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Еще раз обращаем Ваше внимание на то, что это не переоформление патента, а только лишь внесение в него правок.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>2) Оформление второго патента.</strong></span></p>\n<p><span style=\"font-family: Arial;\">Если Вы хотите совмещать работу у одного работодателя по одной профессии и работу в другой компании по другой специальности или Вам необходимо работать сразу в двух регионах, то этот вариант идеально Вам подходит!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Вы оформляете дополнительно новый патент по другой профессии, и тогда сможете выбрать одну из двух специальностей, то есть на руках у Вас будет два патента. Но помните, что оба патента будут считаться действительными если Вы будете вносить ежемесячный&nbsp;<strong>авансовый платеж</strong>&nbsp;за каждый документ. Напомним, что&nbsp;<strong>на 2018 год</strong>&nbsp;он составляет<strong>&nbsp;3 500 рублей.&nbsp;&nbsp;</strong></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35407/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(36, 0, 1535711396, 'Как украинскому беженцу или иностранцу с временным убежищем устроиться на работу', '', '<p><span style=\"font-family: Arial;\">Пока одна часть украинских граждан ожидает вступления в силу нового законопроекта (</span><a href=\"http://all-migration.ru/news/35183/\"><span style=\"font-family: Arial;\">подробности здесь</span></a><span style=\"font-family: Arial;\">), другая категория приезжих из Украины уже давно пользуется определенными льготами. Речь идет о беженцах из Украины и о гражданах, получивших временное убежище. Сегодня расскажем о тонкостях трудоустройства для данных граждан.</span></p>\n<p><span style=\"font-family: Arial;\">Беженец &ndash; это гражданин, который стал жертвой преследований в своем государстве и переезжает в другую страну с целью временной защиты. При этом такой гражданин не является россиянином.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Временное убежище &ndash; это возможность иностранца находиться в России, при этом обладать всеми правами и обязанностями гражданина РФ.</span></p>\n<p><span style=\"font-family: Arial;\">Статус беженца присваивается иностранцу бессрочно до момента потери данного статуса. Временное убежище же предоставляется на один год, но его возможно продлевать неоднократно.&nbsp;</span></p>\n<h2><span style=\"font-family: Arial;\">Что нужно и тем, и другим чтобы устроиться на работу? &nbsp;</span></h2>\n<p><span style=\"font-family: Arial;\">Чтобы трудоустроиться украинским беженцам и гражданам с временным убежищем не нужно оформлять специальное разрешение на работу или трудовой патент. Так как на основании Письма Министерства Образования и Науки РФ от 8.08.2014 №НТ-865/08 такие граждане обладают теми же правами, что и россияне.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому если Вы собираетесь устраиваться на работу, помимо стандартных документов, указанных в ст.65 ч.3 Трудового Кодекса РФ Вам необходимо предоставить:&nbsp;</span></p>\n<p>&nbsp;</p>\n<ul>\n<li><span style=\"font-family: Arial;\">свидетельство о предоставлении статуса беженца или временного убежища.&nbsp;</span></li>\n</ul>\n<p><span style=\"font-family: Arial;\">Есть также список рабочих сфер, в которых Вы не имеете право трудиться. Итак, Вы не можете заниматься розничной торговлей:&nbsp;&nbsp;<br /></span></p>\n<ul>\n<li><span style=\"font-family: Arial;\">вне магазинов (код ОКВЭД 52.63)&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\">фармацевтическими товарами (код ОКВЭД 52.31)&nbsp;</span></li>\n</ul>\n<p><span style=\"font-family: Arial;\">А также:&nbsp;</span></p>\n<ul>\n<li><span style=\"font-family: Arial;\">в палатках и на рынках (код ОКВЭД 52.62)&nbsp;</span></li>\n</ul>\n<div>После приема Вас на работу, работодатель должен уведомить об этом МВД. Не забывайте, что Вам также необходимо будет&nbsp;<span style=\"color: #005824;\"><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824;\"><strong>оформить регистрацию по месту фактического проживания</strong></span></a>!</span></div>\n<div><span style=\"color: #005824;\">&nbsp;</span></div>\n<div><span style=\"color: #005824;\">&nbsp;</span></div>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35459/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(37, 0, 1535711995, 'Снятие с миграционного учета иностранных граждан: когда надо снимать, а когда нет.', '', '<p><span style=\"font-family: Arial;\">Напоминаем, что 10 августа вступит в силу&nbsp;<strong>ФЗ-257 от 29.07.2018</strong>, который внесет новые изменения в закон о миграционном учете иностранцев.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Изменения направлены на снятие иностранцев с миграционного учета.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Многие не понимают, в каких именно случаях необходимо аннулировать иностранцам регистрацию. Именно это мы сегодня и разберем.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В ст. 23 109-ФЗ перечислены основания и порядок снятия с&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>миграционного учета</strong></span></a>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Исходя из данной статьи, можно сказать, что регистрация аннулируется автоматически в следующих случаях:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. Если уже оформлена новая регистрация;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. Если был осуществлен выезд за пределы РФ;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. В случае смерти иностранца;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">4. При выявлении факта фиктивной регистрации у иностранца.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Вскоре к этому списку также добавится 5 пункт &ndash; в случае убытия иностранца с места пребывания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong>&nbsp;Снятие с учета по новому пункту№5 осуществляется во всех случаях, кроме тех, которые перечислены в пунктах 1-4.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Это значит, что снимать иностранца с учета нужно лишь в том случае, если он просто уволился и перешел к другому работодателю или сменил адрес проживания в России.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Однако, проблема в том, что вы не всегда можете быть точно уверены в том, что иностранец выехал за пределы РФ или уже встал на учет по новому месту пребывания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому советуем подавать уведомление в МВД о снятии иностранца с учета в любом случае, если вы точно не знаете, какие планы у иностранца на будущее.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ранее так делали гостиницы, отели, больницы и другие организации, предоставляющие социальные услуги населению. Данные учреждения обязаны уведомлять МВД о том, что иностранец покинул их стены до 12 часов следующего дня после отъезда иностранца, т.е. примерно в течение суток.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Сроки, за которые граждане РФ и работодатели обязаны снимать иностранцев с учета, не прописаны.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому советуем сделать это как можно быстрее, чтобы не нарваться на какие-либо неприятности. Ведь пока вы не снимите иностранца с учета, вы являетесь его принимающей стороной и несете за него ответственность.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Кроме того, вы рискуете забегаться и вовсе забыть о данном обязательстве. Тогда Вам грозит штраф по статье&nbsp;<a href=\"http://all-migration.ru/services/addservices/info/zakon/koap18-9/\"><span style=\"color: #007236;\"><strong>18.9 КоАП</strong></span></a>, а именно: 2-5 тыс. руб. &ndash; для граждан РФ и 400-500 тыс. руб. &ndash; для работодателей.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35534/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(38, 0, 1535713942, 'Авансовый платеж за патент снова повысится в 2019 году?', '', '<p><span style=\"font-family: Arial;\">Как выяснилось, многие иностранцы и их работодатели не были в курсе о том, что с 1 января 2018 года авансовый платеж за патент повысился до 3500 рублей (напоминаем, что с 2015 по 2018 год он был равен 3000 рублей).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Конечно же, узнали они об этом, когда их патенты уже были аннулированы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы настоятельно рекомендуем всем работодателям напомнить о данных изменениях своим сотрудникам, а также проверить все чеки, оплаченные за их патент в 2018 году(не забывайте, что чек за патент нужно бережно хранить или делать копию).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Возможно,&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #007236;\"><strong>патенты</strong></span></a><span style=\"font-family: Arial;\">&nbsp;ваших иностранцев уже аннулированы, а это значит, что вы должны срочно отстранить данного сотрудника от работы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО:&nbsp;</strong>Напоминаем, что за привлечение иностранцев на работу без патента, работодателю грозит штраф до 1 млн. руб. согласно&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/docs/koap18-15/\"><span style=\"color: #007236;\"><strong>ст. 18.15 КоАП</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Кроме того, у нас есть информация о том, что с 2019 года авансовые платежи за патент возрастут до 3800 рублей!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мысль о том, что стоимость патента для мигрантов необходимо снова повысить, была озвучена 11 июля на заседании Координационного совета двух регионов в сфере социально-экономического развития.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Изначально, данное предложение исходило именно от представителей Ленобласти. Санкт-Петербург же не планировал повышать оплату за патент в 2019 году. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Однако, на собрании было принято решение о том, что данные изменения лучше вводить в оба региона сразу.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Тем не менее, мы советуем иностранцам не бить тревогу и не совершать поспешных действий.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Озвученные на каких-либо правительственных совещаниях или собраниях, предложения необходимо брать на заметку. Но все же, пока официальных документов нет, все остается по-прежнему.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35700/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(39, 0, 1536225063, 'Аннулируется ли РВП в случае развода?', '', '<p><span style=\"font-family: Arial;\">Многих иностранцев, которые получали&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Разрешение на временное проживание</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(РВП) по браку, интересует вопрос: смогут ли они в случае развода подать документы дальше на&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Вид на жительство</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(ВНЖ)? Или они теряют данное право и РВП вообще аннулируется? Ответим сегодня.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Напомним, что для получения РВП необходимо иметь квоту или основание. Одним из самых популярных оснований является брак с гражданкой (гражданином) РФ! А все потому, что наряду с остальными, он самый легкий в приобретении. Не зря Госдума занялась вопросом снижения количества регистраций фиктивных браков, и даже разработала законопроект! (Подробности читайте&nbsp;</span><a href=\"http://all-migration.ru/news/35651/\"><span style=\"color: #005824; font-family: Arial;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Так аннулируется ли РВП в случае развода?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Наш ответ &ndash; нет!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если Вы, уже владея РВП, подали на развод, Ваш документ не аннулируется! Так как оформляли Вы его, будучи в браке с россиянином (россиянкой), то есть на легальных основаниях. ВНЖ, в свою очередь, Вы получаете уже на основании самого РВП, а не на основании брака или какого-то другого основания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Это значит, что Вы имеете право стать постоянно проживающим гражданином!Поэтому минимум через полгода и максимум через 2,5 года после выдачи Вам РВП, Вы можете подавать на ВНЖ. О том, что для этого нужно читайте&nbsp;</span><a href=\"http://all-migration.ru/news/35691/\"><span style=\"color: #005824; font-family: Arial;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong>&nbsp;Как по РВП, так и по ВНЖ находиться за пределами РФ можно не больше 6 месяцев суммарно в год!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как мы уже отметили выше, основание, по которому Вы получали РВП для ВНЖ - неважно! Но то, какой Вы соберете пакет документов для подачи и будут ли правильно заполнены необходимые заявления - имеет значение!</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35725/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(40, 0, 1536225485, 'Водительские права для иностранца в РФ: нужны ли российские?', '', '<p><span style=\"font-family: Arial;\">Многие иностранцы планируют по приезде в Россию водить автомобиль: кто-то в личных целях, кто-то собирается устроиться на работу водителем. Сегодня подробно рассмотрим данную тему и расскажем необходимо ли иностранцу получать российские водительские права.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Итак, п.12 и 13 статьи 25 ФЗ-196 гласят о том, что иностранцы могут управлять транспортным средством и при отсутствии российских водительских прав, имея только национальные или международные, но исключительно в личных целях!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если же приезжий собирается трудоустроиться туда, где нужно управлять транспортным средством или он хочет вести предпринимательскую деятельность, то заняться оформлением российских водительских прав все же придется!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Исключением из данного правила являются граждане Киргизии</strong>&nbsp;и приезжие из стран, где русский язык является официальным. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для того, чтобы оформить российские права потребуется пройти три этапа экзамена, а именно:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Теоретическую часть&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Выполнить практические задания на площадке в автошколе &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Вождение по городу.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong>&nbsp;Если же иностранец работает водителем без российских водительских прав, ему будет грозить штраф от 5 до 15 тысяч рублей! Если же сотрудники МВД остановят его при таких обстоятельствах снова,то помимо оплаты штрафа гражданину поставят запрет на въезд (от 3 до 10 лет).</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35754/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(41, 0, 1538392579, 'Информирование целевой аудитории о работе приемной ', '', '<p>28 сентября была организована раздача листовок, информирующих об услугах и режиме работы &nbsp;центра правовой поддержки и образования трудовых мигрантов.&nbsp;<br /> Любой желающий может получить бесплатную консультацию юриста Фонда &laquo;Ориентир&raquo; и получить всю необходимую информацию для решения&nbsp; возникшей проблемы.&nbsp;</p><div id=\"cp_widget_17af161b-11e0-48c0-8b89-6507f7da0eaa\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_17af161b-11e0-48c0-8b89-6507f7da0eaa\"; cpo[\"_fid\"] = \"AMPAWXu01p99\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>New Gallery 2018/10/1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 2080</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.005265</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/14/2018 6:41:42 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 2080</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.004583</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/14/2018 6:41:26 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span></noscript>', NULL, '', '', NULL, NULL, 1),
(42, 0, 1538396304, 'Работа правовой приемной Фонда ОРИЕНТИР', '', '<p>Работа правовой приемной Фонда \"ОРИЕНТИР\" ведется ежедневно c 09:00 по 18:00. Юридические услуги оказываются на безвозмездной основе. Для оповещения целевой аудитории раздаются информационные материалы (листовки и буклеты) на территории ул. Красного текстильщика д. 15 (Отдел МВД по вопросам Миграции), метро Сенная, а также публикации в новостей в сети интернет. В период с 01 июля по 01 октября оказаны юридические услуги более 1000 людям. Ведется журнал регистрации приема обращающихся.</p>\n<p>&nbsp;</p>\n\n<div id=\"cp_widget_7b590820-f981-4563-8b70-e3f5dbb62739\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_7b590820-f981-4563-8b70-e3f5dbb62739\"; cpo[\"_fid\"] = \"A8GAqX-p2FQK\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>New Gallery 2018/10/1</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 960</span><span>height</span><span> 720</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 960</span><span>height</span><span> 720</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 2080</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.03</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/11/2018 4:46:47 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 3120</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.03</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/17/2018 5:06:33 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span></noscript>', NULL, '', '', NULL, NULL, 1),
(43, 0, 1538400011, 'Зарплаты в рассрочку. Работодатели Петербурга и Ленобласти задолжали сотрудникам 290 млн рублей', '<h4 class=\"b-article__lead\">На мегаполис из этой суммы приходится 223,8 млн рублей, 66,2 млн &mdash; на областные предприятия.</h4>', '<p><span>Обращает на себя внимание, что в Петербурге, если верить официальной статистике, весь август активно гасились предыдущие \"долговые накопления\", так что за месяц задолженность по зарплате сократилась аж на 30,8%. Но при этом она все равно продолжает оставаться на 6,5% больше, чем в сентябре прошлого года. Что касается Ленобласти, то здесь общая сумма долгов равняется 66,2 млн рублей. Месяц назад было на 9,1% меньше. Зато по сравнению с сентябрем 2017&ndash;го предприятия 47&ndash;го региона сократили свой долг перед сотрудниками на 17,3%.</span></p>\n<p>Кстати, в прошлом году ситуация с долгами по зарплате выглядела еще более тревожной: с сентября 2016&ndash;го по сентябрь 2017&ndash;го в Ленобласти они еле&ndash;еле сократились на 1,3%, а в Петербурге и вовсе рванули на 60% вверх.</p>\n<p>Получения своих честно заработанных денег на данный момент ожидают 2700 петербуржцев и 690 жителей области. То есть средняя сумма задолженности составляет 82 889 рублей в городе и 95 942 рубля в области. При общей более положительной динамике областные работодатели обижают своих сотрудников несколько больше, чем петербургские.</p>\n<p>Распределение долгов по отраслям показывает, что в основном проблемы со своевременной выплатой зарплат испытывают обрабатывающие предприятия. Причем в Ленинградской области 66,5% (44 млн рублей) долга числится &mdash; что на первый взгляд удивительно &mdash; за \"нефтянкой\". В культурной же столице главные должники занимаются производством электрооборудования (26,9% &mdash; 60,3 млн рублей) и строительством (20,9% &mdash; 46,9 млн рублей). С учетом того что для Петербурга строительный бизнес &mdash; почти \"нефтянка\", поведенческая закономерность становится более отчетливой.</p>\n<p>По данным \"<a href=\"http://whoiswho.dp.ru/cart/company/52050/\" target=\"_blank\">Петростата\"</a>, у граждан, с января по июль 2018 года получавших зарплату вовремя, ее номинальное среднее значение равняется 41 922 рублям в области и 59 176 рублям в городе. С прошлого года это значение выросло соответственно на 10,2% и 11,4%.</p>\n<div id=\"inpage_54ad781d-96d5-4d2a-a24b-5270b3ec7ccd\" class=\"b-clearfix video-advert-container\">\n<div id=\"wrapperinpage_54ad781d-96d5-4d2a-a24b-5270b3ec7ccd\">\n<div class=\"yaAdSdkGui pointer\">\n<div class=\"topRightBlockMinimal\">Опубликованная на днях официальная статистика сообщает и о том, на что граждане тратят свои кровно заработанные. В августе в обоих регионах четко прослеживалась тенденция к сокращению объема розничных продаж пищевых продуктов по сравнению с прошлым годом (город &mdash; 4,4%, область &mdash; 6,7%). Розница непродовольственных товаров, наоборот, устойчиво растет: город &mdash; 7,9%, область &mdash; 14,8%. Оптимисты могут усмотреть здесь движение к более развитому обществу, в котором традиционно на непродовольственные товары должно тратиться кратно больше, нежели на еду. Однако прагматики указывают на то, что август &mdash; сезон традиционных закупок необходимых товаров к школе, а также сбора первого урожая на дачных участках. Эти два фактора тоже могли определить структуру розничного спроса.</div>\n</div>\n</div>\n</div>\n<div class=\"adfoxbanners-wrapper \">&nbsp;</div>\n<div class=\"adfoxbanners-wrapper \">&nbsp;</div>\n<p><span>&nbsp;</span></p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"https://www.dp.ru/a/2018/09/23/Zarplati_v_rassrochku/\"> Деловой Петербург </a></span></p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(44, 0, 1544001225, 'Важно: Новые правила постановки на миграционный учет через МВД!', '', '<p><span style=\"font-family: Arial;\">Мы заботимся о наших клиентах и всегда сообщаем Вам об изменениях законов в сфере миграции. Поэтому информируем Вас о важном нововведении:&nbsp;<strong>уже сегодня (с 19 ноября) действуют новые требования к&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824;\">оформлению временной&nbsp;регистрации</span></a></strong>&nbsp;(миграционного учета) через МВД для работодателей, нанимающих иностранцев!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В связи с усилением мер безопасности и подписанием нового&nbsp;<a href=\"http://all-migration.ru/news/36070/\">закона</a>&nbsp;об уголовной ответственности за фиктивную постановку на учет, на неопределенный период введено новое правило:&nbsp;<strong>при подаче уведомления о прибытии иностранного гражданина через УВМ ГУ МВД, необходимо прикладывать дополнительный документ на бланке организации, подписанный директором</strong>.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;В этом документе необходимо письменно подтвердить, что ваших иностранных работников обеспечат жильем по месту пребывания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Например: если ваша фирма арендует квартиру для сотрудников, то в документе указывается фактический адрес и основание, по которому Вы их регистрируете на данный адрес.</span></p>\n<p><span style=\"font-family: Arial;\">В ином случае, если не предоставить указанный документ в МВД, в постановке на учет будет отказано.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Спешим Вас успокоить:</strong>&nbsp;оформление регистраций через МФЦ остаются прежними!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данная регистрация также отвечает всем требованиям законодательства.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36195/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(45, 0, 1544001309, 'Важная новость: в 2019 году госпошлина за патент повышается!', '', '<p><span style=\"font-family: Arial;\">В прошлом месяце мы писали о рассмотрении нового закона и о вероятном увеличении стоимости<a href=\"http://all-migration.ru/news/36061/\">&nbsp;патента</a>. Мы пристально следили за новостями в данной сфере, и вот на днях приказ Минэкономразвития от 30.10.18 № 595 вступит в силу. Как это отразится на иностранных гражданах?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В соответствии с данным документом, с 2019 возрастет коэффициент, влияющий на размер авансовых платежей. По предварительным данным, величина гос. пошлины в новом году составит от 3800 рублей. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому всем иностранным гражданам, у кого заканчивается патент в январе, феврале, марте или даже в апреле, рекомендуем&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\">продлить</a>&nbsp;патент уже сейчас по текущим ценам!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Важно: продленный патент начинает свое действие после окончания текущего патента. Например, если ваш патент действует до 1 февраля 2019 года, и вы продлили его сегодня, новый патент начнет свое действие 2 февраля 2019 года. И ни одного рабочего дня вы не потеряете!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Также напоминаем, что просрочка в оплате патента даже на один день приводит к его аннулированию. Это означает, что такой патент невозможно продлить, и придется выезжать из страны и оформлять все документы заново.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36220/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(46, 0, 1544001408, 'Строительная фирма в Пушкине была проверена сотрудниками МВД.', '', '<p><span style=\"font-family: Arial;\">Даже по сегодняшний день операция &laquo;Нелегальный мигрант&raquo; не прекращается. Сотрудники МВД активно проверяют как Санкт-Петербург, так и Ленинградскую область.</span></p>\n<p><span style=\"font-family: Arial;\">Подобная проверка прошла совсем недавно на строительном объекте в городе Пушкин.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В ходе проведения рейда выяснилось, что на стройке преимущественно работают иностранные граждане. Таким образом, 6 иностранных сотрудников из 16 работали без разрешительных документов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Протоколы по данной проверке были составлены сотрудниками ГУ МВД и переданы на рассмотрение в суд. Все нелегалы будут привлечены к административной ответственности. Им грозит штраф от 5 до 7 тысяч рублей с административным выдворением (ч 2.ст.18.10 КоАП).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Штраф работодателю иностранных граждан серьезнее. Согласно ст. 18.15 юридическое лицо обязано выплатить штраф за привлечение нелегалов к работе до 1 млн. руб. за каждого сотрудника.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы сообщаем вам о подобных рейдах для того, чтобы вы понимали, что всем компаниям, которые трудоустраивают иностранных сотрудников, не могут относиться к их оформлению халатно. Ведь проверка может нагрянуть в любой момент, как плановая, так и внеплановая.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы настоятельно рекомендуем вам прямо сейчас проверить миграционные документы иностранных сотрудников.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36244/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(47, 0, 1544001518, 'Иностранный сотрудник уходит в отпуск: на заметку работодателю', '', '<p><span style=\"font-family: Arial;\">Приближаются новогодние праздники, и многие иностранные работники собираются уехать в отпуск. О каких миграционных правилах нужно помнить работодателю?</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Во-первых, об оплате действующего&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>&nbsp;следует позаботиться заблаговременно, особенно если ваш иностранный сотрудник выезжает на месяц и более. Оплатить авансовые платежи следует на срок отъезда.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Например, если иностранец оплачивает чеки за патент каждый месяц, и по договоренности с организацией- работодателем уезжает в неоплачиваемый отпуск на два месяца, ему необходимо оплатить несколько чеков вперед.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Имейте в виду: действие патента не приостанавливается на срок отсутствия иностранца в РФ!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Во-вторых, место проведения отпуска тоже имеет значение. Если иностранный гражданин планирует отдыхать в другом субъекте РФ, ему потребуется постановка на миграционный&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">учет&nbsp;</a>в отпуске по месту пребывания. Если же он собирается покинуть пределы РФ, то по возвращении должен повторно оформить временную регистрацию.&nbsp;&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36258/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(48, 0, 1544001622, 'Оформить документы мигрантам получится только в начале декабря', '', '<p><span style=\"font-family: Arial;\">сегодняшнюю статью мы посвятим подробному описанию работы государственных органов и как поступить работодателю в связи с установленными сроками.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Итак, доводим до Вашего сведения, что подать документы на&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><strong><span style=\"color: #005824;\">оформление</span></strong></a>&nbsp;или&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><strong><span style=\"color: #005824;\">продление патентов</span></strong></a>&nbsp;для своих сотрудников Вы сможете только&nbsp;<strong>до 26 декабря 2018 года.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Последним же днем приема документов на&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><strong><span style=\"color: #005824;\">постановку на миграционный учет</span></strong></a>&nbsp;будет&nbsp;<strong>27 декабря текущего года</strong>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">После этих дат сдать документы у Вас не получится, так как вплоть до 16:00&nbsp;<strong>29 декабря</strong>&nbsp;государственные органы будут производить только выдачу готовых документов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В связи с этим мы рекомендуем работодателям заранее позаботиться о продлении разрешительных документов для своих сотрудников, для того чтобы Вы и Ваши иностранцы смогли спокойно уйти на Новогодние праздники, не боясь получить штрафы за недействительные документы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">По практике, продлить документы после праздников иностранец сможет только в первый рабочий день.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Не стоит забывать, что очередей и неразберихи, которая уже стала чуть ли не нормой в первые дни работы после праздников в государственных органах не избежать.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36264/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(49, 0, 1549269667, 'Какие НОВОВВЕДЕНИЯ ждут нас в 2019 году?', '', '<p><span style=\"font-family: Arial;\">По сложившейся традиции, в декабре законодательные органы принимают новые нормативные акты, по которым нам предстоит жить в наступающем году. А министерства и ведомства принимают внутренние приказы и распоряжения, которые непосредственно отражаются на гражданах и организациях. Поэтому наших клиентов интересует, какие же изменения в оформлении разрешительных документов нас ждут в 2019 году?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">На данный момент уже известны следующие данные: &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. ГУ МВД продолжает ужесточать постановку на&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">миграционный учет</a>&nbsp;иностранных граждан на юридические адреса работодателей.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В 2019 году планируется повысить стоимость оформления временных регистраций. Одновременно с этим ужесточаются требования к регистрациям, поданным не через МВД (почта либо МФЦ). Такие документы принимаются только при наличии их в базе МВД. А при подаче документов на миграционный учет через МВД, требуется дополнительное&nbsp;<a href=\"http://all-migration.ru/news/36195/\">письмо</a>&nbsp;от генерального директора о том, что он ознакомлен с административной и (или) уголовной ответственностью за предоставление ложных сведений о месте проживания иностранных граждан.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. Всем уже известно, что стоимость авансового платежа за&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\">патент</a>&nbsp;повысится до 3800 рублей &ndash; как для Санкт-Петербурга, так и Ленинградской области. Также рассматривается повышение стоимости медицинской комиссии, что тоже отразится на итоговой стоимости оформления патента.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В связи с этим, мы рекомендуем заранее позаботиться о продлении разрешительных документов Ваших иностранных сотрудников &ndash;минимум за месяц до истечения их срока!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36374/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(50, 0, 1549269720, 'Каких законов в сфере миграции следует ожидать в 2019 году?', '', '<p><span style=\"font-family: Arial;\">В течение года Госдума принимала различные законы в сфере миграции, и декабрь 2018 года &ndash; не исключение. Важнейшим законопроектом, принятым российским Парламентом в этом году, стал проект&nbsp;<strong>№ 527255-7</strong>&nbsp;об изменениях в закон &laquo;О гражданстве РФ&raquo;. Этот проект пока находится на подписании у Президента, однако уже вызвал множество вопросов: правда ли, что украинцев можно будет трудоустраивать без&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патентов</a>? Планируется ли массовое предоставление гражданства жителям ДНР и ЛНР?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому мы решили рассказать, какие изменения он предусматривает и как это отразится на работодателях.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Проект предусматривает&nbsp;<strong>два важнейших положения:&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">1. упрощение процедуры получения гражданства для тех, кто уже является участником&nbsp;<strong>Государственной программы переселения соотечественников</strong>&nbsp;(не потребуется подтвержденный источник дохода и экзамен на знание русского языка).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. предоставление Президенту права определять&nbsp;<strong>отдельные категории иностранцев</strong>, которые могут получить гражданство в особом порядке. Это могут быть не только граждане ДНР и ЛНР, но и выходцы из других регионов Украины, а также граждане других стран. Помимо послаблений, указанных выше, им не потребуется предоставлять подтверждение отказа от гражданства другого государства.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Однако данный законопроект еще должен быть подписан Президентом. После подписания, он вступит в силу через 90 дней. После этого должен быть издан соответствующий Указ Президента, и уже на основании Указа конкретные иностранные граждане смогут подать документы на получение гражданства по новой процедуре. По самым оптимистичным прогнозам это случится не ранее&nbsp;<strong>апреля 2019 года</strong>. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому в ближайшие месяцы никаких изменений для работодателей, нанимающих граждан Украины, не ожидается. Данные иностранные граждане по-прежнему должны оформлять патент на работу либо предъявлять другой разрешительный документ (РВП, удостоверение беженца).&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36376/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(51, 0, 1549269860, 'С 16 января 2019 вводится новая обязанность работодателей!', '', '<p><span style=\"font-family: Arial;\">В январе 2019 года вступит в силу новый закон, затрагивающий права работодателей иностранных граждан. Он коснется тех компаний, которые приглашают граждан из визовых стран: как по&nbsp;<a href=\"http://all-migration.ru/services/addservices/priglashenie/\">деловой</a>&nbsp;визе, так и по&nbsp;<a href=\"http://all-migration.ru/services/workpermit/\">рабочей</a>&nbsp;визе. Это могут быть&nbsp;<a href=\"http://all-migration.ru/services/addservices/workpermit-vks/\">ВКС</a>, члены их семей, иные категории иностранцев.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В соответствии с положениями закона (п.6 ст.1 Федерального закона от 19.07.2018 № 216-ФЗ и Федерального закона от 19.07.2018 № 215-ФЗ), работодатель должен контролировать фактическое&nbsp;<strong>соблюдение цели въезда иностранца</strong>: если цель въезда рабочая, то нужно подписать трудовой договор, а если иностранец въехал с частной целью, то приглашающая сторона должна поставить его на&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">учет</a>, обеспечить подписание договора аренды жилого помещения.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Также работодатель должен следить за тем, чтобы приглашенный&nbsp;<strong>иностранец вовремя покинул страну</strong>: не позднее чем за 10 дней до окончания визы напомнить о выезде. Если гражданин потерял паспорт или деньги, то ему нужно оказать содействие в восстановлении документов и приобретении билетов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: При нарушении этих правил компании придется заплатить внушительный штраф: от 400 000 до 500 000 руб. Для должностных лиц - от 45 000 до 50 000 руб.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Полный перечень мер, которые должен предпринять работодатель, чтобы соблюсти закон, разработаны Правительством, но пока не приняты. Так что, пока не очень понятно, как будет применяться новый закон на практике, однако несомненно, что у проверяющих органов возникнут дополнительные вопросы к организациям, и новые основания привлечения к ответственности.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому уже сейчас рекомендуется&nbsp;<strong>соблюдать следующие меры</strong>, чтобы избежать проблем в будущем:</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;уведомлять иностранного гражданина под роспись об обязанности соблюдения законодательства РФ, в том числе об обязанности выезда; вести с ним переписку во время пребывания в России.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36516/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(52, 0, 1549269935, 'Минтруд рекомендует работодателям проверять документы иностранных граждан', '', '<p><span style=\"font-family: Arial;\">Новогодние праздники позади, и компании снова приступили к найму сотрудников, в том числе иностранных. Как оформить иностранца в соответствии с законом и не попасть на многомиллионные штрафы? &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Министерство труда и социальной защиты советует всем организациям-работодателям проверять при найме иностранцев их документы на подлинность (<strong>Письмо от 04.12.2018 N 14-2/В-966</strong>). В частности, это касается&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>&nbsp;или иного разрешения на работу, выдаваемого иностранцу. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как мы рассказывали&nbsp;<a href=\"http://all-migration.ru/news/36323/\">ранее</a>,&nbsp;патент&nbsp;можно проверить через специальный сервис на сайте ГУВМ МВД, и если информация о патенте отсутствует в базе, обратиться в МВД с письменным запросом.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Также необходимо обратить внимание на&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">миграционный</a>&nbsp;учет работников: первичная регистрация оформляется на срок 3 месяца через МВД, МФЦ или по почте.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: Последующее продление миграционного учета возможно только на основании патента и только через МВД или МФЦ! &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Нередки случаи, когда иностранцы приносят продленную регистрацию, оформленную через отделение почты, или регистрацию на несуществующий адрес либо недействующее юр.лицо.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;В Письме отмечается, что Трудовым Кодексом не установлена обязанность проверять действительность документов будущих сотрудников, однако работодатель может быть привлечен к административной ответственности за допуск к работе мигранта с нарушениями миграционного законодательства (в том числе с поддельным документом).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36556/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(53, 0, 1549270010, 'Судебная практика: генподрядчик тоже отвечает за нелегального мигранта!', '', '<p><span style=\"font-family: Arial;\">Многие строительные компании полагают, что если они выступают генеральными подрядчиками, а фактически работы выполняет подрядная организация, то вся ответственность за оформление сотрудников лежит на ней, и организация-заказчик тут не причем.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Однако это не так, и подтверждением может служить судебное разбирательство&nbsp;<strong>А56-72575/2018</strong>, которое проходило в Арбитражном Суде Санкт-Петербурга.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;1 февраля 2018 года правоохранительные органы проверили строительный объект в Санкт-Петербурге и выявили гражданина Узбекистана, который работал арматурщиком, не имея&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>. В связи с этим ООО &laquo;Центр-Инвест-Строй&raquo; - генеральный подрядчик - было привлечено к ответственности по ст. 18.15 КоАП.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;В Суде ген. подрядчику не удалось доказать свою невиновность: несмотря на то, что подрядчик обязан получать необходимые разрешения на работу для своих сотрудников, ген подрядчик контролирует соблюдение законодательства и проверяет легальность привлечения работников на объект и отвечает за осуществление строительства. Поэтому ген. подрядчик в данном случае обязан был проконтролировать наличие у арматурщика всех необходимых документов. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Исходя из этого, Суд подтвердил законность штрафа в размере&nbsp;<strong>400 000</strong>&nbsp;рублей на ген.подрядную организацию.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Таким образом, даже если для выполнения работ вы нанимаете стороннюю организацию, необходимо тщательно проверять, кто именно будет допущен к работе. И бдительность никогда не бывает лишней. Экономия на оформлении и кадровом учете иностранцев может вылиться в штраф до&nbsp;<strong>1 млн. рублей</strong>&nbsp;за каждого нелегального работника!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36576/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(54, 0, 1549270849, 'Уведомления МВД о расторжении трудового договора и об убытии: в каких случаях подавать?', '', '<p><span style=\"font-family: Arial;\">Миграционное законодательство регулярно меняется, и работодатели часто не успевают подготовиться ко всем изменениям. Поэтому мы всегда отвечаем на вопросы о спорных моментах, возникающих на практике. В частности, много затруднений вызывают уведомления МВД &ndash; о&nbsp;<a href=\"http://all-migration.ru/services/addservices/notification/\">расторжении трудового договора</a>&nbsp;и об&nbsp;<a href=\"http://all-migration.ru/services/addservices/snyatiemigruchet/\">убытии</a>&nbsp;(о снятии с миграционного учета). Давайте рассмотрим, в каких случаях нужно подавать каждое из них.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Уведомление о расторжении подается во всех случаях прекращения трудового договора, в том числе при истечении сроков разрешительных документов иностранца. Данное правило распространяется на все категории иностранных граждан &ndash; из стран ЕАЭС, имеющих&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патент</a>,&nbsp;<a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\">РВП</a>,&nbsp;<a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\">ВНЖ</a>&nbsp;и так далее.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">На выполнение данной процедуры у работодателя есть всего&nbsp;<strong>три рабочих дня</strong>! &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Помимо этой обязанности, с 10 августа действует закон&nbsp;<strong>ФЗ-257</strong>, по которому принимающая сторона обязана снимать иностранцев с миграционного учета. Для этого подается отдельное уведомление в МВД об убытии иностранного гражданина.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Подавать его нужно в том случае, если иностранный работник был зарегистрирован по месту работы. А согласно 163-ФЗ, такую регистрацию работодатель может оформить только по адресу фактического проживания работника, например, если по адресу компании есть оборудованные места для сна и отдыха. Другой вариант: у работодателя в собственности или в аренде есть жилье, которое он предоставляет сотрудникам. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>В итоге</strong>: если работодатель оформил миграционный учет иностранцу, он должен подавать оба уведомления &ndash; и о расторжении трудового договора, и об убытии. Если же иностранец принес регистрацию, оформленную на любой другой адрес (частное лицо, общежитие или хостел), то от вас требуется только уведомление о расторжении!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36796/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(55, 0, 1549270914, 'Иностранный сотрудник поменял статус. Как выплачивать страховые взносы?', '', '<p><span style=\"font-family: Arial;\">В миграционном законодательстве России существуют разные категории иностранцев: временно пребывающие граждане, временно проживающие и постоянно проживающие иностранцы. Их правовое положение регулируется разными законами и нормами, в том числе это касается налоговых и страховых отчислений. При этом нередки случаи, когда сотрудник меняет статус, оформляет новый разрешительный документ. Это очень сложные ситуации для работодателя, поэтому мы остановимся на них подробнее.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Например, в феврале вы трудоустроили гражданина по&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патенту</a>, а через месяц он получил&nbsp;<a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\">РВП</a>. В каком размере работодатель оплатит страховые взносы на сотрудника в феврале и марте?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В феврале ставки нужно рассчитывать с учетом того, что иностранный гражданин является временно пребывающим (работает на основании патента):</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Пенсионный фонд &ndash; ставка 22%; социальные взносы (ФСС) - ставка 1,8%. Медицинские взносы (ФОМС) не начисляются.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;С марта иностранный сотрудник стал временно проживающим. С этого момента взносы на социальное страхование должны рассчитываться по тарифу 2,9%. И самое важное - с этого месяца нужно начислять медицинские взносы по ставке 5,1%. Взносы за пенсионное страхование остаются прежними &ndash; 22%.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36809/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(56, 0, 1549270992, 'Обратите внимание: если у вашего сотрудника 2 паспорта', '', '<p><span style=\"font-family: Arial;\">Документы иностранных граждан отличаются большим разнообразием, поэтому работодатели сталкиваются с непредвиденными трудностями. Например, у иностранного гражданина может быть несколько удостоверительных документов (паспортов). Прежде всего это касается граждан СНГ. Поэтому мы решили посвятить данному вопросу отдельный материал.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Граждане из безвизовых стран СНГ могут пересекать границу России как по внутреннему, так и по заграничному паспорту. А у украинских граждан может быть также биометрический паспорт, или паспорт гражданина ДНР/ЛНР. Почему важно обращать на это внимание?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В разных паспортах имя гражданина может быть написано по-разному (с отчеством и без). При этом граждане и организации-работодатели зачастую забывают о том, что все разрешительные документы в РФ должны быть оформлены на один и тот же паспорт (те же реквизиты). Например, при оформлении&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>гражданин использовал внутренний паспорт гражданина Украины. После этого работодатель принял данного гражданина на работу, и подал&nbsp;<a href=\"http://all-migration.ru/services/addservices/notification/\">уведомление</a>&nbsp;МВД о заключении трудового договора, где ФИО иностранца указано как в заграничном паспорте, а не как в патенте.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В итоге уведомление будет считаться поданным на другого человека, и обязанность работодателя уведомить органы МВД о трудоустройстве иностранца не будет исполнена, что грозит штрафами в случае проверки.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36857/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, '', '', NULL, NULL, 1),
(57, 0, 1554554598, 'Почему необходимо заранее подавать документы на патент?', '', '<p>Как известно, для получения права на трудоустройство в России граждане большинства стран СНГ должны оформить специальное разрешение &ndash; патент. При этом на его оформление иностранцу дается 30 дней с момента въезда в Россию. А продление патента необходимо начать оформление не позднее чем за полтора месяца до окончания действующего патента.&nbsp;</p>\n<p>Важно не откладывать процесс оформления и подать документы на патент для вашего сотрудника заблаговременно.</p>\n<p>Дело в том, что датой подачи документов считается день, когда в МВД поданы оригиналы всех требуемых документов для оформления патента и оплачена гос. пошлина. И с этого дня отчитывается 12 рабочих дней, в течение которых МВД подготовит патент.</p>\n<p>А собрать все указанные документы - это достаточно сложная и длительная процедура: гражданину необходимо пройти тестирование по русскому языку (при отсутствии подтверждающего документа), а также медицинское освидетельствование. При этом нередки случаи, когда иностранцев направляют на дополнительное обследование, сдать те или иные анализы, пройти диагностику в специализированных диспансерах. Причем результаты таких доп. обследований можно ожидать достаточно долго: например, результаты сдачи некоторых тестов приходят только в определенный день недели, и ускорить данный процесс невозможно.&nbsp;</p>\n<p>В отсутствие медицинского заключения невозможно подать полный комплект документов, соответственно, гражданин может пропустить последний день оформления патента. В результате, работодатель остается на какое-то время без работника, а иностранец вынужден ехать на границу и получать новую миграционную карту. Это лишние расходы и нервы.</p>', NULL, '', '', NULL, NULL, 1),
(58, 0, 1554554772, 'Участились случаи подделки патентов!', '', '<p>Часто обращаются компании, которые хотят принять на работу сотрудника с уже оформленным патентом, продлить ему регистрацию либо переоформить патент. Однако в процессе подготовки документов выясняется, что данный патент &ndash; подделка, и работодатель не имеет права принимать такого гражданина на работу. По каким данным это можно определить?</p>\n<p>&nbsp;Во-первых, зачастую даже визуальный осмотр может показать, что данный патент был оформлен не на фирменном бланке либо форма бланка заполнена неверно: некорректные символы в номере паспорта; неправильное указание территории действия патента (например, Ленинградский вместо Ленинградская область); орган выдачи иной, нежели УВМ ГУ МВД; некорректные реквизиты патента или бланка (он должен содержать серию и номер).&nbsp;</p>\n<p>Во-вторых, МВД разработал онлайн-сервис для проверки документов иностранного гражданина: достаточно ввести паспортные данные иностранца, чтобы получить данные о его патенте. Однако база данного сервиса обновляется нерегулярно, и результаты носят справочный характер.&nbsp;</p>\n<p><strong>Важно</strong>: Окончательный ответ о действительности патента можно получить, обратившись напрямую в МВД! &nbsp;</p>\n<p>С начала 2019 года мы наблюдаем увеличение числа случаев, когда иностранные граждане предъявляли работодателям поддельные патенты для трудоустройства. Поэтому мы рекомендуем вам проявлять бдительность при проверке документов, и при наличии малейших сомнений обращаться за консультацией к миграционному специалисту.&nbsp;</p>', NULL, '', '', NULL, NULL, 1),
(59, 0, 1554555317, 'ЕС увлек нелегалов. Поток мигрантов в Россию колеблется вместе с курсом рубля', '', '<p>Демографический кризис поразил Россию очень не вовремя. Мы тут только вновь начали говорить об \"экономическом прорыве\", а на тебе: с 2016 года нарастает естественная убыль (смертность россиян все выше рождаемости), с 2017 года пошел резко вниз миграционный прирост населения. И как результат &mdash; с 2018 года впервые за последние 10 лет стала сокращаться численность населения страны. Сразу на 94 тыс. человек.</p>\n<p>О причинах роста естественной убыли населения мы уже писали. Главное, что это было легко прогнозируемо и неизбежно. А вот резкий спад миграционного прироста в 2017&ndash;2018 годах стал неожиданностью.</p>\n<p>Если посмотреть на историю, то резкий всплеск иммиграции в Россию в 1990&ndash;х был связан с политическими причинами, распадом СССР. Новые границы государств повлекли за собой массовое переселение народов &mdash; Россия приняла к себе за 10&ndash;летие почти 4 млн человек. Затем, в нулевых годах, иммиграция приняла экономический, трудовой характер. В быстро растущую Россию потянулись на заработки русскоговорящие люди из стран бывшего СССР.</p>\n<p>Трудовые мигранты ехали в Россию за заработком. Поэтому понятна связь уровня миграции с курсом рубля: растет рубль &mdash; растет миграция. Падает рубль (например, в конце 2008 и 2014 годов) &mdash; сокращается миграция (соответственно в 2009&ndash;м и 2015&ndash;м). Но из этой логики совершенно выбился 2017 год. Когда рубль укрепился (на 5%), а миграция резко упала (почти на 20%), хотя должно было бы быть наоборот.</p>\n<p>На самом деле миграция в Россию, как и следовало ожидать, вслед за рублем немного увеличилась &mdash; за одним исключением. Украинцы. Приток их в Россию сократился сразу на 71 тыс. человек и полностью съел прирост мигрантов из других стран.</p>\n<p>Приток мигрантов с Украины упал на 60% в 2017 году и еще на 69% в 2018&ndash;м. Всего за 2 года &mdash; в 8 раз. Почему?</p>\n<p>Отчасти это было связано с войной на востоке Украины. Из&ndash;за нее поток мигрантов резко вырос &mdash; с 36 тыс. человек в 2013 году до 94 тыс. и 146 тыс. человек в следующие 2 года. Видимо, все, кто мог и хотел уехать с \"поля боя\", это сделали. Но был и еще один фактор.</p>\n<p>11 июня 2017 года заработал безвизовый режим въезда украинцев в ЕС. Конечно, он предполагает запрет на работу в Евросоюзе. Но разве для трудовых мигрантов главное &mdash; это официальные трудовые отношения? В России многие из них точно так же работали на птичьих правах. А заработки в Европе всяко больше. Украинские трудовые мигранты, скорее всего, рванули \"осваивать\" Европу.</p>\n<p>В результате Россия потеряла трудовых мигрантов со славянскими корнями. Им на смену пришли люди из стран с большей разницей в культурных традициях, чем между россиянами и украинцами.</p>\n<p>В 2018 году число украинских мигрантов в Россию переместилось с первого на третье место, пропустив вперед мигрантов из Таджикистана и Казахстана.</p>\n<p>Почему Таджикистан? Если посмотреть статистику по переводам нерезидентов из России в другие страны СНГ, выяснится, что именно туда отправляются самые маленькие переводы &mdash; сумма одного в III квартале 2018&ndash;го составляла всего $210. Средний перевод на Украину был выше в полтора раза &mdash; $322. А в Узбекистан &mdash; вдвое, $468.</p>\n<p>От этой статистики остается такое впечатление, что именно мигранты из Таджикистана получают самые маленькие доходы. И это самые дешевые мигранты. Кстати, в 2018 году из&ndash;за падения рубля их число тоже сократилось, но немного &mdash; всего на 12%.</p>\n<p>Чего ждать в дальнейшем? Украинцев в России осталось совсем мало, и это число может еще сократиться. А число приезжих из других стран будет зависеть от динамики курса рубля &mdash; с падением рубля их будет становиться все меньше. С учетом увеличения естественной убыли и сокращения миграционного прироста будет происходить дальнейшее \"обезлюдивание\".</p>', NULL, '', '', NULL, NULL, 1),
(60, 0, 1555491633, 'Один патент, две работы', '', '<p><span style=\"font-family: Arial;\">Иностранные граждане, оформившие&nbsp;патент&nbsp;на работу, нередко устраиваются сразу в несколько компаний. В этом случае первое место работы будет для них основным, а все последующие &ndash; работой по совместительству. Поэтому у работодателей возникают вопросы: как принять иностранного сотрудника на работу по совместительству? Нужно ли оформлять второй&nbsp;патент? &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Несколько практических рекомендаций.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. Иностранный гражданин может работать по совместительству&nbsp;<strong>только в том регионе</strong>, который указан у него в патенте. К примеру, патент иностранного гражданина получен в Ленинградской области. Может ли в этом случае иностранный гражданин работать в еще одной компании в Санкт-Петербурге? Или в обособленном подразделении вашей организации, которое зарегистрировано в Санкт-Петербурге? Нет, не может, так как патент оформлен на другой регион.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Возможный вариант решения:</strong>&nbsp;оформить второй патент для работы в двух субъектах РФ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. Если в патенте указана&nbsp;<strong>специальность</strong>, должность или вид деятельности, то иностранец может работать по совместительству только на тех же позициях. Например, мигрант получил патент по профессии &laquo;грузчик&raquo; и устроился к вам на работу. Может ли он работать в вашей же компании по внутреннему совмещению &ndash; например, сторожем на складе? Или устроиться к другому работодателю по специальности &laquo;подсобный рабочий&raquo;? Нет, не может.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;В такой ситуации можно только переоформить патент на новую профессию.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;3. Нужно ли&nbsp;уведомлять<strong>&nbsp;ГУ МВД</strong>&nbsp;о заключении трудового договора с иностранным гражданином по совместительству?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Закон 115-ФЗ не предусматривает исключения из данной обязанности работодателя. Поэтому направить уведомление необходимо в трехдневный срок после даты заключения договора.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;<strong>Важно</strong>: Помимо ограничений, установленных миграционным законодательством, нужно учитывать трудовое законодательство. Например, если гражданин работает водителем, его работа по совместительству не может быть связана с вождением автомобиля (<strong>ч. 1 ст. 329 ТК РФ</strong>).&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(61, 0, 1555493270, 'Как выплачивать зарплату иностранцам?', '', '<p><span style=\"font-family: Arial;\">При трудоустройстве иностранного гражданина у компании возникает немало вопросов и трудностей. Одна из них &ndash; каким образом платить иностранцам заработную плату? Ведь гражданам других стран бывает сложно открыть счет в банке и получить карту, либо они просто не хотят этого делать.&nbsp;<strong>Можно ли выплачивать им зарплату наличными?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Чтобы ответить на этот вопрос, мы изучили закон о валютном контроле, разъяснения Налоговой службы, и судебную практику.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Налоговые органы исходят из статуса иностранца в нашей стране (<strong>Письмо ФНС РФ № ЗН-4-17/15799 от 29 августа 2016&nbsp;</strong>). Он может быть валютным резидентом либо нерезидентом.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как иностранцу стать&nbsp;<strong>валютным резидентом</strong>&nbsp;и чем это регулируется? Закон 173-ФЗ о валютном контроле предусматривает этот статус только для иностранцев с ВНЖ (пп.&laquo;а&raquo; п.7 ч.1 ст.1). С понятием &laquo;налогового резидента&raquo; он никак не связан.</span></p>\n<p><span style=\"font-family: Arial;\">Соответственно, если иностранец не имеет ВНЖ, все расчеты с таким гражданином, в том числе выплата зарплаты, являются валютными операциями. А значит, могут осуществляться&nbsp;<strong>только безналичным путем.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Верховный Суд с 2015 года придерживается аналогичной позиции (Постановление от 6 марта 2015 № 307-АД15-69).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Какая возможна ответственность за перечисление зарплаты иностранцу наличными? Административный&nbsp;<strong>штраф от 70% до 100% от выплаченной суммы</strong>. Такая санкция предусмотрена частью 1 статьи 15.25 КоАП РФ по нарушениям закона о валютных операциях.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как видите, надежнее перечислять зарплату иностранцев на рублевый банковский счет. Это поможет избежать вопросов у контролирующих органов. А сотрудникам рекомендовать открыть счет в одном из банков, уполномоченных на открытие счета для валютных нерезидентов. Список таких банков утверждает Центральный Банк.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для этого иностранцам понадобятся документы, подтверждающие законность нахождения в России. Например, для мигрантов из СНГ это паспорт иностранца,&nbsp;миграционная&nbsp;карта, действующий&nbsp;патент, а также нотариальный перевод паспорта иностранца на русский язык.</span></p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(62, 0, 1556541961, 'Мигранты охладели к России', '', '<p>Мигранты разлюбили Россию. Они больше не видят возможности для заработка в нашей стране и начали переориентироваться на страны Евросоюза, отмечают эксперты. При этом миграционный поток перестал покрывать естественную убыль населения России. Для того, чтобы избежать сокращения населения, нужно принимать по 500 тыс. мигрантов в год.</p>\n<p>С каждым годом в Россию приезжает все меньше мигрантов. Количество так называемых долговременных международных мигрантов в Россию не растет с кризисного 2014 года, а их &laquo;выбытие&raquo; увеличивается, выяснили эксперты&nbsp;РАНХиГС.</p>\n<p>Так, миграционный прирост населения в 2018 году оказался самым низким за последние годы.</p>\n<p>&laquo;Начиная с 2014 года число международных мигрантов, прибывающих в Россию, стабилизировалось на уровне 565&ndash;600 тыс. человек, но число уехавших увеличилось с 308 тыс. в 2014 году до 441 тыс. в 2018 году&raquo;,</p>\n<p>&mdash; говорится в очередном выпуске мониторинга РАНХиГС.</p>\n<p>В результате миграционный прирост населения составил в 2018 году 124,9 тыс. человек. Это самый низкий показатель, согласно данным текущего учета миграции, который ведется с 2004 года.</p>\n<p><strong>Прирост за счет левой статистики</strong></p>\n<p>По словам экспертов, в начале 2000-х годов долговременная миграция не учитывалась в полной мере. По этой причине данные миграционного прироста пришлось корректировать по результатам Всероссийской переписи населения от 2010 года.</p>\n<p>Если принять их во внимание, то миграционный прирост населения России в 2018 году был самым низким за весь постсоветский период. То есть, с 1991 года.</p>\n<p>Резкое увеличение числа прибывших в Россию произошло в 2011 году. Рост количества мигрантов был вызван изменением методики статистического учета. Дело в том, что в качестве долговременных мигрантов стали учитываться лица, зарегистрированные не только по месту жительства, но и по месту пребывания на срок 9 месяцев и более.</p>\n<p>По мере того, как у зарегистрированных заканчивалась регистрация по месту пребывания, стала расти численность выбывших.</p>\n<p>При этом с 2016 года снизилось количество прибывших в Россию из Украины. В 2018 году поток украинских мигрантов был меньше, чем в 2015 году почти на 30%, а количество выбывших выросло в 2,6 раза.</p>\n<p><strong>Год большого перелома</strong></p>\n<p>По мнению экспертов, именно 2018 год стал переломным для мигрантов. Он показал, что в настоящее время снижение показателей миграционного прироста обусловлено уже не только методикой учета, а экономическими факторами.</p>\n<p>&laquo;Потенциал миграции в Россию из многих постсоветских стран близок к исчерпанию, особенно это касается стран западной части бывшего СССР&raquo;, &mdash; говорится в мониторинге.</p>\n<p>Мигрирующих в Россию становится все меньше по целому ряду причин. Во-первых, все, кто хотел уехать в Россию после развала СССР, уже уехали. То есть, стали жить в новой России.</p>\n<p>&laquo;Количество проживающих за пределами России русских и представителей титульных народов России сильно сократилось вследствие репатриации на историческую родину. Оставшиеся в значительной степени интегрировались&raquo;, &mdash; считают эксперты.</p>\n<p>Во-вторых, мигранты стали покидать Россию, поскольку уровень жизни в их странах стал расти, тогда как в России снижаться. Не последнюю роль в этом сыграло падение&nbsp;<strong>курса</strong>&nbsp;рубля.</p>\n<p>&laquo;Между многими странами и Россией уже не так велика разница в социально-экономическом развитии даже по сравнению с 2000-ми годами. Это делает выбор миграции в пользу России не столь однозначным&raquo;, &mdash; отмечают в РАНХиГС.</p>\n<p>По мнению экспертов, граждане все большего количества стран бывшего СССР переориентируются на миграцию в ЕС и другие страны.</p>\n<p><strong>Вся надежда на мигрантов</strong></p>\n<p>Между тем число российских регионов, где наблюдается миграционная убыль, растет.</p>\n<p>&laquo;Чем меньше прирост населения России в целом за счет международной миграции, тем меньшее число регионов сохраняют прирост. В 2018 году из 85 регионов России миграционный прирост имели 18 регионов, в 2017 г. &ndash; 26, в 2016 г. &ndash; 35 регионов&raquo;, &mdash; говорится в обзоре РАНХиГС.</p>\n<p>Миграционная статистика также напрямую связана с возобновившейся естественной убылью населения в России. В 2018 году миграция уже не смогла компенсировать эту убыль. Миграционный прирост лишь на 57,2% компенсировал естественную убыль, возросшую впервые за последние 10 лет и составившую 99,7 тыс. человек.</p>\n<p>Потенциал роста временной миграции из стран ЕАЭС (Евразийского экономического союза) также практически исчерпан.</p>\n<p>Численность временных мигрантов, пребывающих в течение года в России, последние три года колеблется на уровне 9,2&ndash;10,2 млн человек. В 2013&ndash;2014 годах показатели в основном находились в пределах 10&ndash;11,5 млн.</p>\n<p>В начале 2019 года также не наблюдается роста потока мигрантов.</p>\n<p>Так, на 1 апреля 2019 года на территории РФ пребывало 9,56 млн иностранцев.</p>\n<p>При этом временная миграция &ndash; это по-прежнему в подавляющем большинстве миграция из стран СНГ: на 1 апреля в России находилось 8,2 млн граждан из этих стран (86% от всех прибывших). Лидирующие позиции сохраняют страны Средней Азии и Украина.</p>\n<p>Опыт последних 25 лет показывает, что иммиграция стала серьезным демографическим ресурсом для нашей страны. Если население России сейчас растет, то почти исключительно за счет иммиграции. И даже когда население убывало (в 1993-2008 гг.), иммиграция на 60% компенсировала его естественную убыль.</p>\n<p>Население России за эти годы сократилось на 5,2 млн человек, но если бы не было иммиграции, то сокращение составило бы 13,2 млн.</p>\n<p>Всего же за 1992-2015 гг. миграционный прирост населения России составил около 9 млн человек. Роль иммиграции как основного источника роста населения России сохранится и в будущем, причем масштабы миграционного притока должны быть достаточно большими.</p>\n<p>Только чтобы перекрыть неизбежную естественную убыль населения и избежать сокращения населения России, может понадобиться принимать 500 тыс. мигрантов в год, а то и более.</p>\n<p>По мнению Анатолия Вишневского, директора Института демографии&nbsp;ВШЭ, негативный тренд может сохраниться до 2035 года. И мигранты уже не смогут компенсировать естественную убыль населения в России.</p>\n<p>&laquo;Проблема в том, что рождаемость сокращается. А сокращается она из-за того, что сейчас вступило в жизнь менее многочисленное поколение женщин детородного возраста, родившихся в 90-е годы прошлого века&raquo;, &mdash; говорит Вишневский.</p>\n<p>Второй негативный фактор &mdash; рост количества смертей послевоенного поколения 50-х. По оптимистическому сценарию Института демографии, естественная убыль может достигать 400 тысяч человек, по пессимистическому &mdash; будет доходить даже до миллиона человек в год.</p>\n<p>Ранее в&nbsp;Минэкономразвития&nbsp;прогнозировали, что из-за тяжелой демографической ситуации страна в ближайшие пять-шесть лет будет терять ежегодно примерно 800 тыс. населения в трудоспособном возрасте.</p>', NULL, '', '', NULL, NULL, 1),
(63, 0, 1556542183, 'Трудовая миграция больше не компенсирует естественную убыль населения. Означает ли это, что на рынке труда образуется дефицит кадров и работодателям придется поднимать зарплаты?', '', '<p>Число долговременных международных мигрантов в Россию не растет с 2014 года, а их выбытие продолжает увеличиваться. Миграционный прирост населения в 2018-м был самым низким за все постсоветские годы, он больше не компенсирует потери населения в результате естественной убыли. Об этом говорится в апрельском мониторинге экономической ситуации в России, подготовленном экспертами Института Гайдара.</p>\n<p><strong>Россия не зовет</strong></p>\n<p>&laquo;Потенциал миграции в Россию из многих постсоветских стран близок к исчерпанию, особенно это касается стран западной части бывшего СССР. Число проживающих за пределами России русских и представителей титульных народов России сильно сократилось вследствие более чем четвертьвековой репатриации на историческую родину. Помимо этого, между многими странами и Россией уже не так велика разница в социально-экономическом развитии даже по сравнению с 2000-ми годами. Это делает выбор миграции в пользу России не столь однозначным, жители все большего числа стран переориентируются на миграцию в ЕС и другие страны&raquo;, &mdash; говорится в исследовании.</p>\n<p>Численность временных мигрантов, пребывающих в течение года в России, последние три года колеблется на уровне 9,2&mdash;10,2 млн человек (в 2013&mdash;2014 годах показатели в основном находились в пределах 10&mdash;11,5 млн). В начале 2019 года также не наблюдается признаков возврата к докризисным показателям временной миграции. Временная миграция &mdash; это по-прежнему в основном миграция из стран СНГ: на 1 апреля 2019-го в России находилось 8,2 млн граждан из этих стран (86%), на 1 марта &mdash; 8,1 млн (85%). Лидирующие позиции сохраняют приезжие из стран Средней Азии и с Украины.</p>\n<p>Эксперты Института Гайдара указывают, что перспективы миграционного прироста населения России приобретают особую значимость в условиях возобновившейся естественной убыли населения, которую в 2018 году миграция уже не смогла компенсировать.</p>\n<p><img title=\"migracia.jpg\" src=\"http://fondorientir.ru/filemanager/i/536c1389/migracia.jpg\" alt=\"migracia.jpg\" width=\"700px\" height=\"556px\" /></p>\n<p><strong>Кадрам мешает все</strong></p>\n<p>В ближайшие годы обойтись без трудовых мигрантов будет сложно, считает финансовый аналитик &laquo;БКС Премьер&raquo; Сергей Дейнека. &laquo;В России сложилась ситуация условной &laquo;монополизации&raquo; ряда отраслей, где значительную долю персонала составляют трудовые мигранты, преимущественно из стран Средней Азии. В основном это те сферы, куда по тем или иным причинам не так охотно идут работать российские граждане. Это сферы торговли, транспорта, ЖКХ и благоустройства, а также строительство и сельское хозяйство. Безусловно, какую-то часть дефицита кадров в названных выше отраслях можно будет закрыть, в той же розничной торговле это, на наш взгляд, реально. Но полностью решить проблему кадрового дефицита в России без привлечения приезжей рабочей силы в ближайшие годы будет сложно&raquo;, &mdash; говорит Дейнека.</p>\n<p>Можно предположить, что число долговременных мигрантов в России не растет из-за их невидимости для миграционных служб &mdash; как правило, процесс регистрации затягивается, человек переезжает из одного населенного пункта в другой и переходит в режим нелегального пребывания на территории страны, рассуждает первый вице-президент &laquo;Опоры России&raquo; Павел Сигал. &laquo;Сейчас вводятся поправки в законодательстве, для того чтобы ускорить процесс легализации нахождения мигрантов в России и их депортации в случае установки нежелательности нахождения на территории РФ. На данный момент можно говорить о нехватке именно высококвалифицированных кадров &mdash; на начало 2019 года эта цифра составила 967,8 тысячи вакантных мест, &mdash; однако это проблема сугубо системы образования и повышения квалификации работников&raquo;, &mdash; указывает он.</p>\n<p>По словам начальника отдела глобальных исследований &laquo;Открытие Брокера&raquo; Тимура Нигматуллина, в теории отток квалифицированной рабочей силы из РФ должен оказывать существенное влияние на уровень безработицы. &laquo;В 2018 году безработица в России из-за демографии и слабого рубля закрепилась заметно ниже уровня NAIRU &mdash; показателя естественного уровня, не влияющего на инфляцию. Все, что под ним, обычно несет в себе проинфляционный эффект. Соответственно, в этом случае из-за недостатка на рынке &laquo;свободных рук&raquo; создаются предпосылки для повышения работодателями зарплат темпами, опережающими рост производительности труда. Тем не менее повышение МРОТ (повысилась стоимость рабочей силы) и пенсионного возраста (больше количество экономически активного населения) пока ограничивают данный фактор&raquo;, &mdash; отмечает Нигматуллин.</p>\n<p><strong>Время работодателя, а не работника</strong></p>\n<p>В случае уменьшения числа мигрантов тенденции на рынке не поменяются &mdash; сейчас время работодателя, а не работников, уверена ведущий аналитик &laquo;Эксперт Плюс&raquo; Мария Сальникова. &laquo;Диктовать тенденции оплаты труда будут работодатели, а не население страны. Работа в стране есть, при желании удовлетворять потребности отдельных граждан и семей в целом можно существующими предложениями оплаты на рынке труда. На экономике России сокращение миграционных потоков скажется лишь ухудшением официальной статистики по приросту населения. Улучшить условия для молодых семей и спровоцировать желание поддержать рост демографии в стране за один 2019 год социальными &laquo;плюшками&raquo; не получится&raquo;, &mdash; считает она. &laquo;Вопрос не в количестве мигрантов, а в их качестве. Работу без специализированных знаний и навыков смогут выполнять граждане Российской Федерации. Можно сказать, что наступает момент, когда населению нужно просто работать, невзирая на престижность деятельности и уровень оплаты. Или просиживать дома и ждать лучших времен или лучшего предложения на рынке труда месяцами&raquo;, &mdash; добавляет эксперт.</p>\n<p>Даже при росте кадрового дефицита у бизнеса не будет возможности платить больше, считает Сергей Дейнека. &laquo;Во многом сокращение притока мигрантов в последние годы было вызвано снижением уровня оплаты труда, которое дополнительно усугубилось девальвацией российской национальной валюты. С большой долей вероятности можно предполагать, что кадровый дефицит будет расти, однако бизнес сможет реагировать на эти вызовы крайне ограниченно и точечно, тратя деньги только на особо ценных специалистов. Работодатели всегда заинтересованы в сохранении кадров, но для увеличения выплат персоналу требуется развитие бизнеса и экономическое развитие страны в целом. Мы же наблюдаем в последние годы неуверенный экономический рост, который не позволяет компаниям наращивать свои расходы на персонал&raquo;, &mdash; резюмирует эксперт.</p>', NULL, '', '', NULL, NULL, 1),
(64, 0, 1558353972, '1 мая Путин подписал еще один Указ об упрощенной выдаче паспортов', '', '<p>1 мая 2019 года на сайте Кремля был опубликован новый Указ Президента РФ. Он касается упрощенной выдачи российских паспортов отдельным категориям иностранных граждан (преимущественно гражданам Украины).</p>\n<p><strong>Гражданство в упрощенном порядке смогут получить:</strong></p>\n<ul>\n<li>граждане Украины и лица без гражданства, которые родились в Крыму или Севастополе и постоянно проживали&nbsp; там&nbsp; до18 марта 2014 года;</li>\n<li>граждане Украины, имеющие РВП, вид на жительство или временное убежище, или статус участника Госпрограммы переселения, если они ранее постоянно проживали в Донецкой и Луганской областях Украины до 7 и 27 апреля 2014 года соответственно.</li>\n<li>граждане, которые сами либо их родственники, подверглись незаконной депортации с территории крымской АССР (речь идет о насильственном выселением крымских татар по инициативе Наркома внутренних дел СССР в 1944 году).</li>\n</ul>', NULL, '', '', NULL, NULL, 1),
(65, 0, 1558354787, 'Поменяли регион? Смените патент!', '', '<p><span style=\"font-family: Arial;\">Как известно, у многих работодателей есть несколько офисов (филиалов, отделений), которые могут располагаться как в Санкт-Петербурге, так и в Ленинградской области. Также нередкая ситуация &ndash; это переезд и смена адреса. Как быть в этом случае с иностранцами, работающими по&nbsp;патенту? Сегодня мы расскажем о 3 правилах, которые нельзя забывать.</span></p>\n<p><span style=\"font-family: Arial;\"><strong>&nbsp;Первое правило</strong>: сколько регионов, столько и патентов!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Это значит, что на каждом патенте иностранца указывается его территория действия: определенный субъект РФ. Например, Санкт-Петербург. А Ленинградская область &ndash; это другой регион, и для работы в нем нужен отдельный патент. Поэтому работать одновременно в Петербурге и области по одному патенту нельзя. Иностранец может работать только в одном регионе, либо оформлять два патента. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Второе правило</strong>: при смене региона обязательно нужно получить новый&nbsp;патент!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для этого необходимо оплатить действующий патент на месяц вперед и подать документы на оформление нового патента. Также у иностранца должна быть действующая регистрация в том регионе, где планируется получить новый патент.&nbsp;</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-family: Arial;\"><strong>Третье правило</strong>: пока сотрудник не поменял патент, не допускать его к работе!&nbsp;</span></p>\n<p>&nbsp;</p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(66, 0, 1558961828, '\"Под крылом Петербурга\": для мигрантов провели очередные бесплатные консультации', '', '<p>Очередное Мероприятие <span>&nbsp;\"Под крылом Петербурга\"&nbsp;</span>состоялось 16 мая 2019 года в 18:00 в СПб ГБУК \"Клуб \"Выборгская сторона\" (ул. Смолячкова, д.13)<span>, на котором Фонд \"ОРИЕНТИР\" оказывал юридическую помощь мигрантам.</span></p>\n<p><span><span>В ходе Мероприятия присутствовали специалисты органов государственной власти Санкт-Петербурга (социальной защиты, образования, здравоохранения, культуры и молодежной политики), а также сотрудники правоохранительных органов и представители социально ориентированных некоммерческих организаций, которые осуществляли консультирование мигрантов по вопросам получения социальной, медицинской, правовой и иных видов помощи.</span></span></p>\n<p><span><span>По окончании консультирования был концерт, на котором выступали национальные творческие коллективы Санкт-Петербурга.<img title=\"WBRZ7077.JPG\" src=\"http://fondorientir.ru/filemanager/i/18a3347b/WBRZ7077.JPG\" alt=\"WBRZ7077.JPG\" width=\"768\" height=\"768\" /></span></span></p>', NULL, '', '', NULL, NULL, 1),
(67, 0, 1561714208, 'Новый закон, ограничивающий службу в армии иностранных граждан по контракту', '', '<p><span>На днях президент РФ подписал новый закон, согласно которому иностранные граждане теперь будут иметь ограничения при заключении контракта на службу в российской армии.</span><br /><br /><span>Новый&nbsp;</span>Федеральный закон от 29.05.2019 № 117-ФЗ<span>&nbsp;\"О внесении изменений в статьи 34 и 40 Федерального закона \"О воинской обязанности и военной службе\", говорит о том, что иностранные граждане могут заключать контракт на службу в армии РФ только один раз.</span><br /><br /><span>Другими словами, иностранный гражданин может служить в армии РФ по контракту только раз, то есть по окончанию контракта его нельзя будет продлить.</span><br /><br /><span>Ранее иностранные граждане в возрасте от 18 и до 30 лет, легально пребывающие на территории РФ, могли служить по контракту в армии РФ без каких-либо ограничений, то есть они могли продлевать контракт неограниченное количество раз.&nbsp;</span><br /><br /><span>Теперь же, после того, как закон вступит в силу, иностранцы смогут заключать контракт на прохождение службы в российской армии только раз, если, конечно не получат&nbsp;</span>гражданство РФ<span>, поскольку гражданине РФ могут перезаключать контракты без ограничений.</span><br /><br /><span>Напомним, право иностранцев служить в российской армии было закреплено законом в 2003 году. При этом служащие в российской армии по контракту иностранцы не дают присягу на верность стране, но обязуются соблюдать Конституцию, воинские уставы и четко выполнять приказы командиров.</span></p>', NULL, '', '', NULL, NULL, 1),
(68, 0, 1561714449, 'С 10 июня МФЦ не принимает регистрации!', '', '<p><span style=\"font-family: Arial;\">с 10 июня 2019 встать на&nbsp;</span>миграционный<span style=\"font-family: Arial;\">&nbsp;учет можно&nbsp;<strong>ТОЛЬКО</strong>&nbsp;в территориальных подразделениях УВМ ГУ МВД!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как вы знаете, ранее оформление</span>&nbsp;регистраций<span style=\"font-family: Arial;\">&nbsp;производилось как в УВМ ГУ МВД, так и в Многофункциональных центрах (МФЦ). Однако в связи с масштабными организационными изменениями, прием юридических лиц и индивидуальных предпринимателей (работодателей иностранцев) будет осуществляться только на уровне районных отделений УВМ ГУ МВД. Работодатели&nbsp; должны будут обращаться в подразделение УВМ ГУ МВД по месту регистрации юридического лица.&nbsp;&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(69, 0, 1561720687, 'Не откладывайте на потом – продлите патент сегодня!', '', '<p><span style=\"font-family: Arial;\">Работодатели иностранных граждан знают, что&nbsp;патент&nbsp;действует максимум 12 месяцев: после этого сотрудник должен переоформить разрешительный документ. По закону необходимо подать документы за 10 рабочих дней до истечения срока патента, однако мы рекомендуем оформить продление патента за 2 месяца до окончания действующего документа.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Почему такой большой срок, спросите вы?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Дело в том, что при&nbsp;продлении&nbsp;патента можно столкнуться с рядом трудностей. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Во-первых</strong>, в последнее время участились задержки оформления медицинской комиссии. Иностранных граждан отправляют на дополнительное обследование, а получение результатов затягивается. Задержка в получении медицинского заключения может составить 2 недели, а без медицинского заключения невозможно продлить патент! &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Во-вторых</strong>, если не уложиться в сроки, иностранцу придется делать выезд-въезд для оформления нового патента. А компания на месяц и более останется без работника.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В такой ситуации важно не откладывать&nbsp;продление&nbsp;патента на последние 10 дней. В случае пропуска срока расходы на оформление патента увеличатся, ведь потребуется ехать на границу!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Даже если иностранный гражданин подает документы на&nbsp;патент&nbsp;за 2 месяца до окончания действующего документа, на текущий патент это не повлияет: новый патент начнет действовать со следующего дня после окончания предыдущего.&nbsp;&nbsp;</span></p>', NULL, '', '', NULL, NULL, 1),
(70, 0, 1562245854, 'Упрощение получения гражданства для квалифицированных специалистов', '', '<p>Государственная Дума на пленарном заседании приняла в третьем чтении поправки в Закон &laquo;О гражданстве РФ&raquo;.</p>\n<p>Минимальный срок работы в России, дающий квалифицированным иностранным работникам право на упрощенный прием в российское гражданство, сокращается с трех лет до одного года. Новые правовые нормы должны начать действовать по истечении 90 дней с момента официального опубликования закона.</p>\n<p>Список соответствующих профессий, обладатели которых имеют право на получение упрощенного гражданства РФ утвержден Правительством РФ.</p>\n<p>Среди них &ndash; врачи, фармацевты, высококвалифицированные рабочие, математики, инженеры, специалисты по защите информации.</p>', NULL, '', '', NULL, NULL, 1),
(71, 0, 1562245908, 'В России могут ввести возможность получения бессрочного вида на жительство ', '', '<p>Законопроект, по упрощению порядка предоставления иностранным гражданам разрешения на временное пребывание и вида на жительство (ВНЖ) в России, о котором мы подробно писали месяц назад успешно прошел в первом чтении.</p>\n<p>Государственная Дума на пленарном заседании во вторник, 25 июня, приняла в первом чтении проект поправок в Федеральный закон &laquo;О правовом положении иностранных граждан в РФ&raquo; и отдельные законодательные акты РФ в части упрощения порядка предоставления некоторым категориям иностранных граждан и лиц без гражданства разрешения на временное проживание и вида на жительство&raquo;.</p>\n<p>Отметим, что законопроектом, предлагается ввести возможность получения иностранными гражданами бессрочного вида на жительство в России (сейчас выдается сроком на пять лет с необходимостью последующего продления).</p>\n<p>Также проектом закона сокращаются сроки рассмотрения заявлений о выдаче разрешения на временное проживание в России до четырех месяцев (сейчас полгода).</p>\n<p>Кроме того, расширяется перечень категорий иностранных граждан, которые смогут получить разрешение на временное проживание без учета правительственных квот. Это, в частности, граждане, которые родились на территории России, состоят в браке с гражданином РФ, проживающим в стране, являются участниками госпрограммы по добровольному переселению соотечественников, беженцами из Украины, а также те, кто переселяется в Россию в соответствии с международными договорами о защите прав переселенцев.</p>\n<p>Немаловажно, и предложение расширить круг лиц, которые смогут получить сразу вид на жительство, без получения предварительного разрешения на временное проживание.</p>', NULL, '', '', NULL, NULL, 1),
(72, 0, 1567692200, 'Посольство Узбекистана запустило call-центр для обслуживания своих граждан', '', '<p>В целях создания дополнительных условий для более качественного консульского обслуживания граждан Республики&nbsp;Узбекистан с 1 июля 2019 года на базе Консульского отдела Посольства Республики Узбекистан в Российской Федерации введен в эксплуатацию семиканальный Call-centеr.</p>\n<p>Call-centеr&nbsp;позволяет гражданам Узбекистана в автоматическом режиме получать необходимую информацию консульско-правового характера на узбекском и русском языках (адреса, режим работы, распределение консульских учреждений по регионам России, сведения о предоставляемых услугах и требуемых для каждой услуги документах).</p>\n<p>Имеется опция соединения с оператором для уточнения тех или иных вопросов. Автоответчик подсказывает звонящему лицу алгоритм действий для получения информации или соединения с оператором, а также сообщает, что операторы в ближайшее время смогут ответить и каким по счету в очереди на ожидании находится заявитель. В целях контроля качества оказываемых услуг все звонки записываются.</p>\n<p>- Генеральное консульство Республики Узбекистан в г.Санкт-Петербурге (Северо-Западный федеральный округ):</p>\n<p>190005, г.Санкт-Петербург, ул. 4-я Красноармейская, д.4А. e-mail: gkruspb@mail.ru, Тел: +7 812 6010628, контакт в Telegram+7-965-029-41-49.</p>', NULL, '', '', NULL, NULL, 1),
(73, 0, 1567692238, 'В Госдуму внесен проект об исправительных работах на срок до одного года за фиктивную регистрацию мигрантов', '', '<p>Государственное Собрание - Курултай Республики Башкортостан в лице депутата Государственной думы Рафаэля Марданшина (&laquo;Единая Россия&raquo;) внесло в Госдуму законопроект, которым предлагается дополнить санкции статей Уголовного кодекса РФ о фиктивной регистрации иностранных граждан обязательными и исправительными работами за фиктивную регистрацию иностранных граждан. Документ размещен в электронной базе нижней палаты парламента.</p>\n<p>&laquo;Предлагается дополнить санкции ст. 322.2 (&laquo;Фиктивная регистрация гражданина Российской Федерации по месту пребывания или по месту жительства в жилом помещении в Российской Федерации и фиктивная регистрация иностранного гражданина или лица без гражданства по месту жительства в жилом помещении в Российской Федерации, - прим. Агентства &laquo;Москва&raquo;) и 322.3 (Фиктивная постановка на учет иностранного гражданина или лица без гражданства по месту пребывания в Российской Федерации, - прим. Агентства &laquo;Москва&raquo;) УК РФ обязательными работами на срок до 360 часов либо исправительными работами на срок до одного года&raquo;, - говорится в пояснительной записке.</p>\n<p>В настоящее время фиктивная регистрация гражданина РФ по месту пребывания или по месту жительства в жилом помещении в Российской Федерации, а равно фиктивная регистрация иностранного гражданина или лица без гражданства по месту жительства в жилом помещении в Российской Федерации наказывается штрафом от 100 тыс. до 500 тыс. руб. или в размере заработной платы или иного дохода осужденного за период до трех лет, либо принудительными работами на срок до трех лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до трех лет или без такового, либо лишением свободы на срок до трех лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до трех лет или без такового.</p>\n<p>Как отмечается в документе, законопроект направлен на обеспечение общественной безопасности и правопорядка, профилактику криминогенных проявлений, а также на совершенствование правового регулирования вопросов, которые связаны с противодействием незаконной миграции.</p>\n<p>Подчеркивается, что целью законопроекта является расширение круга лиц, которые объективно смогут понести бремя наказания за совершение преступлений, предусмотренных статьями 322.2 и 322.3 УК РФ. По мнению автора законопроекта, это поможет усилить соблюдение принципа неотвратимости наказания.</p>', NULL, '', '', NULL, NULL, 1),
(74, 0, 1567692280, 'Паспортную амнистию» могут продлить до 2025 года', '', '<p>Граждане, которые получили российский паспорт до 1 января 2010 года, но затем утратили гражданство РФ, смогут повторно приобрести его в упрощённом порядке. При этом срок действия такой льготы может быть продлён на пять лет &mdash; до 2025 года.</p>\n<p>По действующему законодательству приобрести ранее утраченное гражданство России могут граждане бывшего СССР, которые получили российские паспорта до 1 июля 2002 года (когда был принят современный закон о гражданстве), но впоследствии утратили статус гражданина.</p>\n<p>Сюда относятся случаи, когда органы власти &laquo;не определили наличие гражданства&raquo;, то есть когда по каким-то причинам эти сведения отсутствуют в базах данных правоохранительных органов. Также к этой категории относятся люди, которым аннулировали гражданство, так как они при его оформлении предоставили неточные или недостоверные сведения.</p>\n<p>&laquo;Человек получил паспорт, но не знал, что какой-то порядок был нарушен &mdash; не по его вине, а потому что какое-то решение или оформление было неправильным&raquo;, &mdash; пояснял &laquo;Парламентской газете&raquo; соавтор инициативы, зампредседателя комитета Госдумы по развитию гражданского общества, вопросам общественных и религиозных объединений&nbsp;Дмитрий Вяткин.</p>\n<p>По словам же депутатов, после 2002 года ситуаций, когда люди лишались российских паспортов по причине несоответствия каких-то данных, не стало меньше. Такие случаи сошли на нет только через восемь лет, когда к концу 2009 года было завершено формирование единой системы миграционного учёта иностранных граждан и лиц без гражданств.</p>\n<p>Поэтому они предлагают расширить формат применения данной льготы на людей, который получили российское гражданство до 1 января 2010 (а не июля 2009 года, как сейчас), а также продлить срок применения самой льготы до 1 января 2025 года.</p>\n<p>Соответствующий законопроект готовится ко второму чтению в Госдуме. Предварительно, 9 июля, состоится обсуждение инициативы на заседании комитета палаты по развитию гражданского общества, вопросам общественных и религиозных объединений.</p>', NULL, '', '', NULL, NULL, 1),
(75, 0, 1567692427, 'МВД предлагает ввести новый платеж для живущих в России иностранцев', '', '<p>МОСКВА, 11 июл - РИА Новости.&nbsp;Министерство внутренних дел России обсуждает с экспертами вопрос о введении новых платежей для живущих в России иностранцев; по мнению ведомства, это упростит им получение документов, сообщили в МВД в ответ на запрос РИА Новости.</p>\n<p>&laquo;МВД России в инициативном порядке прорабатывается вопрос по внесению в законодательство Российской Федерации изменений, касающихся уплаты налога на доходы отдельными категориями иностранных граждан, осуществляющих трудовую деятельность по найму у физических лиц для личных, домашних и иных подобных нужд&raquo;, &mdash;&nbsp;говорится в ответе на запрос.</p>\n<p>Как объяснили в министерстве, сейчас иностранные граждане, живущие в России на основании разрешения на временное проживание (РВП) и вида на жительство (ВНЖ) должны ежегодно подтверждать наличие у них дохода и сам факт проживания в стране. Однако те иностранцы, которые работают на физических лиц, не всегда могут оформить все необходимые документы.</p>\n<p>&laquo;В этой связи на площадке ГУВМ МВД России с привлечением научного сообщества ведется обсуждение целесообразности установления для временно и постоянно проживающих в Российской Федерации \"самозанятых\" иностранных граждан авансовых платежей по аналогии с платежами, установленными для иностранных граждан, осуществляющих трудовую деятельность на основании патента&raquo;, &mdash;&nbsp;говорится в ответе на запрос РИА Новости.</p>\n<p>Новый платеж, целесообразность введения которого сейчас обсуждается с &laquo;привлечением научного сообщества&raquo;, по мнению МВД, сможет упростить жизнь таким приезжим, так как факт уплаты платежа и подоходного налога одновременно доказывает и факт проживания в стране, и наличие средств к существованию.</p>\n<p>Введение нового платежа, считают в МВД, минимизирует риски аннулирования выданных &laquo;самозанятым&raquo;&nbsp;иностранцам разрешений на проживание и видов на жительство.</p>', NULL, '', '', NULL, NULL, 1),
(76, 0, 1567692595, 'Госдума приняла во II чтении проект о выдаче паспортов, изъятых из-за формальных ошибок', '', '<p>МОСКВА, 11 июля. /ТАСС/. Госдума в четверг приняла во втором чтении законопроект, который позволит вернуть российские паспорта лицам, которым они были выданы до 1 января 2010 года и позже признаны недействительными в связи с ошибками в документах.</p>\n<p>Как пояснил журналистам глава профильного думского комитета по развитию гражданского общества, вопросам общественных и религиозных организаций Сергей Гаврилов, данная инициатива является по существу \"амнистией для примерно 100 тыс. иностранных граждан, которые в конце 90-х и в 2000-е годы без необходимых документальных оснований или с допущением ошибок государственных органов получили российской паспорт, в том числе находясь за границей России\".</p>\n<p>&laquo;Мы предоставляем им право подать в миграционное подразделение органа внутренних дел по месту жительства заявление о приеме в российское гражданство, они в течение трех месяцев получат гражданство без всяких дополнительных условий&raquo;, - продолжил он.</p>\n<p>&laquo;Даже той категории лиц, предоставивших миграционным органам заведомо ложные сведения и получивших на их основании российский паспорт до 2010 года, в связи с чем паспорт у них был изъят, мы даем возможность продолжать законно находиться в России на основании бессрочного вида на жительство, у них есть право после его получения претендовать на российское гражданство&raquo;, &mdash;&nbsp;резюмировал депутат.</p>', NULL, '', '', NULL, NULL, 1),
(77, 0, 1567692735, 'Переселенцам из постсоветских стран упрощают легализацию в России', '', '<p>Категории иностранных граждан, которые могут получить разрешение на временное проживание (РВП) и вид на жительство (ВНЖ) в упрощённом порядке в скором времени может быть расширен. При этом срок рассмотрения заявлений на выдачу РВП сократится с полугода до четырёх месяцев, а вид на жительство станет бессрочным.</p>\n<p>Такие поправки в миграционное законодательство предусмотрены законопроектом, который Госдума планирует рассмотреть во втором чтении на пленарном заседании 17 июля.</p>\n<p>В частности, предлагается дать право получить РВП вне квоты гражданам, которые:</p>\n<ul>\n<li>родились на территории России;</li>\n<li>являются гражданами Украины (или лицами без гражданства) и имеют статус беженца или временное убежище в России;</li>\n<li>переселяются в Россию на постоянное жительство в рамках международного договора о регулировании процесса переселения и защите прав беженцев (заключено с Азербайджаном, Туркменистаном, Латвией, Грузией, Арменией и прочее).</li>\n</ul>\n<p>Ранее к этой категории предлагалось также отнести выпускников российских вузов, которые имеют гражданство страны бывшего СССР. Однако в ходе подготовки документа ко второму чтению было предложено предоставлять иностранцам, с отличием окончившим российские вузы, сразу вид на жительство, то есть вообще не обременять их оформлением РВП.&nbsp;Такую поправку рекомендовал принять Комитет Госдумы по развитию гражданского общества, вопросам общественных и религиозных объединений.</p>\n<p>В текущей версии законопроекта право оформлять ВНЖ без предварительного получения РВП предлагается целому ряду категорий иностранцев. Среди них:</p>\n<ul>\n<li>граждане, которые родились в РСФСР и в прошлом имели гражданство СССР;</li>\n<li>дети, которые родились на территории России, чьи родители-иностранцы имеют вид на жительство;</li>\n<li>граждане, чьи родители или дети имеют гражданство РФ и постоянно здесь проживают;</li>\n<li>иностранцы, которые проработали в России не меньше полугода в качестве квалифицированных специалистов;</li>\n<li>люди, которые проживают на территории России и у которых было прекращено гражданство РФ (кроме осуждённых по тяжким статьям УК РФ, прежде всего за терроризм).</li>\n</ul>\n<p>В ходе второго чтения в документ также может быть внесена поправка, которая позволит квалифицированным специалистам получить бессрочный ВНЖ наравне с остальными иностранными гражданами.</p>\n<p>К слову, в связи с тем, что вид на жительство станет бессрочным, Правительство предлагает повысить размер государственной пошлины за его выдачу &mdash; с 3,5 тысяч рублей до 5 тысяч рублей. Законопроект с такой инициативой Госдума также рассмотрит во втором чтении 17 июля.</p>', NULL, '', '', NULL, NULL, 1),
(78, 0, 1567692794, 'Госдума приняла закон об увеличении госпошлины за выдачу вида на жительство', '', '<p>МОСКВА, 23 июл - РИА Новости.&nbsp;Госдума приняла закон об увеличении до 5 тысяч с 3,5 тысячи рублей размера госпошлины за выдачу вида на жительство иностранному гражданину или лицу без гражданства, в том числе в случае его замены.</p>\n<p>Данное изменение вносится в связи с введением нового документа о виде на жительство, содержащего электронный носитель, пояснял ранее заместитель министра финансов РФ Илья Трунин.</p>\n<p>Аналогичный размер госпошлины сейчас установлен за выдачу загранпаспорта, содержащего такой носитель (паспорта нового поколения). Закон должен вступить в силу через 90 дней после его официального опубликования.</p>\n<p>Госдума во вторник также приняла закон, который упрощает порядок предоставления иностранцам и лицам без гражданства, владеющим русским языком и желающим жить и работать в России, разрешений на временное пребывание и видов на жительство.</p>\n<p>На данный момент вид на жительство необходимо продлевать каждые пять лет, а этот закон предусматривает выдачу бессрочного вида на жительство, за исключением отдельных случаев.</p>\n<p>Высококвалифицированные специалисты и члены их семей получат его на срок действия разрешения на работу, а квалифицированные специалисты - на три года.</p>\n<p>Поскольку вид на жительство станет бессрочным, предлагается повысить и госпошлину за его выдачу, уровняв ее с госпошлиной, которую уплачивают россияне при оформлении загранпаспорта, пояснял ранее РИА Новости заместитель председателя комитета по бюджету и налогам Леонид Симановский.</p>\n<p>В 2018 году территориальными органами МВД России выдано 191,445 тысячи видов на жительство (в 2017 году &ndash; 185,8 тысячи), а также продлен срок действия 28,331 тысячи видов на жительство (в 2017 году - 34,002 тысячи).</p>', NULL, '', '', NULL, NULL, 1),
(79, 0, 1567692828, 'Совет Федерации одобрил закон о праве на временное проживание и виде на жительство в России', '', '<p>МОСКВА, 26 июл - РИА Новости.&nbsp;Совет Федерации одобрил в пятницу закон, упрощающий выдачу разрешения на временное проживание в России отдельным категориям иностранцев без учета квоты.</p>\n<p>Сокращается с 6 до 4 месяцев срок рассмотрения заявлений о выдаче разрешений на временное проживание. Также разрешение на временное проживание будет выдаваться отдельным категориям иностранных граждан без учёта квоты. Эта норма будет распространяться на граждан, которые родились в России, состоят в браке с гражданином РФ, являются участниками госпрограммы по оказанию содействия добровольному переселению в РФ соотечественников, признаны беженцами либо получившие временное убежище в России.</p>\n<p>Вводится практика предоставления бессрочного вида на жительство (в настоящее время по общему правилу он выдается на пять лет), за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года).</p>\n<p>Расширяется круг иностранных граждан, которые могут получить вид на жительство без получения разрешения на временное проживание. К таким иностранным гражданам относятся родившиеся на территории РСФСР и имевшие в прошлом гражданство СССР, а также те, кто был депортирован с территории Крымской автономной союзной республики и их родственники по прямой линии. Вид на жительство также смогут получить родившиеся в России дети, которых усыновили иностранцы.</p>\n<p>Помимо этого, уточняется перечень случаев, при которых разрешение на временное проживание может быть выдано иностранному гражданину без учета квоты правительства.</p>\n<p>Теперь оно должно выдаваться иностранцу, который прожил в РФ не менее одного года на основании разрешения на временное проживание и которому вид на жительство выдается без получения разрешения на временное проживание. Также разрешение несовершеннолетним иностранцам будет выдаваться на срок временного проживания его родителя или опекуна.</p>\n<p>Кроме того, на срок временного проживания родителя будет выдаваться разрешение совершеннолетним иностранцам, если родитель признан недееспособным или ограниченным в дееспособности.</p>\n<p>На эти же категории распространяется право получить вид на жительство без разрешения на временное проживание, а также на тех, кто признан носителем русского языка в соответствии с ФЗ &laquo;О гражданстве Российской Федерации&raquo;&nbsp;и успешно закончил российский вуз по очной форме обучения и получил документ об образовании и о квалификации с отличием.</p>\n<p>При рассмотрении в Госдуме отмечалось, что этим законом смогут воспользоваться и граждане Украины (независимо от места постоянного проживания на Украине), планирующие жить и работать в РФ на протяжении достаточно длительного периода времени.</p>', NULL, '', '', NULL, NULL, 1),
(80, 0, 1567692878, 'Путин подписал закон об упрощенной выдаче паспортов, изъятых из-за ошибок', '', '<p>МОСКВА, 26 июл - РИА Новости.&nbsp;Президент РФ Владимир Путин подписал закон, позволяющий получать российские паспорта лицам, которым они были выданы до 1 января 2010 года, но позже признаны недействительными в связи с ошибками в документах.</p>\n<p>Соответствующий документ, опубликованный на официальном интернет-портале правовой информации, предусматривает предоставление права получить российский паспорт в упрощенном порядке лицам, имевшим гражданство СССР, которым он был выдан до 1 января 2010 года, но в отношении которых затем установили факт отсутствия гражданства, а паспорт был изъят или признан недействительным. Отмечается, что эта мера коснется тех, решение о приеме в гражданство которых никогда не принималось, а паспорт был выдан ошибочно, либо первичные документы, подтверждающие гражданство РФ, были утрачены.</p>\n<p>&laquo;Предлагается предоставить право получить в упрощенном порядке российское гражданство лицам, имевшим гражданство бывшего СССР, получившим паспорт гражданина Российской Федерации до 1 января 2010 года&raquo;, &mdash;&nbsp;отмечалось в пояснительной записке.</p>\n<p>Подчеркивалось, что дата - 1 января 2010 года - обусловлена тем, что в 2009 году в основном было завершено формирование системы миграционного учета иностранных граждан и лиц без гражданства.</p>\n<p>&laquo;В этой связи с 1 января 2010 года число лиц, которые были приняты в гражданство Российской Федерации и получили паспорт гражданина РФ на основании подложных документов или заведомо ложных либо недостоверных сведений, является крайне незначительным&raquo;, &mdash;&nbsp;добавлялось в пояснительной записке.</p>\n<p>Из документа следует, что такие лица до 1 января 2025 года смогут подать заявление о признании их гражданами РФ без выполнения каких-либо дополнительных условий.</p>', NULL, '', '', NULL, NULL, 1),
(81, 0, 1567692921, 'Высококвалифицированным специалистам предложили предоставлять гражданство РФ в упрощённом порядке', '', '<p>Минэкономразвития предложило предоставлять иностранным высококвалифицированным специалистам&nbsp;российское гражданство в упрощённом порядке. Соответствующий пакет законопроектов размещён на портале проектов нормативных правовых актов.</p>\n<p>В пояснительной записке к одному из проектов отмечается, что это необходимо для привлечения на постоянное место жительства в Россию наиболее востребованных иностранных граждан и создания дополнительных условий ведения бизнеса и повышения инвестиционной привлекательности.</p>\n<p>При этом, согласно проекту, гражданство в упрощённом порядке будет предоставляться этой категории специалистов без соблюдения условий о проживании на территории России в течение пяти лет и отказа от имеющегося у них иного гражданства.&nbsp;</p>\n<p>Минэкономразвития также предложило снизить минимальный порог заработной платы, которая полагается в России иностранному высококвалифицированному специалисту.</p>\n<p>Сейчас, согласно закону &laquo;О правовом положении иностранных граждан в РФ&raquo;, среди критериев статуса высококвалифицированного специалиста прописан размер зарплаты, который должен составлять не менее 167 тысяч рублей за один месяц (2 миллиона рублей в год).</p>\n<p>Чтобы создать благоприятные условия для привлечения таких работников, законопроектом предлагается снизить размер ежемесячной зарплаты как критерия статуса высококвалифицированного специалиста до 100 тысяч рублей и установить её выплату пропорционально отработанному времени.</p>\n<p>Отмечается, что решение этого вопроса позволит увеличить количество специалистов в среднесрочной перспективе, что позитивно повлияет на изменения в инвестиционном климате, а также на формирование инновационной экономики.</p>\n<p>Также предлагается ввести повышенную стоимость госпошлины за выдачу разрешения на работу высококвалифицированному специалисту. Предполагается, что размер этой пошлины будет установлен на уровне минимального платежа по налогу на доходы высококвалифицированного специалиста за месяц &mdash; 13 тысяч рублей.</p>\n<p>Как отмечается в сводном отчёте, предложенная мера призвана снизить риски злоупотребления работодателями права на привлечение иностранных сотрудников в качестве высококвалифицированных специалистов.&nbsp;</p>\n<p>Кроме того, проектом предлагается усовершенствовать контроль соблюдения работодателями условий привлечения таких специалистов в части начисления и уплаченных сумм НДФЛ. Так, предлагается установить для работодателей штрафные санкции в случае неуплаты ими указанного налога. Согласно документу, это повлечёт взыскание штрафа в двукратном размере от суммы неисполненных обязательств.</p>\n<p>Как указывается в пояснительной записке, в среднесрочной перспективе экономика России будет функционировать в условиях сокращения численности граждан трудоспособного возраста и сохранения естественной убыли населения.</p>\n<p>Миграция останется одним из важных факторов стабилизации и роста численности населения. При этом миграционные потоки, идущие в Россию, не в полной мере способны восполнить потребности экономики, в том числе и перспективные, в квалифицированных и высококвалифицированных работниках, отмечается в пояснении.</p>\n<p>В связи с этим, &laquo;необходимо совершенствование существующего порядка привлечения и осуществления трудовой деятельности высококвалифицированных специалистов&raquo;, указывают авторы.</p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(82, 0, 1567772708, 'Путин упростил получение права на временное проживание в России', '', '<p>МОСКВА, 2 авг - РИА Новости.&nbsp;Президент Владимир Путин подписал закон, упрощающий выдачу разрешения на временное проживание в России отдельным категориям иностранцев,&nbsp;документ&nbsp;размещен на официальном интернет-портале правовой информации.</p>\n<p>Закон сокращает срок рассмотрения заявлений о выдаче разрешений на временное проживание с шести до четырех месяцев. Документ будут выдавать отдельным категориям иностранцев без учета квоты.</p>\n<p>Норма будет распространяться на:</p>\n<p>&mdash;&nbsp;граждан, которые родились в России;</p>\n<p>&mdash;&nbsp;состоят в браке с россиянином или россиянкой;</p>\n<p>&mdash;&nbsp;являются участниками госпрограммы по оказанию содействия добровольному переселению соотечественников;</p>\n<p>&mdash;&nbsp;признаны беженцами;</p>\n<p>&mdash;&nbsp;получившие временное убежище в России.</p>\n<p>Закон также вводит практику предоставления бессрочного вида на жительство. В настоящее время по общему правилу его выдают только на пять лет.</p>\n<p>Исключение сделано для высококвалифицированных специалистов и членов их семей - они смогут получить вид на жительство на срок действия разрешения на работу.</p>\n<p>Квалифицированные работники смогут рассчитывать на получение документа сроком на три года.</p>\n<p>Кроме того, расширяется круг иностранных граждан, которые могут получить вид на жительство без получения разрешения на временное проживание. К ним относятся:</p>\n<p>&mdash;&nbsp;родившиеся на территории РСФСР и имевшие в прошлом гражданство СССР;</p>\n<p>&mdash;&nbsp;те, кто был депортирован с территории Крымской автономной союзной республики и их родственники по прямой линии;&nbsp;</p>\n<p>&mdash;&nbsp;родившиеся в России дети, которых усыновили иностранцы.</p>\n<p>Помимо этого, законом уточняется перечень случаев, при которых разрешение на временное проживание может быть выдано иностранцу без учета квоты правительства. Теперь его могут получить только граждане, которые прожил в России не менее одного года на основании разрешения на временное проживание и которым вид на жительство выдается без получения разрешения на временное проживание.</p>\n<p>Разрешение несовершеннолетним иностранцам будет выдаваться на срок временного проживания его родителя или опекуна. Кроме того, совершеннолетние иностранные граждане смогут получить разрешение на срок временного проживания родителя, если тот признан недееспособным или ограниченным в дееспособности.</p>\n<p>На эти категории распространяется право получить вид на жительство без разрешения на временное проживание, а также на тех, кто признан носителем русского языка в соответствии с федеральным законом \"О гражданстве Российской Федерации\" и успешно закончил российский вуз по очной форме обучения и получил документ об образовании и о квалификации с отличием.</p>\n<p>При рассмотрении в Госдуме отмечалось, что этим законом смогут воспользоваться и граждане Украины (независимо от места постоянного проживания в этой стране), планирующие жить и работать в России на протяжении достаточно длительного периода времени.</p>', NULL, '', '', NULL, NULL, 1),
(83, 0, 1567772821, 'В ДНР запущен сайт для записи в электронную очередь на получение гражданства РФ', '', '<p>Государственное предприятие &laquo;Почта Донбасса&raquo; запустило&nbsp;<span style=\"text-decoration: underline;\">портал госуслуг</span>, с помощью которого жители ДНР смогут записаться в электронную очередь на получение российских и республиканских паспортов онлайн. Об этом сегодня заявил генеральный директор предприятия Денис Неудачин.</p>\n<p>&laquo;Мы представляем портал&nbsp;<span style=\"text-decoration: underline;\">госуслуги-днр.рус</span>, созданный нашими специалистами, который уже с сегодняшнего дня позволит упростить процедуру получения талона в электронную очередь миграционной службы МВД ДНР. Нововведение касается граждан, оформивших электронно-цифровую подпись в наших Единых центрах связи (ЕЦС)&raquo;, &mdash; цитирует Неудачина пресс-служба госпредприятия.</p>\n<p>Оформить подпись можно в любом ЕЦС на территории Республики либо заказать онлайн через сайт аккредитованного удостоверяющего центра &laquo;Почты Донбасса&raquo;. Портал госуслуг в свою очередь пока предоставляет возможность регистрации для подачи документов на оформление гражданства ДНР и России. В дальнейшем предполагается расширение спектра предоставляемых услуг.</p>\n<p>Напомним, что 2 августа главный военный прокурор Украины Матиос заявил, что&nbsp;Киев идентифицировал значительную часть получивших паспорта РФ&nbsp;жителей Донбасса и уже начались допросы.</p>\n<p>23 июля&nbsp;Госдума РФ приняла в третьем, окончательном чтении закон, упрощающий предоставление отдельным категориям иностранных граждан разрешения на временное пребывание и получение вида на жительство в России.&nbsp;Также сообщалось, что&nbsp;за полгода почти 45 тыс. граждан Украины получили российское гражданство.</p>\n<p>19 июля министр иностранных дел Украины&nbsp;Климкин назвал паспортизацию Донбасса посланием Путина Западу.&nbsp;В тот же день стало известно, что&nbsp;Россия увеличит темпы паспортизации Донбасса до 4-5 тыс. документов в сутки.</p>', NULL, '', '', NULL, NULL, 1),
(84, 0, 1567772874, 'В магазинах сети «220 Вольт» начал работу робот-переводчик с таджикского и узбекского языков', '', '<p>В одном из магазинов федерального DIY-ретейлера &laquo;220 Вольт&raquo; появился новый продавец-консультант - робот Кувват, сообщает пресс-служба компании. Робот оснащен встроенным голосовым переводчиком с таджикского и узбекского.&nbsp;&nbsp;</p>\n<p>Кувват воспринимает устную речь и синхронно отображает перевод на экране. Программное обеспечение также автоматически подбирает подходящие товары из каталога магазина. Именно робот-продавец помогает &laquo;живым&raquo; работникам общаться с потенциальными покупателями.</p>\n<p>Помимо переводчика робот оборудован несколькими камерами. Первая распознает лица клиентов и формирует базу данных. В будущем Кувват будет приветствовать &laquo;знакомых&raquo; покупателей на их родном языке. Вторая нужна для того, чтобы беспрепятственно перемещаться по рабочей зоне.</p>\n<p>В тестовом режиме Кувват курсирует по разным торговым точкам. Особая эффективность зафиксирована в районах новостроек, где новый сотрудник пользовался высоким спросом.</p>\n<p>&laquo;За последний год поток трудовых мигрантов поставил новый рекорд. Больше всего в Россию едут из Армении, Белоруссии, Таджикистана, Узбекистана и Молдавии. Прибывающие не всегда в совершенстве владеют русским языком, так согласно нашему исследованию, до 10% клиентов не могут объясниться с продавцом. Именно это и подтолкнуло нас к идее оснастить робота переводчиком, включающим в себя языки ближнего зарубежья.</p>\n<p>Интенсивное развитие технологий позволяет усовершенствовать и упростить трудоемкие процессы во всех сферах, в том числе и в ретейле&raquo;, &mdash;&nbsp;комментирует управляющий партнер ГК &laquo;220 Вольт&raquo; Алексей Фёдоров.</p>', NULL, '', '', NULL, NULL, 1),
(85, 0, 1567772910, 'Соотечественникам продлили возможность стать гражданами России в упрощенном порядке после изъятия паспорта', '', '<p>Граждане, российский паспорт которых до 1 января 2010 года был изъят или признан недействительным в связи с допущенными при оформлении ошибками, теперь смогут вновь обратиться за получением гражданства в упрощенном порядке.</p>\n<p>Соответствующий федеральный закон от 26 июля 2019 года № 236-ФЗ &laquo;О внесении изменений в Федеральный закон &laquo;О гражданстве Российской Федерации&raquo; и статью 3 Федерального закона &laquo;О внесении изменений в Федеральный закон &laquo;О гражданстве Российской Федерации&raquo; вступил в силу 6 августа.</p>\n<p>До вступления нового закона в силу по упрощенному порядку российскими гражданами могли стать лица, имевшие гражданство бывшего СССР, затем получившие паспорт гражданина РФ до 1 июля 2002 года и имеющие при этом иностранное гражданство, при условии отсутствия у них действительного документа, подтверждающего право на проживание в иностранном государстве, и у которых впоследствии полномочным органом в сфере миграции был изъят паспорт гражданина РФ.</p>\n<p>Это положение касалось также и несовершеннолетних детей таких граждан. Новый предел изъятия российских паспортов у соотечественников &mdash; до 1 января 2010 года считается оптимальным, поскольку в 2009 году было завершено формирование системы миграционного учета иностранных граждан и лиц без гражданства и количество паспортов, полученных на основании подложных документов или заведомо ложных либо недостоверных сведений, крайне незначительно.</p>\n<p>Упрощенный порядок получение гражданства подразумевает рассмотрение заявки без учета требований о проживании на территории России со дня получения вида на жительство и до дня обращения с заявлениями о приеме в гражданство в течение пяти лет непрерывно, без подтверждения наличия законного источника средств к существованию и без проверки владения русским языком.</p>', NULL, '', '', NULL, NULL, 1),
(86, 0, 1567772952, 'Более 500 украинских студентов ежегодно получают квоты на учебу в России', '', '<p>МОСКВА, 6 авг - РИА Новости.&nbsp;Российский центр науки и культуры (РЦНК) на Украине активно работает, ежегодно там более 500 студентов получают квоты на обучение в РФ, сообщила в интервью журналу &laquo;Международная жизнь&raquo;&nbsp;и&nbsp;радио Sputnik&nbsp;руководитель Россотрудничества Элеонора Митрофанова.</p>\n<p>&laquo;Динамичный, посещаемый (центр) на Украине. С Украины мы набираем более 500 студентов на обучение по квотам, центр там работает, там и кружки. В общем, большая востребованность&raquo;, - сказала она.</p>\n<p>По словам Митрофановой, работа в странах СНГ является приоритетом для федерального агентства.</p>\n<p>&laquo;В СНГ во всех странах, кроме Грузии, есть культурные центры. СНГ является приоритетом нашей работы, поэтому мы очень много внимания уделяем странам СНГ и стараемся работать не только в центрах, где находимся в столицах по межправительственному соглашению, но и работать на периферии &ndash; со школами, университетами, местной общественностью&raquo;, &mdash;&nbsp;отметила глава Россотрудничества.</p>\n<p>Ежегодно Россия выделяет 15 тысяч бюджетных мест для иностранных граждан, говорится на официальном сайте агентства. Функции единого оператора по отбору кандидатов на обучение выполняют Россотрудничество и его представительства за рубежом.</p>\n<p>&nbsp;</p>', NULL, '', '', NULL, NULL, 1),
(87, 0, 1567773039, 'МВД разработает порядок депортации опасно больных иностранцев', '', '<p>Министерство внутренних дел предложило подготовить порядок исполнения решений о депортации и административном выдворении из страны иностранных граждан и лиц без гражданства, страдающих заболеваниями, представляющими опасность для окружающих.</p>\n<p>Уведомление о начале разработки проекта поправок в ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo; опубликовано на портале проектов нормативных правовых актов.</p>\n<p>Разработчик отмечает, что сейчас отсутствует документ, регламентирующий порядок и правила перевозки больных депортируемых, их медицинское обеспечение, а также требования по соблюдению мер личной и общественной гигиены, правила поведения в общественных местах и контроль за их исполнением.</p>\n<p>Также законодательством не установлены полномочия федеральных органов исполнительной власти по определению такого порядка.</p>\n<p>Отсутствие правового акта в указанной области негативно сказывается на санитарно-эпидемиологической обстановке и гигиенической безопасности населения, говорится в описании проекта.</p>', NULL, '', '', NULL, NULL, 1),
(88, 0, 1567773323, 'Минтруд предлагает перераспределить квоты на иностранных работников в России', '', '<p>12 августа. FINMARKET.RU - Министерство труда и социальной защиты России вынесло на общественное обсуждение проект поправок в ведомственный приказ, который установил распределение квот для выдачи разрешений на работу иностранцам в 2019 году.</p>\n<p>Документ опубликован на официальном портале для размещения информации о подготовке нормативных актов в понедельник. Согласно поправкам, общее число квот в целом по России останется без изменений - 144 тыс. 583. Тем не менее,&nbsp;квоты перераспределяются по федеральным округам и субъектам в пределах этой цифры, и увеличивается резерв общероссийской квоты.</p>\n<p>В частности, квоту для Москвы предлагается увеличить на 10,6% - с 7 тыс. 555 до 8 тыс. 357 штук. Согласно документу, квота увеличивается во многом за счет рабочих, занятых на строительно-монтажных и ремонтно-строительных работах: с 3 тыс. 240 до 3 тыс. 497 (на 257 человек), а также за счет руководителей разных уровней (на 87 человек), других профессий квалифицированных рабочих (на 55 человек) и др.</p>\n<p>Многократно увеличивается квота для Республики Крым: с 15 до 274. Увеличение планируется произвести за счет &laquo;иных профессионально-квалификационных групп&raquo;&nbsp;- с 10 до 218.</p>\n<p>Как говорится в пояснительной записке к проекту, корректировка квоты осуществлена на основании предложений органов исполнительной власти регионов &laquo;с учетом сложившегося положения на рынке труда, демографической ситуации, принципа приоритетного использования национальных трудовых ресурсов, возможностей обустройства иностранных граждан&raquo;.</p>\n<p>Между тем, в большинстве субъектов, согласно документу, общее число квот снижено за счет увеличения резерва общероссийской квоты.</p>\n<p>Так, число квот увеличивается для регионов Центрального федерального округа (с 25 тыс. до 26,2 тыс.), Сибири (с 14,3 тыс. до 15,9 тыс.), уменьшается для Уральского (с 13,2 тыс. до почти 11,8 тыс.), Дальневосточного (с 60 тыс. до 53,4 тыс.) и Северо-Западного федеральных округов (с 12,5 тыс. до 12,1 тыс.), остается на прежнем уровне для Приволжского федерального округа (7,0 тыс.) и не меняется для Северного Кавказа (не упомянут в поправках).</p>\n<p>Одновременно резерв квоты в России увеличивается с 8 тыс. до 14,1 тыс. штук.</p>\n<p>Проект приказа Минтруда проходит процедуру общественного обсуждения до 26 августа, процедуру антикоррупционной экспертизы до 18 августа.</p>', NULL, '', '', NULL, NULL, 1),
(89, 0, 1567774178, 'В России за полгода поставили на учет почти 3 млн прибывших для работы иностранцев', '', '<p>МОСКВА, 16 августа. /ТАСС/. Порядка 3 млн иностранных граждан, прибывших с целью трудоустройства, были поставлены на миграционный учет в России в первом полугодии года. Это следует из показателей миграционной ситуации в Российской Федерации за январь - июнь 2019 года, которые опубликованы на&nbsp;сайте&nbsp;МВД.</p>\n<p>Всего в первом полугодии на миграционный учет в России поставлены свыше 8,7 млн иностранцев, из них почти 1,5 млн человек прибыли с туристическими целями, свыше 234 тыс.&nbsp;человек - для учебы, свыше 445 тыс. - с частной целью. Таким образом, практически каждый третий иностранец, поставленный на учет, приехал в Россию для трудоустройства, почти половина из них приходится на Центральный федеральный округ, где зарегистрированы более 3,4 млн иностранных граждан, свыше 1,3 млн из которых встали на миграционный учет с целью работы.</p>\n<p>На втором месте - Северо-Западный федеральный округ, где на учет в первом полугодии было взято более 1,9 млн иностранцев, но половина из них - свыше 836. тыс. - это туристы, а для работы на северо-запад страны приехали лишь более 619 тыс. человек.</p>\n<p>Третье место занимает Приволжский федеральный округ, где в первом полугодии на миграционный учет встали свыше 711 тыс. человек, 230 тыс. из них - для трудоустройства.</p>\n<p>В Сибири было зарегистрировано порядка 707 тыс. иностранцев, 190 тыс. из них приехали для работы.</p>\n<p>В Южном федеральном округе из 673 тыс. поставленных на учет иностранцев свыше 66 тыс. - туристы, почти 160 тыс. приехали для работы. На Урале встали на миграционный учет свыше 33 тыс. иностранных граждан, около 200 тыс. из них для последующего трудоустройства.</p>\n<p>На Дальний Восток для работы приехали 137 тыс. иностранцев, всего в этом федеральном округе в первом полугодии было зарегистрировано порядка 580 тыс. иностранных граждан.</p>', NULL, '', '', NULL, NULL, 1),
(90, 0, 1567774219, 'ФСБ впервые раскрыла данные о приезжающих на учебу в Россию иностранцах', '', '<p>Погранслужба&nbsp;ФСБ&nbsp;впервые опубликовала статистические данные об иностранцах, которые приехали на учебу в Россию. Об этом сообщает принадлежащее&nbsp;Григорию Березкину&nbsp;РБК.</p>\n<p>Всего за первые два квартала 2019 года в страну въехали 181,5 тысяч студентов. Больше всего учащихся приехали из Казахстана (59,3 тысячи), Китая (21,2 тысячи), Узбекистана (14 тысяч) и Украины (13,7 тысячи).</p>\n<p>Всего в России учатся приезжие из 174 стран. Так, из Великобритании к нам приехали 767 человек, из Белоруссии &mdash; 231, из Канады &mdash; 99, по три человека с Мальты и из Гондураса и один гражданин Исландии. Также в страну приезжают граждане Ирана, Индии, Южной и Северной Кореи, Египта, Марокко, Вьетнама и США.</p>\n<p>ФСБ с начала года ведет статистику по учету иностранных граждан, приезжающих учиться в Россию. Это произошло после того, как&nbsp;Росстат&nbsp;в 2018 году обновил приказ, утверждающий статистическое наблюдение ФСБ за иностранцами. Помимо учебы также добавился сбор данных о работе.</p>\n<p>По данным Минобрнауки, в начале 2018 года в России было более 274 тысяч иностранных студентов. Кроме того, по данным министерства, первое место по числу учащихся занял Казахстан (65,6 тысячи), второе &mdash; Туркменистан (27,4 тысячи), третье &mdash; Узбекистан (25,7 тысячи), на четвертом&nbsp;&mdash; Китай (18,3 тысячи).</p>\n<p>Ранее&nbsp;стало известно, что с начала 2019 года в Россию приехали работать более двух миллионов иностранцев. Больше всего трудовых мигрантов прибыло из Узбекистана (918 тысяч), Таджикистана (523,9 тысячи), Киргизии (265 тысяч) и Украины (164,6 тысячи).</p>', NULL, '', '', NULL, NULL, 1),
(91, 0, 1567774311, 'В Узбекистане трудовым мигрантам выделят квартиры в новостройках', '', '<p>Принят указ президента&nbsp;&laquo;О мерах по дальнейшему усилению гарантий защиты граждан Узбекистана, осуществляющих временную трудовую деятельность за рубежом, и членов их семей&raquo; от 20 августа текущего года. Об этом&nbsp;сообщает&nbsp;официальный телеграм-канал Министерства Юстиции Узбекистана.</p>\n<p><strong>Указом предусмотрено:</strong></p>\n<p>- в 2020 году выделить трудовым мигрантам, нуждающимся в улучшении жилищных условий, 3 462 квартиры в многоквартирных домах в рамках реализации государственных программ по строительству жилья;</p>\n<p>- внедрить механизм оплаты трудовым мигрантам платежей по кредитам, полученным на территории Узбекистана, в российских рублях через Акционерное общество &laquo;Азия инвест банк&raquo; и другие российские банки-партнеры, в том числе путем осуществления расчетов посредством электронных платежных систем.</p>\n<p>С 1 октября 2019 года:</p>\n<p>- будет внедряться практика предоставления микрозаймов сроком на 1 год, с ускоренным оформлением его выдачи в течение 1 банковского дня с пакетом документов (паспорт, заявление и ходатайство органа по труду), на оплату расходов, связанных с трудоустройством за рубежом, в размере 2 млн. сум, в 2019&ndash;2020 гг. &ndash; по ставке в размере 10% и в 2021г. &ndash; 12%;</p>\n<p>- страховым компаниям будут предоставляться единовременные субсидии на покрытие суммы страховой премии по страхованию жизни и здоровья граждан и других рисков в период осуществления ими временной трудовой деятельности за рубежом 1 раз в год в размере 50 тыс. сум. на каждого застрахованного трудового мигранта.</p>\n<p><strong>Согласно Указу:</strong></p>\n<p>- будет образована Республиканская комиссия по вопросам внешней трудовой миграции;</p>\n<p>- также создан в структуре Кабинета Министров Узбекистана Департамент по вопросам защиты прав и поддержки граждан Узбекистана, осуществляющих временную трудовую деятельность за рубежом;</p>\n<p>- до 1 октября 2019 года в рамках разрабатываемой информационной системы &laquo;Единая национальная система труда&raquo; будет внедрен программный комплекс &laquo;labor-migration&raquo;;</p>\n<p>Предусмотренные Указом кредиты, субсидии, микрозаймы и квартиры в многоквартирных домах, будут предоставляться трудовым мигрантам, зарегистрированным в программном комплексе &laquo;labor-migration&raquo;.</p>', NULL, '', '', NULL, NULL, 1),
(92, 0, 1567774358, 'В Петербурге собираются ввести «Электронную карту мигранта»', '', '<p>В настоящее время в Петербурге работают над созданием &laquo;Электронной карты мигранта&raquo;. Об этом рассказал первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политике в Петербурге Вадим Окрушко на заседании&nbsp;межведомственной комиссии по профилактике правонарушений в Смольном.</p>\n<p>Целью этого проекта является создание в городе единого механизма контроля за&nbsp;миграционными потоками и оперативного достоверного учета трудовых мигрантов.</p>\n<p>Карта будет представлять собой пластиковый документ со встроенным чипом, содержащим персональные данные о гражданине, его фотографию, отпечатки пальцев, место работы,&nbsp;информацию о налоговых платежах и другие важные сведения.</p>\n<p>Отмечается, что организация единой системы контроля позволит исключить подделку документов. Аналогичная система уже несколько лет успешно работает в других странах &mdash; Германии, Канаде, Нидерландах.</p>\n<p>Также на заседании выступил заместитель председателя комитета по труду и занятости населения Петербурга Петр Тищенко, который рассказал, что в экономике города в настоящее время работает более трех миллионов человек &mdash; это самый высокий уровень занятости населения в стране при самом низком уровне безработицы. При этом по ряду специальностей наблюдается дефицит кадров, который необходимо восполнять за счет привлеченных специалистов (обычно это наименее востребованные у петербуржцев профессии в сфере строительства, ремонта, обрабатывающих производств, общественного питания и торговли).</p>\n<p>В свою очередь вице-губернатор Петербурга Владимир Кириллов сообщил, что, по данным проведенных исследований, большинство горожан положительно оценивает состояние межнациональных отношений в городе, и характеризует ситуацию как значительно более благоприятную, чем в целом по России.</p>\n<p>&laquo;Одной из важных составляющих данной работы должно быть обеспечение контроля за предоставлением работодателями нормальных жилищно-бытовых условий для нанятых ими мигрантов. Эти люди не должны оставаться без правовой и социальной помощи. Решение данных вопросов требует согласованных действий правоохранительных органов и исполнительных органов государственной власти&raquo;, &mdash; заявил Кириллов.</p>\n<p>Кроме того, в&nbsp;целях борьбы с фиктивными адресами регистрации иностранных граждан, информация о так называемых &laquo;резиновых квартирах&raquo; заносится в &laquo;черный список&raquo; СПб ГКУ &laquo;МФЦ&raquo;. После чего производится блокировка этих&nbsp;адресов, и документы на регистрацию по ним в &laquo;МФЦ&raquo; не принимаются.</p>', NULL, '', '', NULL, NULL, 1),
(93, 0, 1568979125, 'В Петербурге введут новые правила контроля для трудовых мигрантов', '', '<p class=\"font_8\"><span>В Петербурге разрабатывается принципиально новая система учета иностранных работников.</span></p>\n<p class=\"font_8\"><span>Уже с сентября миграционный контроль будут проходить не только все въезжающие в Петербург трудовые мигранты из-за рубежа, но и все, кто покидает город. Кроме того, для иностранных рабочих разрабатывается Электронная карта мигранта.</span></p>\n<p class=\"font_8\"><span>- В 2019 году за первые полгода в Петербурге было оформлено свыше 300 тысяч патентов, а на миграционный учет поставлено более трех миллионов человек (включая туристов. -&nbsp;Прим. ред.). Большинство мигрантов попадают в крупные города легальным способом. А когда заканчивается срок безвизового пребывания, становятся нелегалами, - говорит руководитель Департамента развития Паспортно-визового центра МВД России Максим Щепелев.</span></p>\n<p class=\"font_8\">Нарушителей как в Петербурге, так и по всей стране традиционно выявляют в ходе рейдов.</p>\n<p class=\"font_8\">Самый крупный за последнее время был проведен на Апраксином рынке в середине лета, когда за один день было выявлено более 300 иностранных граждан, незаконно находящихся на территории России.</p>\n<p class=\"font_9\"><span>Система миграционного контроля в России есть, и, в принципе, она позволяет отслеживать нарушителей.</span></p>\n<p class=\"font_9\"><span>- Другой вопрос, что после реорганизации ФМС посты миграционного контроля в пограничных пропускных пунктах сокращены и поэтому регистрация осуществляется Пограничной службой ФСБ. В этих условиях взаимодействие двух силовых ведомств играет важную роль в организации учета миграционных потоков. В настоящее время существует институт закрытия въезда, который позволяет профилактировать подобные нарушения, - сообщил Владимир Зорин, заместитель директора Института современных языков, межкультурной коммуникации и миграции РУДН, главный научный сотрудник Института этнологии и антропологии имени Миклухо-Маклая.</span></p>\n<p class=\"font_9\"><span>В частности, если иностранный мигрант превысил срок своего пребывания в России или дважды привлекался к административной ответственности, то въезд в страну ему закрывают на три года.</span></p>\n<p class=\"font_9\"><span>Закрытие въезда - массовая история. Суды Петербурга и Ленинградской области завалены исковыми заявлениями от иностранных граждан, которым запретили въезжать в страну, например, за то, что дважды их поймали с пивом на улице. Суд встает на сторону миграционных служб: еще в Пулково при прохождении контроля иностранцев предупреждали, что в Петербурге пиво на улице пить не принято.</span></p>\n<p class=\"font_8\"><span>На нелегальном положении может находиться до одной четверти от всех въехавших в Россию трудовых мигрантов.</span></p>\n<p class=\"font_9\"><span>И хотя система, в принципе, эффективна, вся мощь миграционного контроля направляется на тех, кого выявили, задержали в ходе рейдов или случайной проверки документов на улице. Но выявляют не всех. Многие годами избегают встречи с сотрудниками правоохранительных органов. Сколько их? Владимир Зорин склоняется к мнению, что на нелегальном положении может находиться до одной четверти от всех въехавших в Россию трудовых мигрантов. В целом в стране речь может идти о 2,3 миллиона человек. Сколько из них находится в Петербурге, не скажет ни один эксперт, но, очевидно, речь идет о сотнях тысяч человек.</span></p>\n<p class=\"font_8\">- Подавляющее большинство нелегальных мигрантов работает в сфере теневой экономики. Законопослушные работодатели крайне редко используют труд нелегалов в легальных сферах деятельности, - говорит Зорин.</p>\n<p class=\"font_9\"><span>Новая система контроля, которая вводится в Петербурге, призвана как раз находить тех, кто живет в тени. На совещании в Смольном, посвященном работе над новой системой, говорилось о создании единой базы данных по трудовым мигрантам.</span></p>\n<p class=\"font_9\"><span>У нововведений две цели: снизить число правонарушений со стороны мигрантов и защитить самих иностранных граждан, ведь практика показывает, что когда у них истекает срок пребывания в стране и они \"залегают на дно\", их начинают обманывать с зарплатой, вымогать деньги, а иногда и просто грабить. Обратиться в полицию или прокуратуру эти люди не могут.</span></p>\n<p class=\"font_9\"><span>После первой же проверки документов их депортируют и закроют им въезд в страну.</span></p>\n<p class=\"font_9\"><span>А еще нелегалы кормят целую армию посредников, которые позволяют им создавать видимость законного пребывания в городе. Верхушка айсберга - поддельные справки о временной регистрации по месту жительства, купить их предлагают возле каждой станции метро. Еще продают санитарные книжки, разрешения на работу, миграционные карты. Периодически такие фирмы обнаруживают, на владельцев и сотрудников заводят уголовные дела, но липовые справки продолжают пользоваться спросом.</span></p>\n<p class=\"font_9\"><span>Чтобы сделать всю эту систему невыгодной в первую очередь самим иностранным работникам, Смольный начал разрабатывать Электронную карту мигранта. На чипе карты будут храниться данные паспорта, номер патента, разрешение на работу. Сейчас все эти документы мигрант должен каждый день носить с собой, поскольку отсутствие любого из них - повод для задержания и установления личности.</span></p>\n<p class=\"font_9\"><span>Есть те, кто использует поддельные документы. Если сейчас при встрече с сотрудником полиции некоторые пытаются предъявить, например, фальшивую миграционную карту, то когда будет введена Электронная карта мигранта, подделка станет невозможной.</span></p>\n<p class=\"font_8\">То, что переход на базу данных реально способен исправить ситуацию, показывает история с патентами. Миграционный юрист Андрей Фоменко приводит пример: когда патенты только появились, их массово подделывали. С 2013 года заработал сервис проверки разрешений и патентов на работу, можно проверить действительность любого документа. В результате патенты подделывать практически перестали.</p>\n<p class=\"font_9\"><span>Правда, возникает вопрос, как будет действовать карта, ведь для считывания информации нужно соответствующее оборудование. Андрей Фоменко говорит, что полицейские, проверяющие документы у мигрантов, еще могут получить соответствующие гаджеты, но, например, работодатель, к которому устраивается иностранец, просто не сможет считать данные с его карты. Тем не менее эксперт считает, что сама по себе карта может быть удобной для мигрантов, если она сможет заменить собой другие документы. По крайней мере, иностранному работнику нужно будет носить при себе меньше бумаг.</span></p>\n<p class=\"font_9\"><span>В Паспортно-визовом центре МВД России уточняют: сама по себе карта идентификатором личности стать не сможет, поэтому мигрантам все же придется вместе с ней носить паспорт.</span></p>\n<p class=\"font_9\"><span>Тем не менее опрошенные \"РГ\" эксперты склоняются к тому, что создаваемая в Петербурге система способна вывести из тени значительное число мигрантов и если не разрушить, то хотя бы подорвать институт посредников, который паразитирует на нелегалах. Правда, чтобы система заработала эффективно, важно, чтобы похожие мероприятия проводились и в других регионах, ведь если в Петербурге требования миграционного контроля ужесточат, а в Ленинградской области они останутся теми же, то весь смысл нововведений может потеряться.</span></p>', NULL, '', '', NULL, NULL, 1),
(94, 0, 1568979636, 'Для российских таксистов ужесточат правила приема на работу', '', '<p class=\"font_9\"><span>Принимать водителей на&nbsp;работу в&nbsp;такси можно будет только после проверки по&nbsp;базам данных на&nbsp;предмет наличия судимостей, лишения права управления транспортом, а&nbsp;также алкогольной и&nbsp;наркотической зависимости. С&nbsp;такой инициативой обратились в&nbsp;государтсвенно-правовое управление президента России сразу несколько крупных агрегаторов заказов.</span></p>\n<p class=\"font_9\"><span>Представители крупных игроков рынка, среди которых &laquo;Яндекс.Такси&raquo;, &laquo;Максим&raquo;, &laquo;Везет&raquo; и&nbsp;некоторых других считают, что проверка по&nbsp;базам данных положительно скажется на&nbsp;безопасности перевозок пассажиров. &laquo;Замечания сформированы в&nbsp;рамках обсуждения закона о&nbsp;такси, который Госдума должна рассмотреть во&nbsp;втором и&nbsp;третьем чтениях в&nbsp;осеннюю сессию&raquo;,&nbsp;&mdash; сообщают &laquo;Известия&raquo; со&nbsp;ссылкой на&nbsp;письмо агрегаторов в&nbsp;ГПУ президента.</span></p>\n<p class=\"font_9\"><span>Авторы письма отмечают: в&nbsp;ходе обсуждений законопроекта о&nbsp;такси они уже направляли свои предложения в&nbsp;Госдуму РФ, но&nbsp;не&nbsp;встретили там поддержки. Впрочем, в&nbsp;профсоюзе таксистов не&nbsp;верят в&nbsp;то, что агрегаторы заинтересованы в&nbsp;безопасности поездок: для них главное доходность, а&nbsp;все остальное вторично.</span></p>\n<p class=\"font_9\"><span>Также возникают вопросы, смогут&nbsp;ли бизнесмены обеспечить безопасность персональных данных на&nbsp;водителей. Кроме того, эксперты отмечают, что новая система отбора водителей закроет доступ к&nbsp;работе в&nbsp;такси для иностранных граждан: если россиян проверить по&nbsp;базам сейчас не&nbsp;сложно, то&nbsp;к&nbsp;иностранным базам доступ, скорее всего, окажется закрытым.</span></p>', NULL, '', '', NULL, NULL, 1),
(95, 0, 1568980836, 'Платежная система «Мир» готовится запустить денежные переводы за границу', '', '<p class=\"font_9\"><span>Платежная система &laquo;Мир&raquo;&nbsp;вводит новую услугу. В ближайшее время с карт национальной платежной системы можно будет осуществлять переводы за границу.</span></p>\n<p class=\"font_9\"><span>Российская платежная система &laquo;Мир&raquo;&nbsp;уже осенью начнет тестирование трансграничных переводов с российских карт на карты, выпущенные иностранными банками. Об этом сообщил глава Национальной системы платежных карт Владимир Комлев.</span></p>\n<p class=\"font_9\"><span>На первом этапе введения новой услуги деньги можно будет перечислять на карты банков, выпущенных в странах СНГ. Затем переводы можно будет делать и в страны дальнего зарубежья.</span></p>\n<p class=\"font_9\"><span>По словам Комлева, &laquo;Мир&raquo;&nbsp;сможет предоставить своим клиентам более выгодный курс при переводах. Эксперты прогнозируют, что оборот будет исчисляться миллиардами рублей.</span></p>\n<p class=\"font_9\"><span>Как отмечают &laquo;Известия&raquo;, возможность отправить деньги с карты &laquo;Мир&raquo;&nbsp;на карту иностранного банка будет важна для граждан других государств, работающих в России.</span></p>', NULL, '', '', NULL, NULL, 1),
(96, 0, 1568982021, 'Иностранным выпускникам вузов РФ станет проще получить гражданство', '', '<p><span>В среду стало известно, что МВД намерено значительно упростить порядок получения российского гражданства иностранными студентами, окончившими с отличием российские вузы. Уведомление о разработке соответствующего законопроекта вчера было размещено на федеральном портале проектов нормативных правовых актов.</span></p>\n<p><span><span>Правда, нововведение коснется только выпускников государственных вузов и только тех, кто \"грыз гранит науки\" по дневной форме обучения.</span></span></p>\n<p>&nbsp;</p>\n<p>Законопроектом рассматриваемой категории иностранных граждан и лиц без гражданства предоставляется право приобрести российское гражданство в упрощенном порядке при наличии регистрации по месту жительства либо состоянии на учете по месту пребывания на территории Российской Федерации. То есть как в случае получения разрешения на временное проживание и вида на жительство, так и в отсутствие таких документов.</p>\n<p>Дело в том, в настоящее время выпускники российских вузов вправе приобрести российское гражданство в упрощенном порядке при наличии вида на жительство и трудовой деятельности не менее трех лет.</p>\n<p>Таким образом, после окончания обучения иностранцы остаются в нашей стране в статусе временно пребывающих. До получения вида на жительство им необходимо ещё получить разрешение на временное проживание.</p>\n<p>\"Планируется срок приобретения российского гражданства указанной категорией лиц сократить до одного года\", - сообщила официальный представитель МВД России Ирина Волк.</p>', NULL, '', '', NULL, NULL, 1),
(97, 0, 1569486710, 'Новый закон об упрощении порядка предоставления разрешения на временное проживание и вида на жительство иностранным гражданам', '', '<p><span>На днях президент РФ подписал новый закон 2019 года об упрощенном получении РВП и ВНЖ для некоторых категорий иностранных граждан.</span></p>\n<h2>Новый закон об РВП с 2019 года</h2>\n<p><span>Согласно новому закону, с 2019 года будет сокращен срок рассмотрения заявлений о выдаче РВП с полугода до четырех месяцев.</span><br /><br /><span>По этому закону некоторые категории иностранных граждан смогут&nbsp;</span>получить РВП<span>&nbsp;без квоты. В том числе:</span></p>\n<ul>\n<li>иностранные граждане, родившиеся в РФ;</li>\n<li>иностранные граждане, состоящие в законном браке с гражданами РФ и имеющие место жительства в РФ;</li>\n<li>иностранные граждане, являющиеся участниками государственной программы по добровольному переселению соотечественников;</li>\n<li>иностранные граждане, поступившие на военную службу в РФ на срок службы;</li>\n<li>иностранные граждане, осуществившие инвестиции в РФ в установленном правительством размере;</li>\n<li>иностранные граждане, являющиеся гражданами государств СССР и получивших высшее профессиональное образование в РФ;</li>\n<li>граждане Украины или лица без гражданства, постоянно проживающие на территории Украины, признанные беженцами либо получившие временное убежище на территории РФ;</li>\n<li>иностранные граждане, переселяющиеся в РФ на постоянное место жительства в соответствии с международными договорами о регулировании процесса переселения и защите прав переселенцев.</li>\n</ul>\n<p><span>Кроме того, закон об РВП говорит, что разрешение на временное проживание несовершеннолетним иностранным гражданам, а также недееспособным совершеннолетним иностранным гражданам будут выдавать на срок РВП его родителя или опекуна.</span><br /><br /></p>\n<h2>Новый закон о ВНЖ с 2019 года</h2>\n<p><span>Согласно новому закону, с 2019 года иностранцам будет предоставляться бессрочный&nbsp;</span>вид на жительство<span>, на текущий момент его выдают на срок до 5 лет.</span><br /><br /><span>Кроме иностранных граждан со статусом ВКС и членов их семей, поскольку им вид на жительство будет выдаваться только на срок действия разрешения на работу - до 3 лет.</span><br /><br /><span>Кроме того, новый закон о ВНЖ в РФ от 2019 года расширил круг лиц, которые будут иметь возможность получить ВНЖ без получения РВП. К ним относятся:</span></p>\n<ul>\n<li>иностранные граждане, которые родились на территории РСФСР и которые в прошлом имели гражданство СССР;</li>\n<li>несовершеннолетние иностранные граждане, родители которого постоянно проживают в РФ;</li>\n<li>несовершеннолетние иностранные граждане, получающие ВНЖ вместе с родителями / опекунами;</li>\n<li>иностранные граждане, имеющие родителей или детей, которые являются гражданами РФ и постоянно проживают в России.</li>\n<li>признанные недееспособными иностранные граждане, получающие ВНЖ вместе с родителями/опекунами или имеющие родителей/опекунов с действующим видом на жительство;</li>\n<li>иностранные граждане, признанные&nbsp;носителями русского языка;</li>\n<li>иностранные граждане, депортированные с территории Крымской автономной союзной республики и их родственники по прямой линии;</li>\n<li>высококвалифицированные специалисты, работающие в РФ не менее полугода и члены их семей;</li>\n<li>иностранные граждане, успешно освоившие в РФ имеющую государственную аккредитацию образовательную программу высшего образования по очной форме обучения и получившие документ об образовании и о квалификации с отличием;</li>\n<li>лица, проживающие в РФ, у которых было прекращено&nbsp;гражданство РФ;</li>\n</ul>\n<p><strong>Важно!</strong><span>&nbsp;Новый закон о ВНЖ и РВП от 2 августа 2019 года вступит в силу через 90 дней.</span></p>', NULL, '', '', NULL, NULL, 1),
(98, 0, 1569488666, 'Почти 1000 узбекистанцев могут остаться без гражданства!', '', '<p><span>Немногие знают, что при выезде из Узбекистана за границу с целью постоянного проживания, необходимо встать на консульский учет в посольстве. Иногда, некоторые граждане пренебрегают этим правилом и в случае возвращения на Родину, гражданство может быть признано утраченным, что повлечет за собой множество юридических проблем.</span></p>\n<p><span><span>Совсем недавно, посольство Узбекистана в России опубликовало список из более чем 1000 человек, которым грозит потеря узбекского гражданства. В нем размещены люди, которые выехали из Узбекистана более 3 лет назад, но до сих пор не встали на учет в консульстве и не уведомили о смене гражданства.</span></span></p>', NULL, '', '', NULL, NULL, 1),
(99, 0, 1569505475, 'Пакет законопроектов о едином подходе к экзамену для иностранцев внесён в Госдуму', '', '<p class=\"font_9\"><span>Правительство России внесло на рассмотрение в Государственную Думу пакет законопроектов о едином подходе к проведению комплексного экзамена для иностранных граждан. Документы имеются в распоряжении &laquo;Парламентской газеты&raquo;.</span></p>\n<p class=\"font_9\"><span>Документ позволит обеспечить единые подходы проведения комплексного экзамена в России за её пределами для иностранцев для подтверждения ими владения русского языка, истории России и основ законодательства РФ, его порядок и критерии и требования к нему.</span></p>\n<p class=\"font_9\"><span>Законопроектом предполагается установить эффективный контроль и надзор за образовательными организациями, проводящими экзамен, повысить качество его проведения. Помимо этого, будет установлен срок действия выдаваемого по итогам экзамена сертификата. Он будет зависеть от цели его получения: три года для разрешения на временное проживание, разрешения на работу или патента, бессрочно &mdash; для вида на жительство.</span></p>\n<p class=\"font_9\"><span>Эти поправки предложено внести в Федеральный закон &laquo;О правовом положении иностранных граждан в РФ&raquo;.</span></p>\n<p class=\"font_9\"><span>Также поправки вносятся в закон о предоставлении государственных и муниципальных услуг.</span></p>\n<p class=\"font_9\"><span>Сертификаты о прохождении экзамена предлагается исключить из списка документов, которые необходимо предоставлять при получении госуслуг, поскольку данные сертификатов будут внесены в Федеральный информационный реестр и их передачу можно будет осуществлять в электронном виде.</span></p>\n<p class=\"font_9\"><span>Кроме того, Правительство внесло поправки в КоАП, предусматривающие административное наказание в виде штрафа для сотрудников образовательных учреждений за нарушения требований к проведению экзамена для иностранных граждан.</span></p>\n<p class=\"font_9\"><span>Так, за нарушение методики расчёта стоимости экзамена или порядка и сроков хранения сертификатов должностных лиц предложено штрафовать в размере от пяти до 10 тысяч рублей, юридических лиц &mdash; от 50 до 100 тысяч рублей. За нарушение формы или порядка проведения экзамена предусматривается штраф должностным лицам в размере от 20 до 30 тысяч рублей, для юридических лиц &mdash; от 200 до 300 тысяч рублей.</span></p>\n<p class=\"font_9\"><span>Пакет законопроектов разработан во исполнение ряда поручений Президента России Владимира Путина.</span></p>\n<p class=\"font_9\"><span>О необходимости установления единого порядка проведения комплексного экзамена для иностранца и единых критериев отбора организаций, которые его проводят,&nbsp;высказывалась&nbsp;первый заместитель председателя Комитета Совфеда по конституционному законодательству и государственному строительству Людмила Бокова.</span></p>', NULL, '', '', NULL, NULL, 1),
(100, 0, 1569505554, 'Сбербанк подключил таджикские банки к сервису переводов по номеру телефона', '', '<p class=\"font_9\"><span>Сбербанк&nbsp;сообщил&nbsp;о подключении трех банков Таджикистана &mdash; &laquo;Душанбе Сити&raquo;, Коммерцбанка и &laquo;Спитамен Банка&raquo; &mdash; к сервису мгновенных переводов по номеру телефона. С октября к системе присоединится &laquo;Банк Эсхата&raquo;.</span></p>\n<p class=\"font_9\"><span>Услуга доступна в разделе переводов за рубеж по номеру телефона на карту или счет.</span></p>\n<p class=\"font_9\"><span>Комиссия составляет 1%. Максимальная сумма перевода &mdash; 50 тыс. руб. При этом производить операцию можно в рублях, сумма автоматически будет сконвертирована в валюту карты.</span></p>\n<p class=\"font_9\"><span>В Сбербанке заявили, что переводы будут производиться быстрее. На данный момент сервис доступен для пользователей Android, в конце октября 2019 года он появится на iOS.</span></p>\n<p class=\"font_9\"><span>Ранее к сервису мгновенных переводов Сбербанка по номеру телефона присоединились Тинькофф Банк, Совкомбанк, СДМ-банк, а также &laquo;Связной&raquo;, &laquo;Евросеть&raquo; WebMoney, МТС, Проинвестбанк и &laquo;МОБИ.Деньги&raquo;.</span></p>\n<p class=\"font_9\"><span>Напомним, по данным Центробанка, в России доля пользователей дистанционных услуг банков превысила 55%. Почти на 20% увеличилось количество клиентов, которым стали доступны мгновенные переводы.</span></p>', NULL, '', '', NULL, NULL, 1),
(101, 0, 1581387218, 'Требования к сдаче экзамена для иностранных граждан станут едиными по всей России:', '', '<p style=\"text-align: justify;\">Госдума приняла в первом чтении правительственный законопроект о введении единой процедуры проведения комплексного экзамена по русскому языку как иностранному, истории России и основам законодательства РФ для иностранцев.<br /> <br /> Согласно тексту, такой экзамен должен проводиться осуществляющими образовательную деятельность организациями из специального перечня, утвержденного Министерством образования и науки .<br /> <br /> Сегодня субъекты РФ самостоятельно утверждают порядок и форму проведения комплексного экзамена, а также список организаций, которые его проводят.<br /> <br /> Министерством также определяется, какие на экзамене должны использоваться контрольные измерительные материалы - комплексы заданий стандартизированной формы, разработанные на основе требований к минимальному уровню знаний.<br /> <br /> Формы и порядок проведения комплексного экзамена, требования к минимальному уровню знаний, необходимых для сдачи экзамена, порядок и сроки хранения материалов его проведения, а также формы сертификата и технические требования к нему, согласно законопроекту, также должны утверждаться на федеральном уровне, а не на региональном, как сейчас.<br /> <br /> Сроки действия выдаваемого сертификата о владении русским языком, необходимого для получения разрешения на временное проживание или вида на жительство и разрешения на работу, утверждаются правительством.<br /> <br /> В законопроекте уточняется, что требования к уровню знаний устанавливаются в зависимости от цели иностранного гражданина.</p>', NULL, '', '', NULL, NULL, 1),
(102, 0, 1581387777, 'С 1 ноября вид на жительство в России стал бессрочным :', '', '<p style=\"text-align: justify;\">До изменений в законодательство вид на жительство выдавался на 5 лет с возможностью продления.<br /> <br /> Теперь постоянно проживающий в России иностранец, имеющий вид на жительство, обязан будет каждый год подавать уведомление о подтверждении своего проживания в РФ в орган внутренних дел, в том числе в электронной форме. По истечении каждого пятого года уведомление подается только лично. Если непрерывно в течение любых двух календарных лет иностранец не пошлет такое уведомление, вид на жительство будет аннулирован<br /> <br /> Для иностранных высококвалифицированных специалистов и членов их семей вид на жительство будет выдаваться на срок действия их разрешения на работу.</p>', NULL, '', '', NULL, NULL, 1),
(103, 0, 1581387829, 'Правительство России установило допустимую долю приема на работу иностранных граждан в 2020 году.', '', '<p style=\"text-align: justify;\">Отмечается, что допустимая доля иностранных работников, используемых в различных отраслях экономики, устанавливается с учетом региональных особенной рынка.<br /> <br /> В частности, в сферах деятельности сухопутного пассажирского и автомобильного грузового транспорта допустимая доля составляет 26% общей численности работников, в области спорта - 25%, в сфере выращивания овощей - 50%. Отмечается, что в 2020 г. норма будет распространена на всю территорию РФ (в 2019 г. она не распространяется на Астраханскую область и Приморский край).<br /> <br /> Кроме того, в 2020 году допустимая доля в сфере строительства сохранится на уровне 80% общей численности работников. &laquo;Эта норма не будет распространяться на территории Республики Бурятия, Амурской области и Москвы, а на территории Республики Дагестан составит 50% (в 2019 г. она не распространяется на территории Москвы, Хабаровского края и Амурской области)&raquo;, &mdash; сообщается на сайте правительства.<br /> <br /> Мигранты не смогут заниматься розничной торговлей в нестационарных торговых объектах, в аптеках, на рынках, а также прочей торговлей вне магазинов, палаток и рынков &mdash; в соответствующих категориях установленная правительством допустимая доля иностранных граждан равна 0%.</p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(104, 0, 1581387875, 'Вступил в силу новый закон, в Госдуму внесен новый закон по НРЯ', '', '<p>Начало ноября 2019 года &mdash; значимое событие для миграционного законодательства в России. Вступил в силу самый ожидаемый пакет поправок в законодательство РФ. Расширился перечень оснований для получения разрешения на временное проживание вне квоты и вида на жительство, минуя РВП. Таким образом, российские власти облегчили процесс оформления РВП и ВНЖ для иностранных граждан. Сокращены и сроки оформления и выдачи соответствующих документов.</p>\n<p><strong>РВП по новому закону <br /> <br /> </strong></p>\n<p>РВП по браку можно будет оформляться только по месту жительства супруга (супруги).</p>\n<p>Теперь заявление о получении разрешения на временное проживание иностранных граждан, пребывание которых в России требует получения визы, будут рассматриваться не 6, а <strong>4 месяца.</strong></p>\n<p><strong>&nbsp;</strong></p>\n<p><strong>Новые основания получения РВП без квоты</strong></p>\n<p><strong>РВП без квоты теперь можно получить:</strong></p>\n<ul>\n<li>Несовершеннолетним детям, родители которых уже имеют разрешение на временное проживание. Ранее РВП ребенку можно было оформить только совместно с родителем. Упрощен порядок выдачи РВП и недееспособным , чьи опекуны проживают на территории РФ и уже имеют РВП.</li>\n<li>Гражданам Украины (а также лицам без гражданства, которые постоянно проживали на территории Украины) при наличии временного убежища или статуса беженца.</li>\n<li>Переселяющимся на постоянное место жительство в РФ в соответствии с международными договорами РФ о регулировании процесса переселения и защите прав переселенцев.</li>\n</ul>\n<p><strong>&nbsp;</strong></p>\n<p><strong>РВП выпускникам российских ВУЗов.</strong></p>\n<p>Выпускникам российских ВУЗов разрешение на временное проживание будет выдано без квоты. А тем, кто окончил ВУЗ с красным дипломом, смогут получить сразу вид на жительство!</p>\n<p>&nbsp;</p>\n<p><strong>Новые основания аннулирования РВП</strong></p>\n<p>В законе появился новый пункт, в соответствии с которым &nbsp; можно добровольно подать заявление о его аннулировании.</p>\n<p>Так же обратите внимание на то, что теперь РВП аннулируется, если гражданин находится за территорией РФ <strong>6 месяцев суммарно за год</strong> (а не непрерывно, как это было ранее), за исключением&nbsp;болезни,&nbsp;смерти&nbsp;близкого&nbsp;родственника&nbsp;и&nbsp;служебной&nbsp;необходимости&nbsp;(законодатель не даёт определение служебной необходимости, предположительно, это командировка).</p>\n<p>&nbsp;</p>\n<p><strong>ВНЖ по новому закону</strong></p>\n<p><strong>&nbsp;</strong></p>\n<p>Теперь граждане смогут оформить ВНЖ&nbsp; без предварительного оформления разрешения на временное проживание. Изменения коснутся порядка и сроков подачи ежегодного уведомления. А вид на жительство станет бессрочным!</p>\n<p>По новому закону вид на жительство, минуя РВП, смогут получить те, кто родился в РСФСР и имел в прошлом гражданство СССР. Упрощения коснутся и лиц, которые сами либо их близкие родственники были подвергнуты незаконной депортации из Крымской АССР; высококвалифицированных работников, имеющих полугодовой трудовой стаж; окончивших ВУЗы&nbsp; с красным дипломом; лиц&nbsp; лишенных гражданства.</p>\n<p><strong>Кто сможет получить ВНЖ по новым правилам минуя РВП?</strong></p>\n<p><strong>Теперь вид на жительство, минуя разрешение на временное проживание, выдаётся:</strong></p>\n<ul>\n<li>Иностранному гражданину, родившемуся на территории РСФСР, и состоявшему в прошлом в гражданстве СССР.</li>\n<li>Несовершеннолетнему иностранному гражданину, родитель (усыновитель, опекун или попечитель) которого уже имеет ВНЖ.</li>\n<li>Иностранному гражданину, имеющему родителя, сына или дочь постоянно проживающих и состоящих в гражданстве РФ (данным лицам не нужно будет сдавать экзамен по русскому языку). Нельзя будет &nbsp;получить ВНЖ по ребенку тем, кто лишен родительских прав.</li>\n<li>Признанным в соответствии с законодательством иностранного государства недееспособными либо ограниченными в дееспособности совершеннолетние, родители (усыновители, опекуны, попечители) которых постоянно проживают на территории РФ, если они получают совместно ВНЖ (теперь без ограничений) или если опекуны уже его имеют.</li>\n<li>Депортированным с территории Крымской АССР и их родственникам по прямой восходящей и нисходящей линиях.</li>\n<li>Квалифицированным специалистам, осуществлявшим на протяжении 6 месяцев трудовую деятельность по профессии, включенной в специальный перечень.</li>\n<li>Закончившим высшее учебное заведение с государственной аккредитацией, по очной форме обучения и получившим &nbsp;документ об образовании и квалификации с отличием.</li>\n<li>Лицам, проживающим в РФ и, у которых прекращено гражданство.</li>\n</ul>\n<p>&nbsp;</p>\n<p><strong>Изменены сроки уведомления по ВНЖ</strong></p>\n<p>Ежегодное уведомление так же можно будет направить по почте или подать лично как раньше, но теперь <strong>в течение 2 месяцев с момента истечения очередного.</strong></p>\n<p>Лично нужно подавать уведомление каждые 5 лет. Вместо продления ВНЖ теперь раз в пять лет необходимо лично подать уведомление о проживании.</p>\n<p><strong>Вид на жительство станет бессрочным!</strong></p>\n<p>Теперь вид на жительство бессрочный (за исключение ВНЖ для высококвалифицированных специалистов). Документы на его оформление, если вы идёте через РВП, надо подать через 8 месяцев после получения РВП, но <strong>за 4 месяца до его конца.</strong></p>\n<p>Вид на жительство нужно будет менять как паспорт: в 14, 20 и 45 лет или при изменении данных, гражданства, пола, непригодности документа, ошибки.&nbsp; ВНЖ старого образца можно будет&nbsp; заменить на новый&nbsp; бессрочный до истечения действия нынешнего вида на жительство.</p>\n<p><strong>Новые основания для аннулирования ВНЖ</strong></p>\n<p>Аннулируется ВНЖ теперь не только по уже имеющимся основаниям, но и по дополненным:</p>\n<ul>\n<li>По заявлению данного иностранного гражданина.</li>\n<li>В случае не уведомления о подтверждении проживания в течение 2 лет.</li>\n<li>Нельзя также находиться за пределами &nbsp;РФ в течение 6 месяцев суммарно за год. В этом случае ВНЖ может быть аннулирован.</li>\n<li>ВНЖ высококвалифицированного специалиста аннулируется, если после расторжения трудового или гражданско-правового договора в течение 30 дней не был заключен новый. Если же был заключен, то нужно будет получать новое разрешение на работу и новый вид на жительство.</li>\n</ul>', NULL, '', '', NULL, NULL, 1),
(105, 0, 1581387952, 'Юридическую поддержку мигрантам Санкт-Петербурга и Ленинградской области предоставляют юристы-адвокаты совершенно бесплатно!', '', '<p style=\"text-align: justify;\">Благодаря поддержке Фонда Президентских Грантов мигрантам Санкт-Петербурга и Ленинградской области в 2019 году предоставлена возможность совершенно бесплатно&nbsp;получить не только квалифицированную юридическую помощь по миграционному и трудовому праву, но и на безвозмездной основе получить защиту в судах.</p>\n<p style=\"text-align: justify;\">Юридическую консультацию на безвозмездной основе предоставляют юристы-адвокаты коллектива Фонда \"ОРИЕНТИР\",&nbsp;а также юристы-стажеры ведущих ВУЗов Санкт-Петербурга, обучающихся на юридическом факультете, таких как: Университет Правосудия (РГУП), Северо-Западный Институт Управления РАНХиГС, Полицейский колледж, СПбГУ и РГПУ им. А.И. Герцена.</p>\n<p style=\"text-align: justify;\">Стоит отметить, что Фонд \"ОРИЕНТИР\" с июня по октябрь вёл работу по проекту \"Правовая приемная выходного дня\", который предполагал оказание юридической помощи мигрантам Санкт-Петербурга в нерабочие дни. Такая концепция была одобрена городской администрацией и приветствовалось Комитетом по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</p>\n<p style=\"text-align: justify;\">С июля 2019 года консультации также оказываются по будням и в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(106, 0, 1581388016, 'Новость про гражданство !', '', '<p style=\"text-align: justify;\">Власти планируют в перспективе разрешить иностранцам, желающим стать гражданами России, не отказываться от иного гражданства. Это указано в материалах правительства, размещенных вместе с проектом федерального бюджета на 2020&ndash;2022 годы.<br /> <br /> Одним из основных направлений политики РФ в сфере миграции является обеспечение простоты и прозрачности условий приобретения гражданства России, следует из утвержденной&nbsp;президентом в прошлом году концепции миграционной политики на 2019&ndash;2025 годы.<br /> <br /> Сегодня для получения российского паспорта в общем порядке иностранцы должны подать заявление об отказе от имеющегося у них гражданства в уполномоченные органы другого государства. Отказ не требуется, если это предусмотрено международным договором России или если это невозможно в силу не зависящих от человека причин. Кроме того, отказ необязателен при получении гражданства РФ в упрощенном порядке, который предусмотрен, например, для тех граждан иностранных государств, у которых хотя бы один родитель имеет российское гражданство и живет в России.</p>', NULL, '', '', NULL, NULL, 1),
(107, 0, 1581388370, 'Цивилизованная миграция - Правовое просвещение и бесплатные консультации от Фонда \"ОРИЕНТИР\" ', '', '<p style=\"text-align: justify;\">25 ноября в 18 часов в Концертном зале \"У Финляндского\" (Арсенальная наб. д.13/1) состоялось информационно-культурное мероприятие для иностранных граждан и петербуржцев \"Под крылом Петербурга\", на котором Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках проекта \"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\", финансируемого Фондом-оператором Президентских грантов.</p>\n<p style=\"text-align: justify;\">В ходе мероприятия присутствовали специалисты органов государственной власти Санкт-Петербурга (социальной защиты, образования, здравоохранения, культуры и молодежной политики), а также сотрудники правоохранительных органов и представители социально ориентированных некоммерческих организаций, которые осуществляли бесплатное консультирование мигрантов по вопросам образования, трудоустройства, здравоохранения и налогообложения, а также по вопросам культурного досуга и профессионального образования.</p>\n<p style=\"text-align: justify;\">В рамках мероприятия состоялись бесплатные консультации и семинар правовой грамотности от Фонда \"ОРИЕНТИР\" по вопросам миграционного законодательства и трудового права.</p>\n<p style=\"text-align: justify;\">Как получить временную регистрацию? Где оформить патент на работу? Куда обратиться за медицинской помощью? На эти и другие вопросы мигрантов отвечали специалисты отделов администрации Калининского района Санкт-Петербурга, представители налоговой инспекции, а также управления по вопросам миграции.</p>\n<p style=\"text-align: justify;\">Для справки - Калининский район является одним из крупнейших по численности административным районом Санкт-Петербурга. На территории района проживает 538 258 чел, площадь района - 4012 гектаров, протяженность территории в направлении с севера на юг - свыше 10 км.</p>\n<p style=\"text-align: justify;\">Калининский район крупный промышленный центр, в котором на сегодняшний день насчитывается более 290 крупных и средних промышленных предприятий. Около 30 из них входит в число ведущих предприятий Санкт-Петербурга и страны в целом.</p>', NULL, '', '', NULL, NULL, 1),
(108, 0, 1581388447, 'Как оформить РВП без квоты при наличии диплома?', '', '<p><strong>С 1 ноября 2019 года выпускники ВУЗов, техникумов и колледжей, вправе оформить РВП без квоты</strong></p>\n<p>Право на оформление РВП вне квоты получили выпускники&nbsp; &nbsp;российских учебных заведений,<strong> при условии, что они являются гражданами государства, которое ранее входило в состав СССР.</strong> В состав СССР входили: Армения, Грузия, Латвия, Литва, Эстония, Украина, Молдова, Азербайджан, Казахстан, Киргизия. Если Вы гражданин одной из указанных стран и получили на территории Российской Федерации образование, Вы вправе оформить РВП сроком на 3 года.</p>\n<p><strong>Какой диплом годится для получения РВП?</strong></p>\n<p>Согласно новому закону, право на получение разрешения на временное проживание вне квоты имеют иностранные граждане &laquo;<em>являющиеся гражданином государства, входившего в состав СССР, и получившие профессиональное образование в государственной образовательной организации высшего образования, государственной профессиональной образовательной организации или государственной научной организации на территории Российской Федерации, образовательным программам, по имеющим государственную аккредитацию&raquo;.</em></p>\n<p>Получить РВП можно не только на основании диплома о высшем образовании, но и в случае успешного окончания государственного колледжа, техникума и даже училища. Все эти виды учреждений реализуют профессиональные образовательные программы. Главное условие &mdash; образовательная организация должна быть государственной. Дипломы негосударственных образовательных учреждений не дают права на оформление РВП.</p>\n<p>Те, кто получил <strong>красный диплом</strong> смогут оформить сразу вид на жительство.</p>\n<p><strong>Аккредитация &nbsp;&mdash; как ее подтвердить?</strong></p>\n<p>Важно понимать, что аккредитацию имеет ни сама образовательная организация (ВУЗ, техникум, колледж), а образовательные программы, которые она реализует.</p>\n<p><strong>Как подтвердить аккредитацию образовательной программы, по которой Вы обучались?</strong></p>\n<p>Необходимо получить в ВУЗе справку, в которой должно быть указано на факт обучения по конкретной программе и сведения об аккредитации этой программы. К справке необходимо заказать <strong><em>копию лицензии образовательной организации и свидетельства об аккредитации образовательной деятельности.</em></strong></p>', NULL, '', '', NULL, NULL, 1),
(109, 0, 1581388504, 'Китайский остров. Жители Поднебесной скупают недвижимость на Васильевском острове', '', '<p><strong>Китайская диаспора активно создает в&nbsp;городе инфраструктуру \"для&nbsp;своих\". Закрытые спа&ndash;салоны, гостиницы, которые прячутся за&nbsp;вывеской \"дом народного творчества\", ночные клубы, медицинские центры и&nbsp;даже школы. Около 80% этого малого бизнеса сконцентрировано на&nbsp;Васильевском острове. Эксперты отмечают, что&nbsp;большая часть подвальных помещений недалеко от станции&nbsp;метро \"Василеостровская\" и&nbsp;1&ndash;й линии уже выкуплена китайцами. </strong></p>\n<p>Китайские рестораны, бары и несколько магазинов уже давно на слуху у местных жителей, но это лишь верхушка айсберга, о существовании многих заведений они даже не слышали и не могли их посетить. Адреса многих парикмахерских, игровых клубов, медицинских центров, продуктовых магазинов, массажных кабинетов передаются с помощью современных гаджетов через мобильное приложение WeChat. Подробные карты, коды домофонов, контактные телефоны жители Поднебесной пересылают своим через своих.Не владеющему китайским языком просто не понять, о чем идет речь.</p>\n<p>Среди таких заведений \"ДП\" известны \"Dao Yitang. Традиционный китайский магазин оздоровительного массажа\", Институт традиционной китайской медицины (Клиника Солнце), чайная Unicorn Milk Tea Shop, караоке K&ndash;BOX, игровой клуб Yike Lounge, парикмахерская Institut Eben, множество торговых точек и клубов. Почти все они сосредоточены на Васильевском острове, который скоро может превратиться в Chinatown.</p>\n<p>Кроме того, появляются всё новые рестораны. Недавно, например, открылся A little tea. В Петербурге есть и домашние школы, где обучение организовано по китайской программе.</p>\n<p>\"Они создают мир для себя. Формально китайцы редко в реальности нарушают закон, прямых заявлений на них очень мало. В этом и выражается устойчивость такой диаспоры: никто почти об этом бизнесе не знает, кроме самих членов диаспоры\", &mdash; отмечает профессор НИУ ВШЭ китаевед Алексей Маслов.</p>\n<p><strong>Заведения только для&nbsp;своих</strong></p>\n<p>Председатель Петербургской ассоциации гидов&ndash;переводчиков c китайского Виктория Баргачева рассказала \"ДП\", что таких заведений в городе много, они создаются для закрытого этносообщества. \"Их сложно подсчитать &mdash; на то и теневая экономика. Некоторые заведения без вывески, за железной дверью, а вход &mdash; по звонку. Есть магазины китайских товаров повседневного спроса рядом с \"<span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/55776/\" target=\"_blank\">Прибалтийской</a></span>\", там торгуют без российской маркировки и растаможки. Они привозят все в чемоданах\", &mdash; отмечает Баргачева.</p>\n<p>Бизнес в сфере услуг, по мнению эксперта, не слишком прибыльный, но именно создание замкнутой инфраструктуры отвечает задачам диаспоры и в конечном итоге выгодно. По оценкам отдельных экспертов, совокупная прибыль этих малых компаний достигает 1 млрд рублей.</p>\n<p>\"Эта инфраструктура зарабатывает и на продаже документов: видов на жительство, прописки, создании юрлиц\", &mdash; считает Баргачева. Многие разрешения на работу выдаются в Василеостровском и Адмиралтейском районах &mdash; там сосредоточен бизнес и больше всего выходцев из Поднебесной. Эксперты сходятся во мнении, что значительное влияние на предпринимателей в этой локации может оказывать гендиректор Российско&ndash;китайского бизнес&ndash;парка Чэнь Чжиган.</p>\n<p>По данным <span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/52050/\" target=\"_blank\">Петростата</a></span>, в 2018&ndash;м миграционный приток жителей Китая в Петербург &mdash; порядка 120 тыс. человек. Алексей Маслов отмечает, что для китайской деловой миграции Северная столица &mdash; одно из главных российских направлений.</p>\n<p>По мнению эксперта, в Москве китайский бизнес не так сконцентрирован. Это объясняется жестким контролем за мигрантами и дороговизной входа на рынок. \"В Россию приезжают не самые богатые, чаще всего &mdash; не очень удачливые дельцы, которые находятся на стадии обслуживания своих земляков\", &mdash; считает синолог.</p>\n<p>Маслов отмечает, что в Китае малый и средний бизнес практически полностью освобожден от налогов. \"Их условия в десятки раз лучше, чем российские. Те, кто приезжает сюда, скорее всего, не сумели воспользоваться преференциями, то есть у них есть какие&ndash;то проблемы. К нам идет очень маленький и маргинализированный капитал\", &mdash; считает эксперт. При этом самым прибыльным направлением у китайцев считаются игровые и ночные клубы.</p>\n<p><strong>Уйти от&nbsp;уплаты налогов</strong></p>\n<p>Во многих заведениях, созданных китайцами, оплата идет через системы WeChat pay и Alipay. Однако не всегда они установлены правомерно. \"Везде, где существуют официальные терминалы приема платежей WeChat pay и Alipay, эти транзакции абсолютно легальные. В Москве с ними работают <span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/13778/\" target=\"_blank\">ЦУМ</a></span> и <span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/2893895/\" target=\"_blank\">ГУМ</a></span>, в Петербурге &mdash; <span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/2915508/\" target=\"_blank\">ДЛТ</a></span>, их устанавливают в аэропортах. Сама по себе схема таких платежей абсолютно легальна, но она может быть и непрозрачной\", &mdash; говорит исполнительный директор туристической ассоциации \"Мир без границ\" Александр Агамов.</p>\n<p>В июле 2017&ndash;го банк \"<span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/company/38057/\" target=\"_blank\">Русский стандарт</a></span>\" организовал в России доступ к Alipay &mdash; в первую очередь для быстрой и безопасной оплаты покупок в сегменте класса люкс. И только через год был организован прием платежей через WeСhat pay.</p>\n<p>Установкой системы занимаются четыре российских оператора: ООО \"Крафтек\", Sendy, АО \"КБ \"Солидарность\" и \"Русский стандарт\". Но многие китайские компании не устанавливают системы через них, а оплату проводят с помощью qr&ndash;кода с одного кошелька на другой. Это не всегда значит, что средства ушли в Китай. \"Деньги могли остаться и в российском обороте, и уйти, но в любом случае &mdash; мимо кассы. Об этом говорят туроператоры, гиды. WeChat по всему миру продвигают свою систему легально, значит, нужно выйти на диалог и сделать так, чтобы нельзя было перевести напрямую. Даже если предприятие легализовано, фактически это его уход от уплаты налогов\", &mdash; отмечает Агамов.</p>\n<p><strong>Видим, но&nbsp;не&nbsp;делаем</strong></p>\n<p>Эксперты убеждены, что Васильевский становится классическим чайна&ndash;тауном. Алексей Маслов полагает, что в дальнейшем власти поймут, что такие замкнутые зоны невыгодны и опасны, как было в Европе. Тогда России придется разработать меры по размыванию этого бизнеса. \"Это будет очень болезненно и дорого для государства\", &mdash; отмечает он.</p>\n<p>Ряд комитетов Смольного сообщили \"ДП\", что работают исключительно с официальным бизнесом и не могут прокомментировать \"китайский квартал\". В управлениях МВД России по Василеостровскому району и ФСБ России по Петербургу и Ленобласти тоже заявили, что вопрос не попадает под их полномочия, в Василеостровском МВД предположили, что темой может заниматься администрация района.</p>\n<p>Руководство же района заявило \"ДП\", что не имеет ни полномочий лицензирующего органа, ни разрешительных функций в отношении предпринимательства. \"У нас также отсутствуют полномочия для регионального госнадзора и контроля экономической деятельности, что исключает право проведения проверок юр&ndash; и физлиц. Поэтому у нас нет сведений о действующих на территории организациях, принадлежащих гражданам КНР\", &mdash; сообщили там.</p>\n<p>Отстаивать остров решили местные жители. Васильевский остров пестрит объявлениями с призывом реагировать на незаконное открытие заведений гостей из Поднебесной.</p>\n<p>Председатель городского комитета по межнациональным отношениям и реализации миграционной политики <span style=\"text-decoration: underline;\"><a href=\"http://whoiswho.dp.ru/cart/person/1936393/\" target=\"_blank\">Олег Капитанов</a></span> отмечает, что китайцы в городе на Неве появились еще в 1999&ndash;м, а недовольство местных жителей вызывают только приехавшие недавно и продолжающие жить по собственным законам. \"В большей мере это актуально для Василеостровского района, исторического места компактного проживания китайцев\", &mdash; сказал он. Ничего предосудительного в том, что китайские бизнесмены открывают парикмахерские, кафе и рестораны, чиновник не видит. \"Главное, чтобы они работали в легальном поле, давали рабочие места нашим гражданам и платили налоги в бюджет. В таком случае у петербуржцев не будет к ним претензий\", &mdash; продолжает он. Комитет обещает доработать \"Единую карту мигранта\", по которой можно будет в реальном времени узнавать, чем занимается иностранный гражданин, где живет и работает. Видимо, тогда горожанам расскажут правду о том, где работают китайцы.</p>\n<p><strong>В контексте</strong></p>\n<p>Нам не страшен чайна&ndash;таун, скажет коренной петербуржец, сославшись на изначально интернациональный характер Северной столицы России, подаренный ее основателем Петром I. Действительно, до революции 1917 года в городе, в том числе и компактно, проживали представители многих диаспор. Достаточно будет для примера вспомнить немецкую. Но тогда это были сплошь европейцы. А как же быть, если вдруг на Васильевском острове действительно состоится \"шокирующая Азия\"?</p>\n<p>На самом деле чайна&ndash;тауны (китайские кварталы) существуют во многих крупных городах мира, в том числе, например, в европейских Париже и Лондоне. Есть и в столице США Вашингтоне. Это районы, где работает множество национальных ресторанов, магазинов, сувенирных лавок и т. п., открытых для всех и вся. Здесь проходят яркие фестивали и художественные акции, привлекающие и местных жителей, и туристов. Казалось бы, это вполне подходит и Петербургу с его почетным званием культурной столицы России.</p>\n<p>Есть, правда, одно существенное но. Китайские кварталы в европейских и американских городах появились еще 150, а то и 200 лет назад, и свой нынешний благообразный и привлекательный вид приобрели отнюдь не сразу. Пройден долгий путь, начавшийся с сосредоточия трущоб, опиумных курилен, злачных заведений и рассадников мафиозной преступности. Все это победить, трансформировав чайна&ndash;тауны в чуть ли не культурные заповедники, удалось кропотливой и постоянной работой властей по преобразованию клоповников в нечто хотя бы более&ndash;менее цивилизованное и чистенькое.</p>\n<p>И вот возникает вопрос: если действительно Васильевский остров так вот снизу, с подвалов и дворовых флигелей, закрытых для горожан некитайского происхождения парикмахерских и клубов, превратится в чайна&ndash;таун, сколько лет и усилий потребуется для преображения его потом в оазис цивилизации и туристическую Мекку? Похоже, что власти Петербурга эта проблема вообще не интересует. По крайней мере, так можно предположить по тому \"футболу\", который различные городские и василеостровские официальные инстанции устроили журналистам \"ДП\", такой вопрос задавшим.</p>\n<p>Кстати, по оценкам экспертов, численность китайской диаспоры в мире составляет 35&ndash;80 млн человек, проживающих более чем в 140 странах мира. Ее суммарный годовой доход превышает $1 трлн. Так называемое китайское экономическое чудо последних десятилетий было бы невозможно без эмигрантов, расселение которых вне территории Поднебесной поощряется властями КНР, которые жестко лимитируют переводы денежных средств за рубеж, но полностью сняли ограничения на денежные переводы из&ndash;за границы в Китай.</p>', NULL, '', '', NULL, NULL, 1),
(110, 0, 1581388552, 'Для РВП - нужна ли справка о несудимости и где ее достать? Данная справка требуется только визовикам!', '', '<p style=\"text-align: justify;\">Требование справки о несудимости до сих пор имеет место быть. Обойти это требование едва ли получится, поскольку информация о криминальном прошлом иностранца может быть для проверки запрошена не только в базах ГИАЦ, но и через международные каналы.<br /> <br /> Где взять и как получить в России справку о несудимости гражданину другой страны?<br /> <br /> Иностранным гражданам за справкой можно также обращаться в консульский отдел при посольстве своего государства либо в свое консульство.<br /> <br /> Их сотрудники передают запрос в правоохранительные органы государства, откуда приехал иностранец, оформляют документ о несудимости и пересылают его дипломатической почтой на территорию РФ.<br /> <br /> При этом может потребоваться нотариально заверенный перевод справки или проставление на ней апостиля.<br /> <br /> Срок действия справки об отсутствии судимости не установлен законодательно.<br /> <br /> Условие о сроке годности выдвигает тот, кто его запрашивает. Но из нашего опыта можем сказать, что справка такого типа на РВП действительна 3 месяца.</p>', NULL, '', '', NULL, NULL, 1),
(111, 0, 1581388594, 'Как получить квоту на РВП или ВНЖ в 2020 году', '', '<p><strong>Когда начнется прием заявлений на квоту?</strong></p>\n<p>Приём заявлений на 2020 год ещё не был официально объявлен. Направлять заявления на квоту в Комиссию , можно будет в том же порядке &mdash; почтой России, уже в первые рабочие дни после новогодних праздников.</p>\n<p><strong>Как увеличить шансы на получение квоты?</strong></p>\n<p>Шансы получить квоту значительно вырастут, если Вы подготовите качественный пакет документов. Заявление должно содержать как можно больше сведений, указывающих на вашу связь с Россией. Форма заявления установлена в Приложение № 3 к Приказу ГУ МВД России по г. Санкт-Петербургу и Ленинградской области от 21.11.2018 № 580, в заявление вносится самая основная информация о Вас.</p>\n<p>При необходимости вы вправе приложить к заявлению автобиографию, в которой&nbsp; максимально подробно отразить все то, что связывает Вас с Россией. Но каждый факт должен быть подтвержден документами. Документы должны быть представлены в надлежащем виде с переводом на русский язык. Копии заверены у нотариуса.</p>\n<p>По опыту 2019 году можно судить о том, какие обстоятельства увеличивают шансы на получение квоты. Это наличие родственников в России, в том числе проживающих на основании РВП и ВНЖ, постоянная работа, длительность проживания, законопослушное поведение, уплата налогов, положительные характеристики и т.п.</p>\n<p>Иными словами, если у вас нет других законных возможностей оформить своей пребывание в России, вы можете подать заявление на квоту. Если Вы прибыли в Россию только чтобы работать и более Вас с Россией ничего не связывает, то получить РВП по квоте будет весьма затруднительно. Если &nbsp;Вас удерживают прочные связи, &nbsp;&nbsp;&ndash; получить квоту вполне реально, нужно только подробно обосновать свою заявку. Те, кто собираются работать, тоже вправе обратиться за квотой.</p>\n<p><strong>Кому не дадут квоту?</strong></p>\n<p>Квоты не дадут тем, у кого есть два и более штрафа. Два и более штрафа в течение трех лет подряд являются основанием для установления запрета на въезд, а с запретом документы оформить невозможно. Кроме этого Комиссия возвращает без рассмотрения документы, если они неправильно оформлены. Например, в том случае, если заявление либо приложенные к нему документы не поддаются прочтению, содержат опечатки и неточности. Копии документов, исполненных на иностранном языке, необходимо перевести, а их копии &mdash; заверить у нотариуса.</p>', NULL, '', '', NULL, NULL, 1),
(112, 0, 1581388666, '«Сахар и соль мигрантам» - доклад Цивилизованной миграции', '', '<p>С 1 ноября 2019 г. вступит в силу Федеральный закон от 2 августа 2019 г. № 257- ФЗ &laquo;О внесении изменений в Федеральный закон от 25 июля 2002 г. № 115-ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo;, в связи с чем упрощается порядок предоставления некоторым категориям иностранных граждан и лиц без гражданства разрешения на временное проживание (РВП) и вида на жительство (ВНЖ) в Российской Федерации. Авторы законопроекта и депутаты уверены, что данный закон позволит сократить бюрократическую волокиту при узаконивании своего статуса в Российской Федерации гражданам, которые имеют российские корни, стремятся жить, получать образование и работать на территории страны.</p>\n<p>Благодаря поддержке Фонда Президентских Грантов мигрантам Санкт-Петербурга и Ленинградской области в 2019 году предоставлена возможность совершенно бесплатно получить квалифицированную юридическую помощь по миграционному и трудовому праву. Общественная приемная правового консультирования функционирует рядом с отделом по вопросам вынужденных переселенцев и беженцев Управления по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области (г. Санкт-Петербург, пр. Римского-Корсакова, д.39).</p>\n<p>В правовую приемную Фонда &laquo;ОРИЕНТИР&raquo;&nbsp; с ноября буквально посыпались вопросы по РВП и ВНЖ от иностранных граждан, в день примерно 15-20 обращений. Поскольку заявление о ВНЖ с 1 ноября 2019 года должно рассматриваться в течение четырех месяцев, а не полугода, как это было до того. При этом большинство заявителей вправе его подать не ранее, чем через восемь месяцев первого года пребывания в РФ. Упрощенный порядок предполагает не процедуру продления документа, а только подтверждение факта дальнейшего проживания на территории Российской Федерации.</p>\n<p>Но Управление по вопросам миграции ГУ МВД России по г. Санкт-Петербургу и Ленинградской области до сих пор документы не принимают, ссылаясь на то, что закон есть, а единого регламента ещё нет.</p>\n<p>Юристы Фонда &laquo;ОРИЕНТИР&raquo; ежедневно сталкиваются с различными ситуациями и стараются оказывать полную правовую поддержку мигрантам-соотечественникам, чтобы они как можно скорее адаптировались к условиям проживания и трудились на законных основаниях.</p>\n<p>Юридическую консультацию на безвозмездной основе предоставляют юристы-адвокаты коллектива Фонда &laquo;ОРИЕНТИР&raquo;, а также юристы-стажеры ведущих ВУЗов Санкт-Петербурга, обучающихся на юридическом факультете, таких как: Университет Правосудия (РГУП), Северо-Западный Институт Управления РАНХиГС, Полицейский колледж, СПбГУ и РГПУ им. А.И. Герцена.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; ( <a href=\"http://www.fondorientir.ru/\">http://www.fondorientir.ru/</a> ) осуществлял свою деятельность в рамках проекта \"\"Цивилизованная миграция\". Правовое просвещение и оказание юридической помощи трудовым мигрантам и мигрантам- соотечественникам Санкт-Петербурга и Ленинградской области\", финансируемого Фондом-оператором Президентских грантов.</p>\n<p>По всем вопросам обращаться по адресу: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В, по телефону \"горячей линии\": 8 (812) 385-69-89 или оставить письменное обращение по адресу в интернете http://www.fondorientir.ru/.</p>', NULL, '', '', NULL, NULL, 1),
(113, 0, 1581388701, 'в Смольном прошло заседание Консультативного совета по вопросам реализации государственной национальной политики при Правительстве Санкт-Петербурга', '', '<p>12 декабря в Смольном прошло заседание Консультативного совета по вопросам реализации государственной национальной политики при Правительстве Санкт-Петербурга. Представители органов исполнительной власти, национально-культурных автономий и религиозных объединений обсудили основные направления государственной политики в сфере межнациональных отношений и координации деятельности органов власти.</p>\n<p>Член Правительства Санкт-Петербурга &ndash; председатель Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге Олег Капитанов выступил с докладом о миграционной ситуации на территории Санкт-Петербурга в 2019 году и представил проект &laquo;Электронная карта мигранта&raquo;, который позволит городу контролировать&nbsp; иностранные трудовые потоки и повысит собираемость налогов.</p>\n<p>Председатель Комитета сообщил, что за 10 месяцев 2019 года в Санкт-Петербург прибыло 327&nbsp;738 иностранных граждан, которые указали в качестве цели въезда работу. Из них только 199&nbsp;615&nbsp;оформили трудовые патенты, то есть почти 130 тыс. человек находятся &laquo;в тени&raquo;. &laquo;Легальные&raquo; мигранты перечислили в бюджет Санкт-Петербурга за 10 месяцев 2019 года 5,8 млрд рублей. Еще 4,8 млрд рублей в казну не попали. Олег Капитанов предлагает&nbsp;комплексное решение проблемы:</p>\n<p>&laquo;Задача Комитета &ndash; разработать инструмент, который позволит иностранным гражданам максимально просто легализоваться и даст городу механизм контроля миграционных потоков. Таким решением является Электронная карта мигранта &ndash; пластиковая банковская карточка, которая заменит иностранцу множество бумажных документов: паспорт, ИНН, трудовой патент, свидетельство о регистрации по месту пребывания. С помощью ЭКМ и государственной информационной системы &laquo;Миграция&raquo; город сможет в режиме реального времени получать информацию обо всех трудовых мигрантах, находящихся в Санкт-Петербурге, контролировать поступление налога на доходы физических лиц, узнавать, оплатил ли мигрант трудовой патент&raquo;.</p>\n<p>Член Правительства Олег Капитанов подчеркнул, что с внедрением ГИС &laquo;Миграция&raquo; медицинское освидетельствование, тестирование по русскому языку и истории, а также экзамен на знание основ законодательства РФ из формальности, которую мигрантам за деньги помогают преодолеть многочисленные фирмы-посредники, превратится в реально работающий способ&nbsp;отбора лучших. При запуске ГИС &laquo;Миграция&raquo; и Электронной карты мигранта ситуация, когда иностранцы&nbsp;предоставляют контролирующим органам фиктивные документы, например, квитанции об оплате патента, станет невозможной. &nbsp;&nbsp;</p>\n<p>Запуск ГИС \"Миграция\" и проекта \"Электронная карта мигранта\" запланирован на 2020 год.</p>', NULL, '', '', NULL, NULL, 1),
(114, 0, 1581388766, 'Для Цивилизованной миграции в Санкт-Петербурге органы государственной власти объединяются с НКО', '', '<p style=\"text-align: justify;\">18 декабря в Санкт-Петербургском Доме национальностей (ул. Моховая, д. 15) состоялся круглый стол на тему &laquo;Взаимодействие органов государственной власти с социально ориентированными некоммерческими организациями, осуществляющими мероприятия по адаптации иностранных граждан в Санкт-Петербурге&raquo;, на котором приняли участие представители исполнительных органов государственной власти Санкт-Петербурга и выступили с докладом о деятельности своей&nbsp; организации Фонд &laquo;ОРИЕНТИР&raquo;, Фонд СиЭСЛО, БФ ПиРПСП, СПб РОО помощи социализации и языковой адаптации детей-инофонов &laquo;Дети Петербурга&raquo;, СПб ОООО &laquo;Российский Красный Крест&raquo;, ЕОЦ СПб и СПб ООТ &laquo;Аджам&raquo;.</p>\n<p style=\"text-align: justify;\">НКО было поддержано предложение Комитета по межпациональным отношениям и реализации миграционной политики в Санкт-Петербурге о создании Совета социально ориентированных некоммерческих организаций, осуществляющих мероприятия по адаптации иностранных граждан в Санкт-Петербурге при Комитете по межпациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</p>\n<p style=\"text-align: justify;\">Фонд &laquo;ОРИЕНТИР&raquo; привёл статистику о проделанной работе за 2019 год, где благодаря поддержке Фонда Президентских Грантов мигрантам Санкт-Петербурга и Ленинградской области в 2019 году предоставлена возможность совершенно бесплатно&nbsp;получить не только квалифицированную юридическую помощь по миграционному и трудовому праву, но и на безвозмездной основе получить защиту в судах.</p>\n<p style=\"text-align: justify;\">С июля 2019 года консультации оказываются по будням и в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(115, 0, 1581388808, 'Фонд \"ОРИЕНТИР\" провел итоговую в 2019 году выездную правовую приемную - Цивилизованная миграция', '', '<p>В пятницу 20 декабря при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" была проведена, по счёту уже десятая в 2019 году, выездная правовая приемная - Цивилизованная миграция.</p>\n<p>&nbsp;</p>\n<p>Выездная правовая приемная работала на территории Сенного рынка в Санкт-Петербурге, рядом с молельной комнатой, расположенной по адресу: Московский пр., дом 12. Приемная проводилась в течение двух часов, после пятничной молитвы, с 14:00 до 16:00. Прихожане молельной комнатой (мечети) могли задать любой интересующий вопрос юристам Фонда.</p>\n<p>Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано 39 человек. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда \"ОРИЕНТИР\", как избежать нарушения законодательства.</p>\n<p>&nbsp;</p>\n<p>РАСПРЕДЕЛЕНИЕ ОБРАЩЕНИЙ ПО КАТЕГОРИЯМ ВОПРОСОВ</p>\n<p>&nbsp;</p>\n<p>Вопросы связанные с РВП &ndash; 3 обращений</p>\n<p>Вопросы связанные с ВНЖ &ndash; 2 обращений</p>\n<p>Восстановление документов &ndash; 1 обращений</p>\n<p>Участие в программе переселения - 6 обращений&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>Написание обращений в суд &ndash; 1 обращений</p>\n<p>Вопросы связанные с патентом &ndash; 6 обращений</p>\n<p>Трудоустройство в СПб и ЛО &ndash; 5 обращений</p>\n<p>Оформление регистрации &ndash; 1 обращений</p>\n<p>Получение гражданства РФ &ndash; 11 обращений&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>Вопросы связанные с ДМС - 1 обращений</p>\n<p>Прочие вопросы &ndash; 1 обращений&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; .</p>\n<p>&nbsp;</p>\n<p>Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(116, 0, 1581390383, 'Новые законы для мигрантов в 2020 году', '', '<p><strong>Изменения в миграционном законодательстве &mdash; какие законы вступят в силу в 2020 году?</strong></p>\n<p>В 2019 году был принят масштабный пакет поправок к Федеральному закону &laquo;О правовом положении иностранных граждан&raquo;. &nbsp;Были ведены новые основания оформления РВП и ВНЖ, изменены сроки подачи уведомления о проживании и многое другое. Поправки к закону вступили в силу 1 ноября 2019 года, однако новые административные регламенты МВД еще не утверждены. Ожидается, что они вступят в силу сразу после нового года.</p>\n<p><strong>Какие законы будут приняты в 2020 году? </strong></p>\n<p><strong>Студенты смогут официально работать без патента и разрешения на работу</strong></p>\n<p>Возможно, что в 2020 году иностранным студентам разрешат официально работать без оформления патента или разрешения на работу. Государственная дума намерена освободить иностранных граждан, обучающихся в государственных колледжах, техникумах и ВУЗах от необходимости получения патента или разрешения на работу. Ранее, студентам для осуществления трудовой деятельности требовалось оформлять <strong>специальную рабочую визу</strong>. Законопроект одобрен в первом чтении. Принятие ожидается в 2020 году.</p>\n<p><strong>Носитель русского языка без экзамена</strong></p>\n<p>Госдума планирует освободить граждан Украины и Республики Беларусь от прохождения собеседования в комиссии при получении статуса носителя русского языка &mdash; НРЯ. Цель принятия закона &ndash; освободить белорусов и украинцев от бюрократических процедур по подтверждению ими уровня владения русским языком, так сказано в пояснительной записке к законопроекту. На данный момент законопроект прошел стадию первого чтения, принятие закона ожидается в 2020 году.</p>\n<p>Статус НРЯ присваивается комиссией по результатам собеседования с иностранным гражданином. В рамках собеседования проверяются навыки владения русским языком. Гражданам предлагают составит рассказ о себе, задают вопросы из грамматики, стилистики русской речи, проверяют знание русской литературы. Однако единой методики, порядка проведения собеседования, требований к контрольным материалам не установлено.</p>\n<p><strong>Государство возьмет под контроль порядок проведения экзаменов при получении патента, РВП, ВНЖ</strong></p>\n<p>Власти намерены ужесточить требования к проведению экзаменов по русскому языку, истории и основам законодательства РФ, а также контроль за выдачей сертификатов. Госдума предлагает установить не только единый порядок проведения собеседования, но и &nbsp;единую методику расчета стоимости экзамена, сроки хранения контрольных материалов, но и обязательность проведения проверок центров тестирования со стороны органов надзора и контроля. Одновременно вводится административная ответственность за нарушения порядка проведения собеседования.</p>\n<p><strong>Государственная дума намерена продлить гражданам, постоянно проживающим в Донецкой и Луганской области, сроки пребывания в Российской Федерации </strong></p>\n<p>Если законопроект будет принят жители ДНР и ЛНР получат право пребывать в России <strong>1 </strong>год и продлевать данный срок неограниченное количество раз без выезда за пределы РФ. Это позволит указанным граждан фактически непрерывно находится в РФ в течение неопределенного времени. На сегодняшний день срок пребывания иностранного гражданина в РФ не может быть более 90 суток суммарно в течение очередного 180 дневного периода. Данное правило распространяется на всех без исключений.</p>\n<p>Это основные изменения, принятие которых ожидается в следующем году. Информация приведена на основании данных о законопроектах, находящихся на рассмотрении Государственной Думы.</p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(117, 0, 1581390430, 'В Санкт-Петербурге Фондом \"ОРИЕНТИР\" реализуется социально-правовой проект - Цивилизованная миграция', '', '<p>31 декабря 2019 года завершается работа по 1 этапу социально-правового проекта \"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\", с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с Указом Президента Российской Федерации от 30 января 2019 года № 30 &laquo;О грантах Президента Российской Федерации, предоставляемых на развитие гражданского общества&raquo;.</p>\n<p>&nbsp;</p>\n<p>В рамках реализации проекта проведена работа по внедрению комплекса социальных мероприятий, направленных на адресное информирование, оказание консультационной и оперативной профессиональной юридической помощи гражданам, мигрантам, лицам без гражданства, беженцам, иностранным гражданам в критических ситуациях, а именно: потеря документов, невыплата заработной платы, отказ от оформления трудового договора, незаконное привлечение к дисциплинарной и административной ответственности, невыплата социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p>&nbsp;</p>\n<p>Основным инструментом механизма реализации социально-значимого проекта являлась организованная общественная приемная правового консультирования в устной и письменной форме для оказания юридической помощи на безвозмездной основе мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области по гражданскому и трудовому праву, миграционному законодательству (в т. ч. в целях предупреждения нарушений миграционного и трудового законодательства, удаления &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области). Всего оказано свыше 2700 устных и письменных консультаций, в досудебном претензионном порядке взыскано с работодателей свыше 3,1 млн рублей.</p>\n<p>Всего за весь период было проведено 12 выездных правовых приемных на безвозмездной основе на территории прилегающей к ООО &laquo;Единый центр документов&raquo;, расположенной по адресу ул. Красного Текстильщика, д. 10-12, а также на территориях Мечетей Санкт-Петербурга. Проконсультировано порядка 2,7 тыс. человек. Роздано информационных материалов свыше 100 тыс. штук, в том числе на таджикских и узбекских языках. Оказана помощь мигрантам в правильном заполнении заявлений на участие в программе переселения соотечественников, а также на получение патента. Направлены запросы в государственные органы для разрешения проблем мигрантов.</p>\n<p>&nbsp;</p>\n<p>Общественная приемная правового консультирования функционировала рядом с отделом по вопросам вынужденных переселенцев и беженцев Управления по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области (г. Санкт-Петербург, пр. Римского-Корсакова, д.39). А также в рамках реализации проекта была организована выездная работа общественной приемной по оказанию юридической помощи в защите законных прав и интересов трудовых мигрантов в городских мероприятиях &laquo;Под крылом Петербурга&raquo;, проводимых в различных районах города, где проведено 3 семинара правовой грамотности от Фонда \"ОРИЕНТИР\" по вопросам миграционного законодательства и трудового права.</p>\n<p>&nbsp;</p>\n<p>Помимо личных консультаций проектом было предусмотрено продвижение сайта &ndash; интернет-приемной для работы с обращениями, заявлениями, просьбами, связанными с юридической, социальной поддержкой и адаптацией трудовых мигрантов, иностранных граждан, беженцев и лиц без гражданства. Дистанционная юридическая помощь по таким обращениям была оказана устно или письменно по оставленным контактным сведениям (по номеру телефона или почтовому адресу), что увеличило численность благополучателей почти на 230 человек.</p>\n<p>&nbsp;</p>\n<p>В ходе работы с конкретными обращениями трудовых мигрантов при мере необходимости были привлечены специалисты органов исполнительной власти, таких как Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге, Комитет по местному самоуправлению, межнациональным и межконфессиональным отношениям Ленинградской области, Комитеты по труду и занятости Санкт-Петербурга и Ленинградской области, Управление по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области, а также Советы по межнациональным отношениям муниципальных образований Ленинградской области, Прокуратура Санкт-Петербурга и Ленинградской области, представители государственной трудовой инспекции, представители национальных диаспор, общин и землячеств Санкт-Петербурга и Ленинградской области.</p>\n<p>&nbsp;</p>\n<p>Для информационного сопровождения проекта были привлечены специалисты на возмездной основе для оказания услуг на высоком профессиональном уровне (юристы, адвокат, дизайнер, типограф, специалист по администрированию и сопровождению веб-сайта), а также задействованы СМИ Ленинградской области и Санкт- Петербурга, а также раздача листовок на территории самых оживленных мест Санкт-Петербурга и при выездной работе приемной в районах города, Соборной Мечети Санкт-Петербурга, на территории Ленинградской области.</p>\n<p>&nbsp;</p>\n<p>К реализации проекта были привлечены добровольцы (в т.ч. студенты СЗИУ РАНХиГС при Президенте Российской Федерации и Российского Государственного университета Правосудия в Санкт-Петербурге, кафедры гражданского и трудового права).</p>\n<p>&nbsp;</p>\n<p>По итогам реализации проекта осуществлен анализ и определена природа наиболее частых обращений мигрантов - соотечественников по вопросам их правовой поддержки. К их числу относятся вопросы, связанные с миграционным и трудовым законодательством, невыплата заработной платы, нарушения прав и свобод человека со стороны органов МВД (незаконное задержание под стражей от 3 суток и выше, намеренная порча документов, незаконное изъятие миграционных карт и паспортов иностранных граждан и т.п.).</p>\n<p>&nbsp;</p>\n<p>В результате проведенной по проекту работы повышен уровень правовой грамотности среди мигрантов-соотечественников, находящихся на территории Санкт-Петербурга и Ленинградской области и обратившихся за юридической помощью в общественную приемную (свыше 1900 человек лично и свыше 800 человек посредством телефонного и электронного обращения); снижена социальная напряженность среди этих мигрантов и оказано содействие в их комфортную интеграцию в единое правовое поле Санкт-Петербурга и Ленинградской области.</p>\n<p>&nbsp;</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.&nbsp;</p>\n<p>&nbsp;</p>\n<p>По всем вопросам обращаться по адресу: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В, по телефону \"горячей линии\": 385-69-89 или оставив письменное обращение по адресу в интернете <a href=\"http://www.fondorientir.ru\">http://www.fondorientir.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(118, 0, 1600776022, 'Фонд \"ОРИЕНТИР\" провел первую в 2020 году выездную правовую приемную - Цивилизованная миграция', '<p><strong>В пятницу 19 июня при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" была проведена первая в 2020 году выездная правовая приемная с просветительским семинаром - Цивилизованная миграция.</strong></p>', '<p>Выездная правовая приемная работала на территории второй Санкт-Петербургской мечети - Коломяжская мечеть (у неё два адреса: ул. Парашютная,7 и ул. Репищева, 1).</p>\n<p>Приемная проводилась в течение двух часов, после пятничной молитвы с 13:30 до 15:30, между тем 20 минутный семинар по насущным вопросам миграционного законодательства. Прихожане Коломяжской мечети могли задать любой интересующий вопрос юристам Фонда. Основными вопросами стали продление разрешительных документов по пребыванию в РФ и патентов на работу.</p>\n<p>Указом Президента № 392 &laquo;О внесении изменений в Указ Президента Российской Федерации от 18 апреля 2020 г. № 274 &laquo;О временных мерах по урегулированию правового положения иностранных граждан и лиц без гражданства в Российской Федерации в связи с угрозой дальнейшего распространения новой коронавирусной инфекции (COVID-19)&raquo; были продлены документы, срок действия которых заканчивается с 15 марта по 15 сентября, ровно на 185 дней (кроме патентов).</p>\n<p>Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано 42 мигранта. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории РФ, а также рекомендации от юристов Фонда, как избежать нарушения законодательства.</p>\n<p>Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(119, 0, 1600776101, 'Фонд \"ОРИЕНТИР\" провел вторую в 2020 году выездную правовую приемную - Цивилизованная миграция', '', '<p>Выездная правовая приемная работала на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом от Единым центром документов.</p>\n<p>Приемная проводилась в течение шести часов с 10:00 до 16:00. Несмотря на то, что полиция без особого разрешения из Смольного не разрешала возвести шатёр правовой приёмной &laquo;в полный рост&raquo;, а только установить баннер без опор и провести консультации юристов. Обратившийся мигранты могли задать любой интересующий вопрос юристам Фонда. Основными вопросами стали продление разрешительных документов по пребыванию в РФ и патентов на работу.</p>\n<p>&nbsp;С 27 июня иностранные граждане, которые хотят жить или работать в России, будут обследоваться на коронавирус при прохождении медкомиссии</p>\n<p>В соответствии с приказом Министерства здравоохранения N 384н от 29.06.2015, при прохождении медосмотра необходимо сдать анализы на туберкулез, лепру, сифилис, ВИЧ, бессимптомный инфекционный статус, вызванный вирусом иммунодефицита человека (ВИЧ). Однако 27 июня 2020 года в силу вступает новый приказ Министерства здравоохранения Российской Федерации от 15.06.2020 № 581н, в соответствии с которым список анализов был дополнен анализом на коронавирус.</p>\n<p>Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано 52 мигрантов. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории РФ, а также рекомендации от юристов Фонда, как избежать нарушения законодательства.</p>', NULL, '', '', NULL, NULL, 1),
(120, 0, 1600776172, 'Фонд \"ОРИЕНТИР\" проводит выездные приемные правового консультирования ', '<p><strong>В пятницу 26 июня и в понедельник 29 июня при поддержке Фонда президентских грантов Фондом \"ОРИЕНТИР\" были проведены выездные приемные правового консультирования с просветительским семинаром в Коломяжской мечети в рамках проекта - Цивилизованная миграция.</strong></p>', '<p>Одна выездная правовая приемная работала на территории второй по величине Санкт-Петербургской мечети - Коломяжская мечеть (у неё два адреса: ул. Парашютная, д.7 и ул. Репищева, д. 1) в пятницу 26 июня в течение двух часов, после пятничной молитвы с 13:00 до 15:00, между тем краткий семинар по насущным вопросам миграционного законодательства. Прихожане Коломяжской мечети могли задать любой интересующий вопрос юристам Фонда.</p>\n<p>Другая выездная правовая приемная работала 29 июня на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом от Единым центром документов, с 9:00 часов утра .</p>\n<p>Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 80 мигрантов. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории РФ, а также рекомендации от юристов Фонда, как избежать нарушения законодательства.</p>\n<p>&nbsp;МВД РФ выпустило методические рекомендации о порядке применения указа президента № 274 от 18.04.2020 г. с учетом последних изменений, внесенных в данный указ указом президента N392 от 15.06.2020 г., подробней с рекомендациями можно ознакомиться в новостной ленте на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>\n<p>&nbsp;</p>\n<p>Фонд \"ОРИЕНТИР\" осуществляет свою деятельность в рамках реализации проекта &nbsp;&nbsp;&nbsp;\"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(121, 0, 1600776236, 'Фонд \"Ориентир\" подводит итоги проекта - Цивилизованная миграция 2019/2020', '<p><img src=\"data:image/png;\" alt=\"\" /></p>', '<p>Итоговая и пятая по счёту выездная правовая приемная работала 30 июня на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом от Единым центром документов, в рамках проекта - Цивилизованная миграция 2019/2020.</p>\n<p>Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 35 мигрантов. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории РФ, а также рекомендации от юристов Фонда, как избежать нарушения законодательства</p>\n<p>&nbsp;Благодаря Фонду Президентских Грантов только за 2019-2020 годы была дана возможность более 100 тыс. мигрантам, проживающих и ведущих трудовую деятельность на территории Санкт-Петербурга и Ленинградской области, получить юридические консультации по миграционному и трудовому праву. Из них реально обратившихся в правовую приемную Фонда \"ОРИЕНТИР\" составляет более 4501, в том числе права мигрантов были представлены в судах и более 20 случаев по невыплате заработной платы.</p>\n<p>&nbsp;Юридическую консультацию на безвозмездной основе предоставляли юристы-адвокаты коллектива Фонда \"ОРИЕНТИР\", а также юристы-стажеры ведущих ВУЗов Санкт-Петербурга, обучающихся на юридическом факультете, таких как: Университет Правосудия (РГУП), Северо-Западный Институт Управления РАНХиГС, Полицейский колледж, СПбГУ и РГПУ им. А.И. Герцена.</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \"\"Цивилизованная миграция\". Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области\", финансируемого Фондом-оператором Президентских грантов. С информацией можно ознакомиться в новостной ленте на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществляет свою деятельность в рамках реализации проекта &nbsp;&nbsp;&nbsp;\"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, '', '', NULL, NULL, 1),
(122, 0, 1600776471, 'Гражданство РФ в упрощенном порядке', '<p><strong>24 июля 2020 вступает в силу Федеральный закон № 134-ФЗ от 24.04.2020 г. об упрощении процедуры приема в гражданство РФ</strong></p>', '<p>Согласно закону, с 24 июля иностранные граждане смогут обращаться с заявлением о приеме в гражданство РФ в упрощенном порядке. В частности, иностранцам не нужно будет отказываться от своего гражданства для вступления в гражданство РФ.</p>\n<p>Граждане Белоруссии, Казахстана, Молдовы и Украины, имеющие вид на жительство в РФ, смогут обратиться с заявлением о приеме в гражданство Российской Федерации в упрощенном порядке без соблюдения условия о сроке проживания.</p>\n<p>Кроме того, иностранные граждане, которые находятся в браке с гражданами РФ, проживают в России и имеют в браке общих детей, а также иностранные граждане, один из родителей которых имеет российское гражданство и живет в РФ смогут получить гражданство РФ в упрощенном порядке &ndash; им не нужно будет находиться в России не менее трех лет и подтверждать источник дохода.</p>\n<p>Также для иностранных граждан, которые получили профессиональное образование в образовательных или научных учреждениях РФ будут сокращены необходимые сроки работы в России с 3 лет до 1 года.</p>', NULL, '', '', NULL, NULL, 1),
(123, 0, 1600776559, 'Отмена 14-дневного карантина для иностранных граждан, въезжающих в Россию с 15 июля', '<p>С 15 июля в России будет отменен обязательный 14-дневный карантин для граждан, прибывших из-за рубежа, которые предоставили отрицательный результат теста на коронавирус.</p>', '<p>Соответствующее постановление №18 было подписано 7 июля главным санитарным врачом России и опубликовано на портале правовой информации.<br /> Согласно новому постановлению, с 15 июля иностранные граждане могут въезжать на территорию РФ и не находиться на двухнедельном карантине при наличии отрицательного результата теста на COVID-19.<br /> При въезде в Россию иностранному гражданину необходимо предъявить документ об отрицательном тесте на коронавирус, при этом тест должен быть сделан не позднее чем за 3 дня до прибытия.<br /> Если иностранный гражданин не сделал тест на коронавирус заранее, то он обязан будет сдать тест на территории РФ в течение трех дней с момента прибытия.<br /> Что касается граждан РФ, то с 15 июля 2020 года россияне могут въезжать в РФ и без результатов теста, однако они также обязаны пройти тест на коронавирус в течение 3 дней с момента прибытия на родину.</p>', NULL, '', '', NULL, NULL, 1),
(124, 0, 1600776726, 'Обязательная дактилоскопия при вступлении в гражданство РФ – подписан новый миграционный закон', '', '<p>13 июля 2020 года президент подписал Федеральный закон от 13.07.2020 № 209-ФЗ \"О внесении изменений в Федеральный закон \"О гражданстве Российской Федерации\" и Федеральный закон \"О государственной дактилоскопической регистрации в Российской Федерации\".</p>\n<p>Согласно новому закону, право на вступление в гражданство РФ в упрощенном порядке без необходимости соблюдения условий о сроке проживания на территории РФ получит еще одна категория иностранцев &ndash; иностранные граждане, которые проживают в России и имеют детей, которые являются совершеннолетними и дееспособными гражданами РФ.</p>\n<p>Кроме того, на упрощенный порядок получения гражданства РФ смогут также рассчитывать совершеннолетние иностранные граждане, на которых было подано заявление на гражданство РФ, когда им еще не исполнилось 18 лет.</p>\n<p>Также данный закон вводит обязательную дактилоскопическую регистрацию иностранных граждан при приеме в гражданство РФ. То есть для получения гражданства РФ необходимо будет пройти дактилоскопию.</p>\n<p>Закон вступит в силу через 90 дней после подписания, то есть уже в октябре 2020 года.</p>', NULL, '', '', NULL, NULL, 1),
(125, 0, 1600776831, 'Узбекистан и Беларусь взаимно признали дипломы об образовании', '', '<p class=\"font9\">Этот шаг&nbsp;способствует устранению бюрократических барьеров на пути приобретения знаний и созданию благоприятных условий для узбекских и белорусских граждан, отметили в МИД РУз.</p>\n<p class=\"font9\">Соглашение между правительствами Узбекистана и Беларуси о&nbsp;взаимном признании документов об обучении и образовании вступило в силу,&nbsp;сообщили&nbsp;в пресс-службе Министерства иностранных дел РУз.</p>\n<p class=\"font9\">&nbsp;Данный&nbsp;международный договор был подписан 1 августа 2019 года в ходе&nbsp;визита президента Узбекистана Шавката Мирзиёева в РБ.</p>\n<p class=\"font9\">&nbsp;&laquo;Документы государственного образца об образовании, выданные государственными образовательными учреждениями или научными организациями одной из сторон, признаются на территории другой стороны и позволяют продолжать обучение по соответствующим программам последующего уровня&raquo;,&nbsp;&mdash; рассказали в МИД.</p>\n<p class=\"font9\">&nbsp;Отмечается, что соглашение&nbsp;способствует устранению бюрократических барьеров на пути приобретения знаний и созданию благоприятных условий для узбекских и белорусских граждан, намеревающихся продолжить свое обучение в образовательных учреждениях двух стран.</p>\n<p class=\"font9\">&nbsp;&laquo;При этом соглашение не распространяется на образовательные документы, выданные до его вступления в силу (за исключением документов, выданных в Узбекской ССР или Белорусской ССР), а также не отменяет процедуру признания в целях осуществления профессиональной деятельности в обеих странах&raquo;,&nbsp;&mdash; уточнили в министерстве.</p>\n<p class=\"font9\">&nbsp;Уполномоченными органами по реализации договора являются Государственная инспекция по надзору за качеством образования при Кабмине Узбекистана и Министерство образования Беларуси.</p>\n<p>Источник- Sputnik</p>', NULL, 'Источник- Sputnik', '', NULL, NULL, 1),
(126, 0, 1600776918, 'Новый порядок депортаций', '', '<p>6 июля вступил в силу Приказ МВД РФ № 239 от 24.04.2020 г. об утверждении порядка депортации иностранных граждан и лиц без гражданства.</p>\n<p>Приказ МВД № 239 регламентирует процедуру подготовки материалов для принятия решения о депортации иностранца из России, порядок оформления документов на выезд из РФ для депортируемого иностранного гражданина, а также исполнения решения о депортации.</p>\n<p>Помимо этого, данный приказ МВД регламентирует ситуации, при которых иностранный гражданин подлежит депортации.</p>\n<p>Согласно новому приказу МВД, иностранный гражданин подлежит депортации:</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В случае, если аннулированы РВП или ВНЖ либо сокращен срок проживания или временного пребывания иностранного гражданина в РФ.</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Если в отношении иностранца было принято решение о неразрешении въезда в Россию или решение о нежелательности пребывания или проживания в РФ.</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; В случае, если иностранный гражданин передан иностранным государством в соответствии с договором о реадмиссии и не имеющий законных оснований для пребывания (проживания) в РФ.</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Если отказали в признании беженцем или он утратил этот статус, и у него нет других законных оснований для пребывания на территории РФ, и, если он отказывается от добровольного выезда за пределы РФ &ndash; предусмотрена депортация иностранного гражданина и членов его семьи.</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Если лишили статуса беженца или временного убежища за совершение преступления на территории РФ.</p>\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Если он утратил временное убежище или был лишен временного убежища, и у него нет других законных оснований для пребывания на территории РФ, и, если он отказывается от добровольного выезда за пределы РФ.</p>', NULL, '', '', NULL, NULL, 1),
(127, 0, 1600777238, 'В Подмосковье запустили приложение для оплаты патента', '<p>На днях, Единый миграционный центр предоставил мобильное приложение для оплаты ежемесячных авансовых платежей по НДФЛ для иностранцев работающих в России, а конкретно в Московской области.</p>', '<p>Как рассказывает директор Единого Миграционного центра Московской области Татьяна Куприна:&nbsp;<br /> &laquo;Иностранные граждане, работающие на территории России, в соответствии с действующим законодательством, должны ежемесячно оплачивать налог на доходы. Наше мобильное приложение формирует QR-код, который позволяет упростить оплату авансовых платежей в отделениях банка, терминалах самообслуживания или в мобильных приложениях банков. Кроме этого, в приложении содержится актуальная справочная информация о порядке пребывания и осуществления трудовой деятельности иностранных граждан&raquo;<br /> <br /> Благодаря считыванию QR-кода, вы не сможете ошибиться при заполнении информации или реквизитов, а платеж точно поступит по назначению. Также благодаря этому нововведению, скорость проверки патента значительно увеличена. Помимо прочего, приложение напомнит вам о том сколько и когда нужно платить за патент, что очень важно, ведь в случае если платеж будет пропущен, то трудовая деятельность на территории РФ становится нелегальной.</p>', NULL, '', '', NULL, NULL, 1),
(128, 0, 1600777404, 'Кто имеет право ездить на авто с иностранными номерами ', '', '<p>До начала года не было четко прописано, какой транспорт участвует в международном движении, какой должен быть зарегистрирован в России. Но с начала этого года вступил в силу закон о регистрации транспортных средств. И в итоге некоторые автовладельцы оказались вне закона.</p>\n<p>Дело в том, что они пользовались льготным законодательством соседних государств для ввоза автомобилей. Но, вот беда, не захотели действовать в соответствии с российским законодательством, хотя намеревались использовать машины именно в России. А по закону владелец такого транспортного средства обязан зарегистрировать его в течение десяти дней со дня ввоза на территорию России на срок более одного года либо со дня приобретения прав владельца транспортного средства, ранее не состоявшего на государственном учете на территории России.</p>\n<p>&nbsp;Грубо говоря, некий российский гражданин стал собственником машины, которая стоит на учете в Армении. Казалось бы, и бог с ним, но нет. Дело в том, что до недавнего времени в этой республике действовали льготные правила растаможивания машин. Они не соответствовали международным договорам. По сути, купив за бесценок машину в Армении и поставив там ее на учет, автовладелец пользовался безграничными правами. В том числе возможностью безнаказанно проезжать под камерами фотовидеофиксации нарушений. Когда-то штраф дойдет до него. Да и дойдет ли?</p>\n<p>Теперь Госавтоинспекция уточнила, что требования не регистрировать автомобиль в России распространяются только на транспортные средства, участвующие в международном движении или ввезенные на территорию России на срок менее года. Транспортное средство считается находящимся в международном движении, если оно принадлежит лицу, имеющему место жительства вне этого государства, не зарегистрировано в этом государстве и ввезено в него на срок менее года. Для регистрации необходимо наличие действительного паспорта транспортного средства в бумажном или электронном виде - его оформление осуществляется таможенными органами. Таким образом, в случае, если автомобиль зарегистрирован в другом государстве - члене ЕАЭС за гражданином России, имеющим постоянное место жительства только в Российской Федерации, требуется его регистрация в подразделениях Госавтоинспекции в 10-дневный срок на основании паспортов транспортных средств, выданных таможенными органами.</p>\n<p>&nbsp;Отсутствие такой регистрации - это административное нарушение, которое влечет за собой штраф в размере от 500 до 800 рублей, а при повторном нарушении - штраф в размере 5 тысяч рублей или лишение права управления на срок от 1 до 3 месяцев. Но если владелец такого транспорта зарегистрирован в России и имеет доверенность на управление этим автомобилем, то предъявить к нему претензии будет очень сложно.</p>', NULL, '', '', NULL, NULL, 1),
(129, 0, 1600777455, 'Единая электронная виза для въезда иностранных граждан в РФ с 1 января 2021 – принят новый законопроект', '', '<p>21 июля государственная дума в окончательном чтении приняла законопроект о введении электронной визы для краткосрочного въезда иностранных граждан на территорию Российской Федерации. Электронные визы будут доступны иностранцам уже с января 2021 года.<br /> &nbsp;&nbsp;</p>\n<p>Электронную визу смогут оформить иностранные граждане из стран, указанных в перечне, установленном правительством РФ. По предварительным данным в него войдут 113 государств. <br /> Электронная виза будет однократной, срок действия - до 60 дней с разрешенным сроком пребывания на территории РФ до 16 дней.<br /> <br /> Электронная виза с 01.01.2021 будет выдаться иностранным гражданам, которые въезжают в Россию с целью туризма, гостевым или деловым визитом, для участия в научных, культурных, общественно-политических, экономических и спортивных мероприятиях.<br /> <br /> Для того, чтобы оформить электронную визу иностранцу нужно будет заполнить соответствующее заявление онлайн.<br /> <br /> В течение 4 дней МИД России рассмотрит заявление и примет решение о выдаче иностранному гражданину электронной визы на въезд в РФ.<br /> <br /> Въезд и выезд иностранных граждан из России на основании электронной визы будет осуществляться через определенные правительством РФ пункты прохождения государственной границы.</p>', NULL, '', '', NULL, NULL, 1),
(130, 0, 1600777526, 'Закон об упрощенном получении гражданства РФ иностранными гражданами вступил в силу', '', '<p>24 июля вступили в силу поправки в закон о гражданстве РФ, упрощающие процедуру получения российского гражданства для иностранных граждан.</p>\n<p>Теперь для вступления в гражданство РФ иностранцам не придется отказываться от прежнего гражданства.</p>\n<p>Была упрощена процедура для граждан Белоруссии, Казахстана, Молдовы и Украины. Теперь при получении вида на жительство в РФ они сразу смогут обратиться с заявлением на получение гражданства. Больше не нужно соблюдать условие о пятилетнем сроке проживания в РФ по ВНЖ.</p>\n<p>Также упрощен порядок получения гражданства РФ для иностранных граждан, которые окончили российские вузы. Теперь для подачи на гражданство выпускникам нужно официально проработать в России всего один год вместо трех.</p>\n<p>Кроме того, теперь подать на гражданство РФ в упрощенном порядке без соблюдения условия о сроке проживания смогут:</p>\n<p>&nbsp;иностранные граждане, дети которых являются совершеннолетними гражданами РФ;</p>\n<ul>\n<li>иностранные граждане, находящиеся в браке с гражданами РФ, проживающие в России и имеющие в браке общих детей;</li>\n<li>иностранные граждане, один из родителей которых имеет гражданство РФ и живет в РФ.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Также согласно закону, теперь при вступлении в гражданство Российской Федерации иностранные граждане в обязательном порядке должны будут проходить дактилоскопическую регистрацию.</p>', NULL, '', '', NULL, NULL, 1),
(131, 0, 1600777585, 'Фонд \"ОРИЕНТИР\" провёл первую неделю выездных правовых приёмных 2020/2021 – «Цивилизованная миграция»', '<p><img src=\"data:image/png;></p>', '<p>С 20 по 24 июля при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" была проведена первая неделя выездных правовых приемных - \"Цивилизованная миграция\" 2020/2021.</p>\n<p>&nbsp;Выездная правовая приемная работала с 20 по 23 июля на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом с Единым центром документов. Приемные проводилась с 09:00 до 15:30.</p>\n<p>24 июля состоялась выездная правовая приемная на территории второй по величине&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;в Санкт-Петербургской мечети - Коломяжская мечеть (у неё два адреса: ул. Парашютная, д.7 и ул. Репищева, д. 1) с 12:00 и после намаза до 16:00</p>\n<p>Основными вопросами у мигрантов по прежнему остаются, продление разрешительных документов по пребыванию в РФ и продление патентов на работу, а также восстановление и продление регистрации по месту жительства, получение гражданства РФ.</p>\n<p>&nbsp;Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 200 мигрантов, а также мигранты получили рекомендации от юристов Фонда, как избежать нарушения законодательства РФ.</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \" Цивилизованная миграция - Санкт-Петербург и Ленинградская область \" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\n<p>&nbsp;В рамках реализации проекта при поддержке Фонда Президентских Грантов Фонд \"ОРИЕНТИР\" занимается содействием и оказанием помощи иностранным гражданам в законном оформлении и нахождении на территории России, защитой социально-экономических прав трудовых мигрантов и представительством интересов трудовых мигрантов в суде. С информацией можно ознакомиться на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(132, 0, 1600777636, 'Путин поручил разобраться с \"поборами\" за получение гражданства ', '<p>Процедуры подачи иностранцами заявлений о приеме в гражданство РФ будут оптимизированы, чтобы у заявителей не было необоснованных расходов. Такое поручение дал президент по итогам встречи с рабочей группой по подготовке предложений о внесении поправок в Конституцию.</p>', '<p>К октябрю кабинет министров должен проанализировать процедуры подачи таких заявлений и стоимость услуг посреднических организаций - которые помогают оформлять эти бумаги.</p>\n<p>Затем правительство подготовит предложения по оптимизации процедур и ликвидации необоснованных расходов заявителей, проработает их с кремлевской администрацией и представит президенту.</p>\n<p>На встрече главе государства жаловались на то, что все усилия руководства страны по упрощению получения гражданства, в том числе для соотечественников, \"нивелируются\" по сути посредниками, которые за свои коммерческие услуги берут с них по 8 тысяч рублей.</p>\n<p>\"Что касается конкретных вещей, вот этих поборов, уплат и так далее, - это, конечно, то, что мы можем сделать достаточно быстро... Коллеги из администрации и из правительства меня слышат, мы над этим еще поработаем в самое ближайшее время\", - обещал президент.</p>', NULL, '', '', NULL, NULL, 1),
(133, 0, 1600777679, 'Фонд \"ОРИЕНТИР\" провёл вторую неделю выездных правовых приёмных 2020/2021 – «Цивилизованная миграция»', '', '<p>С 29 по 31 июля при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" была проведена вторая неделя выездных правовых приемных - \"Цивилизованная миграция\" 2020/2021.</p>\n<p>&nbsp;Выездная правовая приемная работала с 29 по 31 июля на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом с Единым центром документов. Приемные проводилась с 09:00 до 15:30.</p>\n<p>Основными вопросами у иммигрантов по прежнему остаются, продление разрешительных документов по пребыванию в РФ и продление патентов на работу, а также восстановление и продление регистрации по месту жительства, получение РВП, ВНЖ и &nbsp;гражданства РФ.</p>\n<p>&nbsp;Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 125 иммигрантов, а также иммигранты получили рекомендации от юристов Фонда, как избежать нарушения законодательства РФ.</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \"Цивилизованная миграция &ndash; ОНЛАЙН, Санкт-Петербург и Ленинградская область\" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\n<p>&nbsp;В рамках реализации проекта при поддержке Фонда Президентских Грантов Фонд \"ОРИЕНТИР\" занимается содействием и оказанием помощи иностранным гражданам в законном оформлении и нахождении на территории России, защитой социально-экономических прав трудовых мигрантов и представительством интересов трудовых мигрантов в суде. С подробной информацией можно ознакомиться на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(134, 0, 1600777750, 'Новые правила въезда трудовых мигрантов', '<p>Дмитрий Медведев предложил обсудить новые правила въезда трудовых мигрантов</p>', '<p>В период пандемии коронавируса нашей стране в целом удалось обеспечить безопасность граждан, но сейчас нужно выработать дополнительные эффективные меры для противодействия криминалу. Об этом заявил на совещании заместитель председателя Совета безопасности Дмитрий Медведев. Так, сразу несколько предложений, которые обсудит Совбез в ближайшее время, касаются трудовых мигрантов и гарантий их занятости в России.</p>\n<p>Актуальные проблемы обеспечения правопорядка в период преодоления последствий эпидемии COVID-19 рассматривались вчера на совещании у замглавы Совбеза. Оценка за первое полугодие дана положительная. Количество зарегистрированных преступлений снизилось на 0,1 процента и составило чуть более одного миллиона, число выявленных преступников уменьшилось на 5 процентов. \"Благодаря оперативным и своевременным действиям нам в целом удалось обеспечить безопасность граждан\", - считает Дмитрий Медведев.</p>\n<p>Такая ситуация сложилась далеко не во всех странах мира. Чтобы убедиться в этом, достаточно посмотреть на происходящее в США. \"Нам нужно сделать все, чтобы у нас события развивались совершенно по иному сценарию\", - подчеркнул зампред Совета безопасности.</p>\n<p>Хорошие показатели не означают отсутствия проблем. В период пандемии многие компании и граждане перевели свою деятельность в онлайн-формат, и это привело к расширению сетевого криминала. \"На две трети возросло число противоправных деяний, связанных со сбытом наркотиков через интернет\", - подчеркнул Дмитрий Медведев. На фоне непростой социально-экономической обстановки значительную опасность стал представлять сетевой экстремизм. В первом полугодии зарегистрировано 442 экстремистских преступления, что на треть больше, чем в аналогичный период 2019 года.</p>\n<p>Отдельную обеспокоенность вызывает положение в миграционной сфере. Здесь Медведев сослался на обращения граждан. Многие мигранты оказались в сложной жизненной ситуации: с одной стороны, они не могут уехать домой из-за закрытых границ, а с другой - остались без средств к существованию. Около 40 процентов трудовых мигрантов лишились работы. \"Это создает весьма питательную среду для потенциального роста преступности в данной сфере\", - заявил замглавы Совбеза. По его словам, в Москве, Санкт-Петербурге, Екатеринбурге, Мурманской и Амурской областях в июле зафиксированы случаи нарушения общественного порядка мигрантами.</p>\n<p>Экспертами подготовлен ряд предложений к правилам трудовой миграции, в том числе изменить порядок предоставления права на их въезд в Россию. \"Речь идет о том, чтобы давать иностранному гражданину такое право только в том случае, если от работодателя получено недвусмысленное подтверждение его трудоустройства\", - пояснил Дмитрий Медведев.</p>\n<p>Изучен опыт других государств, где есть совсем жесткие меры. Например, на работодателя возлагается полная ответственность за действия иностранного гражданина, которого он нанял на работу. Замглавы Совбеза считает, что как минимум и это следует обсудить.</p>', NULL, '', '', NULL, NULL, 1),
(135, 0, 1600777870, '\"ОРИЕНТИР\" продолжает проводить выездные правовые приёмные в рамках проекта 2020/2021 – «Цивилизованная миграция»', '', '<p>С 17 по 21 августа при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" была проведена очередная неделя выездных правовых приемных - \"Цивилизованная миграция\" 2020/2021.</p>\n<p>&nbsp;Выездная правовая приемная работала с 17 по 21 августа на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом с Единым центром документов. Приемные проводилась с 09:00 до 15:00.</p>\n<p>По прежнему поток мигрантов не снижается и основные вопросы остаются теми-же, продление разрешительных документов по пребыванию в РФ и продление патентов на работу, а также восстановление и продление регистрации по месту жительства, получение РВП, ВНЖ и&nbsp; гражданства РФ. В последнее время заметно выросло количество обращений связанных с получением Российского гражданства.</p>\n<p>&nbsp;Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 140 иммигрантов, а также иммигранты получили рекомендации и квалифицированную юридическую помощь от юристов Фонда, как правильно оформлять заявления, какой пакет документов нужен мигранту для подачи в ЕМЦ, и как избежать нарушения законодательства РФ.</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \"Цивилизованная миграция &ndash; ОНЛАЙН, Санкт-Петербург и Ленинградская область\" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\n<p>&nbsp;В рамках реализации проекта при поддержке Фонда Президентских Грантов Фонд \"ОРИЕНТИР\" занимается содействием и оказанием помощи иностранным гражданам в законном оформлении и нахождении на территории России, защитой социально-экономических прав трудовых мигрантов и представительством интересов трудовых мигрантов в суде. С подробной информацией можно ознакомиться на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(136, 0, 1600777951, 'Что делать мигрантам в РФ после 15 сентября', '<p>Миграционные органы пытаются легализовать иностранных граждан, которые находится в стране без правового статуса</p>', '<p>Поэтому после 15 сентября у иностранцев будет временная отсрочка для легализации в России &ndash; иностранным гражданам с действующими документами предоставят еще 185 дней для того, чтобы урегулировать свой правовой статус в России.</p>\n<p>Порядок продления срока пребывания иностранных граждан</p>\n<p>Визы иностранных граждан, действие которых истекает в период с 15 марта по 15 сентября 2020 года, будут являться действительными с даты окончания срока действия, который указан в визе, в течение еще 185 дней, в том числе для выезда из РФ, без необходимости оформления транспортной визы.</p>\n<p>&nbsp;То есть если виза закончилась 1 мая 2020 года, то она будет действительна для выезда из РФ еще 185 дней, то есть до ноября 2020 года.</p>\n<p>&nbsp;Важно! Из-за технических особенностей при истечении срока действия электронной визы в указанный период, иностранному гражданину необходимо будет обратиться за оформлением транзитной визы для выезда из РФ. Госпошлина в этом случае не взимается.</p>\n<p>Осуществление миграционного учета</p>\n<p>Иностранные граждане, прибывшие в новое место пребывания в период с 15 марта по 15 сентября 2020 года, подлежат постановке на миграционный учет по месту пребывания в соответствии с Федеральным законом № 109-ФЗ.</p>\n<p>&nbsp;Однако течение срока временного пребывания иностранного гражданина в РФ, в случае если он истекает в период с 15 марта по 15 сентября 2020 года, приостанавливается до конца 185 дневного периода.</p>\n<p>&nbsp;То есть если срок пребывания иностранного гражданина истек 20 мая 2020 года, в том числе в случае окончания срока оплаты его патента, то срок его пребывания продлевается на 185 дней начиная с 20 мая 2020 года и действует до ноября.</p>', NULL, '', '', NULL, NULL, 1),
(137, 0, 1600777994, '\"ОРИЕНТИР\" провёл итоговые выездные правовые приёмные в 2020 году – «Цивилизованная миграция» 2020/2021', '', '<p>С 25 по 27 августа при поддержке Фонда президентских грантов Фондом поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" были проведены очередные и в этом году итоговые выездные правовые приемные - \"Цивилизованная миграция\" 2020/2021.</p>\n<p>&nbsp;Выездная правовая приемная работала с 25 по 27 августа на территории Единого миграционного центра на улице Красного Текстильщика, д. 10-12, рядом с Единым центром документов. Приемные проводилась с 09:00 до 15:00.</p>\n<p>По прежнему поток мигрантов не снижается и основные вопросы остаются теми-же, продление разрешительных документов по пребыванию в РФ и продление патентов на работу, а также восстановление и продление регистрации по месту жительства, получение РВП, ВНЖ и об упрощенном получении гражданства РФ, &mdash; Федеральный закон от 24.04.2020 № 134-ФЗ &ldquo;О внесении изменений в Федеральный закон &ldquo;О гражданстве Российской Федерации&rdquo; в части упрощения процедуры приема в гражданство Российской Федерации иностранных граждан и лиц без гражданства&rdquo;. Этим документом вносятся изменения в действующий Федеральный закон №62 &laquo;О гражданстве&raquo;.</p>\n<p>&nbsp;Юристами и помощниками юристов Фонда \"ОРИЕНТИР\" было проконсультировано более 90 иммигрантов, а также иммигранты получили рекомендации и квалифицированную юридическую помощь от юристов Фонда, как правильно оформлять заявления, какой пакет документов нужен мигранту для подачи в ЕМЦ, и как избежать нарушения законодательства РФ.</p>\n<p>&nbsp;Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках реализации проекта \"Цивилизованная миграция &ndash; ОНЛАЙН, Санкт-Петербург и Ленинградская область\" с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>\n<p>&nbsp;В рамках реализации проекта при поддержке Фонда Президентских Грантов Фонд \"ОРИЕНТИР\" занимается содействием и оказанием помощи иностранным гражданам в законном оформлении и нахождении на территории России, защитой социально-экономических прав трудовых мигрантов и представительством интересов трудовых мигрантов в суде. С подробной информацией можно ознакомиться на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>', NULL, '', '', NULL, NULL, 1),
(138, 0, 1600778039, 'В Ленобласти запустят цифровую платформу мигрантов Salamport ', '', '<p>Ленинградская областная Торгово-промышленная палата запускает уникальный проект \"Цифровая платформа мигранта Salamport\". До конца нынешнего месяца оцифруют порядка одной тысячи мигрантов, в следующем году этот показатель планируют довести до нескольких сотен тысяч.</p>\n<p>Цифровая платформа разработана как современный инструмент наведения порядка на рынке трудовой миграции. Она значительно упростит взаимодействие, с одной стороны - мигрантов, которые хотят трудиться на законных основаниях, с другой - работодателей, желающих принять новых сотрудников.</p>\n<p>Salamport состоит из двух блоков. Первый предназначен для работодателей. Здесь можно завести свой кабинет, разместить вакансию, отобрать нужного работника, оформить его, организовать обучение и аттестацию. Второй блок - для мигранта. Потенциальный сотрудник может оцифровать документы, подать заявку работодателю, дистанционно записаться на курсы русского языка, приобрести билеты на поезд или автобус, арендовать жилье, выполнить другие необходимые операции.</p>\n<p>- Цифровая платформа позволит организовать взаимодействие между работодателями, службами занятости региона и мигрантами с целью мониторинга и эффективного перераспределения трудовых ресурсов. Каждый мигрант на своем родном языке сможет получить бесплатный доступ к официальному банку вакансий от проверенных работодателей, служб занятости, частных агентств занятости и операторов организованного набора, - прокомментировал ситуацию исполнительный директор Торгово-промышленной палаты Ленинградской области Игорь Муравьев.</p>\n<p>Основные языки на платформе - русский и узбекский. Последний выбран потому, что, согласно статистике, 80 процентов трудовых мигрантов в стране - это граждане Узбекистана. Но чуть позже появится информация и на киргизском, и на таджикском языках.</p>\n<p>Директор центра инициатив направления \"Молодые профессионалы\" Агентства стратегических инициатив Жанна Кадылева уверена, что функционал платформы Salamport дает возможность в режиме онлайн отслеживать потребности работодателей в трудовых ресурсах и количество мигрантов, заинтересованных в трудоустройстве.</p>', NULL, '', '', NULL, NULL, 1),
(139, 0, 1600778114, 'Правила миграционного учета с 7 сентября', '<h1>теперь иностранцы могут регистрировать других иностранных граждан</h1>', '<p>7 сентября вступили в силу изменения в закон о миграционном учете иностранных граждан, внесенные Федеральным законом от 08.06.2020 № 182-ФЗ.</p>\n<p>Согласно закону, теперь иностранные граждане и лица без гражданства, у которых в собственности есть жилое помещение на территории Российской Федерации, могут предоставлять это жилье другим иностранным гражданам для фактического проживания и регистрировать их у себя, выступая в качестве принимающей стороны.</p>\n<p>Другими словами, с 7 сентября иностранные граждане смогут регистрировать иностранных граждан в своей недвижимости, то есть ставить их на миграционный учет по своему адресу.</p>\n<p>Кроме того, теперь уведомление о прибытии иностранного гражданина в место пребывания можно подать в органы миграционного учета лично, в электронной форме через портал Госуслуг, через МФЦ, или по почте.</p>\n<p>При подаче уведомления о прибытии иностранца в электронной форме, принимающей стороне необходимо распечатать копию отрывной части и передать ее прибывшему иностранному гражданину.</p>\n<p>Сроки регистрации составляет 7 рабочих дней со дня прибытия иностранного гражданина в место пребывания.</p>\n<p>То есть подать уведомление о прибытии необходимо в течение семи дней. Однако если прибывший иностранный граждан является участниками программы добровольного переселения соотечественников, то они и члены их семей могут быть поставлены на миграционный учет в течение 37 дней.</p>', NULL, '', '', NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(140, 0, 1600778156, 'Для трудовых мигрантов в России может появиться новый налог', '<p>МВД РФ предложило ввести фиксированный авансовый платеж для трудовых мигрантов, сообщило со ссылкой на соответствующий проект закона.</p>', '<p>Для уплаты такого налога предлагается ввести оформление электронного патента. Работодателям, которые хотят нанять мигранта, потребуется зарегистрироваться в личном кабинете и заполнить специальную форму. После этого нужно заполнить заявление о включении в реестр работодателей, привлекающих иностранную рабочую силу (РРПИРС).</p>\n<p>Проектом закона предлагается и отразить условия найма иностранцев. При неуплате НДФЛ в виде фиксированного авансового платежа мигрантам направят уведомление с требованием заплатить налог в течение 15 дней. При отказе власти могут запретить неплательщику работать в России и, возможно, депортировать из страны.</p>\n<p>Перед въездом в РФ иностранцам выпустят специальную ID-карту с персональными данными, возьмут отпечатки пальцев.</p>\n<p>МОСКВА, Радио Sputnik.</p>', NULL, '', '', NULL, NULL, 1),
(141, 0, 1600790344, 'МВД предложило выдавать иностранцам ID-карты для идентификации личности ', 'МВД России разработало проект концепции изменений в федеральный закон, предусматривающий выпуск для иностранцев ID-карт с персональными данными, документ есть в распоряжении РИА Новости.', '\r\n<p class=\"MsoNormal\" style=\"margin-bottom: .0001pt; line-height: normal;\"><span style=\"font-size: 12.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: \'Times New Roman\'; mso-fareast-language: RU;\">Ранее начальник Главного управления по вопросам миграции МВД России Валентина Казакова сообщила, что министерство подготовило и уже обсуждает концепцию нового федерального закона, который упростит процедуру получения российского гражданства и трудоустройства иностранцев.</span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: .0001pt; line-height: normal;\"><span style=\"font-size: 12.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: \'Times New Roman\'; mso-fareast-language: RU;\"><span style=\"mso-spacerun: yes;\">&nbsp;</span>По ее слова, законопроект может появиться в 2021 году. \"В рамках цифровой трансформации государственного управления миграционными процессами в проекте федерального закона необходимо закрепить, что для иностранных граждан, приобретающих статус долгосрочно пребывающих на территории РФ, а равно получивших право на постоянное проживание, документом, удостоверяющим личность на территории РФ, будет являться единый документ с электронным носителем информации (ID-карта)\", - отмечается в документе. </span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: .0001pt; line-height: normal;\"><span style=\"font-size: 12.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: \'Times New Roman\'; mso-fareast-language: RU;\">Согласно проекту, карта будет выдаваться на основании заявления иностранца. Обязательным условием для ее получения станут прохождение дактилоскопической регистрации, фотографирование и медицинское освидетельствование на предмет отсутствия опасных инфекционных заболеваний. </span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: .0001pt; line-height: normal;\"><span style=\"font-size: 12.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: \'Times New Roman\'; mso-fareast-language: RU;\">Срок действия планируется установить на 10 лет. Предполагается, что карту не обязательно получать, если мигрант намерен прожить в России менее 90 суток. \"Для разных категорий иностранных граждан в зависимости от статуса предлагается определить различные основания приостановления и последующего возобновления действия ID-карты, при этом для возобновления действия ID-карты при наличии установленных федеральным законом оснований обязательным будет прохождение иностранным гражданином биометрической идентификации, медицинского освидетельствования\", - уточняется в концепции.</span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"margin-bottom: .0001pt; line-height: normal;\"><span style=\"font-size: 12.0pt; font-family: \'Times New Roman\',\'serif\'; mso-fareast-font-family: \'Times New Roman\'; mso-fareast-language: RU;\"><span style=\"mso-spacerun: yes;\">&nbsp;</span>Кроме того, в проекте федерального закона предлагается определить основные требования к ID-карте. В частности, она должна быть изготовлена на основе отечественных технологий; содержать криптографическую защиту от подделки; иметь возможность совершения юридических операций в интернете, в том числе получения государственных, банковских и иных услуг; доступность для сервиса \"электронная подпись\"; возможность подключения мобильного приложения иностранного гражданина. \"Выдача ID-карты, ее замена и возобновление действия, а также внесение в нее изменений будут осуществляться территориальными органами МВД России в рамках предоставления государственной услуги после уплаты государственной пошлины (за исключением отдельных категорий иностранных граждан)\", - сообщается в проекте документа.</span></p>\r\n<p>&nbsp;</p>\r\n<div style=\"position: absolute; left: -99999px;\">МВД России разработало проект концепции изменений в федеральный закон, предусматривающий выпуск для иностранцев ID-карт с персональными данными, документ есть в распоряжении РИА Новости.\r\n<p>Источник: <a href=\"https://www.vestnik-migranta.ru/2020/09/id-karta.html\">https://www.vestnik-migranta.ru/2020/09/id-karta.html</a></p>\r\n</div>', NULL, '', '', NULL, NULL, 1),
(142, 0, 1600858529, 'Попасть в Россию любым путем: таджикистанцы пробретают авиабилеты в Минск и Дубай', '<p>Десятки жителей Таджикистана выстраиваются в очереди у авиакасс, желая приобрести билеты на рейс Душанбе-Минск-Душанбе. Недавно министерство иностранных дел Таджикистана сообщило о совершении авиакомпанией &laquo;Сомон Эйр&raquo; 21 и 27 сентября 2020 года чартерных рейсов по этому направлению.</p>', '<p>Чартерные рейсы предназначены, прежде всего, для вывоза обучающихся в Республике Беларусь и Украине студентов-таджикистанцев, а также граждан иностранных государств, говорилось в сообщении внешнеполитического ведомства.</p>\n<p>Авиабилеты на рейс Душанбе-Минск-Душанбе продаются согласно спискам Министерства образования и науки Таджикистана, однако сотни трудовых мигрантов не теряют надежды приобрести билеты и добраться до России через белорусскую территорию.</p>\n<p>Житель Душанбе Одина Неъматзод 21 сентября ждал своей очереди у одной из столичных авиакасс. &laquo;Я зарегистрировался на Едином портале государственных услуг и функций &laquo;Госуслуги&raquo; и заполнил специальную электронную форму предварительной регистрации на авиарейсы в Россию. Но оказалось, что это бесполезно. Сейчас хочу купить билет до Минска&raquo;, сказал он корреспонденту Радио Озоди.</p>\n<p>Саиджон Саттори, другой житель таджикской столицы заявил, что готов приобрести авиабилеты по самой высокой цене. &laquo;Люди говорят, что билет до Минска стоит то ли 5200 сомони ($520), то ли 6800 ($680). Не знаю, достанется ли мне билет или нет. Сколько стоит билет &ndash; неважно. Если надо будет, попрошу деньги в долг у друзей и знакомых&raquo;, говорит наш собеседник.</p>\n<p>Россия с 21 сентября возобновила авиасообщение с Беларусью, Казахстаном и Киргизией. Когда возобновятся регулярные полеты между Таджикистаном и Россией &ndash; неизвестно.</p>\n<p>Амриё Бобоев, замглавы Департамента информации и печати МИД Таджикистана в телефонной беседе с Радио Озоди отметил, что таджикские власти еще 22 июля сообщили российской стороне о готовности к возобновлению авиасообщения и отметили, что таджикские авиакомпании будут выполнять рейсы с соблюдением всех санитарных требований. &laquo;В Москве, в свою очередь, сообщили, что дата возобновления регулярных полетов будет уточнена в ходе переговоров авиационных властей двух стран. Переговоры продолжаются, окончательное решение остается за Россией&raquo;, - отметил официальный представитель МИД Таджикистана.</p>\n<p>Авиасообщение между Таджикистаном и другими странами, в том числе РФ, было временно приостановлено в марте из-за угрозы коронавируса. Сейчас совершаются только чартерные рейсы для вывоза таджикских граждан из российских городов.</p>\n<p>МИД Таджикистана на днях сообщил, что авиакомпания &laquo;Сомон Эйр&raquo; в период с 16 по 30 сентября совершит 71 дополнительный чартерный рейс в целях вывоза таджикистанцев из России. Решение об этом было принято Республиканским штабом по усилению противоэпидемических мер по предотвращению распространения COVID-19. Все чартерные рейсы в Москву, Санкт-Петербург, Казань, Краснодар, Новосибирск, Екатеринбург, Иркутск, Уфу и Сургут будут выполнены по мере согласования с российскими властями, отмечалось в сообщении.</p>\n<p>В авиакомпании &laquo;Сомон Эйр&raquo; 21 сентября сообщили Радио Озоди, что самолеты в Россию летают пустыми, из-за чего авиабилеты из РФ в Таджикистан стоят 395 евро.</p>\n<p>Тем временем, некоторые таджикские мигранты собираются вылететь в Москву через Дубай. Стоимость авиабилета по маршруту Душанбе-Дубай-Москва составляет $1300. Однако, как сообщили Радио Озоди в представительствах Flydubai и &laquo;Сомон Эйр&raquo;, они гарантируют полет только до Дубая, оттуда в Москву могут вылететь только граждане России.</p>\n<p>Источник - Радио ОЗОДИ</p>', NULL, '', '', NULL, NULL, 1),
(143, 0, 1600858587, '22 сентября Россию покинули почти 2 тысячи узбеков', '<p>1&nbsp;792 граждан Узбекистана вернулись домой из Самарской области и Новосибирска 22&nbsp;сентября. Об этом сообщила пресс-служба минтранса Узбекистана.</p>', '<p><strong>Известно, что людей доставили два спецпоезда АО \"Узбекистон темир йуллари\".</strong></p>\n<ul>\n<li>854 человека приехали из Новосибирска в город Пан Наманганской области.</li>\n<li>938 человек приехали в Ташкент из Кинеля Самарской области.</li>\n</ul>\n<p>На данный момент все прибывшие люди отправлены в карантинный центр.</p>\n<p>Напомним, с 15&nbsp;августа в город Кинель Самарской области начали прибывать мигранты, которые пытались уехать домой. Сначала люди обустроились на платформе вокзала. 21&nbsp;августа власти области организовали для них палаточный лагерь.</p>\n<p>В сентябре спецпоездами на родину вернулись более 3,5&nbsp;тыс. человек. По данным на 16&nbsp;сентября, в пункте временного пребывания находились более 4&nbsp;тыс. граждан Узбекистана.</p>\n<p>Также мигранты прибывали в Ростовскую область, в Башкирию, Оренбуржье и другие регионы.</p>\n<p>По данным властей Самарской области, для оставшихся людей в лагере запланированы еще три поезда и рейсы из аэропорта Курумоч.</p>', NULL, '', '', NULL, NULL, 1),
(144, 0, 1480435695, NULL, NULL, '<p style=\"text-align: justify;\">С 1 апреля 2016 года открыта Приемная правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области. Мигранты получают юридическую помощь исключительно на безвозмездной основе. Консультации предоставляются в устной и письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы Приемной &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области.</p>\n<p style=\"text-align: justify;\">Фактически Приемная начала работу с 15 февраля 2016 года в режиме апробирования услуг.&nbsp; За это время было предоставлено более пятидесяти бесплатных консультаций иммигрантам, из них большинство являются гражданами Украины. С первых дней работы Приемной идут обращения мигрантов-соотечественников на телефон горячей линии правовой поддержки. Основные вопросы, с которыми обращаются граждане, - это миграционное законодательство и трудовые споры.</p>\n<p style=\"text-align: justify;\">Прием мигрантов ведут квалифицированные юристы, которые в полном объеме дают разъяснение по назначению государственной социальной помощи по всем интересующим вопросам миграции. Все случаи обращений будут рассматриваться в пределах компетенции правовой Приемной, а также в тесном сотрудничестве со всеми партнерами. При необходимости к рассмотрению обращений будут привлекаться непосредственно Управление Федеральной миграционной службы по Санкт-Петербургу и Ленинградской области, а также национальные диаспоры, посольства и представительства миграционных служб других государств.</p>\n<p style=\"text-align: justify;\">Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для переселенцев-соотечественников на территориях вселения. Фонд &laquo;ОРИЕНТИР&raquo; имеет представительства не только в России, но и в Европе.</p>\n<p style=\"text-align: justify;\">Расположение Приемной правовой поддержки выбрано не случайно. Приемная находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(145, 0, 1517327817, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">В декабре 2014 г. завершается проводимый&nbsp; в пяти районах Ленинградской области на площадках пятнадцати крупных предприятий научно-исследовательский проект, в ходе которого изучались социально-экономические и социально-психологические аспекты жизнедеятельности трудовых мигрантов. Коллектив квалифицированных научных работников (Бондарук А.Ф., Голянич В.М., Перепелкина В.А., Седлиньш Э.,) по согласованию с правительством Ленинградской области провел неструктурированный опрос почти тысячи трудовых мигрантов. Значительная часть опрошенных (более 250 респондентов) была обследована с использованием специально разработанной социологической анкеты, содержащей 49 вопросов и более 150 вариантов ответов. Инициатором проекта выступил Фонд поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, главной задачей которого является обоснование, разработка и апробация Программы социально-психологической адаптации соотечественников-переселенцев. В данный момент готовится отчет для правительства Ленинградской области и органов федеральной власти.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Президент Фонда Эдгарс Седлиньш рассказал, что заказчиком проекта выступило, в первую очередь, гражданское сообщество и только потом правительство Ленинградской области, а также, что этот проект назревал и готовился более трех лет, но свою реальную реализацию получил только в 2014 году благодаря финансовой поддержке Комитета по печати и связям с общественностью Ленинградской области. &laquo;Особо хочу отметить, что исследование провели специалисты-профессионалы в области социологии и психологии миграции, которые разработали информативную социологическую анкету, осуществили опрос и анкетирование респондентов, подготовили интересный отчет. По результатам работы разработаны рекомендации для федеральные структуры и институты. Полученные данные будут способствовать развитию форм взаимодействия принимающего сообщества (местного населения, предпринимателей и органов всех уровней власти) мигрантами/переселенцами в лице соотечественников&raquo; - заверил Эдгарс Седлиньш.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">По словам доктора медицинских наук, профессора, заведующего кафедрой психологии и педагогики СПбГУКИ &nbsp;Валерия Михайловича Голянича, сейчас миграционная служба лишь знает, что мигрант приехал в нашу страну, а о том, где он будет находиться потом, не знает никто &ndash; в законе не прописано, какая служба должна контролировать его дальнейшие перемещения. В ходе исследования стала очевидной необходимость в разработке мероприятий-механизмов адаптации, предполагающих дифференциацию прибывших на постоянное место жительства переселенцев с учетом гендерных, возрастных, профессиональных и иных социально-демографических особенностей.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Социолог Валентина Александровна Перепелкина (кандидат философских наук, доцент кафедры психологии и педагогики СПбГУКИ) также уверена, что нужен контроль и учет миграционных потоков. Специалист полагает, что &laquo;&hellip;. большой проблемой является социальное сопровождение иммиграции, которая не всегда протекает в соответствии с народнохозяйственными интересами. Высокая интенсивность современной иммиграции, сопровождаемая ее несистемностью и отсутствием направленности, ведет к крупным экономическим издержкам: способствует текучести кадров, снижению производительности труда, повышает материальные и временные затраты на обучение и адаптацию иммигрантов. Большинству из них приходится не только получать первую в их жизни профессию, но и овладевать непривычным стилем жизни, осваивать иные культурные, психологические и поведенческие стереотипы. Сложившиеся инструменты и механизмы поддержки иммиграции нуждаются в серьезных изменениях, прежде всего &ndash; посредством повышения внимания к проблемам устойчивой адаптации (ресоциализации) иммигратов и нормативной социализации детей, рожденных в их семьях&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">По мнению социального психолога Александра Феодосьевича Бондарука (кандидата психологических наук, доцента кафедры психологии и педагогики СПбГУКИ), &laquo;реализация проекта послужит развитию деловых и общественных связей между Россией и странами, в которых проживает русскоязычное население, развитию народной дипломатии, пропаганде культурных и социальных ценностей&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Полученные в ходе исследования результаты и разработанные рекомендации в начале 2015 г. будут опубликованы на сайте Правительства Ленинградской области, сайте Фонда &laquo;ОРИЕНТИР&raquo; и в научном журнале &laquo;Управленческое консультирование&raquo;, </span></p>\n<p style=\"text-align: justify;\"><span>CПРАВКА:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span>Деятельность Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; представляет собой системную работу, направленную на развитие форм взаимодействия между принимающим сообществом (в т.ч. органами исполнительной власти всех уровней) и нашими соотечественниками, проживающими за рубежом и желающими добровольно переселиться в Россию.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(146, 0, 1467393107, NULL, NULL, '<p style=\"text-align: justify;\">Информационно-аналитический&nbsp;отчет о&nbsp;реализации проекта &laquo;Социально-экономические и социально-психологические аспекты жизнедеятельности переселенцев - соотечественников в Ленинградской области: современное состояние и перспективы развития&raquo;.</p>\n<p><a href=\"https://yadi.sk/i/1DARD7HRsu9yq\">отчет </a></p>\n<p><a href=\"https://yadi.sk/i/JRi2OO49suAY3\">презентация</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(147, 0, 1467393021, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Петербургские и областные эксперты анализируют ситуацию, обобщают результаты научных изысканий по проблемам миграции&nbsp;&mdash; сформирована и проводится фокус&nbsp;&mdash; группа из представителей пяти национальных диаспор</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Исследовательская группа ученых&nbsp;&mdash; специалистов в области социологии, юриспруденции, экономики, психологии из ведущих вузов Санкт-Петербурга и Ленинградской области, а также семь квалифицированных экспертов (руководители кадровых служб) заняты оценкой основных проблем правовой, социальной и культурной интеграции мигрантов в организациях, использующих труд иностранной рабочей силы. Об этом сегодня, 12 мая, корреспонденту </span><a href=\"http://www.regnum.ru/\" target=\"_blank\"><span style=\"color: #0000ff; font-family: Times New Roman; font-size: medium;\">ИА REGNUM</span></a><span style=\"font-family: Times New Roman; font-size: medium;\"> сообщили в Фонд поддержки добровольного переселения соотечественников &laquo;Ориентир&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Ключевыми мероприятиями проекта &laquo;Адаптация мигрантов проживающих на территории Ленинградской области, интегрирование их в единое правовое и культурное поле России, развитие традиционных духовных ценностей&raquo; стали круглые столы и семинары.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">В частности, круглый стол &laquo;Проблемы и перспективы интеграции мигрантов в единое правовое и культурное поле России&raquo; с представителями национальных диаспор, УФМС России по Санкт-Петербургу и Ленинградской области, Советов по межнациональным отношениям муниципальных образований Ленинградской области, Комитета по труду и занятости населения Ленинградской области и руководителей кадровых служб организаций-работодателей Ленинградской области. Информационные семинары посвящены правам и обязанностям трудовых мигрантов адресованы непосредственно трудовым мигрантам, работающим на предприятиях региона. В ходе семинаров проводятся дополнительные консультирования трудовых мигрантов по правовым вопросам миграции и по вопросам предпринимательства в РФ.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Эксперты предложили авторскую методику для оценки основных характеристик миграционных процессов. Подготовлен специальный регистрационный бланк для автоматизированной обработки с использованием сканера для анализирования и ранжирования перечня основных интеграции мигрантов в организациях, использующих труд иностранной рабочей силы.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Подготовлены и изданы методические материалы для представителей районных администраций Ленинградской области и Советов по межнациональным отношениям по организации работы с руководителями предприятий района, использующими труд мигрантов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">На основании проблем, выявленных в ходе социологического исследования, будет разработана программа семинара и круглого стола в формате деловой игры для представителей Советов по межнациональным отношениям.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">По итогам семинара у представителей районных администраций и Советов по межнациональным отношениям будут сформированы компетенции: по организации и проведению образовательных семинаров для руководителей предприятий; по управлению процессами интеграции мигрантов в правовое и культурное поле региона. Будут разработаны рекомендации для властных структур по интеграции трудовых мигрантов в единое правовое и культурное поле региона. Разработанные рекомендации будут направлены в профильные структурные подразделения районных администраций Ленинградской области.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Напомним, проект реализуется при поддержке комитета по печати и связям с общественностью Ленинградской области для эмигрантов и принимающего сообщества. Реализацией данного проекта занимается Фонд поддержки добровольного переселения соотечественников &laquo;Ориентир&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Times New Roman; font-size: medium;\">Источник : ИА REGNUM<br /></span></p>\n<p><span style=\"font-family: Times New Roman; font-size: medium;\">&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(148, 0, 1467395305, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Фонд поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; при поддержке Комитета по молодежной политике и взаимодействию с общественными организациями на средства гранта Санкт-Петербурга для общественных объединений и Комитета по образованию Правительства Санкт-Петербурга в период&nbsp; с 01.09.2015г. по 15.11.2015 г. реализовал проект &laquo;Исследование проблем языковой и социокультурной адаптации детей мигрантов в Санкт-Петербурге&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">На первом этапе реализации проекта было проведено социологическое исследование около 2000 школьников 7-8 классов общеобразовательных школ Адмиралтейского, Центрального, Невского, Колпинского и Василеостровского районов Санкт-Петербурга, в ходе которого выявлены и систематизированы основные проблемы адаптации и интеграции детей-мигрантов в языковое и социокультурное поле Санкт-Петербурга. Анкета являлась анонимной и включала в себя пятнадцать социальных и общекультурных вопросов. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Отдельный опрос был проведен среди учителей школ на предмет выявления основных проблем при работе с детьми из семей мигрантов и путей их решения, а также среди специалистов в области социокультурной адаптации иммигрантов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">На материалах социологического исследования в общеобразовательных школах Санкт-Петербурга научным коллективом Фонда \"ОРИЕНТИР\" разработана целевая программа языкового и социокультурного сопровождения семей мигрантов в виде рекомендаций для общеобразовательных учреждений Санкт-Петербурга (где рассматривается проблема социальной адаптации детей мигрантов сквозь призму их обучения в школе, показываются реальные и возможные пути её решения).</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Финальным этапом работы проекта стал тематический Круглый стол &laquo;Социокультурная и языковая адаптация&nbsp;детей&nbsp;мигрантов&nbsp;в общеобразовательной школе&raquo; с презентацией результатов социологического исследования и обсуждением рекомендаций по совершенствованию механизма адаптации и интеграции детей мигрантов и их родителей в языковое и социокультурное пространство города.&nbsp; В мероприятии приняли участие представители Комитета по молодежной политике и взаимодействию с общественными организациями, Комитета по межнациональным отношениям и реализации миграционной политики Правительства Санкт-Петербурга, УФМС России по СПб и ЛО, специалисты Информационно-методического центра Центрального района, Главы муниципальных образований, представители РОНО, директора школ, психологи и социологи. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\"><a href=\"https://yadi.sk/i/cv5atufysukEF\">Отчет</a> <br /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\"><a href=\"https://yadi.sk/i/9X2SkVRnswLfa\">Презентация</a><br /></span></p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(149, 0, 1467392780, '13 ноября 2015 года в Информационно-методическом центре Центрального района состоялся круглый стол по проблемам адаптации детей из семей эмигрантов', '', '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">13 ноября 2015 года в Информационно-методическом центре Центрального района состоялся круглый стол &laquo;ОСНОВНЫЕ ПРОБЛЕМЫ и ПРОГРАММА СОПРОВОЖДЕНИЯ АДАПТАЦИИ И ИНТЕГРАЦИИ ДЕТЕЙ ИЗ СЕМЕЙ МИГРАНТОВ И ИХ РОДИТЕЛЕЙ В ЯЗЫКОВОЕ И СОЦИОКУЛЬТУРНОЕ ПОЛЕ САНКТ-ПЕТЕРБУРГА&raquo;. Мероприятие стало завершающим этапом реализации проекта &laquo;Исследование проблем языковой и социокультурной адаптации детей мигрантов в Санкт-Петербурге&raquo; Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, проходящего в период&nbsp; с 01.09.2015г. по 15.11.2015 г. в Санкт-Петербурге &nbsp;при поддержке Комитета по молодежной политике и взаимодействию с общественными организациями на средства гранта Санкт-Петербурга для общественных объединений и при содействии Комитета по образованию Правительства Санкт-Петербурга.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">На круглом столе была представлена презентация по результатам проведенного социологического исследования среди 2000 школьников 7-8 классов общеобразовательных школ Центрального, Невского, Адмиралтейского, Колпинского и Василеостровского районов Санкт-Петербурга, а также среди учителей школ и специалистов в области социокультурной адаптации мигрантов на предмет выявления основных проблем при работе с детьми из семей мигрантов и путей их решения.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Все присутствующие получили тематические брошюры &laquo;ОСНОВНЫЕ ПРОБЛЕМЫ и ПРОГРАММА СОПРОВОЖДЕНИЯ АДАПТАЦИИ И ИНТЕГРАЦИИ ДЕТЕЙ ИЗ СЕМЕЙ МИГРАНТОВ И ИХ РОДИТЕЛЕЙ В ЯЗЫКОВОЕ И СОЦИОКУЛЬТУРНОЕ ПОЛЕ САНКТ-ПЕТЕРБУРГА&raquo;, в которых изложены основные выводы по результатам соцопроса и работы над проектом в целом. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Исследование защищала кандидат психологических наук, старший научный сотрудник факультета политологии Санкт-Петербургского государственного университета, психолог Мария Александровна Пушкина, которая после основной части презентации ответила на вопросы присутствующих. На вопросе о проблеме &laquo;учебной адаптации детей мигрантов&raquo;&nbsp; презентация перешла в оживленную дискуссию. Свое авторитетное мнение высказали почти все участники круглого стола: помощник начальника УФМС по СПб и ЛО В.Д. Ильченко, ведущий специалист отдела реализации миграционной политики, законности и межведомственного взаимодействия Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге А.А. Енбулаев, начальник сектора образовательных учреждений Администрации Центрального района СПб С.А. Зенцова, Глава муниципального образования Смольнинское Г.М. Ранков, заместитель директора ИМЦ Морозов Антон Юрьевич, заместители директора и директора школ №№ 163, 5, 294, 122, 193 Центрального района и многие другие. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В ходе полуторачасовой дискуссии были обсуждены вопросы преодоления языкового барьера, роли родителей, школы и государства в процессе адаптации детей-инофонов, мотивации приезжих к социокультурной и языковой адаптации в петербургском обществе, пробелов в законодательстве и, безусловно, возникающих трудностей в учебном процессе как у педагогов, так и у детей из семей мигрантов.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Единодушно было принято, что проблемы, поднятые на круглом столе, являются острыми и требуют деликатного, тщательного и профессионального подхода. Также участники заседания сошлись в едином мнении, что адаптация мигрантов &ndash; это двухсторонний процесс, в котором одна сторона должна быть готова принять жителей других стран и законодательно, и социально, и профессионально, как и мигранты, приезжающие жить в Санкт-Петербург временно либо на постоянное место жительства, должны одинаково понимать и принимать нормы, принятые в нашем обществе.</span></p>\n<p><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(150, 0, 1467475219, NULL, NULL, '<p style=\"text-align: justify;\">Информационно-аналитический <a href=\"https://yadi.sk/i/dwKqGB34swLQB\">отчет </a>о результатах реализации проекта</p>\n<p style=\"text-align: justify;\">\"Адаптация мигрантов, проживающих на территории Ленинградской области, интегрирование их в единое правовое и культурное поле России, развитие традиционных духовных ценностей\"</p>', NULL, NULL, NULL, NULL, NULL, 1),
(151, 0, 1467475312, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">С начала февраля по конец марта 2016 года на территории восьми районов Ленинградской области на средства федеральных субсидий Фондом социальной и экономической стабилизации (Фонд СиЭС) ЛО совместно с РОО Марийская НКА \"ШИЙ КОРНО\" и Фондом поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; при поддержке Комитета по печати и связям с общественностью Ленинградской области был реализован&nbsp;&nbsp; Проект &laquo;Исследование проблем языковой и социокультурной адаптации детей-мигрантов в Ленинградской области&raquo;. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Проект представлял собой масштабное социологическое исследование, состоящее из двух частей. Благодаря содействию и поддержке Комитета общего и профессионального образования Ленинградской области было проведено анкетирование, в котором приняли участие 2221 учащийся общеобразовательных школ 7-8 классов из восьми районов Ленинградской области: Выборгского, Тосненского, Тихвинского, Гатчинского, Ломоносовского, Кировского, Кингисеппского и Всеволожского. Целью данного опроса стало выявление проблем адаптации и интеграции детей-мигрантов в языковое и социокультурное поле Ленинградской области. Анкетирование было анонимным и включало в себя 14 вопросов по социальной и культурной тематике. Кроме этого, в опросе приняли участие более тридцати экспертов: представители школ, муниципальных образований и органов управления миграционной политикой Ленинградской области. С помощью опроса данной группы респондентов выявлялись трудности работы с детьми-мигрантами и возможные пути их преодоления.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Результаты исследования показывают, что социальный профиль учащихся из семей жителей Ленинградской области и учащихся из семей мигрантов, обучающихся в школах Ленинградской области, приблизительно одинаковый. Дети проводят свободное время с друзьями, ходят в кружки и на секции, большинство учащихся из разных групп респондентов планируют поступать в высшие учебные заведения. То есть национальные различия не являются причиной ограничения у детей социальных контактов. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">При этом стоит отметить, что миграционная нагрузка на общеобразовательные школы в разных районах Ленинградской области отличается. В ходе проведенного опроса на вышеперечисленных территориях больше всего учащихся-мигрантов из других стран оказалось в Тосненском, Всеволожском и Гатчинском районах Ленинградской области. При этом стоит отметить, что в некоторых школах/классах нет детей-мигрантов по свидетельству учителей.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">О&nbsp; языковой адаптации учащихся из семей мигрантов. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">79% опрошенных учащихся 7-8 классов из семей, приехавших из других стран в Ленинградскую область, считают, что русским языком владеют свободно (аналогичный показатель в Санкт-Петербурге составляет 70%). </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">21% учащихся из семей-мигрантов прибегают к внешней помощи при изучении русского языка как неродного. Чаще всего эту помощь оказывает учитель в школе. Если говорить о национальных особенностях, то 62% учащихся родом из Таджикистана в ходе опроса отметили, что прибегают к помощи со стороны при изучении русского языка. Каждый третий ребенок, приехавший с Украины, прибегает к внешней помощи при изучении русского языка. Стоит обратить внимание на школьников с Северного Кавказа, хотя это и регион Российской Федерации, но в помощи при изучении русского языка нуждается каждый пятый ребенок из этой категории. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Выводы по опросу школьников подтверждаются экспертным интервью. 58% опрошенных эксперта в области образования отметили, что сталкивались с языковым барьером при работе с детьми-мигрантами. Об этом прямо говорили учителя Ломоносовского и Выборгского районов Ленинградской области. Претензии педагогов обращены к плохому письменному русскому языку с наличием множества грамматических ошибок. Присутствует мнение, что ребенок из семьи мигрантов в домашних условиях разговаривает на своем родном языке, ребенку в школе трудно перестроиться на русскую речь и у него вообще пропадает стремление учиться. Эксперты отмечали несовпадение программ обучения, по которым обучались дети до поступления в школы Ленинградской области. По этой причине наблюдаются отставания учащихся от предлагаемой школьной программы. У некоторых детей из семей мигрантов отсутствуют знания начальной школы.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">При общении с родителями-мигрантами со стороны учителей было выделено две проблемные точки. Во-первых, это незнание русского языка родителями-мигрантами.&nbsp; Педагогу сложно визуально определить, понимают ли родители его требования или нет.&nbsp; Разница менталитетов, что также усложняет взаимопонимание. &laquo;Родители часто ставят &laquo;во главу угла&raquo; сохранение ценностей, традиций и языка своей страны. Русские традиции, язык учитывают и принимают недостаточно!&raquo; - такую точку зрения выразил один из педагогов-участников опроса.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">О формировании &laquo;конфликтной среды&raquo;. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Большинство опрошенных экспертов в области образования Ленинградской области отметили, что не сталкивались с детскими или подростковыми межнациональными конфликтами в школе. Подобные конфликты в классе сводятся к &laquo;обзыванию&raquo; детей из семей мигрантов. При этом учителя сразу добавляли, что решаются такие инциденты путем личных бесед с учениками. Также были единичные высказывания, что дети из семей мигрантов категорически не принимают культуру страны прибытия, что тоже является причиной конфликтов. Можно сказать, что возникающие проблемы решаются в рабочем порядке силами учреждений системы образования. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;Отвечая же на вопрос, на чьей ответственности лежат основные функции по адаптации детей-мигрантов в социокультурное пространство, эксперты называли одновременно три института: семья и родители, школа и государство. </span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В целом результаты проекта показывают, что по сравнению со школами Санкт-Петербурга, в которых аналогичное исследование проводилось осенью 2015 года, ситуация в Ленинградской области более гармоничная: количество детей-мигрантов меньше, а отношения между подростками проще и добрее.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;Результаты исследования оформлены в методическую брошюру: &laquo;Основные проблемы и программа адаптации и интеграции детей из семей мигрантов и их родителей в языковое и социокультурное поле Ленинградской области&raquo;. В частности в пособии говорится о том, что возникает новый тип школ &ndash; школы с полиэтничным контингентом, где учатся дети, приехавшие в Ленинградскую область из Ближнего Зарубежья в разном возрасте с нулевым уровнем владения русским языком и поступающие не только в первый класс, но и в старшие классы. Это новые реалии современной школы. В то же время школа с полиэтническим контингентом по-прежнему является российской национальной школой.</span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;<em>Участник аналитической группы проекта Пушкина Мария Александровна, кандидат психологических наук, старший научный сотрудник факультета политологии СПбГУ.</em></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\"><a href=\"https://yadi.sk/i/k8QbQeamsx3jk\">Отчет</a><br /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\"><a href=\"https://yadi.sk/i/jzw6Gnh4sx3m4\">Презентация</a><br /></span></p>\n<p style=\"text-align: justify;\">&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(152, 0, 1467475281, NULL, NULL, '<p style=\"text-align: justify;\"><a href=\"https://yadi.sk/i/b1QcTiIdsx3Mr\">Брошюра </a>\"Основные проблемы и программа сопровождения адаптации и интеграции детей из семей мигрантов и их родителей в языковое и социокультурное поле Санкт-Петербурга\"</p>', NULL, NULL, NULL, NULL, NULL, 1),
(153, 0, 1467475150, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">21 марта в Доме молодежи \"Рекорд\" прошло мероприятие для трудовых мигрантов \"Под крылом Петербурга\". Участники получили ответы на вопросы по миграционному законодательству, была оказана социальная, медицинская и образовательная поддержка от сотрудников Администрации Адмиралтейского района Санкт-Петербурга, Управления Федеральной миграционной службы России и от представителей общественных организаций, среди которых был Фонд поддержки добровольного переселения соотечественников \"Ориентир\".</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Трудовые мигранты напрямую задали свои наболевшие вопросы специалистам социальной защиты населения, здравоохранения, Управления Федеральной миграционной службы России по Адмиралтейскому району Санкт-Петербурга.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Вступительную речь перед иностранными гражданами произнес первый заместитель председателя Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге Вадим Окружко, который отметил, что Петербург всегда был центром гармоничного взаимодействия многих народов мира.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В заключительной части перед участниками выступили творческие коллективы подросткого-молодежных клубов Адмиралтейского района и национально-культурные объединения, которые представили традиции своих народов в музыке, вокале и танцах, познакомив с многообразтием культур и творчества разных национальностей.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(154, 0, 1467474525, NULL, NULL, '<p style=\"text-align: justify;\"><span lang=\"LV\"><span style=\"font-family: Times New Roman; font-size: medium;\">24 марта 2016 года в Культурном центре \"Троицкий \" состоялось информационно-культурное мероприятие для трудовых мигрантов России. Как и прежде в Адмиралтейском районе Санкт-Петербурга, организатором данной встречи в Невском районе города выступил Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</span></span></p>\n<p style=\"text-align: justify;\"><span lang=\"LV\"><span style=\"font-family: Times New Roman; font-size: medium;\">На мероприятии мигранты получили бесплатные консультации по вопросам миграционной политики, здравоохранения, образования, трудоустройства и досуга. Среди участников данного мероприятия были сотрудники отдела социальной защиты&nbsp; администрации Невского района, Управление Федеральной миграционной службы, общественные организации, оказывающие правовую защиту интересов мигрантов. Фонд добровольного переселения соотечественников \"Ориентир\" проинформировал трудовых мигрантов об открытии с 1 апреля 2016 года Общественной приемной по оказанию бесплатной юридической помощи иностранным гражданам по адресу: г. Санкт-Петербург, пр-т Римского-Корсакова, д. 39 лит.В.</span></span></p>\n<p style=\"text-align: justify;\"><span lang=\"LV\"><span style=\"font-family: Times New Roman; font-size: medium;\">По традиции встреча был завершена концертом с выступлением творческих коллективов детских и подростковых клубов Невского района. Зрители познакомились с национальной культурой многих народов России в музыке, вокале и танцах.</span></span></p>\n<p><span lang=\"LV\"><span style=\"font-family: Times New Roman; font-size: medium;\">&nbsp;</span></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(155, 0, 1467475124, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">22 апреля 2016 года в СПб ГБУ Культурно-досуговый центр &laquo;Московский&raquo; состоялось информационно-культурное мероприятие для трудовых мигрантов \"Под крылом Петербурга\" (Московский район). </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Организатор данного мероприятия - Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге. Целью проведенной встречи является оказание мигрантам бесплатной информационной помощи по вопросам миграционной политики, здравоохранения, образования, трудоустройства и досуга. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Перед собравшимися выступили сотрудники отделов социальной защиты Московского района Санкт-Петербурга, Управления Федеральной миграционной службы, различные общественные организации, представители национальных диаспор и общин. Фонд добровольного переселения соотечественников \"Ориентир\" как участник общественного мероприятия оказал информационно-правовую поддержку трудовым мигрантам, а также разъяснил обратившимся за помощью гражданам порядок реализации их прав как мигрантов, проживающих и работающих в Санкт-Петербурге.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Мероприятие было завершено ярким выступлением лауреатов различных песенных конкурсов, творческих коллективов детских и подростковых клубов Московского района. Музыка, танцы и песни познакомили гостей мероприятия с национальной культурой народов, живущих на территории Санкт-Петербурга.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(156, 0, 1467475101, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге в очередной раз организовал информационную поддержку трудовым мигрантам, проживающим и работающим во Фрунзенском районе Санкт-Петербурга. Цель проведенного мероприятия - оказание иностранным гражданам бесплатной информационной помощи по вопросам миграционной политики, здравоохранения, образования, трудоустройства и досуга.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Перед собравшимися выступили представители Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге, сотрудники Администрации, отделов социальной защиты Фрунзенского района Санкт-Петербурга, Управления Федеральной миграционной службы, различные общественные организации, представители национальных диаспор и общин. Фонд поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" дал разъяснение обратившимся за помощью гражданам порядок реализации их прав мигрантов, а также проинформировал о работе приемной по бесплатной правовой поддержке мигрантов по адресу: г. Санкт-Петербург, пр. Римского-Корсакова, д. 39 лит. В. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;</span><span style=\"font-family: Calibri; font-size: medium;\">В конце мероприятия перед собравшимися выступили лауреаты различных песенных конкурсов, творческие коллективы детских и подростковых клубов Фрунзенского района Санкт-Петербурга. </span></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(157, 0, 1467475497, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">12 мая 2016 года в Соборной мечети Санкт-Петербурга состоялась встреча президента Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Эдгарса Седлиньша с настоятелем Соборной мечети Санкт-Петербурга, Муфтием Равилем Панчеевым, возглавляющим крупнейшую среди мусульманских объединений Северо-Западного региона централизованную религиозную организацию &laquo;Духовное управление мусульман Санкт-Петербурга и Северо-Западного региона России&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">На встрече обсуждались вопросы, связанные с текущей деятельностью Фонда &laquo;ОРИЕНТИР&raquo; и его открытой приемной правового консультирования в устной и письменной форме для оказания квалифицированной юридической помощи на безвозмездной основе мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области по гражданскому и трудовому праву, миграционному законодательству (в т. ч. в целях предупреждения нарушений миграционного и трудового законодательства, удаления &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области). Муфтий поддержал работу приемной устным соглашением о сотрудничестве с Фондом &laquo;ОРИЕНТИР&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В ходе встречи Муфтий рассказал о ближайших планах и мероприятиях, проводимых Духовным управлением мусульман Санкт-Петербурга и Северо-Западного региона России. Равиль-Хазрат Панчеев призвал Фонд поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; участвовать в мероприятиях проводимых Духовным управлением, для того чтобы просвещать мигрантов о их правах и оказывать юридическую помощь мигрантам оказавшимся в трудной жизненной ситуации, в том числе в решении трудовых споров.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В Соборной мечети Санкт-Петербурга 20.05.2016г. планируется чтение лекции \"Российское трудовое законодательство (практика оформления трудовых отношений в РФ, вопросы трудоустройства) в Санкт-Петербурге\" в рамках просветительского мероприятия по социально-культурной адаптации трудящихся мигрантов Санкт-Петербурга совместно с Централизованной религиозной организацией \"Духовное управление мусульман Санкт-Петербурга и Северо-Западного региона\".</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;</span></p>\n<p><span style=\"font-family: Calibri; font-size: medium;\">&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(158, 0, 1467475845, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">13 мая 2016 года на базе ГАОУ ДПО &laquo;Ленинградский областной институт развития образования&raquo; проведен Круглый стол на тему &laquo;Пути решения трудовых проблем трудящихся-мигрантов&raquo; по наиболее важным социально-правовым вопросам. Мероприятие подготовлено по инициативе Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; в рамках проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">К участию в Круглом столе были приглашены специалисты Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге, представители системы образования и администрации Санкт-Петербурга и районов Ленинградской области, представители НКО, руководители местных национальных диаспор, землячеств и общин, социологи, психологи, эксперты исследовательской группы.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">На встрече были рассмотрены проблемы правового характера, с которыми сталкиваются мигранты в части нарушения их социальных и трудовых прав в Санкт-Петербурге и Ленинградской области, так и проблемы связанные с адаптацией и интеграцией мигрантов в языковое и социокультурное поле в Санкт-Петербурга и Ленинградской области. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Кроме того, были подняты вопросы о текущей деятельности Фонда &laquo;ОРИЕНТИР&raquo;, а также о работе его открытой приемной правового консультирования, действующей на безвозмездной основе для оказания квалифицированной юридической помощи мигрантам-соотечественникам, которые живут и работают в Санкт-Петербурге и Ленинградской области. Основные обращения трудовых мигрантов относятся к гражданскому и трудовому праву, миграционному законодательству.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В заключении мероприятия участниками были обозначены пути решения поднятых социально-правовых вопросов и предложено провести дополнительный Круглый стол на данную тему уже в Законодательном Собрании Санкт-Петербурга.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(159, 0, 1467475866, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">18 мая 2016 года в Концертном зале Санкт-Петербургского Дома национальностей состоялась рабочая встреча на тему &laquo;Изменение миграционного законодательства РФ и вопросы профилактики межнационального, межрелигиозного экстремизма на законодательном уровне&raquo;. Среди организаторов мероприятия были Председатель Комитета Государственной Думы Федерального собрания по конституционному законодательству и государственному строительству Плигин В.Н., глава Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге Махно О.И. и его первый заместитель Окрушко В.Я. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Участник мероприятия Фонд &laquo;ОРИЕНТИР&raquo; проинформировал глав национальных диаспор о работе открытой приемной по правовому консультированию на безвозмездной основе по вопросам миграции, защите интересов мигрантов в сложных ситуациях, трудовых спорах и т.п.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В настоящий момент в Санкт-Петербурге проживают несколько сотен национальностей и по мнению организаторов мероприятия, в руках руководителей диаспор сохранить единство всех народов. </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Основные вопросы, которые были вынесены на повестку дня, - это упрощение программ получения гражданства Российской Федерации, регулирование миграционных вопросов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Руководителям диаспор от Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге была поставлена задача &ndash; не допустить превращения в рабов своих соотечественников и сохранить свой национальный мир и единство с Россией. Россия &ndash; позитивное многонациональное государство, и все народы в ней едины. &laquo;Мы помним ценности СССР, и их нужно сохранить и преумножить&raquo; - такими словами завершил мероприятие Глава Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге Махно О.И.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(160, 0, 1467476084, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">20 мая 2016 года в Соборной мечети Санкт-Петербурга по инициативе Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; в рамках проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo; проведен семинар на тему &laquo;Российское трудовое законодательство (практика оформления трудовых отношений в Российской Федерации, вопросы трудоустройства в Санкт-Петербурге) и социальные гарантии трудящихся&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">В мероприятии приняли участие Начальник отдела надзора за соблюдением трудового законодательства Государственной инспекции труда в г. Санкт-Петербурге Фролова О.Н., специалист отдела трудовой миграции и сотрудничества с работодателями Комитета по труду и занятости населения Санкт-Петербурга Викулов А.П., директор Санкт-Петербургского государственного автономного учреждения &laquo;Центр трудовых ресурсов&raquo; Чистяков А.И.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Участники семинара были проинформированы о действующей практике оформления трудовых отношений с иностранными работниками, особо подчеркнув об обязательном наличии трудового договора при начале трудовой деятельности у работодателя, о работе Центра трудовых ресурсов при необходимости в поиске работы и о грамотном порядке решения возникших трудовых споров с работодателем.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">По окончанию просветительского мероприятия участники семинара &ndash; трудовые мигранты рассказали об основных трудностях, с которыми сталкиваются, находясь в России. По общему мнению, причина у всех одна &ndash; российское миграционное законодательство практически не предусматривает, что вместе с &laquo;дешевой рабочей силой&raquo; сюда пребывают и семьи, которым бывает практически невозможно получить государственную социальную помощь из-за недостатка информации и правовой неграмотности мигрантов. Трудящиеся-мигранты напрямую обратились в насущными вопросами к представителям власти и специалистам по трудоустройству и получили компетентные ответы из первых уст. Некоторые обращения были зафиксированы специалистами для дальнейшей проработки и решения вопросов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Calibri; font-size: medium;\">Фонд &laquo;ОРИЕНТИР&raquo; вручил трудящимся-мигрантам листовки с информацией о работе приемной по правовому консультированию на безвозмездной основе по вопросам миграционного и трудового законодательства в ходе реализации вышеназванного социально-значимого проекта.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(161, 0, 1471363354, NULL, NULL, '<p>В рамках реализации социально значимого проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 01.04.2015 № 79-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (<a href=\"http://civildignity.ru/\">http://civildignity.ru</a>) 8 июля 2016 года президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М подписал Соглашение о сотрудничестве с Северо-Западным Институтом Управления -&nbsp; филиалом Федерального государственного образовательного учреждения высшего образования &laquo;Российской Академией народного хозяйства и государственной службы при Президенте Российской Федерации&raquo; (СЗИУ РАНХиГС).</p>\n<p>Целью данного Соглашения является регулирование вопросов сотрудничества в сфере образовательной, научной, консультационной и иной деятельности между Академией и Фондом.</p>\n<p>Фонд и Академия намерены осуществлять сотрудничество в таких направлениях, как организация и проведение практики студентов и аспирантов, совместных научно-практических исследований и проектов, семинаров и симпозиумов, организация совместных научных учебно-методических публикаций, проведение экспертной оценки, совместная работа со СМИ, обмен информационными и другими материалами, проведение совместных социально значимых мероприятий, в т. ч. международных, а также развитие партнерских отношений с общественными организациями.</p>\n<p>С начала запуска реализуемого проекта привлечены 6 добровольцев &ndash; студентов кафедры гражданского и трудового права СЗИУ РАНХиГС при Президенте РФ.</p>\n<p>В настоящее время Фондом &laquo;ОРИЕНТИР&raquo; направлены обращения в адрес посольств и консульств стран ближнего зарубежья с предложением о сотрудничестве, оказании содействия и помощи иностранным гражданам в законном оформлении и пребывании на территории Санкт-Петербурга и Ленинградской области, защите социально-экономических прав трудовых мигрантов и представительстве интересов трудовых мигрантов в суде.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(162, 0, 1471363528, NULL, NULL, '<p>3 июня 2016 года в рамках проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo; проведен семинар на тему &laquo;Доступ к среднему и высшему образованию. Гарантии в сфере здравоохранения&raquo;.</p>\n<p>В мероприятии приняли участие начальник сектора подтверждения документов об образовании Комитета по науке и высшей школе Чернов Ю.С., начальник отдела общего образования Комитета по образованию Грубская А.В., специалист Комитета по здравоохранению Андреева М.Р.</p>\n<p>Трудовые мигранты получили информацию по видам оказываемой медицинской помощи, которую можно получить за счет полисов ОМС и ДМС бесплатно. Иностранным гражданам были даны рекомендации по выбору страховых компаний, выдающих полиса ОМС и ДМС, в качестве перечня данных компаний организаторами встречи был предложен раздаточный материал.</p>\n<p>Поскольку среди участников просветительского мероприятия были выпускники медицинских ВУЗов, то отдельным предметом разговора было содействие трудоустройству молодых врачей по государственным программам РФ.</p>\n<p>Специалисты Комитета по образованию Санкт-Петербурга дали полное пояснение об определении детей мигрантов в дошкольные и школьные учреждения. Участникам семинара был передан информационно-справочный материал с указанием телефонов специалистов по образованию районов администраций Санкт-Петербурга и Ленинградской области.</p>\n<p>Представитель Комитета по науке и высшей школе изложил порядок подтверждения в РФ дипломов государственного образца о среднем и высшем профессиональном образовании.</p>\n<p>Основной целью семинара была организация возможности предоставить бесплатную консультацию представителями исполнительной власти по актуальным вопросам мигрантов в сфере образования и здравоохранения в Санкт-Петербурге и Ленинградской области. Трудящиеся иностранные граждане порой даже не имели представления, куда обращаться по имеющимся вопросам из-за недостатка информации.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; вручил трудящимся-мигрантам листовки с информацией о работе приемной по правовому консультированию на безвозмездной основе по вопросам миграционного и трудового законодательства в ходе реализации вышеназванного социально-значимого проекта.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(163, 0, 1471363654, NULL, NULL, '<p>В Санкт-Петербурге с 1 апреля 2016 года действует приемная по правовой поддержке иностранных граждан. Создание приемной осуществлено в рамках реализации социально значимого проекта &laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге и Ленинградской области&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 01.04.2015 № 79-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (http://civildignity.ru). Данный грант получен Фондом поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, который является организатором открытия Приемной. Приемная находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.</p>\n<p>В своей практике, юристы, оказывающие квалифицированную помощь, часто сталкиваются с обращениями иностранных граждан, связанными с фактами нарушений их прав со стороны должностных лиц государственных органов, а именно &ndash; незаконное изъятие документов, необоснованные аресты и задержания, отказ в приеме документов, наложение штрафов. Защититься от произвола представителей власти и обезопасить себя от необоснованных претензий в дальнейшем можно, обжаловав их действия в административном и судебном порядке.</p>\n<p>Случай из практики. Гражданин Таджикистана Н. обратился за помощью в приёмную по юридической защите с целью урегулирования спора по неправомерным действиям сотрудников миграционной службы по Санкт-Петербургу и Ленинградской области. Суть обращения такова.</p>\n<p>Гражданин Таджикистана Н. легально работает в России на протяжении 5 лет водителем маршрутного автобуса. В мае 2016 года он вновь подал документы на получение патента на работу.</p>\n<p>10 июня 2016 года состоялась очередная выездная проверка документов водителей транспортных средств города Санкт-Петербурга сотрудниками миграционной службы и полиции. Гражданин Таджикистана Н. в это время находился в рабочем автомобиле в качестве пассажира и обучался новому маршруту следования у своего коллеги. В ходе проверки было установлено, что у гражданина Таджикистана Н. нет в наличии патента на работу, поэтому согласно составленному протоколу ему предъявили обвинение в незаконной трудовой деятельности с наложением штрафа и выдворением из России. И дело было передано в суд Невского района Санкт-Петербурга. На все объяснения гражданина Таджикистана Н. о том, что патент он получит 10 июня в 16.00 ч, сотрудники миграционной службы и полиции не реагировали и считали пустыми. У иностранного гражданина Н. были изъяты все документы до решения суда.</p>\n<p>В оперативном порядке юристами приёмной правовой защиты выдана консультация о незамедлительных действиях в данной ситуации. А именно при получении патента его следовало предъявить судье, рассматривающему дело, и в кротчайшие сроки.</p>\n<p>16 июня 2016 года гражданин Таджикистана Н., получив патент, предъявил его судье, который вернул материалы судебного производства миграционной службе, т.к. не было обнаружено состава нарушения.</p>\n<p>В результате выполнения рекомендованных юристами правовой приемной действий и полного контроля над возникшей проблемой ситуация была полностью урегулирована и не допущены судебные тяжбы. Мигрант не был выдворен, а ему грозили именно выдворением.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(164, 0, 1471363763, NULL, NULL, '<p>В текущем году жители Санкт-Петербурга и Ленинградской области в очередной раз отметили национальный праздник башкирского и татарского народов Сабантуй, в котором принимают участие все народности. В 2016 года Сабантуй прошел 9 июля в деревне Энколово, Всеволожский район Ленинградской области.</p>\n<p>Сабантуй в Энколово - это масштабное событие, на котором гости праздника смогли поучаствовать в конкурсах по борьбе и бегу в мешках, принять участие в перетягивании каната, посмотреть концертную программу с выступлениями различных коллективов и состязания в национальной борьбе куреш, посетить выставку национальных ремесел, попробовать национальную кухню - чак-чак, плов, бишбармак и др.</p>\n<p>На празднике Сабантуй 2016 в Санкт-Петербурге были замечены почетные гости - председатель Государственной Думы РФ Сергей Нарышкин, губернатор Ленинградской области Александр Дрозденко, вице-губернатор Санкт-Петербурга Александр Говорунов, председатель Государственного Совета Республики Татарстан Фарид Мухаметшин и полномочный представитель Республики Башкортостан при президенте России Артур Шайнуров.</p>\n<p>Спикер Госдумы Сергей Нарышкин в своей речи отметил ценность и важность праздника Сабантуй с таким ярким национальным колоритом и всеми почитаемого в России.</p>\n<p>К участию в мероприятии были приглашены все руководители национально-культурных объединений, а также президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М., который поздравил глав татарской и башкирской диаспор с национальным праздником с пожеланием продуктивного сотрудничества на благо всех представителей этих народностей, населяющих Санкт-Петербург и Ленинградскую область.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(165, 0, 1471363861, NULL, NULL, '<p>31 июля во Всеволожском районе состоялся межнациональный праздник \"Энколово собирает друзей\".</p>\n<p>Он объединил сразу несколько праздников народов, населяющих Ленинградскую область, которые традиционно отмечаются в летнее время: марийский &laquo;Пеледыш пайрем&raquo; (&laquo;Праздник цветов&raquo;), армянский &laquo;Вардавар&raquo;, чувашский &laquo;Акатуй&raquo;, финнов-ингерманландцев &laquo;Юханнус&raquo;, татаро-башкирский &laquo;Сабантуй&raquo;, славянский Ивана Купала, якутский &laquo;Ысыах&raquo;, эвенкийский &laquo;Нургэнэк&raquo;, литовский &laquo;Йонинес&raquo;, вепсский &laquo;Древо жизни&raquo;.</p>\n<p>Организаторы праздника &laquo;Энколово собирает друзей&raquo;: &laquo;Дом дружбы Ленинградской области&raquo; и &laquo;Дом дружбы Всеволожского района&raquo; при поддержке комитета по местному самоуправлению, межнациональным и межконфессиональным отношениям Ленинградской области.</p>\n<p>В программу празднования вошли концерт творческих коллективов и исполнителей, молодецкие игры и забавы; мастер-классы художников по живописи и национальных ремесел гончарного и декоративно-прикладного искусства, выставка национальных костюмов, дегустация национальных блюд. Также организована ярмарка традиционных изделий и промыслов.</p>\n<p>Президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М. поздравил всех руководителей приглашенных национально-культурных объединений с летним праздником с пожеланием продуктивного сотрудничества &nbsp;и вручил им официальные письма-приглашения на &laquo;Дни диаспор&raquo;, организуемые Фондом &laquo;ОРИЕНТИР&raquo; в рамках реализации социально значимого проекта &laquo;&laquo;Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(166, 0, 1480435248, NULL, NULL, '<p>8 июля 2016 года президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М подписал Соглашение о сотрудничестве с Северо-Западным Институтом Управления -&nbsp; филиалом Федерального государственного образовательного учреждения высшего образования &laquo;Российской Академией народного хозяйства и государственной службы при Президенте Российской Федерации&raquo; (СЗИУ РАНХиГС).</p>\n<p>Целью данного Соглашения является регулирование вопросов сотрудничества в сфере образовательной, научной, консультационной и иной деятельности между Академией и Фондом.</p>\n<p>Фонд и Академия намерены осуществлять сотрудничество в таких направлениях, как организация и проведение практики студентов и аспирантов, совместных научно-практических исследований и проектов, семинаров и симпозиумов, организация совместных научных учебно-методических публикаций, проведение экспертной оценки, совместная работа со СМИ, обмен информационными и другими материалами, проведение совместных социально значимых мероприятий, в т. ч. международных, а также развитие партнерских отношений с общественными организациями.</p>\n<p>С начала запуска реализуемого проекта привлечены 6 добровольцев &ndash; студентов кафедры гражданского и трудового права СЗИУ РАНХиГС при Президенте РФ.</p>\n<p>В настоящее время Фондом &laquo;ОРИЕНТИР&raquo; направлены обращения в адрес посольств и консульств стран ближнего зарубежья с предложением о сотрудничестве, оказании содействия и помощи иностранным гражданам в законном оформлении и пребывании на территории Санкт-Петербурга и Ленинградской области, защите социально-экономических прав трудовых мигрантов и представительстве интересов трудовых мигрантов в суде.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(167, 0, 1490802714, NULL, NULL, '<p>С целью наиболее продуктивного и эффективного информирования мигрантов (в том числе предупреждения нарушений миграционного и трудового законодательства, удаления &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области) была организована и открыта приемная правовой поддержки в устной и письменной форме для оказания юридической помощи на безвозмездной основе трудящимся-мигрантам.</p>\n<p>Социально значимый проект стал возможным благодаря средствам государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (http://civildignity.ru).</p>\n<p>Проведены отдельные встречи, оказаны в устной и письменной форме юридические консультации и дана правовая помощь на безвозмездной основе по гражданскому и трудовому праву для мигрантов на базе открытой приемной правового консультирования по адресу: г. Санкт-Петербург, пр. Римского-Корсакова, д. 39, лит. В, рядом с отделом по вопросам вынужденных переселенцев и беженцев Управления по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области.</p>\n<p>Данное направление работы Фонда &laquo;ОРИЕНТИР&raquo; является основным инструментом механизма реализации социально-значимого проекта &laquo;Оказание юридической помощи на безвозмездной основе мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;.</p>\n<p>По итогам работы приемной на основании заполненных анкет посетителей были выявлены и проанализированы самые актуальные проблемы правовой грамотности мигрантов в вопросах миграционного и трудового законодательства.</p>\n<p>По данным проведенного социологического исследования на базе поступивших обращений мигрантов за юридической помощью Фондом &laquo;ОРИЕНТИР&raquo; с привлечением необходимых специалистов &ndash; юристов, социолога, методиста, дизайнера-верстальщика &ndash; разработаны брошюры по миграционному и трудовому законодательству в доступной и понятной мигрантам форме. Брошюры переведены на узбекский и таджикский языки.</p>\n<p>Проектом предусмотрена бесплатная раздача выпущенных брошюр волонтерами в районных отделах Управления по вопросам миграции ГУ МВД России по Санкт-Петербургу и Ленинградской области. Раздача брошюр проводится по настоящее время и среди посетителей приемной правовой поддержки.</p>\n<p>&nbsp;<strong>Скачать брошюры:</strong></p>\n<p><a title=\"Тадж_ГД.pdf\" href=\"http://fondorientir.ru/filemanager/i/d93e6a2b/Тадж_ГД.pdf\">Тадж_ГД.pdf</a>&nbsp; - &nbsp;для граждан Таджикистана</p>\n<p><a title=\"Узб_ГД.pdf\" href=\"http://fondorientir.ru/filemanager/i/7d076756/Узб_ГД.pdf\">Узб_ГД.pdf</a>&nbsp;- для граждан Узбекистана</p>', NULL, NULL, NULL, NULL, NULL, 1),
(168, 0, 1487146139, NULL, NULL, '<p>Фонд &laquo;ОРИЕНТИР&raquo; с использованием средств государственной поддержки, выделенных в качестве гранта, в соответствии с распоряжением Президента Российской Федерации&nbsp; от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo;, с 01.10.2016 г. по настоящее время безвозмездно оказывает юридическую помощь иностранным гражданам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области, в сфере миграционного и трудового законодательства Российской Федерации.</p>\n<p>В рамках указанного проекта открыта общественная приемная бесплатной юридической помощи, действующая с 1 октября 2016 года по президентскому гранту, где оказывается помощь в решении миграционных вопросов и трудовых споров.</p>\n<p>Согласно календарному плану проекта, предусмотрена выездная работа общественной приемной бесплатной юридической помощи Фонда &laquo;ОРИЕНТИР&raquo; в районных центрах Ленинградской области для консультирования и оказания социальной поддержки мигрантам-соотечественникам.</p>\n<p>Очередной выезд специалистов состоится в Гатчинский район 16 февраля 2017 года, приемная будет работать с 11.00 часов до 17.00 часов в здании районного Центра занятости по адресу г. Гатчина, ул. Карла Маркса, д. 66 А<span style=\"font-family: Calibri, sans-serif;\">.</span></p>\n<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по миграционному законодательству, а также гражданскому и трудовому праву. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>\n<p>Оперативную юридическую помощь смогут получить трудовые мигранты, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в PФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для соотечественников на территориях вселения.&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(169, 0, 1490602305, NULL, NULL, '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(170, 0, 1490604330, NULL, NULL, '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>\n<p>Проект бесплатной юридической помощи мигрантам-соотечественникам в районах Ленинградской области реализуется в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(171, 0, 1491133890, NULL, NULL, '<p>Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга.</p>\n<p>Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной форме по миграционному законодательству, а также гражданскому и трудовому праву. Проект оказания бесплатной юридической помощи реализуется с использованием средств государственной поддержки, выделенных в качестве гранта, в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo;.</p>\n<p>Оперативную юридическую помощь получили свыше 300 трудовых мигрантов, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в PФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п. Свыше 1000 человек оповещены о работе приемной бесплатной юридической помощи.</p>\n<p>Среди участников мероприятия и прихожан мечети были бесплатно распространены брошюры по миграционному и трудовому законодательству. Данные издания выпущены в 2016 году Фондом &laquo;ОРИЕНТИР&raquo; в доступной и понятной мигрантам форме по наиболее актуальным вопросам в сфере миграции и трудовых споров, которые выявлены за все время работы приёмной правовой поддержки с помощью проведенного социологического исследования среди обращающихся иностранных граждан. Волонтерами проекта передано лично в руки мигрантам порядка 500 брошюр.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для соотечественников на территориях вселения.&nbsp;</p>\n<p><img style=\"vertical-align: middle; display: block; margin-left: auto; margin-right: auto;\" title=\"20170310_Мечеть_1.jpg\" src=\"http://fondorientir.ru/filemanager/i/b897c719/20170310_Мечеть_1.jpg\" alt=\"20170310_Мечеть_1.jpg\" width=\"576px\" height=\"576\" /></p>\n<p><img style=\"vertical-align: middle; display: block; margin-left: auto; margin-right: auto;\" title=\"20170310_Мечеть_2.jpg\" src=\"http://fondorientir.ru/filemanager/i/a3491a6e/20170310_Мечеть_2.jpg\" alt=\"20170310_Мечеть_2.jpg\" width=\"576px\" height=\"576\" /></p>\n<p><img style=\"vertical-align: middle; display: block; margin-left: auto; margin-right: auto;\" title=\"20170310_Мечеть_3.jpg\" src=\"http://fondorientir.ru/filemanager/i/e36959d7/20170310_Мечеть_3.jpg\" alt=\"20170310_Мечеть_3.jpg\" width=\"576px\" height=\"576\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(172, 0, 1490705146, 'О выездной работе приемной Фонда \"ОРИЕНТИР\"', '<p>13 марта 2017 года с 11.00 часов до 14.00 часов в здании администрации Подпорожского муниципального района Ленинградской области по адресу: г. Подпорожье, пр. Ленина, д. 3 состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Подпорожском районе Ленинградской области.</p>', '\r\n<p>&nbsp;13 марта 2017 года с 15.00 часов до 18.00 часов в здании Лодейнопольского филиала ГКУ центр занятости населения Ленинградской области по адресу: г. Лодейное Поле, ул. Гагарина, д. &nbsp;22 состоится выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в Лодейнопольском районе Ленинградской области.</p>\r\n<div>\r\n<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>\r\n</div>', NULL, NULL, NULL, NULL, NULL, 1),
(173, 0, 1492353548, NULL, NULL, '<p>Мероприятие возглавлял&nbsp; руководитель Администрации Губернатора Санкт-Петербурга&nbsp; - вице-губернатор Санкт-Петербурга&nbsp; А.Н. Говорунов.</p>\n<p>Консультативный совет по вопросам реализации государственной миграционной политики в Санкт-Петербурге является постоянно действующим коллегиальным совещательным органом при Правительстве Санкт-Петербурга. Целью работы Совета является обеспечение комплексного подхода к решению вопросов, связанных с реализацией государственной миграционной политики в Санкт-Петербурге. Решения Совета носят рекомендательный характер.</p>\n<p>Среди участников заседания Совета присутствовали &nbsp;представители исполнительных органов государственной власти, в том числе главы Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге, руководители национально-культурных объединений и общественных организаций Санкт-Петербурга, а также президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Э.М. Фонд &laquo;ОРИЕНТИР&raquo; в настоящее время реализует социально значимый проект по оказанию бесплатной юридической помощи мигрантам (с использованием средств государственной поддержки, выделенных в качестве гранта в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп и на основании конкурса, проведенного Движением &laquo;Гражданское достоинство&raquo; (http://civildignity.ru).</p>\n<p>На повестке дня рассматривались вопросы реализации в Санкт-Петербурге Концепции государственной миграционной политики Российской Федерации, организация государственного многофункционального миграционного центра Санкт-Петербурга, реализация полномочий органов местного самоуправления в области адаптации и интеграции мигрантов.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>По итогам заседания Совета приняты следующие решения: разработать Концепцию реализации миграционной политики в Санкт-Петербурге, поддержать идею создания государственного многофункционального миграционного центра Санкт-Петербурга (инициатор&nbsp; - Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге) и вынести этот вопрос на рассмотрение Правительства Санкт-Петербурга, органам местного самоуправления&nbsp; предусмотреть эффективные мероприятия в области адаптации и интеграции мигрантов.</p>\n<div><img title=\"DSC05425 &mdash; копия.jpg\" src=\"http://fondorientir.ru/filemanager/i/c5f7eefc/DSC05425 &mdash; копия.jpg\" alt=\"DSC05425 &mdash; копия.jpg\" width=\"800px\" height=\"475px\" /></div>\n<p><img title=\"DSC05397 &mdash; копия.jpg\" src=\"http://fondorientir.ru/filemanager/i/cd991ff1/DSC05397 &mdash; копия.jpg\" alt=\"DSC05397 &mdash; копия.jpg\" width=\"800px\" height=\"414px\" /></p>\n<div>\n<div>\n<div><img title=\"DSC05406 &mdash; копия.jpg\" src=\"http://fondorientir.ru/filemanager/i/6dc6cbe1/DSC05406 &mdash; копия.jpg\" alt=\"DSC05406 &mdash; копия.jpg\" width=\"800px\" height=\"410px\" /></div>\n<div>&nbsp;</div>\n<div>&nbsp;</div>\n</div>\n</div>', NULL, NULL, NULL, NULL, NULL, 1),
(174, 0, 1490711221, NULL, NULL, '<div>&nbsp;</div>\n<div>В минувшую субботу, 25 марта, в&nbsp;России начала действовать недавно объявленная российскими властями миграционная амнистия для&nbsp;граждан Таджикистана.</div>\n<div>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.2\">У тех, кому необходимо самостоятельно подать документы на&nbsp;легализацию, времени не&nbsp;так много&nbsp;&mdash; амнистия будет действовать ровно месяц, до&nbsp;24 апреля 2017 года.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.3\">Что касается порядка оформления документов и&nbsp;лиц, на&nbsp;которых эта амнистия не&nbsp;распространяется, то обо&nbsp;всех подробностях рассказал президент Федерации мигрантов России Вадим Коженов.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.5\">\"Амнистироваться будут люди трёх категорий. Первая&nbsp;&mdash; это те, кто въехал на&nbsp;территорию РФ и, скажем так, потерялся, растворились в&nbsp;правовом поле. У них уже давно просрочен въезд, режим пребывания нарушен. Но они не&nbsp;были пойманы, суд их не&nbsp;осудил, выдворение по&nbsp;ст. 18.8 КоАП им не&nbsp;оформлялось, они просто остались в&nbsp;тени\",&nbsp;&mdash; отмечает Коженов.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.7\">Соответственно, тем мигрантам, кто находится вне&nbsp;поля зрения российских надзорных органов, срок временного пребывания в&nbsp;РФ будет продлён на&nbsp;90 дней. \"Будут амнистированы ещё две категории граждан&nbsp;&mdash; те, кому уже закрыт въезд по&nbsp;двум нарушениям и&nbsp;те, кому был закрыт въезд за&nbsp;нарушение режима пребывания. Если въезд был закрыт программой Управления МВД по&nbsp;вопросам миграции, то, при&nbsp;подаче необходимых документов, въезд им будет открыт автоматически\",&nbsp;&mdash; пояснил Коженов Sputnik Таджикистан.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.9\">Амнистия будет распространяться на&nbsp;тех мигрантов, кто нарушил федеральный закон \"О порядке выезда из&nbsp;РФ и&nbsp;въезда в&nbsp;РФ\" двумя способами&nbsp;&mdash; либо в&nbsp;течение трех лет более двух раз привлекался к&nbsp;административной ответственности, либо после истечения срока пребывания не&nbsp;успел выехать за&nbsp;пределы страны в&nbsp;течении 30 дней. Оба этих нарушения являются основанием для&nbsp;запрета на&nbsp;въезд.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.b\">По словам президента Федерации мигрантов, единственное исключение из&nbsp;этих трёх категорий амнистированных&nbsp;&mdash; это лица, серьёзно провинившиеся перед российской Фемидой. К примеру, если мигрант попадает под&nbsp;ч.3 ст.27 упомянутого закона о&nbsp;порядке въезда, где чётко говориться о&nbsp;том, что иностранный гражданин не&nbsp;имеет права въезжать в&nbsp;страну, если имеет неснятую или&nbsp;непогашенную судимость за&nbsp;совершение умышленного преступления на&nbsp;территории РФ.&nbsp;</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.d\">Кроме того, это касается тех уроженцев Таджикистана, кто уже попадал под&nbsp;депортацию, выдворение и реадмиссию из&nbsp;РФ или&nbsp;в данный момент находится в&nbsp;специальном учреждении временного содержания иностранных граждан (СУВСИГ). В этом случае иностранному работнику на&nbsp;амнистию рассчитывать не&nbsp;стоит.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.f\">Куда идти за&nbsp;документами И всё же, каков порядок получения амнистии?</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.h\">\"Люди должны явиться в&nbsp;дежурную часть любого УВД, там им будет выдано подготовленное заявление установленного образца. Они это заявление заполняют, фотографируются, проходят процедуру дактилоскопии&nbsp;&mdash; снятия отпечатков пальцев\",&nbsp;&mdash; объясняет Коженов, делая упор на&nbsp;том, что по&nbsp;закону, на&nbsp;начальном этапе этим должны заниматься именно полицейские в&nbsp;дежурной части.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.j\">Затем иностранцы идут в&nbsp;ближайшее подразделение Главного управления по&nbsp;вопросам миграции МВД РФ (бывшее ФМС), где получают новую миграционную карту, либо печать в&nbsp;паспорт. После этого они становятся чисты перед законом и&nbsp;на общих основаниях могут находиться в&nbsp;России в&nbsp;течении 90 дней. За это время они должны успеть подать заявление о&nbsp;продлении срока временного пребывания и&nbsp;встать на&nbsp;миграционный учет.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.l\">Что же касается общей численности граждан, которые смогут воспользоваться миграционной амнистией, то ГУВМ МВД по&nbsp;Москве сообщает о&nbsp;том, что на&nbsp;подведомственной им территории въезд был закрыт для&nbsp;20 тысяч человек, у&nbsp;которых не&nbsp;было серьёзных проблем с&nbsp;законом и&nbsp;которые смогут воспользоваться действующим послаблением.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.n\">По данным столичных правоохранителей, ещё порядка 75 тысяч человек находятся в \"тени\", вне&nbsp;правового поля и&nbsp;должны в&nbsp;ближайшее время встать на&nbsp;учёт. Напомним, что в&nbsp;конце февраля президент России Владимир Путин совершил дипломатическое турне по&nbsp;странам Центральной Азии. После визита в&nbsp;Таджикистан и встречи с&nbsp;Эмомали Рахмоном, вице-премьер РФ Игорь Шувалов заявил о&nbsp;том, что по&nbsp;итогам переговоров российских и&nbsp;таджикистанских властей около&nbsp;200 тысяч трудовых мигрантов смогут вновь приехать в&nbsp;Россию.</p>\n<p class=\"font_8\" data-reactid=\".0.$SITE_ROOT.$desktop_siteRoot.$PAGES_CONTAINER.1.1.$SITE_PAGES.$c1fr0.1.$ia0v0vc0_0.0.0.$child.$0.1.$1.$5.$0.0.p\">Хотя власти РТ настаивали на&nbsp;полной отмене \"запретных\" списков, куда входят порядка 400 тысяч граждан республики, амнистия для&nbsp;половины из&nbsp;них&nbsp;&mdash; уже немалый успех для&nbsp;дипломатического корпуса и&nbsp;экономики Таджикистана, считают эксперты.</p>\n</div>\n<div>&nbsp;</div>\n<div>Источник: http://ru.sputnik-tj.com</div>', NULL, NULL, NULL, NULL, NULL, 1),
(175, 0, 1490801635, NULL, NULL, '<p>Организатор: Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге, администрация Невского района Санкт-Петербурга.</p>\n<p>Место проведения:Дом молодежи Василеостровского района,&nbsp;Большой проспект В.О. дом 65 литер А.</p>\n<p>План мероприятия:</p>\n<ul>\n<li>с 17.00 до 18.00 консультации для мигрантов;</li>\n<li>с 18.00 до 19.00 концерт творческих коллективов.</li>\n</ul>\n<p>Вход свободный.</p>\n<p><img src=\"http://s019.radikal.ru/i623/1703/84/17a7fe4622c0.jpg\" alt=\"афиша\" width=\"679\" height=\"960\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(176, 0, 1493124259, NULL, NULL, '<p>Цель мероприятия - повышение уровня правовой грамотности мигрантов, работающих в Санкт-Петербурге; ознакомление приезжих с их конституционными, социальными и трудовыми правами и обязанностями.</p>\n<p>Представители органов исполнительной власти Санкт-Петербурга совместно со специалистами социально ориентированных организаций напрямую отвечали на проблемные вопросы со стороны участников мероприятия, преимущественно состоящих из наиболее заинтересованных слушателей &ndash; иностранных граждан-жителей Санкт-Петербурга и Ленинградской области, в том числе трудовых мигрантов, приехавших в Петербург из стран Средней Азии.</p>\n<p>В мероприятии непосредственное участие приняли сотрудники Администрации Василеостровского района, представители Комитета по межнациональным отношениям и реализации миграционной политики, Комитета по вопросам законности, правопорядка и безопасности, Комитета по труду и занятости населения, Комитета по социальной политике Санкт-Петербурга; специалисты Санкт-Петербургского регионального отделения &laquo;Российский Красный Крест&raquo;, юристы Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;.</p>\n<p>В заключительной части перед участниками выступили национальные творческие коллективы, которые представили традиции своих народов в музыке, вокале и танцах, познакомив с многообразием культур и творчества разных национальностей.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(177, 0, 1493123977, NULL, NULL, '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;<br /><br />Проект бесплатной юридической помощи мигрантам-соотечественникам в районах Ленинградской области реализуется с использованием средств государственной поддержки в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп.&nbsp;<br /><br />Оперативную юридическую помощь смогут получить трудовые мигранты, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в PФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.&nbsp;<br /><br />Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для соотечественников на территориях вселения.&nbsp;<br /><br />По всем вопросам обращаться по телефонам приемной бесплатного правового консультирования Фонда &laquo;ОРИЕНТИР&raquo; в Санкт-Петербурге: 8 (812)385-69-89, 8(911)253-85-01 (с 10.00 часов до 18.00 часов по будням).<img src=\"data:image/png;', NULL, NULL, NULL, NULL, NULL, 1),
(178, 0, 1493123943, NULL, NULL, '<p>10 апреля 2017 года с 11.00 часов до 17.00 часов в городском молодежно-спортивном центре по адресу: г. Сосновый бор, ул. Ленинградская, д. 30 состоялась выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих в г. Сосновый бор Ленинградской области.</p>\n<p>Более 60 мигрантов получили юридическую помощь исключительно на безвозмездной основе. Консультации были предоставлены в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.<iframe style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://vk.com/video_ext.php?oid=-16374902&amp;id=456242384&amp;hash=211139c98c3ef313\" frameborder=\"0\" width=\"640\" height=\"360\"></iframe></p>', NULL, NULL, NULL, NULL, NULL, 1),
(179, 0, 1493123866, NULL, NULL, '<p>Мигранты получат юридическую помощь исключительно на безвозмездной основе. Консультации будут предоставляться в устной и, при необходимости, в письменной форме по гражданскому и трудовому праву, миграционному законодательству, сообщает пресс-служба администрации Всеволожского района.</p>\n<p>Цель работы выездной приемной Фонда &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</p>\n<p>Проект бесплатной юридической помощи мигрантам-соотечественникам в районах Ленинградской области реализуется в соответствии с распоряжением Президента Российской Федерации от 05.04.2016 № 68-рп.</p>\n<p>Оперативную юридическую помощь смогут получить трудовые мигранты, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в РФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p><strong>По всем вопросам обращаться по телефонам приемной правового консультирования Фонда &laquo;ОРИЕНТИР&raquo; в Санкт-Петербурге: 8 (812) 385-69-89, 8-911-253-85-01 (с 10.00 до 18.00 по будням).</strong></p>\n<p><strong>Приемная Фонда&nbsp;<strong>&laquo;ОРИЕНТИР&raquo;</strong>&nbsp;будет работать в кабинете 134-а администрации Всеволожского района, расположенной по адресу: г. Всеволожск, Колтушское шоссе, д. 138.</strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(180, 0, 1505287291, NULL, NULL, '<p>gdfgdfgdfgdfgdfg</p>', NULL, NULL, NULL, NULL, NULL, 1),
(181, 0, 1505293574, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><span>&nbsp; &nbsp; &nbsp;</span>Законопроект внес депутат Сысоев. Согласно инициативе, законную силу проект обретет в 2018 году 1 января.</span></p>\n<p style=\"text-align: left;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Закон предусматривает обязательное страхование на случай административного выдворения либо депортации. Дело в том, что в прошлом году российский бюджет &laquo;похудел&raquo; на 100 миллионов рублей из-за выдворения иностранных граждан.</span></p>\n<p style=\"text-align: left;\"><span style=\"font-family: \'times new roman\', times; font-size: medium; text-align: justify;\"><span>&nbsp; &nbsp; &nbsp;</span>Сысоев предлагает обязать мигрантов оплачивать страховку на случай выдворения из сраны.&nbsp; Правила страхования:</span></p>\n<ul style=\"text-align: left;\">\n<li style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Обязанность страховщика возместить расходы, которые связаны с депортацией или административным выдворением.</span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Возмещение средств должно происходить независимо от даты окончания полиса.</span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\">В зависимости от того можно ли установить срок будущего пребывания мигранта на территории РФ, срок полиса от 180 при установленных законных сроках и 270 дней, если период возможного пребывания в стране иностранного гражданина не установлен.</span></li>\n<li style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Страховая сумма устанавливается в размере не менее 100 тысяч рублей.</span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"text-align: justify; font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Согласно официальной статистике только 40-45% иностранцев выезжают за собственный счет, все остальные покидают страну за счет российского бюджета. На текущий момент казна не готова к таким тратам, поэтому и был внесен данный законопроект. В перспективе он поможет и сократить расходы на содержание мигрантов в специальных учреждениях.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><strong>Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(182, 0, 1505293599, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp; &nbsp; &nbsp;В Доме национальностей 22 марта прошло собрание совета позванного реализовывать государственный курс в области миграционной политики в Санкт-Петербурге. Председателем заседания был Вице-Губернатор Александр Говорунов. Встреча объединила представителей всех государственных структур &ndash; силовики, главы национально-культурных автономий, исполнительные органы, а также приняла участие Светлана Агапитова &ndash; уполномоченная по правам ребенка Петербурга.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Центральным вопросом заседания стала инициатива по созданию в северной столице миграционного многофункционального центра. Оценивалась как целесообразность самой идеи, так и проблемы связанные с реализации данного проекта. Дело в том, что сейчас миграционные услуги предоставляют только частные бюро и агентства. В связи с этим сложно проконтролировать деятельность всех организаций, что приводит к фактам мошенничества со стороны недобросовестных учреждений. Спекулируют на желании мигранта легализовать свой статус и, обещая скорую выдачу РВП, ВНЖ и гражданства лишают иностранного гражданина последних средств на существование. Некоторые коммерческие организации завышают реальную цену на услуги в полтора, а то и в два раза. Например, трудовые патенты можно получить только через ООО &laquo;Единый миграционный центр&raquo;, который, в свою очередь, является частной посреднической организацией и неподконтролен государству. Стоимость оформления патента в ЕМЦ достигает до 40 тысяч рублей, при том, что альтернативы оформить трудовой патент бесплатно непосредственно в Управлении по вопросам миграции попросту нет.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>МФЦ станет не только регулятором цен на рынке, но и часть прибыли будет отправлять в государственную казну. Эту информацию сообщили в пресс-службе правительства Питера.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Общим собранием было одобрена идея создания многофункционального миграционного центра. Следующий этап обсуждений пройдет на уровне Правительства Санкт-Петербурга.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(183, 0, 1505293334, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>9 мая 2017 года Президент РФ В. Путин подписал Указ &laquo;Об особенностях применения усиленных мер безопасности в период проведения в Российской Федерации чемпионата мира по футболу FIFA 2018 года и Кубка конфедераций FIFA 2017 года&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Президентский Указ предписывает органам МВД России в период с 1 июня по 12 июля 2017 года осуществлять постановку на учет по месту пребывания или регистрация по месту жительства иностранных граждан и лиц без гражданства, в течение одних суток со дня их прибытия в место пребывания или место жительства при представлении документов, предусмотренных законодательством Российской Федерации, принимающей стороной либо непосредственно иностранными гражданами или лицами без гражданства в соответствующий территориальный орган Министерства внутренних дел Российской Федерации.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong><span id=\"_________FIFA_2017\">Изменения, касающиеся сроков постановки на миграционный учет, введены временно в целях обеспечения безопасности во время проведения Кубка конфедераций FIFA 2017 года.</span></strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>В связи с началом действия Указа изменен график работы Управления по вопросам миграции. В соответствии с последней актуальной информацией встать на миграционный учет можно ежедневно, включая выходные дни. Физические лица, выступающие в качестве принимающей стороны, могут подать уведомление в районном отделе Управления по вопросам миграции по месту пребывания мигранта, представители юридических лиц в Управлении по вопросам миграции на ул. Красных Текстильщиков, 15.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong><span id=\"_________FIFA_2017____9052017\">Единый режим работы миграционной службы по вопросу постановки на учет иностранных граждан в период проведения Кубка конфедераций FIFA 2017 года на основании Указа Президента РФ от 9.05.2017 года.</span></strong></span></p>\n<ul style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Понедельник с 9.00-13.00 и с 14.00-17.30</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Вторник&nbsp; с 9.00-13.00 и с 14.00-17.30</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Среда с 9.00-13.00 и с 14.00-16.00</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Четверг с 9.00-13.00 и с 14.00-19.30</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Пятница с 9.00-13.00 и с 14.00-16.30</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Суббота с 9.00-13.00 и с 14.00-16.00</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Воскресение с 10.00-13.00</span></li>\n</ul>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(184, 0, 1505293895, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Каждый иностранный гражданин, который хочет легально работать в РФ, обязан ежегодно получать патент на работу.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><span>&nbsp; </span>Патент &ndash; это документ, который позволяет иностранному гражданину работать в определенном регионе РФ и только по той специальности, которая указана в патенте.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\">Все чаще от наших клиентов мы получаем информацию о том, что после того, как они собрали полный пакет документов для оформления патента и сдали его в ЕМЦ &ndash; готовый патент они получить не могут. Вместо патента они получают официальный отказ в выдаче патента на работу.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><strong><span>&nbsp; &nbsp; &nbsp;</span>Что это значит?</strong>&nbsp;Официальный отказ &ndash; это документ, который выдается иностранному гражданину, в нем указывается причина отказа выдачи патента. Получив данный документ, иностранный гражданин обязан покинуть территорию РФ по истечению его временной регистрации. При этом вновь подать документы на патент он может ТОЛЬКО ЧЕРЕЗ ГОД.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><strong>Основные причины отказа в выдаче патента:&nbsp;</strong></span></p>\n<ol>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Аннуляция предыдущего патента за не вовремя оплаченный налог;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Подача в ЕМЦ неполного пакета документов или предоставление ложных данных;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Предоставление поддельных (нелегальных) документов для оформления патента.&nbsp;</span></li>\n</ol>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><strong>&nbsp; &nbsp; &nbsp;Что делать, если Вам отказали в патенте?</strong>&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><span>&nbsp; &nbsp; &nbsp;</span>В данной ситуации существует только одно решение проблемы:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp; &nbsp; &nbsp;1. Обжаловать решение об отказе выдачи патента в суде.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-size: medium; font-family: \'times new roman\', times;\"><span>&nbsp; &nbsp; &nbsp;</span>Задача юриста: доказать необоснованный отказ в выдаче патента, получить решение о том, чтобы Вам выдали патент и Вы смогли остаться работать в РФ.&nbsp;</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(185, 0, 1505294081, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Ненадлежащее проведение проверки знаний претендентов обойдется ВУЗам и центрам по работе с мигрантами в сумму от 100 до 300 тыс. руб. Должностные лица &laquo;отделаются&raquo; штрафами до 30 тыс. руб. Ожидается, что подготовленные поправки в закон позволят улучшить ситуацию в области тестирования знаний мигрантов и выдачи им соответствующих сертификатов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp;&nbsp;</span>С 2015 года все мигранты, желающие остаться в России с целью работать либо жить, должны подтвердить знание таких предметов, как русский язык, история и основы законодательства, пройдя тестирование. Однако экспертам Центра ОНФ было поручено провести исследование, которое подтвердило бы или опровергло эффективность данного мероприятия. По результатам работы, проведенной в январе 2017 года, выяснилось: приобрести сертификат достаточно просто. Для этого не обязательно даже присутствовать при сдаче. Как сообщили специалисты, некоторые предлагают доставку документа курьером в указанное заказчиком место.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Минобразования и науки, изучив результаты исследования, внесло поправки в КоАП. Их целью выступает установление ответственности за нарушения, возникающие при проведении комплексного экзамена. Так, должностным лицам грозит штраф в 15-30 тыс. руб. В случае выявления нарушений в вузах или локальных центрах придется заплатить от 100 до 300 тыс. руб. Следует отметить, что наказание грозит также за несвоевременную подачу информации об образовательных документах по итогам экзамена. Предположительно поправки начнут действовать с 1 января 2018 года.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(186, 0, 1505295700, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>МВД подготовило Концепцию миграционной политики на 2018&ndash;2020 годы, которая предполагает перенаправление миграционных потоков на Дальний Восток. Документ предполагает и передачу МВД права по адаптации мигрантов. Это может привести к конфликту между министерством и Федеральным агентством по делам национальностей (ФАДН), которое также претендует на эти полномочия. Источник &ldquo;Ъ&rdquo;, близкий к администрации президента, допускает, что в итоге миграционная служба может быть воссоздана как отдельное ведомство.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Разработанный МВД (ведомству передали в 2016 году полномочия в области миграции &mdash; после расформирования ФМС) документ призван заменить действующую&nbsp;<a href=\"http://kremlin.ru/events/president/news/15635\" target=\"_blank\">концепцию</a>&nbsp;миграционной политики до 2025 года, которая была принята в 2012 году. Необходимость обновления концепции авторы объясняют &laquo;политической нестабильностью в различных регионах мира&raquo;, которая &laquo;усиливает миграционную подвижность&raquo; населения. МВД говорит о необходимости &laquo;создания условий для привлечения россиян в приграничные, стратегически важные и трудонедостаточные регионы&raquo;. Одной из важных целей МВД считает &laquo;обеспечение миграционного потока на территорию Дальнего Востока и Байкальского региона России&raquo;. Для приезжающих предлагается создать инфраструктуру, упростить допуск к образовательным, медицинским и социальным услугам, увеличить подъемные для россиян до 1&nbsp;млн руб. По данным МВД, в 2012&ndash;2016 годах количество мигрантов в РФ удвоилось по сравнению с 2007&ndash;2011 годами, при этом доля внутренних мигрантов составила 88%, а внешних - 12%.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"https://www.kommersant.ru\" target=\"_blank\">Коммерсант.ru</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(187, 0, 1505295992, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>У мигрантов будут снимать отпечатки пальцев и фотографировать в процессе оформления регистрации по месту пребывания или жительства. В Госдуме уже находится на рассмотрении соответствующий проект федерального закона. Инициатором изменений миграционного законодательства выступает Министерство внутренних дел, сообщает РИА Новости со ссылкой на выступление Александра Горового, занимающего должность первого заместителя министра МВД, в Совете Федерации.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; </span>Как сообщил представитель ведомства, соответствующий проект федерального закона обусловлен текущими реалиями в миграционной сфере. Если решение по нему будет положительным, то изменения коснутся всех без исключения иностранцев, временно пребывающих на территории страны. Подчеркивается, что дактилоскопировать и фотографировать будут и граждан Евразийского союза, каких-то особых преференций в сравнении с другими иностранцами МВД решило им не предоставлять. Не играет роли и цель приезда в Россию &ndash; процедура снятия отпечатков пальцев с фотографированием обязательна независимо от причины. Известно, что проводиться она будет как по месту пребывания гражданина, так и по месту его проживания.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Стали известны и мотивы инициативы по изменению миграционного законодательства. Как подчеркнул Горовой, только в прошлом году зафиксировано более 26 тысяч случаев, когда мигранты пытались въехать в страну, используя чужие документы. Названы и основные государства, граждане которых чаще всего пытались пересечь границу, выдавая себя за других людей. По словам представителя ведомства, основной поток таких мигрантов &ndash; выходцы из стран Средней Азии.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; </span>Проверка граждан проводилась с помощью системы &laquo;Папилон&raquo;, которая сейчас используется во всех регионах России и позволяет в считанные минуты проверить личность человека. &laquo;Папилон&raquo; принадлежит к типу автоматизированных дактилоскопических систем, которые пришли на смену картотечной регистрации. По состоянию на 2007 год в базах &laquo;Папилон&raquo; насчитывалось 32 млн. дактилокарт и они постоянно пополнялись новыми данными.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; </span>Напомним, что в скором времени можно ожидать и других изменений миграционного законодательства. Об этом, в частности, ранее рассказал журналистам Игорь Зубков, занимающий пост заместителя главы Министерства внутренних дел. Несколько соответствующих проектов уже находятся на рассмотрении в Государственной думе, но все детали пока что неизвестны.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(188, 0, 1505296574, NULL, NULL, '<p><span style=\"font-size: medium; font-family: \'times new roman\', times;\">&nbsp; &nbsp;&nbsp;Многие иностранные граждане желают получить&nbsp;Вид на жительство, так как наличие данного документа подразумевает ряд неоспоримых&nbsp;<strong>преимуществ</strong>:&nbsp;</span></p>\n<ol style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Возможность находиться в России легально весь срок действия документа без привязки к определенному региону;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Возможность свободно въезжать и выезжать из страны;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Трудоустройство в любом городе РФ без оформления патента и иных документов;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Бесплатная медицинская помощь и пенсионное обеспечение;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Последующее получение гражданства РФ;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Право на владение жилой собственностью на любых основаниях</span></li>\n</ol>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Когда можно подавать документы на ВНЖ:&nbsp;</strong></span></p>\n<ol style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Если Вы прожили полгода по РВП, то уже можете подавать документы на ВНЖ; &nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Если Вы проживаете по РВП дольше, тогда на ВНЖ Вы можете подать&nbsp;<strong>за полгода до истечения срока РВП</strong>.&nbsp;</span></li>\n</ol>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp;&nbsp;</span>Срок рассмотрения заявления о выдаче ВНЖ составляет&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium;\">6 месяцев</strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Мы советуем&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium;\">заранее</strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;подготовить все необходимые документы. Помимо стандартного набора документов, которые иностранец уже имеет на руках при наличии РВП, ему также необходимо предоставить в государственные органы:&nbsp;</span></p>\n<ol style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Документ, подтверждающий наличие денежных средств; &nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Документ, подтверждающий владение русским языком;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Нотариально заверенный перевод паспорта;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Документ об отсутствии инфекционных и ВИЧ заболеваний.&nbsp;</span></li>\n</ol>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">ВНИМАНИЕ: Одним из самых&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium;\">важных</strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;документов для подачи документов на ВНЖ является&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium;\">справка о подтверждении доходов</strong><span style=\"font-family: \'times new roman\', times; font-size: medium;\">, например, справка 2-НДФЛ.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp;&nbsp;</span>Также обращаем Ваше внимание на то, что, если у вас отсутствует аттестат или диплом об образовании, Вам необходимо пройти тестирование по русскому языку на уровень ВНЖ.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(189, 0, 1505297198, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>В среду в Госдуме был принят законопроект, который, помимо лишения российского гражданства за террористическую деятельность, включал в себя также ряд других поправок, в частности, упрощенную процедуру получения гражданства украинцами, сообщает Первый канал.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp;</span>Основные изменения коснулись перечня необходимых документов. Если раньше украинцам, желающим получить российский паспорт, необходимо было в обязательном порядке предоставить справку от уполномоченных органов Украины об отказе от гражданства, то теперь ее требовать не будут. Достаточно нотариально оформить две копии заявления об отказе. Одна из них будет подаваться в миграционную службу России, другая &ndash; отправляться на родину.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp;З</span>аконопроект поддержали и представители МВД. По их словам, на сегодня превалирующее большинство украинцев, претендующих на получение российского гражданства, не могли осуществить это по причине особенностей украинского законодательства. Получить справку из уполномоченных органов своей страны удавалось единицам, тогда как, по подсчетам МВД, на территории России уже проживают сотни тысяч граждан сопредельного государства. Как подчеркнул Александр Горовой, занимающий пост первого замглавы МВД РФ, около 600 тыс. украинцев ждут решения вопроса по получению российского паспорта, они уже привезли семьи, влились в общество, но законодательно все оформить не представлялось возможным, так как большинство запросов игнорируется украинской стороной. Принятый в среду законопроект устранит данную проблему в корне.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Согласно статистическим данным, сейчас в РФ насчитывается около 476 тыс. украинцев, которые получили право на временное проживание, еще 176 тыс. оформили вид на жительство. Право на работу в России каждый год продлевает порядка 777 тыс. украинских граждан.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>Напомним, что в первом чтении вышеупомянутый законопроект был принят 19 мая. Помимо этого, в России и раньше существовал механизм более быстрого оформления гражданства для иностранцев, при условии, что они &ndash; носители русского языка.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span>&nbsp; &nbsp; &nbsp;</span>В ответ на упрощение получения российского гражданства, представители миграционной службы Украины заявили, что будут продолжать считать тех, кто воспользуется такой процедурой, своими гражданами. Действующее законодательство предусматривает, что прекращение гражданства Украины возможно лишь соответствующим указом президента страны.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://ufms.spb.ru/\" target=\"_blank\">Миграционный форум Санкт-Петербурга</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(190, 0, 1505297448, NULL, NULL, '<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">После того, как иностранный гражданин въехал на территорию РФ и указал в миграционной карте цель въезда &laquo;Работа&raquo;, он может&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">оформить трудовой патент</a>&nbsp;для того, чтобы легально работать в России.</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Обращаем Ваше внимание на то, что оформить трудовой патент иностранец обязан&nbsp;<strong>в первые 30 дней</strong>&nbsp;с момента приезда в Россию!&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">К моменту подачи документов на патент в госорганы, у иностранного гражданина&nbsp;<strong>уже должны быть готовы</strong>&nbsp;такие документы, как:&nbsp;</span></p>\n<ol>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/medkomissiya_na_raboty/\">Медицинская комиссия</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;&ndash; срок оформления от 5 дней;&nbsp;</span></li>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/test/\">Тестирование по русскому языку</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;&ndash; срок оформления от 7 дней;&nbsp;</span></li>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/migracionnyi-uchet/\">Миграционный учёт</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">;&nbsp;</span></li>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/dmc/\">Полис ДМС</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">.&nbsp;</span></li>\n</ol>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Таким образом, процесс подготовки документов перед подачей в госорганы&nbsp;<strong>в среднем занимает 10 дней</strong>. Это значит, что у иностранца есть&nbsp;<strong>максимум неделя</strong>&nbsp;после въезда, чтобы уже начинать подготавливать необходимые документы, чтобы успеть по срокам.&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Поэтому наши юристы настоятельно рекомендуют Вам не оставлять на последние дни оформление патента и&nbsp;<strong>сразу после въезда в РФ уже начать собирать необходимые документы</strong>!&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>В случае, если Вы не успели сдать нужные документы за 30 дней:&nbsp;</strong></span></p>\n<ul>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Вам необходимо сделать &laquo;выезд-въезд&raquo;; &nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Получить новую миграционную карту на границе;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Заново пробовать подавать документы на патент.</span></li>\n</ul>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(191, 0, 1505297850, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span style=\"text-decoration-line: underline;\"><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\">Гражданство РФ</a></span>&nbsp;желают получить многие иностранные граждане, особенно те, у кого в РФ живет семья или близкие родственники. Но не каждый знает о том, что Российское гражданство можно получить по упрощённой схеме.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Что значит упрощённая схема получения гражданства РФ?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Это значит, что иностранец, минуя стадию&nbsp;<a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\">оформления РВП</a>, сразу получает&nbsp;<a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\">ВНЖ</a>, а затем&nbsp;<a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\">гражданство РФ</a>&nbsp;по сокращенному периоду рассмотрения документов.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Классическая схема получения гражданства предполагает проживание по РВП, затем по ВНЖ 5 лет, и только потом иностранец может подавать на гражданство. В то время как упрощенная схема позволяет&nbsp;<span style=\"text-decoration: underline;\">сразу получить ВНЖ и после подать документы на гражданство</span>.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Итак, кто же имеет возможность получить гражданство РФ по упрощённой схеме?&nbsp;</strong></span></p>\n<ol style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Иностранный гражданин, который хорошо владеет русским языком;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Иностранец, чьи близкие родственники по восходящей линии (мама/папа, бабушка/дедушка и т.д.) проживают или ранее проживали на территории РФ, РСФСР, Российской Империи.</span></li>\n</ol>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">В совокупности эти два условия дают право иностранному гражданину получить гражданство РФ по упрощённой схеме!</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Таким образом, получение гражданства РФ по упрощённой схеме выглядит так:&nbsp;</strong></span></p>\n<ol style=\"text-align: justify;\">\n<li>Сбор и подача документов на носителя русского языка;</li>\n<li>Получение&nbsp;<a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/status_nositelya/\">статуса носителя русского языка</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">;</span></li>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\">Оформление ВНЖ</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">;</span></li>\n<li><a style=\"font-family: \'times new roman\', times; font-size: medium;\" href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\">Оформление гражданства</a><span style=\"font-family: \'times new roman\', times; font-size: medium;\">.</span></li>\n</ol>\n<p style=\"text-align: right;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;</span><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(192, 0, 1505298721, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><a href=\"http://docs.cntd.ru/document/456076096\" target=\"_blank\">Приказом МВД России от 24.05.2017 N 311</a>&nbsp;установлено, что заявление подается по месту постановки на учет соотечественника в качестве участника&nbsp;<a href=\"http://docs.cntd.ru/document/901985595\" target=\"_blank\">государственной программы</a>&nbsp;и рассматривается территориальным органом МВД России на региональном уровне.</span></p>\n<p class=\"font8\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Решение о выплате пособия на обустройство принимается комиссией в течение 15 дней со дня подачи заявления, оформляется протоколом и утверждается руководителем (начальником) территориального органа МВД России.</span></p>\n<p class=\"font8\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">О принятом решении получатель пособия уведомляется не позднее трех рабочих дней со дня его принятия.&nbsp;</span></p>\n<p class=\"font8\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><a href=\"http://docs.cntd.ru/document/420227231\" target=\"_blank\">Приказ ФМС России от 10.10.2014 N 555</a>&nbsp;признан утратившим силу с 20 августа 2017 года.</span><br /><span style=\"font-family: \'times new roman\', times; font-size: medium;\"> <a href=\"http://docs.cntd.ru/document/456076096\" target=\"_blank\">Приказ</a>&nbsp;зарегистрирован в Минюсте России 08.08.2017.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Источник:</strong>&nbsp;<a href=\"http://www.компания-дис.рф\" target=\"_blank\">ГК \"Деловые информационные системы\"</a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(193, 0, 1505298999, NULL, NULL, '<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Мы уже не раз писали Вам о том, что каждый иностранец, который работает по патенту, обязан иметь на руках&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">действующий миграционный учёт</a>, который делается на основании оплаченных чеков за&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патент</a>.&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Сообщаем Вам о том, что&nbsp;<strong>с сентября 2017 года ужесточаются требования миграционного учёта</strong>, на которые важно обратить внимание&nbsp;<strong><span style=\"text-decoration: underline;\">уже сейчас!</span></strong>&nbsp;&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">На сегодняшний день документы на продление миграционного учёта принимаются, даже если регистрация просрочена на несколько дней и уже не действует.&nbsp;</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Что ждёт Вас в сентябре:&nbsp;</strong></span></p>\n<ol>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium;\">Инспектора больше не будут принимать документы на продление с просроченной регистрацией!</strong>&nbsp;При отсутствии действующей регистрации с иностранца будет взиматься штраф в размере от 2 000 до 5 000 рублей;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">При подаче документов на продление регистрации&nbsp;<strong style=\"font-family: \'times new roman\', times; font-size: medium;\">с просрочкой хоть на 1 день</strong>&nbsp;&ndash; это отказ в выдаче регистрации, штраф и выезд на границу!&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Оплаченные чеки за патент&nbsp;<strong style=\"font-family: \'times new roman\', times; font-size: medium;\">не будут являться основанием</strong>&nbsp;для продления регистрации в Санкт-Петербурге, так как у предыдущей регистрации истёк срок действия!&nbsp;</span></li>\n</ol>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">ВАЖНО:&nbsp;МФЦ Санкт-Петербурга&nbsp;<strong>уже не принимает документы на продление миграционного учёта с просроченной регистрацией!&nbsp;</strong></span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Что делать, если в сентябре иностранец просрочит миграционный учёт?&nbsp;</strong></span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">В случае если Вы вовремя не успели продлить регистрацию, необходима следующая последовательность действий:</span></p>\n<ol>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Вам необходимо выехать на границу;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Обновить миграционную карту;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Оформить новую регистрацию.&nbsp;</span></li>\n</ol>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Только в таком случае Вы можете легально находиться в России и осуществлять трудовую деятельность.</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(194, 0, 1505299208, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Каждый иностранный гражданин, который прибыл на территорию РФ, обязан соблюдать законы страны. В противном случае иностранцу грозит&nbsp;<a href=\"http://all-migration.ru/services/addservices/Advokat/\">постановка запрета на въезд в РФ</a>&nbsp;сроком от 3 до 10 лет. Это очень большой срок, особенно для тех иностранных граждан, кто уже трудоустроился, завел семью, а в будущем планировал стать Гражданином РФ.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Поэтому сегодня специально для Вас мы подготовили&nbsp;<strong>список основных причин постановки запрета на въезд в РФ</strong>. Внимательно изучите их, чтобы в будущем у Вас не возникло никаких проблем с законодательством!</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>Итак, за что могут поставить запрет на въезд:</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Согласно 114 ФЗ &laquo;О порядке выезда и въезда в РФ&raquo;, запрет на въезд иностранному гражданину ставится за:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>1. Нарушение сроков пребывания в России;&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Согласно 27 ст. 114 ФЗ, срок запрета на въезд зависит от того, сколько дней иностранный гражданин находился на территории РФ нелегально:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&ndash; до 180 суток, запрет на въезд на 3 года;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&ndash; от 180 до 270 суток, запрет на въезд на 5 лет;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&ndash; более 270 суток, запрет на въезд на 10 лет (максимальный срок).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><em>Комментарий юриста:&nbsp;</em><strong>Нарушение сроков пребывания в РФ</strong>&nbsp;&ndash;&nbsp;<strong>наиболее распространённая причина постановки запрета на въезд в РФ</strong>&nbsp;у наших клиентов. Поэтому каждому иностранному гражданину необходимо&nbsp;<strong>внимательно следить</strong>&nbsp;за тем, когда заканчиваются сроки документов и&nbsp;<strong>заранее</strong>&nbsp;выезжать до окончания их действия!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>2. Привлечение к административной ответственности более 2 раз за последний год ( 11п. 27 ст. 114 ФЗ);&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">К административным правонарушениям относятся: &nbsp;</span></p>\n<ul style=\"text-align: justify;\">\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Фиктивный миграционный учёт;&nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Курение или распитие в спиртных напитков в общественных местах; &nbsp;</span></li>\n<li><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Нарушения ПДД (штрафы за парковку, превышение скорости, управление автомобилем без водительского удостоверения и т.д.) и другое.&nbsp;</span></li>\n</ul>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">ВАЖНО:&nbsp;Даже если Вы оплатили штрафы за административное нарушение &ndash; это не избавляет Вас от постановки запрета на въезд в РФ!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>3. Трудовую деятельность иностранного гражданина в России без патента.</strong>&nbsp;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Работа без&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>&nbsp;&ndash; это административное нарушение, которое ведет к мгновенной&nbsp;<strong>депортации</strong>&nbsp;иностранного гражданина с возможной постановкой запрета на въезд!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Это далеко не все причины постановки запрета на въезд, более подробно о причинах постановки запрета на въезд, Вы сможете узнать из 26 и 27 п. 114ФЗ</span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://migrant.ru\" target=\"_blank\">МИГРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(195, 0, 1505299508, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">С 15 сентября изменятся &nbsp;формы заявлений об оформлении патента, его переоформлении, выдаче дубликата, и внесении изменений в сведения, содержащиеся в нем (<a href=\"http://www.garant.ru/hotlaw/federal/1133639/\">приказ МВД России от 14 августа 2017 г. № 635</a>).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Речь идет о выдаче патентов на работу иностранцам, прибывающим в Россию в безвизовом порядке. Ранее это функция входила в полномочия ФМС России, однако после упразднения ведомства перешла к МВД России.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Приказ ФМС России от 8 декабря 2014 г. № 639 \"<a href=\"http://base.garant.ru/70839638/\">Об утверждении форм заявлений, представляемых в связи с оформлением патента, его переоформлением, выдачей его дубликата или внесением изменений в сведения, содержащиеся в патенте</a>\" будет считаться утратившим силу.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Отметим, что ранее ведомство представило для публичного обсуждения законопроект об установлении административной ответственности&nbsp;<a href=\"http://www.garant.ru/news/1132323/\">за прием на работу иностранцев, при въезде в Россию не указавших в качестве цели визита \"работу\"</a>. В частности, граждан планируется наказывать штрафом в размере от 2 тыс. до 5&nbsp;тыс. руб., должностных лиц &ndash; от 25 тыс. до 50 тыс. руб, юрлиц &ndash; от 250 тыс. до 800 тыс. руб. либо административным приостановлением деятельности на&nbsp;срок от 14 до 90 суток.<br /></span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://www.garant.ru\" target=\"_blank\">ГАРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(196, 0, 1509535668, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>ентября в 17.00</strong>&nbsp;в Доме еврейской культуры ЕСОД (ул. Б. Разночинная, д. 25) прошло информационно-культурное мероприятие для мигрантов &laquo;Под крылом Петербурга&raquo;, организованное Комитетом по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">В ходе мероприятия, специалистом фонда поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" были проведены консультации мигрантам, по вопросам получения социальной, правовой и иных видов помощи.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span style=\"background-color: #f7f7f7; color: #333333;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;</span><em style=\"font-size: 1em; background-color: #f7f7f7; font-family: \'Open Sans\', Helvetica, Arial, sans-serif; margin: 0px; padding: 0px;\">адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em><span style=\"background-color: #f7f7f7; color: #333333;\">&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"background-color: #f7f7f7; color: #333333; font-family: \'times new roman\', times; font-size: medium;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span style=\"background-color: #f7f7f7; color: #333333;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;</span><em style=\"margin: 0px; padding: 0px; color: #000000;\">адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em><span style=\"background-color: #f7f7f7; color: #333333;\">&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</span></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; background-color: #f7f7f7; color: #333333; font-size: medium;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</span></p>\n<p style=\"text-align: justify;\"><img style=\"float: left;\" src=\"http://fondorientir.ru/filemanager/i/2b495c5c/01_01.jpg\" alt=\"\" width=\"425\" height=\"402\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(197, 0, 1509542028, NULL, NULL, '<p style=\"text-align: justify;\"><strong>13 cентября в 17.00</strong>&nbsp;в Доме еврейской культуры ЕСОД (ул. Б. Разночинная, д. 25) прошло информационно-культурное мероприятие для мигрантов &laquo;Под крылом Петербурга&raquo;, организованное Комитетом по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</p>\n<p style=\"text-align: justify;\">В ходе мероприятия, специалистом фонда поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" были проведены консультации мигрантам, по вопросам получения социальной, правовой и иных видов помощи.</p>\n<p style=\"text-align: justify;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;<em>адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em>&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</p>\n<p style=\"text-align: justify;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</p>\n<p style=\"text-align: justify;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;<em>адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em>&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</p>\n<p style=\"text-align: justify;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</p>\n<div id=\"cp_widget_cec7a70b-811d-4b03-9a88-070d393c5b86\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_cec7a70b-811d-4b03-9a88-070d393c5b86\"; cpo[\"_fid\"] = \"AoDAwEOV6qNS\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>no name</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 1024</span><span>height</span><span> 1024</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 1024</span><span>height</span><span> 1024</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 1024</span><span>height</span><span> 1024</span></noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(198, 0, 1509541680, NULL, NULL, '<p style=\"text-align: justify;\"><strong>12 октября</strong> в &laquo;Городском информационно-расчетном центре&raquo; (пр. Шаумяна, 20). состоялось информационно-культурное мероприятие для иностранных граждан &laquo;Под крылом Петербурга&raquo;.</p>\n<p style=\"text-align: justify;\">Специалист фонда фонда поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" провел консультации для иностранных граждан по вопросам миграционного законодательства, трудоустройства, образования, социального обеспечения.</p>\n<p style=\"text-align: justify;\">Также на данном мероприятии приняли участие представители других ведомств, в том числе специалисты отделов районной администрации и сотрудники правоохранительных органов.&nbsp;</p>\n<p style=\"text-align: justify;\">В 18.00 состоялся концерт для иностранных граждан, где выступили национальные творческие коллективы нашего города.</p>\n<div id=\"cp_widget_db6912ef-6089-4487-ab60-ded1095b12ad\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_db6912ef-6089-4487-ab60-ded1095b12ad\"; cpo[\"_fid\"] = \"A8OAgEOp6qvY\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2017/11/1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(199, 0, 1510049275, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>10 сентября</strong> во Всеволожском районе, в деревне Новое Девяткино, состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;</span></p>\n<div id=\"cp_widget_c839c3f1-4fe9-418b-979c-4bbce21385d1\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_c839c3f1-4fe9-418b-979c-4bbce21385d1\"; cpo[\"_fid\"] = \"A4CADGOROv1K\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2017/11/7&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(200, 0, 1510049776, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>5 сентября</strong> в Сосновом Бору состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области, специалисты провели 2 семинара. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;</span></p>\n<div id=\"cp_widget_2ecaf711-1348-4e33-a3be-06699bb24a95\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_2ecaf711-1348-4e33-a3be-06699bb24a95\"; cpo[\"_fid\"] = \"A0JAlFe8O36M\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2017/11/7&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(201, 0, 1510055021, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>03 сентября</strong> в Кингисеппском &nbsp;районе, в городе Ивангород, состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">&nbsp;</span></p>\n<div id=\"cp_widget_b3ed5fc2-f867-4497-9abf-1e2dc5b8cf85\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_b3ed5fc2-f867-4497-9abf-1e2dc5b8cf85\"; cpo[\"_fid\"] = \"AsCAXEO0ODlS\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2017/11/7&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 1024&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(202, 0, 1511332637, NULL, NULL, '<p style=\"text-align: justify;\"><strong>ДУШАНБЕ, 21 ноя&nbsp;&mdash; Sputnik, Дмитрий Матвеев.</strong>&nbsp;Число иностранцев, которые заключают фиктивные браки в&nbsp;России, по-прежнему велико, заключат эксперты.</p>\n<p style=\"text-align: justify;\">На прошлой неделе прокуратура одного из&nbsp;районов Воронежа через&nbsp;суд&nbsp;<a href=\"http://ru.sputnik-tj.com/migration/20171113/1023877559/zhitelnitsa-voronezha-vyshla-zamuzh-tadzhik-radi-besplatnogo-remonta.html\" target=\"_blank\" rel=\"noopener\">оспорила фиктивный брак</a>&nbsp;россиянки и&nbsp;гражданина Таджикистана. Пара заключила союз, но&nbsp;вместе супруги при&nbsp;этом не&nbsp;проживали. А буквально на&nbsp;следующий день прокуратура Башкортостана потребовала&nbsp;<a href=\"http://ru.sputnik-tj.com/migration/20171114/1023886704/rossii-zakroyut-saytov-fiktivnye-braki-migrantov.html\" target=\"_blank\" rel=\"noopener\">заблокировать десять сайтов</a>, предлагающих мигрантам заключить фиктивный брак.</p>\n<p style=\"text-align: justify;\">Sputnik Таджикистан узнал, является ли фиктивный брак правонарушением и&nbsp;какими последствиями он грозит мигрантам.</p>\n<p><img src=\"http://fondorientir.ru/filemanager/i/91a7d0c2/1022329413.jpg\" alt=\"\" width=\"547\" height=\"296\" /></p>\n<h3 style=\"text-align: justify;\">Что это такое и&nbsp;для чего</h3>\n<p style=\"text-align: justify;\">Цели заключения фиктивного брака вполне банальны. Для мигранта это возможность получить разрешение на&nbsp;временное проживание в&nbsp;России или&nbsp;подать в&nbsp;упрощенном порядке документы на&nbsp;российское гражданство. Для россиянок это способ заработать.</p>\n<p style=\"text-align: justify;\">Количественного пика в&nbsp;России фиктивные браки с&nbsp;мигрантами достигли три-четыре года назад. К нынешнему моменту их число несколько снизилось. Объясняется это и&nbsp;ужесточением миграционного законодательства и&nbsp;снижением числа мигрантов в&nbsp;России.</p>\n<p style=\"text-align: justify;\">По словам депутата муниципального образования \"Литейный округ\" в&nbsp;Петербурге Равшанбека Курбанова, в&nbsp;России сейчас даже существуют целые брачные агентства, предлагающие мигрантам заключить фиктивные союзы.<br />\"Говорят, что стоит такая услуга от&nbsp;50 тысяч рублей и&nbsp;выше\",&nbsp;&mdash; пояснил парламентарий.</p>\n<p style=\"text-align: justify;\">Преподаватель кафедры конституционного и&nbsp;административного права РАНХиГС Сергей Андрейцо уточнил, что на&nbsp;заключение таких браков обычно идут гражданки России в&nbsp;возрасте, которым рисковать уже особо нечем.</p>\n<p style=\"text-align: justify;\">\"Тем более, в&nbsp;большинстве случаев это делается более или&nbsp;менее грамотно. Они подписывают брачный договор, и&nbsp;при расторжении брака мигрант ничего от&nbsp;женщины не&nbsp;получает\",&nbsp;&mdash; добавил Андрейцо.</p>\n<h3 style=\"text-align: justify;\">Что светит за&nbsp;фиктивный брак</h3>\n<p style=\"text-align: justify;\">В России законы по&nbsp;отношению к&nbsp;иностранцам, которые заключили фиктивный брак, не&nbsp;такие жесткие, как&nbsp;в европейских странах. Например, законодательство Германии предусматривает за&nbsp;это уголовное наказание. За фиктивный брак с&nbsp;иностранцем можно получить до&nbsp;трех лет лишения свободы.</p>\n<p style=\"text-align: justify;\">Занимающиеся этим вопросом немецкие социальные работники приходят к&nbsp;людям домой и&nbsp;проверяют, живет ли пара вместе в&nbsp;случае, если появляется подозрение, что иностранец не&nbsp;проживает с&nbsp;супругой-немкой.</p>\n<p style=\"text-align: justify;\">В России уголовной ответственности за&nbsp;это пока нет. Хотя в&nbsp;2015 году, например, депутаты законодательного собрания Калужской области выступали с&nbsp;инициативой ввести уголовную ответственность для&nbsp;мигрантов, которые ради получения легального статуса заключают фиктивные браки с&nbsp;введением новой статьи в&nbsp;Уголовный кодекс России&nbsp;&mdash; \"Незаконное получение разрешения на&nbsp;временное проживание в&nbsp;России, гражданства РФ\".</p>\n<h3 style=\"text-align: justify;\">Почему фиктивных браков так много</h3>\n<p style=\"text-align: justify;\">Сам факт фиктивного брака в&nbsp;России зачастую очень сложно выявить.</p>\n<p style=\"text-align: justify;\">\"Получить судебное решение на&nbsp;право входить в&nbsp;их (супругов&nbsp;&mdash; ред.) дом и&nbsp;проверять, ведут ли они совместное хозяйство, очень тяжело\",&nbsp;&mdash; пояснил Сергей Андрейцо.</p>\n<p style=\"text-align: justify;\">Через суд приходится оспаривать фиктивный брак и&nbsp;после того, как&nbsp;его выявляют. Обычно этим занимается прокуратура.</p>\n<p style=\"text-align: justify;\">В итоге мигранты перестали бояться ответственности за&nbsp;фиктивные браки, надеясь на&nbsp;существующие недоработки в&nbsp;законе. По словам юриста Александра Ерошкина, в&nbsp;его практике был случай, когда выходец из&nbsp;одной из&nbsp;стран Центральной Азии женился в&nbsp;России, будучи уже женатым на&nbsp;родине.</p>\n<p style=\"text-align: justify;\">\"Он предоставил поддельную справку о&nbsp;том, что в&nbsp;браке на&nbsp;родине не&nbsp;состоит, и&nbsp;сумел жениться здесь. А потом обратился ко мне с&nbsp;вопросом о&nbsp;том, каким образом он может побыстрее получить российское гражданство\",&nbsp;&mdash; рассказал Ерошкин.</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Юрист добавил, что, несмотря на&nbsp;поддельность справки, брак у&nbsp;мигранта был признан и&nbsp;принят на&nbsp;территории России.</p>\n<p style=\"text-align: justify;\">\"А оспорить его можно только в&nbsp;судебном порядке, если первая супруга на&nbsp;родине подаст соответствующий иск, причем в&nbsp;судебные органы Российской Федерации\",&nbsp;&mdash; уточнил Ерошкин.</p>\n<p style=\"text-align: justify;\">В этом случае возникнет очень интересная юридическая коллизия, добавил юрист. Российский брак мужчины будет признан \"ничтожным\". Этот термин означает, что брака на&nbsp;территории России у&nbsp;мигранта как&nbsp;будто и&nbsp;не было.</p>\n<p class=\"marker-quote1\" style=\"text-align: justify;\">\"То есть, никаких правовых последствий этот брак не&nbsp;порождает. Все, условно говоря, возвращается к&nbsp;исходному моменту, как&nbsp;будто этого брака и&nbsp;не было. Вот тут и&nbsp;возникает коллизия. Она заключается в&nbsp;том, что человека, если он к&nbsp;этому моменту уже получил российское гражданство, будет очень трудно его лишить\",&nbsp;&mdash; объяснил Ерошкин.</p>\n<p style=\"text-align: justify;\">Ситуация может усугубиться и&nbsp;тем, что человек к&nbsp;моменту, когда его фиктивный брак признают \"ничтожным\", может уже и&nbsp;отслужить в&nbsp;российской армии, и&nbsp;поработать в&nbsp;каких-то государственных органах. Поэтому рассматривать все эти моменты приходится опять же только в&nbsp;судебном порядке.</p>\n<p style=\"text-align: justify;\">Преподаватель РАНХиГС Сергей Андрейцо добавил, что если суд все-таки докажет факт фиктивности брака, то мигранта, по&nbsp;идее, должны не&nbsp;только лишить гражданства, если он получил его посредством такого брака, но&nbsp;и выдворить с&nbsp;территории России.</p>\n<div class=\"b-inject m-inject-min\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong style=\"text-align: right;\">Источник:&nbsp;</strong><a href=\"http://m.ru.sputnik-tj.com/analytics/20171121/1023951869/migranty-fiktivnye-braki-rossiya.html?mobile_return=no\">Sputnik Таджикистан</a></span></div>', NULL, NULL, NULL, NULL, NULL, 1),
(203, 0, 1511766720, NULL, NULL, '<p style=\"text-align: justify;\"><img src=\"http://fondorientir.ru/filemanager/i/33349951/n_1.jpg\" alt=\"\" width=\"325\" height=\"244\" />&nbsp;</p>\n<p style=\"text-align: justify;\">&nbsp; Постановлением Правительства от 20 ноября 2017 года №1400 вводится новая разновидность обыкновенной визы &ndash; виза на въезд в Россию в целях&nbsp;получения разрешения на временное проживание в России. Эта виза будет&nbsp;выдаваться иностранному гражданину на срок до четырёх месяцев дипломатическим представительством или консульским учреждением Российской Федерации при наличии решения территориального органа МВД России о выдаче этому иностранному гражданину разрешения на временное проживание в России.</p>\n<p style=\"text-align: justify;\">Документ внесен МИД России, сообщается на сайте Кабмина.<br />&nbsp;даваться иностранному гражданину на срок дФедеральным законом от 7 июня 2017 года №111-ФЗ внесены изменения в Федеральный закон &laquo;О порядке выезда из Российской Федерации и въезда в Российскую Федерацию&raquo;, которыми установлено, что иностранному гражданину или лицу без гражданства (далее &ndash; иностранный гражданин) при наличии решения территориального органа МВД России о выдаче разрешения на временное проживание в России выдаётся обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России. Виза временно проживающего лица выдаётся иностранному гражданину территориальным органом МВД России при выдаче ему разрешения на временное проживание в России на срок действия такого разрешения.</p>\n<p style=\"text-align: justify;\">&nbsp; В&nbsp;связи с этим подписанным постановлением внесены изменения в Положение об установлении формы визы, порядка и условий её оформления и выдачи, продления срока её действия, восстановления её в случае утраты, а также порядка аннулирования визы (утверждено постановлением Правительства от 9 июня 2003 года №335). Категория &laquo;обыкновенная виза&raquo; дополнена новой разновидностью &ndash; обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России.</p>\n<p style=\"text-align: justify;\">&nbsp; Обыкновенная виза на въезд в Россию в целях получения разрешения на временное проживание в России будет выо четырёх месяцев дипломатическим представительством или консульским учреждением Российской Федерации при наличии решения территориального органа МВД России о выдаче этому иностранному гражданину разрешения на временное проживание в России. Такая виза на въезд может быть только однократной.</p>\n<p style=\"text-align: justify;\">&nbsp;Кроме того, соответствующие изменения внесены в положение, касающееся визы временно проживающего лица. Установлено, что такая виза может быть только многократной.</p>\n<p style=\"text-align: justify;\">Источник:&nbsp;<a href=\"http://www.allmedia.ru\">ИА \"Альянс Медиа\"</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(204, 0, 1516264027, NULL, NULL, '<div>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong>03 сентября</strong> в Кингисеппском&nbsp;районе, в городе Ивангород, состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области. Также во время выездной работы был проведён первый семинар по миграционному законодательству и трудовому праву. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.</span></p>\n</div>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина;', NULL, NULL, NULL, NULL, NULL, 1),
(205, 0, 1516264130, NULL, NULL, '<div style=\"text-align: justify;\">\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">5 сентября в Сосновом Бору состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области, специалисты провели 2 семинара. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области и Санкт-Петербурга.</span></p>\n<p><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.', NULL, NULL, NULL, NULL, NULL, 1),
(206, 0, 1511866805, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">С 15 сентября изменятся &nbsp;формы заявлений об оформлении патента, его переоформлении, выдаче дубликата, и внесении изменений в сведения, содержащиеся в нем (<a href=\"http://www.garant.ru/hotlaw/federal/1133639/\">приказ МВД России от 14 августа 2017 г. № 635</a>).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Речь идет о выдаче патентов на работу иностранцам, прибывающим в Россию в безвизовом порядке. Ранее это функция входила в полномочия ФМС России, однако после упразднения ведомства перешла к МВД России.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Приказ ФМС России от 8 декабря 2014 г. № 639 \"<a href=\"http://base.garant.ru/70839638/\">Об утверждении форм заявлений, представляемых в связи с оформлением патента, его переоформлением, выдачей его дубликата или внесением изменений в сведения, содержащиеся в патенте</a>\" будет считаться утратившим силу.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Отметим, что ранее ведомство представило для публичного обсуждения законопроект об установлении административной ответственности&nbsp;<a href=\"http://www.garant.ru/news/1132323/\">за прием на работу иностранцев, при въезде в Россию не указавших в качестве цели визита \"работу\"</a>. В частности, граждан планируется наказывать штрафом в размере от 2 тыс. до 5&nbsp;тыс. руб., должностных лиц &ndash; от 25 тыс. до 50 тыс. руб, юрлиц &ndash; от 250 тыс. до 800 тыс. руб. либо административным приостановлением деятельности на&nbsp;срок от 14 до 90 суток.<br /></span></p>\n<p style=\"text-align: right;\"><strong style=\"font-family: \'times new roman\', times; font-size: medium; text-align: right;\">Источник:&nbsp;<a href=\"http://www.garant.ru\" target=\"_blank\">ГАРАНТ.РУ</a></strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(207, 0, 1516780336, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">10 сентября во Всеволожском районе, в деревне Новое Девяткино, состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области. Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;', NULL, NULL, NULL, NULL, NULL, 1),
(208, 0, 1516780908, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><strong>13 cентября в 17.00</strong>&nbsp;в Доме еврейской культуры ЕСОД (ул. Б. Разночинная, д. 25) прошло информационно-культурное мероприятие для мигрантов &laquo;Под крылом Петербурга&raquo;, организованное Комитетом по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">В ходе мероприятия, специалистом фонда поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" были проведены консультации мигрантам, по вопросам получения социальной, правовой и иных видов помощи.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;<em>адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em>&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Первый заместитель председателя комитета по межнациональным отношениям и реализации миграционной политики Окрушко Вадим Яковлевич в своей речи перед концертом отметил важность подобных мероприятий в целях &laquo;<em>адаптации и интеграции трудовых мигрантов и предотвращения межнациональных и межэтнических конфликтов</em>&raquo;. Это нужно для того, чтобы каждый петербуржец и каждый гость нашего города ощущали себя большой и дружной семьей, добавил Окрушко. Представитель комитета отдельно поблагодарил представителей диаспор и землячеств.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Напомним, что подобные информационно-культурные мероприятия проводятся комитетом по межнациональным отношениям и реализации миграционной политики Санкт-Петербурга третий год.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><span style=\"font-family: times new roman, times; font-size: medium;\">А Фонд \"ОРИЕНТИР\" реализует проект &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></span></p>\n\n<div id=\"cp_widget_ad6f31db-c830-4889-a529-948e849d9d1a\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_ad6f31db-c830-4889-a529-948e849d9d1a\"; cpo[\"_fid\"] = \"AoDAwEOV6qNS\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script>', NULL, NULL, NULL, NULL, NULL, 1),
(209, 0, 1516780528, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><strong>18 сентября</strong>&nbsp;в Киришском районе, в городе Кириши, состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области. Основная часть слушателей состояла из мигрантов, работающих на заводе Русджам.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></p>\n<div id=\"cp_widget_c93cb633-2611-4951-a148-d6870d37cb4c\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_c93cb633-2611-4951-a148-d6870d37cb4c\"; cpo[\"_fid\"] = \"AwIAOJeqTko3\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;amp;lt;span&amp;amp;gt;New Gallery 2017/11/28&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(210, 0, 1516780941, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><strong>02 октября</strong>&nbsp;в Тосненском районе, в поселке Тельмана, на территории ЗАО \"Племхоз\" им. Тельмана состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></p>\n<div id=\"cp_widget_91da6ee6-9228-4ee0-afd3-41f3000da5de\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_91da6ee6-9228-4ee0-afd3-41f3000da5de\"; cpo[\"_fid\"] = \"AsDA8JOJUcEA\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;New Gallery 2017/11/28&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;originaldate&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;width&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;height&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;originaldate&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;width&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;height&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;originaldate&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;width&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;height&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1024&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(211, 0, 1516780967, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><strong>06 октября</strong>&nbsp;в Выборгском районе состоялась выездная приемная выездная работа приемной Фонда &laquo;ОРИЕНТИР&raquo; по правовому консультированию мигрантов-соотечественников, проживающих и работающих на территории Ленинградской области.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Мигранты получили юридическую помощь исключительно на безвозмездной основе. Консультации предоставлялись в устной и при необходимости в письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы выездной приемной Фонда&ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Ленинградской области.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Данная работа ведётся в рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></p>\n<div id=\"cp_widget_37b5583e-5967-4931-8cfb-f725f349e1fd\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_37b5583e-5967-4931-8cfb-f725f349e1fd\"; cpo[\"_fid\"] = \"AAAAdKenUQXC\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;lt;span&amp;gt;New Gallery 2017/11/28&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;originaldate&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1/1/0001 6:00:00 AM&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;width&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;height&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;originaldate&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1/1/0001 6:00:00 AM&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;width&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;height&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;originaldate&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1/1/0001 6:00:00 AM&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;width&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;height&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1024&amp;lt;/span&amp;gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(212, 0, 1516781016, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\"><strong>12 октября</strong> в &laquo;Городском информационно-расчетном центре&raquo; (пр. Шаумяна, 20). состоялось информационно-культурное мероприятие для иностранных граждан &laquo;Под крылом Петербурга&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Специалист фонда фонда поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" провел консультации для иностранных граждан по вопросам миграционного законодательства, трудоустройства, образования, социального обеспечения.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Также на данном мероприятии приняли участие представители других ведомств, в том числе специалисты отделов районной администрации и сотрудники правоохранительных органов.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">В 18.00 состоялся концерт для иностранных граждан, где выступили национальные творческие коллективы нашего города.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times new roman, times; font-size: medium;\">Фонд \"ОРИЕНТИР\" реализует проект &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;</span></p>\n<div id=\"cp_widget_db6912ef-6089-4487-ab60-ded1095b12ad\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_db6912ef-6089-4487-ab60-ded1095b12ad\"; cpo[\"_fid\"] = \"A8OAgEOp6qvY\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;amp;lt;span&amp;amp;gt;New Gallery 2017/11/1&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;originaldate&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1/1/0001 6:00:00 AM&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;width&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt;height&amp;amp;lt;/span&amp;amp;gt;&amp;amp;lt;span&amp;amp;gt; 1024&amp;amp;lt;/span&amp;amp;gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(213, 0, 1511870808, NULL, NULL, '<p style=\"text-align: justify;\"><strong>ДУШАНБЕ, 21 ноября &mdash; Sputnik, Дмитрий Матвеев.</strong>&nbsp;Число иностранцев, которые заключают фиктивные браки в&nbsp;России, по-прежнему велико, заключат эксперты.</p>\n<p style=\"text-align: justify;\">На прошлой неделе прокуратура одного из&nbsp;районов Воронежа через&nbsp;суд&nbsp;<a href=\"http://ru.sputnik-tj.com/migration/20171113/1023877559/zhitelnitsa-voronezha-vyshla-zamuzh-tadzhik-radi-besplatnogo-remonta.html\" rel=\"noopener\" target=\"_blank\">оспорила фиктивный брак</a>&nbsp;россиянки и&nbsp;гражданина Таджикистана. Пара заключила союз, но&nbsp;вместе супруги при&nbsp;этом не&nbsp;проживали. А буквально на&nbsp;следующий день прокуратура Башкортостана потребовала&nbsp;<a href=\"http://ru.sputnik-tj.com/migration/20171114/1023886704/rossii-zakroyut-saytov-fiktivnye-braki-migrantov.html\" rel=\"noopener\" target=\"_blank\">заблокировать десять сайтов</a>, предлагающих мигрантам заключить фиктивный брак.</p>\n<p style=\"text-align: justify;\">Sputnik Таджикистан узнал, является ли фиктивный брак правонарушением и&nbsp;какими последствиями он грозит мигрантам.</p>\n<p><img src=\"http://fondorientir.ru/filemanager/i/91a7d0c2/1022329413.jpg\" alt=\"\" width=\"547\" height=\"296\" /></p>\n<h3 style=\"text-align: justify;\">Что это такое и&nbsp;для чего</h3>\n<p style=\"text-align: justify;\">Цели заключения фиктивного брака вполне банальны. Для мигранта это возможность получить разрешение на&nbsp;временное проживание в&nbsp;России или&nbsp;подать в&nbsp;упрощенном порядке документы на&nbsp;российское гражданство. Для россиянок это способ заработать.</p>\n<p style=\"text-align: justify;\">Количественного пика в&nbsp;России фиктивные браки с&nbsp;мигрантами достигли три-четыре года назад. К нынешнему моменту их число несколько снизилось. Объясняется это и&nbsp;ужесточением миграционного законодательства и&nbsp;снижением числа мигрантов в&nbsp;России.</p>\n<p style=\"text-align: justify;\">По словам депутата муниципального образования \"Литейный округ\" в&nbsp;Петербурге Равшанбека Курбанова, в&nbsp;России сейчас даже существуют целые брачные агентства, предлагающие мигрантам заключить фиктивные союзы.<br />\"Говорят, что стоит такая услуга от&nbsp;50 тысяч рублей и&nbsp;выше\",&nbsp;&mdash; пояснил парламентарий.</p>\n<p style=\"text-align: justify;\">Преподаватель кафедры конституционного и&nbsp;административного права РАНХиГС Сергей Андрейцо уточнил, что на&nbsp;заключение таких браков обычно идут гражданки России в&nbsp;возрасте, которым рисковать уже особо нечем.</p>\n<p style=\"text-align: justify;\">\"Тем более, в&nbsp;большинстве случаев это делается более или&nbsp;менее грамотно. Они подписывают брачный договор, и&nbsp;при расторжении брака мигрант ничего от&nbsp;женщины не&nbsp;получает\",&nbsp;&mdash; добавил Андрейцо.</p>\n<h3 style=\"text-align: justify;\">Что светит за&nbsp;фиктивный брак</h3>\n<p style=\"text-align: justify;\">В России законы по&nbsp;отношению к&nbsp;иностранцам, которые заключили фиктивный брак, не&nbsp;такие жесткие, как&nbsp;в европейских странах. Например, законодательство Германии предусматривает за&nbsp;это уголовное наказание. За фиктивный брак с&nbsp;иностранцем можно получить до&nbsp;трех лет лишения свободы.</p>\n<p style=\"text-align: justify;\">Занимающиеся этим вопросом немецкие социальные работники приходят к&nbsp;людям домой и&nbsp;проверяют, живет ли пара вместе в&nbsp;случае, если появляется подозрение, что иностранец не&nbsp;проживает с&nbsp;супругой-немкой.</p>\n<p style=\"text-align: justify;\">В России уголовной ответственности за&nbsp;это пока нет. Хотя в&nbsp;2015 году, например, депутаты законодательного собрания Калужской области выступали с&nbsp;инициативой ввести уголовную ответственность для&nbsp;мигрантов, которые ради получения легального статуса заключают фиктивные браки с&nbsp;введением новой статьи в&nbsp;Уголовный кодекс России&nbsp;&mdash; \"Незаконное получение разрешения на&nbsp;временное проживание в&nbsp;России, гражданства РФ\".</p>\n<h3 style=\"text-align: justify;\">Почему фиктивных браков так много</h3>\n<p style=\"text-align: justify;\">Сам факт фиктивного брака в&nbsp;России зачастую очень сложно выявить.</p>\n<p style=\"text-align: justify;\">\"Получить судебное решение на&nbsp;право входить в&nbsp;их (супругов&nbsp;&mdash; ред.) дом и&nbsp;проверять, ведут ли они совместное хозяйство, очень тяжело\",&nbsp;&mdash; пояснил Сергей Андрейцо.</p>\n<p style=\"text-align: justify;\">Через суд приходится оспаривать фиктивный брак и&nbsp;после того, как&nbsp;его выявляют. Обычно этим занимается прокуратура.</p>\n<p style=\"text-align: justify;\">В итоге мигранты перестали бояться ответственности за&nbsp;фиктивные браки, надеясь на&nbsp;существующие недоработки в&nbsp;законе. По словам юриста Александра Ерошкина, в&nbsp;его практике был случай, когда выходец из&nbsp;одной из&nbsp;стран Центральной Азии женился в&nbsp;России, будучи уже женатым на&nbsp;родине.</p>\n<p style=\"text-align: justify;\">\"Он предоставил поддельную справку о&nbsp;том, что в&nbsp;браке на&nbsp;родине не&nbsp;состоит, и&nbsp;сумел жениться здесь. А потом обратился ко мне с&nbsp;вопросом о&nbsp;том, каким образом он может побыстрее получить российское гражданство\",&nbsp;&mdash; рассказал Ерошкин.</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">Юрист добавил, что, несмотря на&nbsp;поддельность справки, брак у&nbsp;мигранта был признан и&nbsp;принят на&nbsp;территории России.</p>\n<p style=\"text-align: justify;\">\"А оспорить его можно только в&nbsp;судебном порядке, если первая супруга на&nbsp;родине подаст соответствующий иск, причем в&nbsp;судебные органы Российской Федерации\",&nbsp;&mdash; уточнил Ерошкин.</p>\n<p style=\"text-align: justify;\">В этом случае возникнет очень интересная юридическая коллизия, добавил юрист. Российский брак мужчины будет признан \"ничтожным\". Этот термин означает, что брака на&nbsp;территории России у&nbsp;мигранта как&nbsp;будто и&nbsp;не было.</p>\n<p class=\"marker-quote1\" style=\"text-align: justify;\">\"То есть, никаких правовых последствий этот брак не&nbsp;порождает. Все, условно говоря, возвращается к&nbsp;исходному моменту, как&nbsp;будто этого брака и&nbsp;не было. Вот тут и&nbsp;возникает коллизия. Она заключается в&nbsp;том, что человека, если он к&nbsp;этому моменту уже получил российское гражданство, будет очень трудно его лишить\",&nbsp;&mdash; объяснил Ерошкин.</p>\n<p style=\"text-align: justify;\">Ситуация может усугубиться и&nbsp;тем, что человек к&nbsp;моменту, когда его фиктивный брак признают \"ничтожным\", может уже и&nbsp;отслужить в&nbsp;российской армии, и&nbsp;поработать в&nbsp;каких-то государственных органах. Поэтому рассматривать все эти моменты приходится опять же только в&nbsp;судебном порядке.</p>\n<p style=\"text-align: justify;\">Преподаватель РАНХиГС Сергей Андрейцо добавил, что если суд все-таки докажет факт фиктивности брака, то мигранта, по&nbsp;идее, должны не&nbsp;только лишить гражданства, если он получил его посредством такого брака, но&nbsp;и выдворить с&nbsp;территории России.</p>\n<div class=\"b-inject m-inject-min\" style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times; font-size: medium;\"><strong style=\"text-align: right;\">Источник:&nbsp;</strong><a href=\"http://m.ru.sputnik-tj.com/analytics/20171121/1023951869/migranty-fiktivnye-braki-rossiya.html?mobile_return=no\">Sputnik Таджикистан</a></span></div>', NULL, NULL, NULL, NULL, NULL, 1),
(214, 0, 1516264689, NULL, NULL, '<p style=\"text-align: justify;\"><img title=\"Эксперт Ерошкин -1.jpg\" src=\"http://fondorientir.ru/filemanager/i/72b734de/Эксперт Ерошкин -1.jpg\" alt=\"Эксперт Ерошкин -1.jpg\" width=\"267px\" height=\"299px\" /></p>\n<p style=\"text-align: justify;\">Боевые действия в Донбассе продолжаются уже три года, и поэтому жители этого региона постоянно уезжают к родственникам и знакомым в Россию, проживая там без регистрации до 90 дней. Статус беженца или временное убежище за время конфликта получили 280 тысяч жителей Донбасса.</p>\n<p style=\"text-align: justify;\">Однако в России украинские беженцы зачастую сталкиваются с серьезными бытовыми проблемами. Одной из них оказалось то, что работодатели отказываются брать их на работу, несмотря на статус беженца. В то же время граждан Таджикистана и Узбекистана они берут на работу гораздо охотнее.</p>\n<p style=\"text-align: justify;\">Юрист Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Александр Ерошкин рассказал, что украинцы постоянно обращаются к ним с такой проблемой. В то же время от общего числа обращающихся в правовую приемную Фонда в Санкт-Петербурге их немного &mdash; 5-10%. Юрист уточнил, что по этому вопросу беженцы с Украины приходят в правовую приемную как минимум раз в неделю, поскольку правовая приёмная это то официальное и легальное место, где мигранты-соотечественники бесплатно получают квалифицированную юридическую помощь в сфере миграционного и трудового законодательства, включая представления их интересов в судах на безвозмездной основе.</p>\n<p style=\"text-align: justify;\">Проблема заключается в том, что работодатели просто опасаются брать на работу беженца из Украины. По законодательству украинцу, как и мигрантам из Таджикистана или Узбекистана, для трудоустройства нужен трудовой патент. Однако он не требуется в том случае, если человек уже получил статус беженца.</p>\n<p style=\"text-align: justify;\">&laquo;Работодатели боятся штрафов и поэтому просто не хотят разбираться в законодательстве и связываться с беженцами. Штрафы у нас очень высокие &mdash; от 400 тысяч до одного миллиона рублей за одного человека за один проступок. Если у мигранта есть патент, то сотрудник отдела кадров принимает его по общей схеме. Он смотрит по своей таблице и видит, что мигрантам с Украины нужен патент. В вопрос особого статуса, который имеют беженцы и вынужденные переселенцы, он не вдается&raquo;, &mdash; пояснил Ерошкин.</p>\n<p style=\"text-align: justify;\">Он добавил, что работодатель действует по схеме &laquo;лучше я откажу, и мне будет безопаснее&raquo;.</p>\n<p style=\"text-align: justify;\">По словам Ерошкина, единственный способ решить эту проблему &mdash; это информирование работодателей. Юристы Фонда &laquo;ОРИЕНТИР&raquo; регулярно проводят семинары с работодателями, какие-то встречи с мигрантами на территории работодателя. В том случае, если украинский беженец обращается в приемную Фонд, то юристы звонят работодателю. Однако здесь есть и негативный момент. &laquo;Если у человека отношения с работодателем начинаются с такого конфликта, то ему потом трудно будет устроиться в эту компанию. Его просто не возьмут под каким-то другим предлогом &mdash; допустим, скажут, что у него недостаточно квалификации&raquo;, &mdash; привел пример юрист.</p>\n<p style=\"text-align: justify;\">Ерошкин уточнил, что обращающийся в Фонд беженец и сам понимает это, поэтому даже чаще боится рассказывать о своей проблеме. &laquo;Мигранты всего вообще в России боятся&raquo;, &mdash; добавил он.</p>\n<p style=\"text-align: justify;\">Что касается вопроса о том, беженцы из каких частей Украины преимущественно обращаются в приемную Фонда &laquo;ОРИЕНТИР&raquo;, то подавляющее большинство их представляет Донбасс. &laquo;Больше 90% переселенцев &mdash; именно оттуда. Почти все с кем я лично общался &mdash; выходцы из Донецкой и Луганской областей&raquo;, &mdash; рассказал Ерошкин.</p>\n<p style=\"text-align: justify;\">Юрист предположил, что мигрантам из других украинских областей получить статус беженца в России очень трудно. &laquo;Если человек просто приехал и работает, то тогда он действительно может быть выходцем и из другой украинской области. А что касается беженцев и вынужденных переселенцев &mdash; то это только Донбасс, потому что они являются жертвами войны&raquo;, &mdash; говорит Ерошкин.</p>\n<p style=\"text-align: justify;\">А вот с выходцами из Крыма ситуация совсем простая, поскольку они давно граждане России. По словам Ерошкина, на них уже даже косо не смотрят. &laquo;Мне трудно себе представить ситуацию, при которой человеку с российским паспортом отказали бы в трудоустройстве в России&raquo;, &mdash; ответил юрист на вопрос о том, нет ли у работодателей какого-то предвзятого отношения к жителям Крыма. Впрочем, юрист сделал оговорку, что в иностранных компаниях человеку из Крыма могут отказать в работе.</p>\n<p style=\"text-align: justify;\">По словам Ерошкина, положительный пример трудоустройства украинского беженца после обращения к юристам в его практике был совсем недавно. В Фонд &laquo;ОРИЕНТИР&raquo; обратился молодой человек, которого отказывались брать на работу. Однако у него была очень востребованная специальность &mdash; украинец оказался специалистом по компьютерной технике и мог работать системным администратором. Юристам Фонда &laquo;ОРИЕНТИР&raquo; без труда удалось устроить его в одну из партнерских организаций Ленинградской области. &laquo;Сразу после того, как он обратился к нам, его и взяли на работу&raquo;, &mdash; подытожил Ерошкин.</p>\n<p style=\"text-align: justify;\">Создание правовой приемной в центре Санкт-Петербурга осуществлено в рамках реализации социально значимого проекта &laquo;\"Цивилизованная миграция\". Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo;, где мигранты бесплатно получают не только квалифицированную юридическую помощь в сфере миграционного и трудового законодательства, но и профессиональное представления их интересов в судах на безвозмездной основе. Проект реализуется с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(215, 0, 1516265237, NULL, NULL, '<p style=\"text-align: justify;\"><img title=\"TM_1.jpg\" src=\"http://fondorientir.ru/filemanager/i/52c31c72/TM_1.jpg\" alt=\"TM_1.jpg\" width=\"300px\" height=\"200px\" /></p>\n<p style=\"text-align: justify;\">В рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; финансируемого &laquo;Фондом-оператором президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;, в сентябре &ndash;декабре 2017 года в районах Ленинградской области и Санкт-Петербурга прошли семинары для трудовых мигрантов и выездные приёмные Фонда &laquo;ОРИЕНТИР&raquo;</p>\n<p style=\"text-align: justify;\">В доме №39 по проспекту Римского-Корсакова работу ведёт правовая приемная Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, где квалифицированные юристы оказывают бесплатную правовую поддержку мигрантам по всем правовым вопросам.</p>\n<p style=\"text-align: justify;\">По словам юристов фонда Сергея Попова, Анатолия Тимонина и Александра Ерошкина, в основном мигранты обращаются в приемную по вопросам правильного оформления документации, чтобы избежать нарушения закона о пребывании иностранных граждан на территории Российской Федерации, а также о порядке получения РВП (разрешение на временное проживание) и гражданства РФ. Начиная с 1 сентября по 30 декабря 2017 года юристы оказали бесплатные консультации близко двум тысячам мигрантов, почти половина из них по вопросу, с которым всё чаше за последнее время сталкиваются мигранты &ndash; это невыплата заработной платы работодателем. За четыре месяца юристы фонда помогли вернуть мигрантам задолженность по заработной плате на сумму свыше 1,3 млн. рублей, а также на сумму превышающую 4 млн. рублей не смогли вернуть.</p>\n<p style=\"text-align: justify;\"><strong>&ndash;</strong>&nbsp;Интересно! Юристи не смогли вернуть мигрантам задолженность по заработной плате?</p>\n<p style=\"text-align: justify;\"><strong>Юристи фонда &mdash;</strong>&nbsp;Да, не смогли. И это связанно с самым элементарным &ndash; отсутствием трудовых или гражданско-правовых договоров между работодателем и работником, это в первую очередь, а во вторых &mdash; для того чтобы факт трудовых отношений доказать без того подтверждающих документов нужны свидетели, показания свидетелей и их согласие выступить с показаниями в суде. Вот здесь самое простое и сложное для мигрантов одновременно &ndash; кто-то просто боится публично давать показания или не верит в судебную волокиту с позитивным итогом для себя, кому-то уже сроки по документам поджимают вернутся домой на родину, у кого-то просто деньги, нервы и доверие к людям закончились и т.д.</p>\n<p style=\"text-align: justify;\"><strong>&ndash;</strong>&nbsp;Так это безнаказанность в отношении работодателя получается, если только от конкретных обращений по задолженности заработной платы возможно решить всеволишь 20-25% примерно, а сколько ещё таких кто не знает куда или кому обращаться?</p>\n<p style=\"text-align: justify;\"><strong>Юристи фонда &ndash;</strong>&nbsp;Вы совершенно правы, мы (мы &ndash; я понимаю нас всех сотрудников Фонда &laquo;ОРИЕНТИР&raquo;) осознаём, что всё то что мы помогли мигрантам по выплатам з/п, реально это капля в море. Хотя работа для нас тоже очень не простая, нам как опытным юристам при каждом обращении мигрантов уже всё юридической точки зрения понятно с полслова или сразу, но надо чтобы он&nbsp;или они поняли &ndash; для этого мы включаем педагога в себе, хочется им и по человечески помочь и для этого нужно для начала их успокоить и в доверительные отношения войти &ndash; то есть нужно ещё выполнять работу психолога.</p>\n<p style=\"text-align: justify;\">&mdash; Есть перспективы в улучшении общей ситуации по выплатам з/п мигрантам, вы её видите?</p>\n<p style=\"text-align: justify;\"><strong>Юристи фонда &ndash;</strong>Если всё больше и больше, особенно недобросовестных работодателей будут знать, что наказание за нарушения Трудового законодательства в отношении мигрантов нигде не истлеет, а на оборот &ndash; обойдётся в разы дороже по закону, чем не выплатить з/п гражданину РФ.</p>\n<p style=\"text-align: justify;\">Поскольку работа правовой приемной Фонда &laquo;ОРИЕНТИР&raquo; поддерживается грантами Президента РФ, правда с небольшими перерывами, но тем не менее уже третий год под ряд, у нас не только база данных и статистика образуется по данным вопросам, но и естественным путём выявляются &laquo;злостные&raquo; компании неплательщики з/п мигрантам, которые как-бы на этом свой бизнес и выстраивают. Вот на данный момент есть одна группа деятелей, работающих в строительном бизнесе Санкт-Петербурга, Москвы и других субъектов РФ. Со слов потерпевших мигрантов, по невыплатам сроком три-шесть месяцев, кода время подходит попрощаться с трудовыми ресурсами, им особого труда это не составляет, ведь они что называется коротко, жестко и на понятном для мигранта языке, поскольку сами из СКФО и Кавказа и Закавказье.</p>\n<p style=\"text-align: justify;\">На данный момент уже собран большой объем доказательной базы.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(216, 0, 1516265231, NULL, NULL, '<p style=\"text-align: justify;\"><span><img title=\"тм_2.jpg\" src=\"http://fondorientir.ru/filemanager/i/8a1e7778/тм_2.jpg\" alt=\"тм_2.jpg\" width=\"300px\" height=\"199px\" /></span></p>\n<p style=\"text-align: justify;\"><span>В рамках проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов, работу ведёт правовая приемная Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, где квалифицированные юристы оказывают бесплатную правовую поддержку мигрантам, включая восстановление документов и получение долгов по зарплате. Также с сентября 2017 года проведено 5 семинаров в пяти районах Ленинградской области и девять выездных приемных, из них две в Санкт-Петербурге на информационно-культурных мероприятиях для мигрантов &laquo;Под крылом Петербурга&raquo;.</span><br /><span>По словам юристов фонда - бывают и случаи, когда мигрант теряет все свои документы. Однажды в приемную обратилась гражданка Узбекистана по имени Гулджахон, у которой украли сумку в магазине. В сумке находились все ее документы. Юристы фонда &laquo;ОРИЕНТИР&raquo; посоветовали ей немедленно заявить в полицию об этом инциденте, чтобы они выдали ей справку. После получения справки женщину направили в консульство республики Узбекистан для восстановления паспорта и всех остальных необходимых документов.</span><br /><span>Кроме того, бывают случаи, когда мигрантам выносят запрет на въезд в Россию, что лишает их возможности трудиться и обеспечивать свои семьи. Юристы фонда несколько раз помогали мигрантам отменить запрет в административном и судебном порядке.</span><br /><span>Если всё больше и больше, особенно недобросовестных работодателей будут знать, что наказание за нарушения Трудового законодательства в отношении мигрантов нигде не истлеет, а на оборот &ndash; обойдётся в разы дороже по закону, чем не выплатить з/п гражданину РФ.</span><br /><span>Периодические проявляются всплески работорговли трудовыми мигрантами, чаше всего в самой среде мигрантов.</span><br /><span>Поскольку работа правовой приемной Фонда &laquo;ОРИЕНТИР&raquo; поддерживается грантами Президента РФ, правда с небольшими перерывами, но тем не менее уже третий год подряд, у нас не только база данных и статистика образуется по данным вопросам, но и естественным путём выявляются &laquo;злостные&raquo; компании неплательщики з/п мигрантам, которые как-бы на этом свой бизнес и выстраивают. Вот на данный момент есть одна группа деятелей работающих в строительном бизнесе Санкт-Петербурга, Москвы и др.. Это группа людей которые с нулевых уже в этом бизнесе, у каждого по одной &laquo;основной&raquo; компанией, где их сотрудники (управленческое звено) тоже имеют по одной компании, где руководителями они же сами являются. Работают с партнёрами в РФ и поставщиками в РФ на доверии в стиле &laquo;мы прозрачные, вот мой паспорт с пропиской, а потом попросить в рассрочку 500 тыс., если нет - то 200 тыс., если ни как &ndash; то хоть на 50 тыс., но в рассрочку&raquo; и потом (через пару или тройку месяцев) в стиле &laquo;мы не виноваты, что заказчик нам не платит, ну и денег у нас тоже соответственно нет&raquo;. А с мигрантами им проще, чем другим российским &laquo;кидалам&raquo;, ведь они сами из СКФО и Кавказа и Закавказье.</span><br /><span>На данный момент уже собран большой объем доказательной базы для её передачи в следственные органы.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(217, 0, 1516715928, NULL, NULL, '<p><span style=\"color: #00a650; font-family: Arial;\"><strong><img title=\"21-768x512.jpg\" src=\"http://fondorientir.ru/filemanager/i/9ed6430a/21-768x512.jpg\" alt=\"21-768x512.jpg\" width=\"305\" height=\"202\" /></strong></span></p>\n<p><span style=\"font-family: Arial;\">Новость о том, что с января 2018 года авансовый платеж по патенту равен 3500 рублей, взбудоражила всех иностранцев и их работодателей! Но еще больше их волнует другой вопрос: нужно ли доплачивать 500 рублей, если налог за патент был внесен еще в 2017 году в размере 3000 рублей!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">К сожалению, в постановлении Правительства Санкт-Петербурга от 09.11.2017 N 934 или в законе от 30.11. 2017 N 77-оз по Ленинградской области о принятии нового коэффициента, которые как раз и изменили стоимость авансового платежа за 2018 год в СПб и ЛО соответственно, не прописано никаких рекомендаций иностранцам, которые внесли оплату за патент еще в 2017 году. Также ГУ МВД по вопросам миграции не выпустило никаких приказов или письменных разъяснений о том, как поступать в данном случае.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но мы настоятельно рекомендуем Вам доплатить недостающую сумму до той, которая уже официально принята на 2018 год.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Объясним, почему.</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Во-первых</strong>, новая сумма платежа установлена с 01.01.2018, а значит, все авансовые платежи за 2018 год должны составлять 3500 руб, независимо от того, когда они были оплачены.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Во-вторых</strong>, по практике 2015 года, когда цена налога за патент тоже повышалась, все иностранные граждане, которые не доплатили эту разницу, впоследствии не смогли продлить себе документы (их не принимали), патенты были аннулированы, иностранцы получили штраф и были депортированы из страны.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>В-третьих</strong>, без правильно оплаченных авансовых платежей за 2018 год иностранцам не продлят миграционный учет (временную регистрацию).</span></p>\n<p>Обратите внимание, у тех, кто вносил в 2017 году платежи по 3000 рублей за месяцы 2018 года, имеют действующие регистрации. Но в данном случае это не значит, что патент продлен. Если сумма налога за патент не будет внесена полностью, то считается, что налог по патенту не оплачен вовсе, а патент иностранца автоматически перестает действовать.</p>\n<p>Кроме того впереди два важнейший в России мероприятия &ndash; Президентские выборы и Чемпионат мира по футболу, в связи с чем лучше не рисковать и не пытаться сэкономить, доверившись слухам или устным заверениям!</p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-family: Arial;\">Источник: </span><a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(218, 0, 1518615692, NULL, NULL, '<div><span style=\"font-family: times\\ new\\ roman, times; font-size: small;\"><img title=\"regnum_picture_15185143591878495_big.jpg\" src=\"http://fondorientir.ru/filemanager/i/e4a01d41/regnum_picture_15185143591878495_big.jpg\" alt=\"regnum_picture_15185143591878495_big.jpg\" width=\"405\" height=\"294\" /></span></div>\n<p><span style=\"font-family: times\\ new\\ roman, times; font-size: small;\">&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Правительство РФ внесло в Госдуму законопроект, которым приглашающая сторона обязывается следить за своевременным выездом иностранных граждан из России, а также за соблюдением ими целей визита, передает корреспондент&nbsp;<strong style=\"font-size: 16px;\">ИА&nbsp;REGNUM&nbsp;</strong>13 февраля.</span></p>\n<div class=\"news_body\" data-id=\"2379695\">\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Вводится ответственность юридических лиц, должностных лиц и физлиц, приглашающих иностранных граждан, за нарушение ими сроков выезда с территории РФ и за несоблюдение заявленной цели посещения страны.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Штраф за нарушение на граждан составит от 2 тыс. до 4 тыс. рублей, на должностных лиц &mdash; от 40 тыс. до 50 тыс. рублей; на юридических лиц &mdash; от 400 тыс. до 500 тыс. рублей.</span></p>\n<div class=\"news_body_ad ads_click partner_block\" style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">В документе отмечается, что действующим законодательством не установлена обязанность приглашающей стороны осуществлять контроль за соблюдением иностранными гражданами и лицами без гражданства заявленной цели въезда в РФ. Вместе с тем, по данным МВД, в 2017 году территориальными органами МВД России было пресечено более 136 тыс. подобных случаев.</span></div>\n<div id=\"detail_media_banner\" style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">В Госдуме поддерживают инициативу правительства и намерены учесть при подготовке законопроекта ко второму чтению, депутаты считают необходимым детально его проработать на предмет возможного расширения сферы его действия на иностранцев с безвизовым въездом в РФ, так как именно на таких граждан приходится большая часть нарушений по срокам выезда и несоблюдению цели приезда.', NULL, NULL, NULL, NULL, NULL, 1),
(219, 0, 1518703536, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">10 февраля 2018 года президент Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; Седлиньш Эдгарс принял участие в мероприятии &laquo;Встреча таджикской творческой интеллигенции&raquo;, организаторами которой выступали Генеральное Консульство Республики Таджикистан в Санкт-Петербурге и Санкт-Петербургская общественная организация Таджикистанцев &laquo;Аджам&raquo;. На мероприятии присутствовали Генеральный Консул Таджикистана Назризода Давлатали Хайдар, депутат муниципального образования Ульянка Бердов Бахтибек Худобердиевич, председатель общественной организации Таджикистанцев &laquo;Аджам&raquo; Усманов Мурод Абдуллоевич, а также председатель РОО &laquo;Национальное-культурное объединение друзей азербайджанцев &laquo;НАХЧЫВАН&raquo; Гахраманов Идрис Исрафилович.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">В ходе мероприятия президент Фонда &laquo;ОРИЕНТИР&raquo; проинформировал присутствующих на мероприятии о деятельности Фонда &laquo;ОРИЕНТИР&raquo;, а также были обсуждены ключевые моменты о сотрудничестве с Генеральным Консульством Таджикистана в Санкт-Петербурге по вопросам содействия и оказания безвозмездной правовой помощи мигрантам из Таджикистана в рамках реализации проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; при поддержке &laquo;Фонда-оператора президентских грантов по развитию гражданского общества&raquo; на основании распоряжения Президента Российской Федерации от 3 апреля 2017 г. № 93-рп &laquo;Об обеспечении в 2017 году государственной поддержки некоммерческих неправительственных организаций, участвующих в развитии институтов гражданского общества и реализующих социально значимые проекты и проекты в сфере защиты прав и свобод человека и гражданина&raquo;.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Представитель Генерального Консульства сообщил, что основными проблемами с которыми сталкиваются мигранты, которые ведут трудовую деятельность на территории Санкт-Петербурга и Ленинградской области, являются невыплата заработной платы, сложный процесс оформления документации, разрешающей ведение трудовой деятельности на территории Санкт-Петербурга и Ленинградской области. Президент Фонда &laquo;ОРИЕНТИР&raquo; уведомил о готовности в сотрудничестве по всем вышеизложенным вопросам.</span></p>\n\n<div id=\"cp_widget_d6922c9b-0198-4ef6-a977-517d563946cf\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_d6922c9b-0198-4ef6-a977-517d563946cf\"; cpo[\"_fid\"] = \"AsAAuKOdYj9m\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(220, 0, 1520325710, NULL, NULL, '<p><img title=\"33-1.jpg\" src=\"http://fondorientir.ru/filemanager/i/6f390bd5/33-1.jpg\" alt=\"33-1.jpg\" width=\"416\" height=\"250\" /></p>\n<p>Как стать студентом вуза в России не выезжая из Таджикистана? В этом году Россия предоставила Таджикистану свыше 600 квот примерно в 90% российских вузов. Как утверждают в Россотрудничестве, стать претендентом на российскую государственную квоту может любой желающий, окончивший школу. Расскажем о том, как и где будет проходить отбор на бюджетное обучение.<br /> <strong>Регистрация &nbsp;на квоту началась еще с 1 февраля 2018 года и продолжится до 5 марта 2018 года (до 18:00 по душанбинскому времени). </strong></p>\n<p>Ежедневно с понедельника по пятницу в 15:00 в РЦНК в Душанбе&nbsp;(ул. Мирзо Турсунзаде, д. 28) проводятся консультации по вопросам подачи заявления и участия в отборочных мероприятиях.</p>\n<p>Уровень знание русского языка:</p>\n<p><em>Абитуриент должен свободно выражать своё мнение на русском языке. В отборочном туре будет проходить тест по русскому языку. </em></p>\n<p>В случае некоторых незначительных сложностей в знании языка в некоторых вузах есть подготовительные курсы.</p>\n<p>В 2017 году, по данным ГУВМ МВД России, число граждан Таджикистана указавших цель въезда в Россию как &laquo;учеба&raquo; составило 30,2 тыс. человек и их число увеличилось по сравнению с 2016 годом на 5 тыс. человек.</p>\n<p><strong>Итак, что нужно чтобы стать претендентом на квоту? </strong></p>\n<p><strong>Шаг №1:&nbsp;&nbsp;</strong></p>\n<p>Внимательно ознакомиться с информацией о порядке приёма документов и участия в отборе на обучение,&nbsp; на сайте&nbsp;<a href=\"http://tjk.rs.gov.ru/ru/news/23734\">http://tjk.rs.gov.ru/ru/news/23734</a></p>\n<p>Желающие поступить по квоте должны до 5 марта 2018 года зарегистрироваться на&nbsp;портале&nbsp;<a href=\"https://www.russia.study/event/admission-2018-19\">https://www.russia.study/event/admission-2018-19</a> и заполнить анкету (внести свои данные, информацию об образовании и достижениях, указать желаемое направление обучения).</p>\n<p>Кандидатам, не сформировавшим полностью личный кабинет на&nbsp; портале <a href=\"http://www.RUSSIA.STUDY\">www.RUSSIA.STUDY</a>, будет&nbsp;отказано&nbsp;в участии в отборочных испытаниях.</p>\n<p><strong>&nbsp;Шаг №2: </strong></p>\n<p>После получения подтверждения прикрепить все необходимые документ и приложить следующие скан-копии документов:</p>\n<p>Паспорт гражданина Республики Таджикистан с пропиской (если нет паспорта, то свидетельство о рождении и справку с места жительства);</p>\n<p>Фотографии как на паспорт, любительские фото не допускаются;</p>\n<p>Справки с места учебы (табель успеваемости за первое полугодие) или документа, подтверждающего уровень полученного образования (аттестат) для поступающих по программам СПО, бакалавриата, специалитета; отсканированную копию зачетки или академическую справку об успеваемости из университета, либо документ, подтверждающий уровень полученного образования (диплом) &mdash; для поступающих по программам магистратуры, ординатуры, аспирантуры и др.</p>\n<p>Скан-копию медицинской справки формы № 038, выданной не ранее 1&nbsp;января 2018 года.</p>\n<p>Скан-копию медицинской справки ВИЧ/СПИД, выданной не ранее 1 января 2018 года.</p>\n<p>После заполнения личного кабинета на портале&nbsp;http://www.RUSSIA.STUDY в соответствии с вышеназванными требованиями, Вам необходимо нажать кнопку&nbsp;&laquo;отправить на проверку&raquo;.</p>\n<p><strong>Шаг №3:&nbsp;&nbsp;</strong></p>\n<p>Регулярно проверять анонсы и информацию на сайте <a href=\"http://www.tjk.rs.gov.ru\">www.tjk.rs.gov.ru</a> о датах и месте проведения отборочных испытаний.</p>\n<p><strong>Шаг №4: </strong></p>\n<p>Принять участие в отборочных испытаниях согласно графика, который будет опубликован на сайте <a href=\"http://tjk.rs.gov.ru\">tjk.rs.gov.ru</a>;</p>\n<p><strong>После этого кандидаты на квоты будут приглашены на письменное и устное тестирование так называемые отборочное испытание, экзаменационные предметы будут зависеть от выбранной специализации. </strong></p>\n<p>Отборочные испытания будут проводиться комиссией, состоящей из представителей Российского центра науки и культуры в Душанбе и министерства образования и&nbsp; науки Таджикистана, а также сотрудников российских вузов.</p>\n<p><strong>Шаг №5: </strong></p>\n<p>Получить информацию о результатах отборочных испытаний на сайте <a href=\"http://tjk.rs.gov.ru\">tjk.rs.gov.ru</a>;</p>\n<p><em>Когда тест будет сдан, а собеседование пройдено,&nbsp; можно будет выбрать шесть вузов на личное усмотрение (в Москве и Санкт-Петербурге можно указать не более двух). Тот университет, который в списке будет указан первым, первым получит данные и результаты экзамена претендента. </em></p>\n<p>В том случае, если результаты тестирование не удовлетворят данный вуз, то данные передаются в следующий по списку. И так до&nbsp;шестого.</p>\n<p><strong>Шаг №6:</strong></p>\n<p>В случае получения кандидатом права на обучение в вузах России, подать необходимый дополнительный пакет документов, перечень которых будет размещен на сайте <a href=\"http://tjk.rs.gov.ru\">tjk.rs.gov.ru</a>;</p>\n<p><strong>Шаг №7:&nbsp;</strong></p>\n<p>Получить из Минобрнауки России ответ о зачислении на Вашу эл. почту, указанную при регистрации на <a href=\"http://russia.study/registration\">russia.study/registration</a> или на сайте <a href=\"http://tjk.rs.gov.ru\">tjk.rs.gov.ru</a>;</p>\n<p><strong>Где будут проходить экзамены? </strong></p>\n<p><strong>Экзамены, будут проводиться в городах: Душанбе, Худжанде, Кулябе, Курган-Тюбе и Хороге.&nbsp;</strong></p>\n<p>Точная информация о дате и месте проведения отборочных испытаний будет размещена на сайте&nbsp;<a href=\"http://tjk.rs.gov.ru\">tjk.rs.gov.ru</a>&nbsp;и на официальной странице&nbsp;<a href=\"https://www.facebook.com/%D0%A0%D0%A6%D0%9D%D0%9A-%D0%B2-%D0%94%D1%83%D1%88%D0%B0%D0%BD%D0%B1%D0%B5-1505498853042696/\">РЦНК в Душанбе в Facebook</a>.</p>\n<p><strong>Правительство Российской Федерации ежегодно предоставляет 15&nbsp;000 мест для бесплатного обучения иностранных граждан. </strong></p>\n<p>Конкурсный отбор на бесплатное обучение в рамках квоты организуют представительства Россотрудничества в странах мира.</p>\n<p>Если в стране нет представительства Россотрудничества, конкурсный отбор организует посольство (консульство) Российской Федерации.</p>\n<p>Чтобы уточнить сроки и форму проведения отборочных испытаний и задать вопросы&nbsp;о процедуре&nbsp;отбора, обратитесь в&nbsp;представительство <a href=\"http://rs.gov.ru/ru/contacts/inworld\">Россотрудничества</a>&nbsp;или&nbsp;<a href=\"http://www.mid.ru/ru/maps\">дипломатическое представительство Российской Федерации</a>&nbsp;в вашей стране.</p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://migrant.ru\" target=\"blank_\">МИГРАНТ.РУ</a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(221, 0, 1520326317, NULL, NULL, '<p style=\"text-align: justify;\"><img title=\"migrant.news.tj-2018-03-02_11-33-45_676319.jpg\" src=\"http://fondorientir.ru/filemanager/i/d5c38575/migrant.news.tj-2018-03-02_11-33-45_676319.jpg\" alt=\"migrant.news.tj-2018-03-02_11-33-45_676319.jpg\" width=\"474\" height=\"238\" /></p>\n<p style=\"text-align: justify;\">Незаконной деятельностью за денежное вознаграждение занимались восемь женщин (одна из них была ранее судима) и мужчина в течение 2016 года. Сообщается, что преступники вносили в бланки уведомлений о прибытии ложные сведения о месте пребывания иностранцев. Сообщники организовали себе офис в Красном Селе, в доме по улице Первого Мая, и учредили несколько фиктивных фирм. Трудовые договоры иностранцев они якобы заключали с ООО &laquo;Союз+&raquo; и ООО &laquo;Венера&raquo;, предоставляя затем в УВМ ГУ МВД России документы для постановки на миграционный учет. За год фиктивную регистрацию прошли порядка 2042 человек. В конце января 2018 года полиция возбудила по данному факту уголовное дело по ст. 322.1 ч. 2 п.&laquo;а&raquo; УК РФ (Организация незаконной миграции). При осмотре офиса изъяла с места незаконной деятельности банды оргтехнику, порядка 350 000 рублей и 200 долларов США, более 20 миграционных карт, паспорта и учредительные документы на шесть &laquo;фирм-однодневок&raquo;. Всех участников банды задержали на основании ст.91 УПК РФ. На данный момент полиция устанавливает дополнительные факты преступной деятельности и ищет соучастников. 16:53 19 Февраль На что имеет право мигрант в России? Как отмечается в портале Migranto.ru, в данной информации сообщается о том, что фиктивную регистрацию прошли порядка 2042 человек. Но, не сообщается, какова теперь их дальнейшая судьба. Если исходить из практики ФМС России, то при выявлении мошенников, которые оформляли липовые регистрации мигрантам, абсолютно все регистрации с использованием подставных фирм автоматически аннулируются. Скорее всего так будет и в этот раз. Поэтому важная рекомендация тем, кто попал в подобную ситуацию.</p>\n<p style=\"text-align: justify;\">Если вы оформляли себе &laquo;регистрации&raquo; через посредников, нужно срочно проверить, какой работодатель &laquo;поставил&raquo; вас к себе на учет? В данном случае, если в регистрации указаны фирмы ООО &laquo;Союз +&raquo; или ООО &laquo;Венера&raquo;, то вам стоит срочно сделать&nbsp;пересечение границы.</p>\n<p><strong><em>Иначе вы станете нарушителем миграционного законодательства, так как в базе ФМС будет отметка, что вы не встали на миграционный учет и нарушили сроки пребывания</em></strong></p>\n<p style=\"text-align: justify;\">Отдельно обращаем внимание работодателей: также проверьте регистрации ваших сотрудников. Если выявите регистрации фирм-мошенников, то также отправляйте работников делать пересечение границы. Иначе получите в штате нелегала и при этом у вас все признаки принимающей стороны. При проверке вам могут выписать штраф от 400 до 500тыс.руб.</p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"https://migrant.news.tj/post/2878\" target=\"blank_\">МИГРАНТ.news.tj</a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(222, 0, 1520326496, NULL, NULL, '<p><img title=\"migrant.news.tj-2018-03-06_04-49-30_782988.jpg\" src=\"http://fondorientir.ru/filemanager/i/1043be87/migrant.news.tj-2018-03-06_04-49-30_782988.jpg\" alt=\"migrant.news.tj-2018-03-06_04-49-30_782988.jpg\" width=\"473\" height=\"313\" /></p>\n<p>Преподавателями этих курсов будут волонтеры, которые живут и работают в российской столице и не равнодушны к проблеме знания языка со стороны мигрантов, которые часто сталкиваются со сложностями общения приезжая в Россию. &laquo;Программа курсов включает два блока &mdash; русский и английский язык &mdash; и направлена на решение основных проблем коммуникации в новой языковой и культурной среде. Наши курсы &mdash; это активное расширение словаря, повышение грамотности устной и письменной речи и параллельное знакомство со значимыми литературными текстами&raquo;, &mdash; отмечают организаторы курсов</p>\n<p>Начало кусов с 28 марта 2018 года.</p>\n<p>Занятия проходят по средам:</p>\n<p>Английский &mdash; 20:00</p>\n<p>Русский язык &mdash; 21:00</p>\n<p>Место проведения занятий: Бауманская ул., 58/25 с14 Библиотека им. Н.А. Некрасова: 503 аудитория</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"https://migrant.news.tj/post/2878\" target=\"blank_\">МИГРАНТ.news.tj</a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(223, 0, 1521795108, NULL, NULL, '<p><a title=\"11.04.2017г. Мигранты-соотечественники получили бесплатную юридическую помощь.mp4\" href=\"http://fondorientir.ru/filemanager/i/45fc39b6/11.04.2017г. Мигранты-соотечественники получили бесплатную юридическую помощь.mp4\">11.04.2017г. Мигранты-соотечественники получили бесплатную юридическую помощь.mp4</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(224, 0, 1521797071, NULL, NULL, '<div>Бесплатную юридическую помощь получили мигранты-соотечественники в Сосновом Бору. На протяжении целого дня в молодежном центре \"Диалог\" работала приемная Фонда \"ОРИЕНТИР\".</div>\n<div id=\"cp_widget_ca25a63c-c5b4-4331-82f8-e5b73efcb7ea\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_ca25a63c-c5b4-4331-82f8-e5b73efcb7ea\"; cpo[\"_fid\"] = \"AQDAlN-sR1SS\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>', NULL, NULL, NULL, NULL, NULL, 1),
(225, 0, 1522744835, NULL, NULL, '<p><span style=\"font-family: Arial;\">Напоминаем, что для того, чтобы трудоустроить визового иностранного гражданина, работодателю требуется привлечь его в Россию на основании полученной квоты (если будущая профессия мигранта&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/info/klass/\"><span style=\"color: #007236;\"><strong>квотируемая</strong></span></a><span style=\"font-family: Arial;\">). О том, как получить квоту на иностранца читайте&nbsp;</span><a href=\"http://all-migration.ru/news/34345/\"><span style=\"color: #007236;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Далее, проделав ряд процедур, работодатель оформляет для иностранца разрешение на работу, в котором указана профессия, на которую привлекался мигрант. Любое нарушение в процессе трудоустройства визового иностранца приведет работодателя к штрафам.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Рассмотрим данный вопрос на конкретном примере. 28 апреля 2016 года в Якутске было проведено судебное разбирательство в отношении ООО &laquo;Марыкчаан&raquo; (далее ООО).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В результате проверки данной компании инспектор МВД обнаружил, что ООО привлекло к работе гражданку визовой страны по профессии, отличной от той, что указана в ее разрешении на работу (ч. 4.2 ст. 13 115-ФЗ).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Расследование данного дела показало, что в 2015 году ООО оформило несколько разрешений на работу по профессии &laquo;каменщик&raquo; на основании полученных им квот на привлечение иностранцев в РФ. После этого работодатель заключил с каждым иностранцем трудовой договор. Среди них был трудовой договор гражданки, в котором была указана профессия &laquo;маляр&raquo;.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Видимо сотрудник отдела кадров оформил договор на иностранку неверно из-за незнания закона или просто по ошибке.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Представитель ООО в суде признал вину компании и просил назначить штраф в виде приостановления деятельности компании.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Заметим, что данное правонарушение подходит под статью&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/docs/koap18-15/\"><span style=\"color: #007236;\"><strong>ч.1 ст.18.15 КоАП РФ</strong></span></a><span style=\"font-family: Arial;\">, за которое следует наказание для компании работодателя в виде штрафа от 250 000 руб. до 800 000 руб. или приостановления деятельности компании до 3 месяцев.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">На основании представленных данных по делу, суд признал ответчика виновным в совершении данного правонарушения. Учитывая тот факт, что ответчик признал свою вину, судья назначил ему наказание в виде приостановления деятельности компании на 45 суток, путем опечатывания входных дверей.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Тем самым хотим сказать, что оформляя визового иностранца, работодателю необходимо учитывать множество нюансов, чтобы не допустить ошибок. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34348/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(226, 0, 1522744954, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для того, чтобы работодателю оформить визового иностранца, ему необходимо получить для него приглашение в Россию. Чаще всего работодатели оформляют деловое либо рабочее&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/priglashenie/\"><span style=\"color: #007236;\"><strong>приглашение</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Деловое приглашение требуется, если иностранный специалист приглашается в Россию для каких-то деловых встреч или переговоров. Например, Вам необходимо пригласить в Россию специалиста из немецкой компании-партнера, чтобы он обучил Ваш персонал правильно применять новые технологии в работе.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Рабочее же приглашение выдается для того, чтобы иностранец непосредственно работал в Вашей компании на основании трудового договора.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Сразу обратим внимание, что рабочее приглашение для визовых иностранных граждан оформляется только вместе с&nbsp;</span><a href=\"http://all-migration.ru/services/workpermit/\"><span style=\"color: #007236;\"><strong>разрешением на работу</strong></span></a><span style=\"font-family: Arial;\">. Более подробно об этом&nbsp;</span><a href=\"http://all-migration.ru/services/workpermit/\"><span style=\"color: #007236;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Чтобы оформить деловое приглашение, работодателю надо подготовить и подать <strong>в МВД</strong>:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Учетную карту Вашей компании;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Ходатайство на въезд в Россию иностранцу;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Копию паспорта иностранца;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Данные компании, в которой иностранец работает за границей.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">С момента подачи документов, Вы можете забрать готовое приглашение спустя 20 рабочих дней.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Далее Вы отправляете приглашение иностранцу, после чего он получает у себя на Родине визу (в российском консульстве) для того, чтобы приехать в Россию.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong> Приглашение можно отправить и в электронном виде. Главное, чтобы в консульстве была техническая возможность считать штрих-код электронного&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/priglashenie/\"><span style=\"color: #197b30;\"><strong>приглашения</strong></span></a><span style=\"font-family: Arial;\">. Поэтому узнайте об этом заранее. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34371/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(227, 0, 1522745161, NULL, NULL, '<p style=\"text-align: justify;\">Многих иностранцев волнует, имеют ли они право управлять транспортным средством в России, не имея российского водительского удостоверения.&nbsp;</p>\n<p style=\"text-align: justify;\">Сегодня разберемся в этом вопросе подробнее.&nbsp;</p>\n<p style=\"text-align: justify;\">В статье 25 196-ФЗ &laquo;О безопасности дорожного движения&raquo; подробно расписано, кто имеет право управлять автомобилем в РФ, а кто нет.&nbsp;</p>\n<p style=\"text-align: justify;\">Исходя из этого, можем сказать, что иностранцам не нужно оформлять российские права, если они управляют автомобилем в личных целях, т. е. если они не работают водителями (п.12 ст. 25 рассматриваемого закона).&nbsp;</p>\n<p style=\"text-align: justify;\">Если же трудовая деятельность иностранца связана с управлением автомобиля или другого транспортного средства, то ему необходимо переоформить свои права на удостоверение российского образца.&nbsp;</p>\n<p style=\"text-align: justify;\"><strong>ВАЖНО:</strong> Исключением являются граждане Киргизии и граждане стран, в которых русский язык является официальным.&nbsp;</p>\n<p style=\"text-align: justify;\"><strong>Что делать иностранному гражданину, если он хочет работать водителем?&nbsp;</strong></p>\n<p style=\"text-align: justify;\">В данной ситуации выход только один-получение российских прав. Чтобы мигранту получить водительское удостоверение российского образца, ему необходимо наравне с гражданами РФ пройти 3 этапа экзамена:&nbsp;</p>\n<p style=\"text-align: justify;\">&bull; Тестовое задание по теоретической части;&nbsp;</p>\n<p style=\"text-align: justify;\">&bull; Выполнение практических заданий на площадке в автошколе;&nbsp;</p>\n<p style=\"text-align: justify;\">&bull; Вождение в городе.&nbsp;</p>\n<p style=\"text-align: justify;\">Если же сотрудник МВД остановит иностранца за управлением служебного автомобиля, у которого отсутствуют российские национальные права, то инспектор выпишет следующий штраф:&nbsp;</p>\n<p style=\"text-align: justify;\">&bull; от 5 тыс. руб. до 15 тыс. руб. &ndash; иностранцу; &nbsp;</p>\n<p style=\"text-align: justify;\">&bull; 50 тыс. руб. - должностному лицу, отвечающему за техническое состояние автомобиля.</p>\n<p style=\"text-align: justify;\">Также хотим напомнить иностранцам о том, что 2 административных правонарушения в течение трех лет пребывания в РФ, грозят иностранцу запретом на въезд в Россию или же выдворением за пределы нашей страны.&nbsp;</p>\n<p style=\"text-align: justify;\">Надеемся, что данная статья была для Вас актуальной и полезной.</p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34389/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(228, 0, 1522745534, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Еще до 2014 года иностранцы могли въезжать в РФ без особых ограничений, но теперь мигранты могут находиться в РФ легально только 90 дней в каждом полугодии (180 суток). В связи с данным законом у многих иностранных граждан появляется масса вопросов.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Один из них это: как не нарушая правило &laquo;90 из 180&raquo; пребывать в РФ, если подал документы на&nbsp;</strong></span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Разрешение на временное проживание</strong></span></a><span style=\"font-family: Arial;\"><strong>&nbsp;(РВП)?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Безвизовые иностранцы могут не переживать по поводу законности своего пребывания, если в течение 90 суток они успели подать документы на&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>РВП</strong></span></a><span style=\"font-family: Arial;\">. Так как в статье 5 п.2 №115-ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo; сказано о том, что мигрант имеет право находится в РФ не только на основании уже полученных разрешительных документов, но и, если у него уже были приняты документы на рассмотрение.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Сложнее ситуация обстоит с визовыми приезжими, так как формально такие мигранты могут отталкиваться от закона, упомянутого выше, но в данной статье есть отсылка к статье 6.1 (положение, относящиеся к безвизовым гражданам). Поэтому, после окончания визы иностранец должен покинуть пределы РФ, затем снова въехать в нашу страну для выдачи ему&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>РВП</strong></span></a><span style=\"font-family: Arial;\">.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Напоминаем, что срок рассмотрения заявки на&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><strong><span style=\"color: #005824; font-family: Arial;\">РВП</span></strong></a><span style=\"font-family: Arial;\">&nbsp;для иностранцев, прибывших без визы составит 2 месяца, для визовых мигрантов &ndash; 6 месяцев. <br /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34411/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(229, 0, 1522746719, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Этим летом в нашей стране пройдет крупнейшее событие мирового масштаба - <strong>Чемпионат мира по футболу (далее ЧМ)</strong>.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Иностранцы и их работодатели задают много вопросов, связанных с данным мероприятием.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Поэтому мы продолжаем информировать Вас о том, какие изменения затронут в этом году из-за проведения ЧМ трудовых мигрантов, а, следовательно, и их работодателей.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В прошлой статье, мы делали акцент на том, почему Вы должны направить иностранных сотрудников продлевать трудовой патент до начала ЧМ. Прочитать об этом можно&nbsp;</span><a href=\"http://all-migration.ru/news/34427/\"><span style=\"color: #007236;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Сегодня мы разберем, как правильно регистрировать мигрантов во время проведения чемпионата.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В прошлом году, а именно 09.05.17 президентом был подписан указ № 202, который усиливает меры по повышению безопасности в нашей стране во время ЧМ 2018.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Поэтому в тех городах, где непосредственно будут проводиться соревнования (в их числе Санкт-Петербург), сроки постановки на&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>миграционный учет</strong></span></a><span style=\"font-family: Arial;\">&nbsp;сократились с 7 рабочих дней до <strong>1 календарного дня!&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Таким образом, все иностранцы обязаны оформить первичную временную регистрацию <strong>за сутки</strong> после въезда в страну.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Кроме того, подать уведомление о прибытии в этот промежуток времени можно будет <strong>только через ГУ МВД.&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong> Это правило касается абсолютно всех категорий иностранных граждан. Иностранцы из Таджикистана и стран ЕАЭС не могут пользоваться своими привилегиями в период проведения данных соревнований в РФ!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Данная информация очень важна для тех работодателей, которые ожидают приезд в Россию визовых мигрантов!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Все обязанности по оформлению первичной регистрации иностранца в России лежат только на Ваших плечах.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для того, чтобы подать в МВД уведомление о прибытии иностранца, нужны следующие документы:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Данные Вашей компании, как принимающей стороны;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Паспорт иностранца;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Миграционная карта (в ней должна быть рабочая цель);&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Виза.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong> Каждый документ будет тщательно проверен сотрудниками госорганов!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Тем работодателям, у которых работают безвизовые мигранты, мы советуем <strong>уже сейчас&nbsp;</strong></span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>продлить регистрацию</strong></span></a><span style=\"font-family: Arial;\">&nbsp;сотрудникам на весь период проведения ЧМ.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для этого иностранцу необходимо внести <strong>авансовые платежи по патенту</strong> до августа 2018 года, после чего Вам требуется&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>продлить</strong></span></a>&nbsp;<span style=\"font-family: Arial;\">миграционный учет сотрудника на основании чеков по патенту. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34459/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(230, 0, 1522748003, NULL, NULL, '<p>27 марта 2018 года прошло мероприятие &laquo;Под крылом Санкт-Петербурга&raquo;. В рамках данного мероприятия бесплатно оказано консультирование по вопросам миграционного законодательства, трудоустройства, образования, здравоохранения и налогообложения при поддержки Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге.</p>\n<p>В рамках реализации проекта &laquo;Цивилизованная миграция&raquo;. Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области&raquo; при поддержи &laquo;Фонда-оператора президентских грантов по развитию гражданского общества&raquo;, Фонд &laquo;ОРИЕНТИР&raquo; провел выездную приемную. Юристом Фонда были оказаны консультации, по итогу которых он отметил, что тематика вопросов не поменялась, тенденция остается прежней. Основными вопросами являются: Что делать, если запретили въезд на территорию РФ? Как получить патент? Как получить временную регистрацию?</p>\n<p>В течение 1 часа, которая была выделена непосредственно для оказания консультации, были проконсультированы более 30 человек. Фонд &laquo;ОРИЕНТИР&raquo; регулярно проводит выездные приемные в рамках мероприятия &laquo;Под крылом Санкт-Петербурга&raquo;.</p>\n\n<div id=\"cp_widget_fce3482f-48cf-476b-bf16-a811b5fb73a8\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_fce3482f-48cf-476b-bf16-a811b5fb73a8\"; cpo[\"_fid\"] = \"AgEAQMepy9dx\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script>', NULL, NULL, NULL, NULL, NULL, 1),
(231, 0, 1522749414, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Достаточно часто у работодателей возникают проблемы с трудоустройством иностранцев и оформлением для них миграционных документов, так как при въезде в страну сотрудники случайно указывают неверную цель въезда.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Напоминаем, что Вы можете принять на работу иностранца, только если в его миграционной карте стоит <strong>рабочая цель</strong> въезда!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Если же это не так, то мигрант не сможет оформить&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>регистрацию</strong></span></a><span style=\"font-family: Arial;\">&nbsp;и&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #007236;\"><strong>легальный патент</strong></span></a><span style=\"font-family: Arial;\">, а Вы не сможете его оформить в штат.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Где купить новую миграционную карту?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Понятие &laquo;купить&raquo; в данном вопросе не применяется, так как <strong>миграционную карту</strong> возможно только получить на границе России.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>На какие границы можно ездить иностранным гражданам?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Наша страна очень большая и граничит с множеством других государств. Поэтому у нас часто спрашивают, на какой именно границе можно получить новую миграционную карту. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">По сути, для получения новой карты иностранцу необходимо выехать за пределы России. Но здесь есть много нюансов.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Получить карту на российско-белорусской границе не представляется возможным, так как граница между нашими странами &laquo;стерта&raquo; из-за соглашений между государствами. Именно поэтому белорусы вовсе не оформляют карту при въезде в РФ.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Кроме того, для выезда на территорию большинства стран, граничащих с нами нужна виза. Таким образом, данный вариант тоже не совсем удобен.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Из этого можно сделать вывод, что выезжать за получением новой миграционной карты можно только в страны СНГ.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Ближе всего к нам расположены Украина и Казахстан, поэтому чаще всего поездки осуществляются именно в эти страны.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Но Украина тоже усложнила процесс въезда на территорию их страны, и поездка на эту границу подойдет только украинцам, а также гражданам Молдавии, так как на данную страну ограничения Украины не распространяются.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Остальным иностранным гражданам проще всего будет оформить новую миграционную карту на российско-казахстанской границе. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34480/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(232, 0, 1523864628, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Основополагающим моментом для трудового мигранта, несомненно, является выбор профессии. У иностранцев, приезжающих в Россию с целью работы возникает масса вопросов по данной теме.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Как найти работу по профессии? Есть ли профессии, которые запрещены для приезжих? Последний вопрос, мы уже рассматривали в одной из статей (подробности&nbsp;</span><a href=\"http://all-migration.ru/news/34344/\"><span style=\"color: #005824; font-family: Arial;\"><strong>здесь</strong></span></a><span style=\"font-family: Arial;\">).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Сегодня ответим на часто задаваемый вопрос: <strong>может ли иностранец быть генеральным директором и в целом занимать руководящую должность?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Наш ответ &ndash; да, может!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">По теме профессий не так много ограничений для иностранных граждан. Мигрант не имеет права занимать только несколько должностей, связанных с государственной и муниципальной службой, а также работать главным бухгалтером могут только постоянно и временно проживающие иностранцы (статья 14 115-ФЗ). &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Исходя из этого, делаем вывод, что стать руководителем мигрант может без проблем.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для временно-пребывающих иностранцев важно, чтобы его должность совпадала с профессией, указанной в&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>патенте</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для граждан с&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Разрешением на временное проживание</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(РВП), обязательно трудиться в том регионе, в котором был получен такой документ.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Что касается, иностранцев с&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Видом на жительство</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(ВНЖ), для них тем более нет преград, так как такие граждане практически россияне и могут работать на всей территории РФ и по любой профессии без каких-либо ограничений. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34508/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(233, 0, 1523864675, NULL, NULL, '<p><span style=\"font-family: Arial;\">В теме трудоустройства мигрантов есть куча нераскрытых вопросов. Легче всего приезжим, которые устраиваются на работу в компанию, то есть заключают трудовые отношения с юридическим лицом.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Но, как быть иностранцу, который собирается сотрудничать с частным лицом? Следует ли заключать трудовой договор? Как фиксируется такой труд?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Многие мигранты совершают ошибку, не заключая со своим работодателем договор. В итоге, это чревато различными последствиями: такой иностранец легко может остаться без зарплаты, и доказать он это никак не сможет, так как нет документального подтверждения его работы. Кроме того, если приезжий находился дольше положенного срока в РФ, да еще и работал нелегально &ndash; его пребывание в стране считается незаконным, и проблем с госорганами не избежать.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">И все же многих иностранцев волнует вопрос: <strong>обязательно ли заключать трудовой договор (ТД), если иностранец работает на физическое лицо?&nbsp; </strong></span></p>\n<p><span style=\"font-family: Arial;\">Конечно, обязательно!&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">Согласно п.1 статьи 13.3 115-ФЗ граждане РФ имеют право привлекать на работу иностранцев, для обеспечения личных или домашних нужд, при наличии у данного иностранца&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>патента</strong></span></a><span style=\"font-family: Arial;\">&nbsp;или&nbsp;</span><a href=\"http://all-migration.ru/services/workpermit/\"><span style=\"color: #005824; font-family: Arial;\"><strong>разрешения на работу</strong></span></a><span style=\"font-family: Arial;\">, но только на основании трудового или гражданско-правового договора.&nbsp; </span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34523/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(234, 0, 1523865060, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Прием в гражданство нашей страны, конечно, подразумевает тщательную проверку документов иностранца. Как мы уже говорили ранее, есть несколько вариантов вступления в<strong><span style=\"color: #005824;\">&nbsp;</span></strong></span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><span style=\"color: #005824; font-family: Arial;\"><strong>гражданство РФ</strong></span></a><span style=\"font-family: Arial;\">: общий и упрощенный порядки, а также участие в государственной программе переселения соотечественников.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для каждого из вышеперечисленных оснований комплект документов индивидуальный, но при любом из этих порядков иностранец, претендующий на&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><span style=\"color: #005824; font-family: Arial;\"><strong>гражданство РФ</strong></span></a><span style=\"font-family: Arial;\">, обязан подтвердить документально то, что он может содержать себя в нашей стране.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Часто задаваемый вопрос постоянно проживающих иностранцев: <strong>как подтвердить свою финансовую состоятельность если трудоустроен неофициально?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В основном, мы призываем иностранных граждан устраиваться на работу официально, так как это гарантирует стабильный доход и сокращает риски возникновения проблем с представителями закона. Но некоторые работодатели избегают официального трудоустройства, так как считают, что этим защищают себя от штрафов. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Второй часто задаваемый вопрос: <strong>что делать если официальная зарплата ниже МРОТ (минимальный размер оплаты труда)?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Это случается потому, что работодатель,например, принял их на полставки или по совместительству.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Ответ на оба вопроса один:</strong> предоставить другие документы, подтверждающие наличие средств к существованию.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Это могут быть: &nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">- справка о доходах 2 НДФЛ или 3 НДФЛ (например, супруг на иждивении у супруги или наоборот, то есть зарплата одного из них должна позволять обеспечивать двоих человек).&nbsp; <br /></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34544/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(235, 0, 1523865093, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Существуют несколько схожих моментов в процедуре трудоустройства иностранцев и граждан РФ. Сходство это заключается в обязательных документах, которые следует предоставить каждому потенциальному работнику. В данном случае речь пойдет о&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/snils/\"><strong><span style=\"color: #005824; font-family: Arial;\">СНИЛС</span></strong></a><span style=\"font-family: Arial;\">. Если Вы собираетесь работать в нашей стране, уже оформили трудовой патент и нашли потенциального работодателя, то эта статья для Вас!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>&nbsp;Что такое&nbsp;</strong></span><a href=\"http://all-migration.ru/services/addservices/snils/\"><span style=\"color: #005824; font-family: Arial;\"><strong>СНИЛС</strong></span></a><span style=\"font-family: Arial;\"><strong>?&nbsp;</strong></span></p>\n<p><a href=\"http://all-migration.ru/services/addservices/snils/\"><strong><span style=\"color: #005824;\">СНИЛС</span></strong></a><span style=\"font-family: Arial;\">&nbsp;(страховой номер индивидуального лицевого счета) &ndash; представляет собой ламинированный документ зеленого цвета, в котором указан лицевой счет мигранта, который в свою очередь числится в системе пенсионного страхования. Именно на этот номер, направляются отчисления в счет будущей пенсии мигранта.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Для чего он нужен иностранцу?&nbsp; </strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Согласно статье 7 п. 1 ФЗ- 167 абсолютно все иностранные граждане, собираясь временно-пребывать в нашей стране с целью устройства на работу обязаны оформить себе&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/snils/\"><span style=\"color: #005824; font-family: Arial;\"><strong>СНИЛС</strong></span></a><span style=\"font-family: Arial;\">. Ни один работодатель не имеет права принимать на работу приезжего без такого документа. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Продлевать и переоформлять его иностранцу не потребуется, так как выдается&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/snils/\"><span style=\"color: #005824; font-family: Arial;\"><strong>СНИЛС</strong></span></a><span style=\"font-family: Arial;\">&nbsp;один раз и на всю жизнь.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Какие документы понадобятся для оформления&nbsp;</strong></span><a href=\"http://all-migration.ru/services/addservices/snils/\"><span style=\"color: #005824; font-family: Arial;\"><strong>СНИЛС</strong></span></a><span style=\"font-family: Arial;\"><strong>? &nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Иностранцу следует предоставить:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Паспорт&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Миграционную карту&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; Действующую временную регистрацию&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Оформлением данного документа занимается Пенсионный Фонд России, в который изначально должно поступить заявление от организации, в которой Вы собираетесь трудиться, вместе с Вашими документами. Поэтому, в первую очередь нужно договориться о подаче документов на СНИЛС со своим работодателем.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34561/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(236, 0, 1523865215, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Отсутствие штатного мигранта на рабочем месте без уважительных причин более 4х часов подряд уже считается прогулом (согласно ст.81 пункту 6(а) ТК РФ).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Можно ли уволить иностранца за отсутствие на работе без уважительных причин и как это сделать, не нарушив закон? &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Разберёмся, что делать кадровому сотруднику, если иностранец пропал и не выходит на работу.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для начала нужно выяснить, где сейчас находится сотрудник. Возможно, его прогул вынужденный (был задержан полицией, находился в плену и т.д.).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Конкретные уважительные причины, по которым сотрудник может отсутствовать на рабочем месте, законом не регламентированы.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Чаще всего это решается через суд.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Предположим, что Вам не удалось установить причины прогула, и Ваш работник так и не появился.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В первую очередь нужно отправить иностранцу заказное письмо с уведомлением о вручении на известный домашний адрес (или иной адрес, указанный иностранным гражданином в договоре, на который он может принимать корреспонденцию от работодателя) с просьбой явиться на работу и объяснить причины отсутствия. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Если ответ не получен, уже можно составлять акт (ст. 193 ТК РФ).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Помимо этого, нужно обязательно зафиксировать факт отсутствия сотрудника в табеле (с самого первого дня неявки).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Дополнительно рекомендуем собрать показания свидетелей и докладные записки руководителю прогульщика.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Сложность в том, что факт прогула необходимо подтвердить документально, а в законе конкретного списка документов нет. Поэтому, для подтверждения прогула, в случае судебного разбирательства по вопросу неправомерного увольнения сотрудника, необходимо иметь:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">1. Уведомление о вручении письма (которое вы должны отправить сотруднику на фирменном бланке с запросом объяснений о его отсутствии и с указанием срока, в течение которого сотрудник должен предоставить Вам ответ или выйти с Вами на связь).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">2. Несколько актов, которые составлены из-за отсутствия объяснений сотрудника из-за неявки на работу (с подписями не менее двух свидетелей).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">3. Табель рабочего времени с фиксацией отсутствия сотрудника .&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">4. Докладная на сотрудника его руководителю.&nbsp;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: #197b30;\"><strong>ВАЖНО:</strong></span> Если Ваш иностранец не был на работе по уважительной причине, даже если это выяснилось впоследствии, его необходимо восстановить (ст. 394 ТК РФ).&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"color: #007236;\"><strong>И НАПОСЛЕДОК:</strong></span> Из-за отсутствия определённого законодательного порядка увольнения за прогул, кадровым сотрудникам ничего не остаётся, как самостоятельно это регламентировать в трудовом договоре. Каким образом? Вы можете подстраховать себя, если впишите в договор:&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; сотрудников, уполномоченных составлять акты об отсутствии мигранта на рабочем месте;&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">&bull; адрес, по которому иностранец может принимать письма от работодателя (не обязательно по месту проживания).</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Если дело дошло до увольнения, работодатель обязан в течение 3х рабочих дней&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/notification/\"><span style=\"color: #007236;\"><strong>уведомить УВМ ГУ МВД</strong></span></a><span style=\"font-family: Arial;\">&nbsp;о расторжении трудового договора с иностранным гражданином.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">При увольнении за самовольное оставление рабочего места визового иностранца (который прибыл в РФ по приглашению от работодателя), работодатель дополнительно обязан направить информацию о прогуле в УВМ ГУ МВД и ФСБ (согласно пп. 8 п. 8 ст. 18 ФЗ№ 115).&nbsp; </span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34580/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(237, 0, 1523865240, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Чтобы получить какой-либо разрешительный документ в нашей стране, подачи полного комплекта документов в срок недостаточно. Одно из самых главных условий в успешной обработке уже поданных документов &ndash; это легальность пребывания иностранца.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">К сожалению, не все приезжие перед въездом в РФ \"знакомятся\" с внутренними законами страны. На данный момент в РФ существует правило для мигрантов <strong>&laquo;90 из 180&raquo;</strong>. Данное правило распространяется только на временное пребывающих граждан, вне зависимости,приехал он из визовой или безвизовой страны. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><strong><span style=\"font-family: Arial;\">Что представляет собой правило &laquo;90 из 180&raquo;? &nbsp;</span></strong></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Согласно статье 5 ФЗ 115 мигрант, прибывший в РФ может находиться в РФ только 90 дней в одном полугодии (180 суток). Разрешённые 90 дней иностранец может по необходимости находиться не подряд, он имеет право выезжать.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Помните, что Вы сможете пребывать в РФ законно только три месяца в течение полугода. Но, если Вы все же решили остаться в нашей стране, Вам следует оформить разрешительные документы. Мы уже рассказывали подробнее об этом (можно прочитать</span><a href=\"http://all-migration.ru/news/34411/\"><span style=\"color: #005824; font-family: Arial;\"><strong> здесь</strong></span></a><span style=\"font-family: Arial;\">). &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Какие могут быть последствия нарушения данного закона? &nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Если Вы до сих пор находитесь в РФ, но Ваши законные сроки пребывания истекли, и Вы только собираетесь оформить себе, например, патент или разрешение на временное проживание, вероятность получения отказа в оформлении Вам документов крайне велика.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">К тому же, такому иностранцу грозит штраф от пяти до семи тысяч рублей и выдворение из страны! Если же Вы подаете документы вовремя, в течение 90 дней Вашего законного пребывания, Вы автоматически продлеваете срок нахождения на основании принятых документов (п.2 статья 5 ФЗ- 115). &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong> Мигрант даже может не знать о том, что гос органы осведомлены о его нарушении, он спокойно выедет из страны, но, если соберется приехать снова, на границе его может ожидать неприятный &laquo;сюрприз&raquo;: его попросту не впустят.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Поэтому, призываем Вас следить за сроками Ваших разрешительных документов, и оформлять их только через проверенные компании! <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34581/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(238, 0, 1524045313, NULL, NULL, '<p><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><strong><span style=\"color: #005824; font-family: Arial;\">Разрешение на временное проживание</span></strong></a><span style=\"font-family: Arial;\">&nbsp;(РВП) &ndash; тот документ, с которого у мигранта, зачастую, начинается процедура оформления гражданства РФ. Конечно, получить его смогут не все мигранты, как и для множества разрешительных документов в РФ, иностранцу потребуются основания.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">Сегодня расскажем Вам, как получить гражданство, пройдя 4 этапа, благодаря родителю - россиянину.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">1. Получение&nbsp;<strong><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\">РВП</a>&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Этот документ имеет множество плюсов. Во-первых, благодаря ему мигранту не нужно оформлять патент на работу, он может в течение трех лет жить и работать в РФ по любой профессии.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Единственное условие: делать это можно только в том регионе, где он его получил. Если хотя бы один Ваш родитель является россиянином, Вы сможете воспользоваться данным основанием для получения РВП без квоты.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но здесь есть три обязательных условия, которые следует подтвердить документально:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;&bull; - Вам еще нет 18 лет&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; - Вы уже совершеннолетний, и Ваш родитель недееспособный&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; - Если один или оба родителя нетрудоспособные \\ пенсионеры Кроме того, в МВД потребуется: Ваш паспорт(+перевод) и Вашего родителя-россиянина, миграционная карта, временная регистрация, документ о владении русским языком, медицинское заключение, свидетельство о рождении с переводом, документ, подтверждающий законное пребывание в РФ за последние 5 лет, оплата гос пошлины. Заявка будет рассмотрена для безвизовых граждан в течение трех месяцев, для визовых в течение полугода.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;2. Далее Вы должны прожить в России по&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>РВП</strong></span></a><span style=\"font-family: Arial;\">&nbsp;шесть месяцев (не нужно проживать целых 3 года).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. Теперь Вы сможете оформить Вид на жительство.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">4. После получения&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\"><span style=\"color: #005824; font-family: Arial;\"><strong>ВНЖ</strong></span></a><span style=\"font-family: Arial;\">&nbsp;можно сразу же приступить к оформлению гражданства. Напоминаем Вам о том, что наличие братьев, сестер, тетей, дядей и т.д. - не является основанием для получения РВП или&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><strong><span style=\"color: #005824; font-family: Arial;\">гражданства</span></strong></a><span style=\"font-family: Arial;\">. <br /></span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34586/\" target=\"blank_\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(239, 0, 1524124731, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Не многие иностранцы в курсе того, что в 2018 госорганы все больше уделяют времени проверке подлинности регистраций у мигрантов. А кто в курсе ситуации не предпринимает никаких действий, и очень зря, ведь сейчас определить фиктивность&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>регистрации</strong></span></a><span style=\"font-family: Arial;\">&nbsp;для МВД не составит труда.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Как представители МВД распознают ненастоящую регистрацию?</strong>&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Во-первых, госорганы владеют информацией о самых распространенных адресах \"резиновых\" квартир и офисов по Санкт-Петербургу и Ленинградской области. Такие офисы оформляют нелегальный&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционный учет</strong></span></a><span style=\"font-family: Arial;\">, следовательно, иностранец, воспользовавшись услугами такой организации автоматически нарушает закон.</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Кроме того, представители МВД могут направится с проверкой прямо по адресу на основе предъявленной им регистрации. Если выяснится, что данный гражданин там не проживает, или не работает (если указан адрес организации), то это также говорит о фальшивом документе. Иностранцу с таким нарушением грозит штраф от пяти до семи тысяч рублей с выдворением за пределы страны с последующим возможным запретом на въезд на 5 лет!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Для того чтобы быть уверенным в легальности своей&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>регистрации</strong></span></a><span style=\"font-family: Arial;\">, Вам следует оформлять ее по месту пребывания или по месту работы. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Мы напоминаем Вам о важности легального&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционного учета</strong></span></a><span style=\"font-family: Arial;\">, тем более накануне Чемпионата мира по футболу 2018.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>На что следует обратить внимание при выборе компании для оформления разрешительных документов?</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">1) сколько лет существует компания на рынке&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">2) отзывы о компании&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">3) возможное партнерство с гос учреждениями &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">4) цены на услуги (вас должна насторожить низкая стоимость, также стоит уточнять, что входит в данное предложение.&nbsp; </span></p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34613/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(240, 0, 1524577075, NULL, NULL, '<p><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Трудовой патент</strong></span></a><span style=\"font-family: Arial;\">&nbsp;&ndash; самый основной документ для мигрантов, планирующих работать в нашей стране. Ведь легально трудоустроиться можно только при наличии такого разрешительного документа. Более того, патент на работу предоставляет право мигранту продлить свое пребывание в РФ до одного года.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">Но для начала его нужно получить. Как оказалось, для многих иностранных граждан это вызывает некоторые трудности. В предыдущей статье мы рассмотрели самые популярные причины отказа госорганов в выдаче патента.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что делать иностранцу, который столкнулся с такой проблемой?&nbsp; </strong></span></p>\n<p><span style=\"font-family: Arial;\">Решение об отказе в выдаче&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>патента</strong></span></a><span style=\"font-family: Arial;\">&nbsp;следует обжаловать. Поэтому, если Вы не согласны с решением, Вы имеете право его оспорить:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. Если Вам отказали в выдаче&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>патента</strong></span></a><span style=\"font-family: Arial;\">, Вам должны предоставить официальный отказ в письменной форме с указанием причины отказа.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2.На основании данного подтверждения Вы можете обжаловать решение в суде или направить ходатайство о его пересмотре руководству МВД. &nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong> Так как времени на обжалование полученного отказа очень мало, то лучше обратиться к&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Advokat/\"><span style=\"color: #005824; font-family: Arial;\"><strong>высококвалифицированному юристу</strong></span></a><span style=\"font-family: Arial;\">, который найдет основания для вашего дальнейшего легального пребывания в РФ.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">В противном случае, Вам придется выехать из России до истечения законного срока пребывания. И только через год у вас появится возможность попробовать подать документы на патент снова. Мы настоятельно рекомендуем с первых дней приезда в РФ оформлять только легальные документы!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В особенности, это касается&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционного учета</strong></span></a><span style=\"font-family: Arial;\">. Ведь в большинстве случаев именно из-за покупки фиктивной регистрации, мигрант в последствии оказывается без патента. <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34638/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(241, 0, 1525332047, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В канун <strong>Чемпионата Мира по футболу 2018 </strong>(ЧМ) возвращаем наших клиентов к теме&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционного учета</strong></span></a><span style=\"font-family: Arial;\">! До старта чемпионата осталось совсем мало времени, а изменений в миграционном законодательстве все больше.&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><strong>Какие изменения коснутся абсолютно всех мигрантов?&nbsp;</strong></span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Ровно через месяц, с 25 мая по 25 июля 2018 года, каждому иностранному гражданину, вне зависимости из какой страны он приехал, придется вставать на миграционный учет в течение суток!&nbsp; </span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Акцентируем Ваше внимание на том, что в данный период на всех иностранцев будут распространятся одинаковые правила! Даже если Вы гражданин ЕАЭС, которому обычно нужно было зарегистрироваться за 30 календарных дней, с 25 мая Вам также придется оформлять миграционный учет в течение суток!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Конечно, все это не просто так: наша страна примет у себя миллионы иностранных гостей, поэтому согласно указу Президента РФ от 9 мая 2017 года №202 в период Чемпионата будут усилены меры безопасности на территории РФ!&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Кроме того, представители МВД будут тщательно отслеживать нелегальных мигрантов и аннулировать им просроченные разрешительные документы, а также документы, сделанные на основании фиктивного миграционного учета (подробнее о причинах аннулирования регистрации читайте&nbsp;</span><a href=\"http://all-migration.ru/news/34660/\"><strong><span style=\"color: #005824; font-family: Arial;\">здесь</span></strong></a><span style=\"font-family: Arial;\">).&nbsp; <br /></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34667/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(242, 0, 1526549865, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">В основном, иностранные граждане, которые уже долгое время пребывают в нашей стране на основании&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>патента</strong></span></a><span style=\"font-family: Arial;\">, хотели бы&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #005824; font-family: Arial;\"><strong>продлить</strong></span></a><span style=\"font-family: Arial;\">&nbsp;его еще на один год. Мы часто информируем своих клиентов о том, какие документы необходимо собрать и в какой срок их следует подать в МВД, но как быть если у мигранта особенная ситуация, например, нет трудового договора (ТД). Разберем данную тему в сегодняшней статье.&nbsp; </span></p>\n<p><span style=\"font-family: Arial;\">У иностранца может не быть трудового договора по различным причинам. К примеру, работал неофициально, вовремя не направил копию ТД в МВД, нет уведомления о заключении такого договора, трудился в организации, которая не предоставила ему таковых документов, и теперь планирует сотрудничать с другим работодателем.&nbsp;</span></p>\n<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">К сожалению,&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #005824; font-family: Arial;\"><strong>продлить трудовой патент</strong></span></a><span style=\"font-family: Arial;\">&nbsp;возможно только <strong>на основании трудового договора</strong>, но это не повод придаваться панике - <strong>выход есть</strong>!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чтобы дальше работать в РФ, Вам необходимо оформить новый патент. Для этого Вам следует сделать въезд\\выезд на границу РФ, заполнить новую миграционную карту (не забудьте отметить цель въезда &ndash; \"работа\"), и уже после получения данной карты Вы начинаете подготовку комплекта документов для оформления нового трудового патента.&nbsp; </span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34715/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>\n<p>v</p>', NULL, NULL, NULL, NULL, NULL, 1),
(243, 0, 1526549984, NULL, NULL, '<p style=\"text-align: justify;\"><span style=\"font-family: Arial;\">Тема&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционного учета</strong></span></a><span style=\"font-family: Arial;\">&nbsp;сейчас одна из самых актуальных для иностранных граждан! В особенности, в преддверии Чемпионата Мира по футболу 2018!</span></p>\n<p><span style=\"font-family: Arial;\">Очень важно, когда правительство стран иностранцев заботится о них не только в периметре своего государства, но и за его пределами. Так, еще в конце апреля правительство Таджикистана выдвинуло вопрос о продлении сроков пребывания своим соотечественникам без регистрации в России до 30 дней!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Государственная дума России, в свою очередь, выразила готовность сотрудничать! Был рассмотрен вопрос пенсии и семейного статуса иностранцев. Власти Таджикистана хотели бы чтобы члены семей Таджикских мигрантов смогли без проблем находится в РФ вместе с ними.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Более того, стороны договорились по поводу увеличения квот для студентов Таджикистана в РФ и о строительстве 5 школ с русским языков в Таджикистане!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Напоминаем, что в нашей стране максимальное количество времени на оформление миграционного учета дается гражданам ЕАЭС &ndash; это 30 календарных дней, <strong>приезжим из Таджикистана положено сделать это - в течение 15 календарных дней.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Пока, к сожалению, <strong>подобного постановления нет</strong>, но будем надеется, что в скором времени российские госорганы примут меры по увеличению сроков пребывания гражданам Таджикистана.&nbsp; <br /></span></p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34769/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>\n<p>v</p>', NULL, NULL, NULL, NULL, NULL, 1),
(244, 0, 1527160761, NULL, NULL, '<p><span style=\"font-family: Arial;\">Проведение Чемпионата Мира по футболу очень волнует трудовых мигрантов и их работодателей. И это неудивительно!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данное мероприятие определенно внесет коррективы в обыденную жизнь иностранных сотрудников в нашей стране, так как осуществить подачу документов в госорганы на оформление либо продление миграционных документов будет крайне сложно.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Мы также как и Вы хотим, чтобы все иностранные граждане чувствовали себя в безопасности во время ЧМ и знали, что их миграционные документы, такие как&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #007236;\"><strong>патент</strong></span></a>,&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>регистрация</strong></span></a>&nbsp;и т. п. в полном порядке.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому мы советуем всем работодателям уточнить у своих сотрудников:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; До какого месяца внесена оплата авансовых платежей за патент;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; До какого месяца действует временная&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #007236;\"><strong>регистрация</strong></span></a>;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; До какого числа действует его патент на работу и можно ли его&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #007236;\"><strong>переоформить</strong></span></a>&nbsp;заранее (это возможно в том случае, если иностранец еще не продлевал патент на второй год без выезда за пределы страны, так как продлить патент на третий год невозможно). <br /></span></p>\n<p><span style=\"font-family: Arial;\">После этого соотнесите время окончания действия документов с периодом проведения ЧМ. </span></p>\n<p><span style=\"font-family: Arial;\">Если Вы понимаете, что продлить регистрацию или патент надо как раз в летние месяцы, то сделайте это уже сейчас!</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong> Не забывайте, что неаккредитованные работодатели имеют права оформлять иностранцев и не смогут подавать документы на продление регистрации для иностранных сотрудников.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">А пройти&nbsp;<a href=\"http://all-migration.ru/services/addservices/akkreditaciya-ur-lica-fms-emc/\"><span style=\"color: #007236;\"><strong>аккредитацию</strong></span></a>&nbsp;во время ЧМ возможно не получится, поэтому позаботьтесь об этом&nbsp;<a href=\"http://all-migration.ru/services/addservices/akkreditaciya-ur-lica-fms-emc/\"><span style=\"color: #007236;\"><strong>заранее</strong></span></a>. <br /></span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34823/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(245, 0, 1535615505, NULL, NULL, '<p><span style=\"font-family: Arial;\">То, о чем мы Вас предупреждали - случилось! В связи с приближающимся Чемпионатом Мира для иностранцев и их принимающих сторон теперь действуют новые правила! О каких правилах идет речь и с какими трудностями придется столкнуться иностранцам и их работодателям, расскажем сегодня.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Согласно&nbsp;</span><a href=\"http://all-migration.ru/upload/medialibrary/ukaz%20mu.docx\"><span style=\"color: #007236; font-family: Arial;\"><strong>Указу Президента Российской Федерации</strong></span></a><span style=\"font-family: Arial;\">&nbsp;от 12.05.2018 № 214 уже с 25 мая иностранцам дается всего трое суток, чтобы&nbsp;</span><span style=\"font-family: Arial;\"><strong>оформить себе регистрацию</strong></span><span style=\"font-family: Arial;\">&nbsp;вне зависимости от страны гражданства.&nbsp;<strong>Сделать это можно как через МВД, так и через МФЦ</strong>. Обратите внимание,&nbsp;<strong>почта заниматься оформлением временной регистрации не будет!</strong>&nbsp;Ранее сообщалось, что следует&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>встать на миграционный учет</strong></span></a><span style=\"font-family: Arial;\">&nbsp;за одни сутки и только в МВД. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Иностранным гражданам следует помнить, что вместе с повышенным вниманием к безопасности во время ЧМ 2018 усилился надзор за их деятельностью и передвижениями. Госорганами проверяется каждый документ. И если иностранного гражданина остановят, проверят его документы и, например,<strong>&nbsp;</strong>миграционного учета</span><span style=\"font-family: Arial;\">&nbsp;не окажется в базе, то его задержат и ему будет грозить штраф с возможной депортацией и запретом на въезд на 5 лет.</span></p>\n<p><span style=\"font-family: Arial;\">К сожалению, сейчас участились случаи, когда иностранцам оформляют миграционный учет</span><span style=\"font-family: Arial;\">&nbsp;через МФЦ, но к моменту остановки их регистраций все еще не оказывалось в базе МВД. И чтобы быть уверенными в том, что<span style=\"color: #005824;\">&nbsp;</span>миграционный учет</span><span style=\"font-family: Arial;\">&nbsp;(регистрация) точно внесен в базу МВД, наученные опытом иностранцы и их работодатели обращаются за его оформлением напрямую в МВД.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но и здесь не обходится без трудностей: из-за этого в МВД сформировались длинные очереди (см. видео ниже) и происходят задержки как при подаче, так и при получении разрешительного документа.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Внимание:</strong>&nbsp;с наступлением лета все только усугубится. Поэтому мы рекомендуем заняться оформлением документов сейчас, т.к. уже сегодня&nbsp;<strong>задержки на выдачу документов составляют около 2-3 дней</strong>.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34847/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(246, 0, 1535615714, NULL, NULL, '<p><span style=\"font-family: Arial;\">Иностранцев, желающих оформить себе статус временно проживающего гражданина, с каждым годом становится все больше. И это неудивительно, ведь&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>разрешение на временное проживани</strong></span><span style=\"color: #005824; font-family: Arial;\"><strong>е</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(РВП) имеет массу преимуществ перед другими разрешительными документами!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Например, оформить РВП для иностранца намного выгоднее, чем патент на работу:</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- РВП так же, как и&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>трудовой патент</strong></span></a><span style=\"font-family: Arial;\">, дает право жить и работать в определенном регионе, но не ограничивает в профессии;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- В отличие от патента разрешение на временное проживание не нуждается в ежегодном продлении, т.к. действует целых три года;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- Кроме этого РВП не предусматривает ежемесячного авансового платежа, а значит не придется контролировать их и постоянно продлевать миграционный учет;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- А еще такое разрешение является первым шагом к&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><span style=\"color: #005824; font-family: Arial;\"><strong>получению гражданства РФ</strong></span></a><span style=\"font-family: Arial;\"><strong><span style=\"color: #005824;\">.</span></strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Получить разрешение на временное проживание можно двумя способами:</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1) по квоте (на каждый регион Правительство РФ выделяет ограниченное количество квот). Мы не занимаемся получением квоты, но Вы сможете сделать это самостоятельно, обратившись в МВД. Однако если квота у вас уже есть, мы можем помочь с последующей подачей документов на РВП.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2) по основанию (оформить можно через нашу компанию или самостоятельно через МВД).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что может выступать основанием для получения РВП?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">К сожалению, оснований для оформления данного разрешения не так много. Но если Вы:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- состоите в браке с гражданином РФ,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- родились в РСФСР,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- имеете детей или родителей &ndash; граждан РФ,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- поступили на военную службу,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">то у Вас есть все шансы получить заветное РВП!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Все, что должен знать иностранец об РВП, помимо условий получения &ndash; это как и где легально его оформить. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Самое важное для иностранца, в данном случае - это не попасть к мошенникам. Необходимый перечень документов и все подробности верного оформления Вашего РВП смогут подсказать только опытные миграционные юристы. А такие у нас есть!&nbsp;&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34854/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(247, 0, 1535618434, NULL, NULL, '<p><span style=\"font-family: Arial;\">Мы уже не раз призывали иностранцев осуществлять постановку на&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>миграционный учет</strong></span></a><span style=\"font-family: Arial;\">&nbsp;исключительно на тот адрес по которому они будут проживать или работать. Ведь теперь госорганы не только тщательно проверяют документы у каждого встречного иностранца, но и выезжают с проверкой по адресам!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Каждый миграционный учет вносится в базу ГИС МУ (государственная система миграционного учета), на основе нее МВД отслеживает адреса, на которые происходят массовые регистрации, и в первую очередь выезжают с проверкой по ним!&nbsp;<strong>Если окажется, что Вы не проживаете или не работаете по адресу, который указан в регистрации</strong>, либо он является \"резиновой\" квартирой или \"резиновым\" офисом,&nbsp;<strong>то Вашу регистрацию аннулируют!</strong>&nbsp;Вы даже можете не узнать об этом.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Зачастую, временно пребывающие граждане не придают особого значения своей регистрации: намеренно экономят на оформлении, покупая ее у мошенников, и не задумываются о последствиях.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong>&nbsp;За нарушения правил въезда и режима временного пребывания иностранцу грозит штраф в размере от 5 до 7 тысяч рублей (по Санкт-Петербургу или ЛО) с возможным выдворением за пределы страны!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому, призываем Вас не рисковать, а изначально обращаться в проверенную компанию, которая оформит Вам временную регистрацию: во-первых, на Ваш адрес, где вы непосредственно проживаете или работаете, и, во-вторых, легально!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Не забывайте, также, и о сроках постановки на миграционный учет. В особенности, если Вы заехали в РФ после 25 мая</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34860/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(248, 0, 1535618601, NULL, NULL, '<p><span style=\"font-family: Arial;\">Для граждан Таджикистана после приезда в Россию самый быстрый способ легализоваться и остаться в РФ на срок до одного года, имея право работать, - это оформление трудового патента. Но сегодня мы расскажем об одном способе длительного проживания в РФ для граждан Таджикистана.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Речь пойдет о&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>разрешении на временное проживание</strong></span></a><span style=\"font-family: Arial;\">&nbsp;(РВП).&nbsp;<strong>Благодаря этому разрешению, граждане Таджикистана смогут:&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">- три года жить и работать в РФ,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- им не нужно будет постоянно продлевать временную регистрацию (миграционный учет),&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- ежемесячно платить авансовые платежи (чеки по патенту)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- и через год заниматься продлением или оформлением нового разрешительного документа.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">И единственным обязательным условием РВП является ежегодное подтверждение своего проживания в МВД.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong>&nbsp;Проживать и работать по&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/razreshenie-na-vremennoe-progivanie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>РВП</strong></span></a><span style=\"font-family: Arial;\">&nbsp;можно только в том регионе, в котором оно будет выдано.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Помимо всего прочего, после получения РВП уже через год можно претендовать на&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\"><span style=\"color: #005824; font-family: Arial;\"><strong>Вид на жительство</strong></span></a><span style=\"font-family: Arial;\"><strong><span style=\"color: #005824;\">&nbsp;</span></strong>(ВНЖ), а затем -&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\"><span style=\"color: #005824; font-family: Arial;\"><strong>гражданство РФ</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Каких-либо льгот на получение РВП для приезжих из Таджикистана нет.&nbsp;<strong>Поэтому если Вы хотите оформить РВП, у Вас есть два варианта:&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">-занять очередь на получении квоты (только МВД);&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">-определить, есть ли у Вас основание на получение РВП (если есть, можно обратиться в нашу компанию за оформлением РВП).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чтобы узнать, есть ли у Вас основание, можно позвонить по телефону нашим юристам или прочесть здесь.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Какие нужны документы для РВП для граждан Таджикистана?</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для каждого основания Вам потребуется определенный перечень документов, но есть те, которые Вам нужно будет предоставить в любом случае, а именно:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; паспорт и его нотариальный перевод,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; миграционную карту,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; миграционный учет,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; документ, подтверждающий владение русским языком (обязательно уровень: РВП),&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; медицинское заключение,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; заполненное заявление,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; чек об уплате госпошлины. Информацию об остальных документах для РВП для граждан Таджикистана нужно уточнять индивидуально, так как все зависит от конкретного основания.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Рассматриваться документы после подачи будут 2 месяца в МВД.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/34887/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(249, 0, 1535709244, NULL, NULL, '<p><span style=\"font-family: Arial;\">Не каждый иностранец знает миграционные законы нашей страны, и в курсе правил пребывания&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>на основании трудового патента</strong></span></a><span style=\"font-family: Arial;\">. Из-за чего некоторые приезжие сталкиваются с его аннулированием. Сегодня мы разберем две самые частые причины аннулирования патента на работу. Обе, как ни странно, связаны с оплатой авансового платежа за патент.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Напомним, что патент на работу предоставляет право безвизовому приезжему законно работать в РФ сроком до одного года. Кроме того, документ можно продлить еще на один год безвыездно, но только один раз.&nbsp;<strong>Итак, перейдем к причинам аннулирования</strong>. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>1) Оплата налога по чужому или несуществующему ИНН</strong>. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">ИНН - обязательный документ для оплаты трудового патента. Он представляет собой документ размером А 4, с индивидуальным номером. Выдается такой документ всего 1 раз в жизни. ИНН иностранному работнику нужен для того, чтобы его работодатель смог по нему делать отчисления в налоговую. Если ваш работодатель при приеме на работу просит предоставить ИНН, будьте уверены, он оформляет вас официально.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но ИНН требуется иностранцу не только для официального трудоустройства, но и для оплаты налога за патент. Ведь без ИНН сделать это невозможно.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но к сожалению, многие иностранцы пренебрегают его оформлением и указывают при оплате авансового платежа чужой или несуществующий номер. В этом случае трудовой патент будет аннулирован и дальнейшие платежи по нему не имеют смысла, так как оплата просто не дойдет до налоговой или будет зачислена на другого человека.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если вы оформляете патент через нашу компанию, то Вы сможете оформить ИНН даже после получения патента. Ведь первый авансовый платеж за Вас вносим мы! (Подробности по телефону у менеджеров)&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>2) Просрочка платежа.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Действует трудовой патент тот период, за который был внесен за него ежемесячный налог. При внесении платежа иностранцу необходимо ориентироваться на дату, указанную на обратной стороне вашего документа (см. фото ниже)!&nbsp;</span></p>\n<p><img title=\"трудовой патент, дата выдачи \" src=\"http://all-migration.ru/upload/medialibrary/540/540dde14b6ad87131ff9a954c2c53855.jpg\" alt=\"трудовой патент, дата выдачи \" width=\"300\" height=\"209\" border=\"0\" /></p>\n<p><span style=\"font-family: Arial;\">Дата окончания патента на работу считается на сутки раньше, чем в нем указано! То есть если Вы оплатите авансовый платеж в день, указанный на патенте или на следующий день, то аннулирование неизбежно!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Настоятельно рекомендуем оплачивать налог за 3-4 дня до указанной даты в патенте. Не забывайте, что очень важно также внести и верную сумму, в 2018 году она составляет &ndash;&nbsp;<strong>3 500 рублей</strong>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что делать если уже совершил одну из вышеперечисленных ошибок? &nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">В этом случае выход один &ndash; оформлять новый патент.&nbsp;</span><span><strong>Для этого необходимо:&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">&bull; Выехать на границу&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Въехать в РФ снова&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; На границе заполнить новую миграционную карту&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; По приезде подать документы на новый патент.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35252/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(250, 0, 1535713407, NULL, NULL, '<p><span style=\"font-family: Arial;\">В Санкт-Петербурге даже после ЧМ 2018 по футболу регулярно проходят рейды на выявление нелегалов. Об этом вы и сами регулярно читаете в газетах, журналах и интернете.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Сегодня мы расскажем, чем завершился один из подобных рейдов для работодателя.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Еще в прошлом году сотрудники МВД пришли с проверкой в компанию ООО \"РЦТ\", которая осуществляет строительную деятельность и располагается в Приморском районе Санкт-Петербурга.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В результате данной проверки был задержан гражданин Узбекистана, который выполнял функции маляра на территории данной компании.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Оказалось, что у данного сотрудника не было&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><strong><span style=\"color: #007236;\">патента на работу</span></strong></a>, что запрещено п. 4.ст.13 ФЗ-115 &laquo;О правовом положении иностранцев в РФ&raquo;.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Дело было передано в суд.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Среди материалов дела были предоставлены: копия договора подряда и список сотрудников, допущенных на строительный объект ООО &laquo;РТЦ&raquo; (в их числе был нелегальный иностранец).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Это говорит о том, что работодателем данного иностранца являлся не ООО &laquo;РТЦ&raquo;, а его подрядчик, который не позаботился о легализации своего сотрудника.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">А согласно примечанию&nbsp;<a href=\"http://all-migration.ru/services/addservices/docs/koap18-15/\"><span style=\"color: #007236;\"><strong>ст.18.15 КоАП</strong></span></a>&nbsp;любое использование труда иностранцев считается привлечением к трудовой деятельности.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">И так как ответчик не перестраховался и не проверил документы иностранца, прежде чем допустить мигранта к работе, он был признан виновным в совершении правонарушения.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ответчик в свою очередь, свою вину не опровергал.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Исходя из этого, суд постановил ООО &laquo;РТЦ&raquo; признать виновным в совершении административного правонарушения по&nbsp;<strong>ч. 4 ст.18.15 КоАП</strong>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В результате компании выписали штраф за допуск к работе иностранного гражданина без патента. А это штраф от 200 тыс. руб. до 1 млн. руб. в зависимости от региона.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данной статьей хотим обратить ваше внимание, насколько важно, чтобы на вашей территории к работе были допущены иностранцы только с действующими патентами!&nbsp;</span></p>\n<p class=\"order_service\"><span style=\"font-family: Arial;\">А закончиться они могут&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1)или по сроку действия, &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2)или из-за неправильно оплаченного чека.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чтобы не попасть в суд, нужно как можно быстрее&nbsp;<strong>оформить новый патент иностранцу</strong>!</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35591/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(251, 0, 1538395345, NULL, NULL, '<p>Фонд поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; провел выездную правовую приемную на территории мечети на &laquo;Сенной&raquo;, расположенной по адресу Московский проспект дом 10-12. Приемная проводилась в течение двух часов, с 14:00 по 16:00. Любой желающий мог подойти и задать интересующий его вопрос. Юристами и помощниками юристов Фонда &laquo;ОРИЕНТИР&raquo; было проконсультировано более 100 человек. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда &laquo;ОРИЕНТИР&raquo;, как избежать нарушения законодательства.</p>\n<div id=\"cp_widget_d71a01a1-4e30-4f27-87c2-ea7449fb00dd\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_d71a01a1-4e30-4f27-87c2-ea7449fb00dd\"; cpo[\"_fid\"] = \"A4GAKWe_OhtU\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>', NULL, NULL, NULL, NULL, NULL, 1),
(252, 0, 1538387860, NULL, NULL, '<p><span>21 сентября Фондом поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; была проведена вторая выездная правовая приемная на территории мечети на &laquo;Сенной&raquo;, расположенной по адресу Московский проспект дом 10-12. Приемная проводилась&nbsp;</span><br /><span>в течение двух часов, с 14:00 по 16:00. Любой желающий мог получить консультацию юриста по интересующим его вопросам. По итогам приемной правовую поддержку получили 65 человек. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда &laquo;ОРИЕНТИР&raquo;.&nbsp;</span></p>\n<div id=\"cp_widget_ec30ca01-fd3c-4db7-b67a-e92642f6c71b\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_ec30ca01-fd3c-4db7-b67a-e92642f6c71b\"; cpo[\"_fid\"] = \"AcCAWWeI1d5s\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>', NULL, NULL, NULL, NULL, NULL, 1),
(253, 0, 1538395339, NULL, NULL, '<p><span>28 сентября была проведена третья выездная правовая приемная на территории мечети на &laquo;Сенной&raquo;, расположенной по адресу Московский проспект дом 10-12. В</span><span>&nbsp;течение двух часов любой желающий мог обратиться за консультацией юриста по интересующим его вопросам. По итогам приемной правовую поддержку получили 56 человек. Также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда &laquo;ОРИЕНТИР&raquo;.</span></p>\n<p><span>Кроме того, в Комитете по межнациональным отношениям и реализации миграционной политики была проведена встреча формата вопрос-ответ с 17 гражданами Узбекистана по трудовому праву.</span></p>\n<div id=\"cp_widget_5499c7ca-ddec-4c38-918f-e8fbedf6e783\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_5499c7ca-ddec-4c38-918f-e8fbedf6e783\"; cpo[\"_fid\"] = \"AIGAhX-61tY1\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2018/10/1&lt;/span&gt;&lt;span&gt;cameramake&lt;/span&gt;&lt;span&gt; HUAWEI&lt;/span&gt;&lt;span&gt;focallength&lt;/span&gt;&lt;span&gt; 3.46&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 2448&lt;/span&gt;&lt;span&gt;fnumber&lt;/span&gt;&lt;span&gt; 4&lt;/span&gt;&lt;span&gt;exposuretime&lt;/span&gt;&lt;span&gt; 0.001405&lt;/span&gt;&lt;span&gt;camerasoftware&lt;/span&gt;&lt;span&gt; FIG-LX1 8.0.0.130(C1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 9/28/2018 6:40:12 PM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 3264&lt;/span&gt;&lt;span&gt;cameramodel&lt;/span&gt;&lt;span&gt; FIG-LX1&lt;/span&gt;&lt;span&gt;cameramake&lt;/span&gt;&lt;span&gt; HUAWEI&lt;/span&gt;&lt;span&gt;focallength&lt;/span&gt;&lt;span&gt; 3.46&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 3264&lt;/span&gt;&lt;span&gt;fnumber&lt;/span&gt;&lt;span&gt; 4.5&lt;/span&gt;&lt;span&gt;exposuretime&lt;/span&gt;&lt;span&gt; 0.01&lt;/span&gt;&lt;span&gt;camerasoftware&lt;/span&gt;&lt;span&gt; FIG-LX1 8.0.0.130(C1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 9/28/2018 7:01:33 PM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 2448&lt;/span&gt;&lt;span&gt;cameramodel&lt;/span&gt;&lt;span&gt; FIG-LX1&lt;/span&gt;&lt;span&gt;cameramake&lt;/span&gt;&lt;span&gt; HUAWEI&lt;/span&gt;&lt;span&gt;focallength&lt;/span&gt;&lt;span&gt; 3.46&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 2448&lt;/span&gt;&lt;span&gt;fnumber&lt;/span&gt;&lt;span&gt; 4.5&lt;/span&gt;&lt;span&gt;exposuretime&lt;/span&gt;&lt;span&gt; 0.00281&lt;/span&gt;&lt;span&gt;camerasoftware&lt;/span&gt;&lt;span&gt; FIG-LX1 8.0.0.130(C1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 9/28/2018 6:59:29 PM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 3264&lt;/span&gt;&lt;span&gt;cameramodel&lt;/span&gt;&lt;span&gt; FIG-LX1&lt;/span&gt;&lt;span&gt;cameramake&lt;/span&gt;&lt;span&gt; HUAWEI&lt;/span&gt;&lt;span&gt;focallength&lt;/span&gt;&lt;span&gt; 3.46&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 2448&lt;/span&gt;&lt;span&gt;fnumber&lt;/span&gt;&lt;span&gt; 4&lt;/span&gt;&lt;span&gt;exposuretime&lt;/span&gt;&lt;span&gt; 0.03&lt;/span&gt;&lt;span&gt;camerasoftware&lt;/span&gt;&lt;span&gt; FIG-LX1 8.0.0.130(C1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 9/28/2018 5:58:54 PM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 3264&lt;/span&gt;&lt;span&gt;cameramodel&lt;/span&gt;&lt;span&gt; FIG-LX1&lt;/span&gt;&lt;span&gt;cameramake&lt;/span&gt;&lt;span&gt; HUAWEI&lt;/span&gt;&lt;span&gt;focallength&lt;/span&gt;&lt;span&gt; 3.46&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 2448&lt;/span&gt;&lt;span&gt;fnumber&lt;/span&gt;&lt;span&gt; 4&lt;/span&gt;&lt;span&gt;exposuretime&lt;/span&gt;&lt;span&gt; 0.03&lt;/span&gt;&lt;span&gt;camerasoftware&lt;/span&gt;&lt;span&gt; FIG-LX1 8.0.0.130(C1&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 9/28/2018 6:18:54 PM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 3264&lt;/span&gt;&lt;span&gt;cameramodel&lt;/span&gt;&lt;span&gt; FIG-LX1&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(254, 0, 1538398052, NULL, NULL, '<p>В Ленинградской области с 19 по 21 сентября прошел VIII&nbsp;Балтийский форум соотечественников, ставший уже традиционным.</p>\n<p>В своем приветственном слове вице-губернатор Ленинградской области по внутренней политике Сергей Пименов отметил важность и приоритетность работы правительства региона во взаимодействии с соотечественниками, проживающими за пределами Российской Федерации.</p>\n<p>В рамках Форума соотечественников проходили дискуссии на темы, которые затрагивали острые и актуальные вопросы дискриминации в сфере образования, информационные войны, русскоязычные СМИ, выходящие за рубежом, правозащитная деятельность и механизмы защиты соотечественников.</p>\n<p>Так же стороной не обошли вопросы международной политики в регионе Балтийского моря, кризисы, с которыми приходится сталкиваться, а также вызовы, которые предстают перед обществом. Участники форма обсудили и вопросы, которые помогут найти пути решения в сложившихся экономико-политических реалиях, возможности международного, культурного, информационного, правового сотрудничества, а также в рамках поддержки молодежной политики.&nbsp;</p>\n<p>Не прошли стороной и такую тему как добровольное переселение соотечественников. На сегодняшний день открыт 66 регион. Только в Ленобласть за последние 3 года переселилось свыше 6 тысяч человек. В тоже время процент отказов немалый, поскольку отбор проходит по определенным критериям &ndash; одними из которых являются образование и востребованность на рынке труда. Как отметил Дмитрий Никифоров, заместитель генерального директора Агентства трудовых ресурсов, проводится индивидуальная работа с каждым соотечественником-переселенцем.&nbsp;</p><div id=\"cp_widget_a95ff181-b065-4aeb-90f6-5399483ffd0c\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_a95ff181-b065-4aeb-90f6-5399483ffd0c\"; cpo[\"_fid\"] = \"AQHA7WOe2RJQ\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>New Gallery 2018/10/1</span><span>cameramake</span><span> HUAWEI</span><span>focallength</span><span> 3.46</span><span>height</span><span> 2448</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.03</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/20/2018 4:33:57 PM</span><span>width</span><span> 3264</span><span>cameramodel</span><span> FIG-LX1</span><span>cameramake</span><span> HUAWEI</span><span>focallength</span><span> 3.46</span><span>height</span><span> 1632</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.03</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/20/2018 4:35:33 PM</span><span>width</span><span> 3264</span><span>cameramodel</span><span> FIG-LX1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 1632</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.03</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/20/2018 5:43:09 PM</span><span>width</span><span> 3264</span><span>cameramodel</span><span> FIG-LX1</span></noscript>\n<p><strong>По словам Эдгарса Седлиньша, президента Фонда <strong>поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, <em>в ходе совместной реализации Государственной Программы по добровольному переселению соотечественников в Ленинградскую область, наш Фонд столкнулся с рядом вопросов, касающихся работодателей. На сегодняшний день фактически вся нагрузка по трудоустройству и обустройству переселенцев ложится на плечи работодателей. Отсутствие на законодательном уровне положений по мотивации работодателей существенно тормозит ход реализации Госпрограммы.</em> </strong></strong></p>\n<p><strong><em>26.03.2012г. в Государственную Думу Фондом &laquo;ОРИЕНТИР&raquo; было направлено официальное обращение к депутатам, где была приведена реальная статистика за 2007-2011гг. по реализации Госпрограммы и её участникам из трех прибалтийских республик, а также представлено &laquo;ПРЕДЛОЖЕНИЯ О ПРЕДОСТАВЛЕНИИ НАЛОГОВЫХ ЛЬГОТ ПЕРЕСЕЛЕНЦАМ И РАБОТОДАТЕЛЯМ, ПРЕДОСТАВЛЯЮЩИМ ИМ РАБОЧИЕ МЕСТА&raquo;, которое осталось в ГД без должного внимания. Вместе с тем, в начале 2013 года от депутата Государственной Думы шестого созыва Дмитриевой Оксаны Генриховны прозвучало, что примерно через пять лет можно будет вернуться к рассмотрению данного предложения&hellip;</em></strong></p>\n<p><strong>&nbsp;</strong>На форуме собрались журналисты, общественные деятели, представители экспертных обществ из России, стран Прибалтики, Финляндии, Норвегии, Германии, Белоруссии &ndash; в общей сложности более 100 экспертов.</p>\n<div id=\"cp_widget_0880daf5-82e3-4235-8a3d-f7612c1bef89\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_0880daf5-82e3-4235-8a3d-f7612c1bef89\"; cpo[\"_fid\"] = \"AsOALVuW11-5\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;New Gallery 2018/10/1&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;bitrate&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 2048 kb/s&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;height&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 720&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;duration&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 00:01:55.12&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;fps&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 25&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt;width&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;&amp;amp;amp;amp;lt;span&amp;amp;amp;amp;gt; 1280&amp;amp;amp;amp;lt;/span&amp;amp;amp;amp;gt;</noscript>\n<p>Как отметил Игорь Павловский, заместитель главного редактора ИА Regnum, с появлением социальных платформ, как генераторы новостей, мы потеряли смысл, потому что наше преимущество в эксклюзивности и скорости доставки нивелируется социальными сетями и теми информационными платформами, которые предлагаются. Мне нет смысла лезть на любимый сайт, если, например, Яндекс-новости мне дает целиком картинку дня.</p>\n<p><strong>&nbsp;</strong><em><span style=\"text-decoration: underline;\">Для справки:</span></em></p>\n<p><strong><em>Фонд &laquo;ОРИЕНТИР&raquo; является первой и на сегодняшний день единственной некоммерческой организацией, как в России, так и за рубежом, которая оказывает прикладную помощь добровольному переселению соотечественников как в регионы, охваченные Государственной Программой по добровольному переселению соотечественников в РФ, так и не входящие в перечень территорий на которых реализуется Госпрограмма. </em></strong></p>\n<p><strong><em>Концептуальным принципом деятельности Фонда &laquo;ОРИЕНТИР&raquo; является переселение соотечественников в регионы с дефицитом трудовых ресурсов по заявкам работодателей на конкретные рабочие места с обеспечением полного набора социальных гарантий.</em></strong></p>\n<div id=\"cp_widget_3de8d85a-a185-4c8b-8ee5-438d0efa133a\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_3de8d85a-a185-4c8b-8ee5-438d0efa133a\"; cpo[\"_fid\"] = \"AwGA_Uun11z6\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })();\n// ]]></script>\n<noscript>&amp;lt;span&amp;gt;New Gallery 2018/10/1&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;focallength&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 3.46&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;flash&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 24&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;cameramake&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; HUAWEI&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;height&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 2557&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;fnumber&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 2.2&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;exposuretime&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 0.02&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;orientation&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;camerasoftware&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; Microsoft Windows Ph&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;originaldate&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 9/29/2018 3:31:45 AM&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;width&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; 1604&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt;cameramodel&amp;lt;/span&amp;gt;&amp;lt;span&amp;gt; FIG-LX1&amp;lt;/span&amp;gt;</noscript>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(255, 0, 1538392376, NULL, NULL, '<p>28 сентября была организована раздача листовок, информирующих об услугах и режиме работы центра правовой поддержки и образования трудовых мигрантов. Любой желающий может получить бесплатную консультацию юриста Фонда &laquo;Ориентир&raquo; и получить всю необходимую информацию для решения возникшей проблемы.</p>\n<p>&nbsp;</p><div id=\"cp_widget_e8d505db-2006-4317-ac60-2cc8d939d904\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_e8d505db-2006-4317-ac60-2cc8d939d904\"; cpo[\"_fid\"] = \"AkNAkUe01Ff9\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>New Gallery 2018/10/1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 2080</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.005265</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/14/2018 6:41:42 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span><span>focallength</span><span> 3.46</span><span>flash</span><span> 24</span><span>cameramake</span><span> HUAWEI</span><span>height</span><span> 2080</span><span>fnumber</span><span> 2.2</span><span>exposuretime</span><span> 0.004583</span><span>camerasoftware</span><span> FIG-LX1 8.0.0.130(C1</span><span>originaldate</span><span> 9/14/2018 6:41:26 PM</span><span>width</span><span> 4160</span><span>cameramodel</span><span> FIG-LX1</span></noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(256, 0, 1540987330, NULL, NULL, '<p><span style=\"font-family: Arial;\">Всем иностранцам, прибывшим в Россию, со дня въезда и на протяжении всего срока нахождения в стране нужен&nbsp;<strong>полис Добровольного Медицинского страхования</strong>&nbsp;(п.5 ст.27 ФЗ №114).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Для чего он необходим?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">1. Данный документ предоставляет иностранцу право на&nbsp;<strong>бесплатную экстренную помощь.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">2. Помимо этого, полис ДМС входит в перечень документов, подтверждающий&nbsp;<strong>законное пребывание иностранного гражданина в России</strong>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. <strong>Без него невозможно оформить</strong>&nbsp;основные миграционные документы, такие как:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull;&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">Трудовой патент</a>,</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;&bull; Трудовой договор,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;&bull;&nbsp;<a href=\"http://all-migration.ru/news/35959/services/addservices/razreshenie-na-vremennoe-progivanie/\">РВП</a>,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;&bull;&nbsp;<a href=\"http://all-migration.ru/services/addservices/vid-na-zhitelstvo-v-rf/\">ВНЖ</a>,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull;&nbsp;<a href=\"http://all-migration.ru/services/addservices/Grazhdanstvo/\">Гражданство РФ</a>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Таким образом, без полиса ДМС&nbsp;<strong>Вы не сможете&nbsp;&nbsp;</strong><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">легально трудоустроить</a>&nbsp;<strong>иностранного гражданина.&nbsp;</strong></span></p>\n<p class=\"order_service\"><br /><br /></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО!</strong>&nbsp;Срок заключения полиса ДМС для оформления вышеизложенных миграционных документов &ndash; 1 год, а минимальный объем страхового возмещения &ndash; 100 000 рублей.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что делать, если полис Вашего сотрудника пришел в негодность?</strong>&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если полис ДМС пришел в негодность, потерян или просрочен, его необходимо восстановить. Для этого нужны следующие документы:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Паспорт иностранного гражданина,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull;&nbsp;<a href=\"http://all-migration.ru/services/addservices/migkarta/\">Миграционная карта</a>,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull;&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">Регистрация</a>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">После восстановления полиса ДМС работодатель может оформить трудовой договор с иностранцем в соответствии с законом Российской Федерации.&nbsp;&nbsp;</span></p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35959/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(257, 0, 1540987463, NULL, NULL, '<p><span style=\"font-family: Arial;\">Все предприниматели стараются просчитать каждый свой шаг, чтобы не совершить ошибок, которые каким-то образом могут отразиться на их бизнесе. Любая ошибка, допущенная в отношении иностранного персонала, может привести бизнесмена к потере крупной суммы денег, вплоть до 1 млн. руб.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Именно поэтому мы хотим напомнить вам о том, что у ваших сотрудников может стоять запрет на въезд в Россию.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Наверное, вы думаете, что это не так страшно, ведь иностранец находится в стране и спокойно работает. Тем не менее, если ваш сотрудник покинет страну, то вернуться обратно он уже не сможет, если у него запрет на въезд.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чаще всего, мигранты даже не подозревают о том, что у них могут возникнуть проблемы, при въезде в страну после отпуска или поездки домой. И для иностранца, и для вас это может стать неожиданностью, которая произойдет в самый неподходящий момент.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как же обезопасить себя и определить, есть ли у сотрудника запрет на въезд?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">На самом деле, чтобы проверить запрет на въезд иностранному сотруднику, для начала нужно просто поговорить с иностранцем и выяснить, не совершал ли он каких-либо правонарушений, а также проверить его документы на предмет легальности.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Но, к сожалению, иногда бывают случаи, когда госорганы запрещают иностранцу пересекать границу РФ по ошибке.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В этом случае, вы должны помочь своему сотруднику вернуться к своим родным, друзьям и коллегам в Россию.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Обжаловать запрет на въезд можно с помощью&nbsp;<a href=\"http://all-migration.ru/services/addservices/Advokat/\">миграционного адвоката в суде</a>. Сразу скажем, что данные вопросы редко решаются быстро. Тем не менее, это возможно.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО</strong>: Подойдите осознанно к выбору адвоката!</span></p>\n<p><span style=\"font-family: Arial;\">Он должен специализироваться именно на миграционном</span><span>&nbsp;законодательстве.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35970/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(258, 0, 1540987592, NULL, NULL, '<p><span style=\"font-family: Arial;\">Нам поступает множество вопросов относительно устройства на работу выходцев из стран ЕАЭС (Белоруссия, Армения, Казахстан, Киргизия). Поэтому мы решили коротко изложить основные моменты оформления в штат такого сотрудника. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; С будущим сотрудником необходимо заключить трудовой договор &ndash; документ, подтверждающий трудовые отношения. В нем указывается данные паспорта,&nbsp;<a href=\"http://all-migration.ru/services/addservices/migkarta/\">миграционной карты</a>&nbsp;(кроме белорусов), полиса ОМС/<a href=\"http://all-migration.ru/services/addservices/dmc/\">ДМС</a>&nbsp;работника.&nbsp;<a href=\"http://all-migration.ru/services/addservices/inn/\">ИНН</a>&nbsp;и&nbsp;<a href=\"http://all-migration.ru/services/addservices/snils/\">СНИЛС</a>&nbsp;при их отсутствии оформляются после заключения трудового договора. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Далее необходимо отправить&nbsp;&nbsp;<a href=\"http://all-migration.ru/services/addservices/notification/\">уведомление</a>&nbsp;в ГУ МВД о подписании трудового договора с иностранцем.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">ВАЖНО: уведомление подается в 3-х дневный срок после заключения трудового договора!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&bull; Также у такого сотрудника должна быть действующая регистрация. О том, как проверить действительность регистрации, вы можете узнать&nbsp;<a href=\"http://all-migration.ru/news/35929/\">здесь</a>. На&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">миграционный учет</a>&nbsp;ему нужно вставать в течение 30 суток (календарных дней) с даты въезда в РФ.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/35983/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(259, 0, 1541582682, NULL, NULL, '<p><span>12 октября была проведена выездная правовая приемная на территории Соборной Мечети, расположенной по адресу Кронверкский проспект дом 7. В</span><span>&nbsp;течение двух часов любой желающий мог обратиться за консультацией юриста по интересующим его вопросам. По итогам приемной правовую поддержку получили 37 человек. Также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда &laquo;ОРИЕНТИР&raquo;.</span></p>\n<p>На текущий момент Фонд &laquo;ОРИЕНТИР&raquo; реализовывает социально-значимый проект&nbsp;\"Цивилизованная миграция\". Правовое просвещение и оказание юридической помощи трудовым мигрантам и мигрантам- соотечественникам Санкт-Петербурга и Ленинградской области, в рамках договора с Фондом-оператором президентских грантов.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<div id=\"cp_widget_654680ca-f435-4ff6-a84c-df02b76cf87f\">...</div>\n<script type=\"text/javascript\">// <![CDATA[\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_654680ca-f435-4ff6-a84c-df02b76cf87f\"; cpo[\"_fid\"] = \"AsFA1UuVj_uh\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); \n// ]]></script>\n<noscript>&lt;span&gt;New Gallery 2018/11/7&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 960&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 480&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 960&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 720&lt;/span&gt;&lt;span&gt;originaldate&lt;/span&gt;&lt;span&gt; 1/1/0001 6:00:00 AM&lt;/span&gt;&lt;span&gt;width&lt;/span&gt;&lt;span&gt; 960&lt;/span&gt;&lt;span&gt;height&lt;/span&gt;&lt;span&gt; 480&lt;/span&gt;</noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(260, 0, 1541583116, NULL, NULL, '<p><span style=\"font-family: Arial;\">Наличие в штате иностранных сотрудников налагает на работодателя немало обязательств, в том числе по миграционному учету таких работников. Ведь отсутствие любого документа может привести к административной ответственности и депортации мигранта. Один из самых часто задаваемых вопросов касается&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">временной регистрации&nbsp;</a>иностранного работника: должен ли работодатель следить за сроками, по необходимости продлевать ее, подавать документы за иностранца в госорганы? &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Главное правило: оформлять регистрацию и снимать иностранца с учета обязана принимающая сторона!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Рассмотрим 2 возможных варианта регистрации работника. Первый &ndash; работник зарегистрирован по адресу работодателя. В соответствии с законом N 163-ФЗ, такая регистрация возможна только в случае, если гражданин фактически там проживает (спит, отдыхает). К тому же юридическое лицо должно соблюдать санитарные, технические нормы содержание такого помещения. Так как работодатель предоставляет место проживания, то на него ложатся обязанности по регистрации работников, продлению и снятию с миграционного учета.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если же работник только представляет регистрацию при заключении трудового договора, но оформил он ее сам &ndash; например, снимает квартиру или живет в общежитии &ndash; работодатель не подает документы на ее продление. Ранее мы рассказывали о том, как можно определить, что регистрация является&nbsp;<a href=\"http://all-migration.ru/news/35929/\">поддельной</a>. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Однако во втором случае Вам не стоит терять бдительность! Сроки окончания любых миграционных документов всегда должны быть в компании на контроле. Так, если у работника действующая регистрация до 1 ноября, работодатель должен иметь в виду, что 1 ноября иностранный гражданин должен принести новый документ.</span></p>\n<p><span style=\"font-family: Arial;\">Что делать в случае, если мигрант не принес подтверждающий документ? &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В случае истечения срока действия регистрации работодатель должен отстранить сотрудника от работы на срок 30 календарных дней. В этот срок иностранец должен предоставить новую регистрацию, после чего заключается дополнительное соглашение к трудовому договору. Если же он так и не принесет документы по миграционному учету &ndash; необходимо уволить такого работника.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36014/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(261, 0, 1541583633, NULL, NULL, '<p><span>19 октября была проведена третья выездная правовая приемная на территории мечети на &laquo;Сенной&raquo;, расположенной по адресу Московский проспект дом 10-12. В</span><span>&nbsp;течение двух часов любой желающий мог обратиться за консультацией юриста по интересующим его вопросам. По итогам приемной правовую поддержку получили 35 человек. Также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов Фонда &laquo;ОРИЕНТИР&raquo;</span></p>\n<p>&nbsp;На текущий момент Фонд &laquo;ОРИЕНТИР&raquo; реализовывает социально-значимый проект&nbsp;\"Цивилизованная миграция\". Правовое просвещение и оказание юридической помощи трудовым мигрантам и мигрантам- соотечественникам Санкт-Петербурга и Ленинградской области, в рамках договора с Фондом-оператором президентских грантов.</p>\n<p>&nbsp;</p>\n<div id=\"cp_widget_15273be0-5ccb-448e-a934-951341817b24\">...</div><script type=\"text/javascript\">\nvar cpo = []; cpo[\"_object\"] =\"cp_widget_15273be0-5ccb-448e-a934-951341817b24\"; cpo[\"_fid\"] = \"AwDARVeKj7Jj\";\nvar _cpmp = _cpmp || []; _cpmp.push(cpo);\n(function() { var cp = document.createElement(\"script\"); cp.type = \"text/javascript\";\ncp.async = true; cp.src = \"//www.cincopa.com/media-platform/runtime/libasync.js\";\nvar c = document.getElementsByTagName(\"script\")[0];\nc.parentNode.insertBefore(cp, c); })(); </script><noscript><span>New Gallery 2018/11/7</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 960</span><span>height</span><span> 720</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 960</span><span>height</span><span> 720</span><span>originaldate</span><span> 1/1/0001 6:00:00 AM</span><span>width</span><span> 960</span><span>height</span><span> 720</span></noscript>', NULL, NULL, NULL, NULL, NULL, 1),
(262, 0, 1541583807, NULL, NULL, '<p><span style=\"font-family: Arial;\">Несмотря на то, что основная часть трудовых мигрантов в нашей стране- это безвизовые иностранцы, иногда работодатели оформляют в компании граждан из визовых стран.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Сегодня мы постараемся расставить все на свои места и разъясним, как вам оформить визового мигранта в компанию.&nbsp;</span></p>\n<h3><span style=\"font-family: Arial;\">Процесс оформления данной категории иностранцев достаточно длинный и сложный.&nbsp;</span></h3>\n<p><span style=\"font-family: Arial;\"><strong>1 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Изначально, работодатель должен&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/akkreditaciya-ur-lica-fms-emc/\"><span style=\"color: #005824; font-family: Arial;\"><strong>получить учетную карту в МВД</strong></span></a><span style=\"font-family: Arial;\">, т.е. пройти аккредитацию как работодателя иностранных работников.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Сложность этого этапа лишь в том, что после передачи данных в МВД, в компанию приходят сотрудники госорганов с проверкой.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Они проверяют факт наличия организации по юридическому адресу, который указан в документах.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>2 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Далее вы должны разместить объявление о том, что вы находитесь в поиске сотрудника в Центр Занятости.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО:</strong>&nbsp;Данная вакансия должна быть размещена минимум 30 дней. А условия работы должны в точности совпадать с теми, которые вы предоставите иностранному сотруднику. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>3 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Получить квоту для компании. Данный этап является обязательным, если профессия, на которую вы ищете сотрудника квотируемая.&nbsp; Если вы хотите убедиться, что профессия необходимая Вам профессия неквотируемая, позвоните нашему юристу по номеру&nbsp;</span><a href=\"tel:+78122428829\"><strong>8(812)242-88-29</strong></a><span style=\"font-family: Arial;\">, наши юристы Вам ответят.</span></p>\n<p><span style=\"font-family: Arial;\"><strong>4 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Получить разрешение на привлечение иностранцев к работе&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данный документ дает право компании привлекать к работе визового иностранца на срок не более года.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>5 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Заняться&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/priglashenie/\"><span style=\"color: #005824; font-family: Arial;\"><strong>оформлением приглашения в Россию</strong></span></a><span style=\"font-family: Arial;\">&nbsp;и&nbsp;</span><a href=\"http://all-migration.ru/services/workpermit/\"><span style=\"color: #005824; font-family: Arial;\"><strong>разрешения на работу</strong></span></a><span style=\"font-family: Arial;\">. Готовое приглашение вы отправляете потенциальному иностранному сотруднику на родину.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Он, в свою очередь, отправляется в своей стране в российское консульство и получает визу для въезда в Россию.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>6 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">После того, как иностранец приехал в РФ, он должен&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"color: #005824; font-family: Arial;\"><strong>оформить себе регистрацию</strong></span></a><span style=\"font-family: Arial;\">&nbsp;за 7 рабочих дней.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>7 этап.&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Далее ваш сотрудник должен пройти медкомиссию и получить разрешение на работу в МВД.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Теперь у вас есть все основания для заключения трудового договора с иностранцем. Но не забывайте, что вам еще нужно подать в МВД уведомление о трудоустройстве за 3 рабочих дня.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Уже после трудоустройства иностранец обязан:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. Продлить себе визу на основании полученного разрешения на работу&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. Продлить миграционный учет&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. Сдать тест по русскому языку и предоставить сертификат в МВД&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если данные пункты не будут выполнены, а в особенности третий, то считайте, что вы зря потратили время на оформление данного иностранца в штат. Его разрешение на работу будет аннулировано, а вы будете вынуждены сначала его отстранить, а далее уволить.</span></p>\n<p>&nbsp;</p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36043/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(263, 0, 1541586934, NULL, NULL, '<p><span style=\"font-family: Arial;\">Набор документов для иностранного работника постоянно меняется, и работодателям сложно уследить за всеми нововведениями.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">К нам часто обращаются с вопросом, какой комплект документов всегда должен быть оформлен у иностранного работника, поэтому мы решили составить памятку по всем необходимым разрешениями, сертификатам и т.д. для безвизовых трудовых мигрантов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">У иностранца в наличии обязательно должны быть следующие документы:&nbsp;<br /></span></p>\n<ul>\n<li><span style=\"font-family: Arial;\">Паспорт: удостоверение личность иностранного гражданина в РФ;</span></li>\n<li><span style=\"font-family: Arial;\">Миграционная карта;</span></li>\n<li><span style=\"font-family: Arial;\"><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><strong><span style=\"color: #005824;\">Патент (действующий)</span></strong></a>;&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\"><a href=\"http://all-migration.ru/services/addservices/dmc/\"><strong><span style=\"color: #005824;\">Полис ДМС</span></strong></a>&nbsp;(действующий),&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\">Все вовремя оплаченные чеки за действующий патент;&nbsp;&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\"><a href=\"http://all-migration.ru/services/addservices/snils/\"><strong><span style=\"color: #005824;\">СНИЛС</span></strong></a>;&nbsp;&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\"><a href=\"http://all-migration.ru/services/addservices/inn/\"><strong><span style=\"color: #005824;\">ИНН</span></strong></a>;&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\">Отрывная часть&nbsp;<a href=\"http://all-migration.ru/services/addservices/notification/\"><strong><span style=\"color: #005824;\">уведомления о заключении трудового договора</span></strong></a>, сам трудовой договор;&nbsp;</span></li>\n<li><span style=\"font-family: Arial;\">Временная регистрация (действующая).</span></li>\n</ul>\n<p><span style=\"font-family: Arial;\"><strong>ВАЖНО</strong>: регистрация на юридическое лицо возможна только при фактическом проживании сотрудника в помещении этого юридического лица.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36052/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(264, 0, 1541589341, NULL, NULL, '<p><span style=\"font-family: Arial;\">Ранее мы уведомляли вас о том, что с 1 октября обязательно указание ИНН при&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>оформлении патента</strong></span></a><span style=\"font-family: Arial;\">.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Таким образом, оформить его следует до или во время получения патента. Важность получения данного документа состоит в том, что по нему проводится идентификация оплаченных чеков за патент. А как известно, без своевременно оплаченных чеков патент недействителен. Поэтому рекомендуется заранее оплатить патент на максимально возможный срок (патент действует сроком на год). &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Важно: без&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/inn/\"><strong><span style=\"color: #005824;\">ИНН</span></strong></a><span style=\"font-family: Arial;\">&nbsp;иностранный работник не может оплатить налог за свой патент на работу!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чтобы не возникло проблем с оплатой и продлением патента, нужно очень внимательно проверять реквизиты платежного документа: ФИО плательщика, назначение платежа (конкретный период &ndash; месяц и т.д., за который оплачивается авансовый платеж), реквизиты налоговой инспекции.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ведь в случае неверно указанных данных платеж не будет засчитан, а значит, патент не продлен и будет признан недействительным, по нему больше нельзя будет работать. &nbsp;</span></p>\n<h3><span style=\"font-family: Arial;\">Что делать, если ошибка все же допущена при оплате патента? &nbsp;</span></h3>\n<p><span style=\"font-family: Arial;\">Необходимо в кратчайшие сроки написать в налоговый орган заявление о произошедшей ошибки, с приложением подтверждающих документов, в том числе все оплаченные ранее чеки по патенту.&nbsp;</span></p>\n<p>&nbsp;</p>\n\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36089/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(265, 0, 1544000610, NULL, NULL, '<p><span style=\"font-family: Arial;\">Нам&nbsp;задают много вопросов относительно&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">миграционного учета</a>&nbsp;для патентных работников, о соотношении регистрации (учета) и&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патента</a>&nbsp;для работы. Важно знать, что патент является строго территориальным документом, он ограничен границами субъекта федерации, где был выдан. В связи с этим мы разберем важные вопросы действия этих документов: всегда ли обязательно, чтобы регион их выдачи совпадал?</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;С июля 2018 года, иностранец может зарегистрироваться только по&nbsp;<a href=\"http://all-migration.ru/news/35670/\">фактическому месту пребывания</a>, а патент оформляется в том регионе, где он намеревается работать. Поэтому здесь все зависит от территориальной близости таких регионов. Например, если гражданин проживает в Ленинградской области, он может получить патент в Санкт-Петербурге. Т.е. в этом случае регистрация и патент могут быть оформлены в разных регионах. Если же он оформил регистрацию в Мурманской или Псковской области (что также является Северо-Западом), оформить патент в Северной столице не получится.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36123/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(266, 0, 1544000705, NULL, NULL, '<p><span style=\"font-family: Arial;\">Ранее мы выясняли, как поменять&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патент</a>&nbsp;при переезде иностранного сотрудника&nbsp;<a href=\"http://all-migration.ru/news/36113/\">в другой регион</a>. Сейчас мы разберем другую частую ситуацию, когда гражданина необходимо оформить по специальности, не зафиксированной в патенте. Например, гражданин не находит работу по первоначальной профессии, либо текущий работодатель предлагает нанять его по другой должности. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Такому работнику не требуется оформлять новое разрешение: у иностранца имеется действующий патент, достаточно поменять в нем специальность, при этом заново проходить медицинскую комиссию и тест не надо. В результате переоформления мигранту выдадут патент по новой профессии.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><span style=\"color: #ff0000;\">Обратите внимание</span>: срок нового патента будет привязан к первоначальному. Для примера: если иностранец сделал патент по профессии &laquo;кассир&raquo; сроком до декабря 2019 года, далее переоформил его на профессию &laquo;повар&raquo;, такой патент будет действовать также до декабря 2019 г. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно: этой процедурой нельзя воспользоваться для смены территории действия патента!&nbsp;&nbsp;</strong></span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36129/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(267, 0, 1544000865, NULL, NULL, '<p><span style=\"font-family: Arial;\">К нам часто поступают вопросы от работодателей по поводу заполнения и подачи&nbsp;<a href=\"http://all-migration.ru/services/addservices/notification/\">уведомлений</a>&nbsp;МВД о приеме на работу и увольнении иностранного гражданина. Данные вопросы возникают в силу незнания миграционного законодательства: законы часто меняются, дополняются и за всеми нюансами самостоятельно невозможно уследить. Из-за этого работодатели часто совершают ошибки, о которых они даже не догадываются.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Чаще всего встречаются следующие ошибки:</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;1. Заполнение уведомления на неверной форме бланка.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. В уведомление внесена не вся информация, необходимая для подачи.&nbsp;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. Неправильная распечатка уведомления.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">4. Неправильное указание оснований трудовой деятельности (если человек устраивается не по патенту).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">5. Подача уведомления не в установленный законодательством срок.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">6. В случае отправки уведомления почтой России работодатель отправляет его по неверному адресу.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: неправильно поданное уведомление (в том числе поданное не по верному адресу/ поданное не в установленный законодательством срок) приравнивается к отсутствию уведомления! За это нарушение предусмотрены штрафы от 400 000 до 1 000 000 рублей.&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36141/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(268, 0, 1544001001, NULL, NULL, '<p><span style=\"font-family: Arial;\">Многие работодатели уже давно принимают на работу сотрудников с патентами. Это выгодно по многим причинам. И если с ежемесячной оплатой налога за патент своего сотрудника работодатели уже разобрались, то с продлением патента на 2 год многие испытывают некоторые трудности. О чем стоит не забывать работодателю при продлении патента сотрудником и как же успеть&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #005824; font-family: Arial;\"><strong>продлить патент до 2019</strong></span></a><span style=\"font-family: Arial;\">&nbsp;года, расскажем сегодня.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Первое, что стоит не забывать работодателям, нанимающих иностранцев с патентом, так это то, что продлить патент без выезда на границу сотрудник сможет только 1 раз. Если у Вас работает патентный работник у которого уже есть переоформленный патент, то ему обязательно потребуется выезжать и затем&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"color: #005824; font-family: Arial;\"><strong>оформлять себе новый трудовой патент</strong></span></a><span style=\"font-family: Arial;\">. Для этого Вам потребуется отстранить его от работы на месяц, пока он не принесет новый патент.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Но также не забывайте, что&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/prodleniepatenta/\"><span style=\"color: #045f20; font-family: Arial;\"><strong>продлить патент</strong></span></a><span style=\"font-family: Arial;\">&nbsp;возможно не позднее, чем за 10 рабочих дней до даты окончания действующего патента. То есть заниматься продлением патента необходимо еще пока тот патент, по которому он у вас работает, еще действует.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Обращаем Ваше внимание, что продлить патент в этом году Вы сможете только примерно до середины декабря, так как государственные органы в последние 2 недели декабря занимаются выдачей уже готовых документов.&nbsp;</span></p>\n<h3><span style=\"font-family: Arial;\">Что же делать, если патент у сотрудника истекает в начале января?&nbsp;</span></h3>\n<p><span style=\"font-family: Arial;\">В данном случае необходимо позаботиться об этом заранее. Ведь месячное отстранение сотрудника или, что самое плохое, потеря работница отнимет очень много нервов и денег. Не стоит откладывать продление патента, даже если у сотрудника в запасе еще 2 месяца, не забывайте очереди в государственные органы перед праздниками не избежать.</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Внимание работодателям!</strong>&nbsp;Продленный патент будет действовать только с даты окончания действия предыдущего патента, поэтому продлить его можно как за 1 месяц, так и за 2 и даже 4 месяца.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36167/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(269, 0, 1544605088, NULL, NULL, '<h4>Почему российские регионы так сильно отличаются друг от&nbsp;друга?</h4>\n<p><span>&mdash; Региональное неравенство возрастает не только у нас. Повсюду обнаруживаем то же самое. Государства различны, степень устойчивости экономики и уровень экономического развития весьма отличаются, но неравенство возрастает одинаково. От Польши до Таджикистана &mdash; все развивается примерно по одному сценарию.</span></p>\n<h4>В&nbsp;чем&nbsp;выражаются эти различия?</h4>\n<p>&mdash; Во&ndash;первых, уровень жизни. Его более&ndash;менее можно посчитать через средний доход. Сразу видно нарастание неравенства между столичными регионами и всеми прочими. Среднестатистическая столица (неважно, Таллин, Минск, Киев или Москва) заведомо богаче вторых городов &mdash; Харькова, Могилева или Петербурга. То же в Средней Азии, и в Закавказье, и у поляков. Люблин и Варшава &mdash; это две разные Польши.</p>\n<p>Во&ndash;вторых, качество жизни. Например, в Ямало&ndash;Ненецком округе очень приличный доход! И на Урале, и в Сибири немало городов, где реальные доходы населения весьма велики. Но нужны ли вам эти доходы, если вы живете с видом на медеплавильный комбинат? На котором, кстати, и работаете. И школа ваших детей стоит во дворе этого комбината.</p>\n<p>Все преимущества выпадают на долю столичных и субстоличных городов. В Москве или Петербурге &mdash; вот вам, пожалуйста, Большой театр, а если не хотите &mdash; есть и Малый. Как только перемещаемся в условный Воронеж, то варианты куда более скромные. А уж в малом городе, где советская власть (при всех ее недостатках) держала хотя бы Дом культуры, сейчас зачастую нет вообще ничего. Так что доходы, конечно, вещь важная, и считать их нужно. Но основные проблемы лежат в различиях качества жизни. И ни одна страна еще не придумала, как их решить. Мы абсолютно в русле мировых тенденций.</p>\n<h4>В&nbsp;США тоже есть \"Нью&ndash;Йорк и&nbsp;все остальные\" или&nbsp;все&nbsp;же статус Штатов гарантирует другое положение вещей?</h4>\n<p><span><span>&mdash; Да, главное отличие в том, что в США много \"Нью&ndash;Йорков\". Не нравится в Нью&ndash;Йорке &mdash; езжайте в Вашингтон. Не по душе там &mdash; мигрируйте в Сан&ndash;Франциско, Лос&ndash;Анджелес или Чикаго. Около полутора десятков таких мест найдется. Но разрыв с одноэтажной Америкой все же остается таким же. И расслоение между богатыми и бедными не уменьшается, а увеличивается. Это, кстати, одна из предпосылок появления трампизма.</span></span></p>\n<p>&nbsp;</p>\n<h4>Это способствует тому, что&nbsp;люди в&nbsp;Америке более мобильны?</h4>\n<p>&mdash; Абсолютно. Привязанность к месту жительства &mdash; это в известной степени наследие советской прописки. И в Соединенных Штатах она, по понятным причинам, отсутствует. Впрочем, и в той же Германии или Бельгии, где прописки не было, мобильность на порядок меньше. Там чаще выглядит так: квалифицированный специалист живет в одном городе, работает в другом, а на субботу&ndash;воскресенье едет в свой загородный дом.</p>\n<p>Постепенно что&ndash;то такое проникает и к нам. Мы видим целый класс людей, которые проводят рабочую неделю в Москве, а потом возвращаются к семьям на субботу&ndash;воскресенье.</p>\n<h4>А&nbsp;почему наряду с&nbsp;центром и&nbsp;Северо&ndash;Западом многие переезжают на&nbsp;юг?</h4>\n<p><span>&mdash; Прежде всего &mdash; чисто климатические причины. Плюс более&ndash;менее развитая инфраструктура. Плюс внимание федеральной власти к проектам, реализуемым на юге. Ведь Сочи сегодня и Сочи 15&ndash;летней давности &mdash; два разных города. Не так явно, но видим это и в Краснодаре, в Ростове.</span></p>\n<h4>При&nbsp;этом уровень зарплат там остается одним из&nbsp;самых низких.</h4>\n<p>&mdash; Помимо легальной зарплаты есть значительная часть серых доходов, связанных с личным подсобным хозяйством, с туризмом и прочим. Там вы легко можете встретить таксиста или работника гостиничной сферы, у которого легальная зарплата &mdash; 10&ndash;15 тыс., но живет он лучше тех гостей, которых принимает из Москвы и Петербурга.</p>\n<p>В нашем регионе для таких доходов возможностей меньше, а в Нарьян&ndash;Маре их вообще практически нет.</p>\n<p><span>&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"https://www.dp.ru/a/2018/10/28/Vezet_stolicam\">Деловой Петербург </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(270, 0, 1549010877, NULL, NULL, '<p><span style=\"font-family: Arial;\">При устройстве на работу, иностранные граждане нередко предоставляют работодателю&nbsp;</span><a href=\"http://all-migration.ru/services/migracionnyi-uchet/\"><span style=\"font-family: Arial;\">регистрацию</span></a><span style=\"font-family: Arial;\">, оформленную на гостиницу, общежитие или хостел. На что следует обратить внимание, если сотрудника поставили на миграционный учет в хостеле?</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;По действующему законодательству (п. 5 ст. 23 109-ФЗ), принимающая сторона (которой в данном случае является хостел) может подать&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/snyatiemigruchet/\"><span style=\"font-family: Arial;\">уведомление&nbsp;</span></a><span style=\"font-family: Arial;\">об убытии после постановки иностранца на учет, при этом факт убытия никто не проверяет. Тогда регистрация уже не будет действовать, хотя сам гражданин может об этом не знать, и предъявить ее работодателю и в миграционные органы.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Как можно подстраховаться? Важно понимать, что легальная регистрация на хостел может быть только с возможностью проживания в нем, то есть у иностранца должно быть оборудовано спальное место, возможность пользоваться душевой, кухней. Хостел обязан подписать с проживающим договор о проживании, а также бланк регистрации.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Внимание</strong>: срок проживания по договору с хостелом и срок регистрации обязательно должны совпадать!&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36343/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(271, 0, 1549011051, NULL, NULL, '<p><span style=\"font-family: Arial;\">Все работодатели при установлении заработной платы сотрудникам должны помнить, что зарплата не может быть ниже установленного минимального размера оплаты труда (МРОТ). Он распространяется как на российских, так и на иностранных сотрудников. При этом МРОТ неуклонно повышается каждый год.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Согласно Региональному соглашению от 28.11.2018, в 2019 году в Санкт-Петербурге МРОТ составит&nbsp;<strong>18 тысяч рублей</strong>. Каковы последствия данного соглашения для работодателей?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если в компании трудятся граждане по&nbsp;</span><a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\"><span style=\"font-family: Arial;\">патенту</span></a><span style=\"font-family: Arial;\">&nbsp;либо из стран ЕАЭС, руководитель должен издать приказ об увеличении заработной платы. А для новых сотрудников в трудовом договоре необходимо предусмотреть заработную плату не ниже 18 тысяч рублей. Как правило, во избежание вопросов от налоговых органов и трудовой инспекции, работодатели устанавливают в договоре зарплату выше минимальной.</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: Для работодателей, которые хотят привлечь&nbsp;<a href=\"http://all-migration.ru/services/workpermit/\">сотрудника</a>&nbsp;из визовых стран (не&nbsp;<a href=\"http://all-migration.ru/services/addservices/workpermit-vks/\">высококвалифицированного</a>&nbsp;специалиста), повышение МРОТ также очень существенно!</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Размер минимальной оплаты труда потребуется указать для прохождения следующих процедур:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">-подача заявки в Центр Занятости (ЦЗ): необходимо разместить вакансию с зарплатой не ниже 18 тысяч рублей;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- разрешение на привлечение иностранного гражданина на работу (все сведения, указанные в заявлении, должны соответствовать вакансии в ЦЗ &ndash; в том числе и зарплата).&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36353/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(272, 0, 1549022878, NULL, NULL, '<p><span style=\"font-family: Arial;\">На дворе уже декабрь, а значит, принимаются новые законодательные акты, которые будут определять нашу жизнь в 2019 году. Миграционные правила не исключение: каждый год Правительство утверждает квоту на привлечение рабочей силы. И такое Постановление на 2019 год уже принято!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Согласно указанному Постановлению (от 06.12.2018 N 1494), в следующем году из визовых стран можно будет привлечь для работы 144 583 человека, что на 3% больше, чем в текущем году. Квоты распределены по профессиям: наибольшее количество мест выделено для работников, занятым на горных, строительно-монтажных, ремонтных работах.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Чтобы принять в штат иностранного гражданина по&nbsp;<a href=\"http://all-migration.ru/services/workpermit/\">квотируемой профессии</a>, работодателю необходимо подать заявку через специальный сайт. При этом он должен обосновать, по какой причине ему требуется трудоустроить иностранцев по выбранной профессии. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: для квотируемых профессий подавать вакансию в Центр занятости не нужно!&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36360/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(273, 0, 1549270706, NULL, NULL, '<p><span style=\"font-family: Arial;\">Вопросы налогообложения и социального страхования сложны и запутаны, даже если речь идет о гражданах России. Если же доходит дело до иностранцев, то вопросы появляется еще больше, так как у них разные статусы и все это регулируется множеством законов. Многие компании вообще отказываются нанимать мигрантов, так как полагают, что за них придется отчислять большие налоги. Поэтому мы решили разобраться, какие взносы и отчисления нужно производить за иностранных граждан отдельных категорий в нынешнем году.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В соответствии со ст. 224 НК,&nbsp;<a href=\"http://all-migration.ru/services/addservices/nalog/\">НФДЛ</a>&nbsp;для следующих категорий иностранцев составляет&nbsp;<strong>13%</strong>:</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;- безвизовые мигранты (работающие по&nbsp;<a href=\"http://all-migration.ru/services/addservices/patent_na_raboty/\">патенту</a>);&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- граждане из государств-членов ЕАЭС;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;- беженцы.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Что касается взносов в Пенсионный фонд (<strong>ПФР</strong>), Фонд Обязательного медицинского страхования (<strong>ФОМС</strong>) и Фонд Социальных страхования (<strong>ФСС</strong>), то размер отчислений зависит от правового положения иностранца в России. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Начнем с граждан стран-членов&nbsp;<strong>ЕАЭС</strong>, статус которых регулируется Договором о ЕАЭС: &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Отчисления в ПФР &ndash; 22 % (разъяснения по этому вопросу есть в письме Минфина от 12.07.2017 № 03-15-06/44430).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Отчисления в ФСС &ndash; 2,9%, поскольку в соответствии с Договором обязательное социальное страхование данных граждан осуществляется наравне с гражданами России.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Отчисления в ФОМС &ndash; 5,1 %.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Как видите, отчисления для граждан ЕАЭС аналогичны тем, что предусмотрены для граждан России.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;По иностранцам, имеющим&nbsp;<strong>ВНЖ/РВП</strong>, а также имеющим статус&nbsp;<strong>беженца</strong>, также предусмотрены выплаты как для граждан России:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">ПФР &ndash; 22%</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;ФСС &ndash; 2,9%&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">ФОМС &ndash; 5,1%. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Для граждан, работающих в России по&nbsp;<strong>патенту</strong>, установлены следующие ставки взносов: ПФР &ndash; 22% ФСС &ndash; 1,8% ФОМС &ndash; 0%.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36663/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(274, 0, 1549270779, NULL, NULL, '<p><span style=\"font-family: Arial;\">После возвращения с Новогодних каникул многие сотрудники и их работодатели забывают о том, что миграционные документы по-прежнему необходимо продлевать, и поздно обнаруживают пропущенные сроки. Прежде всего это касается&nbsp;<a href=\"http://all-migration.ru/services/migracionnyi-uchet/\">миграционного учета</a>&nbsp;(временной регистрации) иностранных граждан, поскольку ему подлежат абсолютно все мигранты &ndash; из стран ЕАЭС, визовые и безвизовые.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому мы еще раз напомним Вам об основных правилах, касающихся&nbsp;<a href=\"http://all-migration.ru/services/addservices/prodlenieregistracii/\">продления</a>&nbsp;временной регистрации.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Во-первых, если регистрация иностранного гражданина заканчивается в нерабочие/праздничные дни, следует заблаговременно позаботиться о ее продлении. Например, дата окончания 4 января 2019 года, в этом случае ее необходимо было продлить в конце декабря.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Во-вторых, если гражданин за выходные был на отдыхе (в гостинице, доме отдыха, санатории, и др) либо попал в больницу, даже в пределах Санкт-Петербурга, ему необходимо повторно встать на учет по возвращении в место пребывания. Это связано с тем, что указанные учреждения по закону обязаны автоматически регистрировать и&nbsp;<a href=\"http://all-migration.ru/services/addservices/snyatiemigruchet/\">снимать&nbsp;</a>с учета всех иностранцев в течение 12 часов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: вставать на миграционный учет повторно необходимо даже в том случае, если срок предыдущей регистрации еще не истек!</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Например, регистрация иностранца действует до 25 января 2019 года, однако с 7 по 13 января он был на отдыхе и останавливался в гостинце. В этом случае с 14 января у него возникает обязанность снова подать документы на продление миграционного учета (сделать это нужно в течение максимум 7 дней).</span></p>\n<p style=\"text-align: right;\"><span style=\"font-family: times\\ new\\ roman, times; font-size: medium;\">Источник: <a href=\"http://all-migration.ru/news/36698/\">Миграционные новости | Альянс Трудовая Миграция </a></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(275, 0, 1554552509, NULL, NULL, '<p>Бугул 6 апрель</p>', NULL, NULL, NULL, NULL, NULL, 1),
(276, 0, 1554555373, NULL, NULL, '<p>Каждому иностранному гражданину для законного трудоустройства в России необходим внушительный комплект документов. А работодателю необходимо следить за их наличием и действительностью, иначе рискуют быть привлеченными к ответственности за нелегальное трудоустройство.</p>\n<p>Почему полис дополнительного медицинского страхования (ДМС) очень важен для трудоустройства иностранных граждан по закону?&nbsp;</p>\n<p>Оформить&nbsp;полис ДМС&nbsp;любому иностранному гражданину необходимо с первого дня въезда в РФ, даже в случае, если он не собирается работать. А при трудоустройстве иностранцев, в том числе планирующих работать на основании&nbsp;патента, Полис медицинского страхования входит в число обязательных документов. Поэтому работодателю важно проверить наличие действительного медицинского полиса у будущего сотрудника.&nbsp;</p>\n<p>Однако полис выдается на определенный срок, по истечении которого его необходимо продлить. Истечение срока действия полиса является основанием для отстранения иностранного гражданина от работы по&nbsp;ст. 327.5<strong>&nbsp;</strong>Трудового Кодекса РФ! И если не оформить отстранение такого сотрудника, то он будет работать у вас нелегально.&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(277, 0, 1555492281, NULL, NULL, '<p><span style=\"font-family: Arial;\">Как принять в штат иностранного гражданина с&nbsp;патентом?&nbsp;&nbsp;Ведь любая ошибка в документах может стоить работодателю очень высоких штрафов. В частности, на что нужно обратить внимание перед заключением трудового договора? Какие важные особенности трудовых договоров с сотрудниками, работающими на основании патента? Что необходимо сделать после подписания трудового договора с таким иностранцем?</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;На эти вопросы мы ответим в нашей сегодняшней статье, дав&nbsp;<strong>5 рекомендаций</strong>&nbsp;по заключению трудового договора с иностранным гражданином.</span></p>\n<p><span style=\"font-family: Arial;\">1. Соглашение с мигрантом может быть заключено только при условии, что его присутствие на территории РФ легализовано (рабочая цель въезда в&nbsp;миграционной&nbsp;карте, оформлен&nbsp;миграционный&nbsp;учет) и он имеет право работать (у иностранца имеется действующий патент, и оригиналы чеков по его оплате).&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2. Сведения о наличии патента необходимо указать в преамбуле договора. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">3. Договор заключается&nbsp;<strong>по профессии</strong>, указанной в патенте иностранного гражданина. Например, гражданин оформил патент по профессии &laquo;подсобный рабочий&raquo;. В трудовом договоре не может быть указана профессия грузчик, разнорабочий и т.д. Должностные обязанности сотрудника также должны соответствовать профессии в патенте.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;4. Фактическое место работы должно находиться на&nbsp;<strong>территории действия патента</strong>. Например, у вас есть несколько обособленных подразделений (складов, строительных площадок, торговых мест) в Санкт-Петербурге и Ленинградской области, а у иностранца оформлен патент на Ленинградскую область. В этом случае его местом работы могут быть только подразделения вашей компании, которые находятся в Ленинградской области.&nbsp;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">5. В течение&nbsp;<strong>трех рабочих дней</strong>&nbsp;после даты подписания договора вы как работодатель должны направить&nbsp;уведомление&nbsp;в ГУ МВД о заключении трудового договора, а иностранный работник обязан направить почтой в ГУ МВД копию трудового договора (в течение 2 месяцев со дня получения патента).</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(278, 0, 1555491874, NULL, NULL, '<p><span style=\"font-family: Arial;\">Уведомление&nbsp;ГУ МВД о заключении трудового договора &ndash; это документ, о котором должен знать каждый работодатель, нанимающий иностранных граждан. При этом на практике требования к комплекту документов, необходимых для подачи уведомления, могут меняться.</span></p>\n<p><span style=\"font-family: Arial;\">Поэтому стараемся оперативно информировать вас о подобных изменениях, и сегодня сообщаем&nbsp;</span><strong style=\"font-family: Arial;\">важную новость</strong><span style=\"font-family: Arial;\">:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">C недавних пор ГУ МВД не принимает трудовые договоры от работодателей при личной подаче!&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">При подаче уведомления о заключении договора напрямую в Управление по вопросам миграции работодатель предоставляет&nbsp;<strong>следующие документы</strong>:&nbsp;</span></p>\n<ul>\n<li><span style=\"font-family: Arial;\">Паспортные данные единоличного исполнительного органа организации (генерального директора);&nbsp;</span></li>\n<li>Договор аренды/ свидетельство о праве собственности (в случае, если юридический адрес отличается от фактического);</li>\n<li>Действующую выписку ЕГРЮЛ;</li>\n<li>Информационное письмо;</li>\n<li>Доверенность на представителя.&nbsp;</li>\n</ul>\n<p><span style=\"font-family: Arial;\">А также документы на иностранного гражданина, по которому подается уведомление: Паспорт, данные&nbsp;миграционного&nbsp;учета.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>:&nbsp; иностранный сотрудник должен самостоятельно направить копию трудового договора почтой в ГУ МВД. Сделать это необходимо в течение 2 месяцев с даты&nbsp;<strong>получения патента</strong>.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данный порядок соответствует законодательству - Закону 115-ФЗ о статусе иностранных граждан (ст. 13.3 пункт 7).</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(279, 0, 1555492183, NULL, NULL, '<p>Уменьшить&nbsp;размер отчислений НДФЛ за иностранных сотрудников, работающих на основании&nbsp;патента. Это означает, что работодателю не придется платить НДФЛ, который не превышает 3 800 рублей (размер гос.пошлины для Санкт-Петербурга и Ленинградской области). Однако сначала нужно получить у<strong>ведомление налоговой инспекции</strong>.&nbsp;</p>\n<p>Что это за уведомление и как его получить?&nbsp;</p>\n<p>Налоговая служба выпустила отдельное письмо, посвященное этому вопросу (письмо ФНС РФ от 07.05.2018 № БС-4-11/8635@). Для вашего удобства мы кратко перечислим его содержимое:</p>\n<p>1. Уведомление &ndash; это&nbsp;<strong>подтверждение</strong>&nbsp;Налоговой инспекцией права на уменьшение НДФЛ, выданное по запросу работодателя.&nbsp;</p>\n<p>2. Согласно п. 6 ст. 227.1 НК РФ, уведомление выдается на один год и действует в течение&nbsp;<strong>одного налогового периода</strong>.&nbsp;</p>\n<p>Уведомление выдается работодателю на&nbsp;<strong>следующих условиях</strong>:&nbsp;</p>\n<p>1) Иностранный сотрудник написал&nbsp;<strong>заявление</strong>&nbsp;на имя работодателя с просьбой уменьшить удерживаемый с его заработной платы налог на доходы физических лиц;&nbsp;</p>\n<p>2) В налоговом органе должна быть&nbsp;<strong>информация</strong>&nbsp;о заключенном трудовом договоре или гражданско-правовом договоре, а также о патенте иностранца;&nbsp;</p>\n<p>3) Данное уведомление в отношении иностранного работника может быть запрошено только&nbsp;<strong>один раз в год</strong>&nbsp;(налоговый период). Например, если предыдущий работодатель иностранца запросил уведомление на возмещение НДФЛ за 2019 год, вы уже не сможете получить такое уведомление.&nbsp;&nbsp;</p>\n<p><strong>Важно</strong>: многие работодатели не могут получить уведомление из-за несоблюдения второго условия.&nbsp;</p>\n<p>С чем это связано? Налоговый орган получает данные о патентах и о трудовых (гражданско-правовых) договорах от территориальных отделений&nbsp;<strong>МВД РФ</strong>&nbsp;по вопросам миграции. Если по каким-то причинам в базе МВД нет информации о заключении договора (уведомление&nbsp;было подано не напрямую в ГУ МВД), или имеется задержка в поступлении информации - налоговая служба не выдаст разрешение на возмещение НДФЛ.&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(280, 0, 1555600579, NULL, NULL, '<p>Иностранные работники в штате - привычное дело для наших работодателей, но, как правило, это касается рядовых должностей, линейного персонала. Но многие организации берут на работу иностранцев на руководящую должность&ndash;&nbsp;<strong>генерального директора</strong>. Это может быть связано с международным характером бизнеса, наличием деловых отношений с зарубежными партнерами и т.д.&nbsp;</p>\n<p>Поэтому мы решили рассмотреть&nbsp;<strong>3 ключевых вопроса&nbsp;</strong>трудоустройства иностранца генеральным директором.</p>\n<p>&nbsp;1.<strong> На какой срок можно заключить трудовой договор с директором-иностранцем?&nbsp;</strong></p>\n<p>Трудовой договор с директором может быть как срочный, так и бессрочный (ч. 2 ст. 59 ТК РФ) &ndash; это определяется соглашением сторон. Данное правило распространяется и на иностранных граждан.</p>\n<p>Как правило, срок трудового договора совпадает со сроком полномочий ген.директора. Он определяется Уставом Общества, либо указывается непосредственно в решении о назначении директора. Например, если по учредительным документам срок полномочий - 3 года, то и трудовой договор можно оформить на тот же срок. &nbsp;</p>\n<p>2. <strong>Когда директор сможет приступить к обязанностям? &nbsp;</strong></p>\n<p>Данный вопрос актуален для граждан из визовых стран, в том числе&nbsp;ВКС. Иностранный гражданин сможет приступить к обязанностям только после того, как получит на руки&nbsp;разрешение&nbsp;на работу. А чтобы получить это разрешение, необходимо предоставить в ГУ МВД подписанный трудовой договор. Поэтому в нем нужно указывать не только дату заключения договора, но и момент его вступления в силу. Поскольку заранее нельзя узнать, какого числа гражданин получит разрешительный документ, в трудовом договоре используется формулировка &laquo;со дня получения разрешения на работу&raquo;.&nbsp;&nbsp;</p>\n<p>3. <strong>Кто подписывает трудовой договор?&nbsp;</strong></p>\n<p>Председатель Общего собрания участников или единственный участник. То есть представительный орган, решением которого был назначен генеральный директор.&nbsp;</p>\n<p><strong>Важно</strong>: Если иностранец, избранный генеральным директором, еще не получил на руки разрешение на работу, он назначает&nbsp;<strong>исполняющего обязанности генерального директора&nbsp;</strong>на этот период (с правом подписи без доверенности).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(281, 0, 1565773185, NULL, NULL, '<p><a title=\"Цивилизованная_миграция.mp4\" href=\"http://fondorientir.ru/filemanager/i/5a685662/Цивилизованная_миграция.mp4\">&nbsp;</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(282, 0, 1556016966, NULL, NULL, '<p>5 и 12 апреля 2019 г.&nbsp;<span>Фондом поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo; были&nbsp;</span>проведены выездные приемные правового просвещения и оказания юридической помощи трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга на территориях прилегающих к Соборной мечети, расположенной по адресу: Санкт-Петербург, Кронверкский проспект, дом 7,к Коломяжской мечети, расположенной по адресу: Санкт-Петербург, ул. Репищева, дом 1 и к молельной комнате, расположенной по адресу: Санкт-Петербург, Московский проспект, дом 12.<img style=\"float: left;\" title=\"IMG_2207-01-04-19-02-17.JPG\" src=\"http://fondorientir.ru/filemanager/i/32920d75/IMG_2207-01-04-19-02-17.JPG\" alt=\"IMG_2207-01-04-19-02-17.JPG\" width=\"700\" height=\"700\" /><img title=\"IMG_2204-01-04-19-02-17.JPG\" src=\"http://fondorientir.ru/filemanager/i/5075f766/IMG_2204-01-04-19-02-17.JPG\" alt=\"IMG_2204-01-04-19-02-17.JPG\" width=\"700\" height=\"700\" /><img title=\"IMG_2392-06-04-19-06-27.JPG\" src=\"http://fondorientir.ru/filemanager/i/9af8f822/IMG_2392-06-04-19-06-27.JPG\" alt=\"IMG_2392-06-04-19-06-27.JPG\" width=\"700\" height=\"700\" /><img title=\"IMG_2393-06-04-19-06-27.JPG\" src=\"http://fondorientir.ru/filemanager/i/19f27afd/IMG_2393-06-04-19-06-27.JPG\" alt=\"IMG_2393-06-04-19-06-27.JPG\" width=\"700\" height=\"700\" /><img title=\"IMG_2394-06-04-19-06-27.JPG\" src=\"http://fondorientir.ru/filemanager/i/095bb262/IMG_2394-06-04-19-06-27.JPG\" alt=\"IMG_2394-06-04-19-06-27.JPG\" width=\"700\" height=\"700\" /><img title=\"IMG_2396-06-04-19-06-27.JPG\" src=\"http://fondorientir.ru/filemanager/i/3960099a/IMG_2396-06-04-19-06-27.JPG\" alt=\"IMG_2396-06-04-19-06-27.JPG\" width=\"700\" height=\"700\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(283, 0, 1565792650, NULL, NULL, '<p>ГИБДД опубликовала новые предложения по реформированию системы подготовки водителей. Служба предлагает запретить обмен иностранных прав без сдачи экзаменов, сделать обязательным наличие стажа для желающих водить автобус, а также обязать обучающихся сдавать права только в конкретном подразделении ГИБДД, к которому приписана автошкола.</p>\n<p>О нововведениях рассказал замначальника ГИБДД России Николай Шеюхин в ходе конференции &laquo;Безопасная дорога&raquo;. Служба уже подготовила соответствующий законопроект.</p>\n<p>Одно из главных изменений &ndash; запрет на обмен прав для иностранных водителей без сдачи экзамена. В настоящее время запрещено управлять транспортными средствами без российских прав при работе по найму (исключение сделано для граждан Киргизии, а также стран, где русский язык считается официальным). В ГИБДД указали, что граждане придумали схему &ndash; они ездят в соседние государства, &laquo;покупают&raquo; местные права, а затем меняют их на российские и таким образом экономят на обучении.</p>\n<p>Кроме того, предлагается сделать обязательным наличие водительского стажа для желающих водить автобус. Чтобы получить категорию D, водитель должен иметь стаж не менее одного года в рамках категорий B, C1 (грузовики массой 3,5-7,5 тонны) или D1 (автобусы, предназначенные для перевозки до 16 пассажиров).</p>\n<p>Другой пункт поправок касается всех кандидатов в водители. ГИБДД хочет ограничить студентов в праве сдавать экзамены в любом подразделении.</p>\n<p><em>&laquo;Ученик получил документы об окончании автошколы и дальше бегает от одного подразделения к другому, пытаясь так или иначе &laquo;порешать вопрос&raquo; со сдачей экзамена,&ndash; рассказал Шеюхин.&ndash; Закрепление ученика за конкретным подразделением минимизирует коррупционную составляющую&raquo;.</em></p>\n<p>Впрочем, пока эта идея находится на стадии обсуждения.</p>\n<p>Напомним, реформа подготовки водителей в России идут с 2013 года. Так Госавтоинспекция планирует объединить в единый экзамен &laquo;площадку&raquo; и &laquo;город&raquo;, пересмотреть вопросы к теоретическому тесту, а также ввести внеплановые проверки автошкол.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(284, 0, 1565792658, NULL, NULL, '<p><strong>Спустя 3&nbsp;года после ареста предпринимателя Дмитрия Михальченко часть созданной им империи, ориентированная на&nbsp;монетизацию услуг по&nbsp;легализации мигрантов, перехвачена. Она не&nbsp;только продолжает существовать, но&nbsp;и&nbsp;развивается, принося своим новым хозяевам и&nbsp;аффилированным с&nbsp;ними лицам огромные барыши. По&nbsp;самым скромным подсчетам, объем этого рынка в&nbsp;Петербурге достигает 6&nbsp;млрд&nbsp;рублей ежегодно.</strong></p>\n<p>Во вторник в Москве прошло координационное совещание сотрудников региональных территориальных подразделений ГУ МВД по вопросам миграции. Начальник Главного управления&nbsp;МВД&nbsp;Валентина Казакова раскритиковала подчиненных за \"недостаточную эффективность мер, реализуемых территориальными органами МВД России по предоставлению гражданам госуслуг в сфере миграции\".</p>\n<p>В Петербурге от имени МВД эти услуги официально представляет региональный филиал ФГУП \"Паспортно&ndash;визовый сервис\". С 1 апреля 2015 года это единственная в Петербурге и Ленобласти организация, уполномоченная выдавать патенты, обязательные для всех иностранных рабочих из стран СНГ (кроме граждан Армении, Белоруссии, Казахстана и Киргизии). Чтобы получить патент, мигранту необходимо собрать ворох бумаг &mdash; от медицинских справок, подтверждающих отсутствие заболеваний, до результатов успешного прохождения тестирования на знание русского языка, а также представить заявление от работодателя о приеме на работу.</p>\n<p>На рынке Петербурга работает множество коммерческих организаций, которые оказывают мигрантам разнообразные \"посреднические услуги\", фактически занимаясь не просто оформлением документов, но и легализацией гостей города.</p>\n<p>При этом определить, частная это компания или государственная, порой просто невозможно: у них совпадают адреса и телефоны.</p>\n<p><strong>Совпадают до&nbsp;полного смешения</strong></p>\n<p>Власти признают наличие частных компаний. В памятке Смольного для мигрантов сообщается следующее: \"В РФ действует множество посредников (организаций или отдельных граждан), предлагающих платную помощь при оформлении патента. Не дайте себя обмануть: не прибегайте к помощи \"частных лиц\", обязательно заключайте договоры на оказание услуг, если вы обратились за помощью в оформлении документов в посредническую организацию\".</p>\n<p>При этом ряд петербургских компаний заявляют, что заключили агентские договоры с&nbsp;\"Единым центром документов\"&nbsp;и \"Единым миграционным центром\" на оказание сервисных услуг по оформлению документов для иностранцев. Ряд фирм прямо указывают в партнерах петербургский филиал \"Паспортно&ndash;визового центра\", а некоторые и вовсе сообщают ложные данные о наличии собственной лицензии на оформление документов.</p>\n<p>По данным официального сайта регионального филиала ФГУП, у него открыты отделения в разных районах. Обособленные подразделения филиала работают на Курской ул., Якорной ул., Московском пр. и Межевом канале. В этих же зданиях (а в двух случаях &mdash; даже в одних и тех же помещениях) находятся коммерческие компании: ООО \"Догма\", ООО \"Восход\", ООО \"БалтикСервис\", ООО \"Град\".</p>\n<p>Основной вид деятельности всех этих фирм &mdash; \"Предоставление прочих персональных услуг, не включенных в другие группировки\". Более четкое представление о специализации дают сайты компаний. Перейдя по ссылкам, пользователь интернета попадает на страницы миграционных центров \"Обводный канал\", \"Заневский\", \"Московский\", \"Нарвский\". Все они выполнены в идентичной стилистике, в том числе даже с точки зрения цветового оформления.</p>\n<p>В принципе логично, что руководители решили, что у подразделений должен быть один стиль. Ведь у всех вышеперечисленных ООО практически один и тот же состав владельцев: Кирилл Сергеевич Резлер, Дина Геннадьевна Цилевич, Елена Николаевна Демина, Елена Анатольевна Буянова. Только в одном случае (ООО \"БалтикСервис\") четвертым участником является не г&ndash;жа Буянова, а Сергей Николаевич Монкин. Отметим, к слову, что это самая старая из всех перечисленных фирм &mdash; зарегистрирована в 2010 году, ее изначальным учредителем как раз и был г&ndash;н Монкин (к 2017 году его доля в \"БалтикСервисе\" уменьшилась со 100 до 15,38%).</p>\n<p>Группа компаньонов Резлер &mdash; Цилевич &mdash; Демина также среди совладельцев компании \"Юридическая фирма \"Ампаро\". Интересно, что у первого ее учредителя, Анны Владимировны Анисимовой (как и у г&ndash;на Монкина), доля в компании также уменьшилась с изначальных 100 до 15,38%. Экс генеральный директор \"Ампаро\" Дмитрий Пархушев сейчас значится гендиректором в ООО \"Град\".</p>\n<p>Анна Анисимова и Сергей Монкин, впрочем, тоже недалеко ушли от кадрово&ndash;миграционной сферы &mdash; они на паритетных началах владеют ООО \"Кадровое решение СПб\".</p>\n<p>\"История нашей компании начинается с 2010 года как \"БалтикСервис\". Для работы мы заключили агентский договор с \"Единым центром документов\" и \"Единым миграционным центром\". Это позволило нам начать оказывать сервисные услуги по оформлению документов для иностранных граждан, &mdash; сообщается на официальном сайте этого ООО. &mdash; Нашими клиентами стали производственные, строительные и торговые компании, которые обращаются уже не только за услугами, но и за поиском кадров\".</p>\n<p>Согласно СПАРК, в 2017 году выручка \"Кадрового решения\" составила 263,7 млн рублей, чистая прибыль &mdash; 104,1 млн. Отчетности за 2018 год пока нет. Равно как и сведений о доходности трех других фирм &mdash; все они зарегистрированы в 2017&ndash;2018 годах.</p>\n<p>Один из видов услуг \"БалтикСервиса\" &mdash; \"подготовка и подача документов для оформления патента\", в том числе \"подача в электронном виде в филиал ФГУП \"Паспортно&ndash;визовый центр\". Согласно копии агентского договора, заключаемого между фирмой \"БалтикСервис\" и ее клиентами (имеется в распоряжении редакции), в 2017 году эта услуга стоила 19 тыс. рублей, в том числе 2 тыс. агентского вознаграждения.</p>\n<p>По официальным данным, в год в Петербурге оформляется порядка 300 тыс. патентов на работу. Таким образом, по самым грубым подсчетам, объем этого рынка составляет 5,7 млрд рублей. Но, по иным данным, реальное количество документов гораздо выше (до 1,3 млн в год). В таком случае объемы рынка возрастают до 25 млрд рублей.</p>\n<p><strong>Империя в&nbsp;новых границах</strong></p>\n<p>Заместителем директора регионального филиала ФГУП является Дмитрий Ромашко, фигурирующий, в частности, в нескольких новостях, размещенных на сайте ФГУП.</p>\n<p>К примеру, в январе этого года Ромашко совместно с директором филиала Иваном Селезневым открывал в Центральном районе отремонтированное здание отделения Управления по вопросам миграции ГУ МВД.</p>\n<p>По данным СПАРК, полный тезка замдиректора, Дмитрий Валерьевич Ромашко, в прошлом являлся совладельцем или руководителем семи коммерческих структур, в том числе ООО \"Балтранзит\", ООО \"Единый миграционный центр\" (ООО \"ЕМЦ\"), ООО \"Единый центр документов\". У истоков всех этих фирм стоял&nbsp;Дмитрий Михальченко. Последний остается гендиректором АО&nbsp;\"Холдинговая компания \"Форум\", которое владело ООО \"ЕДЦ\". Он же был совладельцем ООО \"ЦЕНТРФИНАНС\", которое сейчас владеет 99% ООО \"ЕМЦ\".</p>\n<p>В настоящее время равными долями \"ЦЕНТРФИНАНС\" владеют структуры, принадлежащие&nbsp;Николаю Негодову&nbsp;(70&ndash;й в Рейтинге миллиардеров \"ДП\") и&nbsp;Людмиле Муровой&nbsp;(83&ndash;я в РМ \"ДП\").</p>\n<p>Среди прежних совладельцев \"Балтранзита\" фигурируют автономная некоммерческая организация (АНО) \"Управление делами регионального общественного фонда программ управления ФСБ РФ по г. Санкт&ndash;Петербургу и Ленинградской области\" и учрежденное ею АО \"Холдинговая компания \"Форум\".</p>\n<p>Тезка замдиректора регионального филиала ФГУП является совладельцем компании \"Стройдом\", специализирующейся на строительстве и расположенной в поселке Солнечное на Приморском шоссе, 370. По странному совпадению открытые источники идентифицируют этот адрес как частный дом постройки 2016&ndash;2019 годов в современном стиле.</p>\n<p>Дмитрий Валентинович Ромашко, кроме того, фигурирует в числе учредителей региональной общественной организации \"Патриотическое объединение \"Ленрезерв\".</p>\n<p>Другие учредители &mdash; Анатолий Бернштейн (президент организации), Игорь Батовский (экс&ndash;гендиректор ООО \"Балтранзит\") и Ирина Лобанова.</p>\n<p>Последняя также является президентом адвокатской коллегии \"Ленрезерв\".</p>\n<p>В прошлом Лобанова была учредителем ООО \"Юридическое агентство \"Форум\"; среди совладельцев этой компании открытые источники называют Управление делами регионального общественного фонда программ управления ФСБ РФ по г. Санкт&ndash;Петербургу и Ленинградской области и АО \"ХК \"Форум\" Дмитрия Михальченко.</p>\n<p>Добавим, что соучредителем адвокатского объединения была до января 2019 года Ирина Стрижакова, супруга зампреда Леноблсуда в отставке Алексея Стрижакова. Судья претендовал на должность во Втором апелляционном суде, но Высшая квалификационная коллегия судей РФ отклонила его кандидатуру.</p>\n<p><strong>Продолжение следует</strong></p>\n<p>Вопросы о принципах, которыми силовики руководствуются при выборе той или иной модели работы с миграционными потоками, в Петербурге возникают регулярно. Так, в 2013 году тогдашний руководитель УФМС&nbsp;Елена Дунаева&nbsp;лично объясняла журналистам, почему в Петербурге изменилась программа, которая позволяла ставить на миграционный учет постояльцев гостиниц, и зачем отельеры должны нести дополнительные расходы.</p>\n<p>Разработчиком нового сервиса, который позволял передавать сведения в миграционную службу, называли компанию \"Интерфорум\". Учредителем \"Интерфорума\" было ЗАО \"Холдинговая компания \"Форум\" Дмитрия Михальченко. Сейчас среди учредителей &mdash; Никита Андреевич Муров (по данным СМИ, внук экс&ndash;директора ФСО), Наталья Николаевна Негодова и ООО \"Бронка групп\". А сама Елена Дунаева работает начальником Управления правового обеспечения ФСО РФ.</p>\n<p><img title=\"план.jpg\" src=\"http://fondorientir.ru/filemanager/i/be8753d1/план.jpg\" alt=\"план.jpg\" width=\"930px\" height=\"347px\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(285, 0, 1565249473, NULL, NULL, '<p><span style=\"font-family: Arial;\">В нашем городе много учебных заведений, где учатся иностранцы. Точно также, как российские студенты, они хотели бы подрабатывать в свободное от учебы время. Работодатели не против взять их на работу, но как это сделать?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Иностранного студента можно принять на работу, если он:</strong></span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;- обучается в аккредитованном высшем учебном заведении;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- обучается на дневном отделении (очная форма обучения); &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">- компания-работодатель находится в том же регионе, что и вуз. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Если студент соответствует данным критериям, его трудоустройство&nbsp;<strong>очень выгодно</strong>&nbsp;для работодателя! И вот почему:&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Не требуется аккредитация компании в МВД,&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Нет ограничений по профессии и по зарплате,</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;Нет ограничений по длительности рабочего дня. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Что нужно для оформления иностранного студента в штат?&nbsp;</strong></span></p>\n<p><span style=\"font-family: Arial;\">Необходимо оформить&nbsp;разрешение&nbsp;на работу, которое действует на срок обучения (семестр, учебный год). Если студент продолжает обучение (перешел на следующий курс), разрешение на работу можно продлить. Таким образом, процесс трудоустройства студента не займет много времени, а стоимость оформления вас приятно удивит.&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(286, 0, 1565249484, NULL, NULL, '<p class=\"font_9\"><span>Федеральная таможенная служба Российской Федерации (ФТС) информирует иностранных граждан о появлении на официальном сайте ведомства специального сервиса, благодаря которому в онлайн-режиме можно проверять наличие задолженности по таможенным платежам и решений о запрете на въезд в Россию, принятых таможенными органами страны.</span></p>\n<p class=\"font_9\"><span>ФТС констатирует, что в соответствии со ст. 27 Федерального закона от 15 августа 1996 г. №114-ФЗ &laquo;О порядке выезда из Российской Федерации и въезда в Российскую Федерацию&raquo; уклонение от уплаты налога иностранным гражданином или лицом без гражданства в период своего предыдущего пребывания в Российской Федерации является основанием для принятия в отношении такого лица решения о запрете на въезд в РФ.</span></p>\n<p class=\"font_9\"><span>Указанные положения закона также применяются к иностранным гражданам и лицам без гражданства, не исполнившим в установленные сроки обязанность по уплате таможенных пошлин и налогов, в том числе в связи с нарушением условий временного ввоза на таможенную территорию Евразийского экономического союза транспортных средств для личного пользования.</span></p>\n<p class=\"font_9\"><span><span>Доступ к информационному сервису ФТС осуществляется по электронному адресу:&nbsp;<span><a href=\"https://edata.customs.ru/FtsPersonalCabinetWeb/Services/About/CheckPayments\" rel=\"undefined\" target=\"_blank\" data-content=\"https://edata.customs.ru/FtsPersonalCabinetWeb/Services/About/CheckPayments\" data-type=\"external\">https://edata.customs.ru/FtsPersonalCabinetWeb/Services/About/CheckPayments</a></span></span></span></p>\n<p class=\"font_9\"><span>Чтобы проверить наличие задолженности или запрета на въезд в Российскую Федерацию, достаточно ввести данные: фамилию, имя, отчество, пол, гражданство, дату рождения.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(287, 0, 1565249492, NULL, NULL, '<p class=\"font_9\"><span>Правительство Российской Федерации внесло в нижнюю палату парламента пакет законопроектов, направленных на привлечение иностранных специалистов в РФ. Документы опубликованы в думской электронной базе.</span></p>\n<p class=\"font_9\"><span>&laquo;Проектом федерального закона предлагается сократить с трех лет до одного года необходимый срок осуществления иностранными гражданами и лицами без гражданства трудовой деятельности в Российской Федерации по профессии (специальности, должности), включенной в перечень профессий (специальностей, должностей) иностранных граждан и лиц без гражданства - квалифицированных специалистов, имеющих право на прием в гражданство Российской Федерации в упрощенном порядке&raquo;, - говорится в пояснительной записке.</span></p>\n<p class=\"font_9\"><span>Предполагается, что внесение этих изменений позволит привлечь в Российскую Федерацию специалистов, востребованных российской экономикой.</span></p>\n<p class=\"font_9\"><span>В свою очередь вторым законопроектом предлагается увеличить размер госпошлины за выдачу вида на жительство с 3,5 тыс. руб. до 5 тыс. руб.</span></p>\n<p class=\"font_9\"><span>&laquo;Такое увеличение связано с введением проектом федерального закона &laquo;О внесении изменений в Федеральный закон &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo; и отдельные законодательные акты Российской Федерации в части упрощения порядка предоставления некоторым категориям иностранных граждан и лиц без гражданства разрешения на временное проживание и вида на жительство&raquo; бессрочного вида на жительство, за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года)&raquo;, - отмечается в материале.</span></p>\n<p class=\"font_9\"><span>Как ранее сообщало Агентство городских новостей &laquo;Москва&raquo;, правительство РФ внесло в нижнюю палату парламента законопроект, которым предлагается упростить порядок предоставления иностранным гражданам разрешения на временное пребывание и вида на жительство (ВНЖ) в России.</span></p>\n<p class=\"font_9\"><span>&laquo;Законопроектом, в частности, предлагается выдавать разрешение на временное проживание отдельным категориям иностранных граждан без учета квоты, утверждаемой правительством России. Также предлагается сократить (с шести до четырех месяцев) сроки рассмотрения заявлений о выдаче разрешений на временное проживание. Новеллой законопроекта является введение бессрочного вида на жительство&raquo;, - говорится в пояснении.</span></p>\n<p class=\"font_9\"><span><span>Отмечается, что документом предлагается выдавать разрешение на временное проживание без учета квоты иностранным гражданам, которые:</span></span></p>\n<ul class=\"font_9\">\n<li>\n<p class=\"font_9\"><span>родились на территории России;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>состоят в браке с гражданином РФ, имеющим здесь место жительства;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>осуществили инвестиции в России в установленном правительством размере;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>поступили на военную службу;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>являются участниками государственной программы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом, и членами их семей, переселяющимися совместно с ними;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>не достигли возраста 18 лет (или достигли возраста 18 лет, но в соответствии с законодательством иностранного государства признаны недееспособными либо ограниченными в дееспособности, получают разрешение на временное проживание совместно с родителем&ndash;иностранным гражданином).</span></p>\n</li>\n</ul>\n<p class=\"font_9\"><span><span>Также предлагается предоставлять разрешение на временное проживание в РФ иностранцам, которые:</span></span></p>\n<ul class=\"font_9\">\n<li>\n<p class=\"font_9\"><span>являются гражданами государства, входившего в состав СССР;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>получили профессиональное образование в российской государственной образовательной организации;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>получили временное убежище в России, либо переселяются в Россию на постоянное место жительства в соответствии с международными договорами о регулировании процесса переселения и защите прав переселенцев.</span></p>\n</li>\n</ul>\n<p class=\"font_9\"><span>Кроме того, законопроектом предлагается сократить с шести до четырех месяцев сроки рассмотрения заявлений о выдаче разрешений на временное проживание.</span></p>\n<p class=\"font_9\"><span>Также предлагается расширить круг иностранных граждан, которые могут получить ВНЖ без получения разрешения на временное проживание.</span></p>\n<p class=\"font_9\"><span><span>К таким иностранным гражданам относятся:</span></span></p>\n<ul class=\"font_9\">\n<li>\n<p class=\"font_9\"><span>родившиеся на территории РСФСР и состоявшие в прошлом в гражданстве СССР;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>не достигшие возраста 18 лет, родившиеся на территории России, родитель которых является иностранным гражданином и постоянно проживает на территории России;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>имеющие родителя или ребенка, состоящих в российском гражданстве и постоянно проживающих на территории России;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>признанные носителем русского языка;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>иностранные граждане, которые сами либо родственники по прямой восходящей линии, усыновители или супруга которых были подвергнуты незаконной депортации с территории Крымской АССР, а также их родственники по прямой нисходящей линии, усыновленные дети или супруги, представившие справку о реабилитации;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>высококвалифицированные специалисты и члены их семей;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>осуществляющие не менее шести месяцев до дня обращения с заявлением о выдаче вида на жительство трудовую деятельность в РФ;</span></p>\n</li>\n<li>\n<p class=\"font_9\"><span>лица, проживающие на территории России, у которых прекращено гражданство РФ или в отношении которых отменено решение о его приобретении.</span></p>\n</li>\n</ul>\n<p class=\"font_9\"><span>&laquo;Новеллой законопроекта является введение бессрочного ВНЖ за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года).</span></p>\n<p class=\"font_9\"><span>При этом иностранные граждане, получившие вид на жительство без ограничения срока его действия, обязаны будут в течение двух месяцев со дня истечения очередного года со дня получения ими вида на жительство подать уведомление о подтверждении своего проживания в России в территориальный орган МВД России по месту своего жительства (при отсутствии места жительства - по месту пребывания).</span></p>\n<p class=\"font_9\"><span>По истечении каждого пятого года постоянного проживания в России такое уведомление надо будет подавать непосредственно в территориальный орган МВД России только путем личной явки иностранного гражданина&raquo;, - поясняется в сообщении.</span></p>\n<p class=\"font_9\"><span>В правительстве подчеркнули, что вид на жительство будет аннулирован, если непрерывно в течение любых двух календарных лет после получения вида на жительство иностранный гражданин не уведомит о подтверждении своего проживания в России.</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(288, 0, 1558960136, NULL, NULL, '<p>27 марта в 18 часов в Академии транспортных технологий (ул. Салова, д. 63) состоялось мероприятие \"Под крылом Петербурга\", на котором Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках проекта \"\"Цивилизованная миграция\". Правовое просвещение и оказание юридической помощи трудовым мигрантам и мигрантам- соотечественникам Санкт-Петербурга и Ленинградской области\", финансируемого Фондом-оператором Президентских грантов.</p>\n<p>В ходе мероприятия присутствовали специалисты органов государственной власти Санкт-Петербурга (социальной защиты, образования, здравоохранения, культуры и молодежной политики), а также сотрудники правоохранительных органов и представители социально ориентированных некоммерческих организаций, которые осуществляли консультирование мигрантов по вопросам получения социальной, медицинской, правовой и иных видов помощи.</p>\n<p>Гостей также ждало знакомство с традициями русского чаепития и развлекательная программа, на которой выступили национальные творческие коллективы Санкт-Петербурга. Для маленьких гостей мероприятия работали детские интерактивы и мастер-классы.</p>\n<p><img title=\"IMG_1937-01-04-19-02-17-min.JPG\" src=\"http://fondorientir.ru/filemanager/i/e172e9c6/IMG_1937-01-04-19-02-17-min.JPG\" alt=\"IMG_1937-01-04-19-02-17-min.JPG\" width=\"768\" height=\"768\" /></p>\n<p><img title=\"IMG_1941-01-04-19-02-17-min.JPG\" src=\"http://fondorientir.ru/filemanager/i/1d73c271/IMG_1941-01-04-19-02-17-min.JPG\" alt=\"IMG_1941-01-04-19-02-17-min.JPG\" width=\"768\" height=\"768\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(289, 0, 1565792714, NULL, NULL, '<p><span style=\"font-family: Arial;\">Работодателям необходимо постоянно контролировать численность иностранных граждан. Данное требование установлено Постановлением Правительства РФ от 14.11.2018 № 1365 об ограничении доли мигрантов.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Ограничения затрагивают компании различных отраслей, прежде всего строительной. В Санкт-Петербурге работает много компаний данной сферы, поэтому мы решили рассказать об ограничениях подробнее. &nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">В соответствии с данным документом, впервые было принято решение ограничить численность&nbsp;мигрантов&nbsp;на стройках. Раньше ограничений на иностранцев в строительстве не было и доля иностранцев на стройке доходила до 100%. Теперь на каждом объекте должно быть не более 80% иностранцев. Доля рассчитывается исходя из общей численности сотрудников.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Данное ограничение распространяется на всех иностранцев, временно пребывающих в России: из стран как с&nbsp;визовым, так и с&nbsp;безвизовым&nbsp;режимом. Исключение сделано только для граждан ЕАЭС.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Важно</strong>: Ограничение установлено на весь 2019 год. Так что нужно постоянно контролировать количество мигрантов в компании!&nbsp;&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\"><strong>Штраф</strong>&nbsp;за нарушение правил - до 1 млн руб. или приостановка деятельности до 3 месяцев, а для директора предусмотрен штраф до 50 000 руб. (ч. 1 ст. 18.17 КоАП).&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(290, 0, 1565792721, NULL, NULL, '<div class=\"b-material-wrapper__lead\">Россия ужимает потоки миграции в пользу переселения соотечественников и делает ставку на натурализацию. Такое заявление прозвучало вчера на международной конференции \"Глобальные тренды миграции 2020\" с участием 16 стран.</div>\n<div class=\"b-material-wrapper__text\">\n<p>- Мы находимся в точке поворота, когда миграция может стать и мотором экономического развития, - заявил директор Института диаспоры и интеграции Константин Затулин, - и национальным проклятием. Рост миграции может привести к ее неконтролируемым потокам, что запустит процессы \"неперевариваемости\" миграции, которые переживает ЕС.</p>\n<p>Дмитрий Демиденко, заместитель начальника ГУ по вопросам миграции МВД России, подтвердил, что у \"Концепции миграционной политики\" три приоритета\" - переселение, натурализация и адаптация тех, кто не пожелает интегрироваться. По данным МВД РФ, исторического максимума число мигрантов в стране достигло в 2018 году -17 миллионов. В 2019 году оно составляет 12,5 миллиона, а к концу может вырасти еще на 2-3 миллиона человек. При этом число нелегальных мигрантов сократилось с 3-4,5 миллиона до 2-3 миллионов в 2018 году. Выросло число патентов - разрешений на работу - до 1,6 миллиона.</p>\n<p>- Рост числа патентов внушительный, но мы рассчитывали на большее - до 3 миллионов. Это говорит о том, что нелегальная миграция снизилась, - говорит член Совета Федерации Людмила Бокова, - Росту управления миграцией способствуют введение информационных систем и комплексной оценки владения русским языком.</p>\n<p>По данным Института народно-хозяйственного прогнозирования РАН, 4,1 миллиона мигрантов успешно выдержали экзамен по русскому языку, 1,5 миллиона не сдали его, еще 2 миллиона пока отказались от сдачи.</p>\n<p>Неожиданны и итоги опроса среди ученых ИСЭПН РАН. 60 процентов из них считают, что миграция положительно влияет на развитие страны, 40 процентов - отрицательно.</p>\n<div class=\"incut\">Миграция может стать и мотором экономического развития,и национальным проклятием</div>\n<p>В ряду негативных черт миграции они назвали нагрузку на социальный сектор, криминал и недобросовестную конкуренцию на рынке труда (демпинг), бесправие мигрантов. В ряду благ, - рост качества сферы услуг и чистота улиц городов. Почти 91 процент ученых высказались за сокращение миграции и ужесточение условий для их работодателей.</p>\n<p>- Дружба и чай хороши, когда не очень сладкие, - своеобразно подвел итоги конференции Константин Затулин. - Это и есть особенности идущего поворота миграционной политики в сторону отказа от миграции как способа покрыть убыль населения. - Переориентация на переселенцев-соотечественников и мигрантов, желающих стать россиянами, - вот это шанс .</p>\n</div>', NULL, NULL, NULL, NULL, NULL, 1),
(291, 0, 1565792741, NULL, NULL, '<p><span style=\"font-family: Arial;\">Трудовой патент &ndash; это разрешительный документ, без которого вы не сможете принять на работу иностранца. Оформить&nbsp;патент&nbsp;- сложный и длительный процесс, тем неприятнее ситуация, когда его аннулируют. В последнее время случаи аннулирования патента участились.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Какие самые распространенные причины аннуляции?&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">1. Не вовремя оплаченные чеки за&nbsp;патент.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">Просрочка оплаты патента хотя бы на один день &ndash; это основание для аннулирования, поэтому сроки оплаты нужно отслеживать! Мы рекомендуем оплачивать патент за 3-4 дня до даты, которая указана на обороте патента.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">2.Неоднократное (два и более раза) привлечение к административной ответственности. Если иностранный гражданин совершит два и более правонарушения, его патент аннулируют. Причем это не обязательно нарушения в сфере миграции: например, переход дороги на красный свет или неправильная парковка.</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;3. Поддельные или подложные документы у иностранца. Если за время нахождения в РФ иностранный гражданин оформлял какие-либо нелегальные документы, например, поддельную&nbsp;регистрацию, патент такому иностранцу аннулируют, и поставят запрет на въезд.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">4. Непредоставление копии трудового договора. Многие иностранные граждане забывают, что в течение 2 месяцев после оформления патента нужно направить копию трудового или гражданско-правового договора в УВМ ГУ МВД.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">5. Неверные реквизиты при оплате&nbsp;патента. Если допустить несколько ошибок в реквизитах на оплату, такой авансовый платеж не зачтут. А значит, патент иностранца будет аннулирован.&nbsp;</span></p>\n<p><span style=\"font-family: Arial;\">&nbsp;</span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(292, 0, 1565792751, NULL, NULL, '<p>Россия будет выплачивать пенсии трудовым мигрантам из стран Евразийского экономического союза (ЕАЭС), куда помимо РФ входят Армения, Беларусь, Казахстан и Киргизия. Это предусмотрено Договором о пенсионном обеспечении трудящихся государств - членов Евразийского экономического союза (ЕАЭС) &ndash; ​​которое сейчас готовится к подписанию.</p>\n<p>Директор Департамента трудовой миграции и социальной защиты Евразийской экономической комиссии Паиза Суюмбаева рассказала особенностях соглашения:</p>\n<p>&laquo;Основной целью договора является обеспечение равных прав в сфере пенсионного обеспечения, защита приобретенных трудящимися пенсионных прав в странах Союза, а также развитие сотрудничества в сфере пенсионного обеспечения между государствами-членами\".</p>\n<p>Вступление в силу договора даст гражданам стран ЕАЭС возможность формировать, сохранять и реализовывать пенсионные права, приобретенные ими при осуществлении трудовой деятельности на территориях государств-членов&raquo;.</p>\n<p>Пенсия будет выплачиваться государством, назначившим ее, в том числе, в случае проживания трудящегося на территории другой страны Союза. Трудящийся будет иметь право на получение пенсии на территории государства проживания либо государства, назначившего пенсию.<br /> При определении права на пенсию и ее начислении будет учитываться стаж работы, приобретенный на территориях государств - членов Союза как до, так и после вступления в силу договора о пенсионном обеспечении, а также стаж, приобретенный на территории бывшего СССР.</p>\n<p>Формирование и реализация пенсионных прав трудящихся будут осуществляться на тех же условиях и в том же порядке, что и для граждан государства трудоустройства, как в части уплаты взносов, так и приобретения, и сохранения пенсионных прав.</p>\n<p>Для получения пенсии в будущем важно, чтобы трудящиеся официально оформляли трудовые отношения с работодателями &ndash; заключали трудовые договоры, тем самым формировали свои пенсионные права.</p>\n<p>Проект соглашения Евразийская экономическая комиссия опубликовала еще в марте, а в мае российский Минтруд направил на рассмотрение в Экспертный совет при правительстве соответствующий пакет документов. Планируется, что соглашение будет ратифицировано до конца 2019 года.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(293, 0, 1565792787, NULL, NULL, '<p>МВД &nbsp;предлагает установить новый платеж за возможность вести трудовую деятельность для иностранцев, временно проживающих в РФ или использующих вид на жительство.</p>\n<p>&nbsp;По мнению ведомства, действующая для них пошлина, дающая право на работу, слишком мала по сравнению со стоимостью патента для остальных трудовых мигрантов, страховые взносы они не платят, но имеют возможность пользоваться социальными услугами.</p>\n<p>Это нововведение коснется мигрантов, которые работают в России на основании вида на жительство (ВНЖ) или разрешения на временное проживание (РВП), по закону они вправе работать без получения патента. В ближайшее время ведомство предложит соответствующую инициативу для согласования Минтруду и Минэкономики.</p>\n<p>Как следует из текста предложений МВД, тех, кого может коснуться новое регулирование, в РФ более 1 млн человек. Из них около 530 тыс. человек находятся в стране на основании РВП и более 600 тыс.&mdash; на основании ВНЖ. Сейчас они имеют право работать, заплатив государственную пошлину (1,6 тыс. руб. и 3,5 тыс. руб. соответственно). Это обеспечивает им право на социальное и медицинское страхование, а их детям &mdash; на посещение детских садов и школ, при этом такие работники не платят социальные взносы.</p>\n<p>Те же, кто прибыл в РФ без визы, работают на основании патента и уплачивают 30&ndash;60 тыс. руб. в год в бюджет региона проживания. Чтобы избежать этих платежей, иностранные работники стремятся получить РВП или ВНЖ, &laquo;не планируя дальнейшую интеграцию в российское общество&raquo;, отмечает МВД. Поэтому, по мнению ведомства, на тех из них, кто работает по найму у физлиц, необходимо распространить обязательство по покупке патента. Соответствующие поправки министерство предлагает внести в Налоговый и Бюджетный кодексы.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(294, 0, 1565792771, NULL, NULL, '<p><strong>Успешность ресторанной отрасли&nbsp;&mdash; не&nbsp;только показатель благосостояния граждан. Она дает толчок другим сферам, причем не&nbsp;только связанным с&nbsp;производством продуктов или&nbsp;поставкой напитков. Неизбежные издержки помогает сократить более дешевая рабочая сила. Чаще всего это мигранты, оформление пребывания которых в&nbsp;Петербурге&nbsp;&mdash; сложившаяся индустрия и&nbsp;прибыльный теневой бизнес (см. \"ДП\" № 60 от&nbsp;25.04.2019).</strong></p>\n<p>Исследование, проведенное \"ДП\", показало, что спустя 3 года после ареста&nbsp;Дмитрия Михальченко&nbsp;часть созданной им империи, ориентированная на монетизацию процесса легализации мигрантов, хоть и перешла в другие руки, но продолжает развиваться, принося своим новым хозяевам и аффилированным с ними лицам огромные барыши. По самым скромным подсчетам &mdash; 6 млрд теневых рублей в год. По другим данным, объемы рынка доходят до 25 млрд рублей.</p>\n<p>Главная фишка этой теневой империи &mdash; совпадение до полного смешения функций государственных органов и коммерческих организаций, которым на откуп отдается работа по приему и легализации мигрантов. Однако, как показывает практика, и сами коммерческие пользователи оформившихся легализационных каналов начинают задумываться о монетизации знания о том, как это работает, &mdash; с явной выгодой для себя. Однако приносить доход эта история может только в одном случае: если органы власти закрывают на нее глаза.</p>\n<p>В середине мая в Петербург прибыла начальник Главного управления по вопросам миграции&nbsp;России Валентина Казакова (на фото). Визит связан с итогами федеральной проверки петербургской полиции. Был выявлен, в частности, факт создания условий, \"при которых десятки тысяч иностранных граждан незаконно находятся на территории РФ\". В зоне пристального внимания силовиков теперь оказались не только их коллеги, но и бизнесмены, выступающие мотором всей миграционной схемы.</p>\n<p>Если в мире физических явлений действует закон сохранения энергии (в одном месте прибавилось &mdash; в другом обязательно убудет), то в сфере бизнеса одна из главных потребностей &mdash; сокращение издержек. И зачастую основная статья, которая попадает под оптимизацию, &mdash; кадры. Особенно это проявляется в отраслях, где сотрудники в прямом смысле &mdash; лицо компании. Анализ вакансий одной из крупных ресторанных групп, размещенных на известном job&ndash;сайте, по всей видимости, подтверждает связь между кухней и сотрудниками. Но не известную выгоду для работников (\"поближе к кухне, подальше от начальства\"), а скорее для работодателя: \"подальше от клиента &mdash; меньше цифры в зарплатной ведомости\".</p>\n<p>Весьма затратная категория персонала &mdash; официанты: предлагаемая зарплата &mdash; от 40 тыс. до \"от 100 тысяч\". От будущих работников ждут и \"вежливости\", и \"грамотной, хорошо поставленной речи\". Редко когда в описании вакансии специально упоминается, что эта речь должна быть именно русской. Видимо, самоочевидно, что официант не должен быть \"мечтой ксенофоба\". А вот поварам правильная речь, судя по всему, ни к чему. Но им необходимо быть в состоянии понять \"старшего по званию\" на кухне, так что более чем в половине вакансий требуется знание русского языка &mdash; на уровне родного, свободное или просто владение им без какой&ndash;то конкретизации (к слову, в требованиях к су&ndash; или шеф&ndash;поварам про русский &mdash; вообще ни слова). Но зато и предлагаемая зарплата зачастую меньше официантской.</p>\n<p>А для обслуживающего персонала русский &mdash; вообще, похоже, не главный навык, в конце концов, уборка помещений и мытье посуды особых коммуникаций не требуют. Так что неудивительны следующие объявления. \"В рестораны (разные районы города) требуются уборщицы и посудомойщицы. Помогаем с оформлением иностранцам\", &mdash; сообщает, например, осенний пост в группе \"Работа в Ginza Project\" в соцсети&nbsp;\"ВКонтакте\". Указанный в объявлении контакт используется ООО \"Гинза Стаф\".</p>\n<p>\"Наша группа &mdash; сеть самостоятельных юридических лиц (ресторанов), работающих по франшизе&nbsp;Ginza. Рестораны принимают на работу иностранных граждан с оформленными документами. Но юридические лица могут пользоваться услугами кадровых агентств\", &mdash; пояснил \"ДП\" Вадим Лапин, до середины 2018 года являвшийся совладельцем этой, а также других компаний, управляющих ресторанами группы Ginza (сейчас совладелец компании &mdash; его сын Марк Лапин). Точно так же ответили на запрос \"ДП\" в самой группе компаний.</p>\n<p><strong>Высшие силы</strong></p>\n<p>В современной России у многих бизнесменов вошло в практику в начале больших проектов заручаться благосклонностью высших сил, освящая офис, объект или закладной камень. Но благословение священника &mdash; не 100%&ndash;ная гарантия успеха и поэтому не совсем обязательная опция.</p>\n<p>Куда более верный признак успешных проектов, связанных с предоставлением услуг, где последнее слово за контрольными или правоохранительными органами, &mdash; близкие, почти (или в полном смысле) родственные отношения. Крепкие браки, опять же, основаны на общности жизненного опыта, круга общения, интересов и увлечений. То есть лучше себя друг с другом чувствуют люди из одной системы или хотя бы соприкасающихся сфер. Неудивительно, что врачи женятся на врачах, а у сотрудников правоохранительных органов обычно лучше складывается с себе подобными или \"смежниками\", например, из судебной системы.</p>\n<p>По некоторым данным, Евгения Боричева карьеру могла начать именно в ней. А ее супруг &mdash; по одной из версий &mdash; работает в&nbsp;УФСБ&nbsp;по Петербургу и Ленобласти на руководящей должности и курирует, в частности, Василеостровский район. Впрочем, на момент подготовки материала ответа на запрос в УФСБ о том, работает ли там Боричев, получено не было.</p>\n<p>Как показала история краха Дмитрия Михальченко, выходцы из правоохранительной системы легко вписываются в бизнес по легализации мигрантов. Но эффективной эта система будет только до тех пор, пока государство смотрит на нее сквозь пальцы.</p>\n<p><img title=\"C76440D4-F149-4B26-86EB-8490594CBF5E.png\" src=\"http://fondorientir.ru/filemanager/i/ab763151/C76440D4-F149-4B26-86EB-8490594CBF5E.png\" alt=\"C76440D4-F149-4B26-86EB-8490594CBF5E.png\" width=\"768\" height=\"768\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(295, 0, 1565259266, NULL, NULL, '<p>В целях создания дополнительных условий для более качественного консульского обслуживания граждан Республики&nbsp;Узбекистан с 1 июля 2019 года на базе Консульского отдела Посольства Республики Узбекистан в Российской Федерации введен в эксплуатацию семиканальный Call-centеr.</p>\n<p>Call-centеr&nbsp;позволяет гражданам Узбекистана в автоматическом режиме получать необходимую информацию консульско-правового характера на узбекском и русском языках (адреса, режим работы, распределение консульских учреждений по регионам России, сведения о предоставляемых услугах и требуемых для каждой услуги документах).</p>\n<p>Имеется опция соединения с оператором для уточнения тех или иных вопросов. Автоответчик подсказывает звонящему лицу алгоритм действий для получения информации или соединения с оператором, а также сообщает, что операторы в ближайшее время смогут ответить и каким по счету в очереди на ожидании находится заявитель. В целях контроля качества оказываемых услуг все звонки записываются.</p>\n<p>- Генеральное консульство Республики Узбекистан в г.Санкт-Петербурге (Северо-Западный федеральный округ):</p>\n<p>190005, г.Санкт-Петербург, ул. 4-я Красноармейская, д.4А. e-mail: gkruspb@mail.ru, Тел: +7 812 6010628, контакт в Telegram+7-965-029-41-49.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(296, 0, 1565259694, NULL, NULL, '<p>Государственное Собрание - Курултай Республики Башкортостан в лице депутата Государственной думы Рафаэля Марданшина (&laquo;Единая Россия&raquo;) внесло в Госдуму законопроект, которым предлагается дополнить санкции статей Уголовного кодекса РФ о фиктивной регистрации иностранных граждан обязательными и исправительными работами за фиктивную регистрацию иностранных граждан. Документ размещен в электронной базе нижней палаты парламента.</p>\n<p>&laquo;Предлагается дополнить санкции ст. 322.2 (&laquo;Фиктивная регистрация гражданина Российской Федерации по месту пребывания или по месту жительства в жилом помещении в Российской Федерации и фиктивная регистрация иностранного гражданина или лица без гражданства по месту жительства в жилом помещении в Российской Федерации, - прим. Агентства &laquo;Москва&raquo;) и 322.3 (Фиктивная постановка на учет иностранного гражданина или лица без гражданства по месту пребывания в Российской Федерации, - прим. Агентства &laquo;Москва&raquo;) УК РФ обязательными работами на срок до 360 часов либо исправительными работами на срок до одного года&raquo;, - говорится в пояснительной записке.</p>\n<p>В настоящее время фиктивная регистрация гражданина РФ по месту пребывания или по месту жительства в жилом помещении в Российской Федерации, а равно фиктивная регистрация иностранного гражданина или лица без гражданства по месту жительства в жилом помещении в Российской Федерации наказывается штрафом от 100 тыс. до 500 тыс. руб. или в размере заработной платы или иного дохода осужденного за период до трех лет, либо принудительными работами на срок до трех лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до трех лет или без такового, либо лишением свободы на срок до трех лет с лишением права занимать определенные должности или заниматься определенной деятельностью на срок до трех лет или без такового.</p>\n<p>Как отмечается в документе, законопроект направлен на обеспечение общественной безопасности и правопорядка, профилактику криминогенных проявлений, а также на совершенствование правового регулирования вопросов, которые связаны с противодействием незаконной миграции.</p>\n<p>Подчеркивается, что целью законопроекта является расширение круга лиц, которые объективно смогут понести бремя наказания за совершение преступлений, предусмотренных статьями 322.2 и 322.3 УК РФ. По мнению автора законопроекта, это поможет усилить соблюдение принципа неотвратимости наказания.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(297, 0, 1565259681, NULL, NULL, '<p>Граждане, которые получили российский паспорт до 1 января 2010 года, но затем утратили гражданство РФ, смогут повторно приобрести его в упрощённом порядке. При этом срок действия такой льготы может быть продлён на пять лет &mdash; до 2025 года.</p>\n<p>По действующему законодательству приобрести ранее утраченное гражданство России могут граждане бывшего СССР, которые получили российские паспорта до 1 июля 2002 года (когда был принят современный закон о гражданстве), но впоследствии утратили статус гражданина.</p>\n<p>Сюда относятся случаи, когда органы власти &laquo;не определили наличие гражданства&raquo;, то есть когда по каким-то причинам эти сведения отсутствуют в базах данных правоохранительных органов. Также к этой категории относятся люди, которым аннулировали гражданство, так как они при его оформлении предоставили неточные или недостоверные сведения.</p>\n<p>&laquo;Человек получил паспорт, но не знал, что какой-то порядок был нарушен &mdash; не по его вине, а потому что какое-то решение или оформление было неправильным&raquo;, &mdash; пояснял &laquo;Парламентской газете&raquo; соавтор инициативы, зампредседателя комитета Госдумы по развитию гражданского общества, вопросам общественных и религиозных объединений&nbsp;Дмитрий Вяткин.</p>\n<p>По словам же депутатов, после 2002 года ситуаций, когда люди лишались российских паспортов по причине несоответствия каких-то данных, не стало меньше. Такие случаи сошли на нет только через восемь лет, когда к концу 2009 года было завершено формирование единой системы миграционного учёта иностранных граждан и лиц без гражданств.</p>\n<p>Поэтому они предлагают расширить формат применения данной льготы на людей, который получили российское гражданство до 1 января 2010 (а не июля 2009 года, как сейчас), а также продлить срок применения самой льготы до 1 января 2025 года.</p>\n<p>Соответствующий законопроект готовится ко второму чтению в Госдуме. Предварительно, 9 июля, состоится обсуждение инициативы на заседании комитета палаты по развитию гражданского общества, вопросам общественных и религиозных объединений.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(298, 0, 1565259655, NULL, NULL, '<p>МОСКВА, 9 июля. /ТАСС/. Госдума приняла в первом чтении законопроект, предусматривающий увеличение размера государственной пошлины за выдачу вида на жительство с 3 500 рублей до 5 000 рублей. Законопроект был инициирован правительством, поправки вносятся в Налоговый кодекс РФ.</p>\n<p>Такое увеличение связано с введением проекта федерального закона \"О внесении изменений в Федеральный закон \"О правовом положении иностранных граждан в РФ\" и отдельные законодательные акты Российской Федерации в части упрощения порядка предоставления некоторым категориям иностранных граждан и лиц без гражданства разрешения на временное проживание и вида на жительство\" бессрочного вида на жительство, за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года).</p>\n<p>Согласно сведениям отчетности МВД России, в 2018 году территориальными органами МВД России выдано 191 445 видов на жительство (в 2017 году - 185 800), продлен срок действия 28 331 вида на жительство (в 2017 году - 34 002).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(299, 0, 1565260203, NULL, NULL, '<p>МОСКВА, 11 июл - РИА Новости.&nbsp;Министерство внутренних дел России обсуждает с экспертами вопрос о введении новых платежей для живущих в России иностранцев; по мнению ведомства, это упростит им получение документов, сообщили в МВД в ответ на запрос РИА Новости.</p>\n<p>&laquo;МВД России в инициативном порядке прорабатывается вопрос по внесению в законодательство Российской Федерации изменений, касающихся уплаты налога на доходы отдельными категориями иностранных граждан, осуществляющих трудовую деятельность по найму у физических лиц для личных, домашних и иных подобных нужд&raquo;, &mdash;&nbsp;говорится в ответе на запрос.</p>\n<p>Как объяснили в министерстве, сейчас иностранные граждане, живущие в России на основании разрешения на временное проживание (РВП) и вида на жительство (ВНЖ) должны ежегодно подтверждать наличие у них дохода и сам факт проживания в стране. Однако те иностранцы, которые работают на физических лиц, не всегда могут оформить все необходимые документы.</p>\n<p>&laquo;В этой связи на площадке ГУВМ МВД России с привлечением научного сообщества ведется обсуждение целесообразности установления для временно и постоянно проживающих в Российской Федерации \"самозанятых\" иностранных граждан авансовых платежей по аналогии с платежами, установленными для иностранных граждан, осуществляющих трудовую деятельность на основании патента&raquo;, &mdash;&nbsp;говорится в ответе на запрос РИА Новости.</p>\n<p>Новый платеж, целесообразность введения которого сейчас обсуждается с &laquo;привлечением научного сообщества&raquo;, по мнению МВД, сможет упростить жизнь таким приезжим, так как факт уплаты платежа и подоходного налога одновременно доказывает и факт проживания в стране, и наличие средств к существованию.</p>\n<p>Введение нового платежа, считают в МВД, минимизирует риски аннулирования выданных &laquo;самозанятым&raquo;&nbsp;иностранцам разрешений на проживание и видов на жительство.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(300, 0, 1565792819, NULL, NULL, '<p>За&nbsp;первые пять месяцев в&nbsp;Россию переселились 41,3 тысячи человек по&nbsp;государственной программе по&nbsp;возвращению соотечественников. Это составляет более 50% от&nbsp;планового показателя 2019 года, сообщили &laquo;Ридусу&raquo; в&nbsp;МВД России.</p>\n<p>&nbsp;В&nbsp;прошлом году воспользовались программой примерно 108 тысяч человек, что стало на&nbsp;19,2% выше запланированного показателя (90,6 тысяч участников).</p>\n<p>Кроме того, в&nbsp;МВД зафиксировали рост числа переселенцев в&nbsp;Россию из&nbsp;стран ближнего зарубежья за&nbsp;последние годы. Если в&nbsp;период с&nbsp;2007 по&nbsp;2013 годы в&nbsp;Россию переезжали в&nbsp;среднем около 20 тысяч соотечественников, то&nbsp;с&nbsp;2014 года число число переселенцев возросло до&nbsp;135 тысяч.</p>\n<p>Всего по&nbsp;данным правоохранительных органов, в&nbsp;Россию с&nbsp;момента действия закона&nbsp;переехало около 826 тысяч человек.</p>\n<p>Напомним, государственная программа по&nbsp;оказанию содействия добровольному переселению в&nbsp;РФ соотечественников реализуется с&nbsp;2007 года. Первоначально в&nbsp;ней участвовало 12 субъектов России. Сейчас это число выросло до 75. Наибольший интерес для переселенцев представляет Центральный федеральный округ и&nbsp;Сибирский федеральный округ.</p>\n<p>Ранее &laquo;Ридус&raquo; писал, что за&nbsp;первые шесть месяцев этого года&nbsp;в&nbsp;Россию&nbsp;въехало&nbsp;свыше восьми миллионов иностранных граждан. При этом из&nbsp;них четыре миллиона составили временные трудовые мигранты. В&nbsp;РФ в&nbsp;основном приезжают граждане Узбекистана (37,2% от&nbsp;всех мигрантов), Таджикистана (21,5%) и&nbsp;Киргизии (11,4%).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(301, 0, 1565792826, NULL, NULL, '<p>МОСКВА, 11 июля. /ТАСС/. Госдума в четверг приняла во втором чтении законопроект, который позволит вернуть российские паспорта лицам, которым они были выданы до 1 января 2010 года и позже признаны недействительными в связи с ошибками в документах.</p>\n<p>Как пояснил журналистам глава профильного думского комитета по развитию гражданского общества, вопросам общественных и религиозных организаций Сергей Гаврилов, данная инициатива является по существу \"амнистией для примерно 100 тыс. иностранных граждан, которые в конце 90-х и в 2000-е годы без необходимых документальных оснований или с допущением ошибок государственных органов получили российской паспорт, в том числе находясь за границей России\".</p>\n<p>&laquo;Мы предоставляем им право подать в миграционное подразделение органа внутренних дел по месту жительства заявление о приеме в российское гражданство, они в течение трех месяцев получат гражданство без всяких дополнительных условий&raquo;, - продолжил он.</p>\n<p>&laquo;Даже той категории лиц, предоставивших миграционным органам заведомо ложные сведения и получивших на их основании российский паспорт до 2010 года, в связи с чем паспорт у них был изъят, мы даем возможность продолжать законно находиться в России на основании бессрочного вида на жительство, у них есть право после его получения претендовать на российское гражданство&raquo;, &mdash;&nbsp;резюмировал депутат.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(302, 0, 1565260225, NULL, NULL, '<p>Иностранный гражданин работает в&nbsp;России. Может&nbsp;ли вид на&nbsp;жительство в&nbsp;РФ подтвердить продолжительность его пребывания его в&nbsp;России? Нет, поскольку при&nbsp;определении налогового статуса наличие у&nbsp;физлица вида на&nbsp;жительства значения не&nbsp;имеет. Такой вывод следует из&nbsp;письма Минфина России&nbsp;от&nbsp;24.06.19 №&nbsp;03-04-05/46120.</p>\n<p>Напомним, что&nbsp;налоговые резиденты РФ&nbsp;&mdash; это физические лица, которые фактически находятся в&nbsp;Российской Федерации не&nbsp;менее 183 календарных дней в&nbsp;течение 12 следующих подряд месяцев (п.&nbsp;2 ст.&nbsp;207&nbsp;НК РФ). Налоговые резиденты РФ платят НДФЛ по&nbsp;&laquo;обычной&raquo; ставке&nbsp;&mdash; 13%, а&nbsp;нерезиденты&nbsp;&mdash; по&nbsp;повышенной ставке 30% (п.&nbsp;3 ст.&nbsp;224&nbsp;НК РФ).</p>\n<p>183 дня пребывания в&nbsp;РФ, по&nbsp;достижении которых физлицо признается налоговым резидентом, исчисляются путем суммирования всех календарных дней, в&nbsp;которые гражданин находился в&nbsp;России в&nbsp;течение 12 следующих подряд месяцев. При&nbsp;определении налогового статуса учитывается фактическое время нахождения гражданина в&nbsp;РФ, которое должно быть документально подтверждено.</p>\n<p>Как подтвердить срок нахождения на&nbsp;территории РФ? Можно&nbsp;ли использовать для&nbsp;этой цели отметку о&nbsp;виде на&nbsp;жительство иностранного гражданина в&nbsp;РФ? В&nbsp;Минфине поясняют, что&nbsp;вид на&nbsp;жительство представляет собой документ, выданный иностранному гражданину или&nbsp;лицу без&nbsp;гражданства в&nbsp;подтверждение их права на&nbsp;постоянное проживание в&nbsp;РФ, а&nbsp;также их права на&nbsp;свободный въезд и&nbsp;выезд из&nbsp;России.</p>\n<p>Таким образом, вид на&nbsp;жительство не&nbsp;подтверждает фактическое время нахождения физического лица на&nbsp;территории страны.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(303, 0, 1565260230, NULL, NULL, '<p>Категории иностранных граждан, которые могут получить разрешение на временное проживание (РВП) и вид на жительство (ВНЖ) в упрощённом порядке в скором времени может быть расширен. При этом срок рассмотрения заявлений на выдачу РВП сократится с полугода до четырёх месяцев, а вид на жительство станет бессрочным.</p>\n<p>Такие поправки в миграционное законодательство предусмотрены законопроектом, который Госдума планирует рассмотреть во втором чтении на пленарном заседании 17 июля.</p>\n<p>В частности, предлагается дать право получить РВП вне квоты гражданам, которые:</p>\n<ul>\n<li>родились на территории России;</li>\n<li>являются гражданами Украины (или лицами без гражданства) и имеют статус беженца или временное убежище в России;</li>\n<li>переселяются в Россию на постоянное жительство в рамках международного договора о регулировании процесса переселения и защите прав беженцев (заключено с Азербайджаном, Туркменистаном, Латвией, Грузией, Арменией и прочее).</li>\n</ul>\n<p>Ранее к этой категории предлагалось также отнести выпускников российских вузов, которые имеют гражданство страны бывшего СССР. Однако в ходе подготовки документа ко второму чтению было предложено предоставлять иностранцам, с отличием окончившим российские вузы, сразу вид на жительство, то есть вообще не обременять их оформлением РВП.&nbsp;Такую поправку рекомендовал принять Комитет Госдумы по развитию гражданского общества, вопросам общественных и религиозных объединений.</p>\n<p>В текущей версии законопроекта право оформлять ВНЖ без предварительного получения РВП предлагается целому ряду категорий иностранцев. Среди них:</p>\n<ul>\n<li>граждане, которые родились в РСФСР и в прошлом имели гражданство СССР;</li>\n<li>дети, которые родились на территории России, чьи родители-иностранцы имеют вид на жительство;</li>\n<li>граждане, чьи родители или дети имеют гражданство РФ и постоянно здесь проживают;</li>\n<li>иностранцы, которые проработали в России не меньше полугода в качестве квалифицированных специалистов;</li>\n<li>люди, которые проживают на территории России и у которых было прекращено гражданство РФ (кроме осуждённых по тяжким статьям УК РФ, прежде всего за терроризм).</li>\n</ul>\n<p>В ходе второго чтения в документ также может быть внесена поправка, которая позволит квалифицированным специалистам получить бессрочный ВНЖ наравне с остальными иностранными гражданами.</p>\n<p>К слову, в связи с тем, что вид на жительство станет бессрочным, Правительство предлагает повысить размер государственной пошлины за его выдачу &mdash; с 3,5 тысяч рублей до 5 тысяч рублей. Законопроект с такой инициативой Госдума также рассмотрит во втором чтении 17 июля.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(304, 0, 1565260236, NULL, NULL, '<p>МОСКВА, 23 июл - РИА Новости.&nbsp;Госдума приняла закон об увеличении до 5 тысяч с 3,5 тысячи рублей размера госпошлины за выдачу вида на жительство иностранному гражданину или лицу без гражданства, в том числе в случае его замены.</p>\n<p>Данное изменение вносится в связи с введением нового документа о виде на жительство, содержащего электронный носитель, пояснял ранее заместитель министра финансов РФ Илья Трунин.</p>\n<p>Аналогичный размер госпошлины сейчас установлен за выдачу загранпаспорта, содержащего такой носитель (паспорта нового поколения). Закон должен вступить в силу через 90 дней после его официального опубликования.</p>\n<p>Госдума во вторник также приняла закон, который упрощает порядок предоставления иностранцам и лицам без гражданства, владеющим русским языком и желающим жить и работать в России, разрешений на временное пребывание и видов на жительство.</p>\n<p>На данный момент вид на жительство необходимо продлевать каждые пять лет, а этот закон предусматривает выдачу бессрочного вида на жительство, за исключением отдельных случаев.</p>\n<p>Высококвалифицированные специалисты и члены их семей получат его на срок действия разрешения на работу, а квалифицированные специалисты - на три года.</p>\n<p>Поскольку вид на жительство станет бессрочным, предлагается повысить и госпошлину за его выдачу, уровняв ее с госпошлиной, которую уплачивают россияне при оформлении загранпаспорта, пояснял ранее РИА Новости заместитель председателя комитета по бюджету и налогам Леонид Симановский.</p>\n<p>В 2018 году территориальными органами МВД России выдано 191,445 тысячи видов на жительство (в 2017 году &ndash; 185,8 тысячи), а также продлен срок действия 28,331 тысячи видов на жительство (в 2017 году - 34,002 тысячи).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(305, 0, 1565260191, NULL, NULL, '<p>МОСКВА, 26 июл - РИА Новости.&nbsp;Совет Федерации одобрил в пятницу закон, упрощающий выдачу разрешения на временное проживание в России отдельным категориям иностранцев без учета квоты.</p>\n<p>Сокращается с 6 до 4 месяцев срок рассмотрения заявлений о выдаче разрешений на временное проживание. Также разрешение на временное проживание будет выдаваться отдельным категориям иностранных граждан без учёта квоты. Эта норма будет распространяться на граждан, которые родились в России, состоят в браке с гражданином РФ, являются участниками госпрограммы по оказанию содействия добровольному переселению в РФ соотечественников, признаны беженцами либо получившие временное убежище в России.</p>\n<p>Вводится практика предоставления бессрочного вида на жительство (в настоящее время по общему правилу он выдается на пять лет), за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года).</p>\n<p>Расширяется круг иностранных граждан, которые могут получить вид на жительство без получения разрешения на временное проживание. К таким иностранным гражданам относятся родившиеся на территории РСФСР и имевшие в прошлом гражданство СССР, а также те, кто был депортирован с территории Крымской автономной союзной республики и их родственники по прямой линии. Вид на жительство также смогут получить родившиеся в России дети, которых усыновили иностранцы.</p>\n<p>Помимо этого, уточняется перечень случаев, при которых разрешение на временное проживание может быть выдано иностранному гражданину без учета квоты правительства.</p>\n<p>Теперь оно должно выдаваться иностранцу, который прожил в РФ не менее одного года на основании разрешения на временное проживание и которому вид на жительство выдается без получения разрешения на временное проживание. Также разрешение несовершеннолетним иностранцам будет выдаваться на срок временного проживания его родителя или опекуна.</p>\n<p>Кроме того, на срок временного проживания родителя будет выдаваться разрешение совершеннолетним иностранцам, если родитель признан недееспособным или ограниченным в дееспособности.</p>\n<p>На эти же категории распространяется право получить вид на жительство без разрешения на временное проживание, а также на тех, кто признан носителем русского языка в соответствии с ФЗ &laquo;О гражданстве Российской Федерации&raquo;&nbsp;и успешно закончил российский вуз по очной форме обучения и получил документ об образовании и о квалификации с отличием.</p>\n<p>При рассмотрении в Госдуме отмечалось, что этим законом смогут воспользоваться и граждане Украины (независимо от места постоянного проживания на Украине), планирующие жить и работать в РФ на протяжении достаточно длительного периода времени.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(306, 0, 1565260182, NULL, NULL, '<p>МОСКВА, 26 июл - РИА Новости.&nbsp;Президент РФ Владимир Путин подписал закон, позволяющий получать российские паспорта лицам, которым они были выданы до 1 января 2010 года, но позже признаны недействительными в связи с ошибками в документах.</p>\n<p>Соответствующий документ, опубликованный на официальном интернет-портале правовой информации, предусматривает предоставление права получить российский паспорт в упрощенном порядке лицам, имевшим гражданство СССР, которым он был выдан до 1 января 2010 года, но в отношении которых затем установили факт отсутствия гражданства, а паспорт был изъят или признан недействительным. Отмечается, что эта мера коснется тех, решение о приеме в гражданство которых никогда не принималось, а паспорт был выдан ошибочно, либо первичные документы, подтверждающие гражданство РФ, были утрачены.</p>\n<p>&laquo;Предлагается предоставить право получить в упрощенном порядке российское гражданство лицам, имевшим гражданство бывшего СССР, получившим паспорт гражданина Российской Федерации до 1 января 2010 года&raquo;, &mdash;&nbsp;отмечалось в пояснительной записке.</p>\n<p>Подчеркивалось, что дата - 1 января 2010 года - обусловлена тем, что в 2009 году в основном было завершено формирование системы миграционного учета иностранных граждан и лиц без гражданства.</p>\n<p>&laquo;В этой связи с 1 января 2010 года число лиц, которые были приняты в гражданство Российской Федерации и получили паспорт гражданина РФ на основании подложных документов или заведомо ложных либо недостоверных сведений, является крайне незначительным&raquo;, &mdash;&nbsp;добавлялось в пояснительной записке.</p>\n<p>Из документа следует, что такие лица до 1 января 2025 года смогут подать заявление о признании их гражданами РФ без выполнения каких-либо дополнительных условий.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(307, 0, 1565260308, NULL, NULL, '<p>Минэкономразвития предложило предоставлять иностранным высококвалифицированным специалистам&nbsp;российское гражданство в упрощённом порядке. Соответствующий пакет законопроектов размещён на портале проектов нормативных правовых актов.</p>\n<p>В пояснительной записке к одному из проектов отмечается, что это необходимо для привлечения на постоянное место жительства в Россию наиболее востребованных иностранных граждан и создания дополнительных условий ведения бизнеса и повышения инвестиционной привлекательности.</p>\n<p>При этом, согласно проекту, гражданство в упрощённом порядке будет предоставляться этой категории специалистов без соблюдения условий о проживании на территории России в течение пяти лет и отказа от имеющегося у них иного гражданства.&nbsp;</p>\n<p>Минэкономразвития также предложило снизить минимальный порог заработной платы, которая полагается в России иностранному высококвалифицированному специалисту.</p>\n<p>Сейчас, согласно закону &laquo;О правовом положении иностранных граждан в РФ&raquo;, среди критериев статуса высококвалифицированного специалиста прописан размер зарплаты, который должен составлять не менее 167 тысяч рублей за один месяц (2 миллиона рублей в год).</p>\n<p>Чтобы создать благоприятные условия для привлечения таких работников, законопроектом предлагается снизить размер ежемесячной зарплаты как критерия статуса высококвалифицированного специалиста до 100 тысяч рублей и установить её выплату пропорционально отработанному времени.</p>\n<p>Отмечается, что решение этого вопроса позволит увеличить количество специалистов в среднесрочной перспективе, что позитивно повлияет на изменения в инвестиционном климате, а также на формирование инновационной экономики.</p>\n<p>&nbsp;</p>\n<p>Также предлагается ввести повышенную стоимость госпошлины за выдачу разрешения на работу высококвалифицированному специалисту. Предполагается, что размер этой пошлины будет установлен на уровне минимального платежа по налогу на доходы высококвалифицированного специалиста за месяц &mdash; 13 тысяч рублей.</p>\n<p>Как отмечается в сводном отчёте, предложенная мера призвана снизить риски злоупотребления работодателями права на привлечение иностранных сотрудников в качестве высококвалифицированных специалистов.&nbsp;</p>\n<p>Кроме того, проектом предлагается усовершенствовать контроль соблюдения работодателями условий привлечения таких специалистов в части начисления и уплаченных сумм НДФЛ. Так, предлагается установить для работодателей штрафные санкции в случае неуплаты ими указанного налога. Согласно документу, это повлечёт взыскание штрафа в двукратном размере от суммы неисполненных обязательств.</p>\n<p>Как указывается в пояснительной записке, в среднесрочной перспективе экономика России будет функционировать в условиях сокращения численности граждан трудоспособного возраста и сохранения естественной убыли населения.</p>\n<p>Миграция останется одним из важных факторов стабилизации и роста численности населения. При этом миграционные потоки, идущие в Россию, не в полной мере способны восполнить потребности экономики, в том числе и перспективные, в квалифицированных и высококвалифицированных работниках, отмечается в пояснении.</p>\n<p>В связи с этим, &laquo;необходимо совершенствование существующего порядка привлечения и осуществления трудовой деятельности высококвалифицированных специалистов&raquo;, указывают авторы.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(308, 0, 1567689523, NULL, NULL, '<p>С 1 июля 2019 года официально работает Приемная правового консультирования мигрантов-соотечественников, проживающих и работающих в Санкт-Петербурге и Ленинградской области. Мигранты получают юридическую помощь исключительно на безвозмездной основе. Консультации предоставляются в устной и письменной форме по гражданскому и трудовому праву, миграционному законодательству. Цель работы Приемной &ndash; предупреждение нарушений миграционного и трудового законодательства, удаление &laquo;очагов социальной напряженности&raquo; на территории Санкт-Петербурга и Ленинградской области.</p>\n<p>Прием мигрантов ведут квалифицированные юристы, которые в полном объеме дают разъяснение по назначению государственной социальной помощи по всем интересующим вопросам миграции. Все случаи обращений будут рассматриваться в пределах компетенции правовой Приемной, а также в тесном сотрудничестве со всеми партнерами. При необходимости к рассмотрению обращений будут привлекаться специалисты из Главного Управления по вопросам миграции МВД России по Санкт-Петербургу и Ленинградской области, а также национальные диаспоры, посольства и представительства миграционных служб других государств.</p>\n<p>Фонд &laquo;ОРИЕНТИР&raquo; создан в 2011 году и, согласно своему Уставу, оказывает содействие добровольному переселению в РФ переселенцев-соотечественников, социальную поддержку, защиту и информирование мигрантов, а также создает и развивает инфраструктуру для переселенцев-соотечественников на территориях вселения.</p>\n<p>Расположение Приемной правовой поддержки выбрано не случайно. Приемная находится рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС России по Санкт-Петербургу и Ленинградской области: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В.&nbsp;</p>\n<p><img title=\"ФПГ_logo_2019.jpg\" src=\"http://www.fondorientir.ru/filemanager/i/7f8112c0/ФПГ_logo_2019.jpg\" alt=\"ФПГ_logo_2019.jpg\" width=\"652px\" height=\"263px\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(309, 0, 1567692439, NULL, NULL, '<p>МОСКВА, 9 июля. /ТАСС/. Госдума приняла в первом чтении законопроект, предусматривающий увеличение размера государственной пошлины за выдачу вида на жительство с 3 500 рублей до 5 000 рублей. Законопроект был инициирован правительством, поправки вносятся в Налоговый кодекс РФ.</p>\n<p>Такое увеличение связано с введением проекта федерального закона \"О внесении изменений в Федеральный закон \"О правовом положении иностранных граждан в РФ\" и отдельные законодательные акты Российской Федерации в части упрощения порядка предоставления некоторым категориям иностранных граждан и лиц без гражданства разрешения на временное проживание и вида на жительство\" бессрочного вида на жительство, за исключением случаев его выдачи высококвалифицированным специалистам и членам их семей (на срок действия разрешения на работу) и квалифицированным специалистам (на три года).</p>\n<p>Согласно сведениям отчетности МВД России, в 2018 году территориальными органами МВД России выдано 191 445 видов на жительство (в 2017 году - 185 800), продлен срок действия 28 331 вида на жительство (в 2017 году - 34 002).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(310, 0, 1567692745, NULL, NULL, '<p>За&nbsp;первые пять месяцев в&nbsp;Россию переселились 41,3 тысячи человек по&nbsp;государственной программе по&nbsp;возвращению соотечественников. Это составляет более 50% от&nbsp;планового показателя 2019 года, сообщили &laquo;Ридусу&raquo; в&nbsp;МВД России.</p>\n<p>&nbsp;В&nbsp;прошлом году воспользовались программой примерно 108 тысяч человек, что стало на&nbsp;19,2% выше запланированного показателя (90,6 тысяч участников).</p>\n<p>Кроме того, в&nbsp;МВД зафиксировали рост числа переселенцев в&nbsp;Россию из&nbsp;стран ближнего зарубежья за&nbsp;последние годы. Если в&nbsp;период с&nbsp;2007 по&nbsp;2013 годы в&nbsp;Россию переезжали в&nbsp;среднем около 20 тысяч соотечественников, то&nbsp;с&nbsp;2014 года число число переселенцев возросло до&nbsp;135 тысяч.</p>\n<p>Всего по&nbsp;данным правоохранительных органов, в&nbsp;Россию с&nbsp;момента действия закона&nbsp;переехало около 826 тысяч человек.</p>\n<p>Напомним, государственная программа по&nbsp;оказанию содействия добровольному переселению в&nbsp;РФ соотечественников реализуется с&nbsp;2007 года. Первоначально в&nbsp;ней участвовало 12 субъектов России. Сейчас это число выросло до 75. Наибольший интерес для переселенцев представляет Центральный федеральный округ и&nbsp;Сибирский федеральный округ.</p>\n<p>Ранее &laquo;Ридус&raquo; писал, что за&nbsp;первые шесть месяцев этого года&nbsp;в&nbsp;Россию&nbsp;въехало&nbsp;свыше восьми миллионов иностранных граждан. При этом из&nbsp;них четыре миллиона составили временные трудовые мигранты. В&nbsp;РФ в&nbsp;основном приезжают граждане Узбекистана (37,2% от&nbsp;всех мигрантов), Таджикистана (21,5%) и&nbsp;Киргизии (11,4%).</p>', NULL, NULL, NULL, NULL, NULL, 1),
(311, 0, 1567692751, NULL, NULL, '<p>Иностранный гражданин работает в&nbsp;России. Может&nbsp;ли вид на&nbsp;жительство в&nbsp;РФ подтвердить продолжительность его пребывания его в&nbsp;России? Нет, поскольку при&nbsp;определении налогового статуса наличие у&nbsp;физлица вида на&nbsp;жительства значения не&nbsp;имеет. Такой вывод следует из&nbsp;письма Минфина России&nbsp;от&nbsp;24.06.19 №&nbsp;03-04-05/46120.</p>\n<p>Напомним, что&nbsp;налоговые резиденты РФ&nbsp;&mdash; это физические лица, которые фактически находятся в&nbsp;Российской Федерации не&nbsp;менее 183 календарных дней в&nbsp;течение 12 следующих подряд месяцев (п.&nbsp;2 ст.&nbsp;207&nbsp;НК РФ). Налоговые резиденты РФ платят НДФЛ по&nbsp;&laquo;обычной&raquo; ставке&nbsp;&mdash; 13%, а&nbsp;нерезиденты&nbsp;&mdash; по&nbsp;повышенной ставке 30% (п.&nbsp;3 ст.&nbsp;224&nbsp;НК РФ).</p>\n<p>183 дня пребывания в&nbsp;РФ, по&nbsp;достижении которых физлицо признается налоговым резидентом, исчисляются путем суммирования всех календарных дней, в&nbsp;которые гражданин находился в&nbsp;России в&nbsp;течение 12 следующих подряд месяцев. При&nbsp;определении налогового статуса учитывается фактическое время нахождения гражданина в&nbsp;РФ, которое должно быть документально подтверждено.</p>\n<p>Как подтвердить срок нахождения на&nbsp;территории РФ? Можно&nbsp;ли использовать для&nbsp;этой цели отметку о&nbsp;виде на&nbsp;жительство иностранного гражданина в&nbsp;РФ? В&nbsp;Минфине поясняют, что&nbsp;вид на&nbsp;жительство представляет собой документ, выданный иностранному гражданину или&nbsp;лицу без&nbsp;гражданства в&nbsp;подтверждение их права на&nbsp;постоянное проживание в&nbsp;РФ, а&nbsp;также их права на&nbsp;свободный въезд и&nbsp;выезд из&nbsp;России.</p>\n<p>Таким образом, вид на&nbsp;жительство не&nbsp;подтверждает фактическое время нахождения физического лица на&nbsp;территории страны.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(312, 0, 1567773216, NULL, NULL, '<p>МОСКВА. 6 АВГУСТА. ИНТЕРФАКС &ndash; Правительство РФ предлагает разрешить въезд иностранцам в РФ по электронным визам не только авиатранспортом, но и через автомобильные и речные пункты пропуска.</p>\n<p>Соответствующий законопроект внесён в Госдуму во вторник и размещён в законодательной базе деятельности нижней палаты парламента.</p>\n<p>Законопроектом предусматривается распространение упрощенного порядка въезда иностранных граждан в РФ по электронным визам на автомобильные, речные и смешанные пункты пропуска, расположенные на территории Дальневосточного федерального округа (ДФО).</p>\n<p>Перечень таких пунктов пропуска определяется правительством РФ, говорится в законопроекте.</p>\n<p>Распространение упрощенного порядка въезда иностранных граждан в РФ по электронным визам на железнодорожные и морские пункты пропуска, расположенные на территории Дальневосточного федерального округа, не предусматривается.</p>\n<p>В железнодорожных пунктах пропуска техническая возможность проверки электронных виз отсутствует, говорится в законопроекте. А морские порты, расположенные на территории ДФО, иностранными гражданами не востребованы.</p>\n<p>В пояснительной записке приводится статистика, которая показывает, что за период эксплуатации сервиса оформления виз в форме электронного документа для граждан иностранных государств, прибывающих в РФ через морские пункты пропуска, расположенные на территории свободного порта Владивосток, количество въехавших в РФ в 2018 году иностранных граждан через морской пункт пропуска Корсаков (Сахалинская область) составляет 112 человек, а в морских пунктах пропуска Зарубино, Посьет (Приморский край) и Петропавловск-Камчатский (Камчатский край) &mdash; ни одного человека.</p>\n<p>В правительстве считают, что предлагаемые законопроектом изменения позволят создать дополнительные благоприятные условия для привлечения иностранных туристов в регионы Дальнего Востока, что будет способствовать созданию и дальнейшему развитию в них необходимой туристической и иной социальной инфраструктуры, повышению инвестиционной привлекательности и доходов бюджетов всех уровней, а также укреплению экономики Дальневосточного федерального округа в целом.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(313, 0, 1567773208, NULL, NULL, '<p>Иностранцы должны сдавать&nbsp;отпечатки пальцев при въезде в страну, а МВД &mdash; отслеживать все передвижения мигрантов по российской территории. Во время осенней сессии депутаты собираются добиться финансирования этих мероприятий, направленных на борьбу с &laquo;экстремистами-туристами&raquo;. Об этом &laquo;Парламентской газете&raquo; рассказал&nbsp;председатель Комитета Госдумы по развитию гражданского общества, вопросам общественных и религиозных объединений Сергей Гаврилов.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\n<p><em>Долгожданные решения</em></p>\n<p>&laquo;В течение многих лет ситуация с защитой прав соотечественников в России была, по сути, в тупике&raquo;, &mdash; обозначил депутат корень проблемы. Особенно ярко это проявилось после начала гражданского конфликта на Украине: беженцы столкнулись с серьёзными проблемами в части оформления законного проживания в России, им было сложно защитить свои права. Поставить точку в теме упрощённой натурализации соотечественников удалось только в этом году.</p>\n<p>Сегодня соотечественникам предоставлены удобные и простые механизмы легализации и получения российского гражданства. Самый яркий пример: возможность приобрести российское гражданство в максимально упрощённом порядке для жителей Донецкой и Луганской народных республик и переселенцев из Донбасса, которую им предоставил этой весной президент&nbsp;Владимир Путин.</p>\n<p>Кроме того, в конце весенней сессии был принят закон, который дал право гражданам Украины в числе прочих категорий получить разрешение на временное проживание (РВП) вне квоты.&nbsp;Сергей Гаврилов&nbsp;напомнил, что этот закон также реформировал институт вида на жительство: он будет бессрочным для всех соотечественников,&nbsp;квалифицированных специалистов и иностранных студентов, окончивших с отличием российские вузы.</p>\n<p>Введена в действие &laquo;паспортная амнистия&raquo; для россиян, которые получили свои паспорта до 2010 года, но впоследствии утратившие гражданство из-за ошибок в документах из-за сбоев в информационной системе миграционного учёта. &laquo;Теперь они смогут снова получить гражданство по заявительному принципу. Эти люди годами живут в России, укоренены, работают, многие отслужили в Вооружённых силах РФ&raquo;, &mdash; подчеркнул депутат.&nbsp;</p>\n<p>Параллельно закладывается база для привлечения нужных стране высококлассных работников. &laquo;Мы формируем комфортный миграционный режим для иностранных специалистов, дефицит которых испытывает наша страна. Например, врачей отдельных специальностей&raquo;, &mdash; пояснил парламентарий.</p>\n<p><em>Нелегалам вход воспрещён</em></p>\n<p>Наряду с этим закрываются лазейки для незаконных мигрантов, которые не только вносят дисбаланс на рынок труда, но и грозят национальной безопасности. Это болезненная для нашей страны тема, подчеркнул Сергей Гаврилов. С целью пресечения нелегальной миграции было принято несколько законов:&nbsp;о &laquo;резиновых офисах&raquo; и &laquo;резиновых квартирах&raquo; (ужесточено наказание за фиктивную регистрацию иностранцев), о борьбе с фиктивными браками с российскими гражданами, об ужесточении порядка миграционного учёта иностранцев по месту их фактического пребывания.</p>\n<p>Принимаемые властями меры уже дали свои плоды: доля нелегалов в России снижается, рассказала &laquo;Парламентской газете&raquo; начальник ГУВМ МВД России&nbsp;Валентина Казакова.&nbsp;&laquo;Последнее время неуклонно растёт число иностранцев, которые работают здесь легально. Как следствие, снижается уровень нелегальной составляющей&raquo;, &mdash; отметила она. Глава миграционной полиции подчеркнула, что&nbsp;трудовые мигранты &mdash; самая многочисленная категория: они составляют 40 процентов от общего числа иностранцев, находящихся сегодня в стране.</p>\n<p>На повестке дня &mdash; усиление контроля на внешних границах страны, убеждён Сергей Гаврилов.</p>\n<p>Комитет, который он возглавляет, планирует добиться дополнительного финансирования ключевых мероприятий в этой сфере: обязательной дактилоскопии въезжающих в страну иностранных граждан, формирование базы данных и отслеживание передвижения мигрантов на территории России. &laquo;Мы должны исключить возможность попадания разного рода экстремистов, которые хотят попасть в нашу страну под видом туристов, занимающихся здесь нелегальной деятельностью&raquo;, &mdash; призвал парламентарий.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(314, 0, 1568978382, NULL, NULL, '<p>Трудовые мигранты из Киргизии смогут проще отправлять заработанные в России средства домой.&nbsp;Такую возможность даст новый сервис моментальных&nbsp;&nbsp;переводов от Сбербанка, 21 августа 2019 года сообщили SNG.Today в пресс-службе финансового учреждения.</p>\n<p>Чтобы осуществить перевод, требуется только иметь в смартфоне приложение &laquo;Сбербанк Онлайн&raquo; и знать номер телефона получателя.</p>\n<p>Первым партнером Сбербанка в СНГ стал Коммерческий банк &laquo;Кыргызстан&raquo; &ndash; именно его клиенты смогут получать быстрые платежи. Как отметили в банке, сейчас в России работает более 600 тысяч граждан Киргизии, и вопрос сложности перевода денег для них очень актуален.</p>\n<p>&laquo;Сбербанк продолжает развивать экосистему мгновенных денежных переводов и расширяет ее географию. Наша цель &ndash; дать возможность клиентам переводить за рубеж так же легко и быстро, как они привыкли это делать по России&raquo;, &ndash; сказал&nbsp;Игорь Мамонтов, вице-президент Сбербанка.</p>\n<p>Сервис переводов доступен в приложении &laquo;Сбербанк Онлайн&raquo; пока только на платформе Android.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(315, 0, 1568978372, NULL, NULL, '<p>В Санкт-Петербурге начали проводить выездные правовые приемные в местах массового скопления иностранных граждан: на территории прилегающей к ООО &laquo;Единый центр документов, расположенной по адресу ул. Красного Текстильщика, д. 10-12, а также на территориях, прилегающих к Соборной Мечети, расположенной по адресу: Кронверкский пр-т. д.7, Коломяжской мечети, расположенной по адресу: улица Репищева д.1 и на территории молельной комнаты, расположенной по адресу: Московский пр., дом 12. Гости города могут задать любой интересующий вопрос юристам Фонда &laquo;ОРИЕНТИР&raquo;</p>\n<p>Выездные приемные осуществляются в рамках проекта &laquo;Цивилизованная миграция - Санкт-Петербург и Ленинградская область&raquo;, финансируемого Фондом-оператором Президентских грантов.</p>\n<p>Юристами и помощниками юристов в течение двух месяцев уже было проконсультировано около 500 человек. Обращавшимся также были выданы буклеты на русском, таджикском и узбекском языках, в которых прописаны правила пребывания иностранных граждан на территории Российской Федерации, а также рекомендации от юристов, как избежать нарушения законодательства.</p>\n<p>По всем вопросам обращаться по адресу: г. Санкт-Петербург, пр. Римского-Корсакова, дом 39, лит. В, по телефону \"горячей линии\": 385-69-89 или оставив письменное обращение по адресу в интернете&nbsp;<a href=\"http://www.fondorientir.ru/\" rel=\"nofollow\">http://www.fondorientir.ru/</a>.</p>\n<p><img title=\"ПГ.JPG\" src=\"http://www.fondorientir.ru/filemanager/i/9b8a8a8d/ПГ.JPG\" alt=\"ПГ.JPG\" width=\"591\" height=\"591\" /><img title=\"ПГ_2.JPG\" src=\"http://www.fondorientir.ru/filemanager/i/89709773/ПГ_2.JPG\" alt=\"ПГ_2.JPG\" width=\"591\" height=\"591\" /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(316, 0, 1568978668, NULL, NULL, '<p><span>18 сентября с 18:00 до 20:00 в Доме еврейской культуры ЕСОД (ул. Б. Разночинная, д. 25А) пройдет информационно-культурное мероприятие для иностранных граждан &laquo;Под крылом Петербурга&raquo;.&nbsp;</span><br /><br /><span>Помогать иностранным гражданам адаптироваться в Северной столице стало доброй традицией. Организатором данного проекта выступает Комитет по межнациональным отношениям и реализации миграционной политики в&nbsp;</span><span class=\"nobr\">Санкт-Петербурге</span><span>.&nbsp;</span><br /><br /><span>Как получить временную регистрацию? Где оформить патент на работу? Куда обратиться за медицинской помощью? На эти и другие вопросы мигрантов ответят специалисты органов государственной власти и социально ориентированных некоммерческих организаций. Консультирование по вопросам миграционного законодательства, трудоустройства, образования, здравоохранения и налогообложения будет оказано бесплатно.&nbsp;</span><br /><br /><span>Как отмечают организаторы, за время существования проекта потребности мигрантов изменились. Они стали больше думать об уплате налогов, участии в культурной жизни города и получении российского образования.&nbsp;</span><br /><br /><span>Гостей мероприятия ждет знакомство с традициями русского чаепития, для маленьких гостей будет организована развлекательная программа с мастер-классами и интерактивом.&nbsp;</span><br /><br /><span>В рамках концертной программы состоится подведение итогов конкурса детского рисунка и награждение победителей.&nbsp;</span><br /><br /><span>Каждый гость станет участником интеллектуальной викторины и сможет выиграть ценные призы.&nbsp;</span><br /><br /><span>Особым подарком станет концерт, на котором выступят национальные творческие коллективы&nbsp;</span><span class=\"nobr\">Санкт-Петербурга</span></p>\n<p><span class=\"nobr\"><img src=\"https://nevnov.ru/uploads/2019/09/07/orig-1567878273817e2be02087c6ca30074e24e493a2bb.jpeg\" alt=\"&ETH;&ETH;&frac12;&ETH;&frac34;&Ntilde;&Ntilde;&Ntilde;&ETH;&deg;&ETH;&frac12;&Ntilde;&Ntilde; &Ntilde;&ETH;&frac34;&ETH;&plusmn;&ETH;&micro;&Ntilde;&Ntilde;&Ntilde;&Ntilde;&Ntilde; &ETH;&sup2; &ETH;&ETH;&frac34;&ETH;&frac14;&ETH;&micro; &ETH;&micro;&ETH;&sup2;&Ntilde;&ETH;&micro;&ETH;&sup1;&Ntilde;&ETH;&ordm;&ETH;&frac34;&ETH;&sup1; &ETH;&ordm;&Ntilde;&ETH;&raquo;&Ntilde;&Ntilde;&Ntilde;&Ntilde;&Ntilde; &ETH;&frac12;&ETH;&deg; &ETH;&frac14;&ETH;&micro;&Ntilde;&ETH;&frac34;&ETH;&iquest;&Ntilde;&ETH;&cedil;&Ntilde;&Ntilde;&ETH;&cedil;&ETH;&cedil; &Acirc;&laquo;&ETH;&ETH;&frac34;&ETH;&acute; &ETH;&ordm;&Ntilde;&Ntilde;&ETH;&raquo;&ETH;&frac34;&ETH;&frac14; &ETH;&ETH;&micro;&Ntilde;&ETH;&micro;&Ntilde;&ETH;&plusmn;&Ntilde;&Ntilde;&ETH;&sup3;&ETH;&deg;&Acirc;&raquo; \" width=\"768\" height=\"432\" /></span></p>', NULL, NULL, NULL, NULL, NULL, 1),
(317, 0, 1569487549, NULL, NULL, '<p class=\"first-latter-2-line\">В России максимальная стоимость миграционных патентов в 2018 году была в регионах Дальнего Востока.</p>\n<div class=\"slot-container slot-article-partner\">&nbsp;</div>\n<p data-is-slot-placed=\"1\">Наличие миграционного патента даёт право иностранцам, прибывающим в Россию в безвизовом порядке, работать в выбранном регионе.</p>\n<p data-is-slot-placed=\"1\"><span class=\"ts-direct-speech\">&laquo;Наиболее высокая стоимость патента для мигрантов в 2018 году установлена в дальневосточных и северных регионах. При этом средняя стоимость патента среди всех регионов России составляет всего 3,9 тысячи рублей&raquo;, &mdash; цитирует</span>&nbsp;<span class=\"ts-direct-speech\">РИА Новости&nbsp;экспертов рейтинга эффективности региональной налоговой политики, который представила консалтинговая компания PwC.</span></p>\n<p data-is-slot-placed=\"1\">Желающие работать в Якутии должны заплатить за патент 9,1 тыс. рублей в месяц, в Ямало-Ненецком округе &mdash; 8,4 тыс., в Ненецком &mdash; 7,9 тыс., на Камчатке &mdash; 5,6 тыс., в Приморье &mdash; 4&thinsp;357 рублей.</p>\n<p data-is-slot-placed=\"1\">Платежи за патенты на работу мигрантов из Узбекистана и Таджикистана ежегодно растут &mdash; в 2019 году они оформили 91% всех патентов, в 2018 году &mdash; 89%, в 2017-м &mdash; 86%.</p>\n<p>За четыре месяца 2019 года бюджет Москвы получил 5,6 млрд рублей от миграционных патентов.</p>\n<p><br /><br /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(318, 0, 1569487724, NULL, NULL, '<p data-is-slot-placed=\"1\">Дерзкое нападение группы мигрантов на сотрудника ДПС ГИБДД в Петродворцовом районе Санкт-Петербурга 9 сентября привело к массовым проверкам мигрантов на территории всего района. В результате выявлены нарушители российских законов, которые будут депортированы из РФ.</p>\n<p data-is-slot-placed=\"1\">Как сообщили 13 сентября в пресс-службе ГУ МВД России по Санкт-Петербургу и Ленобласти, тотальная проверка проводилась двое суток среди мигрантов, работающих и живущих в Петродворцовом районе. Ее вели сотрудники Управления по вопросам миграции ГУ МВД России и Полка патрульно-постовой службы полиции Главка при силовой поддержке Росгвардии.</p>\n<p data-is-slot-placed=\"1\"><span class=\"ts-direct-speech\">&laquo;На 12 объектах массового скопления граждан и стройках проверены 365 мигрантов. В том числе проверялись документы у водителей транспортных средств и пассажиров маршрутных такси&raquo;</span>, &mdash; пояснили в полиции.</p>\n<p data-is-slot-placed=\"1\">В результате рейда к административной ответственности за нарушение миграционного законодательства привлечены 28 иностранных граждан. Решением суда 11 иностранцев будут выдворены за пределы России.</p>\n<p data-is-slot-placed=\"1\">В полиции добавили, что проверка проводилась<span class=\"ts-direct-speech\">&nbsp;&laquo;с целью профилактики тяжких и особо тяжких преступлений среди мигрантов, проживающих и работающих в Петродворцом районе&raquo;.</span></p>\n<p data-is-slot-placed=\"1\">Напомним, двое граждан Узбекистана, находившихся в автомобиле &laquo;Газель&raquo;, избили полицейского, который потребовал от них предъявить документы, а затем проследовать в отдел полиции. Оба злоумышленника задержаны и арестованы, возбуждено уголовное дело, преступникам грозит до 10 лет лишения свободы. Пострадавший полицейский лечится в больнице.</p>\n<p>С начала 2019 года по середину августа в Петербурге за пределы РФ было выдворено 1075 мигрантов, нарушивших правила въезда или пребывания в России. Из них 487 человек депортированы в летний период. Как сообщили в Управлении ФССП по Санкт-Петербургу, 60% и 20% высланных составляют, соответственно, граждане Узбекистана и Таджикистана.</p>\n<p>&nbsp;</p>\n<p>Подробности:&nbsp;<a href=\"https://regnum.ru/news/society/2718405.html\">https://regnum.ru/news/society/2718405.html</a><br /><br /></p>', NULL, NULL, NULL, NULL, NULL, 1),
(319, 0, 1569489555, NULL, NULL, '<h4 class=\"b-article__lead\">18 сентября 2019 года в доме еврейской культуры ЕСОД Санкт-Петербурга состоялась выездная работа общественной приемной бесплатной юридической помощи Фонда \"ОРИЕНТИР\" для оказания социальной поддержки мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге.</h4>\n<p>Также был проведен семинар по гражданскому, трудовому праву и по миграционному законодательству, на котором принимали участие специалисты администрации Петроградского района, представители управления по вопросам миграции МВД России, сотрудники социально ориентированных некоммерческих организаций.</p>\n<p>С приветственным словом к участникам мероприятия обратились первый заместитель Председателя Комитета по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге Вадим Окрушко и заместитель Главы Петроградского района Андрей Цибиногин.</p>\n<p>Оперативную юридическую помощь получили свыше 30 трудовых мигрантов, лица без гражданства, вынужденные переселенцы и другие иностранные граждане по следующим вопросам: прибытие в PФ, получение гражданства РФ, участие в Государственной программе переселения соотечественников, восстановление документов, невыплата заработной платы, отказ от оформления трудового договора или отсутствие записи в трудовой книжке, незаконное привлечение к дисциплинарной и административной ответственности, невыплата отпускных, декретных и иных социальных пособий, незаконное задержание под стражей и т.п.</p>\n<p>Мигранты получили юридическую помощь на безвозмездной основе. Консультации предоставлялись в устной форме по миграционному законодательству, а также гражданскому и трудовому праву. Проект оказания бесплатной юридической помощи реализуется с использованием средств государственной поддержки, выделенных Фондом президентских грантов.</p>\n<p>&nbsp;</p>\n<p><img title=\"IMG_2480-20-09-19-03-10.JPG\" src=\"http://www.fondorientir.ru/filemanager/i/3c2928f6/IMG_2480-20-09-19-03-10.JPG\" alt=\"IMG_2480-20-09-19-03-10.JPG\" width=\"736\" height=\"736\" /></p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(320, 0, 1581373751, NULL, NULL, '<p><em>Под действие нового закона попадут все иностранные граждане без исключения.</em></p>\n<p><strong><em>С 1 ноября 2019 года вступает в силу новая редакция статьи 8 Федерального закона &laquo;О правовом положении иностранных граждан&raquo;.&nbsp; Тем, кто получит статус носителя русского языка, не нужно будет оформлять выход &nbsp;из гражданства, а сам вид на жительство станет бессрочным (ранее ВНЖ выдавался на срок до 3 лет с целью завершения процедуры оформления гражданства по носителю языка).</em></strong></p>\n<p>Поправки в закон сильно упростят процесс оформления гражданства через признание носителем русского языка многим гражданам. Ранее, только украинцы могли не представлять документ об отказе от имеющегося гражданства на этапе оформления вида на жительства. Остальные граждане обязаны были проходить длинную процедуру: сначала получать уведомление о возможности приема в гражданство (срок выдачи уведомления 3 месяца), далее обращаться в консульство своей страны, в котором получать справку о подаче заявления о выходе из гражданства . Эту справку и требовало МВД от носителей русского языка при подаче документов на вид на жительство. Без соблюдения этой процедуры вид на жительство не выдавался.</p>\n<p>Для получения статуса носителя языка иностранный гражданин должен свободно владеть русским языком и использовать его в повседневной жизни. Помимо этого он должен либо сам проживать в России на основании вида на жительство, либо иметь родственников по прямо восходящей линии (мама, папа, бабушка, дедушка и т.п.), которые проживают или проживали ранее в границах Российской Федерации. Подтверждаются эти обстоятельства архивными справками, выписками из похозяйственных книг, военными билетами и другими документами. Однако МВД принимает только документы, в которых указано непосредственно на проживание родственника в конкретный период (аттестаты об образовании, свидетельства о рождении не принимаются во внимание). В случае невозможности получения архивных документов, факт проживания родственника может быть установлен в судебном порядке.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(321, 0, 1581373931, 'МВД подготовило проект указа Президента России, который конкретизирует основания для отклонения заявления по вопросам гражданства РФ в части представления заявителем заведомо ложных сведений или подложных документов.', 'МВД подготовило проект указа Президента России, который конкретизирует основания для отклонения заявления по вопросам гражданства РФ в части представления заявителем заведомо ложных сведений или подложных документов.', '<p style=\"text-align: justify;\">МВД подготовило проект указа Президента России, который конкретизирует основания для отклонения заявления по вопросам гражданства РФ в части представления заявителем заведомо ложных сведений или подложных документов.<br /> <br /> Проектом указа предлагается прописать, что отклонение заявления на гражданство или отказ от него возможны, если заявитель предоставил документы, содержащие заведомо ложные сведения о нём или о наличии у него установленных законом оснований и соблюдении условий приобретения или прекращения гражданства РФ, либо указал в заявлении такие ложные сведения. В действующей редакции документа нормы об отказе не конкретизированы.<br /> <br /> Подобным образом уточняются и основания для отмены решений по вопросам гражданства Российской Федерации.<br /> <br /> Также предлагается дополнить перечень документов, подтверждающих владение русским языком на уровне, достаточном для общения в устной и письменной форме, решением о признании заявителя носителем русского языка, принятым соответствующей комиссией.<br /> <br /> Кроме того, предлагается установить максимальный срок проведения проверки обстоятельств, свидетельствующих о наличии либо отсутствии у заявителя гражданства РФ, в 60 дней с возможностью продления на 30 дней. Сегодня этот срок не установлен, что создает предпосылки для нарушения прав граждан.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(322, 0, 1581387455, NULL, NULL, '<p>Вслед за соглашениями с Узбекистаном и Таджикистаном, подразумевающими подготовку в этих странах трудовых мигрантов для работы в России, в российском Правительстве планируют заключить аналогичный документ с руководством Киргизии.<br /> <br /> Организованный набор мигрантов подразумевает подготовку иностранных работников в стране их исхода. Она включает обучение профессиям, которые востребованы в России, основам русского языка, истории и законодательства РФ, а также комплексное медицинское обследование на территории России.<br /> <br /> На данный момент соглашение об организованном наборе мигрантов действует между РФ и Узбекистаном, до конца года предполагается ратифицировать такой же документ с Таджикистаном.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(323, 0, 1581387445, NULL, NULL, '<p>Правительство внесло в Госдуму законопроект, который позволит получать гражданство РФ в упрощенном порядке всем иностранцам, чьи дети уже имеют российские паспорта и достигли 18 лет, а не только нетрудоспособным.<br /> <br /> Согласно действующим нормам, рассчитывать на упрощенный порядок могут лишь нетрудоспособные лица, имеющие дееспособных совершеннолетних детей - граждан РФ.<br /> <br /> Как указывают разработчики, &laquo;в целях привлечения в экономику России дополнительных трудовых ресурсов законопроектом предлагается&raquo; исключить &laquo;такое обязательное условие, как нетрудоспособность, для упомянутой категории соискателей гражданства Российской Федерации&raquo;.<br /> <br /> &laquo;Условие о дееспособности совершеннолетних детей является в данном случае предупредительной мерой и позволит приобретать гражданство РФ иностранным гражданам, поддерживающим родственные отношения со своей семьей и взрослыми детьми&raquo;, &mdash; указывают в кабмине.<br /> <br /> Помимо этого, законопроектом предусмотрено уточнение, согласно которому решение о принятии в гражданство несовершеннолетних в упрощенном порядке может быть принято даже в том случае, если соискателю исполнилось 18 лет в период рассмотрения заявления, поданного его законными представителями.</p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(324, 0, 1581387424, NULL, NULL, '<p style=\"text-align: justify;\">Поправки, которые позволят иностранцам, купившим в России квартиры, прописывать на своей жилплощади других иностранцев, например, своих родных, предлагается внести в закон &laquo;О миграционном учете иностранных граждан и лиц без гражданства в России&raquo;.<br /> <br /> Данный законопроект расширяет круг иностранных граждан, имеющих право выступать принимающей стороной. Иностранные граждане, законные собственники помещений, смогут ставить других граждан на учет на своей жилплощади. При этом будут соблюдаться все требования закона, в том числе на них будет распространяться ответственность за фиктивную регистрацию. А гости иностранного гражданина должны иметь законные основания для пребывания в России.<br /> <br /> Сегодня по закону иностранные граждане не вправе прописывать в принадлежащих им квартирах других иностранцев. Собственники могут прописать только себя. Исключения предусмотрены для высококвалифицированных специалистов из других государств: они могут прописать также своих родных.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(325, 0, 1581387436, NULL, NULL, '<p><strong>Нововведение коснулось лишь жителей отдаленных районов Ленинградской области. Санкт-Петербург и Всеволожский район &ndash; без изменений, прием ведется на Красного Текстильщика.</strong></p>\n<p style=\"text-align: justify;\">Те, кто желает получить РВП, вид на жительство или подается на гражданство, должны обращаться в районные &nbsp;отделы Управления по вопросам миграции на территории Ленинградской области по месту своего жительства (регистрации или миграционного учета).</p>\n<p style=\"text-align: justify;\">Ранее сообщалось, что с октября 2019 года все подачи на РВП, ВНЖ и гражданство будут происходить в районных отделах. Однако реформа пока коснулась только Ленинградской области. До изменений, жители Гатчины, Луги и других отдаленных местностей должны были ехать в Санкт-Петербург , чтобы оформить необходимые документы. Между тем, расстояние от Луги до Санкт-Петербурга более 130 км., &nbsp;поездка занимает более 2-х часов. Жителям Лодейного поля приходилось преодолевать почти 200 км.</p>\n<p style=\"text-align: justify;\"><strong>С 12 сентября 2019 года жители Ленобласти &nbsp;смогут оформить РВП, ВНЖ и гражданство у себя в районе. Для жителей Санкт-Петербурга и Всеволожского района Ленинградской области &mdash; без изменений, прием документов на РВП, ВНЖ, гражданство будет происходить, как и раньше, централизовано в отделах Управления по вопросам миграции на улице Красного Текстильщика дом 10.</strong></p>\n<p><strong>Официальная информация о порядке приема заявлений на РВП, ВНЖ и гражданство в Ленинградской области</strong></p>\n<p style=\"text-align: justify;\">С 12 сентября 2019 года прием документов на РВП, ВНЖ и гражданство от иностранных граждан, зарегистрированных в Ленинградской области (<strong><em>за исключением Всеволожского района Ленинградской области</em></strong>), осуществляется в районных отделах на территории Ленинградской области согласно графику:</p>\n<ul>\n<li>Понедельник &ndash; с 09.00 до 15.00</li>\n<li>Вторник &ndash; с 11.00 до 20.00</li>\n<li>Среда &ndash; с 09.00 до 13.00</li>\n<li>Четверг &mdash; с 11.00 до 20.00</li>\n<li>Пятница &mdash; с 09.00 до 16.45</li>\n<li>Суббота &ndash; с 09.00 до 14.00</li>\n</ul>\n<p><strong><em>Иностранные граждане, зарегистрированные во Всеволожском районе Ленинградской области, подают документы на ул.Красного Текстильщика д.10-12.</em></strong></p>\n<p style=\"text-align: justify;\"><em>Источник &mdash; <span style=\"text-decoration: underline;\"><a href=\"https://78.xn--b1aew.xn--p1ai/ms/%D0%BF%D1%80%D0%BE%D1%84%D0%B8%D0%BB%D1%8C%D0%BD%D1%8B%D0%B5-%D0%BF%D0%BE%D0%B4%D1%80%D0%B0%D0%B7%D0%B4%D0%B5%D0%BB%D0%B5%D0%BD%D0%B8%D1%8F-%D1%83%D0%BF%D1%80%D0%B0%D0%B2%D0%BB%D0%B5%D0%BD%D0%B8%D1%8F/%D0%BE%D1%82%D0%B4%D0%B5%D0%BB-%D0%BE%D1%84%D0%BE%D1%80%D0%BC%D0%BB%D0%B5%D0%BD%D0%B8%D1%8F-%D1%80%D0%B0%D0%B7%D1%80%D0%B5%D1%88%D0%B5%D0%BD%D0%B8%D0%B9-%D0%BD%D0%B0-%D0%B2%D1%80%D0%B5%D0%BC%D0%B5%D0%BD%D0%BD%D0%BE%D0%B5\">сайт УВМ ГУ МВД России по Санкт-Петербургу и Ленинградской области.</a></span></em></p>', NULL, NULL, NULL, NULL, NULL, 1),
(326, 0, 1581387621, NULL, NULL, '<p style=\"text-align: justify;\">23 октября в 18 часов в культурном центре &laquo;Буравестник&raquo; (ул. Подвойского, д. 38) состоялось мероприятие \"Под крылом Петербурга\", на котором Фонд \"ОРИЕНТИР\" осуществлял свою деятельность в рамках проекта \"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\", финансируемого Фондом-оператором Президентских грантов.</p>\n<p style=\"text-align: justify;\">В ходе мероприятия присутствовали специалисты органов государственной власти Санкт-Петербурга (социальной защиты, образования, здравоохранения, культуры и молодежной политики), а также сотрудники правоохранительных органов и представители социально ориентированных некоммерческих организаций, которые осуществляли консультирование мигрантов по вопросам получения социальной, медицинской, правовой и иных видов помощи.</p>\n<p style=\"text-align: justify;\">В рамках мероприятия состоялись бесплатные консультации и семинар правовой грамотности по вопросам миграционного законодательства, трудоустройства, получения медицинской, социальной и иных видов помощи. На вопросы гостей отвечали специалисты отделов администрации Невского района Санкт-Петербурга, представители налоговой инспекции, управления по вопросам миграции, а также представители социально ориентированных некоммерческих организаций.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(327, 0, 1581387727, NULL, NULL, '<p>Пользоваться налоговым режимом самозанятых могут разрешить украинцам, таджикам, узбекам, молдаванам и азербайджанцам. Такой эксперимент планирует провести в России Минфин.</p>\n<p style=\"text-align: justify;\"><br /> Льготный налог в размере 4&ndash;6% от заработка для многих трудовых мигрантов может оказаться выгоднее, чем фиксированная плата за патент (около 4 тыс. рублей ежемесячно), говорят эксперты.<br /> <br /> Минфин предлагает провести эксперимент &laquo;по предоставлению права применять налог на профессиональный доход физическими лицами, являющимися гражданами стран, входящих в Содружество Независимых Государств, которые не являются членами Евразийского экономического союза&raquo;. Это Таджикистан, Узбекистан, Молдавия, Украина и Азербайджан.<br /> <br /> Налоговый режим для самозанятых действует в России с начала 2020 года в четырех регионах: Москве и области, Калужской области и Татарстане. Им могут пользоваться те, кто работает на себя и не использует наемный труд, &mdash; например, это может быть няня, репетитор, водитель. Максимальный доход самозанятых не должен превышать 2,4 млн рублей в год. Налог составляет 4% при работе с физлицами и 6% &mdash; с юрлицами и индивидуальными предпринимателями.<br /> <br /> Зарегистрироваться самозанятыми помимо россиян сейчас могут граждане стран ЕАЭС: Армении, Белоруссии, Казахстана и Киргизии. Остальные иностранцы должны получать патент или оформлять рабочую визу и разрешение на работу.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(328, 0, 1581388391, NULL, NULL, '<p style=\"text-align: justify;\">Согласно Статье 18.10 &laquo;Незаконное осуществление иностранным гражданином или лицом без гражданства трудовой деятельности в Российской Федерации&raquo; Федерального Закона от 30.12.2001 № 195-ФЗ, безвизовый мигрант несёт административную ответственность в следующих ситуациях:<br /> <br /> -осуществление трудовой деятельности без патента;<br /> -работу не по профессии (специальности, должности, виду трудовой деятельности) &ndash; в том случае, если таковая указана в патенте;<br /> -вступление в трудовые отношения вне пределов субъекта РФ, где выдан патент;<br /> -нарушение сроков предоставления сведений для внесения изменений в патент.<br /> <br /> Отсутствием патента как официального разрешения на осуществление трудовой деятельности считаются также случаи, при которых имеющийся патент оказался подложным или аннулированным по истечению срока действия.<br /> <br /> Если иностранный гражданин нелегально трудился на территории Москвы или Санкт-Петербурга, а также в пределах Московской или Ленинградской области, сумма штрафа составит от 5000 до 7000 рублей. За нарушение трудового законодательства в этих регионах иностранца обязательно выдворят из страны</p>', NULL, NULL, NULL, NULL, NULL, 1),
(329, 0, 1594395617, NULL, NULL, '<p>В его основе лежит базовая величина, закрепленная в налоговом кодексе более пяти лет назад &ndash; 1200 руб. Она умножается на устанавливаемый ежегодно Минэкономразвития РФ коэффициент-дефлятор. Он единый для всей страны, его задача - нивелировать влияние инфляции на базовую величину.<br /> <br /> После этого в дело вступают региональные власти, которые устанавливают региональные коэффициенты, которые формируют окончательную стоимость патента.<br /> <br /> 21 октября 2019 года стало известно значение коэффициента дефлятора на 2020 год &ndash; 1,813.<br /> <br /> Промежуточный размер платы за патент в 2020 году составил 1200 х 1,813 = 2175,6 руб.<br /> <br /> Далее к формированию итогового результата подключилось законодательное собрание Санкт-Петербурга, которое своим законом от 26.11.2019 года установило значение регионального коэффициента, отражающего региональные особенности рынка труда города Санкт-Петербурга, в размере 1,83858.<br /> <br /> Таким образом окончательный размер фиксированного авансового платежа по НДФЛ для иностранных работников в Санкт-Петербурге составил 2175,6 х 1,83858 = 4000 руб.<br /> <br /> Однако окончательным он пробыл только 27 дней &ndash; до 27.01.2020 года, до того момента, как вступил в силу новый приказ Минэкономразвития РФ, в котором министерство приняло решение взять и поменять коэффициент-дефлятор на 2020 год!<br /> <br /> Приказом Минэкономразвития России от 10.12.2019 N 793 значение коэффициента изменено с 1,813 на 1,810.<br /> <br /> В итоге размер фиксированного авансового платежа по НДФЛ для иностранцев поменялся по всей стране и, в частности, в Санкт-Петербурге патент с 27 января будет стоить не 4000, а 3993 руб.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(330, 0, 1594395641, NULL, NULL, '<div style=\"margin-right: 8.9pt; text-align: justify;\">\n<div>Модельный Миграционный кодекс для государств &mdash; участников СНГ может быть внесен на рассмотрение пленарного заседания Межпарламентской Ассамблеи (МПА СНГ) осенью 2020 года. Кодекс имеет рекомендательный характер и будет регламентировать вопросы миграции: правила въезда и выезда, установление квот на привлечение иностранной рабочей силы, выдачу разрешений на трудовую деятельность, статуса иностранных граждан и многое другое.</div>\n<div>&nbsp;</div>\n<div>28 февраля 2020 года в Таврическом дворце в рамках IV Санкт-Петербургского международного форума Труда пройдут парламентские слушания, на которых будет обсуждаться тема &laquo;Правовое регулирование миграционных процессов в государствах &ndash; участниках СНГ&raquo; и представлен проект модельного Миграционного кодекса. В дискуссии примут участие депутаты парламентов государств &mdash; участников СНГ, сотрудники профильных международных организаций, в том числе Международной организации по миграции, представители академического сообщества, Комитета по труду и занятости населения Санкт-Петербурга.</div>\n<div>&nbsp;</div>\n<div>Официальные отзывы национальных парламентов государств Содружества, а также озвученные на слушаниях рекомендации в апреле рассмотрит Постоянная комиссия МПА СНГ по социальной политике и правам человека. После того, как работа над проектом модельного Миграционного кодекса будет завершена, документ внесут на рассмотрение пленарного заседания Межпарламентской Ассамблеи. Проект модельного кодекса может быть представлен депутатам в осеннюю сессию МПА СНГ.</div>\n<div>&nbsp;</div>\n<div>&laquo;Для человека, въезжающего в другую страну, важно, каким образом он сможет легализоваться на территории принимающего государства с наименьшими временными и финансовыми затратами. Задача модельного Миграционного кодекса &mdash; сделать этот процесс прозрачным и понятным&raquo;, &mdash; объясняет секретарь Постоянной комиссии МПА СНГ по социальной политике и правам человека Евгения Владимирова.</div>\n<div>&nbsp;</div>\n<div>В частности, обсуждается внесение в модельный Миграционный кодекс нескольких новелл. Депутаты предлагают регламентировать работу частных агентств занятости, оказывающих услуги в сфере миграции: возможно ввести аккредитацию таких компаний. Кроме того, на законодательном уровне могут закрепить механизм, позволяющий работодателям привлекать мигрантов требуемой квалификации напрямую из страны их проживания. К слову, подобное Соглашение об организованном наборе работников Россия уже подписала с Узбекистаном, рассматривается возможность подготовки и подписания подобного соглашения и с Таджикистаном.</div>\n<div>&nbsp;</div>\n<div>После принятия модельный Миграционный кодекс не станет обязательным для исполнения. Документ будет образцом для национального законодательства стран СНГ. При необходимости государства смогут разработать на его основе собственные миграционные кодексы. К примеру, МВД России заявляло, что планирует создать Миграционный кодекс Российской Федерации к 2025 году.</div>\n<div>&nbsp;</div>\n<div>Российская Федерация занимает третье место среди стран с самым большим притоком мигрантов в мире. В 2019 году в среднем в России находилось около 11,6 млн мигрантов, по данным ООН. Это число остается стабильным с 1990-х годов. В то же время 10,5 млн российских граждан живут за рубежом. Из них 3,3 млн человек &mdash; на Украине, 2,5 млн &mdash; в Казахстане и около одного миллиона &mdash; в Германии.</div>\n<div>&nbsp;</div>\n<div><em>Справка</em></div>\n<div><em>Межпарламентская Ассамблея государств &mdash; участников Содружества Независимых Государств была создана в 1992 году. Одна из главных ее задач &mdash; сближение и гармонизация законодательства стран Содружества. В пленарных заседаниях Межпарламентской Ассамблеи участвуют депутаты национальных парламентов государств СНГ. Штаб-квартира организации расположена в Таврическом дворце в Санкт-Петербурге. Председателем Совета Межпарламентской Ассамблеи государств &mdash; участников СНГ является Валентина Матвиенко.</em></div>\n<div><em>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</em></div>\n</div>', NULL, NULL, NULL, NULL, NULL, 1),
(331, 0, 1594395655, NULL, NULL, '<p style=\"text-align: justify;\">Личные проблемы, которые помешали вам вовремя зарегистрироваться, во внимание приняты не будут. Также не стоит забывать и о том, что временная регистрация действует только три месяца, поэтому ее необходимо продлить или же получать регистрацию постоянную.<br /> <br /> Но если самое плохое произошло и вы не успели продлить регистрацию, то вот какие меры вы можете попытаться принять.<br /> <br /> 1.Если регистрация просрочена, то возможно в срочном порядке покинуть страну минимум на девяносто дней, после чего вернуться назад и заново встать на миграционный учет и получить новую временную регистрацию.<br /> 2.Встать на учет в миграционную службу, где будет оформлена прописка, что и зафиксируется в карте мигранта.<br /> <br /> Если вы выбираете первый вариант, то не забывайте, что покинуть территорию России необходимо строго до того, как регистрация кончится. Понимая, что вы не успеваете ее продлить, необходимо срочно выехать за границы государства, тогда вы сможете избежать того, что на вас будет наложен штраф.<br /> <br /> Но когда ситуация складывается таким образом, что вы получили штраф, но считаете это наказание незаслуженным &mdash; можете попытаться обратиться в суд или же подать жалобу непосредственно в УВМ.</p>\n<p style=\"text-align: justify;\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(332, 0, 1594395675, NULL, NULL, '<p style=\"text-align: justify;\"><strong>Добрый день! Подскажите, иностранного гражданина увезли по скорой в клинику, по приезде туда взяли копии документов для того, чтобы направить их в УВМ. Вопрос: для чего сейчас установили данную процедуру? Если не уведомить, то взыскивается штраф с мед.учреждения. Что за ФЗ или приказ регламентирует данное действо?</strong></p>\n<p style=\"text-align: justify;\">Иностранного гражданина собираются поставить на миграционный учет на адрес медицинского учреждения. <span style=\"text-decoration: underline;\"><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;base=LAW&amp;n=303605&amp;fld=134&amp;dst=1000000001,0&amp;rnd=0.5697746384624651#018254881989119265\" target=\"_blank\">ФЗ-109 \"о миграционном учете\":</a></span></p>\n<p style=\"text-align: justify;\">1. Иностранный гражданин подлежит постановке на учет по месту пребывания:</p>\n<p style=\"text-align: justify;\">2) по адресу гостиницы или иной организации, оказывающей гостиничные услуги, санатория, дома отдыха, пансионата, детского оздоровительного лагеря, туристской базы, кемпинга или иного помещения, в которых он фактически проживает, либо <span style=\"text-decoration: underline;\">по адресу медицинской организации, оказывающей медицинскую помощь в стационарных условиях</span>...</p>\n<p style=\"text-align: justify;\">Если мед.учреждение не выполнит обязанность выступить в качестве принимающей стороны и не поставит иностранца на миграционный учет, его ждет штраф до 500 тысяч рублей по статье 18.9 КоАП РФ:</p>\n<p style=\"text-align: justify;\"><span style=\"text-decoration: underline;\"><a href=\"http://www.consultant.ru/document/cons_doc_LAW_34661/b553913f6c77132d02e4214842ae82be45e60857/\" target=\"_blank\">КоАП РФ Статья 18.9. Нарушение правил пребывания в Российской Федерации иностранных граждан и лиц без гражданства</a></span></p>\n<p style=\"text-align: justify;\">4. Неисполнение принимающей стороной обязанностей в связи с осуществлением миграционного учета, если эти действия не содержат признаков уголовно наказуемого деяния,</p>\n<p style=\"text-align: justify;\">влечет наложение административного штрафа на граждан в размере от двух тысяч до четырех тысяч рублей; на должностных лиц - от сорока тысяч до пятидесяти тысяч рублей; на юридических лиц - от четырехсот тысяч до пятисот тысяч рублей.</p>\n<p style=\"text-align: justify;\"><strong>Это значит, что в случае, если администрация медицинского учреждения соблюдает закон - сначала иностранца поставят на миграционный учет, при убытии по окончанию лечения - снимут с миграционного учета.</strong></p>\n<p style=\"text-align: justify;\"><strong>Хотелось бы обратить внимание на тот факт, что при постановке на миграционный учет в новом адресе&nbsp; прежний миграционный учет аннулируется.</strong></p>\n<p style=\"text-align: justify;\"><strong>Пример:</strong> Степан- гражданин Республики Молдова, имеет патент. Живет вместе с девушкой в принадлежащей ей квартире, где она в свою очередь постоянно оформляет ему учет по месту пребывания, а так же продлевает его.</p>\n<p style=\"text-align: justify;\">Неожиданно Степан попал в больницу: сильно заболел желудок. В больнице у него попросили паспорт и миграционную карту, на вопрос \"зачем?\" ответили \"будем вас регистрировать в УВМ \".&nbsp;</p>\n<p style=\"text-align: justify;\">Если сотрудник больницы, ответственный за взаимодействие с управлением по вопросам миграции - человек неравнодушный а так же юридически подкованный, то при выписке Степана из больницы предупредит его, что нужно встать на миграционный учет заново. А если нет, то получится вот так:</p>\n<p style=\"text-align: justify;\">\"Если вдруг вам так не повезет, что придется полежать в больничке, то имейте в виду, что больница ставит иностранцев на миграционный учет. Причем иногда ставит, но самому иностранцу сказать об этом забывает. И так получается после выписки, что однажды могут позвонить из ФМС (УМВД) и пригласить заплатить штраф пожалуйста. А вы ни сном ни духом, что у вас МУ просрочен. Так как больница снимает вас с МУ в день выписки. А могут и не позвонить - потом сами пойдете и вам сюрприз будет.\"</p>\n<p style=\"text-align: justify;\"><strong>Запомните: если вы попали в больницу, после выписки нужно оформить миграционный учет заново.</strong></p>\n<p style=\"text-align: justify;\"><strong>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(333, 0, 1594395684, NULL, NULL, '<p style=\"text-align: justify;\"><strong>Данная справка требуется только визовикам!</strong><br /> <br /> Требование справки о несудимости до сих пор имеет место быть. Обойти это требование едва ли получится, поскольку информация о криминальном прошлом иностранца может быть для проверки запрошена не только в базах ГИАЦ, но и через международные каналы.<br /> <br /> Где взять и как получить в России справку о несудимости гражданину другой страны?<br /> <br /> Иностранным гражданам за справкой можно также обращаться в консульский отдел при посольстве своего государства либо в свое консульство.<br /> <br /> Их сотрудники передают запрос в правоохранительные органы государства, откуда приехал иностранец, оформляют документ о несудимости и пересылают его дипломатической почтой на территорию РФ.<br /> <br /> При этом может потребоваться нотариально заверенный перевод справки или проставление на ней апостиля.<br /> <br /> Срок действия справки об отсутствии судимости не установлен законодательно.<br /> <br /> Условие о сроке годности выдвигает тот, кто его запрашивает. Но из нашего опыта можем сказать, что справка такого типа на РВП действительна 3 месяца.</p>\n<p style=\"text-align: justify;\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(334, 0, 1594395695, NULL, NULL, '<p style=\"text-align: justify;\">В Санкт-Петербурге на проспекте Римского Корсакова 39 расположена правовая приёмная Фонда поддержки добровольного переселения соотечественников &laquo;ОРИЕНТИР&raquo;, который ведет свою деятельность при поддержке Фонда Президентских Грантов. В этом Фонде работает приемная, юристы которой оказывают бесплатную правовую поддержку мигрантам по всем вопросам (по миграционному законодательству, трудовому и гражданскому праву).</p>\n<p style=\"text-align: justify;\">Как рассказывает один из квалифицированных юристов, работающий в этом Фонде, Александр Ерошкин, в основном мигранты обращаются в приемную по вопросам правильного оформления документации, чтобы избежать нарушения закона о пребывании иностранных граждан на территории Российской Федерации, а также о порядке получения РВП (разрешение на временное проживание). Так, например, в январе 2020 года квалифицированными юристами были оказаны бесплатные консультации более 200 мигрантам (в том числе 82 гражданам Республики Узбекистан, 56 гражданам Республики Таджикистан, 3 гражданам Кыргызской Республики и 61 гражданам Украины).</p>\n<p style=\"text-align: justify;\">Еще одна ситуация, с которой часто сталкиваются мигранты &ndash; это невыплата заработной платы работодателем. В частности, по этому вопросу, благодаря консультантам приемной, за 4 месяца была возвращена задолженность многим мигрантам по заработной плате на сумму свыше 2 680 000 рублей.</p>\n<p style=\"text-align: justify;\">Также бывают случаи, когда мигрант теряет все свои документы. Однажды в приемную обратилась гражданка Республики Узбекистан, по имени Гулджахон, у которой украли сумку в магазине, а в её сумке находились все её документы. Юристы Фонда &laquo;ОРИЕНТИР&raquo; посоветовали ей немедленно заявить в полицию об этом инциденте, чтобы они выдали ей справку. После получения справки, её направили в консульство республики Узбекистан для восстановления паспорта и всех остальных необходимых документов.</p>\n<p style=\"text-align: justify;\">Кроме того, бывают случаи, когда мигрантам выносят запрет на въезд в Россию, что лишает их возможности трудиться и обеспечивать свои семьи. Юристы фонда неоднократно помогали мигрантам отменить запрет в административном и судебном порядке.</p>\n<p style=\"text-align: justify;\">Юристы Фонда ежедневно сталкиваются с различными ситуациями и стараются оказывать полную правовую поддержку мигрантам-соотечественникам, чтобы они как можно скорее адаптировались к условиям проживания и трудились на законных основаниях.</p>\n<p style=\"text-align: justify;\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(335, 0, 1594395706, NULL, NULL, '<p><strong>На сегодняшний день сформирован целый перечень оснований, апеллируя которыми можно остаться на территории нашей страны. К таковым относятся:<br /> </strong><br /> 1. Проживание на территории страны ближайших родственников, являющихся гражданами этого государства;<br /> 2. Наличие гражданства у детей или супруга/супруги;<br /> 3. Наличие действующего патента, разрешение на работу, вида на жительство на момент выдворения;<br /> 4. При наличии необходимости прохождения специализированного медицинского лечения на территории страны или в случае, когда иностранный гражданин уже начал его проходить;<br /> 5. Прохождение очного обучения в государственном вузе страны.<br /> <br /> Но наличие хотя бы одного из вышеуказанных признаков не ведёт к автоматической отмене депортации. Своё право на пребывание в пределах государства нужно доказать, собрать необходимые документы.<br /> <br /> При этом нельзя сказать, каковы у человека шансы на то, что его оставят в России. Каждый случай индивидуален и должен рассматриваться отдельно.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(336, 0, 1594395719, NULL, NULL, '<p><strong>Когда начнется прием заявлений на квоту?</strong></p>\n<p>Приём заявлений на 2020 год ещё не был официально объявлен. Направлять заявления на квоту в Комиссию , можно будет в том же порядке &mdash; почтой России, уже в первые рабочие дни после новогодних праздников.</p>\n<p><strong>Как увеличить шансы на получение квоты?</strong></p>\n<p>Шансы получить квоту значительно вырастут, если Вы подготовите качественный пакет документов. Заявление должно содержать как можно больше сведений, указывающих на вашу связь с Россией. Форма заявления установлена в Приложение № 3 к Приказу ГУ МВД России по г. Санкт-Петербургу и Ленинградской области от 21.11.2018 № 580, в заявление вносится самая основная информация о Вас.</p>\n<p>При необходимости вы вправе приложить к заявлению автобиографию, в которой&nbsp; максимально подробно отразить все то, что связывает Вас с Россией. Но каждый факт должен быть подтвержден документами. Документы должны быть представлены в надлежащем виде с переводом на русский язык. Копии заверены у нотариуса.</p>\n<p>По опыту 2019 году можно судить о том, какие обстоятельства увеличивают шансы на получение квоты. Это наличие родственников в России, в том числе проживающих на основании РВП и ВНЖ, постоянная работа, длительность проживания, законопослушное поведение, уплата налогов, положительные характеристики и т.п.</p>\n<p>Иными словами, если у вас нет других законных возможностей оформить своей пребывание в России, вы можете подать заявление на квоту. Если Вы прибыли в Россию только чтобы работать и более Вас с Россией ничего не связывает, то получить РВП по квоте будет весьма затруднительно. Если &nbsp;Вас удерживают прочные связи, &nbsp;&nbsp;&ndash; получить квоту вполне реально, нужно только подробно обосновать свою заявку. Те, кто собираются работать, тоже вправе обратиться за квотой.</p>\n<p><strong>Кому не дадут квоту?</strong></p>\n<p>Квоты не дадут тем, у кого есть два и более штрафа. Два и более штрафа в течение трех лет подряд являются основанием для установления запрета на въезд, а с запретом документы оформить невозможно. Кроме этого Комиссия возвращает без рассмотрения документы, если они неправильно оформлены. Например, в том случае, если заявление либо приложенные к нему документы не поддаются прочтению, содержат опечатки и неточности. Копии документов, исполненных на иностранном языке, необходимо перевести, а их копии &mdash; заверить у нотариуса.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(337, 0, 1594395734, NULL, NULL, '<p>Соответствующие изменения утверждены Федеральным законом от 06.02.2020 N 16-ФЗ \"О внесении изменений в Федеральный закон \"О правовом положении иностранных граждан в Российской Федерации\" в части упрощения порядка трудоустройства в Российской Федерации обучающихся в российских профессиональных образовательных организациях и образовательных организациях высшего образования иностранных граждан и лиц без гражданства\".</p>\n<p>Законом устанавливается следующее:</p>\n<p>1. Иностранные граждане, обучающиеся в Российской Федерации по очной форме в профессиональных образовательных организациях и образовательных организациях высшего образования по основной профессиональной образовательной программе, имеющей государственную аккредитацию, и работающие в свободное от учебы время отнесены к категориям иностранцев, которым для работы в России не требуется получать разрешения на работу или патент, а работодателю (заказчику работ/услуг) не требуется получать разрешение на привлечение и использование иностранных работников.</p>\n<p>Посмотрим, что это дает всем участникам правоотношений и прежде всего &ndash; на кого именно из обучающихся в России иностранцев будут распространяться указанные преференции? Требования к иностранным гражданам предъявляются следующие:</p>\n<p>а.) Обучение должно проходить по очной форме (очно-заочная и заочная формы обучения исключаются);</p>\n<p>б.) Обучение проходит в профессиональной образовательной организации или образовательной организации высшего образования.</p>\n<p>В соответствии с Законом об образовании к профессиональным образовательным организациям относятся образовательные организации, осуществляющие в качестве основной цели деятельности образовательную деятельность по образовательным программам среднего профессионального образования и (или) по программам профессионального обучения (техникумы, колледжи).</p>\n<p>К образовательным организациям высшего образования относятся образовательные организация, осуществляющие в качестве основной цели деятельности образовательную деятельность по образовательным программам высшего образования и научную деятельность (университет, академия, институт);</p>\n<p>в.) Обучение проходит по основной профессиональной образовательной программе, имеющей государственную аккредитацию.</p>\n<p>К основным профессиональным образовательным программам в соответствии с Законом об образовании относятся:</p>\n<p>- образовательные программы среднего профессионального образования - программы подготовки квалифицированных рабочих, служащих, программы подготовки специалистов среднего звена (реализуются техникумами и колледжами);</p>\n<p>- образовательные программы высшего образования - программы бакалавриата, программы специалитета, программы магистратуры, программы подготовки научно-педагогических кадров в аспирантуре (адъюнктуре), программы ординатуры, программы ассистентуры-стажировки (реализуются университетами, академиями и институтами).</p>\n<p>Государственная аккредитация образовательной деятельности подтверждается свидетельством о государственной аккредитации (форма утверждена Приказом Минобрнауки России от 27.01.2014г. N 45). Указанную информацию можно найти на сайте образовательной организации.</p>\n<p>Вывод № 1: при трудоустройстве с соискателя необходимо истребовать документы, подтверждающие его статус. В нашем случае это будет справка с места обучения и, как минимум, студенческий билет, содержащие совпадающие сведения о зачислении и текущем обучении соискателя. Форма студенческого билета утверждена Приказом Минобрнауки РФ от 05.04.2013г. N 240 &laquo;Об утверждении образцов студенческого билета для студентов и зачетной книжки для студентов (курсантов), осваивающих образовательные программы среднего профессионального образования&raquo; и содержит необходимую для проверки первичную информацию.</p>\n<p>В указанных документах должны содержаться:</p>\n<p>Сведения о форме обучения: очная;</p>\n<p>Сведения о зачислении соискателя в образовательную организацию с указанием реквизитов приказа о зачислении;</p>\n<p>Сведения о статусе образовательной организации (что это &ndash; ПТУ, колледж или институт, академия, университет);</p>\n<p>Сведения о сроке обучения. На этом подробнее остановлюсь ниже.</p>\n<p>Почему и справка и студенческий? Вопрос подлинности представленных документов и срока трудоустройства соискателя. Конечно можно направить запрос в организацию по месту предполагаемого обучения и ждать ответ на него в течение месяца, а можно перестраховаться сличением информации в нескольких документах.</p>\n<p>2. Законом установлено дополнительно основание для прекращения заключенного с таким работником договора &ndash; а именно: завершение или прекращение обучения. Чем такие формулировки Закона чреваты работодателю?</p>\n<p>а.) Прежде всего, следует отметить два возможных варианта срока действия трудового договора, заключаемого с указанными лицами. На них в полной мере распространяются нормы трудового права и, соответственно, требования к сроку действия заключаемого с ними договора. Ситуация предполагает возможность выбора: заключение бессрочного договора со ссылкой на статью 327.1 ТК РФ или срочного (по соглашению сторон) со ссылкой на абзац девятый части второй статьи 59 ТК РФ.</p>\n<p>Предположим, что информацию о сроке обучения работодатель получит из справки с места обучения. Но кто его информирует о прекращении обучения (то есть отчислении до завершения обучения)?&nbsp;</p>\n<p>Вывод № 2: такое положение вещей требует введения в текст заключаемого договора или локальную нормативку организации такой обязанности работника как &laquo;сообщать о прекращении обучения в течение, например, рабочего дня, следующего за днем прекращения обучения с представлением копий соответствующих документов. Работодатель считается извещенным о прекращении обучения работника в день получения копий соответствующих документов под подпись&raquo;. Сделать это необходимо во избежание конфликта интересов с органами контроля в сфере миграции, чтобы доказывать, &laquo;что вы не верблюд&raquo; было проще.</p>\n<p>Далее &ndash; основания увольнения. Какие они? Полагаю, что в случае прекращения обучения подходит пункт 14 части 1 статьи 81 ТК РФ со ссылкой на вводимый Законом пункт 8.1 статьи 13 Федерального закона от 25.07.2002г. № 115-ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo;.</p>\n<p>3. Самым загадочным и опасным с позиции правоприменения представляется следующее: да, иностранным студентам &ndash; очникам предоставили возможность работать в России без получения разрешения на работу (как в настоящее время установлено статьей 13.4 Федерального закона от 25.07.2002г. № 115-ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo;.</p>\n<p>НО&hellip;</p>\n<p>В настоящее время студенты &ndash; очники вправе, получив разрешение на работу, работать без каких-либо временных ограничений, поскольку ТК РФ не устанавливает для них особого режима работы (за исключением не достигших 18-ти лет). Закон же вводит возможность привлекать их без получения разрешения на работу или патента&nbsp;<strong>только в свободное от учебы время.</strong></p>\n<p>Что такое &laquo;свободное от учебы время&raquo;? Формулировка встречается по одному разу как в ТК РФ, так и в Законе об образовании, но не дает ответа на вопрос &ndash; что же считать свободным от учебы временем.</p>\n<p>Таким образом, во избежание возникновения пресловутого конфликта интересов, полагаю, что с указанной категорией лиц придется заключать договоры с указанием на неполное рабочее время в порядке статьи 92 ТК РФ. По сути, любое появление такого сотрудника на рабочем месте в дневное время в рабочие дни может вызвать вопросы от проверяющих с далеко идущими последствиями (поскольку суды трактовать норму будут буквально).</p>\n<p>4. Все вышесказанное в равной мере относится и к порядку заключения гражданско-правовых договоров на выполнение работ/оказание услуг.</p>\n<p>5. Закон вступает в силу 05 августа 2020 года. Поэтому всем заинтересованным в привлечении указанных лиц рекомендую обращаться за разъяснениями, нет, не в ваши территориальные УВМы, а непосредственно в ГУВМ МВД России через официальный сайт.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(338, 0, 1594395745, NULL, NULL, '<p style=\"text-align: justify;\">Приказом МВД России от 04.12.2019г. № 907 утвержден новый Административный регламент по предоставлению государственной услуги по оформлению, выдаче, продлению срока действия и восстановлению виз иностранным гражданам (далее &ndash; Административный регламент).</p>\n<p style=\"text-align: justify;\">С текстом&nbsp;<span style=\"text-decoration: underline;\"><a href=\"http://publication.pravo.gov.ru/Document/View/0001202001170001\" target=\"_blank\">Приказа можно ознакомиться на официальном интернет-портале правовой информации</a></span>.</p>\n<p style=\"text-align: justify;\">Приказ, а с ним и новый порядок оформления виз иностранным гражданам вступает в силу с 28-го января 2020 года.</p>\n<p style=\"text-align: justify;\">Первое, что бросается в глаза &ndash; изменен круг заявителей, то есть лиц&cedil; которые вправе обращаться в уполномоченные органы за получением государственной услуги.</p>\n<p style=\"text-align: justify;\">В соответствии с пунктом 2 Административного регламента к числу заявителей теперь относятся либо сам иностранный гражданин (лицо без гражданства), находящийся в Российской Федерации [1] либо надлежащим образом уполномоченный этим иностранным гражданином представитель [2].</p>\n<p style=\"text-align: justify;\">Напомним, что Административный регламент, утв. Приказом МВД России от 16.11.2017г. № 859 допускал представление документов от имени иностранного гражданина принимающей&nbsp;стороной (лицом, поставившим мигранта на учет по месту пребывания).</p>\n<p style=\"text-align: justify;\">[1] Такая формулировка исключает получение визы иностранцем, находящимся за пределами России (так что возможность вклеивания визы в паспорт, присланный DHL исключается).</p>\n<p style=\"text-align: justify;\">Однако, в связи с изменением Закона о миграционном учете работодатели в большинстве своем потеряли возможность ставить своих иностранных работников на учет по месту пребывания и соответственно предоставлять документы для получения, продления, восстановления виз своим работникам-иностранцам. То, что об этом не знали ни вы, уважаемые читатели, ни сотрудники подразделений по вопросам миграции, не опровергает того факта, что последние 2 года визы по стране оформлялись по сути незаконно (о чем смотрите по ссылке, указанной выше).</p>\n<p style=\"text-align: justify;\">[2] Сейчас же важно не это, а то, что новый Административный регламент однозначно указывает на возможность обращения в органы МВД за получением государственной услуги либо самого мигранта, либо уполномоченного представителя.</p>\n<p style=\"text-align: justify;\">Существующий порядок вещей указывает на то, что полномочия представителя буду подтверждаться нотариально удостоверенной доверенностью. Так что готовьтесь, уважаемые читатели из числа работающих с визовыми иностранцами.</p>\n<p style=\"text-align: justify;\">Конечно, ГК РФ дает возможность заверения доверенности в нашем случае сотрудником подразделения по вопросам миграции, в которое обращается экспат для, скажем, продления визы, но это позволит представителю лишь получить визу, но не сдать документы на ее оформление.</p>\n<p style=\"text-align: justify;\">Второе &ndash; из текста Административного регламента исчезло упоминание о графике приема заявителей, который ранее был утвержден по сути для всей территории России. В настоящем регламенте график работы подразделений по вопросам миграции отдается на откуп им самим и размещается на официальных сайтах и стендах.</p>\n<p style=\"text-align: justify;\">Далее Административным регламентом устанавливается следующее.</p>\n<p style=\"text-align: justify;\"><strong>1. Непосредственное оказание государственной услуги осуществляется подразделениями по вопросам миграции территориальных органов МВД Росси на региональном и районном уровне.</strong></p>\n<p style=\"text-align: justify;\">Таким образом, возможно, что процедуру спустят на места (в районы) и обращаться за получением, продлением и восстановлением виз придется не в подразделения УВМ в соответствующих субъектах, а в ОВМ в районах и муниципальных образованиях (пункт 18 Административного регламента).</p>\n<p style=\"text-align: justify;\">Напомним,&nbsp;что в соответствии с положениями пунктов 34, 35, 35 (1) Правил, утв. Постановлением Правительства Постановление Правительства РФ от 09.06.2003 № 335 продление срока действия обыкновенных учебных и рабочих виз осуществляется по месту постановки иностранного гражданина по месту пребывания.</p>\n<p style=\"text-align: justify;\">Именно поэтому сотрудники подразделений по вопросам миграции требуют представления отрывной части бланка уведомления о прибытии иностранного гражданина в место пребывания. Аналогичные нормы приведены и в Административном регламенте.</p>\n<p style=\"text-align: justify;\"><strong>2. Обозначены категории виз, которые вправе оформлять подразделения по вопросам миграции (пункт 20 Административного регламента):</strong></p>\n<p style=\"text-align: justify;\"><strong>Транзитные (ТР1)</strong>. Оформляются для выезда из России. Например, детям иностранцев, родившимся на территории нашей страны.</p>\n<p style=\"text-align: justify;\"><strong>Визы временно проживающего лица.</strong>&nbsp;Оформляются иностранным гражданам, находящимся в России на момент получения разрешения на временное проживание) либо въехавшим в Россию по выданной МИДом визе временно проживающего лица сроком действия до 4-х месяцев.</p>\n<p style=\"text-align: justify;\"><strong>Визы на въезд в Россию в целях приема в гражданство Российской Федерации</strong>. Выдается лицам, признанным носителем русского лица в период нахождения в России.</p>\n<p style=\"text-align: justify;\"><strong>Многократные обыкновенные рабочие визы</strong>&nbsp;(в том числе квотникам и внеквотникам по схеме 90-360 и высококвалифицированным специалистам).</p>\n<p style=\"text-align: justify;\">Также подразделения МВД России по вопросам миграции вправе продлевать и восстанавливать визы всех категорий кроме дипломатических и служебных.</p>\n<p style=\"text-align: justify;\">Срок оказания государственной услуги (оформления визы) установлен не более чем 20 рабочих дней с момента подачи вех необходимых документов. Из чего можно сделать вывод о возможности оформления визы и в меньший срок (пункт 21 Административного регламента).</p>\n<p style=\"text-align: justify;\"><strong>3. Установлен перечень документов, необходимых для оформления визы (пункты 24-24.3 Административного регламента).</strong></p>\n<p style=\"text-align: justify;\">Визовая анкета по форме, утв. Приказом МВД России, ФСБ России, МИД России от 27.04.2017г. № 233/235/7018. Анкета подписывается мигрантом лично.</p>\n<p style=\"text-align: justify;\">Действительный документ, удостоверяющий личность иностранного гражданина (паспорт).</p>\n<p style=\"text-align: justify;\">Паспорт должен иметь не менее 2-х чистых страниц, предназначенных для проставления виз. Срок его действия не должен истекать,&nbsp;как правило, ранее, чем через 6 месяцев после окончания срока действия выдаваемой визы (таким образом, формулировка оставляет лазейку для получения визы по паспорту с истекающим сроком действия).</p>\n<p style=\"text-align: justify;\">Фотографии представляются только в том случае, если в соответствующем подразделении по вопросам миграции выдаются визы на вкладных бланках, а не в виде вклеиваемых &laquo;стикеров&raquo;.</p>\n<p style=\"text-align: justify;\"><strong>3.1. Для продления обыкновенной рабочей или обыкновенной учебной визы дополнительно к перечисленным выше документам необходимо представить (пункт 27 Административного регламента):</strong></p>\n<p style=\"text-align: justify;\">&rarr; Ходатайство от приглашающей стороны, форма которого утверждена (для юридических лиц) Приложением № 2 к Административному регламенту. Ходатайство представляется в одном экземпляре;</p>\n<p style=\"text-align: justify;\">&rarr; Трудовой или гражданско-правовой договор (оригинал) с работодателем или заказчиком выполнения работ/оказания услуг или документ, подтверждающий обучение иностранного гражданина. С учетом того, что форма и требования к такому документу Административным регламентом не утверждены, следует ожидать, что каждое подразделение по вопросам миграции будет истребовать понятные только им документы.</p>\n<p style=\"text-align: justify;\">При этом действующая виза должна истекать не ранее чем через 20 рабочих дней со дня подачи документов. Исключения &ndash; документально подтвержденная уважительная причина: болезнь, утрата паспорта, въезд в Россию менее чем за 20 рабочих дней до истечения срока действия выданной ранее визы, иные уважительные причины (вот она &ndash; почва для коррупции.</p>\n<p style=\"text-align: justify;\">Иные причины могут быть приняты во внимание на усмотрение руководства соответствующего подразделения по вопросам миграции).</p>\n<p style=\"text-align: justify;\"><strong>3.2. В случае, если документы подаются через представителя дополнительно представляются (пункт 28 Административного регламента):</strong></p>\n<p style=\"text-align: justify;\">- Паспорт представителя;</p>\n<p style=\"text-align: justify;\">- Доверенность на представление интересов.</p>\n<p style=\"text-align: justify;\"><strong>4. Установлен перечень документов, которые необходимо представить для исправления допущенных при оформлении визы опечаток (пункт 29-29.5 Административного регламента).</strong></p>\n<p style=\"text-align: justify;\">Помимо имеющихся у иностранного гражданина или его представителя при себе на момент получения визы документов перечень включает Заявление об исправлении опечаток и (или) ошибок, допущенных в выданной визе по форме, утв. Приложением № 3 к Административному регламенту.</p>\n<p style=\"text-align: justify;\">Вывод: при себе при получении визы необходимо (на всякий случай) иметь указанный документ, а у представителя по доверенности соответствующие полномочия (представление в подразделение по вопросам миграции такого заявления) должны быть прописаны в доверенности.</p>\n<p style=\"text-align: justify;\"><strong>5. Паспорт, трудовой или гражданско-правовой договор с иностранным гражданином, документ, подтверждающий обучение иностранного гражданина в Российской Федерации (см. выше) возвращаются заявителю,</strong>&nbsp;а их копии (которые, кстати, должны снять сотрудники подразделения по вопросам миграции по месту обращения) приобщаются к визовой анкете (пункт 30 Административного регламента).</p>\n<p style=\"text-align: justify;\"><strong>6. Дополнительно заявитель вправе (в нашей действительности обязан) представить следующие документы:</strong></p>\n<p style=\"text-align: justify;\">&rarr; Квитанцию об уплате государственной пошлины (совокупный анализ положения пунктов 1 и 8 статьи 45, статьи 333.16, пункта 1 статьи 333.28 НК РФ позволяет обоснованно предположить, что оплата госпошлины возможна от лица иностранного гражданина третьим лицом, например представителем иностранного гражданина);</p>\n<p style=\"text-align: justify;\">&rarr; Отрывную часть бланка уведомления о прибытии в место пребывания</p>\n<p style=\"text-align: justify;\">&rarr; Разрешение на временное проживание</p>\n<p style=\"text-align: justify;\">&rarr; Разрешение на работу</p>\n<p style=\"text-align: justify;\">&rarr; Решение о признании носителем русского языка</p>\n<p style=\"text-align: justify;\">&rarr; Копии всех тех документов, с которых (см. выше) сотрудники подразделения по вопросам миграции должны снять копии и приобщить их к анкете.</p>\n<p style=\"text-align: justify;\">Исходя из суровой российской действительности, представлять придется копии всех перечисленных документов (разумеется с оригиналами).</p>\n<p style=\"text-align: justify;\">Перечень документов на этом заканчивается. Он является&nbsp;<strong>исчерпывающим</strong>. Пунктом 35 Административного регламента запрещается требовать какие-либо дополнительные документы, помимо перечисленных, равно как и настаивать на представлении документов, которые заявитель&nbsp;вправе представить дополнительно&nbsp;(см. выше). Ответственность для особо рьяных сотрудников подразделений по вопросам миграции в этом случае установлена статьей 5.63 КоАП РФ.</p>\n<p style=\"text-align: justify;\"><strong>7. Административный регламент предполагает возможность подачи документов через Единый портал &laquo;Госуслуги&raquo;</strong>&nbsp;(пункт 31 Административного регламента). Однако, на портале соответствующая услуга отсутствует. В случае, если все же эта функция на Госпортале заработает подать запрос на ее получение можно будет при наличии у иностранного гражданина простой электронной подписи в соответствии с Постановлением Правительства от 25.06.2012 № 634.</p>\n<p style=\"text-align: justify;\">8. В случае приема документов к рассмотрению&nbsp;<strong>заявителю или его представителю выдается справка</strong>&nbsp;по форме, утв. Приложением № 5 к Административному регламенту и сообщается предполагаемая дата выдачи визы.</p>\n<p style=\"text-align: justify;\">В случае отказа в приеме документов, о чудо (!), в обязательном порядке выдается справка об отказе в приеме документов по форме, утв. Приложением 3 4 к Административному регламенту (пункты 72, 74, 76 Административного регламента).</p>\n<p style=\"text-align: justify;\"><strong>9. В период рассмотрения документов заявитель вправе отказаться от получения государственной услуги</strong>, о чем представить в подразделение по вопросам миграции заявление в произвольной форме. В таком случае рассмотрение анкеты прекращается (пункт 97 Административного регламента).</p>\n<p style=\"text-align: justify;\"><strong>10. Крайне интересно&nbsp;описана в Административном регламенте процедура исправления допущенных подразделением по вопросам миграции ошибок и опечаток в напечатанной визе.</strong></p>\n<p style=\"text-align: justify;\">При обращении за получением визы заявитель или его представитель проверяют визу на наличие ошибок и опечаток. В случае выявления таковых оформленная виза аннулируется, а взамен нее в тот же день оформляется новая виза.</p>\n<p style=\"text-align: justify;\">Но не будем забывать, что в новом регламенте присутствует такой документ как Заявление об исправлении опечаток и (или) ошибок, допущенных в выданной визе. А также в целом разделе в пунктах со 137-го по 146-й расписана процедура исправления допущенных ошибок и опечаток.</p>\n<p style=\"text-align: justify;\">Из содержания указанных норм следует, что для исправления допущенных&nbsp;по вине органа власти&nbsp;ошибок и опечаток иностранному гражданину необходимо подать указанное заявление, которое будет рассматриваться в течение 1-го рабочего дня со дня приема заявления, еще один рабочий день виза будет изготавливаться и на 3-й рабочий день (ура!) может быть выдана.</p>\n<p style=\"text-align: justify;\">На основании изложенного сложно сделать вывод о том, как схема будет работать на практике.</p>\n<p style=\"text-align: justify;\">В общем следует отметить, что новый Административный регламент нисколько не упрощает процедуру оформления виз иностранным гражданам, включает массу норм, которые дают возможность свободного толкования и, как это модно говорить, содержат коррупционные риски.</p>\n<p style=\"text-align: justify;\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(339, 0, 1594395755, NULL, NULL, '<p><span><span>Каждый год большое количество наших соотечественников выбирает для дальнейшего обучения именно российские вузы, благодаря высокому качеству образования, достаточно небольшой цене на обучение и проживание, а также из-за культурной близости наших стран. Однако по сей день процедура поступления из стран Центральной Азии в вузы России не является очень удобной и может сильно отличаться от учреждения к учреждению. </span></span></p>\n<p>Однако скоро эта ситуация изменится и схема поступления иностранных граждан в вузы РФ значительно упростится и унифицируется. Более того, большую часть действий абитуриент сможет выполнить онлайн, т.е удаленно. Такая схема существенно упросит поступление и будет гораздо дешевле для абитуриента.</p>\n<p>Разработка сервиса уже началась и как ожидают в Россотрудничестве, заработает он совсем скоро, уже к 2021 году.<br /> <br /> А если вы уже находитесь в России и вам нужно оплатить обучения для вашего ребёнка, вы всегда можете получить заём на оплату обучения в компании М Булак, для этого вам нужно позвонить по номеру горячей линии или оформить заявку на нашем сайте.</p>\n<p><span><span>&nbsp;</span></span></p>\n<p><span><span>&nbsp;По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</span></span></p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(340, 0, 1594395770, NULL, NULL, '<p><img src=\"data:image/png;base64,\" alt=\"\" /></p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(341, 0, 1594395780, NULL, NULL, '<p>Министерство внутренних дел России опубликовало статистические данные по всем показателям миграционной обстановки в стране. Документ называется \"Отдельные показатели миграционной ситуации в РФ за январь - декабрь 2019 года с распределением по регионам\". Из отчета следует, что в минувшем году в Россию не были допущены более 250 тысяч иностранцев и лиц без гражданства. Годом ранее въезд был запрещен 253 тысячам человек. Причины отказов в документе не уточняются.</p>\n<p>В то же время вдвое увеличилось число людей, получивших российское гражданство. Если в 2018 году этот показатель составлял 269 тысяч человек, то в 2019-м - уже почти 497 тысяч. Из них более 299 тысяч прибыли из Украины, около 50 тысяч - из Казахстана, более 44 тысяч - из Таджикистана, 24 тысячи - из Армении, 19 тысяч - из Узбекистана, 15,8 тысячи - из Молдовы.</p>\n<p>\"Новыми россиянами\" в минувшем году также стали по одному жителю Египта, Мозамбика, Панамы, Руанды, Сенегала, Сингапура, Словении, Уганды и Швейцарии.</p>\n<p>А еще гражданами России стали 7470 лиц без гражданства. В эту категорию попали, в частности, граждане, имевшие только паспорт СССР, многие из которых отсидели в колониях.</p>\n<p>При этом больше всего \"новых россиян\" зарегистрировались в Южном федеральном округе - более 203 тысяч человек из 497 тысяч. Из них почти 167 тысяч \"прописались\" в Ростовской области, еще около 18 тысяч - в Краснодарском крае. Нетрудно догадаться, что это выходцы с Украины (всего, напомним, 299 тысяч украинцев получили российское гражданство в 2019 году).</p>\n<div class=\"incut\">Больше всего \"новых россиян\" зарегистрировались в ЮФО - более 203 тысяч из 497 тысяч человек</div>\n<p>Напомним, в конце прошлого года прошло заседание Межведомственной комиссии по реализации госпрограммы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом. Возглавляющий эту комиссию глава МВД России генерал полиции Владимир Колокольцев рассказывал, что 125 тысячам жителей Донецкой и Луганской областей Украины одобрены заявки на получение российского гражданства. \"В период с апреля текущего года в пункты приема документов в Ростовской области поступило свыше 160 тысяч заявлений - по 125 тысячам лиц уже приняты решения о приеме в гражданство\", - отмечал министр.</p>\n<div class=\"b-read-more b-read-more_large b-read-more_right\">\n<div class=\"b-read-more__list\">\n<div class=\"b-read-more__item\">\n<div class=\"b-read-more__media\"><source /></div>\n</div>\n</div>\n</div>\n<p>В прошлом году президент России Владимир Путин предоставил возможность воспользоваться упрощенным порядком получения российского гражданства всем жителям Донецкой и Луганской областей Украины. Причем получить российское гражданство в упрощенном порядке могут украинцы, бежавшие от конфликта в Россию не только из республик Донбасса, а со всей территории Донецкой и Луганской областей, включая подконтрольные Киеву районы.</p>\n<p>На сегодняшний день соотечественников принимают уже 80 субъектов РФ.</p>\n<p>Что же касается туристов, то в прошлом году в России, согласно свежей статистике МВД, было оформлено более 506 тысяч приглашений и более 348 тысяч виз. Больше всего виз получили японцы - 105, 8 тысяч. Более 92 тысяч виз получили граждане Китая, более 41 тысяч виз - жители Туркмении, 41,6 - Грузии, 28,7 - Турции, 13,6 - США.</p>\n<p>Кроме того, более 234 тысяч человек первично получили разрешение на временное проживание в России, свыше 182 тысяч - вид на жительство. Также было оформлено более 1,7 млн патентов для иностранных граждан и лиц без гражданства, почти 127 тысяч разрешений на работу, из которых более 34 тысяч - для высококвалифицированных специалистов.</p>\n<p>Ранее руководитель Главного управления по вопросам миграции МВД генерал-майор Валентина Казакова рассказывала, что ежегодно легально въезжают в Россию около 17 миллионов иностранцев.</p>\n<p>Из сайта \"Российской Газеты\".</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(342, 0, 1594395794, NULL, NULL, '<p class=\"paragraph ntvOAbw ntvOAbw_text_m\"><strong class=\"b typo typo_bold\">Минтруд утвердил новый перечень профессий, работая по которым в России иностранцы могут получить гражданство РФ в упрощённом порядке. Приказ ведомства размещен на портале правовой информации.</strong></p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В новый список вошли 135 профессий и специальностей против 74 позиций в старом перечне от 2015 года.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Как следует из документа, среди новых профессий&nbsp;и должностей в списке -&nbsp;инженер&nbsp;по горным работам, мастер&nbsp;буровой, механик&nbsp;по буровым и горным работам, монтажник&nbsp;радиоэлектронной аппаратуры и приборов, сборщик&nbsp;изделий электронной техники.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Также в упрощенном порядке получить гражданство России смогут агрономы и зоотехники, воспитатели и учителя, корреспонденты и редакторы, рыбоводы и энтомологи.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В списке появились и профессии из сферы медицины и фармацевтики &mdash; врач-психиатр детский, врач-психиатр-нарколог, врач-терапевт участковый, врач-хирург, врач-эпизоотолог, офтальмолог, стоматолог, травматолог-ортопед и другие.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Документ подготовлен в связи со вступлением в силу 2 октября 2019 года Федерального закона, по которому высококвалифицированные специалисты &mdash; иностранцы и лица без гражданства, проживающие в России, могут обратиться с заявлениями о приёме в российское гражданство в упрощённом порядке&nbsp;без соблюдения условия о сроке проживания. Для этого они должны не менее одного года до дня обращения с заявлением работать в России по профессии, включённой в специальный перечень.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(343, 0, 1594395807, NULL, NULL, '<p>Получить российское гражданство можно будет, не отказываясь от уже имеющегося иного гражданства. Соответствующие изменения в закон \"О гражданстве РФ\" готовит правительство. Законопроект могут внести в Госдуму уже в этом месяце.</p>\n<p>Речь идет о том, чтобы признать утратившим силу пункт \"г\" части первой статьи 13 закона \"О гражданстве Российской Федерации\". Согласно ему, одним из условий приобретения иностранными гражданами российского гражданства является отказ от имеющегося у них иного гражданства.</p>\n<p>Активная работа российских властей над изменением миграционного законодательства, а также норм, регулирующих получение гражданства РФ, началась в 2018 году. Принятая тогда Концепция государственной миграционной политики (действует до 2025 года) предписывает открыть Россию для всех, кто хочет здесь жить и работать, упростить правила въезда, включая выдачу виз, а также процедуры предоставления российского гражданства. Миграционная политика, говорится в концепции, \"должна быть направлена на создание благоприятного режима для добровольного переселения в РФ лиц, в том числе покинувших ее, которые способны включиться в систему позитивных социальных связей и стать полноправными членами российского общества\".</p>\n<p>В 2020 году консультации продолжают оказываться в рамках реализации проекта \"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\", поддерживаемого \"Фондом-оператором президентских грантов по развитию гражданского общества\", в приемную Фонда \"ОРИЕНТИР\" ежедневно обращаются трудовые мигранты за разъяснениями и консультациями по миграционному законодательству.</p>\n<p>При этом юристы Фонда отмечают, что со стороны мигрантов не редко звучат жалобы на сотрудников миграционных центров. Жалобы поступают по ряду вопросов: некомпетентность сотрудников, грубое отношение сотрудников службы охраны, несоблюдение правил, несвоевременная выдача документов, выдача документов с ошибками, из-за чего мигрантам приходится заново оформлять документы, что требует дополнительных затрат по времени и денежных средств. В то время как срок постановки на миграционный учет трудовых мигрантов ограничен.</p>\n<p>http://www.fondorientir.ru/</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(344, 0, 1594395817, NULL, NULL, '<p class=\"paragraph ntvOAbw ntvOAbw_text_m\"><strong class=\"b typo typo_bold\">Дефицит узкопрофильных врачей и младшего медперсонала, учителей, слесарей, трактористов и многих других квалифицированных специалистов&nbsp;Правительство планирует восполнить за счёт иностранцев. Упростить получение гражданства России хотят приезжим реаниматологам и стоматологам, ветеринарам и зоотехникам, инженерам и механизаторам, журналистам &mdash; всего в списке 135 профессий.​ &laquo;Парламентская газета&raquo; разобралась, как мигранты могут помочь экономике страны.</strong></p>\n<h3 class=\"title title_size_s ntvOAbw ntvOAbw_text_l\">Мигранты исправляют демографию</h3>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Демографический фактор играет заметную роль в социально-экономическом развитии страны. И пока своими силами государство в этом вопросе не справляется: численность населения пошла на убыль. Одна из главных проблем &mdash; устойчивая тенденция старения населения, а также высокий уровень смертности трудоспособных граждан, констатирует Минэкономразвития в аналитических материалах, сопровождающих проект бюджета на 2020-й и плановый период 2021-2022 годов. Конечно, Правительство делает основную ставку на внутренние ресурсы, для чего разрабатывает меры поддержки семей с детьми и программы по улучшению системы здравоохранения. Но всё это не даст мгновенного эффекта, так что до 2023 года численность населения будет расти только благодаря миграционному потоку, отмечается в аналитике ведомства.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">По данным Росстата, в прошлом году естественную убыль населения компенсировал миграционный прирост на 57,2 процента. В кабмине рассчитывают, что привлечение &laquo;высококачественного человеческого капитала&raquo; из-за рубежа позволит&nbsp;обеспечить рынок труда востребованными кадрами.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В приоритете две группы иностранцев: соотечественники, то есть люди, кровно и культурно связанные с нашей страной, и квалифицированные специалисты, перспективная молодёжь. По каждой &laquo;целевой аудитории&raquo; предлагаются механизмы создания комфортных условий для переезда с семьёй и работы в России, упрощения легализации и получения гражданства.</p>\n<h3 class=\"title title_size_s ntvOAbw ntvOAbw_text_l\">Приоритет &mdash; медикам и техникам</h3>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В помощь соотечественникам за прошедший год было подготовлено немало послаблений. Это и указы президента Владимира Путина, по которым в упрощённом порядке гражданство могут получить жители Донбасса, и законопроект по реформированию системы вида на жительство, и расширение преференций для участников госпрограммы переселения соотечественников.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Что касается упрощённого порядка приобретения гражданства квалифицированными специалистами, то он действует с 2014 года. Система простая: Минтруд определяет перечень профессий, в которых нуждается экономика, и иностранцы, владеющие требуемыми навыками, могут в ускоренном порядке натурализоваться.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Изначально квалифицированным трудовым мигрантам нужно было проработать в России три года, прежде чем стать гражданами России. Но 2 октября вступил в силу закон, который сократил этот срок, &mdash; теперь достаточно потрудиться один год. Одно условие: всё это время работодатель обязан перечислять в Пенсионный фонд страховые взносы за своего иностранного специалиста.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Действующий перечень профессий, которые подпадают под такое упрощение, Минтруд утвердил в 2015 году. Он насчитывает 74 позиции, среди них &mdash; медики, монтажники, слесари, токари и электромонтёры разного профиля, газосварщики, математики и директора по экономике.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Со 2 октября квалифицированным трудовым мигрантам, чтобы получить гражданство, достаточно потрудиться в России один год.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">На очереди &mdash;&nbsp;расширение этого перечня. Этот шаг продиктован планом реализации Концепции миграционной политики до 2025 года. На прошлой неделе Минтруд опубликовал проект нового списка для общественного обсуждения, оно продлится до 16 октября.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В новом перечне 135 позиций. Из него выбыло три специальности (они числились в старом списке, а в новом не указаны): врач, врач общей практики (семейный), врач приёмного отделения. Зато стало намного больше узкопрофильных специалистов: реаниматолог, онколог, стоматолог, детский и подростковый психиатр, травматолог, невролог, хирург, энтомолог и т.д. Добавились позиции по среднему звену: медсёстры, акушерки, фельдшеры и лаборанты.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Судя по проекту нового перечня, появился в России спрос и на иностранцев, работающих в области сельского хозяйства: агрономов и агрохимиков, рыбоводов, зоотехников, ветеринаров, слесарей по ремонту сельхозтехники, трактористов. Ещё государство готово упростить приобретение гражданства воспитателям и учителям. Прибавилось и технических специальностей. Среди новых &mdash; техник-картограф, технолог пищевой промышленности, инженер по горной промышленности, мастер буровой, механизатор. Интересно, что упростят натурализацию также работникам медиасферы: ведущим, корреспондентам и редакторам.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Новый перечень формировался на основании предложений федеральной исполнительной власти и регионов, уточнили в Минтруде.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В каких регионах работает больше всего иностранцев</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Москва 24,2% Московская область 12,5% Санкт-Петербург 12,4% Ленинградская область 3,3% Краснодарский край 2,6% Свердловская область 2,2% ХМАО (Югра) 2% Иркутская область 1,9% Тюменская область 1,8%</p>\n<h3 class=\"title title_size_s ntvOAbw ntvOAbw_text_l\">Рынок труда голодает</h3>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">&laquo;Минтруд обозначил те профессии, в которых нуждается страна. У нас действительно есть потребность в таких квалифицированных кадрах&raquo;, &mdash; подтвердил глава Комитета Госдумы по труду, социальной политике и делам ветеранов&nbsp;Ярослав Нилов (ЛДПР). По его словам, в перечне профессий иностранных граждан отражены основные области экономики, испытывающие кадровый голод. Депутат добавил, что проблемы на рынке труда обусловлены в том числе тем, что наши квалифицированные кадры всё больше уезжают из страны.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Кроме того, в некоторых сферах мигранты буквально вытесняют россиян,&nbsp;потому что готовы работать за меньшие деньги, а ещё потому, что им сложнее защитить свои права, чем пользуются корыстные бизнесмены и чиновники. Правда, это больше характерно для рынка низкоквалифицированной рабочей силы, уточнил законодатель.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">&laquo;Вообще, я думаю, миграция должна быть по запросу. Если запрос есть в соответствующем субъекте, то туда должны приезжать нужные специалисты в необходимом количестве. В противном случае мы будем получать дисбаланс на рынке труда и вытеснение из него наших граждан&raquo;, &mdash; сказал Нилов &laquo;Парламентской газете&raquo;.</p>\n<h3 class=\"title title_size_s ntvOAbw ntvOAbw_text_l\">Как иностранцы подтверждают квалификацию</h3>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">В России признаются дипломы следующих стран: Азербайджан (с 2003 года), Армения (с 2009 года), Кипр (с 2017 года), Белоруссия, Казахстан, Киргизия и Таджикистан (с 1998 года), Молдавия (с 2003 года), Туркменистан (с 2009 года), Албания (с 2000 года), Вьетнам (с 2010 года), Китай (с 1995 года), Монголия (с 2006 года), ЮАР (с 2013 года), Венесуэла (с 2010 года), Куба (с 2010 года), Перу (с 2012 года), Мальта (с 2017 года), Украина (с 2003 года), Сербия (с 2015 года), Венгрия (с 2019 года), Абхазия (с 2017 года), Узбекистан (с 2019 года), Франция (с 2016 года), Италия (с 2016 года). Глядя на список &laquo;вакантных&raquo; специальностей, возникает один вопрос: как же приезжие хирурги, учителя и слесари подтверждают своё мастерство? В России этими вопросами занимается ФГБУ Главный государственный экспертный центр оценки образования (Главэкспертцентр). Именно здесь происходит признание, подтверждение и легализация иностранных документов об образовании. Только после проверки Главэкспертцентра работодатель может довериться тому, что написано в дипломах соискателей из Европы, Азии и Африки.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">Российская Федерация заключила соглашения о взаимном признании документов об образовании с десятками государств &mdash; преимущественно, это СНГ и азиатские страны. В реальной жизни это значит, что диплом, скажем, азербайджанского медицинского университета будет проходить проверку по упрощённой процедуре (на подлинность и наличие национальной аккредитации). Дипломы из стран, которые не признаются в нашей стране, также позволяют работать в России, но их проверка занимает больше времени. В том числе потому, что их подтверждают на основе экспертизы. Особенно кропотливо проверяют документы по профессиям медиков, учителей, лётчиков и другим, связанным с безопасностью и людьми. Такие специалисты после признания диплома в Главэкспертцентре должны получить профессиональный допуск в профильном ведомстве &mdash; Росздравнадзоре, Росавиации и так далее.</p>\n<p class=\"paragraph ntvOAbw ntvOAbw_text_m\">По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(345, 0, 1594395828, NULL, NULL, '<div class=\"entry-description\">\n<p>Многие люди, которые приезжают на заработки в Россию часто решают получить российское гражданство, чтобы облегчить себе поиск работы, получение социальных и трудовых гарантий, сэкономить время и деньги на бюрократические процедуры, связанные с положением мигранта, вроде покупки и продления патента и т.&nbsp;д.<br /> <br /> Одним из самых простых способов получить гражданство Российской Федерации в ускоренном режиме является профессиональная квалификация, так как именно квалифицированные специалисты получают право обзавестись гражданством в ускоренном режиме.<br /> <br /> Так, люди которые работают в России не менее года по одной из профессий, утвержденных министерством труда РФ, получают право на ускоренное получение гражданства.</p>\n<p>До недавнего времени список состоял из 74 профессий, однако с 29 февраля 2020 года он увеличился до 135 позиций. Так, в новый список, к примеру, вошли такие профессии как агроном, агрохимик, воспитатель, зоотехник, корреспондент, механизатор, редактор, рыбовод, тракторист, учитель и т.д.</p>\n<p><strong>Перечень профессий (специальностей, должностей) иностранных граждан и лиц без гражданства - квалифицированных специалистов, имеющих право на прием в гражданство Российской Федерации в упрощенном порядке: </strong></p>\n<p>1.&nbsp;Агроинженер</p>\n<p>2.&nbsp;Агроном</p>\n<p>3.&nbsp;Агрохимик</p>\n<p>4.&nbsp;Акушерка</p>\n<p>5.&nbsp;Ведущий программы</p>\n<p>6.&nbsp;Ветеринарно-санитарный врач</p>\n<p>7.&nbsp;Ветеринарно-санитарный эксперт</p>\n<p>8.&nbsp;Ветеринарный врач</p>\n<p>9.&nbsp;Ветеринарный врач репродуктивной зоны</p>\n<p>10.&nbsp;Ветеринарный врач-гинеколог</p>\n<p>11.&nbsp;Ветеринарный врач-терапевт</p>\n<p>12.&nbsp;Воспитатель</p>\n<p>13.&nbsp;Врач здравпункта</p>\n<p>14.&nbsp;Врач клинической лабораторной диагностики</p>\n<p>15.&nbsp;Врач общей практики (семейный врач)</p>\n<p>16.&nbsp;Врач общей практики (семейный врач)</p>\n<p>17.&nbsp;Врач по паллиативной медицинской помощи</p>\n<p>18.&nbsp;Врач приемного отделения</p>\n<p>19.&nbsp;Врач скорой медицинской помощи</p>\n<p>20.&nbsp;Врач станции (отделения) скорой и неотложной медицинской помощи</p>\n<p>21.&nbsp;Врач-анестезиолог-реаниматолог</p>\n<p>22.&nbsp;Врач-бактериолог</p>\n<p>23.&nbsp;Врач-детский онколог</p>\n<p>24.&nbsp;Врач-кардиоревматолог</p>\n<p>25.&nbsp;Врач-лаборант</p>\n<p>26.&nbsp;Врач-невролог</p>\n<p>27.&nbsp;Врач-онколог</p>\n<p>28.&nbsp;Врач-оториноларинголог</p>\n<p>29.&nbsp;Врач-офтальмолог</p>\n<p>30.&nbsp;Врач-педиатр</p>\n<p>31.&nbsp;Врач-педиатр участковый</p>\n<p>32.&nbsp;Врач-психиатр</p>\n<p>33.&nbsp;Врач-психиатр детский</p>\n<p>34.&nbsp;Врач-психиатр детский участковый</p>\n<p>35.&nbsp;Врач-психиатр подростковый</p>\n<p>36.&nbsp;Врач-психиатр подростковый участковый</p>\n<p>37.&nbsp;Врач-психиатр участковый</p>\n<p>38.&nbsp;Врач-психиатр-нарколог</p>\n<p>39.&nbsp;Врач-специалист</p>\n<p>40.&nbsp;Врач-статистик</p>\n<p>41.&nbsp;Врач-стоматолог</p>\n<p>42.&nbsp;Врач-терапевт</p>\n<p>43.&nbsp;Врач-терапевт участковый</p>\n<p>44.&nbsp;Врач-травматолог-ортопед</p>\n<p>45.&nbsp;Врач-фтизиатр</p>\n<p>46.&nbsp;Врач-фтизиатр участковый</p>\n<p>47.&nbsp;Врач-хирург</p>\n<p>48.&nbsp;Врач-эпизоотолог</p>\n<p>49.&nbsp;Газосварщик</p>\n<p>50.&nbsp;Гидромелиоратор</p>\n<p>51.&nbsp;Главный инженер проекта</p>\n<p>52.&nbsp;Главный металлург</p>\n<p>53.&nbsp;Директор по экономике</p>\n<p>54.&nbsp;Зоотехник</p>\n<p>55.&nbsp;Инженер по автоматизированным системам управления производством</p>\n<p>56.&nbsp;Инженер по внедрению новой техники и технологии</p>\n<p>57.&nbsp;Инженер по горным работам</p>\n<p>58.&nbsp;Инженер по добыче нефти и газа</p>\n<p>59.&nbsp;Инженер по защите информации</p>\n<p>60.&nbsp;Инженер по качеству</p>\n<p>61.&nbsp;Инженер по контрольно-измерительным приборам и автоматике</p>\n<p>62.&nbsp;Инженер по метрологии</p>\n<p>63.&nbsp;Инженер по надзору за строительством</p>\n<p>64.&nbsp;Инженер по наладке и испытаниям</p>\n<p>65.&nbsp;Инженер по организации управления производством</p>\n<p>66.&nbsp;Инженер по подготовке производства</p>\n<p>67.&nbsp;Инженер по сварке</p>\n<p>68.&nbsp;Инженер-конструктор</p>\n<p>69.&nbsp;Инженер-проектировщик</p>\n<p>70.&nbsp;Инженер-технолог</p>\n<p>71.&nbsp;Инженер-электрик</p>\n<p>72.&nbsp;Корреспондент</p>\n<p>73.&nbsp;Мастер буровой</p>\n<p>74.&nbsp;Математик</p>\n<p>75.&nbsp;Медицинская сестра</p>\n<p>76.&nbsp;Медицинская сестра процедурной</p>\n<p>77.&nbsp;Медицинская сестра участковая</p>\n<p>78.&nbsp;Медицинский лабораторный техник</p>\n<p>79.&nbsp;Механизатор</p>\n<p>80.&nbsp;Механик по буровым, горным работам</p>\n<p>81.&nbsp;Монтажник по монтажу стальных и железобетонных конструкций</p>\n<p>82.&nbsp;Монтажник радиоэлектронной аппаратуры и приборов</p>\n<p>83.&nbsp;Монтажник санитарно-технических систем и оборудования</p>\n<p>84.&nbsp;Монтажник систем вентиляции, кондиционирования воздуха, пневмотранспорта и аспирации</p>\n<p>85.&nbsp;Монтажник технологических трубопроводов</p>\n<p>86.&nbsp;Монтажник технологического оборудования и связанных с ним конструкций</p>\n<p>87.&nbsp;Наладчик станков и манипуляторов с программным управлением</p>\n<p>88.&nbsp;Операционная медицинская сестра</p>\n<p>89.&nbsp;Провизор</p>\n<p>90.&nbsp;Редактор</p>\n<p>91.&nbsp;Рентгенолаборант</p>\n<p>92.&nbsp;Рыбовод</p>\n<p>93.&nbsp;Сборщик изделий электронной техники</p>\n<p>94.&nbsp;Сборщик корпусов металлических судов</p>\n<p>95.&nbsp;Сварщик арматурных сеток и каркасов</p>\n<p>96.&nbsp;Слесарь по ремонту сельскохохяйственного оборудования</p>\n<p>97.&nbsp;Слесарь по ремонту сельскохохяйственной техники</p>\n<p>98.&nbsp;Слесарь по ремонту сельскохохяйственных машин</p>\n<p>99.&nbsp;Слесарь по сборке металлоконструкций</p>\n<p>100.&nbsp;Слесарь строительный</p>\n<p>101.&nbsp;Слесарь-сборщик летательных аппаратов</p>\n<p>102.&nbsp;Слесарь-судоремонтник</p>\n<p>103.&nbsp;Специалист по технологии машиностроения</p>\n<p>104.&nbsp;Станочник деревообрабатывающих станков</p>\n<p>105.&nbsp;Станочник широкого профиля</p>\n<p>106.&nbsp;Судокорпусник-ремонтник</p>\n<p>107.&nbsp;Техник по бурению</p>\n<p>108.&nbsp;Техник по наладке и испытаниям</p>\n<p>109.&nbsp;Техник-картограф</p>\n<p>110.&nbsp;Техник-технолог</p>\n<p>111.&nbsp;Технолог пищевой и перерабатывающей промышленности</p>\n<p>112.&nbsp;Технолог сельскохозяйственного производства</p>\n<p>113.&nbsp;Токарь</p>\n<p>114.&nbsp;Токарь-карусельщик</p>\n<p>115.&nbsp;Токарь-расточник</p>\n<p>116.&nbsp;Тракторист-комбайнер</p>\n<p>117.&nbsp;Тракторист-машинист</p>\n<p>118.&nbsp;Тракторист-механизатор</p>\n<p>119.&nbsp;Трубопроводчик судовой</p>\n<p>120.&nbsp;Учитель</p>\n<p>121.&nbsp;Фармацевт</p>\n<p>122.&nbsp;Фельдшер</p>\n<p>123.&nbsp;Фельдшер скорой медицинской помощи</p>\n<p>124.&nbsp;Фельдшер-лаборант</p>\n<p>125.&nbsp;Фрезеровщик</p>\n<p>126.&nbsp;Шлифовщик</p>\n<p>127.&nbsp;Электрик участка</p>\n<p>128.&nbsp;Электромонтажник по кабельным сетям</p>\n<p>129.&nbsp;Электромонтажник по освещению и осветительным сетям</p>\n<p>130.&nbsp;Электромонтажник судовой</p>\n<p>131.&nbsp;Электромонтер по ремонту воздушных линий электропередачи</p>\n<p>132.&nbsp;Электромонтер по эксплуатации распределительных сетей</p>\n<p>133.&nbsp;Электросварщик на автоматических и полуавтоматических машинах</p>\n<p>134.&nbsp;Электрослесарь строительный</p>\n<p>135.&nbsp;Энтомолог</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>\n</div>', NULL, NULL, NULL, NULL, NULL, 1),
(346, 0, 1584979697, NULL, NULL, '<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;base=LAW&amp;n=347693&amp;fld=134&amp;dst=1000000001,0&amp;rnd=0.8064491487287057#09718312693694962\" target=\"_blank\">Распоряжение Правительства Российской Федерации от 16.03.2020 № 635-р.</a></h3>\n<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;ts=3202040406068925153168565&amp;cacheid=3D273C965AEE8683EE4A6EAA60465B1F&amp;mode=splus&amp;base=SPB&amp;n=223900&amp;rnd=0.8064491487287057#qv4hlyifl6\" target=\"_blank\">Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 13.03.2020 № 121 \"О мерах по противодействию распространения в <span class=\"nobr\">Санкт-Петербурге</span> новой каронавирусной инфекции(COVID-19)\"</a></h3>\n<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;ts=3202040406068925153168565&amp;cacheid=3D273C965AEE8683EE4A6EAA60465B1F&amp;mode=splus&amp;base=SPB&amp;n=223850&amp;rnd=0.8064491487287057#skqbmscur\" target=\"_blank\">Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 18.03.2020 № 127 \"О внесении изменений в Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 13.03.2020 № 121\"</a></h3>\n<p>&nbsp;</p>\n<h3><a href=\"https://www.garant.ru/products/ipo/prime/doc/73406661/\">Профилактика, диагностика и лечение новой коронавирусной инфекции</a></h3>', NULL, NULL, NULL, NULL, NULL, 1),
(347, 0, 1594395850, NULL, NULL, '<p>1) Миграционная карта &ndash; первый документ, который вы получили на границе. Обратите внимание, какая цель визита указана в вашей МК. Если указана &laquo;работа&raquo;, то вы сможете оформить документы и легально работать в России. Если указана иная цель въезда, а желание работать есть, то необходимо пересечь границу и получить новую миграционную карту.</p>\n<p>2) Следующий документ, о котором часто забывают иностранные граждане, который в обязательном порядке должен быть у каждого иностранца, прибывшего в РФ &ndash; это полис ДМС (страховка). Данный документ оформляется с 1го дня после пересечения границы (ст. 13.5 п.6, ст.13 п.10 115-ФЗ).</p>\n<p>3) Следующее, что вы должны сделать &ndash; встать на миграционный учет по месту своего фактического проживания. Для большинства иностранных граждан срок постановки на миграционный учет составляет 7 дней.</p>\n<p>4) Если вы планируете работать на территории РФ, то важно, чтобы вы получили такие документы, как патент, ИНН и СНИЛС. Без этих документов законно работать в России не получится. Обратите внимание, что на оформление патента у вас есть месяц, после получения МК.</p>\n<p>И помните, все документы необходимо делать только легально, иначе пребывание в России приведет к штрафам и дальнейшей депортации.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(348, 0, 1594395858, NULL, NULL, '<p>Каждый иностранный гражданин, имеющий патент на работу, обязан продлевать свою регистрацию после внесения оплаты за патент (115-ФЗ ст. 5 п. 5)</p>\n<p>Вы должны знать о том, что ваша регистрация всегда должна быть действующей.</p>\n<p>Если у иностранного гражданина отсутствует действующая регистрация по месту фактического пребывания, это грозит аннулированием всех имеющихся документов, штрафом, а также выдворением и постановкой запрета на въезд в Россию.</p>\n<p>&nbsp;Что поменялось:</p>\n<p>1. Продление регистрации происходит только в районном ГУВМ МВД. Ранее это можно было сделать в МФЦ.</p>\n<p>2. При продлении регистрации иностранный гражданин обязан предоставить договор и копию уведомления о принятии на работу. Ранее, при обращении в МФЦ, данные документы не запрашивали, а брали только патент и чеки.</p>\n<p>3. Оформление регистрации должно происходить заранее, так как в государственных органах огромные очереди, в которых можно ожидать до 2 недель. Ранее продление регистрации происходило в МФЦ, куда можно было записаться по телефону на определенную дату и время.</p>\n<p>&nbsp;Предоставить необходимо оригиналы с копиями следующих документов:</p>\n<p>&ndash; паспорт иностранного гражданина;</p>\n<p>&ndash; действующий миграционный учет;</p>\n<p>&ndash; патент;</p>\n<p>&ndash; чеки по патенту хорошего качества;</p>\n<p>&ndash; трудовой договор и уведомление;</p>\n<p>&ndash; паспорт принимающей стороны;</p>\n<p>&ndash; документы, на основании которых жилое помещение принадлежит собственнику;</p>\n<p>&ndash; ходатайство на имя начальника ГУВМ МВД района вашего проживания, заполненное собственником;</p>\n<p>&ndash; заполненные бланки уведомления о прибытии иностранного гражданина.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(349, 0, 1594395868, NULL, NULL, '<p>МОСКВА, 7 мар &mdash; РИА Новости. Президент России Владимир Путин поручил организовать работу по реформированию миграционных режимов и института гражданства. Соответствующий документ опубликован на сайте Кремля.</p>\n<p>За основу планируется взять предложения, подготовленные рабочей группой по реализации Концепции государственной миграционной политики на 2019-2025 годы.</p>\n<p>Рабочая группа по совершенствованию миграционного законодательства предложила:</p>\n<p>&mdash; разрешить получение российского паспорта тем, кто знает русский язык и не был замечен в социальных конфликтах;</p>\n<p>&mdash; сделать вид на жительство основным миграционным статусом, а разрешение на временное проживание как отдельный статус &mdash; исключить;</p>\n<p>&mdash; ввести общий, упрощенный и особый виды порядка приема в гражданство России. Принимать решение о выдаче паспорта в особом порядке будет только президент;</p>\n<p>&mdash; ввести режим &ldquo;контролируемого пребывания&rdquo; для нежелательных мигрантов, а также &ldquo;соглашение о лояльности&rdquo; с перечнем условий;</p>\n<p>&mdash; упростить получение гражданства людям из культурно близких стран;</p>\n<p>&mdash; расширить использование биометрических данных для идентификации личности;</p>\n<p>&mdash; скорректировать механизм, по которому будет вводиться запрет на въезд в Россию при возникновении эпидемий в других странах;</p>\n<p>&mdash; ввести для физлиц регистрацию в специальной системе, которая позволит привлекать иностранцев на работу нянями, сиделками, ремонтниками.</p>\n<p>Доклад об итогах работы представят главе государства до 1 марта 2021 года.</p>\n<p>Ответственным Путин назначил руководителя администрации Кремля Антона Вайно.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(350, 0, 1594395886, NULL, NULL, '<p>Соотечественников за незначительные нарушения миграционных правил выдворяют, не дожидаясь решений кассационной инстанции. По мнению правозащитников, людей таким образом заведомо лишают возможности оспорить приговоры о депортации из России. При этом суды по всем категориям приезжих по-прежнему проходят поточным методом, то есть практически без рассмотрения конкретных обстоятельств.</p>\n<p>Недавно созданные отдельные кассационные суды &ndash; последняя возможность для соотечественников обжаловать решение о депортации, которое чаще всего назначается за мелкие правонарушения, и остаться в России.</p>\n<p>Однако возникла проблема: переселенцев выдворяют из страны еще до того, как кассационная инстанция рассмотрит дело. Обращение в кассационный суд не останавливает исполнения приговора о депортации после вступления его в силу. И поэтому даже в случае отмены такого наказания высланным вернуться обратно затруднительно.</p>\n<p>Есть еще и вариант через суд добиться отсрочки исполнительного производства, но для этого нужно успеть подать ходатайство и надеяться, что судебные приставы не будут спешить. Однако, по словам правозащитников, де-факто приставов не останавливают &laquo;ни определение суда об освобождении из центров временного содержания иностранных граждан, ни подача заявления в суд о приостановке решения о депортации&raquo;. При этом людей продолжают приговаривать к высылке за незначительные административные правонарушения, совершенные дважды, даже если это переход дороги на красный свет.</p>\n<p>При этом депортация автоматически влечет за собой запрет на въезд на территорию РФ. Под вопросом будет и процедура реабилитации в том случае, если кассация удовлетворяет жалобу о незаконности депортации.</p>\n<p>Особенно остро эта проблема стоит для граждан Украины, которых на родине часто после их возвращения арестовывают.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(351, 0, 1594395896, NULL, NULL, '<p>МОСКВА, 11 марта. /ТАСС/. Совет Федерации на пленарном заседании в среду одобрил закон о признании граждан Белоруссии и Украины, свободно владеющих русским языком, его носителями без прохождения собеседования.</p>\n<p>В настоящее время иностранные граждане и лица без гражданства могут быть признаны носителями русского языка по результатам собеседования, проведенного профильной комиссией. Такой статус дает возможность его обладателю получить российское гражданство в упрощенном порядке.</p>\n<p>Закон устанавливает, что украинцы и белорусы, свободно владеющие русским языком, при подаче лично заявления о признании их носителями русского языка и при наличии оснований могут быть признаны комиссией носителями русского языка без прохождения собеседования. Под основаниями подразумевается то, что сами заявители или их прямые родственники постоянно проживают или ранее постоянно проживали на территории РФ либо на территории, относившейся к Российской империи или СССР, в пределах государственной границы РФ.</p>\n<p>В закон о гражданстве РФ вносится уточнение, согласно которому требования к необходимому уровню знаний, критерии оценки и порядок разработки и использования материалов для собеседования определяются МВД России по согласованию с Минпросвещения &nbsp;РФ. Как отметил первый зампред комитета СФ по конституционному законодательству и госстроительству Владимир Полетаев, министерства должны будут подготовить соответствующие подзаконные акты.</p>\n<p>Закон в случае его подписания президентом вступит в силу через 90 дней после официального опубликования.</p>\n<p>Источник: ТАСС</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(352, 0, 1594395907, NULL, NULL, '<p>МОСКВА, 12 марта. /ТАСС/. Прокуратура Москвы впервые в прошлом году применила возможность выявления нелегальных мигрантов по цифровым следам. Об этом в интервью ТАСС рассказал прокурор столицы Денис Попов.</p>\n<p>&laquo;Как известно, для трудовых мигрантов важнейшим фактором является возможность перевода заработанных средств на родину. Поэтому совместно с Росфинмониторингом мы разработали и применили на практике новую методику выявления мест концентрации иностранных граждан&raquo;, &mdash; пояснил прокурор.</p>\n<p>Росфинмониторинг предоставил данные о системе быстрых денежных переводов за рубеж.</p>\n<p>&laquo;Сведения о работающих офисах переводов денежных средств мы наложили на карту города и определили места их концентрации, которые во многом совпали с расположенными рядом строительными и промышленными объектами&raquo;, &mdash; сообщил Попов. Так, самые частые переводы денежных средств в Таджикистан, Узбекистан и Киргизию оказались на юго-востоке столицы в Печатниках и Люблино.</p>\n<p>Проверка в Печатниках выявила мини-городок мигрантов на территории одной из заброшенных промышленных баз, где был налажен бизнес. На якобы заброшенной территории работало кафе и продуктовый магазин, мигранты проживали там с семьями, а заработок давал бизнес по ремонту автомобилей и сортировке отходов.</p>\n<p>&laquo;По результатам проверки более 40 иностранцев задержаны и доставлены в территориальный отдел полиции. 17 из них были привлечены к административной ответственности, а в отношении организации-работодателя прокуратурой инициировано административное производство&raquo;, &mdash; сообщил прокурор Москвы.</p>\n<p>По его словам, &laquo;в перспективе такое взаимодействие с Росфинмониторингом позволит устанавливать недобросовестных предпринимателей, использующих труд нелегальных мигрантов, сокращать серый сектор и выводить деньги из теневого оборота&raquo;.</p>\n<p>Прокурор в интервью также отметил, что работающие в Москве иностранцы только в минувшем году перевели семьям на родину 25 млрд рублей.</p>\n<p>&laquo;Не секрет, что крупным каналом перемещения капиталов является система быстрых денежных переводов. В прошедшем году с территории города иностранцами таким способом переведено порядка 25 млрд рублей&raquo;, &mdash; сообщил он.</p>\n<p>Легальную трудовую деятельность по патентам в Москве в 2019 году осуществляли 644 тыс. иностранцев - на 5% больше, чем в 2018 году.</p>\n<p>Источник: ТАСС</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(353, 0, 1594395921, NULL, NULL, '<p>&laquo;В целях обеспечения реализации прав и свобод граждан, а также учитывая ситуацию, связанную с распространением новой коронавирусной инфекции, c 19 марта 2020 года иностранные граждане могут продлить срок временного пребывания на территории Российской Федерации.</p>\n<p>Для этого им необходимо обратиться в ближайший к месту своего фактического нахождения территориальный орган МВД России с заявлением, составленным в произвольной форме.</p>\n<p>Гражданам, прибывшим в Россию на основании визы, будет продлен срок ее действия, независимо от цели въезда в Россию. Если срок действия имеющейся визы уже истек, срок временного пребывания также будет продлен.</p>\n<p>Гражданам, прибывшим в Россию в порядке, не требующем получения визы, продлевается срок пребывания на территории Российской Федерации, в том числе в случае, если он уже истек.</p>\n<p>Возобновляется прием заявлений о выдаче разрешений на привлечение и использование иностранных работников и разрешений на работу иностранным гражданам, находящимся на территории Российской Федерации.</p>\n<p>Кроме того, трудовые мигранты могут обратиться за получением новых патентов без необходимости выезда за пределы Российской Федерации и без привлечения их к административной ответственности за нарушение срока подачи соответствующих заявлений.</p>\n<p>Иностранным гражданам, проживающим в России на основании разрешения на временное проживание или вида на жительство, будет продлен срок пребывания в случае истечения срока действия выданного им документа&raquo;, &ndash; сообщила официальный представитель МВД России Ирина Волк.</p>\n<p>Источник: МВД РФ</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(354, 0, 1594395930, NULL, NULL, '<p>В связи с распространением коронавируса МИД РФ приостанавливает выдачу иностранным гражданам виз для въезда на территорию Российской федерации.</p>\n<p>Диппредставительства и консульства России больше не принимают документы, а также не оформляют и не выдают визы иностранным гражданам.</p>\n<p>В качестве исключения пока что визы будут выдаваться только дипломатам и членам их семей, а также членам официальных делегаций и лицам, которые постоянно проживают в России.</p>\n<p>На сегодняшний день въезд на территорию РФ ограничен для всех иностранных граждан и лиц без гражданства. Данное ограничение будет действовать до 1 мая 2020 года.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>\n<p>&nbsp;</p>', NULL, NULL, NULL, NULL, NULL, 1),
(355, 0, 1594395942, NULL, NULL, '<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;base=LAW&amp;n=347693&amp;fld=134&amp;dst=1000000001,0&amp;rnd=0.8064491487287057#09718312693694962\" target=\"_blank\">Распоряжение Правительства Российской Федерации от 16.03.2020 № 635-р.</a></h3>\n<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;ts=3202040406068925153168565&amp;cacheid=3D273C965AEE8683EE4A6EAA60465B1F&amp;mode=splus&amp;base=SPB&amp;n=223900&amp;rnd=0.8064491487287057#qv4hlyifl6\" target=\"_blank\">Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 13.03.2020 № 121 \"О мерах по противодействию распространения в <span class=\"nobr\">Санкт-Петербурге</span> новой каронавирусной инфекции(COVID-19)\"</a></h3>\n<h3><a href=\"http://www.consultant.ru/cons/cgi/online.cgi?req=doc&amp;ts=3202040406068925153168565&amp;cacheid=3D273C965AEE8683EE4A6EAA60465B1F&amp;mode=splus&amp;base=SPB&amp;n=223850&amp;rnd=0.8064491487287057#skqbmscur\" target=\"_blank\">Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 18.03.2020 № 127 \"О внесении изменений в Постановление Правительства <span class=\"nobr\">Санкт-Петербурга</span> от 13.03.2020 № 121\"</a></h3>\n<p>&nbsp;</p>\n<h3><a href=\"https://www.garant.ru/products/ipo/prime/doc/73406661/\">Профилактика, диагностика и лечение новой коронавирусной инфекции</a></h3>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(356, 0, 1594395959, NULL, NULL, '<p>Карантин в стране означает и ужесточение надзора за здоровьем мигрантов. По словам правозащитников, закрытие границ точно приведет к тому, что многие иностранцы &ndash; в том числе и российские соотечественники &ndash; будут вынуждены нарушать допустимые сроки пребывания. Потому что если они решат соблюдать миграционные правила и выехать на время за рубеж, то назад вернуться не смогут.</p>\n<p>Об ужесточении контроля за мигрантами говорится, например, в поручениях президента Владимира Путина, в этом документе подчеркивается необходимость &laquo;совершенствования механизмов контроля и обеспечения санитарно-эпидемиологической безопасности в миграционной сфере&raquo;.</p>\n<p>Ведомства уже готовят свои предложения. МВД, к примеру, выступило с инициативой депортировать мигрантов с вирусными инфекциями, в перечне указаны 16 опасных заболеваний &ndash; в том числе коронавирус, туберкулез, чума, холера и т.п. Роспотребнадзор предлагает разработать некую информационную систему надзора за приезжими, в которую можно будет вносить сведения эпидемиологического характера &ndash; о медосмотрах, выданных медицинских книжках, прививках, отсутствии наркотической зависимости и инфекционных заболеваний.</p>\n<p>Сложности возникли даже у участников госпрограммы по переселению соотечественников. Эти люди уже выписались на постоянное место жительство в РФ, продали прежнее жилье, уволились с работы и теперь в короткий период должны покинуть страну исхода. Но в Россию их перестают пускать &ndash; причем не только тех, у кого есть РВП, но и таких, кто уже получил российское гражданство, а теперь должен был приехать для принесения присяги и получения паспорта.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(357, 0, 1594396006, NULL, NULL, '<p>Премьер-министр Михаил Мишустин на совещании с вице-премьерами объявил о новых мерах в рамках борьбы с коронавирусом. Общее количество заболевших за сутки выросло на 71, общее количество заболевших достигло 438. По словам премьер-министра, в нашей стране &laquo;ситуация развивается не так остро, как в других, особенно европейских, странах&raquo;.</p>\n<p>Объявлено о следующих мерах:</p>\n<ul>\n<li>Росздравнадзор с 24 марта будет ежедневно предоставлять актуальную информацию о готовности больниц к эпидемии коронавируса;</li>\n<li>правительство выделит из своего резервного фонда 10 млрд руб. на оборудование для диагностики и лечения эпидемических заболеваний и на средства защиты;</li>\n<li>правительство будет стимулировать российских производителей средств для борьбы с коронавирусом и его профилактики. За последние три дня правительство направило на поддержку фармацевтической промышленности более 23 млрд руб.;</li>\n<li>регионам до 25 марта будет предоставлена отсрочка по выплате или реструктуризации кредитов;</li>\n<li>граждане должны получить возможность получать социальные услуги дистанционно, это касается выплаты пособий;</li>\n<li>Минтруду и Минюсту поручено представить предложения о том, как не допустить необоснованных увольнений на фоне коронавируса;</li>\n<li>будет создана система отслеживания граждан, контактировавших с больными коронавирусом. Она будет работать на основе данных сотовых операторов о геолокации мобильного телефона конкретного человека;</li>\n<li>будет обеспечен бесплатный доступ к &laquo;полезным цифровым сервисам&raquo;. Информация о них будет собрана на едином интернет-ресурсе &laquo;Все.онлайн&raquo;.</li>\n</ul>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(358, 0, 1594396018, NULL, NULL, '<p>На время эпидемии коронавируса в России можно будет переоформить патент без выезда из страны. Соответствующая мера введена на фоне закрытия границ, как Российской Федерации, так и ряда других стран, в том числе Узбекистана, Кыргызстана и Таджикистана.</p>\n<p>Совсем скоро эта временная мера может стать нормой и для переоформления патента не нужно будет выезжать за пределы России. Соответствующий закон на днях был внесен в Государственную Думу. Сейчас патент даётся на максимальный срок до 12 месяцев и его можно один раз переоформить.</p>\n<p>Теперь же, в случае принятия закона, обращения для переоформления патента можно будет осуществлять многократно.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(359, 0, 1594396028, NULL, NULL, '<p>Об этом он заявил в ходе своего обращения к жителям России<br /> Президент России Владимир Путин отменил рабочую неделю, выходные продлятся с 28 марта по 5 апреля включительно. <br /> - Длинные выходные предусмотрены именно для того, чтобы снизить скорость распространения болезни, &mdash; сказал президент. <br /> При этом глава государства пообещал, что зарплата за этот период сохранится.</p>\n<p>Президент страны также объявил о переносе даты голосования по изменениям в Конституцию. Новую дату определят позже.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(360, 0, 1594396043, NULL, NULL, '<p>Чем грозит фиктивная (т.е. внесенная в базу, но вы не проживаете по указанному в ней адресу)&nbsp;регистрации?&nbsp;</p>\n<p>1. Соучастие в преступлении, предусмотренном ст. 322 (с подпунктами) - до 7 лет лишения свободы и штраф до 500 тысяч рублей.</p>\n<p>2. ст. 18 АПК РФ - штраф до 7 тысяч рублей с административным выдворением до 5 лет</p>\n<p>3. ст. за нарушение карантина - до 7 лет лишения свободы и штраф до 300 000 тысяч рублей, с вынесением решения о нежелательности пребывания в РФ на срок от 10 лет до пожизненного</p>\n<p>Чем грозит фальшивая регистрация и фиктивная регистрация?&nbsp;</p>\n<p>Фальшивая регистрация ( т.е. не внесенная в базу)&nbsp; грозит вам, в соответствии с новыми поправками в УК РФ и АПК РФ следующими проблемами:&nbsp;</p>\n<p>1. ст. 18 АПК РФ - штраф до 7 тысяч рублей с административным выдворением до 5 лет</p>\n<p>2. ст. за нарушение карантина - до 7 лет лишения свободы и штраф до 300 000 тысяч рублей, с вынесением решения о нежелательности пребывания в РФ на срок от 10 лет до пожизненного</p>\n<p>3. Использование заведомо подложных документов ст.327 УК РФ - штраф до 80 тыс. рублей и лишение свободы на срок до лет.</p>\n<p><strong>Неважно есть ли в базе купленная вами регистрация или вы не проживаете по указанному адресу - до 7 лет колонии, штраф до 500 000 рублей и пожизненный запрет на въезд вам гарантирован.</strong></p>\n<p><strong>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</strong></p>', NULL, NULL, NULL, NULL, NULL, 1),
(361, 0, 1594396052, NULL, NULL, '<p>Практика показывает, что многие руководители и собственники бизнеса приняли указ президента, как рекомендацию. Больше всего в Сети жалуются сотрудники частных организаций и фирм. Некоторые россияне отмечают, что начальство предложило отпустить домой, но только за свой счет. Другие и вовсе заявили, что уволят в случае неявки на рабочее место.</p>\n<p>Юристы отмечают, что такие действия незаконны. Работодатель не может уволить своего сотрудника за то, что тот отказывается работать во время недели выходных. Человек может со спокойной совестью остаться дома. Если же начальник настаивает на выходе на работу, стоит попытаться задокументировать его действия.</p>\n<p>Важно, что бы работник ничего не подписывал, в том числе добровольное соглашение о работе в период с 28 марта по 5 апреля. Если работник переживает о каких-то действиях со стороны руководства, следует побольше собрать информации о неправомерных действиях, сохранить переписку или записать разговор. Далее все эти документы нужно будет отнести в региональную трудовую инспекцию.</p>\n<p>В таком случае следует быть готовыми к тому, что работу сохранить получится, а вот хорошие отношения с руководством вряд ли. С другой стороны, если начальство грозит увольнением, вряд ли можно что-то говорить о хороших отношениях.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(362, 0, 1594396060, NULL, NULL, '<p><em>В.Путинв в новом&nbsp; телеобращении от 2 апреля продлил нерабочую неделю до 30 апреля. Регионам будет предоставлено право точечно вводить профилактические меры в зависимости от ситуации.</em></p>\n<p>\"<em>Как полагают специалисты-вирусологи, пик эпидемии в мире еще не пройден, в том числе и в нашей стране. В связи с этим мною принято решение продлить режим нерабочих дней до конца месяца, то есть по 30 апреля включительно</em>\", - заявил глава государства в четверг в обращении к гражданам страны. Он особо отметил, что нерабочие дни пройдут \"<em>с сохранением за работниками их заработной платы</em>\".</p>\n<p>Ранее указом Путина нерабочая неделя с сохранением зарплат была объявлена с 30 марта по 5 апреля.</p>\n<p>При этом президент&nbsp;не исключил сокращения срока нерабочих дней в случае изменения ситуации с коронавирусом к лучшему.</p>\n<p>По словам российского лидера, \"<em>сегодня крайне важно обеспечить взаимодействие между разными уровнями власти: федеральным, региональным, муниципальным</em>\". Путин добавил, что в связи с этим поручил своим полномочным представителям в федеральных округах плотно координировать работу регионов.</p>\n<p>\"<em>Вся информация должна поступать в координационный совет при правительстве по борьбе с коронавирусом, обрабатываться там, анализироваться и учитываться при принятии решений. И конечно, действия региональных властей в обязательном порядке должны быть согласованы с санитарно-эпидемиологическими службами</em>\", - указал президент.</p>\n<p>Принятые президентом меры носят рекомендательный характер и основываются&nbsp;на ответственности граждан. Если в Италии и Испании люди могут выходить из дома лишь на определенное время для выгула собак, посещения больницы,&nbsp;магазина или аптеки, что контролируется полицией, то в России таких жестких мер на данный момент нет.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(363, 0, 1594396075, NULL, NULL, '<p style=\"text-align: justify;\">Пандемия COVID-19 является глобальной чрезвычайной ситуацией в области общественного здравоохранения, которая требует скоординированных и широкомасштабных ответных мер со стороны правительств по всему миру. Однако усилия государств по сдерживанию распространения вируса не должны использоваться в качестве прикрытия для того, чтобы открыть новую эру значительного расширения использования систем инвазивного электронного слежения.</p>\n<p style=\"text-align: justify;\">Мы, нижеподписавшиеся организации, призываем правительства проявить лидерство в борьбе с пандемией таким образом, чтобы были соблюдены гарантии того, что использование цифровых технологий для отслеживания и мониторинга отдельных лиц и групп населения осуществляется строго в соответствии с обязательствами в области прав человека.</p>\n<p style=\"text-align: justify;\">Технологии могут и должны сыграть важную роль в усилиях по спасению жизней, в том, что касается распространения информации об общественном здравоохранении и расширения доступа к медицинской помощи. Тем не менее увеличение государственных полномочий в области электронного слежения, в том числе получение доступа к данным о местонахождении средств мобильной связи, угрожает праву на частную жизнь, свободе выражения мнений и свободе объединений в такой степени, что это может нарушить права челвека и подорвать доверие к органам государственной власти, соответственно подрывая и эффективность любых мер в области общественного здравоохранения. Такие меры также создают риск дискриминации и могут нанести непропорциональный ущерб уже маргинализированным сообществам.</p>\n<p style=\"text-align: justify;\">Мы переживаем чрезвычайные времена, но международное право в области прав человека по-прежнему действует. Действительно, общие механизмы защиты прав человека предназначены для обеспечения тщательной сбалансированности различных прав таким образом, чтобы были защищены как права отдельных лиц, так и интересы общества в целом. Государства не могут просто так пренебречь такими правами, как неприкосновенность частной жизни и свобода выражения мнений, даже во имя преодоления кризиса в области общественного здравоохранения. Напротив, защита прав человека только способствует укреплению общественного здоровья. Теперь, как никогда раньше, правительства должны строго следить за тем, чтобы любые ограничения прав человека соответствовали давно установленным правозащитным гарантиям.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3><strong>Государства не могут просто так пренебречь такими правами, как неприкосновенность частной жизни и свобода выражения мнений, даже во имя преодоления кризиса в области общественного здравоохранения.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">Этот кризис даёт возможность продемонстрировать нашу общую человечность. Мы можем приложить огромные усилия для борьбы с этой пандемией таким образом, чтобы это соответствовало стандартам в области прав человека и верховенства закона. Решения, которые принимают правительства сейчас для борьбы с пандемией, будут определять то, как будет выглядеть мир в будущем.</p>\n<p style=\"text-align: justify;\">Мы призываем все правительства отвечать на пандемию COVID-19 усилением мер электронного слежения за населением только при соблюдении следующих условий:</p>\n<p style=\"text-align: justify;\">1. Меры по надзору, принятые для борьбы с пандемией, должны быть законными, необходимыми и соразмерными. Они должны предусматриваться законом, преследовать законную цель общественного здравоохранения, определённую соответствующими органами общественного здравоохранения, и должны быть соразмерны этим потребностям. Правительства должны следовать принципам прозрачности в отношении мер, которые они принимают, с тем, чтобы последние можно было тщательно изучить и, при необходимости, позднее изменить, отозвать или отменить. Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для неизбирательного массового надзора.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для неизбирательного массового надзора.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">2. Если правительства расширяют полномочия по мониторингу и наблюдению за гражданами, то тогда такие полномочия должны быть ограничены во времени и быть задействованы на протяжении только того времени, что необходимо для устранения текущей пандемии. Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для введения бессрочного наблюдения за гражданами.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для введения бессрочного наблюдения за гражданами.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">3. Государства должны гарантировать, что сбор, хранение и сведение воедино увеличенного объёма персональных данных, в том числе данных о состоянии здоровья, осуществляются только в целях реагирования на пандемию COVID-19. Собранные, сохранённые и агрегированные для подготовки ответа на пандемию данные должны быть ограничены по объёму, работа с ними должна быть ограничена во времени относящимся к пандемии периодом, и они не должны использоваться в коммерческих или любых других целях. Мы не можем допустить, чтобы пандемия COVID-19 послужила оправданием для нарушения права человека на неприкосновенность частной жизни.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не</strong> можем допустить, чтобы пандемия COVID-19 послужила оправданием для нарушения права человека на неприкосновенность частной жизни.</h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">4. Правительства должны приложить все усилия для защиты персональных данных людей, в том числе для обеспечения достаточной безопасности для любого набора личных данных, собранных и полученных с любых устройств, приложений, при помощи любых сетей или служб, участвующих в сборе, передаче, обработке и хранении информации. Любые утверждения, что данные являются анонимизированными, должны основываться на доказательствах и подтверждаться достаточной информацией о том, как эта анонимность была достигнута. Мы не можем допустить, чтобы попытки ответить на эту пандемию использовались в качестве оправдания компрометации цифровой безопасности граждан.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не можем допустить, чтобы попытки ответить на эту пандемию использовались в качестве оправдания компрометации цифровой безопасности граждан.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">5. Любое использование технологий электронного слежения в ответ на COVID-19, включая big data и задействование искусственных интеллектуальных систем, должно учитывать риск того, что эти инструменты будут способствовать дискриминации и другим нарушениям прав человека в отношении расовых меньшинств, людей, живущих в нищете, и других маргинализированных групп населения, чьи потребности или жизненное положение могут быть недостаточно представлены или искажены в больших наборах данных. Мы не можем допустить, чтобы пандемия COVID-19 увеличила разрыв между тем, в какой мере способны реализовать свои права человека представители различных групп общества.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не можем допустить, чтобы пандемия COVID-19 увеличила разрыв между тем, в какой мере способны реализовать свои права человека представители различных групп общества.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">6. Если правительства заключат соглашения об обмене данными с другими организациями государственного или частного сектора, это должно произойти на основании закона, факты заключения таких соглашений и информация, необходимая для оценки их воздействия на конфиденциальность и права человека, должны быть публично раскрыты &mdash; в письменной форме, с положениями об ограничении сроков соглашения, механизмах общественного надзора и других правовых гарантиях по умолчанию. Хозяйствующие субъекты, вовлечённые в усилия правительств по борьбе с COVID-19, должны проявить должную осмотрительность, гарантируя, что они уважают права человека и обеспечивают защиту любых своих действий от вмешательства со стороны других деловых и коммерческих интересов. Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для того, чтобы держать людей в неведении относительно того, какую информацию собирают и передают третьим сторонам правительства.</p>\n<blockquote class=\"blockquote is-lined\" style=\"quotes: \'&ldquo;\' \'&rdquo;\' \'&lsquo;\' \'&rsquo;\';\">\n<h3 style=\"text-align: justify;\"><strong>Мы не можем допустить, чтобы пандемия COVID-19 служила оправданием для того, чтобы держать людей в неведении относительно того, какую информацию собирают и передают третьим сторонам правительства.</strong></h3>\n<cite></cite></blockquote>\n<p style=\"text-align: justify;\">7. Любые ответные меры должны включать механизмы отчётности и защиты от злоупотреблений. Расширение механизмов слежения, связанное с COVID-19, не должно входить в сферу ответственности разведывательных служб или служб безопасности и должно подлежать эффективному контролю со стороны соответствующих независимых органов. Кроме того, граждане должны получить возможность узнать и оспорить любые связанные с COVID-19 меры по сбору, агрегированию, сохранению и использованию их данных. Лица, подвергшиеся надзору, должны иметь доступ к эффективным средствам правовой защиты.</p>\n<p style=\"text-align: justify;\">8. Ответные меры, связанные с COVID-19, которые включают меры по сбору данных, должны приниматься при свободном, активном и значимом участии соответствующих заинтересованных сторон, в частности экспертов в области общественного здравоохранения и представителей наиболее маргинализированных групп населения.</p>\n<p>Заявление подписали:</p>\n<p>7amleh &ndash; Arab Center for Social Media Advancement<br /> Access Now<br /> African Declaration on Internet Rights and Freedoms Coalition<br /> AI Now<br /> Algorithm Watch<br /> Alternatif Bilisim<br /> Amnesty International<br /> ApTI<br /> ARTICLE 19<br /> Asociaci&oacute;n para una Ciudadan&iacute;a Participativa, ACI Participa<br /> Association for Progressive Communications (APC)<br /> ASUTIC, Senegal<br /> Athan &mdash; Freedom of Expression Activist Organization<br /> Australian Privacy Foundation<br /> Barrac&oacute;n Digital<br /> Big Brother Watch<br /> Bits of Freedom<br /> Center for Advancement of Rights and Democracy (CARD)<br /> Center for Digital Democracy<br /> Center for Economic Justice<br /> Centro De Estudios Constitucionales y de Derechos Humanos de Rosario<br /> Chaos Computer Club &mdash; CCC<br /> Citizen D / Državljan D<br /> CIVICUS<br /> Civil Liberties Union for Europe<br /> C&oacute;digoSur<br /> Coding Rights<br /> Coletivo Brasil de Comunica&ccedil;&atilde;o Social<br /> Collaboration on International ICT Policy for East and Southern Africa (CIPESA)<br /> Comit&eacute; por la Libre Expresi&oacute;n (C-Libre)<br /> Committee to Protect Journalists<br /> Consumer Action<br /> Consumer Federation of America<br /> Cooperativa Tierra Com&uacute;n<br /> Creative Commons Uruguay<br /> D3 &mdash; Defesa dos Direitos Digitais<br /> Data Privacy Brasil<br /> Democratic Transition and Human Rights Support Center &laquo;DAAM&raquo;<br /> Derechos Digitales<br /> Digital Rights Lawyers Initiative (DRLI)<br /> Digital Rights Watch<br /> Digital Security Lab Ukraine<br /> Digitalcourage<br /> EPIC<br /> epicenter.works<br /> European Digital Rights &mdash; EDRi<br /> Fitug<br /> Foundation for Information Policy Research<br /> Foundation for Media Alternatives<br /> Fundaci&oacute;n Acceso (Centroam&eacute;rica)<br /> Fundaci&oacute;n Ciudadan&iacute;a y Desarrollo, Ecuador<br /> Fundaci&oacute;n Datos Protegidos<br /> Fundaci&oacute;n Internet Bolivia<br /> Fundaci&oacute;n Taig&uuml;ey, Rep&uacute;blica Dominicana<br /> Fundaci&oacute;n V&iacute;a Libre<br /> Hermes Center<br /> Hiperderecho<br /> Homo Digitalis<br /> Human Rights Watch<br /> Hungarian Civil Liberties Union<br /> ImpACT International for Human Rights Policies<br /> Index on Censorship<br /> Initiative f&uuml;r Netzfreiheit<br /> Innovation for Change &mdash; Middle East and North Africa<br /> International Commission of Jurists<br /> International Service for Human Rights (ISHR)<br /> Intervozes &mdash; Coletivo Brasil de Comunica&ccedil;&atilde;o Social<br /> Ipandetec<br /> IPPF<br /> Irish Council for Civil Liberties (ICCL)<br /> IT-Political Association of Denmark<br /> Iuridicum Remedium z.s. (IURE)<br /> Karisma<br /> La Quadrature du Net<br /> Liberia Information Technology Student Union<br /> Liberty<br /> Luchadoras<br /> Majal.org<br /> Masaar &laquo;Community for Technology and Law&raquo;<br /> Media Rights Agenda (Nigeria)<br /> MENA Rights Group<br /> Metamorphosis Foundation<br /> New America&rsquo;s Open Technology Institute<br /> Observacom<br /> Open Data Institute<br /> Open Rights Group<br /> OpenMedia<br /> OutRight Action International<br /> Pangea<br /> Panoptykon Foundation<br /> Paradigm Initiative (PIN)<br /> PEN International<br /> Privacy International<br /> Public Citizen<br /> Public Knowledge<br /> R3D: Red en Defensa de los Derechos Digitales<br /> RedesAyuda<br /> SHARE Foundation<br /> Skyline International for Human Rights<br /> Sursiendo<br /> Swedish Consumers&rsquo; Association<br /> Tahrir Institute for Middle East Policy (TIMEP)<br /> Tech Inquiry<br /> TechHerNG<br /> TEDIC<br /> The Bachchao Project<br /> Unwanted Witness, Uganda<br /> Usuarios Digitales<br /> WITNESS<br /> World Wide Web Foundation</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(364, 0, 1594396083, NULL, NULL, '<p>Согласно новому <a href=\"https://migrantmedia.ru/migracionnoe-zakonodatelstvo-rf/postanovlenie-pravitelstva-rf-ot-26.12.2019-g-n1855-r-o-pravilah-podachi-uvedomleniya-o-podtverxhdenii-prozhivaniya-v-rf/\">Постановлению правительства №1855</a> были утверждены Правила подачи иностранным гражданином или лицом без гражданства уведомления о подтверждении своего проживания в Российской Федерации в редакции от 26 декабря 2019 г.</p>\n<p>В соответствии с постановлением, уведомлять о подтверждении проживания должны все иностранные граждане с РВП и ВНЖ, достигшим 18-летнего возраста. На иностранных граждан до 18 лет уведомление должен подать один из родителей.</p>\n<p>Временно проживающие в Российской Федерации иностранные гражданине, то есть иностранцы с <a href=\"https://migrantmedia.ru/rvp-rf-instruktsiya-po-oformleniyu-razresheniya-na-vremennoe-prozhivanie-v-rossii-po-kvote-rvp-bez-kvot/\">РВП</a>, могут подать <a href=\"https://migrantmedia.ru/ezhegodnoe-uvedomlenie-o-podtverzhdenii-prozhivaniya-po-rvp/\">уведомление о проживании в России по РВП</a> лично или в электронной форме через Единый портал государственных услуг в орган МВД, в котором было получено разрешение на временное проживание.</p>\n<p>К заполненному бланку уведомления о подтверждении проживания в РФ при личной подаче необходимо приложить следующие документы:</p>\n<ul>\n<li>удостоверяющий личность документ;</li>\n<li>разрешение на временное проживание;</li>\n<li>документ, подтверждающий доход и его источник (по желанию иностранцев, вместо документа МВД может запросить копию налоговой декларации в налоговом органе).</li>\n</ul>\n<p>Если уведомление подается в электронном виде, необходимо приложить копии этих документов, подписанные электронной подписью иностранного гражданина.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(365, 0, 1594396093, NULL, NULL, '<p>В МВД РФ сообщили, что с 1 марта по 8 апреля Россию покинуло порядка 1,2 млн иностранных граждан, которые выехали за пределы РФ на фоне распространения <a href=\"https://rg.ru/sujet/covid-19/\">коронавируса</a>, сообщает <a href=\"https://tass.ru\" target=\"_blank\">ТАСС</a> со ссылкой на пресс-центр правоохранительного ведомства.</p>\n<p>В МВД также отметили, что более&nbsp;97,7 тысяч иностранцев с 1 марта по 8 апреля обратились за продлением срока временного пребывания в России. Об этом сообщили в пресс-центре МВД РФ.</p>\n<p>\"С 1 марта по 8 апреля 97,7 тыс. обратились за продлением срока временного пребывания в России\", - сказал собеседник агентства.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(366, 0, 1594396103, NULL, NULL, '<p>Иностранные мигранты, приезжающие в город на заработки, будут востребованы и после окончания пандемии коронавирусной инфекции. Негативных изменений на рынке труда в этом плане не прогнозируется.</p>\n<p>Коронавирус не сможет повлиять на привлечение в Петербург иностранных рабочих. Если же отток мигрантов и будет, цифры не будут значительными. Об этом сообщает ТАСС с ссылкой на директора Центра трудовых ресурсов комитета по труду и занятости населения Санкт-Петербурга Алексей Чистяков.</p>\n<p>Всего в городе легально работают 223 тысячи иностранцев. Их помощь городу весьма серьезна, они задействованы в различных сферах и отраслях. Мигранты составляют 15% от рабочей силы Северной столицы. Большая часть мигрантов приезжает из Узбекистана, Белоруссии и Украины.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(367, 0, 1594396112, NULL, NULL, '<p class=\"article-renderblock\">Работодатели, планирующие привлечение иностранной рабочей силы, не смогут подать заявки с 20 апреля по 24 апреля 2020 года &mdash; такое решение принято в соответствии с указом президента РФ от 25 марта 2020 года № 206 &laquo;Об объявлении в Российской Федерации нерабочих дней&raquo; с 30 марта по 30 апреля 2020 года.</p>\n<p>Напомним, что для привлечения на работу иностранных граждан из стран, с которыми к России установлен визовый режим, работодателю необходимо сначала получить квоту. Данный процесс занимает несколько месяцев, а потому &mdash; подавать на квоту необходимо заблаговременно.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(368, 0, 1594396124, NULL, NULL, '<p>Возврат оплаты НДФЛ за патент , мигрант может осуществить подав заявление в управление ИФНС по району его регистрации.</p>\n<p>Возврат НДФЛ за оплату патента может осуществляться в следующих случаях:&nbsp;</p>\n<ul>\n<li>Ошибка в реквизитах.</li>\n<li>Отказ от оказания государственной услуги</li>\n<li>Перечисление лишней суммы или платежа</li>\n</ul>\n<ul>\n<li>Для возврата платежа НДФД за патент иностранный гражданин должен заполнить указанное ниже заявление , с указанием причины ( Лишний платёж).</li>\n<li><a href=\"https://zagranfast.ru/ssl/u/b6/5a8a58819411eaa660d6aca382d3b4/-/%D0%B7%D0%B0%D1%8F%D0%B2%D0%BB%D0%B5%D0%BD%D0%B8%D0%B5%20%D0%BD%D0%B0%20%D0%B2%D0%BE%D0%B7%D0%B2%D1%80%D0%B0%D1%82%20%D0%B3%D0%BE%D1%81%D0%BF%D0%BE%D1%88%D0%BB%D0%B8%D0%BD%D1%8B.pdf\"><strong>заявление на возврат госпошлины.pdf</strong></a></li>\n<li>Указать в заявлении все реквизиты получателя платежа которые указаны в чеке оплаты патента.</li>\n<li>Пример заявления на возврат платежа НДФЛ за патент на работу для иностранных граждан модно скачать по ссылке.</li>\n<li>Возврат&nbsp; лишнего платёжа НДФЛ за патент производиться в соответствии со пунктом 3 статьи 333.40 НК России.</li>\n</ul>\n<p>В заявлении на возврат оплаты за патент также указывается:.</p>\n<ul>\n<li>Фамилия имя и отчество плательщика.</li>\n<li>Дата оплаты и номер чека об оплате НДФЛ за патент.</li>\n<li>Основание для возврата денег за патент.</li>\n<li>Реквизиты счета для возврата денежных средств</li>\n</ul>\n<p>Важно. К заявлению обязательно приложить копию чеков об оплате.Подписывать заявление имеет право только плательщик, указанный в чеке на оплату патента.</p>\n<p>Возврат денег производиться в течении 14 дней на реквизиты указанные в заявлении.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(369, 0, 1588174756, NULL, NULL, '<p>18 апреля президент подписал <a href=\"https://migrantmedia.ru/migracionnoe-zakonodatelstvo-rf/ukaz-prezidenta-rossii-ot-18-04-2020-n-274-o-merakh-po-uregulirovaniyu-pravovogo-polozheniya-migrantov-v-koronavirus/\">Указ N274 от 18.04.2020</a> о временных мерах по урегулированию правового положения иностранных граждан в России в связи с угрозой распространения коронавирусной инфекции (COVID-19). <br /> <br /> Согласно указу президента, с 15 марта по 15 июня <strong>будет приостановлено течение сроков действия документов</strong>, истекающих в обозначенный период. К данным документам относятся:</p>\n<ul>\n<li>визы;</li>\n<li><a href=\"https://migrantmedia.ru/rvp-rf-instruktsiya-po-oformleniyu-razresheniya-na-vremennoe-prozhivanie-v-rossii-po-kvote-rvp-bez-kvot/\">разрешения на временное проживание</a>;</li>\n<li><a href=\"https://migrantmedia.ru/vnzh-rf-kak-poluchit-vid-na-zhitelstvo-v-rossii-inostrannomu-grazhdaninu-otkaz-prodlenie-podtvergdenie-vng-rf/\">виды на жительство в РФ (ВНЖ)</a>;</li>\n<li><a href=\"https://migrantmedia.ru/migratsionnaya-karta-rossii-2016-pravila-i-obrazets-zapolneniya-blanka-migracionnoy-karty-poluchenie-dublikata/\">миграционные карты</a> с отметками о сроках действия;</li>\n<li>удостоверения беженца;</li>\n<li>свидетельства о рассмотрении ходатайства о признании беженцем в РФ;</li>\n<li>свидетельства о предоставлении временного убежища в РФ;</li>\n<li>свидетельства участника Государственной программы;</li>\n<li><a href=\"https://migrantmedia.ru/privlechenie-i-ispolzovanie-inostrannykh-rabotnikov-poshagovaya-instruktsiya-po-privlecheniyu-migrantov-na-rabotu/\">разрешения на работу</a>;</li>\n<li><a href=\"https://migrantmedia.ru/trudovoy-patent-na-rabotu-dlya-inostrannykh-grazhdan-poshagovaya-instruktsiya-kak-poluchit-rabochiy-patent-migranta/\">патенты на работу</a>;</li>\n<li><a href=\"https://migrantmedia.ru/privlechenie-i-ispolzovanie-inostrannykh-rabotnikov-poshagovaya-instruktsiya-po-privlecheniyu-migrantov-na-rabotu/\">разрешения на привлечение и использование иностранных работников</a>.</li>\n</ul>\n<p>Это означает, что если срок действия любого из этих документов истекает в период с 15 марта по 15 июня, то срок документ автоматически будет действовать до 15 июня. <br /> <br /> Другими словами, если иностранный гражданин законно находится в России 15 марта, то для него следующий день &ndash; это 16 июня. Три месяца между этими датами будет действовать приостановка сроков и они не будут учитываться в дальнейшем.<br /> <br /> <strong>Важно!</strong> с 15 марта по 15 июня иностранным гражданам не нужно <a href=\"https://migrantmedia.ru/v-2020-godu-summa-avansovykh-platezhey-ndfl-po-patentu-na-rabotu-izmenitsya-skolko-platit-za-patent-2020/\">оплачивать патент на работу</a>, продлевать <a href=\"https://migrantmedia.ru/postanovka-na-migratsionnyy-uchet-inostrannyh-grazhdan-v-rf-vremennaya-registraciya-inostrancev-v-rossii/\">регистрацию</a> и другие документы. <br /> <br /> То есть иностранным гражданам не нужно никуда ходить и заниматься документами, он смогут просто жить и спокойно работать.<br /> <br /> Помимо сроков действия документов с 15 марта по 15 июня для иностранных граждан <strong>будет приостановлено течение сроков</strong>:</p>\n<ul>\n<li>временного пребывания в РФ;</li>\n<li>временного или постоянного проживания в РФ;</li>\n<li>поставки на миграционный учет или регистрации;</li>\n<li>сроков нахождения за пределами РФ участников программы переселения соотечественников и членов их семей, лиц, получивших РВП или ВНЖ, в случае если они не могут въехать на территорию РФ до истечения 6-месячного срока;</li>\n<li>сроков добровольного выезда из России иностранцев, в отношении которых было принято решение об административном выдворении за пределы РФ, о депортации или реадмиссии.</li>\n</ul>\n<p>Это означает, что если эти сроки истекают в указанный период с 15 марта по 15 июня, то они автоматически будет приостановлены до 15 июня. <br /> <br /> Кроме того, с 15 марта по 15 июня <strong>в отношении иностранных граждан не будут приниматься решения</strong>:</p>\n<ul>\n<li>о нежелательности их пребывания или проживания в стране,</li>\n<li>об <a href=\"https://migrantmedia.ru/administrativnoe-vydvorenie-inostrannykh-grazhdan-za-predely-rf-osnovaniya-sroki-otmena-obzhalovanie-vydvoreniya/\">административном выдворении</a> за пределы России</li>\n<li>о депортации или реадмиссии,</li>\n<li>о лишении статуса беженца или временного убежища,</li>\n<li>об аннулировании ранее выданных виз, разрешений на работу, патентов, РВП, ВНЖ, свидетельств участника Государственной программы.</li>\n</ul>\n<p>Что касается <strong>работодателей мигрантов</strong>, то с 15 марта по 15 июня работодатели, выполняющие установленные ограничения и санитарно-эпидемиологические меры вправе привлекать к работе:</p>\n<ul>\n<li>визовых иностранцев без разрешения на работу при наличии разрешения на привлечение и использование иностранных работников;</li>\n<li>безвизовых иностранных граждан без патента на работу.</li>\n</ul>\n<p><br /> Данный указ вступил в силу с момента подписания, то есть с 18 апреля.<br /> <br /> С полным текстом указа президента N274 от 18.04.2020 вы можете ознакомиться по <a href=\"https://migrantmedia.ru/migracionnoe-zakonodatelstvo-rf/ukaz-prezidenta-rossii-ot-18-04-2020-n-274-o-merakh-po-uregulirovaniyu-pravovogo-polozheniya-migrantov-v-koronavirus/\">этой ссылке</a>.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(370, 0, 1594396133, NULL, NULL, '<p>18 апреля президент подписал Указ N274 от 18.04.2020 о временных мерах по урегулированию правового положения иностранных граждан в России в связи с угрозой распространения коронавирусной инфекции (COVID-19). <br /> <br /> Согласно указу президента, с 15 марта по 15 июня <strong>будет приостановлено течение сроков действия документов</strong>, истекающих в обозначенный период. К данным документам относятся:</p>\n<ul>\n<li>визы;</li>\n<li>разрешения на временное проживание;</li>\n<li>виды на жительство в РФ (ВНЖ);</li>\n<li>миграционные карты с отметками о сроках действия;</li>\n<li>удостоверения беженца;</li>\n<li>свидетельства о рассмотрении ходатайства о признании беженцем в РФ;</li>\n<li>свидетельства о предоставлении временного убежища в РФ;</li>\n<li>свидетельства участника Государственной программы;</li>\n<li>разрешения на работу;</li>\n<li>патенты на работу;</li>\n<li>разрешения на привлечение и использование иностранных работников.</li>\n</ul>\n<p>Это означает, что если срок действия любого из этих документов истекает в период с 15 марта по 15 июня, то срок документ автоматически будет действовать до 15 июня. <br /> <br /> Другими словами, если иностранный гражданин законно находится в России 15 марта, то для него следующий день &ndash; это 16 июня. Три месяца между этими датами будет действовать приостановка сроков и они не будут учитываться в дальнейшем.<br /> <br /> <strong>Важно!</strong> с 15 марта по 15 июня иностранным гражданам не нужно оплачивать патент на работу, продлевать регистрацию и другие документы. <br /> <br /> То есть иностранным гражданам не нужно никуда ходить и заниматься документами, он смогут просто жить и спокойно работать.<br /> <br /> Помимо сроков действия документов с 15 марта по 15 июня для иностранных граждан <strong>будет приостановлено течение сроков</strong>:</p>\n<ul>\n<li>временного пребывания в РФ;</li>\n<li>временного или постоянного проживания в РФ;</li>\n<li>поставки на миграционный учет или регистрации;</li>\n<li>сроков нахождения за пределами РФ участников программы переселения соотечественников и членов их семей, лиц, получивших РВП или ВНЖ, в случае если они не могут въехать на территорию РФ до истечения 6-месячного срока;</li>\n<li>сроков добровольного выезда из России иностранцев, в отношении которых было принято решение об административном выдворении за пределы РФ, о депортации или реадмиссии.</li>\n</ul>\n<p>Это означает, что если эти сроки истекают в указанный период с 15 марта по 15 июня, то они автоматически будет приостановлены до 15 июня. <br /> <br /> Кроме того, с 15 марта по 15 июня <strong>в отношении иностранных граждан не будут приниматься решения</strong>:</p>\n<ul>\n<li>о нежелательности их пребывания или проживания в стране,</li>\n<li>об административном выдворении за пределы России</li>\n<li>о депортации или реадмиссии,</li>\n<li>о лишении статуса беженца или временного убежища,</li>\n<li>об аннулировании ранее выданных виз, разрешений на работу, патентов, РВП, ВНЖ, свидетельств участника Государственной программы.</li>\n</ul>\n<p>Что касается <strong>работодателей мигрантов</strong>, то с 15 марта по 15 июня работодатели, выполняющие установленные ограничения и санитарно-эпидемиологические меры вправе привлекать к работе:</p>\n<ul>\n<li>визовых иностранцев без разрешения на работу при наличии разрешения на привлечение и использование иностранных работников;</li>\n<li>безвизовых иностранных граждан без патента на работу.</li>\n</ul>\n<p><br /> Данный указ вступил в силу с момента подписания, то есть с 18 апреля.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(371, 0, 1594396145, NULL, NULL, '<p>Иностранные граждане, которые состоят в близком родстве с родственниками в Российской Федерации, получили возможность на воссоединение семей и возможность вернуться. Об этом сообщила в прямом эфире радиостанции &laquo;Эхо Москвы&raquo; директор Департамента информации и печати, официальный представителем МИД России Мария Захарова.</p>\n<p>&laquo;Было принято дополнение об ограничении на въезд, которое регулировало как раз ситуацию с семьями, которые состоят из граждан разных государств. И была сделана корректировка, давно, в конце марта, и эти люди (или дети с иным гражданством) получили возможность на воссоединение семей или возвращение вместе с семьей&raquo;, &mdash; сказала она. Граждане России, отметила Захарова, должны подавать документы на государственный портал госуслуг и дополнительно дублировать свои &laquo;гуманитарные случаи информирования российских загранучреждений&raquo;. &laquo;Это делается, чтобы люди не путешествовали по России, и не было дополнительного распространения инфекции&raquo;, &mdash; сказала Захарова. &laquo;Конечно, у людей могут быть гуманитарные случаи, опять можно вернуться к родству, наличию родственников. Здесь важно понять, зачем все это делается, потому что речь идет не просто о вывозе, а речь идет о пандемии. Необходимо сделать так, чтобы инфекция не распространялась, чтобы ее можно было локализовать&raquo;, &mdash; отметила представитель МИД РФ.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(372, 0, 1594396155, NULL, NULL, '<p>24.04.2020 президент подписал Федеральный закон N134-ФЗ об упрощении процедуры приема в гражданство РФ, согласно которому некоторые иностранные граждане смогут получать гражданство России в упрощенном порядке.</p>\n<p>&nbsp;Согласно новому закону, иностранным гражданам теперь не нужно будет отказываться от своего иностранного гражданства для того, чтобы вступить в гражданство РФ.</p>\n<p>&nbsp;Кроме того, в соответствие с новым законом об упрощении процедуры получения гражданства России, кандидатам на гражданство из нижеуказанных категорий теперь не нужно будет находиться на территории РФ не менее трех лет и подтверждать наличие законного источника дохода.</p>\n<p>&nbsp;Данные изменения распространяются на следующие категории иностранных граждан:</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Иностранных граждан, которые находятся в браке с гражданами РФ, проживают в России и имеют в браке общих детей.</p>\n<p>&nbsp;&nbsp;&nbsp; Иностранных граждан, один из родителей которых имеет российское гражданство и живет в РФ.</p>\n<p>&nbsp;&nbsp;&nbsp; Совершеннолетних лиц без гражданства, имевших гражданство СССР и проживающих на территории бывших союзных республик, но не получивших их гражданства.</p>\n<p>Помимо этого, граждане Беларуси, Казахстана, Молдовы или Украины смогут обратиться с заявлением о приеме в гражданство Российской Федерации в упрощенном порядке без соблюдения условия о сроке проживания.</p>\n<p>&nbsp;Также в соответствии с новым законом об упрощенном получении гражданства от 24.04.2020 года будут сокращены необходимые сроки работы в РФ с 3 лет до 1 года для выпускников &ndash; иностранных граждан, получивших профессиональное образование в образовательных или научных учреждениях России.</p>\n<p>&nbsp;Данный закон вступит в силу через 90 дней с момента подписания, то есть уже в июле 2020 года.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(373, 0, 1594396169, NULL, NULL, '<p>Данный закон исключает второе предложение из пункта 8 статьи 13-3 Федерального закона №115 &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo;. <br /> <br /> Ранее пункт 8 статьи статьи 133 содержал следующие указания: <br /> <br /> Не позднее чем за десять рабочих дней до истечения двенадцати месяцев со дня выдачи патента иностранный гражданин вправе обратиться в территориальный орган федерального органа исполнительной власти в сфере внутренних дел, выдавший патент, за переоформлением патента. <em>При этом число обращений за переоформлением патента не может превышать одного раза.</em><br /> <br /> В соответствие с новым законом, второе предложение о том, что обращаться за переоформлением патента на работу можно только один раз, было исключено.<br /> <br /> Таким образом, согласно новому закону было убрано ограничение на количество обращений за переоформлением патента, а значит иностранные граждане смогут обращаться за переоформлением патента на работу неограниченное количество раз без необходимости выезжать за пределы РФ.<br /> <br /> Другими словами, иностранные граждане смогут продлевать патенты находясь в России столько, сколько потребуется.<br /> <br /> Напомним, ранее иностранные граждане получали патент на работу на срок от одного до 12 месяцев с возможностью его однократного переоформления, то есть иностранные граждане могли непрерывно работать в России, не выезжая из страны, не более двух лет.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(374, 0, 1594396180, NULL, NULL, '<p>МОСКВА, 29 апреля. /ТАСС/. Правительство РФ продлил запрет на въезд иностранцев в Россию до улучшения ситуации с коронавирусом. Об этом заявил премьер-министр России Михаил Мишустин на заседании президиума координационного совета по борьбе с коронавирусом. \"До 30 апреля включительно для иностранных граждан действует ограничение на въезд в нашу страну. Я подписал распоряжение о продлении этого срока до тех пор, пока борьба с инфекцией не будет завершена, и эпидемиологическая ситуация не улучшится\", - сказал он.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(375, 0, 1594396189, NULL, NULL, '<p>На иностранцев приходится каждая седьмая занятая в экономике Петербурга и Ленинградской области вакансия. Отношение к трудовым мигрантам от \"понаехали тут\" за последние годы сменилось на нейтральное, а теперь оно уже едва ли не трогательное: мол, не успели они вернуться на родину до разгула пандемии и вынуждены с нами куковать.</p>\n<p>Надо сказать, далеко не все мигранты из стран СНГ стремятся в разгар эпидемии на родину. Что, собственно, их там сейчас ждет? Ровно такие же (плюс&ndash;минус) ограничительные меры, но еще более тяжелая, чем в России, ситуация в экономике. Поэтому многие мигранты уезжать и не собирались: для них наша страна стала второй родиной, а зачастую и единственным источником дохода для всей оставшейся за границей семьи.</p>\n<p>COVID&ndash;19 уравнял всех. Он сгладил не только межэтнические, но и межрелигиозные противоречия. Потому что вирус не различает, таджик ты или башкир, молдаванин или татарин, узбек или русский. Когда беда становится по&ndash;настоящему общей, она объединяет.</p>\n<p>Российская власть, пусть и с большим опозданием, в середине апреля сделала большой шаг навстречу иностранным \"заложникам\" &mdash; ввела мораторий на их депортацию, продлила срок действия всех документов и даже отменила на 3 месяца авансовые платежи за патент.</p>\n<p>Но время летит стремительно. А с ним меняется и ситуация. Порядка 20&ndash;30% мигрантов все&ndash;таки потеряли работу. При этом у них, в отличие от россиян, нет права на пособие по безработице, нет полиса обязательного медицинского страхования, нет большинства гарантий, которые нам дает Трудовой кодекс РФ. Если заболел &mdash; лечись за деньги. А где их взять?</p>\n<p>Мы, конечно, можем продолжать закрывать глаза на подобные \"мелочи\". Но разве не мы, а кто&ndash;то другой может потом оказаться жертвой всплеска этнического криминала?</p>\n<p>Поэтому обсуждение \"второго пакета\" мер помощи мигрантам почти неизбежно. Мы привыкли, что такие пакеты &mdash; федеральные или региональные &mdash; принимаются в отношении тех или иных отраслей, а не категорий работников. Но ведь никто не станет спорить, что мигранты стали неотъемлемой частью российского экономического ландшафта.</p>\n<p>Источник: Деловой Петербург</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(376, 0, 1594396199, NULL, NULL, '<p>Приложение, по информации <a href=\"https://ria.ru/20200529/1572150359.html\" target=\"_blank\">РИА Новости</a>, может получить название \"Мигрант\", оно будет содержать цифровые профили приехавших на заработки в Россию. Электронная карточка будет включать данные о здоровье мигранта, а также информацию о его правовом статусе. Обсуждается и вариант, при котором будет составляться \"рейтинг социального доверия мигранта\".</p>\n<p>Нововведения могут заработать после открытия границ РФ.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(377, 0, 1594396211, NULL, NULL, '<p>Распоряжением Правительства Российской Федерации от 30 мая 2020 г. №&nbsp;1452-р утвержден План мероприятий по реализации в 2020-2022 годах Концепции государственной миграционной политики Российской Федерации на 2019-2025 годы.</p>\n<p>План разработан Министерством внутренних дел Российской Федерации в развитие предложений по реформированию миграционных режимов и института гражданства Российской Федерации, явившихся результатом совместной деятельности всех органов государственной власти в рамках рабочей группы по реализации Концепции, образованной распоряжением Президента Российской Федерации от 6 марта 2019 г. №&nbsp;58-рп.</p>\n<p>В ходе исполнения плана предусмотрено совершенствование институтов постоянного проживания и гражданства Российской Федерации. Планируется создание нового правового режима для долгосрочного пребывания иностранных граждан в России, модернизация механизмов регулирования трудовой миграции, обеспечения безопасности и правопорядка в миграционной сфере, а также комплексная информатизация системы государственного управления в сфере миграции.</p>\n<p>Планом предусмотрен комплекс мероприятий по созданию единой информационной платформы, содержащей сведения об иностранных гражданах, обеспечивающей предоставление государственных услуг, взаимодействие между гражданами, организациями и государственными органами в электронном виде, а также позволяющей внедрить персонализированные информационные сервисы (личный кабинет, рассылка уведомлений в мобильном приложении). Предполагается сформировать государственную систему планирования и управления миграционными процессами, предназначенную для решения задач сбалансированного пространственного развития Российской Федерации, прогнозирования социально-экономических и инвестиционных потребностей субъектов Российской Федерации, учета и перераспределения кадрового потенциала по отраслям экономики и предприятиям.</p>\n<p>Источник: сайт МВД</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(378, 0, 1594396221, NULL, NULL, '<p>02.06.2020 в Министерстве юстиции был зарегистрирован приказ МВД РФ от 24.03.2020 № 180 \"О внесении изменений в приказ МВД России от 30 июля 2019 г. №514\".</p>\n<p>Изменения в первую очередь связаны с тем, что теперь в большинстве случаев иностранцам выдается бессрочный вид на жительство в РФ, и поэтому прежние формы постановки на миграционный учет необходимо было доработать.</p>\n<p>Таким образом, новый приказ утвердил новые формы бланков регистрации иностранцев в РФ, в частности форму уведомления о прибытии иностранного гражданина в место пребывания и форму заявления иностранного гражданина о регистрации по месту жительства.</p>\n<p>Теперь иностранные граждане с бессрочным ВНЖ смогут регистрироваться по месту жительства на неограниченный срок, поэтому в отметке о регистрации дата окончания прописки указываться не будет.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(379, 0, 1594396231, NULL, NULL, '<p>Накануне, как сообщал &laquo;Рамблер&raquo;, стало известно, что представители Росавиации намерены предложить Роспотребнадзору возобновить авиасообщение РФ с другими странами, которое было приостановлено в связи с угрозой распространения коронавирусной инфекции. Изначально говорилось о старте полетов с 15 июля в 15 стран. При этом отмечалось, что список государств Росавиация и Роспотребнадзор определят сообща. Сотрудники Ассоциации туроператоров России со ссылкой на слова неназванного эксперты, заявили, что 15 июля возобновят полеты из России в 8 стран СНГ. Две недели спустя в воздух поднимутся первые самолеты по маршруту в неопределённые пока страны Европы, Ближнего Востока и Юго-Восточной Азии &mdash; всего 7 направлений. &laquo;В любом случае это будут регулярные рейсы в крупные города, преимущественно столицы&raquo;, &mdash; цитируют сотрудники АТР слова своего собеседника.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(380, 0, 1594396240, NULL, NULL, '<p>08.06.2020 года Президент подписал новый закон &ndash; Федеральный закон от 08.06.2020 № 182-ФЗ \"О внесении изменений в Федеральный закон \"О миграционном учете иностранных граждан и лиц без гражданства в Российской Федерации\", который внес изменения в закон о миграционном учете иностранных граждан и лиц без гражданства в РФ N 109-ФЗ.</p>\n<p>Согласно новому закону, иностранные граждане и лица без гражданства, у которых в собственности есть жилое помещение на территории России, смогут предоставлять жилье другим иностранцам для фактического проживания и выступать в качестве принимающей стороны.</p>\n<p>Другими словами, теперь иностранные граждане смогут регистрировать как себя, так и других иностранных граждан в своей недвижимости, то есть ставить себя и их на миграционный учет по своему адресу.</p>\n<p>Важно! Для постановки на миграционный учет по месту пребывания других иностранных граждан, иностранный гражданин &ndash; владелец жилого помещения, должен представить уведомление о своем прибытии в место пребывания и документ, подтверждающий право собственности на жилое помещение.</p>\n<p>Напомним, ранее иностранцы не имели права прописывать в принадлежащих им квартирах других иностранных граждан, за исключением ВКС. В качестве принимающей стороны могли выступать только высококвалифицированные специалисты, имеющие в собственности жилое помещение на территории РФ и только для членов своей семьи.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1);
INSERT INTO `news` (`id`, `userid`, `dateline`, `title`, `intro`, `body`, `image`, `source_name`, `source_link`, `created_at`, `updated_at`, `category_id`) VALUES
(381, 0, 1594396251, NULL, NULL, '<p>Методические рекомендации о порядке применения положений Указа Президента Российской Федерации от 18 апреля 2020 г. № 274 &laquo;О временных мерах по урегулированию правового положения иностранных граждан и или лиц без гражданства в Российской Федерации в связи с угрозой дальнейшего распространения новой коронавирусной инфекции (COVID -19)&raquo; с учетом внесенных в него изменений1</p>\n<p>1. Указом Президента РФ от 18 апреля 2020 г. № 274 &laquo;О временных мерах по урегулированию правового положения иностранных граждан и лиц без гражданства в Российской Федерации в связи с угрозой дальнейшего распространения новой коронавирусной инфекции (COV1D-19)&raquo; на период с 15 марта по 15 нюня 2020 г. приостанавливалось течение сроков действия разрешительных документов в сфере внешней трудовой миграции.</p>\n<p>&nbsp;</p>\n<p>В этой связи иностранные граждане, имеющие действительные патенты по состоянию на 15 марта т.г., оплата авансовых платежей по налогу на доходы физических лиц, по которым не была осуществлена до 15 июня т.г., должны произвести указанный платеж в срок, за который оплачен патент в период с 15 марта по 15 июня 2020 г.</p>\n<p>При этом положения Указа не распространяются на иностранных граждан, оплативших до 15 марта 2020 г. авансовый платеж на срок, превышающий установленный Указом период.</p>\n<p>С 16 июня 2020 г. иностранные граждане, прибывшие в Российскую Федерацию в порядке, не требующем получения визы, которым срок временною пребывания продлен в соответствии с подпунктом &laquo;а&raquo; пункта 1 Указа, имеют право обратиться за переоформлением патента в порядке, предусмотренном законодательном Российской Федерации.</p>\n<p>2. Указом Президента Российской Федерации продлено действие временных мер по урегулированию правового положения иностранных граждан и лиц без гражданства в Российской Федерации в связи с угрозой дальнейшего распространения новой коронавирусной инфекции до 15 сентября 2020 г.</p>\n<p>В обозначенный период иностранным гражданам и принимающей стороне не требуется совершать действий для продления сроков временного пребывания (включая продление виз), сроков постановки на учет по месту пребывания, сроков временного и постоянного проживания (включая продление вида на жительство), сроков действия свидетельств о временном убежище, удостоверении беженца.</p>\n<p>Иностранные граждане в период с 15 марта по 15 сентября 2020 г. изменившие свое место пребывания (прибывшие в новое место пребывания подлежат постановке на учет по месту пребывания в порядке и на условиях, которые установлены Федеральным законом от 18 июля 2006 г. № 109-ФЗ &laquo;О миграционном учете иностранных граждан и лиц без гражданства в Российской Федерации&raquo;.</p>\n<p>Регистрация по месту жительства иностранного гражданина по адресу жилого помещения, срок действия которой истек в период с 15 марта по 15 сентября 2020 г., является действительной в течение 185 дней с даты окончания срока се действия, указанного в отметке о регистрации иностранного гражданина по месту жительства, в том числе для получения государственных услуг, при условии, что данный иностранный гражданин обладает правом пользования указанным жилым помещением.</p>\n<p>Вместе с тем в случае обращения заявителей за предоставлением государственных услуг в сфере миграции осуществлять их предоставление в полном объеме.</p>\n<p>Для лиц, находящихся за пределами Российской Федерации и имеющих разрешение на временное проживание в Российской Федерации или вид на жительство в Российской Федерации, не засчитывается период с 15 марта по 15 сентября 2020 г. в срок действия указанных документов, а также нахождения за рубежом. Участникам Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом, аналогично не засчитывается указанный период в срок действия статуса.</p>\n<p>С учетом изложенного не принимаются решения об аннулировании (утрате) указанных документов (статусов) лицам, которые не могут въехать в Российскую Федерацию и будут находиться за рубежом более шести месяцев.</p>\n<p>В части осуществления контрольно-надзорной деятельности приостановлено течение сроков добровольного выезда из Российской Федерации иностранных граждан, в отношении которых принято решение об административном выдворении (депортации, реадмиссии). В этой связи к указанным лицам меры административного воздействия не применяются.</p>\n<p>Также в обозначенный период в отношении иностранных граждан не принимаются следующие решения:</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp; о нежелательности пребывания (проживания) в Российской Федерации;</p>\n<p>&nbsp;&nbsp;&nbsp; о не разрешении въезда в Российскую Федерацию;</p>\n<p>&nbsp;&nbsp;&nbsp; о депортации и реадмиссии:</p>\n<p>&nbsp;&nbsp;&nbsp; об аннулировании виз, разрешений на временное проживание, видов на жительство, разрешений на работу, патентов и свидетельств участника Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом;</p>\n<p>&nbsp;&nbsp;&nbsp; о сокращении срока временного пребывания;</p>\n<p>&nbsp;&nbsp;&nbsp; о лишении статуса беженца и временного убежища.</p>\n<p>3. Относительно осуществления иностранными гражданами трудовой деятельности следует учитывать, что внесенные изменения не относятся к продлению сроков действия разрешительных документов в сфере внешней трудовой миграции.</p>\n<p>С 16 июня т.г. трудовая деятельность иностранных граждан осуществляется в порядке, установленном Федеральным законом от 25 июля 2002 г. № 115-ФЗ &laquo;О правовом положении иностранных граждан в Российской Федерации&raquo; за исключением следующих условий:</p>\n<p>иностранные граждане, прибывшие в Российскую Федерацию в порядке, не требующем получения визы, вправе обратиться с заявлением о выдаче (продлении, переоформлении) патента без учета требований к установленному сроку подачи документов для его оформления, заявленной цели визита и без обязанности выезда из Российской Федерации</p>\n<p>работодатели, заказчики работ (услуг), получившие в установленном порядке разрешение на привлечение и использование иностранных работников, при условии выполнения установленных ограничений и иных мер, направленных на обеспечение санитарно-эпидемиологического благополучия населения, вправе обратиться с заявлением о выдаче (продлении) разрешения на работу иностранному гражданину, прибывшему в Российскую Федерацию в порядке, требующем получения визы. Такое разрешение выдается без учета требований к заявленной цели визита на срок, который не превышает 15 сентября 2020 г. включительно.</p>\n<p>В целях снижения нагрузки на личный состав подразделений по вопросам миграции территориальных органов МВД России полагаем возможным ориентировать работодателей, заказчиков работ (услуг), привлекающих иностранную рабочую силу, на положения Трудового кодекса Российской Федерации, согласно которым на период оформления разрешения на работу или патента иностранный гражданин отстраняется от работы, но трудовой договор с ним не расторгается.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(382, 0, 1594396279, NULL, NULL, '<p>Медкомиссия является обязательной процедурой для всех иностранных граждан, которые хотят работать или жить на территории РФ. По результатам медосмотра иностранцу выдается медицинское заключение о состоянии его здоровья для предоставления в госорганы для оформления документов.</p>\n<p>В соответствии с приказом Министерства здравоохранения N 384н от 29.06.2015, при прохождении медосмотра необходимо сдать анализы на туберкулез, лепру, сифилис, ВИЧ, бессимптомный инфекционный статус, вызванный вирусом иммунодефицита человека (ВИЧ).<br /> <br /> Однако 27 июня 2020 года в силу вступает новый приказ Министерства здравоохранения Российской Федерации от 15.06.2020 № 581н, в соответствии с которым список анализов был дополнен анализом на коронавирус.</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(383, 0, 1594396288, NULL, NULL, '<p>О том, что чиновники ведут консультации насчет введения в России &laquo;мер, обеспечивающих приоритет трудоустройства национальных кадров на квалифицированные рабочие места&raquo;, сказано в отчете Минтруда, с которым ознакомились &laquo;Известия&raquo;.</p>\n<p>В документе сообщается о реализации генерального соглашения между объединениями профсоюзов, работодателей и правительством за 2019 год. Также на консультациях обсуждаются вопросы совершенствования порядка определения потребности в иностранной рабочей силе, разработки и реализации политики в области трудовой миграции, указано в документе.</p>\n<p>&laquo;Известия&raquo; направили запрос в Минтруд и администрацию президента с просьбой рассказать о ходе обсуждения.</p>\n<p>На сегодняшний день закон не предусматривает для работодателей обязанности отдавать предпочтение россиянам при найме новых сотрудников. По рядовым должностям оформление на работу граждан и мигрантов почти не отличается. Однако для ряда отраслей существуют квоты на число сотрудников-иностранцев. Например, в этом году по постановлению правительства в строительных организациях до 80% работников могут быть мигрантами, в компаниях, которые занимаются выращиванием овощей, &mdash; до половины, а аптекам привлекать таких сотрудников запрещено.</p>\n<p>Источник: &laquo;Известия&raquo;</p>\n<p>По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области&raquo;. В рамках реализации проекта \"Цивилизованная миграция\" при поддержке Фонда Президентских Грантов.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(384, 0, 1600777136, NULL, NULL, '<p>Фонд поддержки добровольного переселения соотечественников \"ОРИЕНТИР\" (Фонд \"ОРИЕНТИР\") в соответствии с Указом Президента Российской Федерации от 30 января 2019 г.No 30 &laquo;О грантах Президента Российской Федерации, предоставляемых на развитие гражданского общества&raquo; по настоящее время безвозмездно оказывает юридическую помощь иностранным гражданам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области, в сфере миграционного и трудового законодательства Российской Федерации.</p>\n<p>&nbsp;С 1 июля началась реализация проекта \"Цивилизованная миграция &ndash; ОНЛАЙН, Санкт-Петербург и Ленинградская область\". По адресу: СПб, ул. Римского-Корсакова 39, проводится правовое просвещение и оказание юридической помощи на очной и безвозмездной основе, трудовым мигрантам и мигрантам- соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области, а через месяц данные услуги станут доступны для всех мигрантов России в режиме &laquo;онлайн&raquo;.</p>\n<p>&nbsp;Юридическую консультацию на безвозмездной основе предоставляют юристы-адвокаты коллектива Фонда \"ОРИЕНТИР\", юристы-стажеры ведущих ВУЗов Санкт-Петербурга, обучающихся на юридическом факультете, таких как: Университет Правосудия (РГУП), Северо-Западный Институт Управления РАНХиГС, Полицейский колледж, СПбГУ и РГПУ им. А.И. Герцена.</p>\n<p>&nbsp;В рамках реализации проекта при поддержке Фонда Президентских Грантов Фонд \"ОРИЕНТИР\" занимается содействием и оказанием помощи иностранным гражданам в законном оформлении и нахождении на территории России, защитой социально-экономических прав трудовых мигрантов и представительством интересов трудовых мигрантов в суде. С информацией можно ознакомиться на сайте <a href=\"http://fondorientir.ru\">http://fondorientir.ru</a></p>', NULL, NULL, NULL, NULL, NULL, 1),
(385, 0, 1600777817, NULL, NULL, '<p>Учебный год в вузах России начнется 1 сентября. Однако уже сейчас усиливаются меры по эпидемиологической защите от коронавируса.</p>\n<p>В 2020/21 учебном году иностранные студенты, учащиеся российских вузов, будут допускаться к учебе только после 14-дневного карантина и сдачи теста на коронавирус.</p>\n<p>Также со страной иностранного студента должна быть открыта граница и налажено международное сообщение. Если сообщение ограничено, иностранному студенту придется учиться дистанционно до отмены ограничений.</p>\n<p>Таким образом, иностранные студенты начнут учебу 1 сентября дистанционно, а когда границы откроют, смогут перейти на очное обучение, сдав тест и пройдя карантин.</p>\n<p>По данным министерства образования и науки, в прошлом учебном году в России училось почти 300 тысяч иностранных студентов, преимущественно из Казахстана, Туркмении, Узбекистана, Китая, Таджикистана, Украины, Индии, Беларуси, Азербайджана и Киргизии.</p>', NULL, NULL, NULL, NULL, NULL, 1),
(386, 0, 1465948800, 'В Россию - жить и работать', 'В Россию - жить и работать', 'https://yadi.sk/i/a5NGzm0Csx5oN', NULL, 'газета \"Санкт-Петербургский курьер\" ', NULL, NULL, NULL, 3),
(387, 0, 1461110400, 'В школах Ленинградской области организован проект по адаптации детей-мигрантов', 'Организаторами проекта выступили Фонд «ОРИЕНТИР» и общественная организация «Ший корно».', 'Санкт-Петербург, 20 апреля.\r\n\r\nОрганизаторами проекта выступили Фонд «ОРИЕНТИР» и общественная организация «Ший корно».\r\n\r\nОбщественная организация региональная Марийская национально-культурная автономия Ленинградской области «Ший корно» вместе с Фондом поддержки добровольного переселения соотечественников «ОРИЕНТИР» разработала проект под названием «Исследование проблем языковой и социокультурной адаптации детей-мигрантов в общеобразовательных школах Ленинградской области».\r\n\r\nПоддержку данному проекту оказывает Комитет по печати и связям с общественностью Ленобласти. Кроме этого, выразили желание присоединиться к исследованию различные национальные диаспоры и общественные организации. Финансирование в виде субсидий оказывается правительством Ленинградской области.\r\n\r\nПредварительная подготовка к проекту велась несколько месяцев. Первым этапом стало социологическое исследование, в котором участвовали около двух тысяч 7-8-классников из школ восьми районов области: Выборгского, Тосненского, Тихвинского, Гатчинского, Ломоносовского, Кировского, Кингисеппского и Всеволожского. Целью данного этапа стало выявление наиболее острых проблем адаптации и интеграции в общество детей мигрантов. Анкетирование было анонимным и включало в себя 14 вопросов по социальной и культурной тематике.\r\n\r\nКроме этого, в опросе участвовали тридцать экспертов, являющиеся специалистами школ, муниципальных образований и органов управления миграционной политикой Ленобласти. Анкетирование прошли и школьные педагоги. С помощью опроса данной группы респондентов выявлялись трудности работы с детьми-мигрантами и возможные пути их преодоления.\r\n\r\nВоспользовавшись полученными данными, региональная Марийская национально-культурная автономия Ленинградской области «Ший корно» выработала методику сопровождения приезжих ребят для того, чтобы в пределах школы как можно больше облегчить их адаптацию в новом обществе.\r\n\r\nВ завершение проекта пройдет тематический Круглый стол. На нем будут презентованы результаты мероприятия. Также планируется обсуждение механизма адаптации и интеграции детей-мигрантов в социокультурное поле Ленинградской области.\r\n\r\nФонд «ОРИЕНТИР» был образован в 2011 году. Он оказывает помощь в добровольном переселении в Российскую Федерацию переселенцев-соотечественников, соцподдержку, информирование и защиту мигрантов. Кроме этого, фонд развивает и создает инфраструктуру для переселенцев на территориях вселения.\r\n\r\n', NULL, 'Информационное агентство \"Невские Новости\"', 'https://nevnov.ru/443421-v-shkolah-leningradskoi-oblasti-organizovan-proekt-po-adaptacii-detei-migrantov', NULL, NULL, 3),
(388, 0, 1447977600, '16 ноября - международный День толерантности', NULL, 'https://yadi.sk/i/EVAPDzh6swQZC', NULL, 'газета \"Дворцовый округ\"', NULL, NULL, NULL, 3),
(389, 0, 1446336000, 'Адаптация детей мигрантов', NULL, 'https://yadi.sk/i/pHJ8jvabswQ6b', NULL, '\"Смольнинское\" (газета муниципального образования муниципальный округ Смольнинский)', NULL, NULL, NULL, 3),
(390, 0, 1431993600, 'В Ленобласти стартовал интеграционный проект для мигрантов', 'Петербургские и областные эксперты анализируют ситуацию, обобщают результаты научных изысканий по проблемам миграции — сформирована и проводится фокус — группа из представителей пяти национальных диаспор\r\n', '', 'http://www.severinform.ru/?page=newsfull&date=12-05-2015&newsid=228919', 'Информационное агентство \"СеверИнформ\"', NULL, NULL, NULL, 3),
(391, 0, 1431388800, 'В Ленобласти стартовал интеграционный проект для мигрантов', NULL, 'https://regnum.ru/news/polit/1923344.html', NULL, 'Информационное агентство \"REGNUM\"', NULL, NULL, NULL, 2),
(392, 0, 1599436800, 'Квота на РВП 2021. Будь первым!', 'Важная информация для тех, кто планирует получать квоту на РВП в 2021 году', 'Важная информация для тех, кто планирует получать квоту на РВП в 2021 году\r\nМногие граждане, не имеющие прямых оснований к оформлению разрешения на временное проживание, желают получить квоту. В Санкт-Петербурге квота на РВП выделяется ежегодно на специальной комиссии. Порядок формирования и работы комиссии установлен Приказом ГУ МВД России по Санкт-Петербургу и Ленинградской области от 21 ноября 2018 г. N 580.  Прием заявлений происходит раз в году. Как правило, в начале года.\r\n\r\nВот несколько важных советов как увеличить шансы на получение квоты в 2021 году, а главное — не пропустить сроки подачи документов.\r\n\r\nНе относитесь к сбору пакета документов формально. Заявление на квоту заполнить не сложно. Но одного заявления не достаточно. К  нему вы обязаны приложить документы, подтверждающие основания выделения квоты (связь с Россией ). Качественный пакет документов, однозначно, увеличит ваши шансы. Если вы указываете, что у вас имеется постоянное место работы, — приложите трудовой договор, а лучше — еще и ходатайство от работодателя. Если имеете недвижимость, — выписку из реестра. Если снимаете жилье – договор найма. Если обучаетесь – договор с ВУЗом,  характеристику или ходатайство из учебного заведения. Если вы просите выделить квоту членам семьи, то на них также требуется собирать полный комплект документов (справки с места учебы, работы). И т.п.  Чем больше вы приложите документов, указывающих на вашу связь с Россией, тем выше шансы получить квоту.\r\nНачините собирать документы уже сейчас. В этом году те, кто не поторопились, просто не успели подать документы. Из -за большого количества заявок прием длился всего 1 месяц и был приостановлен уже в конце января. Чтобы быть в первых рядах у вас должно быть все готово заранее. Как только начнется прием документов, вы сможете без промедления отправить документы в комиссию.  Те, кто не озаботился данным вопросом заранее — не успели подать заявление. Сейчас самое время серьезно заняться данным вопросом. Как показывает практика нескольких лет, кто первый — тому и квота.\r\nСледите на новостями на сайте Главного Управления по вопросам миграции. О начале приема заявлений будет официально объявлено. Не стоит направлять документы, не дождавшись официальных сообщений. В прошлом году внезапно изменилась форма заявления. Тем, кто поторопился и направил заранее заявление в комиссию, потребовалось переделывать комплект документов и направлять его заново. Как правило, объявление не публикуется заблаговременно. В прошлом году информация о начале приема заявлений на квоту появилась 30 декабря.\r\nС заявлением предоставляйте только подлинники или заверенные копии документов с переводом на русский язык. К заявлению обязательно прикладывается нотариальный перевод паспорта. Копия регистрации и миграционной карты могут быть не заверены, так как данная информация имеется в распоряжении МВД. Остальные документы – необходимо заверять. Если вы отправите в комиссию не заверенные копии документов, это даст формальное основание отказать в рассмотрении вашей заявки.\r\nВнимательно ознакомьтесь с требованиями к оформлению конверта. Это важная деталь.  МВД требует, чтобы конверт был помечен «квота» и направлен ценным письмом с описью вложения. Обратиться в комиссию можно только направив документы по почте. Следуйте этим правилам, иначе ваше письмо может попросту потеряться и не попасть в комиссию.\r\nЕсли Вы сделаете все верно, велика вероятность того, что в 2021 году вы получите квоту на оформление разрешения на временное проживание. Решение комиссии направляется на электронную почту, указанную в заявлении. Как правило, документы рассматривают в течение 2-3 месяцев. В положительном решении указывается дата, когда вам необходимо будет явиться на подачу документов.', NULL, NULL, 'https://ufms.spb.ru/kvota/', NULL, NULL, 2),
(393, 0, 1599436800, 'Квота на РВП 2021. Будь первым!', 'Важная информация для тех, кто планирует получать квоту на РВП в 2021 году', 'Многие граждане, не имеющие прямых оснований к оформлению разрешения на временное проживание, желают получить квоту. В Санкт-Петербурге квота на РВП выделяется ежегодно на специальной комиссии. Порядок формирования и работы комиссии установлен Приказом ГУ МВД России по Санкт-Петербургу и Ленинградской области от 21 ноября 2018 г. N 580.  Прием заявлений происходит раз в году. Как правило, в начале года.\r\n\r\nВот несколько важных советов как увеличить шансы на получение квоты в 2021 году, а главное — не пропустить сроки подачи документов.\r\n\r\nНе относитесь к сбору пакета документов формально. Заявление на квоту заполнить не сложно. Но одного заявления не достаточно. К  нему вы обязаны приложить документы, подтверждающие основания выделения квоты (связь с Россией ). Качественный пакет документов, однозначно, увеличит ваши шансы. Если вы указываете, что у вас имеется постоянное место работы, — приложите трудовой договор, а лучше — еще и ходатайство от работодателя. Если имеете недвижимость, — выписку из реестра. Если снимаете жилье – договор найма. Если обучаетесь – договор с ВУЗом,  характеристику или ходатайство из учебного заведения. Если вы просите выделить квоту членам семьи, то на них также требуется собирать полный комплект документов (справки с места учебы, работы). И т.п.  Чем больше вы приложите документов, указывающих на вашу связь с Россией, тем выше шансы получить квоту.\r\nНачините собирать документы уже сейчас. В этом году те, кто не поторопились, просто не успели подать документы. Из -за большого количества заявок прием длился всего 1 месяц и был приостановлен уже в конце января. Чтобы быть в первых рядах у вас должно быть все готово заранее. Как только начнется прием документов, вы сможете без промедления отправить документы в комиссию.  Те, кто не озаботился данным вопросом заранее — не успели подать заявление. Сейчас самое время серьезно заняться данным вопросом. Как показывает практика нескольких лет, кто первый — тому и квота.\r\nСледите на новостями на сайте Главного Управления по вопросам миграции. О начале приема заявлений будет официально объявлено. Не стоит направлять документы, не дождавшись официальных сообщений. В прошлом году внезапно изменилась форма заявления. Тем, кто поторопился и направил заранее заявление в комиссию, потребовалось переделывать комплект документов и направлять его заново. Как правило, объявление не публикуется заблаговременно. В прошлом году информация о начале приема заявлений на квоту появилась 30 декабря.\r\nС заявлением предоставляйте только подлинники или заверенные копии документов с переводом на русский язык. К заявлению обязательно прикладывается нотариальный перевод паспорта. Копия регистрации и миграционной карты могут быть не заверены, так как данная информация имеется в распоряжении МВД. Остальные документы – необходимо заверять. Если вы отправите в комиссию не заверенные копии документов, это даст формальное основание отказать в рассмотрении вашей заявки.\r\nВнимательно ознакомьтесь с требованиями к оформлению конверта. Это важная деталь.  МВД требует, чтобы конверт был помечен «квота» и направлен ценным письмом с описью вложения. Обратиться в комиссию можно только направив документы по почте. Следуйте этим правилам, иначе ваше письмо может попросту потеряться и не попасть в комиссию.\r\nЕсли Вы сделаете все верно, велика вероятность того, что в 2021 году вы получите квоту на оформление разрешения на временное проживание. Решение комиссии направляется на электронную почту, указанную в заявлении. Как правило, документы рассматривают в течение 2-3 месяцев. В положительном решении указывается дата, когда вам необходимо будет явиться на подачу документов.', 'kbvota.jpg', 'Миграционный форум СПб', 'https://ufms.spb.ru/kvota/', NULL, NULL, 13),
(394, 0, 1465948800, 'Где пройти медкомиссию, сдать экзамен и оформить перевод паспорта в карантин?', 'Управление по вопросам миграции продолжает принимать документы на РВП, ВНЖ и гражданство.', 'Управление по вопросам миграции продолжает принимать документы на РВП, ВНЖ и гражданство. Поэтому иностранных граждан интересует вопрос, где можно оформить необходимо документы: пройти медкомиссию, сдать экзамен и сделать корректный перевод паспорта?\r\n\r\nДокументы оформить можно только в уполномоченных организациях.\r\n\r\nЭкзамен по русскому языку\r\nЭкзамен по русскому языку вправе проводить уполномоченные образовательные учреждения, аккредитованные Центры тестирования иностранных граждан и работающие с ними миграционные центры.  Экзамен всегда проводится в присутствии иностранного гражданина. Дистанционная сдача экзамена, в соответствии с законом, невозможна.\r\n\r\nЭкзамен состоит из двух частей: тестирование и говорение. Тестирование включает вопросы по русскому языку, истории России и законодательству России. Часть по русскому языку включает в себя вопросы лексики и грамматики, письмо и чтение, а часть с историей России, может содержать такие вопросы как:  когда в России отмечают День победы или другие государственные праздники, когда распался СССР, какое было прозвище у Ивана IV (он же – Иван Грозный).\r\nПо результатам экзамена выдается сертификат, который подтверждает владение русским языком, историей и основ законодательства Российской Федерации.\r\n\r\nСертификат имеет три уровня.\r\n\r\nДля трудового патент подойдет самый простой уровень — ИР (иностранный работник).\r\n\r\nДля РВП необходим уровень выше – в сертификате так и указывается — уровень  «РВП».\r\n\r\nА для ВНЖ необходим уровень сертификата – «ВЖ».\r\n\r\nМожно немного сэкономить время и деньги, сдав сразу экзамен на уровень ВНЖ при получении РВП. Это законно, да и по сложности экзамены не сильно отличаются. Результаты этого экзамена подойдут в дальнейшем для оформления вида на жительство, ведь сертификат действует 5 лет.\r\n\r\nСтоимость экзамена варьируется от 5000 рублей до 6000 рублей.\r\n\r\nМедицинская комиссия\r\nМедицинскую комиссию, возможно, пройти в медицинских центрах, которые включены в перечень.  Результаты медицинского освидетельствования оформляются в виде трех документов. На руки выдается справка об отсутствии опасных инфекционных заболеваний, сертификат об отсутствии ВИЧ, а также справка от нарколога.\r\n\r\nДля получения комплекта медицинских документов иностранный гражданин обязан лично пройти медицинский осмотр, сдать анализы крови, сделать флюорографию. В некоторых центрах пройти все этапы медицинского освидетельствования   можно за 1 визит. Как правило, медицинские заключения готовятся в течение 5 рабочих дней. Важно отметить, что в условиях карантина сроки готовности заключений могут увеличиваться.\r\n\r\nСредняя стоимость медкомиссии 3500 – 4000 рублей.\r\n\r\nПереводы документов\r\nПереводы документов должны быть обязательно удостоверены нотариусом. Важно, чтобы перевод был корректно выполнен — данные гражданина должны совпадать с остальными документами, которые составлены на русском языке, в некоторых случаях – требуется перевод печатей в документе. Если данные перевода не совпадут с данными, указанными в других документах, инспектор не примет ваши документы и перевод придется переделывать заново.\r\n\r\nВ шапке перевода указываются все языки, с которых делается перевод. Как правило, в загранпаспортах, иногда в свидетельствах о рождении, присутствуют несколько языков.\r\n\r\nНотариальный перевод паспорта, свидетельства, справки можно заказать в Бюро переводов, которые работают с нотариусами. Стоимость перевода начинается от 1300 рублей и зависит от  количества документов, необходимости нотариальных копий.\r\n\r\nМеры, принятые в связи с пандемией коронавируса, вынудили многие организации временно приостановить работу. Многие медицинские центры и центры тестирования закрыты. Бюро переводов работают в ограниченном режиме. При этом пройти медкомиссию и сдать экзамен дистанционно невозможно.\r\n\r\nГде оформить документы в карантин?\r\nОдной из немногих организаций, которые продолжают оказывать услуги иностранным гражданам является Миграционный центр у м. Чернышевская. Центр работает при адвокатском бюро, поэтому не приостановил свою работу в карантин.\r\n\r\nВ Миграционном центре можно получить консультацию, оформить страховку, заполнить бланки на РВП, ВНЖ и гражданство, а также пройти медицинскую комиссию и сдать экзамен. При миграционном центре работает Бюро переводов, в котором можно заказать нотариальный перевод необходимых документов.', NULL, NULL, 'https://ufms.spb.ru/gde-projti-medkomissiyu-sdat-ekzamen-i-oformit-perevod-pasporta-v-karantin/', NULL, NULL, 12),
(395, 0, 1461110400, 'Телекомпания СТВ. Сосновый Бор', 'Бесплатную юридическую помощь получили мигранты-соотечественники в Сосновом Бору. На протяжении целого дня в молодежном центре \"Диалог\" работала приемная Фонда \"ОРИЕНТИР\".', '<p><a href=\"http://fondorientir.ru/smi_o_nas/telekompaniya_stv_sosnovyj_bor/?cpembed=AQDAlN-sR1SS\"><img src=\"https://www.cincopa.com/media-platform/api/thumb.aspx?size=large&fid=AQDAlN-sR1SS&trs=play\" width=\"400\" height=\"250\" style=\"width: 400px; height: 250px;\"></a></p><p><a href=\"http://fondorientir.ru/smi_o_nas/telekompaniya_stv_sosnovyj_bor/?cpembed=AQDAlN-sR1SS\">New Gallery 2018/3/23</a></p>', NULL, NULL, 'https://www.cincopa.com/media-platform/api/thumb.aspx?size=large&fid=AQDAlN-sR1SS&trs', NULL, NULL, 4),
(396, 0, 1431388800, 'О возвращении соотечественников в Россию 2017!', '19 мая 2017 депутат фракции ЛДПР в Госдуме Александр Шерин задал вопрос о получении российского гражданства нашими соотечественниками Уполномоченному по правам человека в РФ Татьяне Москальковой (19.05.2017 г.)', '<p><a href=\"http://fondorientir.ru/smi_o_nas/o_vozvrawenii_sootechestvennikov_v_rossiyu_2017/?cpembed=AIPAEUey4NOr\"><img src=\"https://www.cincopa.com/media-platform/api/thumb.aspx?size=large&fid=AIPAEUey4NOr&trs=play\" width=\"400\" height=\"250\" style=\"width: 400px; height: 250px;\"></a></p><p><a href=\"http://fondorientir.ru/smi_o_nas/o_vozvrawenii_sootechestvennikov_v_rossiyu_2017/?cpembed=AIPAEUey4NOr\">New Gallery 2018/10/2</a></p>', NULL, NULL, NULL, NULL, NULL, 4),
(397, 0, 1431993600, 'Соотечественники, ВОЗВРАЩАЙТЕСЬ!', 'Почему государственная программа по переселению в Россию бывших граждан СССР не работает должным образом? С какими сложностями сталкиваются люди, получившие российские паспорта, до вступления в силу Закона о гражданстве? Зачем вообще нужно возвращать соотечественников? Материалы с сайта МТРК Мир (www.mirtv.ru) Программа \"Акценты\", ведущий Александр Зайцев (11.11.2009 г.)', '<p><a href=\"http://fondorientir.ru/smi_o_nas/sootechestvenniki_vozvrawajtes/?cpembed=AEDA3Uu24Nom\"><img src=\"https://www.cincopa.com/media-platform/api/thumb.aspx?size=large&fid=AEDA3Uu24Nom&trs=play\" width=\"400\" height=\"250\" style=\"width: 400px; height: 250px;\"></a></p><p><a href=\"http://fondorientir.ru/smi_o_nas/sootechestvenniki_vozvrawajtes/?cpembed=AEDA3Uu24Nom\">New Gallery 2018/10/2</a></p>', NULL, NULL, NULL, NULL, NULL, 4),
(398, 0, 1446336000, 'О возвращении соотечественников в Россию', 'О возвращении соотечественников в Россию или почему нашим соотечественникам не дают паспорта РФ?', 'В декабре 2016 года в Госдуму был внесён законопроект, в котором предлагается выдавать гражданство по «праву почвы». Это юридический термин, закрепляющий право получения гражданства того или иного государства за лицами, родившимися на территории этого государства независимо от их расовой, национальной или языковой принадлежности.\r\n\r\nПри этом законопроект отменяет требование, которое больше всего препятствует массовому получению гражданства России носителями русского языка. Сейчас, чтобы получить гражданство России, нужно предоставить документ, удостоверяющий выход из иностранного гражданства. А, например, Украина, Латвия и Эстония такой документ не выдают, ссылаясь на то, что по международным нормам человек не имеет права даже временно оставаться без какого-либо гражданства. Получается замкнутый круг.\r\n\r\n<p><a href=\"http://fondorientir.ru/smi_o_nas/o_vozvrawenii_sootechestvennikov_v_rossiyu/?cpembed=AoBAZUuX4NPo\"><img src=\"https://www.cincopa.com/media-platform/api/thumb.aspx?size=large&fid=AoBAZUuX4NPo&trs=play\" width=\"400\" height=\"250\" style=\"width: 400px; height: 250px;\"></a></p><p><a href=\"http://fondorientir.ru/smi_o_nas/o_vozvrawenii_sootechestvennikov_v_rossiyu/?cpembed=AoBAZUuX4NPo\">New Gallery 2018/10/2</a></p>\r\n\r\n Лидер ЛДПР Владимир Вольфович Жириновский уже не первый десяток лет говорит о том, что гражданство нашим соотечественникам нужно возвращать по праву принадлежности к русскому языку. Носители русского языка является неотъемлемой частью Русского мира, а значит они имеют полное право проживать в его эпицентре, в России. Это обретает особую актуальность в связи с русофобскими настроениями, которые сейчас распространены по миру. Русские имеют полное право жить в России и не бояться гонений со стороны других национальностей, как это происходит сейчас, например, в Украине и Прибалтике.  ', NULL, NULL, NULL, NULL, NULL, 4);
INSERT INTO `pages` (`id`, `laravel_name`, `title`, `metakey`, `metadesc`, `h1`, `content`, `desc`, `menu_id`, `created_at`, `updated_at`) VALUES
(1, 'main', 'Главная', 'migration razreshenie-na-rabotu', NULL, NULL, '<p>Интернет-приемная по вопросам миграции и трудовым спорам реализована в рамках социально-значимого проекта «Цивилизованная миграция - Санкт-Петербург и Ленинградская область»</p>\r\n\r\n<p>Основные цели и предмет деятельности интернет-приемной - осуществление приема иностранных граждан и лиц без гражданства с обращениями, заявлениями и просьбами, связанными с юридической, социальной поддержкой, натурализацией и адаптацией в российское общество.</p>\r\n\r\n<p>Для того, чтобы оставить письменное обращение, необходимо перейти в раздел \"Интернет-приемная\" в левом боковом меню сайта или нажать ссылку http://fondorientir.ru/feedback/.</p>\r\n\r\n<p>Для приема устных обращений иностранных граждан и лиц без гражданства работает телефон \"горячей линии\": 8(812)385-69-89.</p>\r\n\r\n<p>Дополнительные сведения: при реализации проекта используются средства государственной поддержки, в соответствии с распоряжением Президента Российской Федерации от 30 января 2019 г. № 30 c 01.07.2019 с использованием гранта Президента Российской Федерации на развитие гражданского общества, предоставленного Фондом президентских грантов, Фонд «ОРИЕНТИР» реализует проект «Цивилизованная миграция - Санкт-Петербург и Ленинградская область» (далее – Проект). В рамках Проекта работает правовая приемная Фонда «ОРИЕНТИР», где мигранты-соотечественники бесплатно получают юридическую помощь в сфере миграционного и трудового законодательства.</p>', 0, NULL, NULL, NULL);
INSERT INTO `previous_projects` (`id`, `contract`, `contractor`, `subject`, `terms`, `price`, `created_at`, `updated_at`) VALUES
(1, '№ Ф-21\r\n\r\nот 01.12.2014 г.', 'Комитет по печати и связям с общественностью Ленинградской области, 191311, Санкт-Петербург, ул. Смольного, д.3', 'Предоставление субсидий, поступивших в бюджет Ленинградской области из федерального бюджета, для реализации проекта «Адаптация мигрантов, проживающих на территории Ленинградской области, интегрирование их в единое правовое и культурное полое России, развитие традиционных духовных ценностей»', '01.11.2014 г. – 31.12. 2015 г.', '457 402-00', NULL, NULL),
(2, '№ 03|870\r\n\r\nот 25.11.2015 г.', 'Комитет по молодежной политике и взаимодействию с общественными организациями', 'Предоставление субсидий в целях возмещения затрат, возникших в связи с оказанием услуг в период с 01.01.2015 г. по 15.11.2015 г., направленных на реализацию мероприятий, «Исследование проблем языковой и социокультурной адаптации детей мигрантов в Санкт-Петербурге» на основании распоряжения Комитета от 02.10.2015 №49 «Об итогах конкурсного отбора на право получения в 2015 году субсидии на реализацию мероприятий, направленных на бытовую, языковую и социокультурную адаптацию мигрантов»', '01.01.2015 г. по 15.11.2015 г.', '450 000-00', NULL, NULL),
(3, '№ 160/2015/3\r\nот 15.01.2016', 'Общероссийское общественное движение «Гражданское достоинство»', 'Предоставление гранта на реализацию социально значимого проекта «Правовое просвещение и оказание юридической помощи мигрантам - соотечественникам», проживающим и работающим на территории Санкт-Петербурга и Ленинградской области', '02.2016-08.2016', '1 695 958,00', NULL, NULL),
(4, '№2 от 01.02.2016 г.', 'Общественная организация региональнaя Maрийская национально-культурная автономия Ленинградской области «Ший корно»', 'Услуги по реализации проекта «Исследование проблем языковой и социокультурной адаптации детей-мигрантов в общеобразовательных школах Ленинградской области», в соответствии с проектом Договора о предоставлении субсидии социально ориентированным некоммерческим организациям от Комитета по печати и связям с общественностью Ленинградской области, с постановлением Правительства Ленинградской области от 14 ноября 2013 года № 399 «Об утверждении государственной программы Ленинградской области «Устойчивое общественное развитие в Ленинградской области»', '15.02.2016 г. – 15.04.2016 г', '241 845-00', NULL, NULL),
(5, '№ 213/2016-2', 'Общероссийское общественное движение «Гражданское достоинство»', 'Предоставление гранта на реализацию проекта в сфере защиты прав и свобод человека и гражданина «Оказание юридической помощи на безвозмездной основе мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области»', '10.2016 – 05.2017', '2 190 760,00', NULL, NULL),
(6, '№2 от 21.11.2016', 'Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге', 'Предоставление в 2016 году субсидии в соответствии с Законом Санкт-Петербурга «О грантах Санкт-Петербурга для общественных объединений» в целях возмещения затрат при реализации проекта «Правовая помощь мигрантам в их социальной адаптации и интеграции в сообщество Санкт-Петербурга»', '01.05.2016 – 01.12.2016', '348 518,72', NULL, NULL),
(7, '№ 1-01/2017 от 30.01.2017', 'Фонд социальной и экономической стабилизации Ленинградской области', 'Услуги по проведению массовых социологических исследований на основании опроса школьников общеобразовательных школ педагогов и др. специалистов сферы образования, - для экспертов и специалистов по вопросам миграции и адаптации иммигрантов Ленинградской области', '30.01.2017 г.–15.03.2017 г.', '1 712 000-00 ', NULL, NULL),
(8, '№ 17-1-010915', 'Фонд-оператор президентских грантов по развитию гражданского общества', 'Предоставление гранта осуществление комплекса взаимосвязанных мероприятий, направленных на достижение конкретных общественно полезных результатов в рамках определенного срока и бюджета, – проекта «\"Цивилизованная миграция\". Правовая помощь трудовым мигрантам и мигрантам-соотечественникам Санкт-Петербурга и Ленинградской области»', '01.09.2017–31.05.2018', '2 998 739,00', NULL, NULL),
(9, '№1 от 08.12.2017', 'Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге', 'Предоставление субсидии из бюджета Санкт-Петербурга в 2017 году субсидии в целях финансового обеспечения (возмещения затрат) на реализацию мероприятия «Цивилизованная миграция». Правовая помощь мигрантам в их социальной адаптации и интеграции в сообщество Санкт-Петербурга»', '01.04.2017-15.12.2017', '577 500,00', NULL, NULL),
(10, '№ 18-1-023470', 'Фонд-оператор президентских грантов по развитию гражданского общества', 'Предоставление гранта осуществление комплекса взаимосвязанных мероприятий, направленных на достижение конкретных общественно полезных результатов в рамках определенного срока и бюджета, – проекта\r\n\r\n\"Цивилизованная миграция\". Правовое просвещение и оказание юридической помощи трудовым мигрантам и мигрантам- соотечественникам Санкт-Петербурга и Ленинградской области', '01.07.2018-31.03.2019', '2 998 028,00', NULL, NULL),
(11, '№2\r\n\r\nот 29.06.2018', 'Комитет по межнациональным отношениям и реализации миграционной политики в Санкт-Петербурге', 'Предоставление субсидии из бюджета Санкт-Петербурга в 2018 году субсидии в целях финансового обеспечения на реализацию мероприятия «Оказание бесплатной юридической помощи мигрантам-соотечественникам, проживающим и ведущим трудовую деятельность на территории\r\nСанкт-Петербурга»', '02.04.2018-31.10.2018 на общую сумму 2 034 034,00 рубля', '654 500,00', NULL, NULL),
(12, '№ 9-01/2018\r\n\r\nот 25.01.2018', 'Фонд социальной и экономической стабилизации Ленинградской области', 'Комплексное сопровождение переселению соотечественников в Северо-Западный Федеральный округ Российской Федерации в рамках Указа Президента Российской Федерации от 22 июня 2006 г. N 637 \"О мерах по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом\"', '29.01.2018-31.12.2019', '4 555 000,00', NULL, NULL),
(13, '№ 19-1-044337', 'Фонд-оператор президентских грантов по развитию гражданского общества', 'Предоставление гранта осуществление комплекса взаимосвязанных мероприятий, направленных на достижение конкретных общественно полезных результатов в рамках определенного срока и бюджета, – проекта\r\n\r\n\"Цивилизованная миграция - Санкт-Петербург и Ленинградская область\"', '01.07.2019-31.03.2020', '2 998 945,00', NULL, NULL);
INSERT INTO `projects` (`id`, `term`, `name`, `results`, `created_at`, `updated_at`) VALUES
(1, '12.09.2011-09.12.2011', 'Проведение мониторинга по вопросу участия предприятий Кингисеппского района в Программе по оказанию содействия добровольному переселению соотечественников, проживающих за рубежом', '<p>В результате проведено «Совещание по вопросу участия предприятий Кингисеппского района в Программе по оказанию содействия добровольному переселению соотечественников, проживающих за рубежом». На Совещание 06.12.2011 г. приняли участие около 50 человек, в том числе 16 участников представляли компании работодателей. Составлен Протокол Совещания. </p>\r\n\r\n<p>Результатами работы стало осознание необходимости проведения подобных мероприятий, а также других возможных действий, направленных на формирование профессионального принимающего сообщества, признание необходимости следования принципам открытости процедур по направлению «Взаимодействие федеральных, областных и местных органов власти, бизнеса и институтов гражданского общества по решению проблем дефицита трудовых ресурсов в области, предотвращению нелегальной трудовой миграции, привлечению трудовых ресурсов российских соотечественников и социализации трудовой миграции в Ленинградской области». Также Фонд приобрел известность в Кингисеппском районе.</p>', NULL, NULL),
(2, '01.03.2012-25.04.2012', ' Бесплатные телефонные консультации по вопросам добровольного переселения в РФ и трудовому праву', '<p>Возможностью получить бесплатную консультацию воспользовались более 150 соотечественников проживающих в Латвии. Основными вопросами являлись:</p>\r\n<ul>\r\n<li>права переселенцев,</li>\r\n<li>возможности трудоустройства и образования,\r\nобустройства,</li>\r\n<li>порядок оформления документов и др.</li>\r\n</ul>\r\n<p>В результате выполнения проекта Фонд и его представительство в Латвии (г.Рига) приобрел популярность в среде наших соотечественников.</p>', NULL, NULL),
(3, '07.05.2012-29.06.2012', 'Проведение телефонного опроса (анкетирование) «Мониторинг соотечественников, потенциальных переселенцев в Латвии, их интересов и миграционные настроения»', '<p>Проведено исследование, заключающийся в телефонном опросе (анкетировании) респондентов по телефону на основе заранее разработанной анкеты. Опрошено более 200 соотечественников, проживающих в Латвии.Проведен Анализ анкетирования.\r\n</p>\r\n<p>\r\nВ результате реализации проекта Фонд и его представительство в Латвии (г.Рига) теперь обладает всей полнотой необходимой информации об интересах соотечественников, а также крепкий костяк потенциальных клиентов, лояльных деятельности Фонда. </p>', NULL, NULL),
(4, '07.05.2012-29.06.2012', '<p>Проведено исследование, заключающийся в телефонном опросе (анкетировании) респондентов по телефону на основе заранее разработанной анкеты. Опрошено более 200 соотечественников, проживающих в Латвии.Проведен Анализ анкетирования.</p>\r\n\r\n<p>В результате реализации проекта Фонд и его представительство в Латвии (г.Рига) теперь обладает всей полнотой необходимой информации об интересах соотечественников, а также крепкий костяк потенциальных клиентов, лояльных деятельности Фонда. </p>', '<p>Участие Фонда «ОРИЕНТИР» на Секции 4 – «Перспективы реализации Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом».</p>\r\n\r\n<p>В обсуждении был рассмотрен вопрос о работе Государственной Программы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом в ее новой редакции. Присутствовали представители регионов, куда предполагается переселение соотечественников, и рассказали о своих регионах и их преимуществах. Обмен опытом. С учетом решений секций принят итоговый документ Всемирного конгресса, который определил перспективы и конкретные направления работы на ближайшие несколько лет.</p>\r\n\r\n<p>По итогам работы секции были приняты рекомендации, в которых нашли отражения пожелания соотечественников, касающиеся вопросов скорейшего запуска новой редакции Государственной программы, активизации ее информационного сопровождения, а также усиления работы на данном направлении российских загранучреждений и уполномоченных органов субъектов РФ.</p>\r\n\r\n<p>В результате участия в Фонда к конгрессе Фонд приобрел узнаваемость в среде наших соотечественников, проживающих за рубежом, приобрел связи для дальнейшей работы со СМИ и необходимые контакты для расширения сети представительств Фонда на постсоветском пространстве.</p>', NULL, NULL),
(5, '26.10.2012-27.10.2012', 'IV Всемирный конгресс соотечественников, проживающих за рубежом\r\n\r\nУчастие в работе\r\n\r\nСекции 4 – перспективы реализации Государственной программы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом', 'Участие Фонда «ОРИЕНТИР» на Секции 4 – «Перспективы реализации Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом».\r\n\r\n   В обсуждении был рассмотрен вопрос о работе Государственной Программы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом в ее новой редакции. Присутствовали представители регионов, куда предполагается переселение соотечественников, и рассказали о своих регионах и их преимуществах. Обмен опытом. С учетом решений секций принят итоговый документ Всемирного конгресса, который определил перспективы и конкретные направления работы на ближайшие несколько лет.\r\n\r\n   По итогам работы секции были приняты рекомендации, в которых нашли отражения пожелания соотечественников, касающиеся вопросов скорейшего запуска новой редакции Государственной программы, активизации ее информационного сопровождения, а также усиления работы на данном направлении российских загранучреждений и уполномоченных органов субъектов РФ.\r\n\r\n   В результате участия в Фонда к конгрессе Фонд приобрел узнаваемость в среде наших соотечественников, проживающих за рубежом, приобрел связи для дальнейшей работы со СМИ и необходимые контакты для расширения сети представительств Фонда на постсоветском пространстве.', NULL, NULL),
(6, '28.11.2012-30.11.2012', 'II Балтийский форум соотечественников.Соорганизация и активное участие.\r\nПроведение Заседание рабочий группы по вопросам переселения соотечественников. \r\nПроведение Круглого стола «Информационное обеспечение Госпрограммы по оказанию содействия добровольному переселению соотечественников в РФ».', '<p>День 1: Выступления  на форуме с презентацией Фонда «ОРИЕНТИР», участие в обсуждении всех вопросов на общем форуме, участие в пресс-конференции по вопросам переселения.\r\n\r\nДень 2: Проведения дискуссионного стола: Проблемы переселения российских соотечественников, пути решения и экономические возможности. Составлена итоговая резолюция с рекомендациями.  Проведено Заседание рабочий группы по вопросам переселения соотечественников.  Проведен Круглый стол «Информационное обеспечение госпрограммы по оказанию содействия добровольному переселению соотечественников в РФ». Подготовлена итоговая декларация форума.\r\n\r\nБлагодаря участию и выступлению в качестве со-организатора Балтийского форума Фонд получил необходимые навыки и знания по технологиям организации и проведения подобных мероприятий,  приобрел новые ценные контакты для установления партнерский связей с организациями, вовлеченными в процесс оказания содействия переселенцам, а также заявил о себе как об организации активно участвующей в реализации Государственной Программы по оказанию содействия добровольному переселению в РФ соотечественников, проживающих за рубежом.', NULL, NULL),
(7, '31.01.2013', 'Выездное заседание комиссии Общественной палаты Ленобласти по разработке закона о вселении российских соотечественников.\r\n\r\n1.Соорганизация\r\n\r\n2.Проведение видео презентации ', 'Выступления представителей федеральных, областных, местных органов власти, работодателей, представителей общественных организаций, экспертов, посвящённых различным аспектам касающихся переселения российских соотечественников в Ленинградскую область.\r\n\r\n   Проведена видео презентация «Опыт Фонда «ОРИЕНТИР» и других субъектов федерации по оказанию содействия добровольному переселению соотечественников в РФ»\r\n\r\n   Принята итоговая Резолюция (разработка рег.Программы ЛО)', NULL, NULL),
(8, '25.02.2013', 'Участие в общественно-консультативном совете при УФМС России по С-Петербургу и Лен.области.   Участие формировании программы и вопросов для обсуждения.', 'Фонд подтвердил свою готовность тесно сотрудничать с УФМС России по Санкт-Петербургу и Ленинградской области, для приема переселенцев, а также по социализации трудовой миграции и по противодействию незаконной миграции.   http://www.ufms.spb.ru/desc/zasedanie-obschestvenno-konsultativnogo-dind-1235.html   Фонд «ОРИЕНТИР» включен в состав Общественно-консультативного Совета при УФМС России по СПб и ЛО.', NULL, NULL),
(9, '09.2012-10.2014', 'Осуществление на регулярной основе консультирования соотечественников по вопросам переселения.', '<p>Консультации и рекомендации на безвозмездной основе получили свыше 300 соотечественников, в т.ч. 23 молодых предпринимателей в лице переселенцев - соотечественников. Консультирование позволяет выявить общие тенденции и определить интересы соотечественников, как желающих переселятся, так уже переселившихся в Россию. </p>', NULL, NULL),
(10, '01.08.2014- 18.12.2014', '«Социально-экономические и социально-психологические аспекты жизнедеятельности переселенцев - соотечественников в Ленинградской области: современное состояние и перспективы развития»', 'Проведено эмпирическое социологическое исследование, в т.ч.: 1) подготовлена методики и составлены анкеты (опросные листы), соответствует прим. 49 вопросам и проведена Репрезентативная выборка, имеющая такое же распределение относительных характеристик, что и генеральная совокупность; 2) опрощено 252 респондентов в 4х районах Ленобласти; 3) по результатам разработана методика приема семьей соотечественников-переселенцев и психологическое сопровождение переселенческих семьей; 4) разработаны мероприятия (механизмы адаптации), предполагающие дифференциацию переселенцев, прибывших на постоянное место жительства, с учетом гендерных, возрастных, профессиональных и иных социально-демографических особенностей; 5) проведена серия информационных семинаров для переселенцев активного трудоспособного возраста.', NULL, NULL),
(11, '01.12.2014- 15.06.2015', '«Адаптация мигрантов проживающих на территории Ленинградской области, интегрирование их в единое правовое и культурное поле России, развитие традиционных духовных ценностей»', 'Разработан ранжированный перечень основных проблем правовой, социальной и культурной интеграции мигрантов в организациях, использующих труд иностранной рабочей силы;\r\n\r\nПроведена фокус-группа из представителей национальных диаспор Ленинградской области и определены традиционные ценности, удовлетворение которых затруднено в процессе адаптации мигрантов к социально-экономическим условиям Северо-Западного региона России;\r\n\r\nСистематизированы экономические, социальные и правовые проблемы адаптации мигрантов на территории Ленинградской области и определены ключевые направления поддержки процессов миграции;\r\n\r\nРазработана комплексная программа мероприятий по формированию правовой грамотности и культурной интеграции в российское социокультурное пространство для  мигрантов Ленинградской области;\r\n\r\nРазработана программа семинара в формате деловой игры для представителей Советов по межнациональным отношениям муниципальных образований Ленинградской области;\r\n\r\nПроведены информационные семинары для мигрантов в 3-5 районах Ленинградской области (Кингисеппский, Выборгский, Лужский и Всеволожский или Ломоносовский/Гатчинский район) с представителями районных администраций, принимающего сообщества и предстовителями Советов по межнациональным отношениям муниципальных образований Ленинградской области, в том числе апробирован методический материал для проведения информационных семинаров для будущих мигрантов;\r\n\r\nСформирована рабочая группа специалистов-исследователей, обеспечивающих дизайн проекта, обработку его результатов и подготовку отчетности;\r\n\r\nПроект представляет собой социально и экономически значимое мероприятие, направленное на создание благоприятных условий социально-экономической адаптации и психолого-культурологической интеграции необходимых для Ленинградской области трудовых мигрантов.\r\n\r\nРазработаны, подготовлены и изданы Методические материалы по правовым аспектам интеграции трудовых мигрантов для представителей Советов по межнациональным отношениям районных администраций ленинградской области.', NULL, NULL),
(12, '01.09.2015- 15.11.2015', '«Исследование проблем языковой и социокультурной адаптации детей мигрантов в Санкт-Петербурге»', 'Проведен опрос учащихся 7-8 классов в пяти районах Санкт-Петербурга (Адмиралтейский, Василеостровский, Колпинский, Невский, Центральный). Всего опрошено 1986 учеников, из них 326 учащихся из семей мигрантов. Проведены экспертные интервью: 22 специалиста из сферы образования, 18 специалистов органов управления миграционной политикой Санкт-Петербурга. Общее количество граждан, получивших услуги, в т.ч. принявшие участие в мероприятиях проекта превысило 2050 человек, включительно участников круглого стола (специалисты органов управления миграционной политикой Санкт-Петербурга, муниципальных образований, директора школ, завучи по воспитательной работе, педагоги осуществляющие классное руководство, а также представители профильных комитетов города и представители НКО, социолог, психолог, эксперт-педагог), которые ознакомились с презентацией результатов Проекта и обсудили рекомендации по совершенствованию механизма адаптации и интеграции детей мигрантов в социокультурное пространство Санкт-Петербурга.', NULL, NULL),
(13, '11.01.2016- 29.04.2016', '«Исследование проблем языковой и социокультурной адаптации детей-мигрантов в общеобразовательных школах Ленинградской области»', 'В ходе исследования опрошено 2221 школьника 7-8 классов общеобразовательных школ Ленинградской области из них дети мигрантов 537 человек выявлены и систематизированы основные проблемы адаптации и интеграции детей-мигрантов и их родителей в языковое и социокультурное поле Ленинградской области. Разработаны рекомендации для общеобразовательных учреждений, учреждений молодежной политики Ленинградской области, направленные на решение проблемы социальной адаптации детей мигрантов сквозь призму их обучения в школе. На материалах социологического исследования в общеобразовательных школах Ленинградской области проанализированы два основных вида адаптации: языковая и социокультурная. Рекомендации направлены в профильные комитеты области: Комитет общего и профессионального образования Ленинградской области, Комитет по социальной защите населения Ленинградской области, Комитетом по местному самоуправлению, межнациональным и межконфессиональным отношениям Ленинградской области, Комитет по молодежной политике Ленинградской области.', NULL, NULL),
(14, '01.02.2016- 31.08.2016', '«Правовое просвещение и оказание юридической помощи мигрантам-соотечественникам, проживающим и работающим на территории Санкт-Петербурга и Ленинградской области»', 'С целью наиболее продуктивного и эффективного информирования мигрантов (в т. ч. предупреждения нарушений миграционного и трудового законодательства, удаления очагов социальной напряженности на территории Санкт-Петербурга) Фонд «ОPИЕНТИP» организовал и открыл (пр. Pимского-Корсакова, д.39, рядом с отделом по вопросам вынужденных переселенцев и беженцев УФМС по СПб и ЛО) приемную правового консультирования в устной и письменной форме для оказания юридической помощи на безвозмездной основе мигрантам-соотечественникам, проживающим и работающим в Санкт-Петербурге по гражданскому и трудовому праву, миграционному законодательству (в т. ч. в целях предупреждения нарушений миграционного и трудового законодательства, удаления «очагов социальной напряженности» на территории Санкт-Петербурга). Так же, как средство коммуникации и получения необходимой информации, будет задействован еще один формат - создание интернет-сайта, на котором будет представлена информация по решению актуальных проблем мигрантов, организована интернет-приемная с ответами на самые острые вопросы.', NULL, NULL);
INSERT INTO `replies` (`id`, `body`, `dateline`, `messages_id`, `created_at`, `updated_at`) VALUES
(1, '\n<p>\n	<strong>Ответ:</strong> \n	Согласно <a href=\'http://cms/blog/vopros-otvet/\' title=\'Федеральному закону Российской Федерации от 2 мая 2006 г. N 59-ФЗ \"О порядке рассмотрения обращений граждан Российской Федерации\"\'>\n	Федеральному закону Российской Федерации от 2 мая 2006 г. N 59-ФЗ \"О порядке рассмотрения обращений граждан Российской Федерации\"</a> статья 10 п.4: <br />\n	Ответ на обращение, поступившее в государственный орган, орган местного самоуправления или должностному \n	лицу по информационным системам общего пользования, направляется по почтовому адресу, \n	указанному в обращении.\n</p>\n		', 1465644576, 0, NULL, NULL),
(2, '<p>Иностранный гражданин в случае нахождения в месте пребывания обязан встать на учет по месту пребывания.&nbsp;<a href=\"https://guvm.mvd.ru/document/18147\">Процедура постановки на миграционный учет</a> представляет собой информирование (уведомление) территориального органа ФМС России о прибытии иностранного гражданина в место пребывания.&nbsp;</p>', 1467384859, 0, NULL, NULL),
(3, '<p>Порядок действий иностранного гражданина для получения патента на работу в РФ изложен в специально разработанной <a href=\"https://guvm.mvd.ru/document/46130\" target=\"_blank\">памятке</a>.</p>', 1467385119, 0, NULL, NULL),
(4, '<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Для принятия участия в Госпрограмме необходимо:</strong></p>\n<p>1. выбрать субъект Российской Федерации, участвующий в Госпрограмме;</p>\n<p>2. ознакомиться с региональной программой переселения выбранного субъекта Российской Федерации, в т.ч. обратить внимание на возможные дополнительные требования, предъявляемые в данном регионе (например, уровень образования);</p>\n<p>3. выбрать вид деятельности, планируемой к осуществлению на территории упомянутого субъекта;</p>\n<p>4. подать комплект необходимых документов.</p>\n<p><strong>Для получения свидетельства в территориальный орган необходимо подать&nbsp;</strong><strong>следующие документы</strong>:</p>\n<ol>\n<li><strong>Заявление</strong>, составленное на русском языке &ndash; 1 экз.;</li>\n<li>Копии <strong>документов, удостоверяющих личность</strong> заявителя и членов его семьи, включенных в заявление;</li>\n<li>Копии документов, подтверждающих<strong> право</strong> <strong>заявителя на</strong> постоянное или временное <strong>проживание</strong> в российской федерации, либо копии свидетельства о предоставлении временного убежища на территории российской федерации заявителю и (или) членам его семьи, а также копии документов о семейном положении заявителя и членов его семьи с предъявлением оригиналов перечисленных документов заявителя;</li>\n<li>2 <strong>фотографии</strong> заявителя в черно-белом или цветном исполнении размером 35х45 мм с четким изображением лица строго анфас без головного убора;</li>\n<li>Копии документов<strong> об образовании</strong>, о профессиональной подготовке (аттестат, диплом с вкладышем), наличии ученого звания и степени, а также сведения, характеризующие личность заявителя и членов его семьи, включенных в заявление, его профессиональные навыки и умения (если такие имеются);</li>\n<li>Копии документов <strong>о трудовой деятельности</strong>;</li>\n<li>Копия <strong>военного билета.</strong></li>\n</ol>\n<p>&nbsp; &nbsp; &nbsp; При подаче заявления необходимо обязательное наличие оригиналов документов на заявителя и членов семьи.&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; Документы, составленные на иностранном языке, подлежат переводу на русский язык. <strong>Верность перевода и подлинность подписи переводчика должны быть нотариально удостоверены</strong>.</p>\n<p><strong>&nbsp;</strong></p>\n<p><strong>Участники программы должны отвечать следующим требованиям: </strong></p>\n<ol>\n<li>Свободное владение русским языком;</li>\n<li>Трудоспособный возраст (достигшие 18 лет и младше 55 лет - для женщин и 60 лет - для мужчин);</li>\n<li>Отсутствие заболеваний, представляющих опасность для окружающих;</li>\n<li>&nbsp;Отсутствие судимости (неснятой или непогашенной судимости, а также преследования в уголовном порядке компетентными органами Российской Федерации или за ее пределами за совершение умышленных преступлений, признаваемых таковыми в соответствии с федеральным законодательством);</li>\n<li>Наличие квалификации, подтвержденной документально;</li>\n<li>Стаж работы по диплому в течении 1 (одного) года;</li>\n<li>Желание переселиться на постоянное место жительства в РФ с целью:</li>\n</ol>\n<ul>\n<li>осуществления трудовой деятельности в соответствии с действующим законодательством;</li>\n<li>получения среднего профессионального и (или) высшего профессионального, включая послевузовское образования в образовательных учреждениях, расположенных на территории Ленинградской области, занятия научно-исследовательской деятельностью;</li>\n<li>осуществления инвестиционной и предпринимательской деятельности.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Участники подпрограммы также должны соответствовать <strong>не менее чем одному из следующих требований</strong>:</p>\n<ul>\n<li>имеют среднее профессиональное или высшее образование со стажем работы не менее 1 года по востребованным на рынке труда профессиям и специальностям в соответствии с заявленными вакансиями на территории муниципальных образований Ленинградской области;</li>\n<li>имеют среднее профессиональное или высшее образование и осуществляют на территории Ленинградской области трудовую или иную не запрещенную законодательством Российской Федерации деятельность, приносящую доход;</li>\n<li>обучаются на территории Ленинградской области и Санкт-Петербурга в образовательных организациях высшего и среднего профессионального образования по специальностям, востребованным на рынке труда, в соответствии с заявленными вакансиями на территории муниципальных образований Ленинградской области при условии прохождения не менее половины срока обучения, предусмотренного программой обучения.</li>\n</ul>', 1489045148, 0, NULL, NULL),
(5, '<p><strong>Соотечественниками</strong> являются лица, <strong>родившиеся</strong> в одном государстве, проживающие либо <strong>проживавшие</strong> в нем и обладающие признаками общности языка, истории, культурного наследия, традиций и обычаев, <strong>а также потомки указанных лиц по прямой нисходящей линии</strong>.</p>\n<p><strong>Соотечественниками за рубежом</strong> (далее - соотечественники) являются граждане Российской Федерации, постоянно проживающие за пределами территории Российской Федерации.</p>\n<p>Соотечественниками также признаются лица и их потомки, проживающие за пределами территории Российской Федерации и относящиеся, как правило, к <strong>народам, исторически проживающим</strong> на территории Российской Федерации, а также сделавшие свободный выбор в пользу духовной, культурной и правовой связи с Российской Федерацией лица, чьи родственники по прямой восходящей линии ранее проживали на территории Российской Федерации, в том числе:</p>\n<ul>\n<li>лица, состоявшие <strong>в гражданстве СССР</strong>, проживающие в государствах, входивших в состав СССР, получившие гражданство этих государств <strong>или ставшие лицами без гражданства</strong>;</li>\n<li><strong>выходцы </strong><strong>(эмигранты) из Российского государства, Российской республики, РСФСР, СССР и Российской Федерации</strong>, имевшие соответствующую гражданскую принадлежность и ставшие гражданами иностранного государства или лицами без гражданства.</li>\n</ul>\n<p>На постоянное место жительства в Российскую Федерацию <strong>вместе с участником Государственной программы может переселиться также члены его семьи</strong>, к которым относятся:</p>\n<ul>\n<li>супруга (супруг);</li>\n<li>дети, в том числе усыновленные или находящиеся под опекой (попечительством);</li>\n<li>дети супруги (супруга) участника Государственной программы;</li>\n<li>родители участника Государственной программы и его супруги (супруга), родные сестры и братья участника Государственной программы и его супруги (супруга);</li>\n<li>дети родных сестер и братьев участника Государственной программы и его супруги (супруга), в том числе усыновленные или находящиеся под опекой (попечительством), бабушки, дедушки, внуки. Совершеннолетний член семьи участника Государственной программы, за исключением его супруги (супруга), имеет право самостоятельно участвовать в Государственной программе.</li>\n</ul>', 1489045561, 0, NULL, NULL),
(6, '<p><strong><span style=\"text-decoration: underline;\">Территория вселения</span></strong> &ndash; территория субъекта Российской Федерации или ее часть, куда целенаправленно привлекаются участники Государственной программы в рамках реализации проектов переселения.</p>\n<p>&nbsp;</p>\n<p>&nbsp;В <strong>Северо-Западном федеральном округе </strong>в число таких субъектов входят:</p>\n<p>&nbsp;</p>\n<p>Республика Карелия, Архангельская область, Вологодская область, Калининградская область, Ленинградская область, Мурманская область, Новгородская область, Псковская область, Ненецкий автономный округ.</p>\n<p>В <strong>Центральном федеральном округе </strong>&ndash; Брянская область, Воронежская область, Калужская область, Костромская область, Курская область, Липецкая область, Орловская область, Рязанская область, Смоленская область (не все районы), Тамбовская область, Тверская область, Тульская область, Ярославская область.</p>\n<p>В <strong>Южном федеральном округе </strong>&ndash; Астраханская область, Волгоградская область, Ростовская область, Краснодарский край&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (не все районы).</p>\n<p>В <strong>Северо-Кавказском федеральном округе </strong>&ndash; Ставропольский край (не все районы).</p>\n<p>В <strong>Приволжском федеральном округе </strong>&ndash; Республика Марий Эл, Республика Мордовия, Чувашская Республика, Пермский край, Пензенская область (не все районы), Самарская область, Саратовская область, Ульяновская область, Удмуртская Республика.</p>\n<p>В <strong>Уральском федеральном округе </strong>&ndash; Курганская область, Свердловская область, Тюменская область (не все районы, г. Тюмень не входит в территорию заселения), Челябинская область, Ханты-Мансийский автономный округ, Ямало-Ненецкий АО (не все районы).</p>\n<p>В <strong>Сибирском федеральном округе </strong>&ndash; Томская область, Республика Бурятия,&nbsp; Алтайский край, Забайкальский край (только по проектам переселения), Красноярский край (не все районы), Иркутская область, Кемеровская область, Новосибирская область, Омская область.</p>\n<p>В <strong>Дальневосточном федеральном округе </strong>&ndash; Камчатский край, Приморский край, Хабаровский край, Амурская область, Магаданская область, Сахалинская область, Еврейская автономная область.</p>\n<p>&nbsp;</p>\n<p><strong>САНКТ-ПЕТЕРБУРГ</strong> как отдельный субъект Российской Федерации, в реализации Государственной программы <strong>НЕ УЧАСТВУЕТ</strong>.</p>\n<p><strong>В ЛЕНИНГРАДСКОЙ ОБЛАСТИ </strong>участие принимают <strong>ВСЕ</strong> <strong>РАЙОНЫ.</strong> Наиболее перспективные районы вселения - &nbsp;Кингисеппский и Тихвинский.</p>', 1489045706, 0, NULL, NULL),
(7, '<p><strong><span style=\"text-decoration: underline;\">Для лиц, проживающих на территории РФ:</span></strong></p>\n<p>Территориальным органом Министерства внутренних дел Российской Федерации в субъекте Российской Федерации, участвующем в реализации Государственной программы.</p>\n<p>Уполномоченный орган в Ленинградской области &ndash; Управление по вопросам миграции ГУ МВД России по г. Санкт-Петербург и Ленинградской области. Адрес:&nbsp;г. Санкт-Петербург, ул. Римского-Корсакова д.39.</p>\n<p>&nbsp;</p>\n<p><strong><span style=\"text-decoration: underline;\">Для лиц, проживающих за рубежом </span></strong></p>\n<p>Уполномоченными органами за рубежом в государстве постоянного проживания.</p>\n<p>Примечание: уполномоченными органами являются дипломатические представительства Российской Федерации в иностранных государствах (консульские отделы российских посольств за рубежом) и консульские учреждения Российской Федерации в иностранных государствах, при которых могут создаваться и функционировать временные группы по реализации Государственной программы.</p>\n<p><strong>&nbsp;</strong></p>\n<p><strong>Основания для отказа</strong></p>\n<ol>\n<li><strong>Несоответствие требованиям, указанным в разделе &laquo;Как участвовать в Программе содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом&raquo;.</strong></li>\n<li>Отсутствие необходимого образования, опыта и стажа работы для трудоустройства по указанной соотечественником в заявлении профессии (специальности);</li>\n<li>Отсутствие вакансий для соотечественника в базе данных вакансий муниципального образования Ленинградской области - территории вселения, соответствующих его профессии (специальности), профессиональной квалификации, стажу и опыту работы.</li>\n</ol>', 1489045856, 0, NULL, NULL),
(8, '<ol>\n<li><strong>Заявление</strong> о выдаче РВП в <strong>двух</strong> экземплярах;</li>\n<li>2 личные <strong>фотографии</strong> размером 35x45 мм в черно-белом или цветном исполнении с четким изображением лица анфас без головного убора. Лица без гражданства предоставляют 3 фотографии;</li>\n<li><strong>Документ, удостоверяющий личность</strong>;</li>\n<li><strong>Миграционную карту</strong> с отметкой пограничного органа федеральной службы безопасности о въезде данного иностранного гражданина в Российскую Федерацию или с отметкой территориального органа федерального органа исполнительной власти в сфере миграции о выдаче данному иностранному гражданину указанной миграционной карты;</li>\n<li><strong>В течение тридцати суток</strong> со дня подачи заявления о выдаче РВП &ndash; <strong>документы, подтверждающие отсутствие заболевания</strong> наркоманией и инфекционных заболеваний, которые представляют опасность для окружающих, предусмотренных перечнем, а также сертификат об отсутствии у него заболевания, вызываемого вирусом иммунодефицита человека (ВИЧ-инфекции);</li>\n<li>Документ, подтверждающий <strong>владение</strong> данным иностранным гражданином <strong>русским языком, знание истории России и основ законодательства Российской Федерации</strong>. Указанный документ не предоставляется заявителями &ndash; мужчинами, достигшими возраста шестидесяти пяти лет и женщинами, достигшими возраста шестидесяти лет;</li>\n<li>Квитанция об оплате <strong>государственной пошлины 1600 руб.;</strong></li>\n<li><strong>Свидетельство</strong> об участии в Государственной программе.</li>\n</ol>', 1489046031, 0, NULL, NULL),
(9, '<p><strong>Вид на жительство</strong> указанным иностранным гражданам и членам их семей, переселившимся совместно с ними в РФ, выдается <strong>на срок действия свидетельства участника Государственной программы</strong> по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом.</p>\n<p>К иностранным гражданам, прибывшим в Российскую Федерацию в экстренном массовом порядке, признанным беженцами на территории Российской Федерации или получившим временное убежище на территории Российской Федерации и ставшим участниками Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом, а также членам их семей, переселившимся совместно с ними в Российскую Федерацию, не применяются правила пунктов 1-3 статьи 8 Федерального Закона от 25.07.2002 N 115-ФЗ \"О правовом положении иностранных граждан в Российской Федерации\".</p>', 1489046849, 0, NULL, NULL),
(10, '<p><strong>В случае если</strong> <strong>участники</strong> <strong>Государственной программы</strong> по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом, <strong>а также члены их семей</strong></p>\n<p><strong>имеют регистрацию по месту жительства на территории субъекта РФ</strong>, выбранного ими для постоянного проживания в соответствии с указанной Государственной программой,</p>\n<p><strong>они могут быть приняты в гражданство Российской Федерации без соблюдения условий о сроке проживания, наличии законного источника средств к существованию, владении русским языком</strong>.</p>\n<p>&nbsp;<strong>Перечень документов:</strong></p>\n<ol>\n<li>Нотариально удостоверенная <strong>копия паспорта</strong>, содержащего сведения о разрешении на временное проживание на территории РФ;</li>\n<li><strong>Либо вид на жительство</strong> (независимо от срока проживания на территории РФ по виду на жительство);</li>\n<li><strong>Нотариально удостоверенная копия паспорта с переводом</strong>;</li>\n<li><strong>Свидетельство участника</strong> Государственной программы по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом, утвержденной Указом Президента Российской Федерации от 22 июня 2006 г. N 637 \"О мерах по оказанию содействия добровольному переселению в Российскую Федерацию соотечественников, проживающих за рубежом\" (далее &ndash; Государственная программа), либо копию свидетельства участника Государственной программы, если заявитель является членом семьи участника этой программы;</li>\n<li>Нотариально удостоверенная <strong>копия свидетельства о рождении</strong>;</li>\n<li>Нотариально удостоверенная <strong>копия свидетельства о заключении, расторжении брака (при наличии)</strong>;</li>\n<li><strong>Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства</strong> и квитанция о заказном почтовом отправлении;</li>\n<li>Квитанцию об уплате <strong>государственной пошлины 3 500 рублей</strong>;</li>\n<li><strong>Фотографии</strong> 3х4, матовые (4 шт.).</li>\n</ol>\n<p>Заявление о принятии в гражданство РФ подается в территориальный орган МВД России по месту жительства в двух экземплярах.</p>\n<p>Прием заявлений о приеме в гражданство Российской Федерации осуществляется Управлением по вопросам миграции ГУ МВД России по г.Санкт-Петербургу и Ленинградской области по адресу г. Санкт-Петербург, ул. Красного Текстильщика, д. 10-12.</p>\n<p>Примечание: Все документы, выданные в иностранном государстве, должны сопровождаться нотариально заверенным переводом.</p>', 1489046997, 0, NULL, NULL),
(11, '<div>\n<p>За патентом необходимо обратиться заранее. Рекомендуемый срок - не позднее 20 дней с даты въезда в Россию.</p>\n<div>ФГУП &laquo;Паспортно-визовый сервис&raquo; по адресу: г. Санкт-Петербург, ул. Красного Текстильщика, д. 15 - единственное место, где можно получить подлинный патент для работы в Санкт-Петербурге и в Ленинградской области.</div>\n<div>&nbsp;</div>\n<div>Полный перечень документов для оформления патента на работу в Санкт-Петербурге (или в Ленинградской области):</div>\n</div>\n<div><ol>\n<li><strong>Заявление</strong> о выдаче патента;</li>\n<li>Личная цветная <strong>фотография</strong> размером 30 х 40 мм;</li>\n<li>Документ, удостоверяющий личность иностранного гражданина и признаваемый РФ в этом качестве;</li>\n<li>Миграционная карта с указанием работы как цели визита в РФ \"РАБОТА\" и с отметкой пограничного органа ФСБ России о въезде иностранного гражданина в РФ, либо территориального органа МВД России о выдаче иностранному гражданину миграционной карты;</li>\n<li>Действующий договор (полис) медицинского страхования либо договор о предоставлении платных медицинских услуг;</li>\n<li>Документы, подтверждающие отсутствие заболевания наркоманией и выданные по результатам медосмотра, включающего в себя химико-токсикологические исследования наличия в организме человека наркотических средств, психотропных веществ и их метаболитов, и инфекционных заболеваний, которые представляют опасность для окружающих;</li>\n<li>Сертификат об отсутствии заболевания, вызываемого вирусом иммунодефицита человека (ВИЧ-инфекции);</li>\n<li>Один из документов, подтверждающих владение русским языком, знание истории России и основ законодательства РФ (в случаях, предусмотренных статьей 15_1 Закона N 115-ФЗ):</li>\n</ol>\n<ul>\n<li>Сертификат о владении русским языком, знании истории России и основ законодательства РФ</li>\n<li>Документ государственного образца об образовании (на уровне не ниже основного общего образования), выданный образовательным учреждением на территории государства, входившего в состав СССР, до 01.09.91</li>\n<li>Документ об образовании и (или) о квалификации, выданный лицам, успешно прошедшим государственную итоговую аттестацию на территории РФ с 01.09.91</li>\n<li>Документ о прохождении экзамена на владение русским языком, знание истории и основ законодательства РФ</li>\n</ul>\n&nbsp; &nbsp; &nbsp; &nbsp;9. Документы о постановке на учет по месту пребывания (регистрация);</div>\n<div>&nbsp; &nbsp; &nbsp; 10.<strong> При необходимости - &nbsp;</strong>документ, подтверждающий уплату штрафа за нарушение срока обращения за оформлением патента, в случае представления вышеуказанных документов <strong>по истечении 30 календарных дней со дня въезда в РФ.</strong></div>\n<div><strong>&nbsp;</strong></div>\n<div>Все подготовленные по списку документы нужно передать &nbsp;в миграционную службу в срок до 30 дней с даты въезда в Россию, иначе вам придется оплатить штраф в размере от 10000 до 15000 Рублей&nbsp;</div>\n<p>Любые предложения по оформлению патента в более короткие сроки, в более упрощенном порядке - мошенничество!</p>\n<div>&nbsp;</div>', 1489049120, 0, NULL, NULL),
(12, '<p><strong>Патент иностранному гражданину не выдается и не переоформляется в случае, если иностранный гражданин:</strong></p>\n<ul>\n<li>Осуществляет трудовую деятельность с привлечением труда третьих лиц;</li>\n<li>Выступает за насильственное изменение основ конституционного строя Российской Федерации, иными действиями создает угрозу безопасности Российской Федерации или граждан Российской Федерации;</li>\n<li>Финансирует, планирует террористические (экстремистские) акты, оказывает содействие в совершении таких актов или совершает их, а равно иными действиями поддерживает террористическую (экстремистскую) деятельность;</li>\n<li>В течение пяти лет, предшествовавших дню подачи заявления о предоставлении государственной услуги, подвергался административному выдворению за пределы Российской Федерации, депортации или передавался Российской Федерацией иностранному государству в соответствии с международным договором Российской Федерации о реадмиссии либо в течение десяти лет, предшествовавших дню подачи заявления о предоставлении государственной услуги, неоднократно (два и более раза) подвергался административному выдворению за пределы Российской Федерации, депортации или передавался Российской Федерацией иностранному государству в соответствии с международным договором Российской Федерации о реадмиссии;</li>\n<li>Представил поддельные или подложные документы либо сообщил о себе заведомо ложные сведения;</li>\n<li>Осужден вступившим в законную силу приговором суда за совершение преступления;</li>\n<li>Имеет непогашенную или неснятую судимость за совершение преступления на территории Российской Федерации либо за ее пределами, признаваемого таковым в соответствии с федеральным законом;</li>\n<li>Неоднократно (два и более раза) в течение одного года привлекался к административной ответственности за нарушение законодательства Российской Федерации в части обеспечения режима пребывания (проживания) иностранных граждан в Российской Федерации;</li>\n<li>Выехал из Российской Федерации в иностранное государство для постоянного проживания;</li>\n<li>Находится за пределами Российской Федерации более шести месяцев;</li>\n<li>Является больным наркоманией, либо не имеет сертификата об отсутствии у него заболевания, вызываемого вирусом иммунодефицита человека (ВИЧ-инфекции), либо страдает одним из инфекционных заболеваний, которые представляют опасность для окружающих*(21);</li>\n<li>Не достиг возраста восемнадцати лет;</li>\n<li>Не указал в миграционной карте работу как цель визита в Российскую Федерацию.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Также патент иностранному гражданину не выдается и не переоформляется в случае:</p>\n<ul>\n<li>Вынесения решения о неразрешении въезда в Российскую Федерацию по основаниям, предусмотренным статьями 26, 27 Федерального закона от 15 августа 1996 г. № 114-ФЗ \"О порядке выезда из Российской Федерации и въезда в Российскую Федерацию\".</li>\n<li>Представления работодателем или заказчиком работ (услуг) в территориальный орган ФМС России поддельных или подложных документов либо сообщения работодателем или заказчиком работ (услуг) заведомо ложных сведений о себе или об иностранном гражданине.</li>\n<li>Поступления в территориальный орган ФМС России, выдавший патент иностранному гражданину, заявления данного иностранного гражданина об аннулировании выданного ему патента;</li>\n<li>Непредставления в течение двух месяцев со дня выдачи патента копии трудового договора или гражданско-правового договора на выполнение работ (оказание услуг)*(23);</li>\n<li>Отсутствия сведений о работодателе или заказчике работ (услуг), являющихся юридическими лицами или индивидуальными предпринимателями, в едином государственном реестре юридических лиц или едином государственном реестре индивидуальных предпринимателей.</li>\n<li>Если данный иностранный гражданин передавался иностранным государством Российской Федерации в соответствии с международным договором Российской Федерации о реадмиссии.</li>\n<li>В случае отсутствия в территориальном органе ФМС России сведений об осуществлении иностранным гражданином трудовой деятельности в период действия патента.</li>\n</ul>\n<p>&nbsp;</p>\n<p>Источник : Приказ ФМС России (Федеральной миграционной службы) от 15 января 2015 г. №5 &laquo;Об утверждении административного регламента предоставления ФМС государственной услуги по оформлению и выдаче иностранным гражданам патентов&raquo;</p>', 1489053542, 0, NULL, NULL),
(13, '<p><strong>Не позднее 10 рабочих дней со дня принятия заявления:</strong></p>\n<ul>\n<li>Выдача патента или уведомления об отказе в его выдаче;</li>\n<li>Выдача переоформленного патента или уведомления об отказе в его переоформлении;</li>\n<li>Выдача патента для осуществления трудовой деятельности на территории другого субъекта РФ или уведомления об отказе в его выдаче;</li>\n<li>Внесение изменений в сведения, содержащиеся в патенте, или выдача уведомления об отказе во внесении изменений в сведения, содержащиеся в патенте.</li>\n</ul>\n<p>&nbsp;<strong>Не позднее 3 рабочих дней со дня принятия заявления</strong> &ndash; выдача дубликата патента или уведомления об отказе в его выдаче.</p>\n<p>&nbsp;</p>', 1489053754, 0, NULL, NULL),
(14, '<p><strong>В течение 2 месяцев со дня выдачи</strong> патента иностранный гражданин обязан представить лично либо направить заказным почтовым отправлением с уведомлением о вручении в территориальный орган МВД России, выдавший патент, <strong>копию трудового договора или гражданско-правового договора</strong> на выполнение работ (оказание услуг). Неисполнение указанной обязанности влечет <strong>аннулирование патента.</strong></p>\n<p>В свою очередь, <strong>работодатель</strong> или заказчик работ (услуг), привлекающие и использующие для осуществления трудовой деятельности иностранного гражданина, <strong>обязаны уведомлять </strong>территориальный орган МВД России в субъекте РФ, на территории которого иностранный гражданин осуществляет трудовую деятельность, о заключении и прекращении (расторжении) с иностранным гражданином трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) в срок, не превышающий <strong>3 рабочих дней с даты заключения или прекращения</strong> (расторжения) соответствующего договора.</p>\n<p><strong>ВНИМАНИЕ!</strong></p>\n<p><strong>Иностранный гражданин не вправе</strong> осуществлять трудовую деятельность вне пределов субъекта РФ, на территории которого ему выдан патент.</p>\n<p>&nbsp;<strong>Работодатель или заказчик работ (услуг) не вправе</strong> привлекать иностранного гражданина к трудовой деятельности по патенту вне пределов субъекта РФ, на территории которого данному иностранному гражданину выдан.</p>\n<p>&nbsp;Для осуществления трудовой деятельности вне пределов субъекта РФ иностранному гражданину необходимо получить дополнительный патент.</p>\n<p><strong>ВНИМАНИЕ!</strong></p>\n<p><strong>Иностранный гражданин не вправе</strong> осуществлять на территории субъекта РФ трудовую деятельность по профессии (специальности, должности, виду трудовой деятельности), которая не указана в патенте, - в случае принятия высшим должностным лицом субъекта РФ (руководителем высшего исполнительного органа государственной власти субъекта РФ) решения об указании в патенте, выдаваемом на территории субъекта РФ, профессии (специальности, должности, вида трудовой деятельности) иностранного гражданина.</p>', 1489053921, 0, NULL, NULL),
(15, '<p>Патент выдается иностранному гражданину на <strong>срок от 1 до 12 месяцев.</strong></p>\n<p>Срок действия патента может неоднократно продлеваться <strong>на период от одного месяца</strong> (при этом общий срок действия патента с учетом продлений <strong>не может составлять более 12 месяцев </strong>со дня выдачи патента).</p>\n<p>&nbsp;Срок действия патента считается продленным на период, за который уплачен налог на доходы физических лиц в виде фиксированного авансового платежа (в этом случае обращение в территориальные органы МВД России не требуется; в противном случае срок действия патента прекращается со дня, следующего за последним днем периода, за который уплачен налог на доходы физических лиц в виде фиксированного авансового платежа).</p>\n<p>&nbsp;<strong>Не позднее чем за 10 рабочих дней до истечения 12 месяцев </strong>со дня выдачи патента иностранный гражданин вправе обратиться в территориальный орган МВД России, выдавший патент, за переоформлением патента (число обращений за переоформлением патента не может превышать одного раза).</p>\n<p>&nbsp;</p>', 1489054054, 0, NULL, NULL),
(16, '<ul>\n<li>Работодатели или заказчики работ (услуг), являющиеся юридическими лицами или индивидуальными предпринимателями либо частными нотариусами, адвокатами, учредившими адвокатский кабинет, или иными лицами, чья профессиональная деятельность в соответствии с федеральными законами подлежит государственной регистрации и (или) лицензированию;</li>\n</ul>\n<p>&nbsp;<strong>ВНИМАНИЕ!</strong></p>\n<p>Привлекать иностранных граждан вправе только те работодатели или заказчики работ (услуг), которые не имеют неисполненных постановлений о назначении административных наказаний за незаконное привлечение к трудовой деятельности в РФ иностранных граждан на момент заключения с иностранным гражданином трудового договора или гражданско-правового договора на выполнение работ (оказание услуг) (часть 1 статьи 13_3 Закона N 115-ФЗ).</p>\n<ul>\n<li>Работодатели или заказчики работ (услуг), являющиеся гражданами РФ.</li>\n</ul>\n<p>&nbsp;Цель: обеспечение личных, домашних и иных подобных нужд, не связанных с осуществлением работодателем или заказчиком работ (услуг) предпринимательской деятельности.</p>\n<p>&nbsp;<span style=\"text-decoration: underline;\">Привлечение к трудовой деятельности в РФ иностранного гражданина</span> - допуск в какой-либо форме к выполнению работ или оказанию услуг либо иное использование труда иностранного гражданина.</p>', 1489054220, 0, NULL, NULL),
(17, '<p>Иностранному гражданину, прибывшему в Российскую Федерацию в порядке, не требующем получения визы, <strong>РВП выдается с учетом квоты</strong>, утвержденной Правительством Российской Федерации.</p>\n<p><strong>ВАЖНО! </strong></p>\n<p>Временно проживающий в Российской Федерации иностранный гражданин не вправе по собственному желанию избирать место своего проживания вне пределов субъекта Российской Федерации, на территории которого ему разрешено временное проживание.</p>\n<p>&nbsp;<strong>Пакет документов:</strong></p>\n<ol>\n<li><strong>Заявление</strong> о выдаче РВП в двух экземплярах;</li>\n<li>2 личные <strong>фотографии</strong> размером 35x45 мм в черно-белом или цветном исполнении с четким изображением лица анфас без головного убора. Лица без гражданства предоставляют 3 фотографии;</li>\n<li><strong>Документ, удостоверяющий личность</strong>;</li>\n<li><strong>Миграционную карту</strong> с отметкой пограничного органа Федеральной службы безопасности о въезде данного иностранного гражданина в РФ или с отметкой уполномоченного органа власти в сфере миграции о выдаче данному иностранному гражданину указанной миграционной карты;</li>\n<li><strong>Уведомление</strong> о постановке на <strong>миграционный учет</strong>;</li>\n<li><strong>В течение тридцати суток</strong> со дня подачи заявления о выдаче РВП &ndash; документы, подтверждающие <strong>отсутствие заболевания</strong> наркоманией и инфекционных заболеваний, которые представляют опасность для окружающих, предусмотренных перечнем, а также сертификат об отсутствии у него заболевания, вызываемого вирусом иммунодефицита человека (ВИЧ-инфекции);</li>\n<li>Документ, подтверждающий владение данным иностранным гражданином <strong>русским языком, знание истории и основ законодательства</strong> Российской Федерации. Указанный документ не предоставляется заявителями-мужчинами, достигшими возраста шестидесяти пяти лет и женщинами, достигшими возраста шестидесяти лет;</li>\n<li>Квитанция об оплате <strong>государственной пошлины 1600 руб</strong>.</li>\n</ol>\n<p><strong>ВАЖНО! </strong></p>\n<p><strong>Срок</strong> предоставления государственной услуги по заявлению иностранного гражданина, прибывшего в Российскую Федерацию в порядке, не требующем получения визы, составляет <strong>шестьдесят суток со дня принятия заявления</strong>.</p>\n<p><strong>Все документы, выданные в иностранном государстве, должны сопровождаться нотариально заверенным переводом!</strong></p>', 1489055022, 0, NULL, NULL),
(18, '<p>Иностранный гражданин, претендующий на приобретение РВП без учета квоты одновременно с документами, подаваемыми для получения РВП, подает соответствующий документ, а именно:</p>\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td style=\"width: 189px;\" align=\"center\" valign=\"top\">\n<p align=\"left\"><strong>Родившийся на территории</strong> <strong>РСФСР</strong> и состоявший в прошлом в гражданстве СССР или родившийся на территории РФ</p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Свидетельство о рождении, выданное на территории РСФСР или РФ органом записи актов гражданского состояния.</li>\n</ul>\n<p align=\"left\">В случае отсутствия указанного свидетельства документом, подтверждающим рождение на территории РСФСР и состояние в прошлом в гражданстве СССР, является паспорт гражданина СССР образца 1974 года, в который внесены соответствующие записи.</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Признанный <strong>нетрудоспособным, имеющий дееспособных сына или дочь</strong>, состоящих в <strong>гражданстве РФ</strong></p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Свидетельство о рождении и паспорт гражданина РФ,</li>\n<li>Удостоверяющий личность гражданина РФ на территории РФ, сына или дочери</li>\n<li>Документы, подтверждающие нетрудоспособность заявителя (например: справку об инвалидности, выданную учреждением государственной службы медико-социальной экспертизы либо компетентным органом иностранного государства, пенсионное удостоверение).</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Имеющий хотя бы одного <strong>нетрудоспособного родителя</strong>, состоящего в <strong>гражданстве РФ</strong></p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Свидетельство о рождении</li>\n<li>Паспорт гражданина рф нетрудоспособного родителя</li>\n<li>Документы, подтверждающие нетрудоспособность родителя (например, справку об инвалидности, выданную учреждением государственной службы медико-социальной экспертизы, пенсионное удостоверение).</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Состоящий в <strong>браке</strong> с гражданином РФ, имеющим <strong>место жительства</strong> на территории РФ</p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Свидетельство о браке</li>\n<li>Паспорт супруга-гражданина российской федерации</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Осуществивший <strong>инвестиции</strong> в РФ</p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Письмо руководителя организации, привлекшей инвестиции, об осуществлении данным иностранным гражданином инвестиций в размере, установленном Правительством РФ</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Поступивший на военную службу</p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>Контракт о прохождении военной службы.</li>\n</ul>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Имеющий <strong>ребенка</strong>, состоящего в гражданстве РФ</p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>свидетельство о рождении ребенка.</li>\n</ul>\n<p align=\"left\">Наличие гражданства Российской Федерации у ребенка удостоверяется документами, предусмотренными законодательством Российской Федерации.</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"189\">\n<p align=\"left\">Имеющий <strong>сына / дочь&nbsp;</strong><strong>18 лет,</strong> граждан РФ и решением суда, признанных <strong>недееспособными либо ограниченных в дееспособности</strong></p>\n</td>\n<td valign=\"top\" width=\"434\">\n<ul>\n<li>свидетельство о рождении и паспорт гражданина Российской Федерации сына или дочери, а также копию решения суда о признании лица недееспособным или ограниченным в дееспособности.</li>\n</ul>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>ВАЖНО! Срок</strong> -&nbsp; <strong>шестьдесят суток со дня принятия заявления</strong>.</p>', 1489055112, 0, NULL, NULL),
(19, '<p><strong>Вид на жительство (ВНЖ)</strong> &ndash; документ, выданный иностранному гражданину в подтверждение его права на постоянное проживание в Российской Федерации, а также его права на свободный выезд из Российской Федерации и въезд в Российскую Федерацию. Вид на жительство, выданный лицу без гражданства, является одновременно и документом, удостоверяющим его личность на территории РФ.</p>\n<p><strong>Условия получения вида на жительство:</strong></p>\n<ul>\n<li>наличие РВП;</li>\n<li>проживание в Российской Федерации не менее одного года на основании РВП.</li>\n</ul>\n<p><strong>ВАЖНО!</strong> Возможна подача документов <strong>через шесть месяцев после получения РВП</strong>.</p>\n<p>Для получения вида на жительство иностранный гражданин подает в территориальный орган МВД России:</p>\n<ol>\n<li>Заявление о выдаче ВНЖ в 2-х экземплярах. <strong>Важно!</strong> Заявление о выдаче вида на жительство подается иностранным гражданином в уполномоченный орган власти в сфере миграции не позднее, чем за шесть месяцев до истечения срока действия РВП;</li>\n<li>Личные <strong>фотографии</strong>, размером 35x45 мм в черно-белом или цветном исполнении с четким изображением лица анфас без головного убора. Лица без гражданства предоставляют 2 фотографии;</li>\n<li><strong>Документ, удостоверяющий личность</strong>;</li>\n<li>Документ, подтверждающий <strong>наличие законного источника средств к существованию</strong>, позволяющего содержать себя в РФ в пределах прожиточного минимума, или документ, подтверждающий его нетрудоспособность (справку о доходах физического лица, справку с места работы, пенсионное удостоверение, иной документ, подтверждающий получение доходов от не запрещенной законом деятельности или нетрудоспособность);</li>\n<li>Документ, подтверждающий наличие жилого помещения на основаниях, предусмотренных законодательством РФ, если сведения, содержащиеся в нем, не находятся в распоряжении государственных органов и органов местного самоуправления. Документ, подтверждающий наличие жилого помещения, представляется иностранным гражданином, обращающимся с заявлением по истечении 3 лет со дня въезда в РФ;</li>\n<li>Документ, выданный полномочным учреждением здравоохранения РФ, подтверждающий отсутствие заболевания наркоманией и инфекционных заболеваний, которые представляют опасность для окружающих, предусмотренных перечнем инфекционных заболеваний, представляющих опасность для окружающих и являющихся основанием для отказа в выдаче либо аннулирования РВП иностранным гражданам и лицам без гражданства, или вида на жительство, или разрешения на работу в РФ;</li>\n<li>Сертификат об отсутствии заболевания, вызываемого вирусом иммунодефицита человека (вич-инфекции);</li>\n<li>Квитанция об оплате государственной пошлины 3500 руб.;</li>\n<li>Документ, подтверждающий владение данным иностранным гражданином русским языком, знание истории России и основ законодательства РФ. К таким документам относятся: сертификат о владении русским языком, знании истории России и основ законодательства РФ; документ государственного образца об образовании (на уровне не ниже основного общего образования), выданный образовательным учреждением на территории государства, входившего в состав СССР, до 1 сентября 1991 года; документ об образовании и (или) о квалификации, выданный лицам, успешно прошедшим государственную итоговую аттестацию на территории РФ с 1 сентября 1991 г.</li>\n</ol>\n<p><strong>ВАЖНО!</strong> От подтверждения владения русским языком, знания истории России и основ законодательства РФ при подаче заявления о выдаче РВП или вида на жительство освобождаются:</p>\n<ul>\n<li>недееспособные иностранные граждане или иностранные граждане, ограниченные в дееспособности;</li>\n<li>иностранные граждане, не достигшие возраста восемнадцати лет; мужчины, достигшие возраста шестидесяти пяти лет; женщины, достигшие возраста шестидесяти лет; иностранные граждане, являющиеся гражданами Союзного государства, образованного Российской Федерацией и Республикой Беларусь.</li>\n</ul>\n<p>Общий срок предоставления государственной услуги по выдаче вида на жительство составляет 6 месяцев со дня принятия заявления.</p>\n<p>&nbsp;Прием заявлений об оформлении разрешения на временное проживание и вида на жительство в Российской Федерации осуществляется<strong> Управлением по вопросам миграции ГУ МВД России по г. Санкт-Петербургу и Ленинградской области по адресу г. Санкт-Петербург, ул. Красного Текстильщика, д. 10-12.</strong></p>', 1489055372, 0, NULL, NULL),
(20, '<p><strong>Необходимый срок проживания</strong> в РФ для приобретения гражданства исчисляется со дня получения вида на жительство и до дня обращения с заявлениями о приеме в гражданство Российской Федерации и составляет <strong>пять лет</strong>(непрерывно).</p>\n<p><em>Прием заявлений о приеме в гражданство Российской Федерации в общем порядке осуществляется Управлением по вопросам миграции ГУ МВД России по г. Санкт-Петербургу и Ленинградской области по адресу г. Санкт-Петербург, ул. Красного Текстильщика, д. 10-12.</em></p>\n<p>Приобретение гражданства РФ в общем порядке в соответствии со следующими условиями:</p>\n<p><strong>Часть 1 статьи 13 (после 5 лет проживания на территории РФ по виду на жительство. Срок проживания считается непрерывным, если лицо выезжало за пределы РФ на срок не более 3 месяцев в течение 1 года)</strong></p>\n<p>1. Вид на жительство.</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о рождении (либо справка об отсутствии в государстве прежнего проживания такового документа).</p>\n<p>4. Нотариально удостоверенная копия свидетельства о заключении брака (при наличии брака), расторжении брака (при наличии).</p>\n<p>5. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства (в) и квитанция о заказном почтовом отправлении (не требуется для граждан Таджикистана).</p>\n<p>6. Документ о наличии законного источника средств к существованию (справка формы 2НДФЛ или 3 НДФЛ, или сведения о доходах лица, на иждивении которого находится).</p>\n<p>7. Документ, подтверждающий владение русским языком (от предоставления данного документа освобождаются мужчины, достигшие возраста 65 лет, женщины, достигшие возраста 60 лет).</p>\n<p>8. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p>9. Нотариально удостоверенная копия ИНН.</p>\n<p>10. Копия трудовой книжки.</p>\n<p>11. Подробная автобиография.</p>\n<p><strong>Пункт В части 2 статьи 13 (наличие статуса беженца)</strong></p>\n<p>1. Удостоверение беженца и отрывная часть к бланку уведомления о прибытии иностранного гражданина в место пребывания.</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о рождении (либо справка об отсутствии в государстве прежнего проживания такового документа).</p>\n<p>4. Нотариально удостоверенная копия свидетельства о заключении брака (при наличии брака), расторжении брака (при наличии).</p>\n<p>5. Документ о наличии законного источника средств к существованию (справка формы 2НДФЛ или 3НДФЛ, или сведения о доходах лица, на иждивении которого находится).</p>\n<p>6. Документ, подтверждающий владение русским языком (от предоставления данного документа освобождаются мужчины, достигшие возраста 65 лет, женщины, достигшие возраста 60 лет).</p>\n<p>7. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p>8. Копия ИНН (нотариально удостоверенная).</p>\n<p>9. Копия трудовой книжки.</p>\n<p>10. Подробная автобиография.</p>', 1489055509, 0, NULL, NULL);
INSERT INTO `replies` (`id`, `body`, `dateline`, `messages_id`, `created_at`, `updated_at`) VALUES
(21, '<p>Иностранные граждане и лица без гражданства, достигшие возраста 18 лет и обладающие дееспособностью, вправе обратиться с заявлениями о приеме в <strong>гражданство РФ в упрощенном порядке</strong> без соблюдения условий о 5 летнем сроке проживания в РФ, если указанные лица:</p>\n<p><strong>I. Имеют хотя бы одного родителя, имеющего гражданство РФ и проживающего на территории РФ.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Вид на жительство (независимо от срока проживания на территории РФ по виду на жительство).</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о рождении.</p>\n<p>4. Нотариально удостоверенная копия свидетельства о заключении, расторжении брака (при наличии).</p>\n<p>5. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства и квитанция о заказном почтовом отправлении.</p>\n<p>6. Документ о наличии законного источника средств к существованию (справка формы 2НДФЛ или 3НДФЛ, или сведения о доходах лица, на иждивении которого находится).</p>\n<p>7. Документ, подтверждающий владение русским языком (аттестат, диплом, сертификат о прохождении тестирования).</p>\n<p>8. Копия паспорта родителя-гражданина РФ, проживающего на территории РФ.</p>\n<p>9. Фотографии 3х4, черно-белые (4 шт.)</p>\n<p>10. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p><strong>II. Имели гражданство СССР, проживали и проживают в государствах, входивших в состав СССР, и не получили гражданства этих государств и остаются в результате этого лицами без гражданства (кроме утративших гражданство государств СНГ.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Вид на жительство лица без гражданства (независимо от срока проживания на территории РФ по виду на жительство).</p>\n<p>2. Нотариально удостоверенная копия свидетельства о рождении (или иной документ, установленный п.14 Положения о порядке рассмотрения вопросов гражданства, подтверждающий состояние в гражданстве бывшего СССР).</p>\n<p>3. Свидетельство о заключении, расторжении брака (при наличии).</p>\n<p>4. Документ о наличии законного источника средств к существованию.</p>\n<p>5. Документ, подтверждающий владение русским языком. Фотографии 3х4, черно-белые (4 шт.)</p>\n<p><strong>III. Родились на территории РСФСР и имели гражданство бывшего СССР.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Вид на жительство (независимо от срока проживания на территории РФ по виду на жительство).</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о рождении.</p>\n<p>4. Нотариально удостоверенная копия свидетельства о заключении, расторжении брака (при наличии).</p>\n<p>5. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства и квитанция о заказном почтовом отправлении.</p>\n<p>6. Документ, подтверждающий владение русским языком.</p>\n<p>7. Документ о наличии законного источника средств к существованию.</p>\n<p>8. Фотографии 3х4, черно-белые (4 шт.)</p>\n<p>9. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p><strong>IV. Состоят в браке с гражданами РФ не менее 3-х лет.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Вид на жительство (независимо от срока проживания на территории РФ по виду на жительство).</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о заключении, расторжении брака.</p>\n<p>4. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства и квитанция о заказном почтовом отправлении.</p>\n<p>5. Документ о наличии законного источника средств к существованию.</p>\n<p>6. Документ, подтверждающий владение русским языком.</p>\n<p>7. Свидетельство о заключении брака.</p>\n<p>8. Паспорт супруга &ndash; гражданина РФ.</p>\n<p>9. Фотографии 3х4, черно-белые (4 шт.)</p>\n<p>10. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p>&nbsp;<strong>V.&nbsp;Являются нетрудоспособными и имеют дееспособных сына или дочь, достигших возраста 18 лет и являющихся гражданами РФ.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Вид на жительство.</p>\n<p>2. Нотариально удостоверенная копия паспорта с переводом.</p>\n<p>3. Нотариально удостоверенная копия свидетельства о рождении.</p>\n<p>4. Нотариально удостоверенная копия свидетельства о заключении, расторжении брака (при наличии).</p>\n<p>5. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства, и квитанция о заказном почтовом отправлении.</p>\n<p>6. Документ о наличии законного источника средств к существованию.</p>\n<p>7. Документ, подтверждающий владение русским языком.</p>\n<p>8. Нотариально удостоверенная копия свидетельства о рождении дееспособного сына или дочери, являющихся гражданином РФ.</p>\n<p>9. Нотариально удостоверенная копия свидетельства о браке, расторжении брака (при наличии), подтверждающее перемену фамилии дочери-гражданки РФ.</p>\n<p>10. Копия российского паспорта сына или дочери &ndash; граждан РФ.</p>\n<p>11. Документ, подтверждающий нетрудоспособность родителя (пенсионное удостоверение, справка об инвалидности).</p>\n<p>12. Фотографии 3х4, черно-белые (4 шт.)</p>\n<p>13. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p>&nbsp;<strong>VI.</strong>&nbsp;<strong>Получили после 1 июля 2002 года профессиональное образование по основным профессиональным образовательным программам, имеющим государственную аккредитацию, в образовательных или научных организациях РФ на ее территории и осуществляют трудовую деятельность в РФ в совокупности не менее трех лет до дня обращения с заявлением о приеме в гражданство РФ.</strong></p>\n<p><strong>VII. Нетрудоспособные иностранные граждане и лица без гражданства, прибывшие в Российскую Федерацию из государств, входивших в состав СССР, и зарегистрированные по месту жительства в Российской Федерации по состоянию на 01.07.2002 года, вправе обратиться с заявлениями о приёме в гражданство Российской Федерации в упрощенном порядке без соблюдения условий о пятилетнем сроке проживания, и без предоставления вида на жительство.</strong></p>\n<p><strong>Необходимые документы:</strong></p>\n<p>1. Нотариально удостоверенная копия национального паспорта и свидетельство о регистрации по месту жительства на территории РФ, в том числе на 01.07.2002, либо вид на жительство с регистрацией в РФ по состоянию на 01.07.2002.</p>\n<p>2. Нотариально удостоверенная копия свидетельства о заключении, расторжении брака (при наличии).</p>\n<p>3. Документ, подтверждающий обращение с заявлением об отказе от имеющегося гражданства, и квитанция о заказном почтовом отправлении.</p>\n<p>4. Документ, подтверждающий владение русским языком (от предоставления данного документа освобождаются мужчины, достигшие возраста 65 лет, женщины, достигшие возраста 60 лет).</p>\n<p>5. Документ, подтверждающий нетрудоспособность заявителя (пенсионное удостоверение, справка об инвалидности).</p>\n<p>6. Квитанция об оплате государственной пошлины 3500 руб.</p>\n<p>7. Фотографии 3х4, черно-белые (4 шт.).</p>', 1489055947, 0, NULL, NULL),
(22, '<p>Заявление подается по месту жительства (регистрации) родителя/опекуна либо по месту жительства (регистрации) ребенка/опекаемого.</p>\n<p><strong>1.</strong>&nbsp;<strong>Ребенок, один из родителей которого имеет гражданство Российской Федерации &ndash; по заявлению этого родителя и при наличии согласия другого родителя. </strong>Такое согласие не требуется, если ребенок проживает на территории России (по разрешению на временное проживание или виду на жительство).</p>\n<p>&nbsp; <strong>Необходимый пакет документов:</strong></p>\n<ol>\n<li>Свидетельство о рождении ребенка, а также национальный паспорт ребенка (при наличии).</li>\n<li>ВНЖ или РВП, или миграционная карта и отрывная часть бланка уведомления о прибытии иностранного гражданина или лица без гражданства в место пребывания, или справка о регистрации на территории иностранного государства, или выписка из домовой книги или копия поквартирной карточки, копия финансового лицевого счета.</li>\n<li>Паспорт заявителя-гражданина РФ.</li>\n<li>Паспорт второго родителя.</li>\n<li>Свидетельство о регистрации брака или свидетельство об установлении отцовства.</li>\n<li>Фотографии 3х4, черно-белые (3 шт.).</li>\n<li>Нотариально заверенное согласие о приеме в гражданство (для детей в возрасте от 14 до 18 лет).</li>\n<li>Квитанция об оплате государственной пошлины 3500 руб. (на каждого ребенка)</li>\n</ol>\n<p>&nbsp;<strong>2. Ребенок, единственный родитель которого имеет гражданство Российской Федерации &ndash; по заявлению этого родителя).</strong></p>\n<p>Кроме документов, указанных выше представляются:</p>\n<ol>\n<li>Документ, подтверждающий, что заявитель является единственным родителем.</li>\n<li>Справка Ф-25 (единственный родитель).</li>\n<li>Решение суда о признании второго родителя безвестно отсутствующим.</li>\n<li>Свидетельство о смерти второго родителя.</li>\n<li>Решение суда о лишении родительских прав второго родителя.</li>\n</ol>\n<p>&nbsp;<strong>3. Ребенок или недееспособное лицо, над которым установлены опека или попечительство &ndash; по заявлению опекуна или попечителя, имеющих гражданство РФ.</strong></p>\n<p>Кроме документов, указанных выше представляются:</p>\n<ol>\n<li>Свидетельство об установлении опеки или попечительства.</li>\n<li>Государственная пошлина НЕ ВЗИМАЕТСЯ.</li>\n</ol>\n<p>&nbsp;<strong>4. Гражданство детей и недееспособных лиц, над которыми установлена опека или попечительство.</strong></p>\n<ol>\n<li>Свидетельство о рождении ребенка, а также национальный паспорт ребенка (при наличии).</li>\n<li>Вид на жительство или иной документ, подтверждающий проживание ребенка на территории РФ.</li>\n<li>Паспорт гражданина РФ &ndash; заявителя-руководителя государственного учреждения.</li>\n<li>Свидетельство об установлении опеки или попечительства (или решении суда) (в отношении недееспособного лица также предоставляется решение суда о признании лица недееспособным).</li>\n<li>Нотариально заверенное согласие несовершеннолетнего на прием в гражданство Российской Федерации (для детей в возрасте от 14 до 18 лет).</li>\n</ol>\n<p>&nbsp; &nbsp; &nbsp; Рассмотрение заявлений по вопросам гражданства и принятие решений о приеме и выходе из гражданства РФ в упрощенном порядке осуществляется <strong>в срок до 6 месяцев</strong> со дня подачи всех необходимых документов.</p>\n<p>&nbsp; &nbsp; &nbsp; После состоявшегося решения о приеме в гражданство РФ заявитель в <strong>месячный срок информируется</strong> о принятом решении.</p>\n<p>&nbsp; &nbsp; &nbsp; Для получения паспорта гражданина РФ, отметки в свидетельство о рождении, подтверждающей наличие гражданства РФ у несовершеннолетних детей, не достигших <strong>14-летнего возраста</strong>, заявителю <strong>необходимо обратиться</strong> в территориальный орган МВД по месту регистрации.</p>', 1489056424, 0, NULL, NULL),
(23, '<p><strong>Приобретение гражданства РФ в соответствии с соглашением между Республикой Беларусь, Республикой Казахстан, Кыргызской Республикой и Российской Федерацией от 26.02.99 \"Об упрощенном порядке приобретения гражданства\".</strong></p>\n<p><strong>&nbsp;</strong><strong>Условия приобретения гражданства в упрощенном порядке:</strong></p>\n<p><strong>&nbsp;</strong>А. Если заявитель состоял в гражданстве Белорусской ССР, Казахской ССР, Киргизской ССР или РСФСР и одновременно в гражданстве бывшего СССР, родился или проживал на территории Стороны приобретаемого гражданства до 21 декабря 1991 года;</p>\n<p>&nbsp;Б. При наличии у заявителя одного из постоянно проживающих на территории Стороны приобретаемого гражданства и являющихся ее гражданами близких родственников: супруга (супруги), одного из родителей (усыновителей), ребенка (в том числе усыновленного), сестры, брата, деда или бабушки, внука или внучки.</p>\n<p>&nbsp;<strong>Постоянно проживающий в России близкий</strong> <strong>родственник</strong> &ndash; близкий родственник, являющийся гражданином России и имеющий регистрацию по месту жительства (постоянную регистрацию) на территории страны по внутреннему паспорту гражданина РФ или свидетельству о рождении (во втором случае - для граждан России, не достигших 14-летнего возраста).</p>\n<p>&nbsp;<strong>ВАЖНО !</strong></p>\n<p>&nbsp;Лицам, желающим приобрести гражданство в силу Соглашения, необходимо оформить разрешение на временное проживание и вид на жительство (кроме граждан Республики Беларусь, которым необходимо оформление лишь вида на жительство). Срок постоянного проживания заявителя в России при получении гражданства по соглашению не имеет значения.</p>\n<p>Порядок оформления выезда на постоянное место жительства в Россию, требования, которые следует выполнить, условия, необходимые для этого устанавливаются национальным законодательством государства, гражданской принадлежности заявителя. С точки зрения российского законодательства о гражданстве главным является наличие у заявителя документа, подтверждающего оформление указанного выезда. Именно поэтому, при оформлении выезда на постоянное место жительства в Россию, иностранному гражданину следует руководствоваться положениями национального законодательства и неукоснительно их выполнить. Ведь соответствующие требования к своим гражданам могут иметь определенные особенности и меняться.</p>\n<p>&nbsp;<strong>Пакет документов:</strong></p>\n<ol>\n<li>Нотариально удостоверенная копия национального паспорта гражданина со штампом о выезде на постоянное жительство в РФ;</li>\n<li>Вид на жительство в Российской Федерации;</li>\n<li>Нотариально удостоверенная копия свидетельства о рождении;</li>\n<li>Нотариально удостоверенная копия свидетельства о заключении, расторжении брака (при наличии);</li>\n<li>Для граждан, приобретающих гражданство РФ в соответствии с пунктом А &ndash; в случае, проживания на территории РСФСР до 21.12.1991 года, представляются документы, подтверждающие проживание;</li>\n<li>Для граждан, приобретающих гражданство РФ в соответствии с пунктом Б &ndash; представляется паспорт близкого родственника &ndash; гражданина РФ: супруга (супруги), одного из родителей (усыновителей), ребенка (в том числе усыновленного), сестры, брата, деда или бабушки, внука или внучки), проживающего на территории РФ (имеющего регистрацию места жительства) и документы, подтверждающие родственные отношения;</li>\n<li>Квитанция об оплате государственной пошлины в размере 3500 рублей;</li>\n<li>4 фото ч/б 3х4 см.</li>\n</ol>\n<p>Заявление составляется на русском языке. Все представляемые вместе с заявлением документы, выполненные не на русском языке, подлежат переводу на русский язык.</p>\n<p>Верность перевода либо подлинность подписи переводчика должна быть удостоверена нотариальной записью.</p>\n<p>При наличии несовершеннолетних детей, гражданство которых изменяется одновременно с одним из родителей, к материалам приобщаются нотариально удостоверенные копии свидетельств о рождении детей, копия паспорта второго родителя, нотариально удостоверенное согласие ребенка в возрасте от 14 до 18 лет на приобретение гражданства Российской Федерации. В случае если ребенок состоит на миграционном учете, предоставляется нотариально удостоверенное согласие второго родителя &ndash; не гражданина РФ, на изменение гражданства несовершеннолетнему.</p>\n<div>После состоявшегося решения о приеме в гражданство Российской Федерации заявитель в четырнадцатидневный срок информируется о принятом решении.</div>', 1489057552, 0, NULL, NULL),
(24, '<p style=\"text-align: justify;\">При огромном разнообразии вакансий найти подходящую работу не так-то просто. Это знают все. Но то, что найти квалифицированного специалиста с подходящими личностными качествами не менее сложно, на своем опыте знают только сотрудники отдела кадров (или те, кто непосредственно сталкивался с подбором персонала).</p>\n<p style=\"text-align: justify;\">Найти специалиста из числа многочисленных граждан нашей родины удается не всегда. Например, &laquo;топовые&raquo; специалисты часто оказываются иностранцами. И если для компании это плюс, то для конкретного специалиста по кадрам может оказаться проблемой. Особенно если ранее он не сталкивался с оформлением на работу иностранцев.</p>\n<p style=\"text-align: justify;\">С российскими гражданами все относительно просто &mdash; все требования изложены в Трудовом кодексе РФ (стандартный пакет документов, трудовой договор, приказ о приеме, заполнение трудовой книжки, карточки Т-2), а вот с иностранцами есть свои особенности, разобраться в которых порой нелегко.</p>\n<p style=\"text-align: justify;\">Порядок оформления таких работников зависит, в частности, от того, визовый ли режим у России со страной работника или безвизовый.</p>\n<p style=\"text-align: justify;\">Если работник прибыл в порядке, требующем получения визы, то при трудоустройстве он должен предъявить работодателю разрешение на работу, а также документ, подтверждающий правомерность его нахождения на территории нашей страны (вид на жительство, разрешение на временное проживание или рабочую визу, миграционную карту). Если со страной безвизовый режим, то надо предъявить патент и опять же документ, подтверждающий правомерность нахождения работника на территории нашей страны (вид на жительство, разрешение на временное проживание или миграционную карту).</p>\n<p style=\"text-align: justify;\">От работодателя для найма иностранного работника требуется наличие разрешения на привлечение и использование иностранных работников (для &laquo;визовых&raquo;), а также приглашение на въезд в Российскую Федерацию (для оформления визы).</p>\n<p style=\"text-align: justify;\">От иностранца также стоит получить:</p>\n<p style=\"text-align: justify;\">&mdash; полис (договор) добровольного медицинского страхования (ДМС) (иностранец обязан предъявлять его при въезде в РФ), причем соответствующий договор также может заключить работодатель в пользу работника;</p>\n<p style=\"text-align: justify;\">&mdash; все документы, предусмотренные 65 статьей Трудового кодекса РФ, т.е. те, которые должен предъявить гражданин России при трудоустройстве.</p>\n<p style=\"text-align: justify;\">Итак, все документы оформлены и запрошены, можно приступать к заключению трудового договора. В нем, помимо привычных условий, необходимо также указать сведения о документе, на основании которого работник может трудиться в РФ, а если договор заключается с временно пребывающим иностранцем, то еще и реквизиты полиса ДМС или договора о медицинских услугах.</p>\n<p style=\"text-align: justify;\">Трудовой договор заключен, все необходимые кадровые документы оформлены (при заключении трудового договора с иностранцами и оформлении их документов руководствуемся общими нормами &mdash; трудовой договор, приказ, трудовая книжка, карточка Т-2).</p>\n<p style=\"text-align: justify;\">Но если с российскими гражданами уже можно было бы расслабиться и приступить к другим своим трудовым обязанностям, то с иностранцами еще придется немного поработать.</p>\n<p style=\"text-align: justify;\">В частности, необходимо поставить работника на учет. Успеть это сделать нужно в течение семи рабочих дней со дня его прибытия.</p>\n<p style=\"text-align: justify;\">После заключения трудового договора также придется уведомлять об этом территориальный орган в сфере миграции. На это есть всего 3 дня (с момента заключения трудового договора).</p>\n<p style=\"text-align: justify;\">Указанное выше &mdash; порядок для общих случаев. А если иностранец &mdash; ВКС (высококвалифицированный специалист), или студент, или направлен к вам в филиал из главного офиса иностранной компании и т.д., появляются дополнительные особенности, в которых можно довольно долго разбираться.</p>\n<p style=\"text-align: justify;\">Кроме того, не стоит забывать, что для некоторых стран правила российского законодательства (о порядке въезда, правовом положении иностранных граждан в РФ) могут быть отменены в силу различных международных соглашений. Поэтому, принимая на работу иностранца, первым делом стоит поискать соглашения между данной страной и РФ.</p>\n<p style=\"text-align: justify;\">Отдельно о &laquo;друзьях&raquo;.</p>\n<p style=\"text-align: justify;\">Если перед вами гражданин бывшей &laquo;союзной&raquo; республики, ситуация упрощается. Особенно это касается граждан Республики Беларусь, Казахстана, Армении, Кыргызской Республики. С данными странами у России особые отношения в силу заключенного договора о Евразийском экономическом союзе (подписан в г. Астане 29.05.2014).</p>\n<p style=\"text-align: justify;\">Например, на постановку на миграционный учет у работодателя в отношении таких лиц увеличенные сроки &mdash; 30 дней (а не 7). Но вот сообщить в трехдневный срок о заключении трудового договора придется в отношении любого иностранца.</p>\n<p style=\"text-align: justify;\">Фактически граждане Республики Беларусь приравнены в трудовых правах к гражданам России: им не нужны разрешительные документы для работы в РФ, стаж, полученный в Республике Беларусь, признается в РФ, они имеют равные права на медицинское обеспечение (договор между РФ и Республикой Беларусь от 25.12.1998 &laquo;О равных правах граждан&raquo;). С них даже не стоит требовать миграционную карту &mdash; она им не выдается.</p>\n<p style=\"text-align: justify;\">Работодатели очень часто нарушают порядок действий при оформлении иностранных работников, забывают уведомлять соответствующие органы или не уведомляют по незнанию. Также работодатели при приеме на работу иностранцев часто невнимательно относятся к следующим моментам.</p>\n<p style=\"text-align: justify;\">1) Не обращают внимания на субъект, в котором работнику разрешено осуществлять трудовую деятельность. Временно пребывающие и временно проживающие на территории РФ иностранные граждане могут трудиться только на территории того субъекта, который указан в их патенте или разрешении на работу (или субъекта, на территории которого разрешено временное проживание).</p>\n<p style=\"text-align: justify;\">2) Не обращают внимания на профессию, специальность, должность, по которой работнику разрешено работать. Временно пребывающие на территории РФ иностранные граждане могут трудиться только по профессии (специальности, должности, виду трудовой деятельности), указанной в разрешении на работу.</p>\n<p style=\"text-align: justify;\">Между тем ответственность за ошибки в работе с иностранцами довольно серьезная. Например, за найм без наличия у работодателя разрешения на привлечение к трудовой деятельности в Российской Федерации иностранного гражданина или лица без гражданства ответственность предусмотрена статьей 18.15 Кодекса РФ об административных правонарушениях. Это влечет за собой наложение административного штрафа на граждан в размере от двух тысяч до пяти тысяч рублей; на должностных лиц &mdash; от двадцати пяти тысяч до пятидесяти тысяч рублей; на юридических лиц &mdash;&nbsp;<strong>от двухсот пятидесяти тысяч до восьмисот тысяч рублей</strong>&nbsp;либо административное приостановление деятельности на срок от четырнадцати до девяноста суток.</p>\n<p style=\"text-align: justify;\">&nbsp;</p>\n<p style=\"text-align: justify;\">По материалам сайта&nbsp;https://hr.superjob.ru</p>', 1490282705, 0, NULL, NULL),
(25, '<p>Прежде всего, необходимо уяснить: согласно законодательствую Российской федерации, приехавшие из Украины не имеют ровным счетом никаких преимуществ по сравнению с мигрантами из других стран, за исключением Белоруссии, которая входит в СГРБ.</p>\n<p>Итак, приехав из Украины, заполнив миграционную карту на границе, вы должны встать на миграционный учет в местном управлении МВД, на почте или в МФО, т.е у вас должна быть принимающая сторона, которая придет с вами в МВД\\почтовое отделение или МФО со своим паспортом, заполнит уведомление о прибытии иностранного гражданина и поставить вас на учет на свой адрес проживания. Хорошо если у вас есть те, кто могут вас принять, а если нет? В таком случае есть организации, которые так же могут быть принимающей стороной, или придется кому-то платить и регистрироваться за деньги, встает вопрос о легальности данного способа. Временно пребывать на территории РФ можно не более 90 суток в полгода, при этом нельзя работать, для этого нужно получить разрешение на работу или документы, разрешающие работать.</p>\n<p>Дальше, есть несколько способов легализации, которые на первый взгляд кажутся простыми:</p>\n<h2>1. Получить временное убежище\\статус беженца.</h2>\n<h3>&nbsp;<a href=\"https://мвд.рф/Deljatelnost/emvd/guvm/%D0%BF%D1%80%D0%B8%D0%B7%D0%BD%D0%B0%D0%BD%D0%B8%D0%B5-%D0%B1%D0%B5%D0%B6%D0%B5%D0%BD%D1%86%D0%B5%D0%BC\" target=\"_blank\">На сайте МВД</a></h3>\n<div>На практике это выглядит так: если человек получает его, украинский паспорт остается у местного отдела МВД, а взамен выдают свидетельство о предоставлении временного убежища, с которым можно работать, но нельзя покидать территорию РФ. Статус беженца получить очень сложно, в связи с льготами, социальными гарантиями и выплатами, которые полагаются носителями данного статуса.&nbsp;</div>\n<div>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://s019.radikal.ru/i601/1703/84/56f3a5235552.jpg\" alt=\"Свидетельство о предоставлении временного убежища в РФ\" width=\"226\" height=\"269\" /></p>\n<p><strong>Дальнейшие действия: </strong>стать участником программы содействия добровольному переселению соотечественников и впоследствии получить разрешение на временное проживание (РВП).</p>\n<p><strong>Трудности: </strong>данный статус дается даже не всем жителям Донецкой и Луганской областей. Так, лицам, выехавшим во время действия первого &laquo;Минского&raquo; перемирия, его не предоставляют. Что уж говорить о выходцах из &laquo;мирных&raquo; областей Украины.</p>\n<h2>2.&nbsp; Получить РВП (разрешение на временное проживание).</h2>\n<h3><a href=\"https://xn--b1aew.xn--p1ai/Deljatelnost/emvd/guvm/%D0%B2%D1%8B%D0%B4%D0%B0%D1%87%D0%B0-%D1%80%D0%B0%D0%B7%D1%80%D0%B5%D1%88%D0%B5%D0%BD%D0%B8%D1%8F-%D0%BD%D0%B0-%D0%B2%D1%80%D0%B5%D0%BC%D0%B5%D0%BD%D0%BD%D0%BE%D0%B5-%D0%BF%D1%80%D0%BE%D0%B6%D0%B8%D0%B2%D0%B0%D0%BD%D0%B8\" target=\"_blank\">На сайте МВД</a></h3>\n<p>Это необходимый шаг для получения гражданства в РФ. Главное препятствие &mdash; это квота: на каждый регион выделяется определенное количество квот на РВП в год и если они исчерпаются, можно ехать обратно на родину. Несложно догадаться, что квоты в Санкт-Петербурге и Москве исчерпываются в начале года.</p>\n<p>Квота не нужна, если:</p>\n<p><em>Иностранный гражданин, которому разрешение на временное проживание может быть выдано без учета квоты, одновременно с документами, подаваемыми для получения разрешения, подает соответствующий документ, подтверждающий возможность&nbsp; получения разрешения без учета квоты</em></p>\n<p><em>а) Родившийся на территории РСФСР и состоявший в прошлом в гражданстве СССР или родившийся на территории Российской Федерации - свидетельство о рождении, выданное на территории РСФСР или Российской Федерации органом записи актов гражданского состояния. В случае отсутствия указанного свидетельства документом, подтверждающим рождение на территории РСФСР и состояние в прошлом в гражданстве СССР, является паспорт гражданина СССР образца 1974 года, в который внесены соответствующие записи.</em></p>\n<p><em>б) Признанный нетрудоспособным, имеющий дееспособных сына или дочь, состоящих в гражданстве Российской Федерации, - свидетельство о рождении и паспорт гражданина Российской Федерации, удостоверяющий личность гражданина Российской Федерации на территории Российской Федерации, сына или дочери, а также документы, подтверждающие нетрудоспособность заявителя (например: справку об инвалидности, выданную учреждением государственной службы медико-социальной экспертизы либо компетентным органом иностранного государства, пенсионное удостоверение).</em></p>\n<p><em>в) Имеющий хотя бы одного нетрудоспособного родителя, состоящего в гражданстве Российской Федерации, - свидетельство о рождении и паспорт гражданина Российской Федерации нетрудоспособного родителя, а также документы, подтверждающие нетрудоспособность родителя (например: справку об инвалидности, выданную учреждением государственной службы медико-социальной экспертизы, пенсионное удостоверение).</em></p>\n<p><em>г) Состоящий в браке с гражданином Российской Федерации, имеющим место жительства на территории Российской Федерации, - свидетельство о браке и паспорт супруга - гражданина Российской Федерации.</em></p>\n<p><em>д) Осуществивший инвестиции в Российской Федерации - письмо руководителя организации, привлекшей инвестиции, об осуществлении данным иностранным гражданином инвестиций в размере, установленном Правительством Российской Федерации.</em></p>\n<p><em>е) Поступивший на военную службу - контракт о прохождении военной службы.</em></p>\n<p><em>ж) Имеющий ребенка, состоящего в гражданстве Российской Федерации, - свидетельство о рождении ребенка. Наличие гражданства Российской Федерации у ребенка удостоверяется документами, предусмотренными законодательством Российской Федерации.</em></p>\n<p><em>з) Имеющий сына или дочь, достигших возраста восемнадцати лет, состоящих в гражданстве Российской Федерации и решением суда, вступившим в законную силу, признанных недееспособными либо ограниченных в дееспособности, - свидетельство о рождении и паспорт гражданина Российской Федерации сына или дочери, а также копию решения суда о признании лица недееспособным или ограниченным в дееспособности.</em></p>\n<p>Если вы не попадаете под вышеупомянутые пункты, то остается лишь два варианта: либо оформлять право на работу (так называемый&nbsp;<strong>патент</strong>), либо ждать, пока снова будут выделены квоты, т.е. следующего года.</p>\n<p><strong>Дальнейшие действия</strong>: получение вида на жительство в Российской Федерации через полгода после получения РВП (при этом нужно работать официально и предоставить соответствующие документы).</p>\n<p><strong>Трудности</strong>: получение квоты (в особенности в остро востребованных областях вроде Ленинградской, Ростовской и Московской, а также в Крыму). Помимо подачи заявления, регистрации, паспорта нужно подавать сертификат о знании языка, который стоит 5500 рублей (в СПб) и оплатить пошлину.</p>\n<h2>3. Получение вида на жительство в Российской Федерации.</h2>\n<h3><a href=\"https://xn--b1aew.xn--p1ai/Deljatelnost/emvd/guvm/%D0%B2%D1%8B%D0%B4%D0%B0%D1%87%D0%B0-%D0%B2%D0%B8%D0%B4%D0%B0-%D0%BD%D0%B0-%D0%B6%D0%B8%D1%82%D0%B5%D0%BB%D1%8C%D1%81%D1%82%D0%B2%D0%BE\" target=\"_blank\">На сайте МВД</a></h3>\n<p>Для получения вида на жительство в Российской Федерации большинству переселенцев с Украины необходимо сперва получить РВП. Но существует еще программа для носителей русского языка. Для участия вам нужно иметь родственника по прямой восходящей линии, который постоянно проживают или ранее постоянно проживали в Российской Федерации либо на территории, относившейся к Российской империи или СССР, в пределах государственной границы Российской Федерации, доказать с ним родство и предоставить доказательства его проживания.</p>\n<p>На признание носителем языка вам предстоит пройти комиссию.</p>\n<p><strong>Дальнейшие действия</strong>: получение гражданства РФ через несколько лет проживания в России. Однако есть и положительная новость: если у вас один из родителей &ndash; гражданин РФ, то гражданство вам положено&nbsp;по упрощённой схеме.</p>\n<p><strong>Трудности</strong>: сложности с получением разрешения на временное проживание. В случае с программой носителя русского языка, нужно иметь родственника по прямой восходящей линии и форму 9, в которой указано что они проживал на территории РФ\\СССР\\Российской империи.</p>\n<h2>4. Участие в программе по оказанию содействия добровольному переселению соотечественников, проживающих за рубежом.</h2>\n<h3><a href=\"https://xn--b1aew.xn--p1ai/Deljatelnost/emvd/guvm/%D0%BE%D1%84%D0%BE%D1%80%D0%BC%D0%BB%D0%B5%D0%BD%D0%B8%D0%B5-%D1%81%D0%B2%D0%B8%D0%B4%D0%B5%D1%82%D0%B5%D0%BB%D1%8C%D1%81%D1%82%D0%B2%D0%B0-%D1%83%D1%87%D0%B0%D1%81%D1%82%D0%BD%D0%B8%D0%BA%D0%B0\" target=\"_blank\">На сайте МВД</a></h3>\n<p>&nbsp;Данную программу обычно хвалят как чиновники, так и жители РФ, однако тут есть один важный подводный камень: если вы приехали с Украины, то вы можете участвовать в данной программе, лишь, когда у вас будет официальный статус в Российской Федерации.</p>\n<p>Для начала вам необходимо получить временное убежище, РВП или вид на жительство, а уже потом подавать бумаги на участие в программе. Или же оформляться участником заранее через посольство РФ или российские консульства (к примеру, в Харькове). Для этого, вам надо вернуться в Украину.</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://s019.radikal.ru/i636/1703/34/c95473675436.jpg\" alt=\"свидетельство участника программы переселения\" width=\"311\" height=\"245\" /></p>\n<p align=\"center\"><em>Удостоверение участника программы</em></p>\n<p><strong>Трудности</strong>: невозможность участия, если вы уже находитесь в РФ и не имеете официального статуса (убежища, РВП или вида на жительство).</p>\n<h2>5. Оформление патента (иными словами, разрешения на работу).\n</h2>\n<p>С 1 января 2015 года правительство РФ ввело обязательное получение патентов на работу для иностранных граждан. Для получения данного документа необходимо следующее:</p>\n<p><strong><em>Для получения патента иностранный гражданин в течение тридцати календарных дней со дня въезда в Российскую Федерацию представляет лично или через уполномоченную субъектом Российской Федерации организацию в территориальный орган ФМС России:</em></strong></p>\n<p><em>1. Заявление о выдаче патента.</em></p>\n<p><em>2. Документ, удостоверяющий личность и признаваемый Российской Федерацией в этом качестве.</em></p>\n<p><em>3. Миграционную карту с указанием работы как цели визита в Российскую Федерацию и с отметкой органа пограничного контроля о въезде в Российскую Федерацию или с отметкой территориального органа ФМС России о выдаче указанной миграционной карты.</em></p>\n<p><em>4. Действующий на территории Российской Федерации на срок осуществления трудовой деятельности договор (полис) добровольного медицинского страхования либо договор о предоставлении платных медицинских услуг, заключенный с медицинской организацией, находящейся в субъекте Российской Федерации, на территории которого иностранный гражданин намеревается осуществлять трудовую деятельность.</em></p>\n<p><em><em>5. Документы, подтверждающие отсутствие заболевания наркоманией и инфекционных заболеваний, которые представляют опасность для окружающих, предусмотренных перечнем, утверждаемым уполномоченным Правительством Российской Федерации федеральным органом исполнительной власти, а также сертификат об отсутствии заболевания, вызываемого вирусом.</em><br /></em></p>\n<p><em>иммунодефицита человека (ВИЧ-инфекции), выданные медицинскими организациями, находящимися на территории Российской Федерации.</em></p>\n<p><em>6. Документ, подтверждающий владение русским языком, знание истории России и основ законодательства Российской Федерации.</em></p>\n<p><em>7. Документ, подтверждающий уплату штрафа за нарушение срока обращения за оформлением патента, в случае представления вышеперечисленных документов по истечении тридцати календарных дней со дня въезда в Российскую Федерацию.</em></p>\n<p><em>8. Документы о постановке на учет по месту пребывания.</em></p>\n<p><strong>Дальнейшие действия</strong>: продлевать патент или оформлять разрешение на временное проживание (не забываем про квоту!).</p>\n<p><strong>Трудности</strong>: крупные траты на патент; кроме того, данный документ никак вас не приближает к заветной цели &ndash; гражданству.</p>\n<p><em><em>&nbsp;</em></em></p>\n</div>', 1490861989, 0, NULL, NULL);
